INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1, 1, 1, 'No princípio, Deus criou o céu e a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (2, 1, 2, 'A terra estava sem forma e vazia; as trevas cobriam o abismo e o Espírito de Deus pairava sobre as águas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3, 1, 3, 'Deus disse: “Faça-se a luz!”. E a luz foi feita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4, 1, 4, 'Deus viu que a luz era boa, e separou a luz das trevas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (5, 1, 5, 'Deus chamou à luz dia, e às trevas noite. Sobreveio a tarde e depois a manhã: foi o primeiro dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (6, 1, 6, 'Deus disse: “Faça-se um firmamento entre as águas, e separe ele umas das ou­tras”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (7, 1, 7, 'Deus fez o firmamento e separou as águas que estavam debaixo do firmamento daquelas que estavam por cima.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (8, 1, 8, 'E assim se fez. Deus chamou ao firmamento céu. Sobreveio a tarde e depois a manhã: foi o segundo dia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (9, 1, 9, 'Deus disse: “Que as águas que estão debaixo do céu se juntem num mesmo lugar, e apareça o elemento árido”. E assim se fez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (10, 1, 10, 'Deus chamou ao elemento árido terra, e ao ajuntamento das águas mar. E Deus viu que isso era bom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (11, 1, 11, 'Deus disse: “Produza a terra plan­­tas, ervas que contenham semente e á­r­vores frutíferas que deem fruto segundo a sua espécie e o fruto contenha a sua semente”. E assim foi feito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (12, 1, 12, 'A terra pro­duziu plantas, ervas que contêm semente segundo a sua espécie, e árvores que produzem fruto segundo a sua espécie, contendo o fruto a sua semente. E Deus viu que isso era bom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (13, 1, 13, 'Sobreveio a tarde e depois a manhã: foi o terceiro dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14, 1, 14, 'Deus disse: “Façam-se luzeiros no firmamento do céu para separar o dia da noite. Que sirvam eles de sinais e marquem o tempo, os dias e os anos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (15, 1, 15, 'e resplande­çam no firmamento do céu para iluminar a terra”. E assim se fez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16, 1, 16, 'Deus fez os dois grandes luzeiros: o maior para presidir o dia e o menor para presidir a noite; e fez também as estrelas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (17, 1, 17, 'Deus colocou-os no firmamento do céu para que iluminassem a terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (18, 1, 18, 'presidissem o dia e a noite, e separassem a luz das trevas. E Deus viu que isso era bom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19, 1, 19, 'Sobreveio a tarde e depois a manhã: foi o quarto dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (20, 1, 20, 'Deus disse: “Pululem as águas de uma multidão de seres vivos, e voem aves sobre a terra, debaixo do firmamento do céu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (21, 1, 21, 'Deus criou os monstros mari­nhos e toda a multidão de seres vivos que enchem as águas, segundo a sua espécie, e todas as aves segundo a sua espécie. E Deus viu que isso era bom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (22, 1, 22, 'E Deus os abençoou: “Frutificai – disse ele – e multipli­cai-vos, e enchei as águas do mar, e que as aves se multipliquem sobre a terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (23, 1, 23, 'Sobreveio a tarde e depois a manhã: foi o quinto dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (24, 1, 24, 'Deus disse: “Produza a terra seres vivos segundo a sua espécie: animais domésticos, répteis e animais selvagens, segundo a sua espécie”. E assim se fez.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (25, 1, 25, 'Deus fez os animais selvagens segundo a sua espécie, os animais domésticos igualmente, e da mesma forma todos os animais, que se arrastam sobre a terra. E Deus viu que isso era bom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (26, 1, 26, 'Então Deus disse: “Façamos o homem à nossa imagem e semelhança. Que ele reine sobre os peixes do mar, sobre as aves do céu, sobre os animais domésticos e sobre toda a terra, e sobre todos os répteis que se arrastam sobre a terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (27, 1, 27, 'Deus criou o homem à sua imagem; criou-o à imagem de Deus, criou o homem e a mulher.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28, 1, 28, 'Deus os abençoou: “Frutificai – disse ele – e multiplicai-vos, enchei a terra e submetei-a. Dominai sobre os peixes do mar, sobre as aves do céu e sobre todos os animais que se arrastam sobre a terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (29, 1, 29, 'Deus disse: “Eis que eu vos dou toda a erva que dá semente sobre a terra, e todas as árvores frutíferas que contêm em si mesmas a sua semente, para que vos sirvam de alimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (30, 1, 30, 'E a todos os animais da terra, a todas as aves do céu, a tudo o que se arrasta sobre a terra, e em que haja sopro de vida, eu dou toda a erva verde por alimento”. E assim se fez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (31, 1, 31, 'Deus contemplou toda a sua obra, e viu que tudo era muito bom. Sobreveio a tarde e depois a manhã: foi o sexto dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32, 2, 1, 'Assim foram concluídos o céu, a ter­ra e todo o seu exército.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (33, 2, 2, 'Tendo Deus terminado no sétimo dia a obra que tinha feito, descansou do seu trabalho.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (34, 2, 3, 'Ele abençoou o sétimo dia e o consagrou, porque nesse dia descansou de toda a obra da Criação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (35, 2, 4, 'Tal é a história da criação do céu e da terra. No tempo em que o Senhor Deus fez a terra e o céu,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (36, 2, 5, 'não exis­tia ainda sobre a terra nenhum arbusto nos campos, e nenhuma erva havia ainda brotado nos campos, porque o Senhor Deus não tinha feito chover sobre a terra, nem havia homem que a cultivasse;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (37, 2, 6, 'mas subia da terra um vapor que regava toda a sua superfície.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (38, 2, 7, 'O Senhor Deus formou, pois, o homem do barro da terra, e inspirou-lhe nas narinas o sopro da vida e o homem se tornou um ser vivente.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (39, 2, 8, 'Ora, o Senhor Deus tinha plantado um jardim no Éden, do lado do oriente, e colocou nele o homem que havia criado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (40, 2, 9, 'O Senhor Deus fez brotar da terra toda a sorte de árvores de aspecto agradável, e de frutos bons para comer; e a árvore da vida no meio do jardim, e a árvore da ciência do bem e do mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (41, 2, 10, 'Um rio saía do Éden para regar o jardim, e dividia-se em seguida em quatro braços.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (42, 2, 11, 'O no­me do primeiro é Fison, e é aquele que contorna toda a região de Hévila, onde se encontra o ouro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (43, 2, 12, '(O ouro dessa região é puro; encontra-se ali também o bdélio e a pedra de ônix.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (44, 2, 13, 'O nome do segundo rio é Geon, e é aquele que contorna toda a região de Cuch.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (45, 2, 14, 'O nome do terceiro rio é Tigre, que corre ao oriente da Assíria. O quarto rio é o Eufrates.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (46, 2, 15, 'O Senhor Deus tomou o homem e o colocou no jardim do Éden, para cultivar o solo e o guardar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (47, 2, 16, 'Deu-lhe este preceito: “Podes comer do fruto de todas as árvores do jardim;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (48, 2, 17, 'mas não comas do fruto da árvore da ciência do bem e do mal; porque no dia em que dele comeres, morrerás indubitavelmente”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (49, 2, 18, 'O Senhor Deus disse: “Não é bom que o homem esteja só. Vou dar-lhe uma auxiliar que lhe seja adequada”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (50, 2, 19, 'Tendo, pois, o Senhor Deus formado da terra todos os animais dos campos, e todas as aves do céu, levou-os ao homem, para ver como ele os havia de chamar; e todo o nome que o homem pôs aos animais vivos, esse é o seu verdadeiro nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (51, 2, 20, 'O homem pôs nomes a todos os animais, a todas as aves do céu e a todos os animais do campo; mas não se achava para ele uma auxiliar que lhe fosse adequada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (52, 2, 21, 'Então, o Senhor Deus mandou ao homem um profundo sono; e enquanto ele dormia, tomou-lhe uma costela e fechou com carne o seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (53, 2, 22, 'E da costela que tinha tomado do homem, o Senhor Deus fez uma mulher, e levou-a para junto do homem.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (54, 2, 23, '“Eis agora aqui – disse o homem – o osso de meus ossos e a carne de minha carne; ela se chamará mulher, porque foi tomada do homem.”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (55, 2, 24, 'Por isso, o homem deixa o seu pai e a sua mãe para se unir à sua mulher; e já não são mais que uma só carne.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (56, 2, 25, 'O homem e a mulher estavam nus, e não se envergonhavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (57, 3, 1, 'A serpente era o mais astuto de todos os animais do campo que o Senhor Deus tinha formado. Ela disse à mulher: “É verdade que Deus vos proibiu comer do fruto de toda árvore do jardim?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (58, 3, 2, 'A mulher respondeu-lhe: ‘‘Podemos comer do fruto das árvores do jardim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (59, 3, 3, 'Mas do fruto da árvore que está no meio do jardim, Deus disse: ‘Vós não comereis dele, nem o tocareis, para que não morrais’.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (60, 3, 4, '“Oh, não! – tornou a serpente – vós não morrereis!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (61, 3, 5, 'Mas Deus bem sabe que, no dia em que dele comerdes, vossos olhos se abrirão, e sereis como deuses, conhecedores do bem e do mal.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (62, 3, 6, 'A mulher, vendo que o fruto da árvore era bom para comer, de agradável aspecto e mui apropriado para abrir a inteligência, tomou dele, comeu, e o apresentou também ao seu marido, que comeu igualmente.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (63, 3, 7, 'Então os seus olhos abriram-se; e, vendo que estavam nus, tomaram fo­lhas de figueira, ligaram-nas e fizeram tangas para si.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (64, 3, 8, 'E eis que ouviram o barulho (dos passos) do Senhor Deus que passeava no jardim, à hora da brisa da tarde. O homem e sua mulher esconde­ram-se da face do Senhor Deus, no meio das árvores do jardim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (65, 3, 9, 'Mas o Senhor Deus chamou o homem e perguntou-lhe: “Onde estás?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (66, 3, 10, 'E ele respondeu: “Ouvi o barulho dos vossos passos no jardim; tive medo, porque estou nu; e ocultei-me”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (67, 3, 11, 'O Senhor Deus disse: “Quem te revelou que estavas nu? Terias tu porventura comido do fruto da árvore que eu te havia proibido de comer?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (68, 3, 12, 'O homem respondeu: “A mu­lher que pusestes ao meu lado apresentou-me deste fruto, e eu comi”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (69, 3, 13, 'O Senhor Deus disse à mulher: ‘‘Por que fizeste isso?”. “A serpente enganou-me – res­pondeu ela – e eu comi.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (70, 3, 14, 'Então o Senhor Deus disse à serpente: “Porque fizeste isso, serás maldita entre todos os animais domésticos e feras do campo; andarás de rastos sobre o teu ventre e comerás o pó todos os dias de tua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (71, 3, 15, 'Porei ódio entre ti e a mulher, entre a tua descendência e a dela. Esta te ferirá a cabeça, e tu lhe ferirás o calcanhar”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (72, 3, 16, 'Disse também à mulher: ‘‘Multiplicarei os sofrimentos de teu parto; darás à luz com dores, teus desejos te impelirão para o teu marido e tu estarás sob o seu domínio”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (73, 3, 17, 'E disse em seguida ao homem: “Porque ouviste a voz de tua mulher e comeste do fruto da árvore que eu te havia proibido comer, maldita seja a terra por tua causa. Tirarás dela com trabalhos penosos o teu sustento todos os dias de tua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (74, 3, 18, 'Ela te produzirá espinhos e abrolhos, e tu comerás a erva da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (75, 3, 19, 'Comerás o teu pão com o suor do teu rosto, até que voltes à terra de que foste tirado; porque és pó, e pó te hás de tornar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (76, 3, 20, 'Adão pôs à sua mulher o nome de Eva, porque ela era a mãe de todos os viventes.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (77, 3, 21, 'O Senhor Deus fez para Adão e sua mulher umas vestes de peles, e os vestiu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (78, 3, 22, 'E o Senhor Deus disse: “Eis que o homem se tornou como um de nós, conhecedor do bem e do mal. Agora, pois, cuidemos que ele não estenda a sua mão e tome também do fruto da árvore da vida, e o coma, e viva eternamente”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (79, 3, 23, 'O Senhor Deus expulsou-o do jardim do Éden, para que ele cultivasse a terra “de onde havia tirado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (80, 3, 24, 'E expulsou-o; e colocou ao oriente do jardim do Éden querubins armados de uma espada flamejante, para guardar o caminho da árvore da vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (81, 4, 1, 'Adão conheceu Eva, sua mu­lher, e ela concebeu e deu à luz Caim, e disse: “gerei um homem com a ajuda do Senhor”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (82, 4, 2, 'E deu em seguida à luz Abel, irmão de Caim. Abel tornou-se pastor de ovelhas e Caim, lavrador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (83, 4, 3, 'Passado algum tempo, ofereceu Caim frutos da terra em oblação ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (84, 4, 4, 'Abel, de seu lado, ofereceu dos primogênitos do seu rebanho e das gorduras dele; e o Senhor olhou com agrado para Abel e para sua oblação,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (85, 4, 5, 'mas não olhou para Caim, nem para os seus dons. Caim ficou extremamente irritado com isso, e o seu semblante tornou-se abatido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (86, 4, 6, 'O Senhor disse-lhe: “Por que estás irado? E por que está abatido o teu semblante?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (87, 4, 7, 'Se praticares o bem, sem dúvida alguma poderás reabilitar-te. Mas se procederes mal, o pecado estará à tua porta, esprei­tando-te; mas, tu deverás dominá-lo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (88, 4, 8, 'Caim disse então a Abel, seu irmão: “Vamos ao campo”. Logo que chegaram ao campo, Caim atirou-se sobre seu irmão e o matou.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (89, 4, 9, 'O Senhor disse a Caim: “Onde está teu irmão Abel?”. Caim res­pondeu: “Não sei! Sou porventura eu o guarda de meu irmão?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (90, 4, 10, 'O Senhor disse-lhe: “Que fizeste! Eis que a voz do sangue do teu irmão clama por mim desde a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (91, 4, 11, 'De ora em diante, serás maldito e expulso da terra, que abriu sua boca para beber de tua mão o sangue do teu irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (92, 4, 12, 'Quando a cultivares, ela te negará os seus frutos. E tu serás peregrino e errante sobre a terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (93, 4, 13, 'Caim disse ao Senhor: “Meu castigo é grande demais para que eu o possa suportar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (94, 4, 14, 'Eis que me expulsais agora deste lugar, e eu devo ocultar-me longe de vossa face, tornando-me um peregrino errante sobre a terra. O primeiro que me encontrar, vai matar-me”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (95, 4, 15, 'E o Senhor respondeu-lhe: “Não! Mas aquele que matar Caim será punido sete vezes”. Então, o Se­nhor pôs em Caim um sinal para que, se alguém o encontrasse, não o matasse.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (96, 4, 16, 'Caim retirou-se da presença do Senhor, e foi habitar na região de Nod, ao oriente do Éden.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (97, 4, 17, 'Caim conheceu sua mulher. Ela concebeu e deu à luz Henoc. E construiu uma cidade, à qual pôs o nome de seu filho Henoc.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (98, 4, 18, 'Henoc gerou Irad, Irad gerou Maviael; Maviael gerou Matusael e Matusael gerou Lamec.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (99, 4, 19, 'Lamec tomou duas mulheres, uma chamada Ada e a outra, Sela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (100, 4, 20, 'Ada deu à luz Jabel, que foi o pai daqueles que moram em tendas, entre os rebanhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (101, 4, 21, 'O nome do seu irmão era Jubal, que foi o pai de todos aqueles que tocam a cítara e os instrumentos de sopro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (102, 4, 22, 'Sela, de seu lado, deu à luz Tubalcaim, pai de todos aqueles que trabalham o cobre e o ferro. A irmã de Tubalcaim era Noema.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (103, 4, 23, 'Lamec disse às suas mulhe­res: “Ada e Sela, ouvi a minha voz: mulheres de Lamec, escutai as minhas palavras: Por uma ferida matei um homem, e por uma contusão um menino.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (104, 4, 24, 'Se Caim será vingado sete vezes, Lamec o será setenta e sete vezes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (105, 4, 25, 'Adão conheceu outra vez sua mu­lher, e esta deu à luz um filho, ao qual pôs o nome de Set, dizendo: “Deus deu-me uma posteridade para substituir Abel, que Caim matou”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (106, 4, 26, 'Set teve também um filho, que chamou Enós. E o nome do Senhor começou a ser invocado a partir de então.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (107, 5, 1, 'Este é o livro da história da família de Adão. Quando Deus criou o homem, ele o fez à imagem de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (108, 5, 2, 'Criou-os homem e mulher, e os abençoou, e deu-lhes o nome de homem no dia em que os criou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (109, 5, 3, 'Adão viveu cento e trinta anos, e gerou um filho à sua semelhança, à sua imagem, e deu-lhe o nome de Set.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (110, 5, 4, 'Depois de haver gerado Set, Adão viveu oitocentos anos e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (111, 5, 5, 'Todo o tempo que Adão viveu foi de novecentos e trinta anos. E depois disso morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (112, 5, 6, 'Set viveu cento e cinco anos, e depois gerou Enós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (113, 5, 7, 'E depois do nascimento de Enós, viveu ain­da oitocentos e sete anos e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (114, 5, 8, 'A duração total da vida de Set foi de novecentos e doze anos; e depois disso morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (115, 5, 9, 'Enós viveu noventa anos, e depois gerou Cainã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (116, 5, 10, 'E depois do nascimento de Cainã, Enós viveu ainda oitocentos e quinze anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (117, 5, 11, 'E o tempo da vida de Enós foi de novecentos e cinco anos; e morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (118, 5, 12, 'Cainã viveu setenta anos, e depois gerou Malaleel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (119, 5, 13, 'Após o nascimento de Malaleel, Cainã viveu ainda oitocentos e quarenta anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (120, 5, 14, 'Todo o tempo da vida de Cainã foi de novecentos e dez anos; e morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (121, 5, 15, 'Malaleel viveu sessenta e cinco anos, e depois gerou Jared.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (122, 5, 16, 'Após o nascimento de Jared, Malaleel viveu ainda oitocentos e trinta anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (123, 5, 17, 'Todo o tempo da vida de Malaleel foi de oitocentos e noventa e cinco anos; e morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (124, 5, 18, 'Jared viveu cento e sessenta e dois anos e gerou Henoc.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (125, 5, 19, 'Após o nascimento de Henoc, Jared viveu ainda oitocentos anos e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (126, 5, 20, 'Todo o tempo da vida de Jared foi de novecentos e sessenta e dois anos; e morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (127, 5, 21, 'Henoc viveu sessenta e cinco anos e gerou Matusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (128, 5, 22, 'Após o nascimento de Matusalém, Henoc andou com Deus durante trezentos anos e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (129, 5, 23, 'A duração total da vida de Henoc foi de trezentos e sessenta e cinco anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (130, 5, 24, 'Henoc andou com Deus e desapareceu, porque Deus o levou.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (131, 5, 25, 'Matusalém viveu cento e oitenta e sete anos, e gerou Lamec.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (132, 5, 26, 'Após o nascimento de Lamec, Matusalém viveu ainda setecentos e oitenta e dois anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (133, 5, 27, 'A duração total da vida de Matusalém foi de novecentos e sessenta e nove anos; e morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (134, 5, 28, 'Lamec viveu cento e oitenta e dois anos, e gerou um filho,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (135, 5, 29, 'a quem deu o nome de Noé, dizendo: “Este nos trará, em nossas fadigas e no duro labor de nossas mãos, um alívio tirado da terra mesma que o Senhor amaldiçoou”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (136, 5, 30, 'Após o nascimento de Noé, Lamec viveu ainda quinhentos e noventa e cinco anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (137, 5, 31, 'A duração total da vida de Lamec foi de setecentos e setenta e sete anos; e morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (138, 5, 32, 'Com a idade de quinhentos anos, Noé gerou Sem, Cam e Jafé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (139, 6, 1, 'Quando os homens come­ça­ram a multiplicar-se sobre a terra, e lhes nas­ceram filhas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (140, 6, 2, 'os filhos de Deus viram que as filhas dos homens eram belas, e escolheram esposas entre elas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (141, 6, 3, 'O Se­nhor então disse: “Meu espírito não perma­necerá para sempre no homem, porque todo ele é carne, e a duração de sua vida será só de cento e vinte anos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (142, 6, 4, 'Naquele tempo viviam gigantes na terra, como também daí por diante, quando os filhos de Deus se uniram às filhas dos homens e elas geravam filhos. Estes são os heróis, tão afamados dos tempos antigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (143, 6, 5, 'O Senhor viu que a maldade dos homens era grande na terra, e que todos os pensamentos de seu coração estavam continuamente voltados para o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (144, 6, 6, 'O Senhor arrependeu-se de ter criado o homem na terra, e teve o coração ferido de íntima dor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (145, 6, 7, 'E disse: “Exterminarei da superfície da terra o homem que criei, e com ele os animais, os répteis e as aves do céu, porque eu me arrependo de tê-los criado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (146, 6, 8, 'Noé, entretanto, encontrou graça aos olhos do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (147, 6, 9, 'Esta é a história de Noé: Noé era um homem justo e perfeito no meio dos homens de sua geração. Ele andava com Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (148, 6, 10, 'Noé teve três filhos: Sem, Cam e Jafé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (149, 6, 11, 'A terra corrompia-se diante de Deus e enchia-se de violência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (150, 6, 12, 'Deus olhou para a terra e viu que ela estava corrompida: toda a criatura seguia na ter­ra o caminho da corrupção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (151, 6, 13, 'Então Deus disse a Noé: “Eis chegado o fim de toda a criatura diante de mim, pois eles encheram a terra de violência. Vou exterminá-los juntamente com a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (152, 6, 14, 'Faze para ti uma arca de madeira resinosa: divide-a em compartimentos e a untarás de betume por dentro e por fora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (153, 6, 15, 'E eis como a farás: seu comprimento será de trezentos côvados, sua largura de cinquenta côvados e sua altura de trinta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (154, 6, 16, 'Farás no alto da arca uma abertura com a dimensão de um côvado. Porás a porta da arca a um lado, e cons­truirás três andares de compartimentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (155, 6, 17, 'Eis que vou fazer cair o dilúvio sobre a terra, uma inundação que exterminará todo ser que tenha sopro de vida debaixo do céu. Tudo que está sobre a terra morrerá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (156, 6, 18, 'Mas farei aliança contigo: entrarás na arca com teus filhos, tua mulher e as mulheres de teus filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (157, 6, 19, 'De tudo o que vive, de cada espécie de animais, farás entrar na arca dois, macho e fêmea, para que vivam contigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (158, 6, 20, 'De cada espécie de aves, e de cada espécie de quadrúpedes, e de cada espécie de animais que se arrastam sobre a terra, entrará um casal contigo, para que lhes possas conservar a vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (159, 6, 21, 'Tomarás também contigo de todas as coisas para comer, e as armazenará para que te sirvam de alimento, a ti e aos animais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (160, 6, 22, 'Noé obedeceu, e fez tudo o que o Senhor lhe tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (161, 7, 1, 'O Senhor disse a Noé: “Entra na arca, tu e toda a tua casa, porque te reconheci justo diante dos meus olhos, entre os de tua geração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (162, 7, 2, 'De todos os animais puros tomarás sete casais, machos e fêmeas, e de todos os animais impuros tomarás um casal, macho e fêmea;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (163, 7, 3, 'das aves do céu igualmente sete casais, machos e fêmeas, para que se conserve viva a raça sobre a face de toda a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (164, 7, 4, 'Dentro de sete dias farei chover sobre a terra durante quarenta dias e quarenta noites, e exterminarei da superfície da terra todos os seres que eu fiz”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (165, 7, 5, 'Noé fez tudo o que o Senhor lhe tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (166, 7, 6, 'Noé tinha seiscentos anos quando veio o dilúvio sobre a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (167, 7, 7, 'Para escapar à inundação, entrou na arca com seus filhos, sua mulher e as mulheres de seus filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (168, 7, 8, 'Dos animais puros e impuros, das aves e de tudo que se arrasta sobre a terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (169, 7, 9, 'entraram na arca com Noé um casal macho e fêmea, como o Senhor tinha ordenado a Noé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (170, 7, 10, 'Passados os sete dias, as águas do dilúvio precipitaram-se sobre a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (171, 7, 11, 'No ano seiscentos da vida de Noé, no segundo mês, no décimo sétimo dia do mês, romperam-se naquele dia todas as fontes do grande abismo, e abriram-se as barreiras do céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (172, 7, 12, 'A chuva caiu sobre a terra durante quarenta dias e quarenta noites.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (173, 7, 13, 'Naquele mesmo dia, entrou Noé na arca, com Sem, Cam e Jafé, seus filhos, sua mulher e as três mulheres de seus fi­lhos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (174, 7, 14, 'e com eles os animais selvagens de toda a espécie, os animais domésticos de toda a espécie, os répteis de toda a espécie que se arrastavam sobre a terra, e tudo o que voa, de toda a espécie, todas as aves e tudo o que tem asas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (175, 7, 15, 'De cada espécie que tem um sopro de vida um casal entrou na arca com Noé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (176, 7, 16, 'Eles chegavam, macho e fêmea, de cada espécie. Como Deus tinha ordenado a Noé. E o Senhor fechou a porta atrás dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (177, 7, 17, 'O dilúvio caiu sobre a terra durante quarenta dias. As águas incharam e levantaram a arca, que foi elevada acima da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (178, 7, 18, 'As águas inundaram tudo com violência, e cobriram toda a terra, e a arca flutuava na superfície das águas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (179, 7, 19, 'As águas engrossaram prodigiosamente sobre a terra, e cobriram todos os altos montes que existem debaixo do céu;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (180, 7, 20, 'e elevaram-se quinze côvados acima dos montes que cobriam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (181, 7, 21, 'Todas as criaturas que se moviam na terra foram exterminadas: aves, animais domésticos, feras selvagens e tudo o que se arrasta na terra, e todos os homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (182, 7, 22, 'Tudo o que respira e tem um sopro de vida sobre a terra pereceu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (183, 7, 23, 'Assim foram exterminados todos os seres que se encontravam sobre a face da terra, desde os homens até os quadrúpedes, tanto os répteis como as aves do céu, tudo foi exterminado da terra. Só Noé ficou e o que se encontrava com ele na arca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (184, 7, 24, 'As águas cobriram a terra pelo espaço de cento e cinquenta dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (185, 8, 1, 'Ora, Deus lembrou-se de Noé e de todos os animais selvagens e de todos os animais domésticos que estavam com ele na arca. Fez soprar um vento sobre a terra, e as águas baixaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (186, 8, 2, 'As fontes do abismo fecharam-se, assim como as barreiras do céu, e foram retidas as chuvas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (187, 8, 3, 'As águas foram se retirando progressivamente da terra; e começaram a baixar depois de cento e cinquenta dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (188, 8, 4, 'No sétimo mês, no décimo sétimo dia do mês, a arca parou sobre as montanhas do Ararat.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (189, 8, 5, 'Entretanto, as águas iam dimi­nuin­do pouco a pouco até o décimo mês; e no décimo mês, no primeiro dia do mês, apareceram os cumes das montanhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (190, 8, 6, 'No fim de quarenta dias, abriu Noé a janela que tinha feito na arca');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (191, 8, 7, 'e deixou sair um corvo, o qual, saindo, voava de um lado para outro, até que aparecesse a terra seca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (192, 8, 8, 'Soltou também uma pomba, para ver se as águas teriam já diminuído na face da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (193, 8, 9, 'A pomba, porém, não encon­trando onde pousar, voltou para junto dele na arca, porque havia ainda água na face da terra. Noé esten­deu a mão, e tendo-a tomado, recolheu-a na arca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (194, 8, 10, 'Esperou mais sete dias, e soltou de novo a pomba fora da arca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (195, 8, 11, 'E eis que pela tarde ela voltou, trazendo no bico uma folha verde de oliveira. Assim Noé compreendeu que as águas tinham baixado sobre a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (196, 8, 12, 'Esperou ainda sete dias, e soltou a pomba que desta vez não mais voltou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (197, 8, 13, 'No ano seiscentos e um, no pri­mei­ro mês, no primeiro dia do mês, as águas se tinham secado sobre a terra. Noé descobriu o teto da arca, olhou e viu que a superfície do solo estava seca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (198, 8, 14, 'No segundo mês, no vigésimo sétimo dia do mês, a terra estava seca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (199, 8, 15, 'Então Deus falou a Noé:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (200, 8, 16, '“Sai da arca, com tua mulher, teus filhos e as mulheres de teus filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (201, 8, 17, 'Faze sair igualmente contigo todos os animais que estão contigo de todas as espécies: aves, quadrúpedes, répteis diversos que se arrastam sobre a terra; faze-os sair contigo para que se espalhem sobre a terra e para que cresçam e se multipliquem sobre a terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (202, 8, 18, 'Noé saiu com seus filhos, sua mulher e as mulheres de seus fi­lhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (203, 8, 19, 'Todos os animais selva­gens, todos os répteis, todas as aves, todos os seres que se movem sobre a terra saíram da arca segundo suas espécies.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (204, 8, 20, 'E Noé levantou um altar ao Senhor: tomou de todos os animais puros e de todas as aves puras, e ofereceu-os em holocausto ao Senhor sobre o altar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (205, 8, 21, 'O Senhor respirou um agradável odor, e disse em seu coração: “Doravante, não mais amaldiçoarei a terra por causa do homem – porque os pensamentos do seu coração são maus desde a sua juventude –, e não ferirei mais todos os seres vivos, como o fiz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (206, 8, 22, 'Enquanto durar a terra, não mais cessarão a sementeira e a colheita, o frio e o calor, o verão e o inverno, o dia e a noite”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (207, 9, 1, 'Deus abençoou Noé e seus filhos: “Sede fecundos – disse-lhes ele – multiplicai-vos e enchei a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (208, 9, 2, 'Vós sereis objeto de temor e de espanto para todo animal da terra, toda ave do céu, tudo o que se arrasta sobre o solo e todos os peixes do mar: eles vos são entre­gues nas mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (209, 9, 3, 'Tudo o que se move e vive vos servirá de alimento; eu vos dou tudo isto, como vos dei a erva verde.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (210, 9, 4, 'Somente não comereis carne com a sua alma, com sangue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (211, 9, 5, 'Eu pedirei conta de vosso sangue, por causa de vossas almas, a todo animal; e ao homem (que matar) o seu irmão, pedirei conta da alma do homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (212, 9, 6, 'Todo aquele que derramar o sangue humano terá seu próprio sangue derramado pelo homem, porque Deus fez o homem à sua imagem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (213, 9, 7, 'Sede, pois, fecundos e multiplicai-vos, e espalhai-vos sobre a terra abundantemente”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (214, 9, 8, 'Disse também Deus a Noé e a seus filhos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (215, 9, 9, '“Vou fazer uma aliança convosco e com vossa posteridade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (216, 9, 10, 'assim como com todos os seres vivos que estão convosco: as aves, os animais domésticos, todos os animais selvagens que estão convosco, desde todos aqueles que saíram da arca até todo animal da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (217, 9, 11, 'Faço esta aliança convosco: ne­nhuma criatura será destruída pelas águas do dilúvio, e não haverá mais dilúvio para devastar a terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (218, 9, 12, 'Deus disse: “Eis o sinal da aliança que eu faço convosco e com todos os seres vivos que vos cercam, por todas as gerações futuras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (219, 9, 13, 'Ponho o meu arco nas nuvens, para que ele seja o sinal da aliança entre mim e a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (220, 9, 14, 'Quando eu tiver coberto o céu de nuvens por cima da terra, o meu arco aparecerá nas nuvens,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (221, 9, 15, 'e me lembrarei da aliança que fiz convosco e com todo ser vivo de toda a espécie, e as águas não causarão mais dilúvio que extermine toda criatura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (222, 9, 16, 'Quando eu vir o arco nas nuvens, eu me lembrarei da aliança eterna estabelecida entre Deus e todos os seres vivos de toda a espécie que estão sobre a terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (223, 9, 17, 'Dirigindo-se a Noé, Deus acrescentou: “Este é o sinal da aliança que faço entre mim e todas as criaturas que estão na terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (224, 9, 18, 'Os filhos de Noé que saíram da arca eram Sem, Cam e Jafé. Cam era o pai de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (225, 9, 19, 'Estes eram os três filhos de Noé. É por eles que foi povoada toda a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (226, 9, 20, 'Noé, que era agricultor, plantou uma vinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (227, 9, 21, 'Tendo bebido vinho, embriagou-se, e apareceu nu no meio de sua tenda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (228, 9, 22, 'Cam, o pai de Canaã vendo a nudez de seu pai, saiu e foi contá-lo aos seus irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (229, 9, 23, 'Mas, Sem e Jafé, tomando uma capa, puseram-na sobre os seus ombros e foram cobrir a nudez de seu pai, andando de costas; e não viram a nudez de seu pai, pois que tinham os seus rostos voltados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (230, 9, 24, 'Quando Noé despertou de sua embriaguez, soube o que lhe tinha feito o seu filho mais novo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (231, 9, 25, '“Maldito seja Canaã – disse ele –; que ele seja o último dos escravos de seus irmãos!”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (232, 9, 26, 'E acrescentou : “Bendito seja o Senhor Deus de Sem, e Canaã seja seu escravo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (233, 9, 27, 'Que Deus prospere a Jafé; e este habite nas tendas de Sem, e Canaã seja seu escravo!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (234, 9, 28, 'Noé viveu ainda depois do dilúvio trezentos e cinquenta anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (235, 9, 29, 'A duração total da vida de Noé foi de novecentos e cinquenta anos; e morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (236, 10, 1, 'Eis a posteridade dos filhos de Noé: Sem, Cam e Jafé. Estes tiveram filhos depois do dilúvio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (237, 10, 2, 'Filhos de Jafé: Gomer, Magog, Madai, Javã, Tubal, Mosoc e Tiras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (238, 10, 3, 'Filhos de Gomer: Asquenez, Rifat e Togorma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (239, 10, 4, 'Filhos de Javã: Elisa e Társis, Cetim e Rodanim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (240, 10, 5, 'Destes saíram os povos dispersos nas ilhas das nações, em seus diversos países, cada um segundo sua língua e segundo suas famílias e suas nações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (241, 10, 6, 'Filhos de Cam: Cuch, Mesraim, Fut e Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (242, 10, 7, 'Filhos de Cuch: Sabá, Hévila, Sabata, Regma e Sabataca. Filhos de Regma: Sabá e Dadã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (243, 10, 8, 'Cuch gerou Nemrod, que foi o primeiro homem poderoso da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (244, 10, 9, 'Ele foi um grande caçador diante do Se­nhor. Donde a expressão: “Como Nemrod, grande caçador diante do Eterno”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (245, 10, 10, 'Ele estabele­ceu o seu reino primeiramente em Babilônia, Arac, Acad e Calane, na terra de Senaar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (246, 10, 11, 'Daí foi para Assur e construiu Nínive, Reobot-Ir, Cale');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (247, 10, 12, 'e Resen, a grande cidade entre Nínive e Cale.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (248, 10, 13, 'Mesraim gerou os ludim, os anamim, os laabim, os neftuim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (249, 10, 14, 'os fetrusim, os casluim e os caftorim, donde saíram os filisteus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (250, 10, 15, 'Canaã gerou Sidon, seu primogênito, e Het,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (251, 10, 16, 'assim como os jebuseus, os amorreus, os gergeseus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (252, 10, 17, 'os heveus, os araceus, os sineus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (253, 10, 18, 'os aradeus, os samareus e os hamateus. Em seguida, as famílias dos cananeus se dispersaram,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (254, 10, 19, 'e o território dos cananeus era desde Sidon, na direção de Gerara, até Gaza; e na direção de Sodoma, Gomorra, Adama e Seboim, até Lesa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (255, 10, 20, 'Estes são os filhos de Cam segundo suas famílias, suas línguas, em seus diversos países e suas nações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (256, 10, 21, 'Nasceram também filhos a Sem, pai de todos os filhos de Héber, e irmão mais velho de Jafé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (257, 10, 22, 'Filhos de Sem: Elam, Assur, Arfaxad, Lud e Aram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (258, 10, 23, 'Filhos de Aram: Hus, Hul, Geter e Mes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (259, 10, 24, 'Arfaxad gerou Salé, Salé gerou Héber.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (260, 10, 25, 'Héber teve dois filhos: um se chamava Faleg, porque no seu tempo a terra foi dividida, e o outro se chamava Jectã.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (261, 10, 26, 'Jectã gerou Elmodad, Salef, Asarmot, Jaré,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (262, 10, 27, 'Aduram, Uzal, Decla,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (263, 10, 28, 'Ebal, Abimael, Sabá,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (264, 10, 29, 'Ofir, Hévila e Jobab. Estes são os filhos de Jetã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (265, 10, 30, 'A terra que eles habi­tavam se estendia desde Mesa, na direção de Sefar, até a montanha do oriente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (266, 10, 31, 'Estes são os filhos de Sem, segundo suas famílias, segundo suas línguas, em seus diversos países e suas nações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (267, 10, 32, 'Tais são as famílias dos filhos de Noé, segundo suas gerações e suas nações. É deles que descendem as nações que se espalharam sobre a terra depois do dilúvio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (268, 11, 1, 'Toda a terra tinha uma só lín­gua, e servia-se das mesmas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (269, 11, 2, 'Alguns homens, partindo para o oriente, encontraram na terra de Senaar uma planície onde se estabeleceram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (270, 11, 3, 'E disseram uns aos outros: “Vamos, façamos tijolos e cozamo-los no fogo”. Serviram-se de tijolos em vez de pedras, e de betume em lugar de argamassa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (271, 11, 4, 'Depois disseram: “Vamos, façamos para nós uma cidade e uma torre cujo cimo atinja os céus. Tornemos assim célebre o nosso nome, para que não sejamos dispersos pela face de toda a terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (272, 11, 5, 'Mas o Senhor desceu para ver a cidade e a torre que construíram os filhos dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (273, 11, 6, '“Eis que são um só povo – disse ele – e falam uma só língua: se começam assim, nada futuramente os impedirá de executarem todos os seus empreendimentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (274, 11, 7, 'Vamos: desçamos para lhes confundir a linguagem, de sorte que já não se compreendam um ao outro.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (275, 11, 8, 'Foi dali que o Senhor os dispersou da­quele lugar pela face de toda a terra, e cessaram a construção da cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (276, 11, 9, 'Por isso, deram-lhe o nome de Babel, porque ali o Senhor confundiu a linguagem de todos os habitantes da terra, e dali os dispersou sobre a face de toda a terra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (277, 11, 10, 'Eis a descendência de Sem: Sem, com a idade de cem anos, gerou Arfaxad, dois anos depois do dilúvio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (278, 11, 11, 'Depois do nascimento de Arfaxad, Sem viveu ainda qui­nhentos anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (279, 11, 12, 'Arfaxad, com a idade de trinta e cinco anos, gerou Salé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (280, 11, 13, 'Após o nascimento de Salé, Arfaxad viveu ainda quatrocentos anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (281, 11, 14, 'Salé, com a idade de trinta anos, gerou Héber.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (282, 11, 15, 'Após o nascimento de Héber, Salé viveu ainda quatrocentos anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (283, 11, 16, 'Héber, com a idade de trinta e quatro anos, gerou Faleg.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (284, 11, 17, 'Após o nascimento de Faleg, Héber viveu ainda quatrocentos anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (285, 11, 18, 'Faleg, com a idade de trinta anos, gerou Reu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (286, 11, 19, 'Após o nascimento de Reu, Faleg viveu ainda duzentos e nove anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (287, 11, 20, 'Reu, com a idade de trinta e dois anos, gerou Sarug.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (288, 11, 21, 'Após o nascimento de Sarug, Reu viveu ainda duzentos e sete anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (289, 11, 22, 'Sarug, com a idade de trinta anos, gerou Nacor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (290, 11, 23, 'Após o nascimen­to de Nacor, Sarug viveu ain­da duzentos anos, e gerou filhos e fi­lhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (291, 11, 24, 'Nacor, com a idade de vinte e nove anos, gerou Taré.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (292, 11, 25, 'Após o nascimento de Taré, Nacor viveu ainda cento e dezenove anos, e gerou filhos e filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (293, 11, 26, 'Taré, com a idade de setenta anos, gerou Abrão, Nacor e Aram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (294, 11, 27, 'Eis a descendência de Taré: Taré gerou Abrão, Nacor e Aram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (295, 11, 28, 'Aram gerou Ló. Aram morreu em presença de Taré, seu pai, em Ur da Caldeia, sua terra natal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (296, 11, 29, 'Abrão e Nacor casaram-se: a mu­lher de Abrão chamava-se Sarai, e a de Nacor, Melca, filha de Aram, pai de Melca e de Jesca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (297, 11, 30, 'Sarai era estéril, e não tinha filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (298, 11, 31, 'Taré tomou seu filho Abrão, seu neto Ló, filho de Aram, e Sarai, sua nora, mulher de Abrão, seu filho, e partiu com eles de Ur da Caldeia, indo para a terra de Canaã. Chegados a Harã, estabeleceram-se ali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (299, 11, 32, 'Todo o tempo da vida de Taré foi de duzentos e cinco anos; e morreu em Harã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (300, 12, 1, 'O Senhor disse a Abrão: “Deixa tua terra, tua família e a casa de teu pai e vai para a terra que eu te mostrar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (301, 12, 2, 'Farei de ti uma grande nação; eu te abençoarei e exaltarei o teu nome, e tu serás uma fonte de bênçãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (302, 12, 3, 'Abençoarei aqueles que te abençoarem, e amaldiçoarei aqueles que te amaldiçoarem; todas as famílias da terra serão benditas em ti”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (303, 12, 4, 'Abrão partiu como o Senhor lhe tinha dito, e Ló foi com ele. Abrão tinha setenta e cinco anos, quando partiu de Harã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (304, 12, 5, 'Tomou Sarai, sua mulher, e Ló, filho de seu irmão, assim como todos os bens que possuíam e os escravos que tinham adquirido em Harã, e partiram para a terra de Canaã. Ali chegando,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (305, 12, 6, 'Abrão atravessou a terra até Siquém, até o carvalho de Moré. Os cananeus estavam então naquela terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (306, 12, 7, 'O Senhor apareceu a Abrão e disse-lhe: “Darei esta terra à tua posteridade”. Abrão edificou um altar ao Senhor, que lhe tinha aparecido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (307, 12, 8, 'Em seguida, partindo dali, foi para a montanha que está ao oriente de Betel, onde levantou a sua tenda, tendo Betel ao ocidente e Hai ao oriente. Abrão edificou ali um altar ao Senhor, e invocou o seu nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (308, 12, 9, 'Continuou depois sua viagem, de acampamento em acampamento, para Negueb.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (309, 12, 10, 'Sobreveio, porém, uma fome na região; e sendo grande a miséria, Abrão desceu ao Egito para aí viver algum tempo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (310, 12, 11, 'Quando estava para entrar no Egito, disse a Sarai, sua mulher: “Escuta, sei que és uma mulher formosa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (311, 12, 12, 'Quando os egípcios te virem, dirão: ‘É sua mulher’, e me matarão, conservando-te a ti em vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (312, 12, 13, 'Dize, pois, que és minha irmã, para que eu seja poupado por causa de ti, e me conservem a vida em atenção a ti”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (313, 12, 14, 'Chegando Abrão ao Egito, os egíp­­­cios notaram que sua mulher era extremamente bela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (314, 12, 15, 'Os grandes da corte, vendo-a, elogiaram-na diante do faraó, e a mulher foi introduzida no seu palácio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (315, 12, 16, 'Por causa dela, Abrão foi bem tratado pelo faraó, e recebeu ovelhas, bois, jumentos, servos e servas, jumentas e camelos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (316, 12, 17, 'O Senhor, porém, feriu com grandes pragas o faraó e a sua casa, por causa de Sarai, mulher de Abrão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (317, 12, 18, 'O faraó mandou chamá-lo e disse-lhe: “Que me levaste a fazer? Por que não me disseste que era tua mulher?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (318, 12, 19, 'Por que disseste que ela era tua irmã, levando-me a tomá-la por esposa? Mas agora, eis tua mulher: toma-a e vai-te!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (319, 12, 20, 'Então, o faraó deu ordens aos seus para reconduzir Abrão e sua mu­lher com tudo o que lhe pertencia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (320, 13, 1, 'Abrão voltou do Egito para Ne­gueb com sua mulher e tudo o que lhe pertencia. Ló o acompanhava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (321, 13, 2, 'Abrão era muito rico em rebanhos, prata e ouro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (322, 13, 3, 'Ele foi de acampamento em acampamento de Negueb até Betel, ao lugar onde já uma vez armara sua tenda, entre Betel e Hai,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (323, 13, 4, 'no lugar onde se encontrava o altar que havia edificado antes. Ali invocou o nome do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (324, 13, 5, 'Ló, que acompanhava Abrão, pos­suía também ovelhas, bois e tendas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (325, 13, 6, 'e a região não lhes bastava para aí se estabelecerem juntos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (326, 13, 7, 'Por isso, houve uma contenda entre os pastores dos rebanhos de Abrão e os dos rebanhos de Ló. Os cananeus e os ferezeus habitavam então naquela terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (327, 13, 8, 'Abrão disse a Ló: “Rogo-te que não haja discórdia entre mim e ti, nem entre nossos pastores, pois somos irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (328, 13, 9, 'Eis aí toda a terra diante de ti; separemo-nos. Se fores para a esquerda, eu irei para a direita; se fores para a direi­ta, eu irei para a esquerda”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (329, 13, 10, 'Ló, levantando os olhos, viu que toda a planície do Jordão era regada de água (o Senhor não tinha ainda destruído Sodoma e Gomorra) como o jardim do Senhor, como a terra do Egito ao lado de Tsoar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (330, 13, 11, 'Ló escolheu toda a planície do Jordão e foi para o oriente. Foi assim que se separaram um do outro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (331, 13, 12, 'Abrão fixou-se na terra de Canaã, e Ló nas cidades da planície, onde levantou suas tendas até Sodoma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (332, 13, 13, 'Ora, os habitantes de Sodoma eram perversos e grandes pecadores diante do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (333, 13, 14, 'O Senhor disse a Abrão depois que Ló o deixou: “Levanta os olhos, e do lugar onde estás, olha para o norte e para o sul, para o oriente e para o ocidente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (334, 13, 15, 'Toda a terra que vês, eu a darei a ti e aos teus descendentes para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (335, 13, 16, 'Tornarei tua posteridade tão numerosa como o pó da terra: se alguém puder contar os grãos do pó da terra, então poderá contar a tua posteridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (336, 13, 17, 'Levanta-te, percorre a terra em toda a sua extensão, porque eu te hei de dar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (337, 13, 18, 'Abrão levantou as suas tendas e veio fixar-se no vale dos carvalhos de Mambré, que estão em Hebron; e ali edificou um altar ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (338, 14, 1, 'No tempo de Amrafel, rei de Senaar, de Arioc, rei de Elasar, de Codorlaomor, rei de Elam e de Tadal, rei de Goim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (339, 14, 2, 'aconteceu que estes reis fizeram guerra a Bara, rei de Sodoma, a Bersa, rei de Gomorra, a Senaab, rei de Adama, a Semeber, rei de Seboim e ao rei de Bala, isto é, Segor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (340, 14, 3, 'Todos estes se juntaram no vale de Sidim, que é o mar Salgado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (341, 14, 4, 'Durante doze anos eles tinham servido a Codorlaomor, mas no décimo terceiro ano tinham se revoltado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (342, 14, 5, 'No décimo quarto ano, Codorlaomor pôs-se em marcha com os reis que se tinham aliado a ele, e desbarataram os refaim em Astarot Carnaim, e igualmente os zuzim em Ham, os emim na planície de Cariataim');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (343, 14, 6, 'e os horreus, em sua montanha de Seir até El-Farã, cerca do deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (344, 14, 7, 'Voltando, chegaram à fonte do Julgamento, em Cades, e devastaram a terra dos amalecitas, assim como os amorreus que ha­bitavam em Asasontamar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (345, 14, 8, 'O rei de Sodoma, o rei de Gomorra, o rei de Adama, o rei de Seboim, o rei de Bala, isto é, Segor, saíram e puseram-se em ordem de batalha no vale de Sidim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (346, 14, 9, 'contra Codorlaomor, rei de Elam, Tadal, rei de Goim, Anrafel, rei de Senaar, e Arioc, rei de Elasar, quatro reis contra cinco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (347, 14, 10, 'Ora, havia no vale de Sidim numerosos poços de betume. E os reis de Sodoma e de Gomorra, fugindo, caíram nesses poços, enquanto o restante fugiu para a montanha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (348, 14, 11, 'Os vencedores levaram todos os bens de Sodo­ma e Gomorra, e todos os seus víveres, e partiram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (349, 14, 12, 'Levaram também Ló, filho do irmão de Abrão, que morava em Sodoma, com todos os seus bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (350, 14, 13, 'Mas alguém que conseguiu fugir veio dar parte do sucedido a Abrão, o hebreu, que vivia nos carvalhos de Mambré, o amor­reu, irmão de Escol e irmão de Aner, aliados de Abrão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (351, 14, 14, 'Abrão, tendo ouvido que Ló, seu parente, ficara prisioneiro, escolheu trezentos e dezoito dos seus melhores e mais corajosos servos, nascidos em sua casa, e foi ao alcance dos reis até Dã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (352, 14, 15, 'Ali, dividindo a sua tropa para os atacar de noite com seus servos, desbaratou-os e perseguiu-os até Hoba, que se encontra ao norte de Damasco.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (353, 14, 16, 'Abrão recobrou todos os bens saqueados e recondu­ziu também Ló, seu parente, com os seus bens, assim como as mulheres e os homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (354, 14, 17, 'Voltando Abrão da derrota de Codor­laomor e seus reis aliados, o rei de Sodo­ma saiu-lhe ao encontro no vale de Save, que é o vale do rei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (355, 14, 18, 'Melquisedec, rei de Salém e sacerdote do Deus Altíssimo, mandou trazer pão e vinho,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (356, 14, 19, 'e abençoou Abrão, dizendo: “Bendito seja Abrão pelo Deus Altíssimo, que criou o céu e a terra!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (357, 14, 20, 'Bendito seja o Deus Altíssimo, que entregou os teus inimigos em tuas mãos!”. E Abrão deu-lhe o dízimo de tudo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (358, 14, 21, 'O rei de Sodoma disse a Abrão: “Devolve-me os homens e guarda os bens”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (359, 14, 22, '“Levanto a minha mão para o Senhor Deus Altíssimo que criou o céu e a terra – respondeu Abrão –;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (360, 14, 23, 'de tudo o que é teu, não tomarei sequer um fio nem um cordão de sandália, para que não digas: Enriqueci Abrão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (361, 14, 24, 'Nada para mim, exceto somente o que comeram os meus servos e a parte dos homens que vieram comigo, Aner, Escol e Mambré; estes hão de receber a sua parte.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (362, 15, 1, 'Depois desses aconteci­men­tos, a palavra do Se­nhor foi dirigida a Abrão, numa visão, nestes termos: “Nada temas, Abrão! Eu sou o teu protetor; tua recompensa será muito grande”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (363, 15, 2, 'Abrão res­pondeu: “Senhor Javé, que me dareis vós? Eu irei sem fi­lhos, e o herdeiro de minha casa é Eliezer de Damasco”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (364, 15, 3, 'E ajuntou: “Vós não me destes posteridade, e é um escravo nascido em minha casa que será o meu herdeiro”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (365, 15, 4, 'Então a palavra do Senhor foi-lhe dirigida nestes termos: “Não é ele que será o teu herdeiro, mas aquele que vai sair de tuas entranhas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (366, 15, 5, 'E, conduzindo-o fora, disse-lhe: “Levanta os olhos para o céu e conta as estrelas, se és capaz... Pois bem – ajuntou ele – assim será a tua descendência”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (367, 15, 6, 'Abrão confiou no Senhor, e o Senhor “lhe imputou isso” “para sua justificação”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (368, 15, 7, 'E disse-lhe: “Eu sou o Senhor que te fiz sair de Ur da Caldeia para dar-te esta terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (369, 15, 8, '“Senhor Javé, como poderei saber se a vou possuí-la?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (370, 15, 9, '“Toma uma novilha de três anos – respondeu-lhe o Senhor – uma cabra de três anos, um cordeiro de três anos, uma rola e um pombinho.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (371, 15, 10, 'Abrão tomou todos esses animais, e dividiu-os pelo meio, colocando suas metades uma defronte da outra; mas não cortou as aves.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (372, 15, 11, 'Vieram as aves de rapina e atiraram-se sobre os cadáveres, mas Abrão as expulsou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (373, 15, 12, 'E eis que, ao pôr do sol, veio um profundo sono a Abrão, ao mesmo tempo que o assaltou um grande pavor, uma espessa escuridão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (374, 15, 13, 'O Senhor disse-lhe: “Sabe que teus descendentes habitarão como peregrinos numa terra que não é sua, e que nessa terra eles serão escravizados e oprimidos durante quatrocentos anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (375, 15, 14, 'Mas eu julgarei também o povo ao qual estiverem sujeitos, e sairão em seguida dessa terra com grandes riquezas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (376, 15, 15, 'Quanto a ti, irás em paz juntar-se aos teus pais, e serás sepultado numa ditosa ve­lhice.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (377, 15, 16, 'Somente à quarta geração os teus descendentes voltarão para aqui, porque a ini­quidade dos amorreus não chegou ainda ao seu cúmulo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (378, 15, 17, 'Quando o sol se pôs, formou-se uma densa escuridão, e eis que um braseiro fumegante e uma tocha ardente passaram pelo meio das carnes divididas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (379, 15, 18, 'Naquele dia, o Senhor fez aliança com Abrão: “Eu dou – disse ele – esta terra aos teus descendentes, desde a torrente do Egito até o grande rio Eufrates:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (380, 15, 19, 'a terra dos cineus, dos ceneseus, dos cadmoneus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (381, 15, 20, 'dos heteus, dos ferezeus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (382, 15, 21, 'dos amorreus, dos cananeus, dos gergeseus e dos jebuseus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (383, 16, 1, 'Sarai, mulher de Abrão, não lhe tinha dado filho; mas, possuindo uma escrava egípcia, chamada Agar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (384, 16, 2, 'disse a Abrão: “Eis que o Senhor me fez estéril; rogo-te que tomes a minha escrava, para ver se, ao menos por ela, eu posso ter filhos”. Abrão aceitou a proposta de Sarai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (385, 16, 3, 'Sarai tomou, pois, sua escrava, Agar, a egípcia, passado dez anos que Abrão ha­bitava a terra de Canaã, e deu-a por mulher a Abrão, seu marido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (386, 16, 4, 'Este aproximou-se de Agar e ela concebeu. Agar, vendo que tinha concebido, começou a desprezar a sua senhora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (387, 16, 5, 'Então Sarai disse a Abrão: “Caia sobre ti o ultraje que me é feito! Dei-te minha escrava, e ela, desde que concebeu, olha-me com desprezo. O Senhor seja juiz entre mim e ti!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (388, 16, 6, 'Abrão respondeu-lhe: “Tua escrava está em teu poder, faze dela o que quiseres”. E Sarai maltratou-a de tal forma que ela teve de fugir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (389, 16, 7, 'O anjo do Senhor, encontran­do-a no deserto junto de uma fonte que está no caminho de Sur,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (390, 16, 8, 'disse-lhe: “Agar, escrava de Sarai, donde vens? E para onde vais?”. “Eu fujo de Sarai, minha senhora” – respondeu ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (391, 16, 9, '“Volta para a tua senhora – tornou o anjo do Senhor – e humilha-te diante dela.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (392, 16, 10, 'E ajuntou: “Multiplicarei tua posteridade de tal forma, e será tão numerosa, que não se poderá contar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (393, 16, 11, 'Disse ainda mais: “Estás grávida, e vais dar à luz um filho: darás a ele o nome de Ismael, porque o Senhor te ouviu na tua aflição.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (394, 16, 12, 'Este menino será como um jumento bravo: sua mão se levantará contra todos e a mão de todos contra ele, e levantará sua tenda defronte de todos os seus irmãos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (395, 16, 13, 'Agar deu ao Senhor, que lhe tinha falado, o nome: Vós sois El-roí, “porque – dizia ela – não vi eu aqui mesmo o Deus que me via?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (396, 16, 14, 'E por isso deu-se àquele poço o nome de poço Lahai-roí; ele se encontra entre Cades e Barad.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (397, 16, 15, 'Agar deu à luz um filho a Abrão, o qual lhe pôs o nome de Ismael.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (398, 16, 16, 'Abrão tinha a idade de oitenta e seis anos quando Agar lhe deu à luz Ismael.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (399, 17, 1, 'Abrão tinha noventa e nove anos. O Senhor apareceu-lhe e disse-lhe: “Eu sou o Deus Todo-poderoso. Anda em minha presença e sê íntegro;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (400, 17, 2, 'quero fazer aliança contigo e multiplicarei ao infinito a tua descendência”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (401, 17, 3, 'Abrão prostrou-se com o rosto por terra. Deus disse-lhe:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (402, 17, 4, '“Este é o pacto que faço contigo: serás o pai de uma multidão de povos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (403, 17, 5, 'De agora em diante não te chamarás mais Abrão, e sim Abraão, porque farei de ti o pai de uma multidão de povos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (404, 17, 6, 'Tornarei a ti extremamente fecundo, farei nascer de ti nações e terás reis por descendentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (405, 17, 7, 'Faço aliança contigo e com tua posteridade, uma aliança eterna, de geração em geração, para que eu seja o teu Deus e o Deus de tua posteridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (406, 17, 8, 'Darei a ti e a teus descendentes depois de ti a terra em que moras como peregrino, toda a terra de Canaã, em possessão perpétua, e serei o teu Deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (407, 17, 9, 'Deus disse ainda a Abraão: “Tu, porém, guardarás a minha aliança, tu e tua posteridade nas gerações futuras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (408, 17, 10, 'Eis o pacto que faço entre mim e vós, e teus descendentes, e que tereis de guardar: todo homem, entre vós, será circuncidado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (409, 17, 11, 'Cortareis a carne de vosso prepúcio, e isso será o sinal da aliança entre mim e vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (410, 17, 12, 'Todo homem, no oitavo dia do seu nascimento, será circuncidado entre vós nas gerações futuras, tanto o que nascer em casa, como o que comprardes a preço de dinheiro de um estrangeiro qualquer, e que não for de tua raça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (411, 17, 13, 'Serão circuncidados tanto o homem nascido na casa como aquele que for comprado a preço de dinheiro. Assim será marcado em vossa carne o sinal de minha aliança perpétua.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (412, 17, 14, 'O varão incir­cunciso, do qual não se tenha cortado a carne do prepúcio, será exterminado de seu povo por ter violado minha aliança”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (413, 17, 15, 'Disse Deus a Abraão: “Não chamarás mais tua mulher Sarai, e sim Sara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (414, 17, 16, 'Eu a abençoarei, e dela te darei um filho. Eu a abençoarei, e ela será a mãe de nações e dela sairão reis”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (415, 17, 17, 'Abraão prostrou-se com o rosto por terra, e começou a rir, dizendo consigo: “Poderia nascer um filho a um homem de cem anos? Seria possível a Sara conceber ainda na idade de noventa anos?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (416, 17, 18, 'E disse a Deus: “Oxalá que Ismael viva diante de vossa face!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (417, 17, 19, 'Mas Deus respondeu-lhe: “Não, é Sara, tua mulher que dará à luz um filho, ao qual chamarás Isaac. Farei aliança com ele, uma aliança que será perpétua para sua posteridade depois dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (418, 17, 20, 'Eu te ouvirei também acerca de Ismael. Eu o abençoarei, o tornarei fecundo e multiplicarei extraordinariamente sua descendência: ele será o pai de doze príncipes, e farei sair dele uma grande nação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (419, 17, 21, 'Mas minha aliança eu a farei com Isaac, que Sara te dará à luz dentro de um ano, nesta mesma época”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (420, 17, 22, 'Tendo acabado de falar com ele, retirou-se Deus de Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (421, 17, 23, 'Abraão tomou então Ismael, seu filho, assim como todos os homens nascidos em sua casa e todos aqueles que tinha comprado a preço de dinheiro, tudo o que havia de varões em sua casa, e circuncidou-os no mesmo dia, como Deus lhe tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (422, 17, 24, 'Abraão tinha noventa e nove anos quando foi circuncidado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (423, 17, 25, 'Ismael, seu filho, tinha treze anos quando o foi igualmente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (424, 17, 26, 'Abraão e Ismael, seu filho, foram circuncidados no mesmo dia;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (425, 17, 27, 'e todos os homens de sua casa, nascidos em sua casa ou comprados a preço de dinheiro a estrangeiros, foram circuncidados ao mesmo tempo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (426, 18, 1, 'O Senhor apareceu a Abraão nos carvalhos de Mambré, quando ele estava assentado à entrada de sua tenda, no maior calor do dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (427, 18, 2, 'Abraão levantou os olhos e viu três homens de pé diante dele. Levantou-se no mesmo instante da entrada de sua tenda, veio-lhes ao encontro e prostrou-se por terra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (428, 18, 3, '“Meu Senhor – disse ele – se encontrei graça diante de vossos olhos, não passeis avante sem vos deterdes em casa de vosso servo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (429, 18, 4, 'Vou buscar um pouco de água para vos lavar os pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (430, 18, 5, 'Descansai um pouco sob esta árvore. Eu vos trarei um pouco de pão, e assim restaurareis as vossas forças para prosseguirdes o vosso caminho; porque é para isso que passastes perto de vosso servo.” Eles responderam: “Faze como disseste”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (431, 18, 6, 'Abraão foi depressa à tenda de Sara: “Depressa – disse ele – amassa três medidas de farinha e coze pães”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (432, 18, 7, 'Correu em seguida ao rebanho, escolheu um novilho tenro e bom, e deu-o a um criado que o preparou logo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (433, 18, 8, 'Tomou manteiga e leite e serviu aos peregrinos juntamente com o novilho preparado, conservando-se de pé junto deles, sob a árvore, enquanto comiam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (434, 18, 9, 'E disseram-lhe: “Onde está Sara, tua mulher?”. “Ela está na tenda” – respondeu ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (435, 18, 10, 'E ele disse-lhe: “Voltarei à tua casa dentro de um ano, a esta época; e Sara, tua mulher, terá um filho”. Ora, Sara ouvia por detrás, à entrada da tenda.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (436, 18, 11, '(Abraão e Sara eram velhos, de idade avançada, e Sara tinha já passado da idade.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (437, 18, 12, 'Ela pôs-se a rir secretamente: “Velha como sou – disse ela consigo – conhecerei ainda o amor? E o meu senhor também é já entrado em anos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (438, 18, 13, 'O Senhor disse a Abraão: “Por que se riu Sara, dizendo: ‘Será verdade que eu teria um filho, velha como sou?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (439, 18, 14, 'Será isso porventura uma coisa muito difícil para o Senhor? Em um ano, a esta época, voltarei à tua casa e Sara terá um filho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (440, 18, 15, 'Sara protestou: “Eu não ri” – disse ela – pois tinha medo. Mas o Senhor disse-lhe: “Sim, tu riste”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (441, 18, 16, 'Os homens levantaram-se e partiram em direção de Sodoma, e Abraão os ia acompanhando.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (442, 18, 17, 'O Senhor disse então: “Acaso poderei ocultar a Abraão o que vou fazer?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (443, 18, 18, 'Pois que Abraão deve tornar-se uma nação grande e poderosa, e todos os povos da terra serão benditos nele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (444, 18, 19, 'Eu o escolhi para que ele ordene aos seus filhos e à sua casa depois dele, que guardem os caminhos do Senhor, praticando a justiça e a retidão, para que o Senhor cumpra em seu favor as promessas que lhe fez”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (445, 18, 20, 'O Senhor ajuntou: “É imenso o clamor que se eleva de Sodoma e Gomorra, e o seu pecado é muito grande.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (446, 18, 21, 'Eu vou descer para ver se as suas obras correspondem realmente ao clamor que chega até mim; se assim não for, eu o saberei”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (447, 18, 22, 'Os homens partiram, pois, na direção de Sodoma, enquanto Abraão ficou em presença do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (448, 18, 23, 'Abraão aproximou-se e disse: “Fareis o justo perecer com o ímpio?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (449, 18, 24, 'Talvez haja cinquenta justos na cidade: os farão perecer? Não perdoaríeis antes a cidade, em atenção aos cinquenta justos que nela se poderiam encontrar?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (450, 18, 25, 'Não, vós não poderíeis agir assim, matando o justo com o ímpio, e tratando o justo como o ímpio! Longe de vós tal pensamento! Não exerceria o juiz de toda a terra a justiça?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (451, 18, 26, 'O Senhor disse: “Se eu encontrar em Sodoma cinquenta justos, perdoarei a toda a cidade em atenção a eles”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (452, 18, 27, 'Abraão continuou: “Não leveis a mal, se ainda ouso falar ao meu Senhor, embora seja eu pó e cinza.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (453, 18, 28, 'Se, porventura, faltarem cinco aos cinquenta justos, fareis perecer toda a cidade por causa desses cinco?”. “Não a destruirei – respondeu o Senhor – se nela eu encontrar quarenta e cinco justos.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (454, 18, 29, 'Abraão insistiu ainda e disse: “Talvez só haja aí quarenta”. “Não destruirei a cidade por causa desses quarenta.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (455, 18, 30, 'Abraão disse de novo: “Rogo-vos, Senhor, que não vos irriteis se eu insisto ainda! Talvez só se encontrem trinta!”. “Se eu encontrar trinta – disse o Senhor – não o farei.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (456, 18, 31, 'Abraão continuou: “Desculpai, se ouso ainda falar ao Senhor: pode ser que só se encontre vinte”. “Em atenção aos vinte, não a destruirei.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (457, 18, 32, 'Abraão re­plicou: “Que o Senhor não se irrite se falo ainda uma última vez! Que será, se lá forem achados dez?”. E Deus respondeu: “Não a destruirei por causa desses dez”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (458, 18, 33, 'E o Senhor retirou-se, depois de ter falado com Abraão, e este voltou para sua casa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (459, 19, 1, 'Pela tarde chegaram os dois anjos a Sodoma. Ló, que estava assentado à porta da cidade, ao vê-los, levantou-se e foi-lhes ao encontro e prostrou-se com o rosto por terra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (460, 19, 2, '“Meus senhores – disse-lhes ele – vinde, peço-vos, para a casa de vosso servo, e passai nela a noite; lavareis os pés, e amanhã cedo continuareis vosso caminho.” “Não – res­ponderam eles – passaremos a noite na praça.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (461, 19, 3, 'Mas Ló insistiu tanto com eles que concordaram e entraram em sua casa. Ló preparou-lhes um banquete, mandou cozer pães sem fermento e eles comeram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (462, 19, 4, 'Mas, antes que se tivessem deitado, eis que os homens da cidade, os homens de Sodoma, se agruparam em torno da casa, desde os jovens até os velhos, toda a população.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (463, 19, 5, 'E chamaram Ló: “Onde estão – disseram-lhe – os homens que entraram esta noite em tua casa? Conduze-os a nós para que os conheçamos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (464, 19, 6, 'Saiu Ló a ter com eles no limiar da casa, fechou a porta atrás de si');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (465, 19, 7, 'e disse-lhes: “Suplico-vos, meus irmãos, não cometais este crime.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (466, 19, 8, 'Ouvi: tenho duas filhas que são ainda virgens, eu vo-las trarei, e fazei delas o que quiserdes. Mas não façais nada a estes homens, porque se aco­lheram à sombra de meu teto”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (467, 19, 9, 'Eles responderam: “Retira-te daí! – e acrescentaram: Eis um indivíduo que não passa de um estrangeiro no meio de nós e se arvora em juiz! Pois bem, verás como te havemos de tratar pior do que a eles”. E, empurrando Ló com violência, avançaram para quebrar a porta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (468, 19, 10, 'Mas os dois (viajantes) estenderam a mão e, tomando Ló para dentro de casa, fecharam de novo a porta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (469, 19, 11, 'E feriram de cegueira os homens que estavam fora, jovens e velhos, que se esforçavam em vão por reen­contrar a porta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (470, 19, 12, 'Os dois homens disseram a Ló: “Tens ainda aqui alguns dos teus? Genros, ou filhos, ou filhas, todos os que são teus parentes na cidade, faze-os sair deste lugar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (471, 19, 13, 'porque vamos destruir este lugar, visto que o clamor que se eleva dos seus habitantes é enorme dian­te do Senhor, o qual nos enviou para exterminá-los”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (472, 19, 14, 'Saiu Ló, pois, para falar a seus genros, que tinham desposado suas filhas: “Levantai-vos – disse-lhes – saí daqui, porque o Senhor vai destruir a cidade”. Mas seus genros julgaram que ele gracejava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (473, 19, 15, 'Ao amanhe­cer, os anjos instavam com Ló, dizendo: “Levanta-te, toma tua mulher e tuas duas filhas que estão em tua casa, para que não pereças também no castigo da cidade”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (474, 19, 16, 'E, como ele demorasse, aqueles homens tomaram pela mão a ele, a sua mulher e as suas duas filhas, porque o Senhor queria salvá-los, e o levaram para fora da cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (475, 19, 17, 'Quando já estavam fora, um dos anjos disse-lhe: “Salva-te, se queres conservar a tua vida. Não olhes para trás, e não te detenhas em parte alguma da planície; mas foge para a montanha senão perecerás”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (476, 19, 18, 'Ló disse-lhes: “Oh, não, Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (477, 19, 19, 'Já que vosso servo encontrou graça diante de vós, e usastes comigo de grande bondade, conservando-me a vida, vede, eu não posso me salvar na montanha, porque o flagelo me atingiria antes, e eu morreria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (478, 19, 20, 'Eis uma cidade bem perto onde posso abrigar-me. É uma cidade pequena e eu poderei refugiar-me nela. Permiti que o faça – ela é pequena – e terei a vida salva”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (479, 19, 21, 'Ele disse-lhe: “Concedo-te ainda esta graça: não destruirei a cidade a favor da qual me pedes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (480, 19, 22, 'Apressa-te e refugia-te lá porque nada posso fazer antes que lá tenhas chegado”. Por isso, puseram àquela cidade o nome de Segor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (481, 19, 23, 'O sol levantava-se sobre a terra quando Ló entrou em Segor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (482, 19, 24, 'O Senhor fez então cair sobre Sodoma e Gomorra uma chuva de enxofre e de fogo, vinda do Senhor, do céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (483, 19, 25, 'E destruiu essas cidades e toda a planície, assim como todos os habitantes das cidades e a vegetação do solo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (484, 19, 26, 'A mulher de Ló, tendo olhado para trás, transformou-se numa estátua de sal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (485, 19, 27, 'Abraão levantou-se muito cedo e foi ao lugar onde tinha estado antes com o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (486, 19, 28, 'Voltando os olhos para o lado de Sodoma e Gomorra e sobre toda a extensão da planície, viu subir da terra um fumo espesso como a fumaça de uma grande fornalha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (487, 19, 29, 'Quando Deus destruiu as cidades da planície, lembrou-se de Abraão e livrou Ló do flagelo com que destruiu as cidades onde ele habitava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (488, 19, 30, 'Ló partiu de Segor e veio estabele­cer-se na montanha com suas duas filhas, pois temia ficar em Segor. E habitava numa caverna com suas duas filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (489, 19, 31, 'A mais velha disse à mais nova: “Nosso pai está velho, e não há homem algum na região com quem nos possamos unir, segundo o costume universal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (490, 19, 32, 'Vem, embriaguemos nosso pai e durmamos com ele, para que possamos nos assegurar uma posteridade”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (491, 19, 33, 'Elas fizeram, pois, o seu pai beber vinho naquela noite. Então, a mais velha entrou e dormiu com ele; ele, porém, nada notou, nem quando ela se aproximou dele, nem quando se levantou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (492, 19, 34, 'No dia seguinte, disse ela à sua irmã mais nova: “Dormi ontem com meu pai, façamo-lo beber vinho ainda uma vez, esta noite, e dormirás com ele para nos assegurarmos uma posteridade”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (493, 19, 35, 'Também naquela noite embriagaram seu pai, e a mais nova dormiu com ele, sem que ele o percebesse, nem quando ela se aproximou, nem quando se levantou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (494, 19, 36, 'Assim, as duas filhas de Ló conceberam de seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (495, 19, 37, 'A mais velha deu à luz um filho, ao qual pôs o nome de Moab: este é o pai dos moabitas, que vivem ainda hoje.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (496, 19, 38, 'A mais nova teve também um filho, ao qual chamou Ben-Ami: este é o pai dos amonitas, que vivem ainda hoje.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (497, 20, 1, 'Abraão partiu dali para a região do Negueb. Estabeleceu-se entre Cades e Sur, e viveu algum tempo em Gerara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (498, 20, 2, 'Ele dizia de Sara, sua mulher, que ela era sua irmã. Abimelec, rei de Gera­ra, arrebatou-lha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (499, 20, 3, 'Mas Deus apareceu em sonhos a Abimelec e disse-lhe: ‘‘Vais morrer, por causa da mulher que roubaste, porque é casada”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (500, 20, 4, 'Abimelec, que não a tinha tocado, disse: ‘‘Senhor, fareis perecer mesmo inocentes?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (501, 20, 5, 'Não me disse ele que ela era sua irmã? E ela mesma me disse: É meu irmão. É na simplicidade de meu coração e com as mãos puras que fiz isso’’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (502, 20, 6, 'Deus disse-lhe em sonhos: ‘‘Sei que é na simplicidade do teu coração que agiste assim; por isso, preservei-te de pecar contra mim, e não deixei que a tocasses.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (503, 20, 7, 'Devolve agora a mulher deste homem, que é profeta, e ele rogará por ti para que conserves a vida. Mas, se não a devolveres, sabes que morrerás seguramente, tu e todos os teus’’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (504, 20, 8, 'Ao romper da manhã, Abime­lec convocou todos os seus servos e referiu-lhes essas coisas. Todos ficaram muito atemorizados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (505, 20, 9, 'Depois, Abimelec chamou Abraão e disse-lhe: ‘‘Que nos fizeste? Em que te ofendi para que nos expusesses, a mim e ao meu reino, ao castigo de um tão grande pecado. Fizeste-me o que não devias fazer’’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (506, 20, 10, 'E ajuntou: ‘‘Que tiveste em vista agindo assim?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (507, 20, 11, 'Abraão respondeu: ‘‘Eu pensava que não havia certamente nenhum temor a Deus nesta terra, e que me matariam por causa de minha mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (508, 20, 12, 'Aliás, ela é realmente minha irmã, filha de meu pai, mas não de minha mãe; ela tornou-se minha mulher.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (509, 20, 13, 'Quando Deus me tirou da casa de meu pai, eu lhe disse: Faze-me esta graça: onde quer que formos, dirás de mim que sou teu irmão’’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (510, 20, 14, 'Tomou então Abimelec ovelhas, bois, servos e servas, e deu-os a Abraão, ao mesmo tempo que lhe devolvia Sara, sua mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (511, 20, 15, 'E disse-lhe: ‘‘Minha terra está à tua disposição: fixa-te onde quiseres”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (512, 20, 16, 'Disse também a Sara: ‘‘Dou a teu irmão mil moedas de prata: isto te será um véu sobre os olhos para todos aqueles que estão contigo; eis-te justificada’’.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (513, 20, 17, 'Abraão intercedeu junto de Deus, que curou Abimelec, sua mulher e suas servas, e deram novamente à luz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (514, 20, 18, 'Porque o Senhor tinha ferido de esterilidade todas as mulheres da casa de Abimelec, por causa de Sara, mulher de Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (515, 21, 1, 'O Senhor visitou Sara, como ele tinha dito, e cumpriu em seu favor o que havia prometido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (516, 21, 2, 'Sara concebeu e, apesar de sua velhice, deu à luz um filho a Abraão, no tempo fixado por Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (517, 21, 3, 'Abraão pôs o nome de Isaac ao filho que lhe nascera de Sara.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (518, 21, 4, 'E, passados oito dias do seu nascimento, circuncidou-o, como Deus lhe tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (519, 21, 5, 'Abraão tinha cem anos, quando nasceu o seu filho Isaac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (520, 21, 6, 'Sara disse: “Deus deu-me algo de que rir; e todos aqueles que o souberem se rirão de mim”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (521, 21, 7, 'E ajuntou: “Quem teria previsto que Sara amamentaria filhos a Abraão? Porque eu lhe dei um filho em sua velhice”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (522, 21, 8, 'O menino cresceu e foi desmamado. No dia em que foi desmamado, Abraão fez uma grande festa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (523, 21, 9, 'Sara viu que o filho nascido a Abraão de Agar, a egípcia, zombava de seu filho Isaac,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (524, 21, 10, 'e disse a Abraão: “Expulsa esta escrava com o seu filho, porque o filho desta escrava não será herdeiro com meu filho Isaac”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (525, 21, 11, 'Isso desagradou muitíssimo a Abraão, por causa de seu filho Ismael.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (526, 21, 12, 'Mas Deus disse-lhe: “Não te preocupes com o menino e com a tua escrava. Faze tudo o que Sara te pedir, pois é de Isaac que nascerá a posteridade que terá o teu nome.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (527, 21, 13, 'Mas do filho da escrava também farei um grande povo, por ser de tua raça”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (528, 21, 14, 'No dia seguinte, pela manhã, Abraão tomou pão e um odre de água, e deu-os a Agar, colocando-os às suas costas, e despediu-a com seu filho. Ela partiu, errando pelo deserto de Bersabeia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (529, 21, 15, 'Acabada a água do odre, deixou o menino sob um arbusto,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (530, 21, 16, 'e foi assentar-se em frente, à distância de um tiro de flecha, “porque – dizia ela – não quero ver morrer o menino”. Ela assentou-se, pois, em frente e pôs-se a chorar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (531, 21, 17, 'Deus ouviu a voz do menino, e o anjo de Deus chamou Agar, do céu, dizendo-lhe: “Que tens, Agar? Nada temas, porque Deus ouviu a voz do menino do lugar onde está.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (532, 21, 18, 'Levanta-te, toma o menino e segura-o pela mão, porque farei dele uma grande nação”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (533, 21, 19, 'Deus abriu-lhe os olhos, e ela viu um poço, onde foi encher o odre, e deu de beber ao menino.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (534, 21, 20, 'Deus esteve com este menino. Ele cresceu, habitou no deserto e tornou-se um hábil flecheiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (535, 21, 21, 'E habitou no deserto de Farã, e sua mãe tomou para ele uma mulher egípcia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (536, 21, 22, 'Por aquele tempo, Abimelec, acompanhado de Ficol, general do seu exército, disse a Abraão: “Deus está contigo em tudo o que fazes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (537, 21, 23, 'Jura-me, pois, pelo nome de Deus, que não me enganarás, nem a mim, nem a meus filhos, nem aos meus descendentes, mas que usarás para comigo e com a terra onde habitas da mesma benevolência que eu te tenho testemunhado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (538, 21, 24, '“Eu juro” – respondeu Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (539, 21, 25, 'Mas Abraão queixou-se a Abime­lec por causa de um poço que os seus homens lhe tinham tirado à força.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (540, 21, 26, '“Ignoro quem tenha feito isto – respondeu Abimelec –; tu mesmo nunca me disseste nada a esse respeito, e só hoje estou ouvindo falar disso.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (541, 21, 27, 'Abraão tomou então ovelhas e bois e deu-os a Abimelec, e fizeram aliança entre si.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (542, 21, 28, 'Abraão pôs à parte sete jovens ovelhas do rebanho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (543, 21, 29, '“Que significam – disse-lhe o rei – estas sete ovelhinhas que puseste à parte?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (544, 21, 30, '“Aceitarás de minhas mãos estas sete ovelhinhas – respondeu Abraão – como testemunho de que eu cavei este poço.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (545, 21, 31, 'Por isso, deu-se àquele lugar o nome de Bersabeia; porque ambos ali tinham jurado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (546, 21, 32, 'Foi assim que fize­ram aliança em Bersabeia. Depois disso, voltou Abimelec para a terra dos filisteus com Ficol, general do seu exército.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (547, 21, 33, 'Abraão plantou uma tamareira em Bersabeia e invocou ali o nome do Senhor, Deus da eternidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (548, 21, 34, 'Abraão habitou muito tempo na terra dos filisteus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (549, 22, 1, 'Depois disso, Deus provou Abraão, e disse-lhe: “Abraão!”. “Eis-me aqui” – respondeu ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (550, 22, 2, 'Deus disse: “Toma teu filho, teu único filho a quem tanto amas, Isaac; e vai à terra de Moriá, onde tu o oferecerás em holocausto sobre um dos montes que eu te indicar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (551, 22, 3, 'No dia seguinte, pela manhã, Abraão selou o seu jumento. Tomou dois servos e Isaac, seu filho, e, tendo cortado a lenha para o holocausto, partiu para o lugar que Deus lhe tinha indicado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (552, 22, 4, 'Ao terceiro dia, levantando os olhos,­ viu o lugar de longe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (553, 22, 5, '“Ficai aqui com o jumento – disse ele aos seus servos –. Eu e o menino vamos até lá mais adiante para adorar, e depois voltaremos a vós.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (554, 22, 6, 'Abraão tomou a lenha do holocausto e a pôs aos ombros de seu filho Isaac, levando ele mesmo nas mãos o fogo e a faca. E, enquanto os dois iam cami­nhando juntos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (555, 22, 7, 'Isaac disse ao seu pai: “Meu pai!”. “Que há, meu filho?” Isaac continuou: “Temos aqui o fogo e a lenha, mas onde está a ovelha para o holocausto?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (556, 22, 8, '“Deus, respondeu-lhe Abraão, providenciará ele mesmo uma ovelha para o holocausto, meu filho.” E ambos, juntos, continua­ram o seu caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (557, 22, 9, 'Quando chegaram ao lugar indicado por Deus, Abraão edificou um altar; colocou nele a lenha, e amarrou Isaac, seu fi­lho, e o pôs sobre o altar em cima da lenha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (558, 22, 10, 'Depois, estendendo a mão, tomou a faca para imolar o seu filho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (559, 22, 11, 'O anjo do Senhor, porém, gritou-lhe do céu: “Abraão! Abraão!”. “Eis-me aqui!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (560, 22, 12, '“Não estendas a tua mão contra o menino, e não lhe faças nada. Agora sei que temes a Deus, pois não me recusaste teu próprio filho, teu filho único.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (561, 22, 13, 'Abraão, levantando os olhos, viu atrás dele um cordeiro preso pelos chifres entre os espinhos; e, tomando-o, ofereceu-o em holocausto em lugar de seu filho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (562, 22, 14, 'Abraão chamou a este lugar “O Senhor provará”, de onde se diz até o dia de hoje: “Sobre o monte de “O Senhor provará”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (563, 22, 15, 'Pela segunda vez chamou o anjo do Senhor a Abraão, do céu,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (564, 22, 16, 'e disse-lhe: “Juro por mim mesmo, diz o Senhor: pois que fizeste isto, e não me recusaste teu filho, teu filho único, eu te abençoarei.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (565, 22, 17, 'Multiplicarei a tua posteridade como as estrelas do céu, e como a areia na praia do mar. Ela possuirá a porta dos teus inimigos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (566, 22, 18, 'e todas as nações da terra desejarão ser bendita como ela, porque obedeceste à minha voz”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (567, 22, 19, 'Abraão voltou então para os seus servos, e foram juntos para Bersabeia, onde fixou sua residência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (568, 22, 20, 'Depois desses acontecimentos, vieram dizer a Abraão: “Melca deu também filhos a Nacor, teu irmão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (569, 22, 21, 'Hus, o primogênito, Buz, seu irmão, Camuel, pai de Aram,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (570, 22, 22, 'Cased, Azau, Feldas, Jedlaf e Batuel”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (571, 22, 23, '(Batuel foi o pai de Rebeca.) Estes são os oito filhos que Melca deu a Nacor, irmão de Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (572, 22, 24, 'Sua concubina, chamada Reuma, teve também filhos: Tabé, Gaam, Taás e Maaca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (573, 23, 1, 'Sara viveu cento e vinte e sete anos: essa foi a duração de sua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (574, 23, 2, 'Ela morreu em Cariat-Arbe, hoje Hebron, na terra de Canaã. Abraão veio para prantear e chorar por ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (575, 23, 3, 'Abraão, tendo-se retirado de junto da falecida, falou aos filhos de Het, dizendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (576, 23, 4, '“Sou no meio de vós um simples hóspede e estrangeiro; concedei-me, não obstante, a propriedade de uma sepultura na vossa terra, para que eu possa sepul­tar minha esposa que morreu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (577, 23, 5, 'Os fi­lhos de Het responderam a Abraão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (578, 23, 6, '“Ouve-nos, meu Senhor! Tu és um príncipe de Deus no meio de nós. Sepulta tua falecida no mais belo de nossos sepulcros. Ninguém de nós te recusará o seu túmulo para aí sepultar tua defunta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (579, 23, 7, 'Então Abraão levantou-se e, prostrando-se diante do povo daquela terra, os filhos de Het,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (580, 23, 8, 'disse-lhes: “Se me permitis trazer minha defunta e enterrá-la, ouvi-me: Intercedei por mim junto de Efron, filho de Seor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (581, 23, 9, 'para que ele me ceda a caverna de Macpela que lhe pertence, e que se encontra na extremidade de sua terra. Que ele me ceda em vossa presença, por seu justo valor, a fim de que eu me torne o proprietário dessa sepultura”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (582, 23, 10, 'Ora, Efron achava-se assentado no meio dos filhos de Het. Efron, o hiteu, respondeu a Abraão em presença dos filhos de Het e de todos os que entravam pela porta da cidade:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (583, 23, 11, '“De forma alguma, meu Senhor, será assim, mas ouve-me: dou-te a terra, juntamente com a caverna que nela se encontra; e dou-te essa terra em presença dos filhos do meu povo: enterra tua defunta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (584, 23, 12, 'Abraão prostrou-se diante do povo daquela terra');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (585, 23, 13, 'e, dirigindo-se a Efron, diante de todos, disse: “Rogo-te que me ouças: eu te dou o preço do campo; aceita-o de minhas mãos, e assim enterrarei nele minha defunta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (586, 23, 14, 'Efron respondeu a Abraão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (587, 23, 15, '“Ouve-me, meu Senhor: uma terra no valor de quatrocentos siclos de prata, entre ti e mim, o que é isto? Sepulta tua defunta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (588, 23, 16, 'Abraão aceitou as condições de Efron, e pesou o dinheiro que ele tinha pedido na presença dos filhos de Het, isto é, quatrocentos siclos de prata em moeda corrente no comércio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (589, 23, 17, 'A terra de Efron, situada em Macpela, defronte de Mambré, a terra na qual se encontra a caverna, e todas as árvores que crescem ao redor nos limites desta terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (590, 23, 18, 'tornaram-se assim propriedade de Abraão, em presença dos filhos de Het e de todos aqueles que entravam pela porta da cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (591, 23, 19, 'E Abraão sepultou Sara, sua mulher, na caverna de Macpela, defronte de Mambré, hoje Hebron, na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (592, 23, 20, 'A terra e a caverna que nela se encontra passaram, pois, dos filhos de Het para propriedade de Abraão, a título de lugar de sepultura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (593, 24, 1, 'O velho Abraão estava avançado em idade, e o Se­nhor o tinha abençoado em todas as coisas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (594, 24, 2, 'Abraão disse ao servo mais antigo de sua casa, que administrava todos os seus bens: “Põe tua mão debaixo de minha coxa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (595, 24, 3, 'Quero que jures pelo Senhor, Deus do céu e da terra, que não esco­lherás para mulher de meu filho nenhuma das filhas dos cananeus, no meio dos quais habito;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (596, 24, 4, 'mas irás à minha terra, à minha parentela, e lá escolherás uma mulher para o meu filho Isaac”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (597, 24, 5, 'O servo respondeu: “Talvez essa mulher não me quererá seguir a esta terra; nesse caso, poderei reconduzir o teu filho à terra de onde saíste?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (598, 24, 6, '“Guarda-te bem – disse-lhe Abraão – de reconduzir para lá o meu filho!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (599, 24, 7, 'O Senhor, Deus do céu, que me tirou da casa de meu pai e de minha pátria, que me disse e me jurou dar esta terra à minha posteridade, este Senhor mandará o seu anjo diante de ti, e tu escolherás lá uma mulher para o meu filho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (600, 24, 8, 'Mas, se ela não te quiser seguir, estarás desobrigado do juramento que te impus. Somente não reconduzas (de forma alguma) para lá o meu filho.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (601, 24, 9, 'Pôs, então, o servo sua mão debaixo da coxa de Abraão, seu senhor, e fez-lhe o juramento que ele pedia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (602, 24, 10, 'E, tendo tomado dez camelos do rebanho de seu senhor, partiu, levando as mãos cheias das riquezas de Abraão. E pôs-se a caminho, andando para a Mesopotâmia, para a cidade de Nacor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (603, 24, 11, 'E fez descansar os camelos fora da cidade, perto de um poço. Era pela tarde, à hora em que saíam as mulheres para ir buscar água.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (604, 24, 12, '“Senhor – disse ele –, Deus de Abraão, meu senhor, fazei-me encontrar hoje o que desejo, e manifestar vossa bondade para com meu senhor Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (605, 24, 13, 'Eis-me aqui, de pé, junto desta fonte onde as filhas dos habitantes da cidade virão buscar água.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (606, 24, 14, 'Portanto, a donzela a quem eu disser: ‘Inclina o teu cântaro, por favor, para que eu beba’ –, e me responder: ‘Bebe, e darei de beber também aos teus camelos’ –, essa seja a que destina ao vosso servo Isaac. Assim saberei que manifestais vossa bondade para com meu senhor.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (607, 24, 15, 'Ainda não tinha acabado de falar, quando sobreveio, com um cântaro aos ombros, Rebeca, filha de Batuel, filho de Melca, mulher de Nacor, irmão de Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (608, 24, 16, 'A jovem era extremamente bela, virgem, e homem algum a havia possuído. Ela desceu à fonte, encheu o seu cântaro e ia voltando.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (609, 24, 17, 'O servo correu-lhe ao encontro e disse-lhe: “Queres dar-me de beber um pouco da água de teu cântaro?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (610, 24, 18, '“Bebe, meu senhor” – respondeu ela. E prontamente inclinou o cântaro sobre o seu braço para lhe dar de beber.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (611, 24, 19, 'Tendo ele bebido, ela disse: “Vou buscar água também para os teus camelos, para que todos bebam”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (612, 24, 20, 'E, despejando seu cântaro no bebedouro, correu a buscar água de novo na fonte para todos os camelos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (613, 24, 21, 'O homem contemplava em silêncio, curioso por saber se o Senhor tinha ou não tornado feliz a sua viagem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (614, 24, 22, 'Quando os camelos acabaram de beber, o homem tirou um anel de ouro pesando meio siclo e dois braceletes de ouro pesando dez siclos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (615, 24, 23, 'E disse à jovem: “Dize-me, por favor: De quem és filha? Haveria na casa de teu pai um lugar para passarmos a noite?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (616, 24, 24, '“Eu sou filha de Batuel – respondeu ela – o filho de Melca, que ela deu à luz a Nacor.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (617, 24, 25, 'E ajuntou: “Há em nossa casa palha e forragem em abundância, e também lugar para passar a noite”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (618, 24, 26, 'Inclinou-se, então, o homem e prostrou-se diante do Senhor:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (619, 24, 27, '“Bendito seja – exclamou ele – o Senhor, o Deus de Abraão, meu Senhor, que não faltou com sua bondade e sua fidelidade para com ele. O Senhor conduziu-me diretamente à casa dos parentes de meu se­nhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (620, 24, 28, 'A jovem foi correndo contar à sua mãe tudo o que se tinha passado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (621, 24, 29, 'Rebeca tinha um irmão chamado Labão. Este apressou-se em ir ao encontro do homem que se encontrava junto da fonte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (622, 24, 30, 'Ele tinha visto o anel e os braceletes nas mãos de sua irmã, e ouvido a narração de sua irmã Rebeca: “Esse homem me disse isso e aquilo”. Foi ele, pois, ao encontro do estrangeiro e o achou perto dos camelos, na fonte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (623, 24, 31, '“Vem, bendito do Senhor – disse ele – por que permaneces aí fora? Preparei a casa e um lugar para os camelos.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (624, 24, 32, 'E o homem entrou na casa. Descarregaram os camelos, deram-lhes palha e forragem, enquanto traziam ao estrangeiro e aos seus companheiros água para lavar os pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (625, 24, 33, 'Foram-lhes servido em seguida comida; mas ele disse: “Não comerei nada enquanto não expuser o que tenho a dizer” – “Fala” – disse Labão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (626, 24, 34, '“Eu sou – disse ele – escravo de Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (627, 24, 35, 'O Senhor encheu de bênçãos o meu senhor, que se tornou poderoso; e deu-lhe ovelhas e bois, prata e ouro, servos e servas, camelos e jumentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (628, 24, 36, 'Sara, mu­lher de meu senhor, apesar de sua velhice, deu-lhe à luz um filho, ao qual ele deu todos os seus bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (629, 24, 37, 'Então o meu senhor fez-me jurar que eu não escolheria para o seu filho uma mulher entre as filhas dos cananeus, em cuja terra ele mora,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (630, 24, 38, 'mas que viria à casa de seu pai, à sua família, para aí escolher uma mulher para o seu filho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (631, 24, 39, 'E eu disse-lhe: ‘Talvez a mulher não me quererá seguir’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (632, 24, 40, '‘O Senhor – res­pondeu-me ele – em cujo caminho sempre andei, mandará o seu anjo contigo e fará bem-sucedida a tua viagem: escolherás para o meu filho uma mulher de minha família, na casa de meu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (633, 24, 41, 'Mas serás desobrigado do juramento que me fazes, se, tendo visitado minha pa­rentela, encontrares oposição e não fores recebido’.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (634, 24, 42, 'Ora, chegando hoje à fonte, eu disse: Senhor, Deus de meu senhor Abraão, se vos dignardes tornar bem-sucedida a viagem que empreendi concedei-me isto:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (635, 24, 43, 'Ficarei perto da fonte; a jovem que vier buscar água, e a quem eu disser: por favor, dá-me de beber um pouco da água de teu cântaro,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (636, 24, 44, 'e que me responder: ‘Bebe, e buscarei também água para os teus camelos’, essa será a mulher que o Senhor destina ao filho de meu senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (637, 24, 45, 'Eu não tinha ainda acabado de falar comigo mesmo, quando veio Rebeca com o seu cântaro aos ombros, e desceu à fonte para buscar água. Eu disse-lhe: ‘Dá-me de beber, por favor’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (638, 24, 46, 'E, descendo logo o cântaro dos seus ombros, ela me disse: ‘Bebe, e darei também de beber aos camelos’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (639, 24, 47, 'Perguntei-lhe então de quem era filha. Ela respondeu-me: ‘Sou filha de Batuel, filho de Nacor, que Melca lhe deu à luz’. Eu, pois, coloquei o anel em suas narinas e os braceletes em seus punhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (640, 24, 48, 'Inclinei-me, então, prostrando-me diante do Senhor, e bendisse o Senhor, o Deus de meu senhor Abraão, que me conduziu diretamente ao lugar onde eu podia tomar a filha do parente de meu senhor para o seu filho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (641, 24, 49, 'Agora, se quiserdes testemunhar afeição e fidelidade ao meu senhor, dizei-mo; senão, dizei-me também, para que eu tome outra direção.”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (642, 24, 50, 'Labão e Batuel tomaram então a palavra: “Do Senhor veio tudo isso – disseram eles. Nada temos a dizer.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (643, 24, 51, 'Eis aí Rebeca: toma-a e parte. Que ela seja a mulher do filho de teu senhor, como o Senhor disse”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (644, 24, 52, 'Ouvindo essas palavras, o servo de Abraão prostrou-se por terra diante do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (645, 24, 53, 'Tomando em seguida objetos de prata, objetos de ouro e vestidos, ofereceu-os como presente a Rebeca. Ofereceu também ricos presentes ao seu irmão e à sua mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (646, 24, 54, 'Puseram-se então à mesa, ele e os seus companheiros, e passaram a noite. Levantando-se no dia seguinte, disse o servo: “Deixai-me partir para a casa do meu senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (647, 24, 55, 'Ao que o irmão e a mãe de Rebeca responderam: “Fique a jovem ainda conosco alguns dias, ao menos dez dias; depois disto partirá”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (648, 24, 56, '“Não me retenhais – tornou ele –, pois que o Senhor fez bem-sucedida a minha viagem, deixai-me partir e voltar para o meu senhor.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (649, 24, 57, '“Chamemos a jovem – disseram eles – e perguntemos-lhe o seu parecer.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (650, 24, 58, 'Chamaram Rebeca e disseram-lhe: “Queres partir com este homem?”. “Sim” – respondeu ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (651, 24, 59, 'Dei­xaram-na, pois, partir juntamente com sua ama de leite, com o servo de Abraão e seus companheiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (652, 24, 60, 'Eles abençoaram-na, dizendo: “Ó nossa irmã: possas tu tornar-te a mãe de milhares de miríades! E possua a tua posteridade a porta dos seus inimigos!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (653, 24, 61, 'Rebeca e suas servas levantaram-se, montaram nos camelos e seguiram o homem. Este, conduzindo Rebeca, pôs-se logo a caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (654, 24, 62, 'Isaac tinha voltado do poço de Lahai-Roí, e habitava no Negueb.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (655, 24, 63, 'Uma tarde em que saíra para meditar no campo, levantando os olhos, viu alguns camelos que se aproximavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (656, 24, 64, 'Rebeca também, tendo levantado os olhos, viu Isaac, e desceu do camelo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (657, 24, 65, 'Ela disse ao servo de Abraão: “Quem é aquele homem que vem ao nosso encontro no campo?”. “É o meu senhor” – respondeu ele. E ela tomou depressa o véu e cobriu-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (658, 24, 66, 'O servo contou a Isaac tudo o que tinha feito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (659, 24, 67, 'E Isaac introduziu Rebeca na tenda de Sara, sua mãe. Desposou-a, e ela tornou-se sua mulher muito amada. E, desse modo, Isaac consolou-se da morte de sua mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (660, 25, 1, 'Abraão tomou outra mu­lher, chamada Cetura,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (661, 25, 2, 'a qual lhe deu à luz Zanrã, Jecsã, Madã, Madiã, Jesboc e Sué.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (662, 25, 3, 'Jecsã gerou Sabá e Dadã (dos quais foram filhos os assurim, os latussim e os laomim).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (663, 25, 4, 'Os filhos de Madiã foram Efa, Ofer, Henoc, Abida e Eldaá. Estes foram todos os filhos de Cetura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (664, 25, 5, 'Abraão deu todos os seus bens a Isaac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (665, 25, 6, 'Quanto aos filhos de suas concubinas, só lhes deu presentes, e despediu-os, ainda vivo, mandando-os para longe de seu filho Isaac, para a terra do oriente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (666, 25, 7, 'Eis a duração da vida de Abraão: ele viveu cento e setenta e cinco anos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (667, 25, 8, 'e entregou sua alma, morrendo numa ditosa velhice, em idade avançada e cheio de dias, e foi unir-se aos seus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (668, 25, 9, 'Isaac e Ismael, seus filhos, enterraram-no na caverna de Macpela, situada na terra de Efron, filho de Seor, o hiteu, defronte de Mambré,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (669, 25, 10, 'a terra que Abraão tinha comprado aos filhos de Het. É lá que ele foi enterrado, com Sara, sua mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (670, 25, 11, 'Depois de sua morte, Deus abençoou seu filho Isaac, que habitava perto do poço de Lahai-Roí.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (671, 25, 12, 'Eis a descendência de Ismael, filho que Agar, a egípcia, escrava de Sara, dera à luz a Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (672, 25, 13, 'Estes são os nomes dos filhos de Ismael, segundo sua ordem de nascimento: o primogênito de Ismael, Nabaiot; em seguida, Cedar, Adbeel, Mabsam,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (673, 25, 14, 'Masma, Duma, Massa,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (674, 25, 15, 'Hadad, Tema, Jetur, Nafis e Cedma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (675, 25, 16, 'Tais são os filhos de Ismael, e estes são os seus nomes segundo suas cidades e seus respectivos acampamentos, doze chefes de suas tribos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (676, 25, 17, 'A duração da vida de Ismael foi de cento e trinta e sete anos, e depois ele entregou sua alma, e foi unir-se aos seus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (677, 25, 18, 'Seus filhos habitaram desde Hévila até Sur, que se encontra defronte do Egito, na direção da Assíria. Ele se instalou assim em frente de todos os seus irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (678, 25, 19, 'Eis a história de Isaac, filho de Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (679, 25, 20, 'Abraão gerou Isaac. Isaac tinha a idade de quarenta anos quando se casou com Rebeca, filha de Batuel, o arameu, de Padã-Aram, e irmã de Labão, o arameu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (680, 25, 21, 'Isaac rogou ao Senhor por sua mulher, que era estéril. O Senhor ouviu-o e Rebeca, sua mulher, concebeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (681, 25, 22, 'Como as crianças lutassem no seu ventre, ela disse: “Se assim é, por que me acontece isso?”. E ela foi consultar o Senhor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (682, 25, 23, 'que lhe respondeu: “Tens duas nações no teu ventre; dois povos se dividirão ao sair de tuas entranhas. Um povo vencerá o outro, e o mais velho servirá ao mais novo”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (683, 25, 24, 'Chegado o tempo em que ela devia dar à luz, eis que trazia dois gêmeos no seu ventre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (684, 25, 25, 'O que saiu primeiro era vermelho, e todo peludo como um manto de peles, e chamaram-no Esaú. Saiu em seguida o seu irmão, segurando pela mão o calcanhar de Esaú, e deram-lhe o nome de Jacó.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (685, 25, 26, 'Isaac tinha sessenta anos quando eles vieram ao mundo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (686, 25, 27, 'Os meninos cresceram. Esaú tornou-se um hábil caçador, um homem do campo, enquanto Jacó era um homem pacífico, que morava na tenda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (687, 25, 28, 'Isaac preferia Esaú, porque gostava de caça; Rebeca, porém, se afeiçoou mais a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (688, 25, 29, 'Um dia em que Jacó preparava um guisado, voltando Esaú fatigado do campo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (689, 25, 30, 'disse-lhe: “Deixa-me comer um pouco dessa coisa vermelha, porque estou muito cansado”. (É por isso que puseram o nome a Esaú Edom.)*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (690, 25, 31, 'Jacó respondeu-lhe: “Vende-me primeiro o teu direito de primogenitura”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (691, 25, 32, '“Morro de fome, que me importa o meu direito de primogenitura?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (692, 25, 33, '“Jura-mo, pois, agora mesmo” – tornou Jacó. Esaú jurou e vendeu o seu direito de primogenitura a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (693, 25, 34, 'Este deu-lhe pão e um prato de lentilhas. Esaú comeu, bebeu, depois se levantou e partiu. Foi assim que Esaú des­prezou o seu direito de primogenitura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (694, 26, 1, 'Sobreveio uma fome à região (além da primeira fome que houve no tempo de Abraão), e Isaac foi ter com Abime­lec, rei dos filisteus em Gerara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (695, 26, 2, 'O Senhor apareceu-lhe e disse-lhe: “Não desças ao Egito; fica na terra que eu te indico.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (696, 26, 3, 'Habita nela; eu estou contigo e te abençoarei, porque é a ti e à tua posteridade que darei toda esta terra, e cumprirei o juramento que fiz ao teu pai Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (697, 26, 4, 'Multiplicarei tua posteridade como as estrelas do céu, lhe darei todas estas regiões, e nela serão benditas todas as nações da terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (698, 26, 5, 'porque Abraão obedeceu à minha voz e observou os meus preceitos, meus mandamentos e minhas leis”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (699, 26, 6, 'Isaac ficou, pois, em Gerara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (700, 26, 7, 'Quando os habitantes da região o interrogavam a respeito de sua mulher, ele dizia-lhes que era sua irmã, pois res­pondendo: “É minha mulher”, temia que os homens daquele lugar o matassem por causa de Rebeca, que era muito bela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (701, 26, 8, 'E, como sua estada ali se prolongasse, aconteceu que um dia, olhando Abimelec pela janela, viu Isaac que acariciava Rebeca, sua mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (702, 26, 9, 'Mandou chamá-lo e disse-lhe: “É evidente que é tua mulher! E como dizias tu que era tua irmã”. “Por­que – respondeu Isaac – eu temia que me matassem por causa dela.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (703, 26, 10, 'Abimelec replicou: “Que nos fizeste? Um pouco mais e alguém do povo teria abusado de tua mu­lher, e terias atraído o pecado sobre nós!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (704, 26, 11, 'Então Abimelec mandou publicar diante de todo o povo que seria morto quem quer que tocasse naquele homem ou em sua mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (705, 26, 12, 'Isaac semeou naquela terra, e colheu o cêntuplo naquele mesmo ano; o Senhor o abençoava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (706, 26, 13, 'E este homem cresceu, e seus bens foram aumentando cada vez mais; tornou-se extremamente rico.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (707, 26, 14, 'Possuía rebanhos de ovelhas e de bois e numerosos escravos. E os filisteus o invejavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (708, 26, 15, 'Por isso, entupiram todos os poços que tinham cavado os escravos de seu pai Abraão, quando este ainda vivia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (709, 26, 16, 'Abimelec disse-lhe: “Aparta-te de nós, pois te tornaste muito mais poderoso do que nós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (710, 26, 17, 'Partiu Isaac e, tendo le­vantado o seu acampamento no vale de Gerara, habitou ali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (711, 26, 18, 'Abriu de novo os poços cavados outrora, no tempo de seu pai Abraão, que os filisteus tinham entupido depois de sua morte, e deu-lhes os mesmos nomes que o seu pai lhes tinha dado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (712, 26, 19, 'Seus servos cavaram outro poço no vale, e encontraram ali uma fonte de água viva.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (713, 26, 20, 'Mas os pastores de Gerara começaram a disputar com os pastores de Isaac: “Esta água é nossa” – diziam eles. Isaac chamou então a esse poço Esec, porque lho tinham contestado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (714, 26, 21, 'Abriram seus pastores um segundo poço, mas surgiu outra disputa, e por isso pôs-lhe o nome de Sitna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (715, 26, 22, 'Partindo em seguida dali, abriu outro poço, sobre o qual não houve mais discussão, e pôs-lhe o nome de Rehobot, “porque agora – disse ele – o Senhor nos pôs ao largo, e prospera­remos na terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (716, 26, 23, 'Dali, Isaac subiu a Bersabeia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (717, 26, 24, 'Naquela mesma noite, o Senhor apareceu-lhe e disse-lhe: “Eu sou o Deus de Abraão, teu pai. Nada temas, estou contigo. Eu te abençoarei e multiplicarei tua des­cendência por causa de Abraão, meu servo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (718, 26, 25, 'Isaac construiu um altar nesse lugar e invocou o nome do Senhor. Levantou depois ali sua tenda e seus escravos cavaram um poço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (719, 26, 26, 'Abimelec veio de Gerara procurá-lo, com Ocozat, seu amigo, e Ficol, general do seu exército.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (720, 26, 27, 'Isaac disse: “Por que me procurais, já que me detestais e me expulsastes do meio de vós?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (721, 26, 28, 'Eles responderam: “Nós vimos que o Senhor está contigo, e pensamos: Haja um juramento entre nós e ti. Queremos, pois, fazer aliança contigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (722, 26, 29, 'Jura que não nos farás nenhum mal, assim como também nós não tocamos em nada do que é teu e só te temos feito bem, deixando-te partir em paz. Agora, tu és o bendito do Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (723, 26, 30, 'Isaac preparou-lhes um banquete, e eles comeram e beberam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (724, 26, 31, 'No dia seguinte, pela manhã, fizeram mutuamente os seus juramentos; Isaac despediu-os em seguida, e eles afastaram-se dele em paz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (725, 26, 32, 'Nesse mesmo dia, os escravos de Isaac vieram dar-lhe notícias do poço que estavam cavando: “Encontramos água” – disseram eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (726, 26, 33, 'Ele pôs a esse poço o nome de Seba. De onde vem o nome de Bersabeia, nome que a cidade conserva até o dia de hoje.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (727, 26, 34, 'Esaú, com a idade de quarenta anos, tomou por mulheres Judite, filha de Beeri, o hiteu, e Basemat, filha de Elon, o hiteu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (728, 26, 35, 'Elas foram um motivo de desgosto para Isaac e Rebeca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (729, 27, 1, 'Isaac envelhecera e seus olhos enfraqueceram-se, de modo que não podia ver. Chamou­ Esaú, seu filho primogênito, e disse-lhe: “Meu filho!”. “Eis-me aqui!” – respondeu ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (730, 27, 2, 'Isaac disse: “Tu vês, estou velho e não sei quando vou morrer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (731, 27, 3, 'Toma as tuas armas, tua aljava e teu arco, vai ao campo e mata-me uma caça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (732, 27, 4, 'Prepara-me depois um prato suculento, como sabes que gosto, e me traz para que o coma e minha alma te abençoe antes que eu morra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (733, 27, 5, '(Ora, Rebeca ouviu atentamente enquanto Isaac falava ao seu filho Esaú.) E Esaú partiu para o campo, a fim de matar e trazer a caça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (734, 27, 6, 'Rebeca disse a Jacó, seu filho: “Acabo de ouvir teu pai dizer ao teu irmão Esaú para que lhe traga uma caça');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (735, 27, 7, 'e lhe prepare um bom prato, a fim de comer e o abençoar diante do Senhor antes de morrer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (736, 27, 8, 'Ouve-me, pois, meu filho, e faze o que te vou dizer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (737, 27, 9, 'Vai ao rebanho e traze-me dois belos cabritos. Prepararei com eles um prato suculento para o teu pai, como ele gosta,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (738, 27, 10, 'tu lhe levarás e ele comerá, a fim de que te abençoe antes de morrer”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (739, 27, 11, '“Mas – respondeu Jacó à sua mãe – Esaú, meu irmão, é peludo, enquanto eu sou de pele lisa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (740, 27, 12, 'Se meu pai me tocar, passarei aos seus olhos por um impostor e atrairei sobre mim uma maldição em lugar de bênção.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (741, 27, 13, '“Tomo sobre mim esta maldição, meu filho – disse sua mãe. Ouve-me somente, e vai buscar o que te digo.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (742, 27, 14, 'Jacó foi e trouxe os dois cabritos, com os quais sua mãe preparou um prato suculento, como seu pai gostava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (743, 27, 15, 'Escolheu as mais belas vestes de Esaú, seu filho primogênito, que tinha em casa, e revestiu com elas Jacó, seu filho mais novo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (744, 27, 16, 'Cobriu depois suas mãos, assim como a parte lisa do pescoço, com a pele dos cabritos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (745, 27, 17, 'e pôs-lhe nas mãos o prato suculento e o pão que tinha preparado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (746, 27, 18, 'Jacó foi para junto de seu pai e disse-lhe: “Meu pai! Eis-me aqui!”. “Quem és tu, meu filho?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (747, 27, 19, 'Jacó respondeu: “Eu sou Esaú, teu primogênito; fiz o que me pediste. Levanta-te, assenta-te e come de minha caça, a fim de que tua alma me abençoe”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (748, 27, 20, '“Como encontraste caça tão depressa, meu filho?” “É que o Senhor, teu Deus, fez que ela se apresentasse diante de mim.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (749, 27, 21, '“Aproxima-te, então, meu filho, para que eu te apalpe e veja se, de fato, és o meu filho Esaú.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (750, 27, 22, 'Jacó aproximou-se de Isaac, seu pai, que o apalpou e disse: “A voz é a voz de Jacó, mas as mãos são as mãos de Esaú”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (751, 27, 23, 'E não o reconheceu, porque suas mãos estavam peludas como as do seu irmão Esaú. E abençoou-o.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (752, 27, 24, '“Tu és bem o meu filho Esaú?”. Disse-lhe ele: “Sim”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (753, 27, 25, '“(Então) serve-me, para que eu coma de tua caça, meu filho, e minha alma te abençoe.” Jacó serviu-lhe e ele comeu; e trouxe-lhe também vinho, do qual ele bebeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (754, 27, 26, 'Então Isaac, seu pai, disse-lhe: “Aproxima-te, meu filho, e beija-me”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (755, 27, 27, 'E, aproximando-se Jacó para lhe dar um beijo, Isaac sentiu o perfume de suas vestes, e o abençoou nestes termos. “Sim, o odor de meu filho é como o odor de um campo que o Senhor abençoou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (756, 27, 28, 'Deus te dê o orvalho do céu e a gordura da terra, uma abundância de trigo e de vinho!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (757, 27, 29, 'Sirvam-te os povos e prostrem-se as nações diante de ti! Sê o senhor dos teus irmãos, e curvem-se diante de ti os filhos de tua mãe! Maldito seja quem te amaldiçoar e bendito quem te abençoar!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (758, 27, 30, 'Apenas Isaac acabara de abençoar Jacó, e este saíra de junto do seu pai, chegou Esaú da caça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (759, 27, 31, 'Preparou também ele um prato suculento e trouxe-o ao seu pai, dizendo: “Levanta-te, meu pai, e come da caça de teu filho, a fim de que tua alma me abençoe”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (760, 27, 32, '“Quem és tu?” – perguntou-lhe seu pai Isaac. “Eu sou o teu filho primogênito Esaú.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (761, 27, 33, 'Então Isaac, tomado de emoção violenta, exclamou: “Quem é, pois, aquele que foi à caça e me trouxe o prato que eu comi antes que tu voltastes? Eu o abençoei, e ele será bendito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (762, 27, 34, 'Ouvindo essas palavras de seu pai, Esaú soltou um grito cheio de amargura, e disse-lhe: “Abençoa-me também a mim, meu pai!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (763, 27, 35, '“Teu irmão – respondeu-lhe Isaac – veio, fraudulentamente, tomar a tua bênção.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (764, 27, 36, 'Esaú disse então: “Será porque ele se chama Jacó que me suplantou já duas vezes? Tirou-me meu direito de primogenitura, e eis que agora me rouba minha bênção!”. E ajuntou: “Não reservaste, porventura, uma bênção também para mim?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (765, 27, 37, 'Isaac respondeu-lhe: “Eu o constituí teu senhor, e dei-lhe todos os seus irmãos por servos e o estabeleci na posse do trigo e do vinho. Que posso ainda fazer por ti, meu filho?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (766, 27, 38, 'Esaú disse ao seu pai: “Então só tens uma bênção, meu pai? Abençoa-me também a mim, meu pai!”. E pôs-se a chorar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (767, 27, 39, 'Isaac tomou a palavra: “Eis – disse ele – que a tua habitação será desprovida da gordura da terra e do orva­lho que desce do céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (768, 27, 40, 'Viverás de tua espada, servindo o teu irmão, mas, se te libertares, quebrarás o seu jugo de cima do teu pescoço”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (769, 27, 41, 'Esaú concebeu ódio por Jacó por causa da bênção que lhe tinha dado seu pai e disse em seu coração: “Virão os dias do luto de meu pai, e matarei meu irmão Jacó”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (770, 27, 42, 'E foram referidas a Rebeca estas palavras do seu filho primogênito. Ela mandou chamar seu filho mais novo, Jacó, e disse-lhe: “Teu irmão Esaú quer te matar para se vingar de ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (771, 27, 43, 'Escuta-me, pois, meu filho: vai, foge para junto de Labão, meu irmão, em Harã;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (772, 27, 44, 'fica em casa dele algum tempo, até que se acalme a cólera de teu irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (773, 27, 45, 'Assim que passar a sua cólera e tiver ele esquecido do que lhe fizeste, te mandarei buscar. Por que perderia eu vocês dois num só dia?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (774, 27, 46, 'Rebeca disse a Isaac: “Estou desgostosa da vida por causa das filhas de Het. Se Jacó tomar uma mulher entre as filhas de Het, para que ainda viver?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (775, 28, 1, 'Isaac chamou Jacó e o abençoou, dando-lhe esta ordem: “Não desposarás uma filha de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (776, 28, 2, 'Mas vai a Padã-Aram, à casa de Batuel, pai de tua mãe, e escolhe lá uma mulher entre as filhas de Labão, irmão de tua mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (777, 28, 3, 'Deus Todo-poderoso te abençoe, te faça crescer e multiplicar, de sorte que te tornes uma multidão de povos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (778, 28, 4, 'Conceda-te ele, como também à tua posteridade, a bênção de Abraão, a fim de que possuas a terra onde moras, e que Deus deu a Abraão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (779, 28, 5, 'Isaac despediu Jacó, e este partiu para Padã-Aram, para a casa de Labão, filho de Batuel, o arameu, irmão de Rebeca, mãe de Jacó e de Esaú.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (780, 28, 6, 'Ora, Esaú viu que seu pai tinha abençoado Jacó, e o tinha enviado a Padã-Aram para ali tomar uma mulher e que, depois de o ter abençoado, lhe proibira desposar uma filha de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (781, 28, 7, 'Viu também que Jacó, obedecendo aos seus pais, partira para Padã-Aram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (782, 28, 8, 'E, compreendendo que as filhas de Canaã não eram bem vistas pelo seu pai,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (783, 28, 9, 'foi à casa de Ismael e tomou por mulher, além daquelas que já tinha, a Maelet, filha de Ismael, filho de Abraão, irmã de Nabaiot.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (784, 28, 10, 'Jacó, partindo de Bersabeia, tomou o caminho de Harã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (785, 28, 11, 'Chegou a um lugar, e ali passou a noite, porque o sol já se tinha posto. Serviu-se como travesseiro de uma das pedras que ali se encontravam, e dormiu naquele mesmo lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (786, 28, 12, 'E teve um sonho: via uma escada, que, apoiando-se na terra, tocava com o cimo o céu; e anjos de Deus subiam e desciam pela escada. No alto estava o Senhor,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (787, 28, 13, 'que lhe dizia: “Eu sou o Senhor, o Deus de Abraão, teu pai e o Deus de Isaac; darei a ti e à tua descendência a terra em que estás deitado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (788, 28, 14, 'Tua posteridade será tão numerosa como os grãos de poeira no solo; tu te estenderás, para o ocidente e para o oriente, para o norte e para o meio-dia, e todas as famílias da terra serão benditas em ti e em tua posteridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (789, 28, 15, 'Estou contigo, para te guardar aonde quer que fores, e te reconduzirei a esta terra, e não te abandonarei sem ter cumprido o que te prometi”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (790, 28, 16, 'Jacó, despertando de seu sono, exclamou: “Em verdade, o Senhor está neste lugar, e eu não o sabia!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (791, 28, 17, 'E, cheio de pavor, ajuntou: “Quão terrível é este lugar! É nada menos que a casa de Deus; é aqui, a porta do céu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (792, 28, 18, 'No dia seguinte, pela manhã, tomou Jacó a pedra: sobre a qual repousara a cabeça e a erigiu em estela, derramando óleo sobre ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (793, 28, 19, 'Deu o nome de Betel a este lugar, que antes se chamava Luz.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (794, 28, 20, 'Jacó fez então este voto: “Se Deus for comigo, se ele me guardar durante esta viagem que empreendi, e me der pão para comer e roupa para vestir,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (795, 28, 21, 'e me fizer voltar em paz à casa paterna, então o Senhor será o meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (796, 28, 22, 'Esta pedra da qual fiz uma estela será uma casa de Deus, e pagarei o dízimo de tudo o que me derdes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (797, 29, 1, 'Jacó pôs-se de novo a caminho e foi para a terra dos filhos do oriente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (798, 29, 2, 'Olhando em torno de si, viu no campo um poço junto do qual estavam dei­tados três rebanhos de ovelhas. Esse poço servia de bebedouro para os reba­nhos. Mas, sendo grande a pedra que cobria a abertura do poço');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (799, 29, 3, 'somente a removiam de cima quando todos os rebanhos fossem recolhidos. Davam então de beber aos animais e recolocavam a pedra no seu devido lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (800, 29, 4, 'Jacó disse aos pastores: “Meus ir­mãos, de onde sois?”. “Somos de Harã” – responderam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (801, 29, 5, '“Conheceis porventura Labão, filho de Nacor?”. “Sim.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (802, 29, 6, '“Como vai ele?”. “Vai muito bem; e eis justamente sua filha Raquel que vem com o rebanho.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (803, 29, 7, '“É ainda pleno dia – tornou Jacó – e não é hora de se recolherem os rebanhos. Dai de beber às ovelhas e levai-as de novo ao pasto.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (804, 29, 8, '“Não o podemos – responde­ram eles – antes que todos os rebanhos estejam reunidos. Tiramos então a pedra de cima do poço e damos de beber aos animais.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (805, 29, 9, 'Falava ainda com eles, quando chegou Raquel com o rebanho do seu pai, porque era pastora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (806, 29, 10, 'Logo que Jacó viu Raquel, filha de Labão, irmão de sua mãe, aproximou-se, rolou a pedra de cima da boca do poço e deu de beber às ovelhas de Labão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (807, 29, 11, 'Depois beijou Raquel e pôs-se a chorar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (808, 29, 12, 'Contou-lhe que era parente de seu pai e filho de Rebeca; e ela correu a anunciar isso ao seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (809, 29, 13, 'Tendo Labão ouvido falar de Jacó, filho de sua irmã, correu-lhe ao encontro, abraçou-o, beijou-o e o conduziu à sua casa. Jacó contou-lhe tudo o que se tinha passado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (810, 29, 14, 'e Labão disse-lhe: “Sim, tu és de meus ossos e de minha carne”. Jacó ficou em casa dele um mês inteiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (811, 29, 15, 'E Labão disse-lhe: “Acaso, porque és meu parente, me servirás de graça? Dize-me que salário queres”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (812, 29, 16, 'Ora, Labão tinha duas filhas: a mais velha chamava-se Lia, e a mais nova Raquel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (813, 29, 17, 'Lia tinha os olhos ternos, e Raquel era bela de corpo e de rosto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (814, 29, 18, 'Jacó, que amava Raquel, disse a Labão: “Eu te servirei sete anos por Raquel, tua filha mais nova”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (815, 29, 19, '“É melhor – respondeu Labão – dá-la a ti que a outro: fica comigo.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (816, 29, 20, 'Assim, Jacó serviu por Raquel sete anos, que lhe pareceram dias, tão grande era o amor que lhe tinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (817, 29, 21, 'Disse, pois, a Labão: “Dá-me minha mulher, porque está completo o meu tempo e quero desposá-la”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (818, 29, 22, 'Labão reuniu todos os habitantes do lugar e deu um banquete.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (819, 29, 23, 'Mas, à noite, conduziu Lia a Jacó, que se uniu com ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (820, 29, 24, 'E deu à sua filha Lia, sua escrava Zelfa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (821, 29, 25, 'Pela manhã, viu Jacó que tinha ficado com Lia. E disse a Labão: “Que me fizeste? Não foi por Raquel que te servi? Por que me enganaste?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (822, 29, 26, '“Aqui – respondeu Labão – não é costume casar a mais nova antes da mais velha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (823, 29, 27, 'Acaba a semana com esta, e depois te darei também sua irmã, na condição que me sirvas ainda sete anos.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (824, 29, 28, 'Assim fez Jacó: acabou a semana com Lia, e depois lhe deu Labão por mulher sua filha Raquel,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (825, 29, 29, 'dando por serva a Raquel sua escrava Bala.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (826, 29, 30, 'Jacó uniu-se também a Raquel, a quem amou mais do que a Lia. E serviu ainda por sete anos em casa de Labão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (827, 29, 31, 'O Senhor, vendo que Lia era des­prezada, tornou-a fecunda, enquanto Raquel permanecia estéril.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (828, 29, 32, 'Lia concebeu e deu à luz um filho, ao qual chamou Rúben, “porque – dizia ela – o Senhor olhou minha aflição; agora meu marido me amará”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (829, 29, 33, 'Concebeu de novo e deu à luz outro filho. “O Senhor – disse ela – vendo que era desprezada, deu-me ainda este.” E pôs-lhe o nome de Simeão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (830, 29, 34, 'Concebeu ainda e deu à luz mais um filho. “Desta vez – disse ela – meu marido se apegará a mim, porque já lhe dei à luz três filhos.” Por isso, deu-lhe o nome de Levi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (831, 29, 35, 'Concebeu ainda e deu à luz um filho. E disse: “Desta vez, louvarei ao Senhor”. E chamou-o Judá. Depois cessou de ter filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (832, 30, 1, 'Raquel, vendo que não dava fi­lhos a Jacó, teve inveja de sua irmã: “Dá-me filhos – disse ela ao seu marido – senão morro!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (833, 30, 2, 'E Jacó irritou-se com ela. “Acaso – disse ele – posso eu pôr-me no lugar de Deus que te recusou a fecundidade?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (834, 30, 3, 'Ela respondeu: “Eis minha serva Bala: toma-a. Que ela dê à luz sobre os meus joelhos e assim, por ela, terei também filhos”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (835, 30, 4, 'Deu-lhe, pois, por mu­lher sua escrava Bala, da qual se aproximou Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (836, 30, 5, 'Bala concebeu e deu à luz um filho a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (837, 30, 6, 'Disse então Raquel: “Deus fez-me justiça. Ele ouviu minha voz e deu-me um filho”. Por isso, ela o chamou Dã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (838, 30, 7, 'Bala, escrava de Raquel, concebeu de novo e deu à luz um segundo filho a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (839, 30, 8, 'Raquel disse: “Lutei contra minha irmã junto de Deus, e venci!”. E deu ao menino o nome de Neftali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (840, 30, 9, 'Lia, vendo que não concebia mais, tomou sua escrava Zelfa e deu-a por mulher a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (841, 30, 10, 'Zelfa, escrava de Lia, deu à luz um filho a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (842, 30, 11, 'Lia disse: “Que sorte!”. E chamou-o Gad.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (843, 30, 12, 'Zelfa, escrava de Lia, deu à luz um segundo filho a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (844, 30, 13, 'Lia disse: “Que felicidade! As mulheres me chamarão ditosa”. E cha­mou-o Aser.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (845, 30, 14, 'Um dia, por ocasião da ceifa, Rúben saiu ao campo e, tendo encontrado umas mandrágoras, levou-as à sua mãe Lia. Raquel disse a Lia: “Rogo-te que me dês as mandrágoras do teu filho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (846, 30, 15, 'Lia res­pondeu: “Já não é bastante teres tomado meu marido, para que queiras ainda as mandrágoras do meu filho?”. “Pois bem – tornou Raquel – em troca das mandrágoras do teu filho, que ele durma contigo esta noite.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (847, 30, 16, 'À noite, quando Jacó voltou do campo, Lia saiu-lhe ao encontro: “Vem comigo – disse-lhe ela – eu te aluguei em troca das mandrágoras do meu filho”. E Jacó dormiu com ela aquela noite.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (848, 30, 17, 'Deus ouviu Lia, que concebeu e deu à luz um quinto filho a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (849, 30, 18, '“Deus – disse ela – recompensou-me por ter dado minha escrava ao meu marido.” E o chamou Issacar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (850, 30, 19, 'Lia concebeu ainda e deu à luz um sexto filho a Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (851, 30, 20, 'E disse: “Deus deu-me um belo presente; agora meu marido habitará comigo, pois que lhe dei à luz seis filhos”. E o chamou Zabulon.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (852, 30, 21, 'Depois disso, deu à luz uma filha, a quem chamou Dina.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (853, 30, 22, 'Lembrou-se Deus de Raquel, ouviu-a e tornou-a fecunda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (854, 30, 23, 'Raquel concebeu e deu à luz um filho. “Deus – disse ela – tirou o meu opróbrio.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (855, 30, 24, 'E chamou-o José, dizendo: “Dê-me o Senhor ainda outro filho!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (856, 30, 25, 'Tendo Raquel dado à luz José, Jacó disse a Labão: “Deixa-me partir para a minha casa, na minha terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (857, 30, 26, 'Dá-me minhas mulheres e meus filhos, pelos quais te servi, a fim de que eu me vá; tu sabes quanto tempo servi em tua casa”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (858, 30, 27, 'Labão respondeu-lhe: “Se achei graça aos teus olhos... reconheço que o Senhor me abençoou por causa de ti.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (859, 30, 28, 'Fixa-me o que devo dar-te – ajuntou ele – e te darei”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (860, 30, 29, 'Jacó disse-lhe: “Tu sabes como te tenho servido, e como aumentaram os teus reba­nhos graças a mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (861, 30, 30, 'Tinhas pouca coisa, antes de minha chegada, e tudo aumentou depois. O Senhor abençoou-te a cada um dos meus passos. Agora, quanto a mim, quando trabalharei eu para minha casa?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (862, 30, 31, '“Que te hei de dar?” – disse Labão. Jacó respondeu: “Não me darás nada. Se aceitas o que te vou propor, conti­nuarei a apascentar e guardar o teu rebanho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (863, 30, 32, 'Vou hoje passar pelo meio de todos os teus rebanhos e pôr à parte, entre os cordeiros, todo animal manchado, malhado ou negro, e entre as cabras, tudo o que é manchado ou malhado: isto será o meu salário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (864, 30, 33, 'Minha justiça testemu­nhará em meu favor para o futuro, quando vieres verificar o meu salário: tudo o que não for malhado ou manchado entre as cabras e negro entre os cordeiros, será considerado como roubado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (865, 30, 34, '“Está bem – disse Labão – seja como dizes.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (866, 30, 35, 'Naquele mesmo dia, pôs ele à parte os bodes ma­lhados e manchados, todas as cabras malhadas ou manchadas, todas aquelas que estavam marcadas de branco, e todos os cordeiros negros; confiou-os aos seus filhos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (867, 30, 36, 'e pôs à distância de três dias de jornada entre ele e Jacó, o qual apascentava o resto do rebanho de Labão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (868, 30, 37, 'Jacó tomou então varas verdes de álamo, de amendoeira e de plátano; tirou-lhes parte da casca, fazendo faixas brancas e deixando a nu o ramo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (869, 30, 38, 'Colocou as varas assim preparadas sob os olhos das ovelhas, nas pias e nos bebedouros onde vinham beber. Indo a beber, entravam em calor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (870, 30, 39, 'E como entrassem no calor do coito diante dessas varas, concebiam­ cordeiros riscados, manchados e malhados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (871, 30, 40, 'Jacó punha-os à parte, e voltava a face dos animais para o que era malhado e negro no rebanho de Labão. Constituiu assim rebanhos para si, que não se misturaram aos de Labão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (872, 30, 41, 'Além disso, Jacó só punha suas varas nos bebedouros sob os olhos das ovelhas em calor, a fim de que seu coito se fizesse perto das varas, quando se tratava de ovelhas vigorosas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (873, 30, 42, 'Quando eram fracas, não punha as varas, de sorte que os cordeiros raquíticos eram para Labão e os vigorosos para ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (874, 30, 43, 'Esse homem tornou-se assim extremamente rico, e teve muitos rebanhos, escravas e escravos, camelos e jumentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (875, 31, 1, 'Jacó ouviu as palavras dos fi-lhos de Labão, que diziam: “Jacó tomou tudo o que é de nosso pai, e é à sua custa que ele se tornou de tal forma rico”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (876, 31, 2, 'Obser­vou também, pela fisio­nomia de Labão, que este não tinha mais para com ele os sentimentos de antes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (877, 31, 3, 'O Senhor disse a Jacó: “Volta para a terra dos teus pais, para a tua parentela, e eu estarei contigo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (878, 31, 4, 'Então Jacó mandou Raquel e Lia virem ao campo junto dos seus rebanhos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (879, 31, 5, '“Vejo – disse-lhes ele – pelo semblante de vosso pai, que ele não é mais para comigo o mesmo que antes. Mas o Deus de meu pai está comigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (880, 31, 6, 'Sabeis que servi a vosso pai o melhor que pude,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (881, 31, 7, 'enquanto ele zombou de mim, mudando dez vezes o meu salário; mas Deus não lhe permitiu causar-me prejuízo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (882, 31, 8, 'Quando ele dizia: os animais malhados serão o teu salário, todas as ovelhas davam à luz cordeiros malhados, e se dizia: os animais riscados serão o teu salário, todas as ovelhas davam à luz cordeiros riscados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (883, 31, 9, 'Foi Deus mesmo que tomou o rebanho de vosso pai para me dar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (884, 31, 10, 'No tempo em que os animais deviam conceber, eu levantava os olhos e via em sonhos que os bodes que cobriam as cabras eram listrados, malhados e manchados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (885, 31, 11, 'Um anjo de Deus disse-me em sonhos: ‘Jacó! Eis-me aqui’ – respondi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (886, 31, 12, 'Levanta os olhos e vê: todos os bodes que cobrem as cabras são listrados, malhados e manchados, porque eu vi tudo o que te fez Labão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (887, 31, 13, 'Eu sou o Deus de Betel, onde tu me consagraste uma estela e me fizeste um voto. Agora, vamos, sai daqui e volta para a terra de tua família”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (888, 31, 14, 'Raquel e Lia responderam: “Resta-nos porventura ainda alguma parte de herança na casa de nosso pai?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (889, 31, 15, 'Não nos tratou ele como estrangeiras, vendendo-nos e devorando o nosso dinheiro?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (890, 31, 16, 'Toda a riqueza, que Deus tomou de nosso pai, é para nós e para nossos filhos. Faze, pois, o que Deus te disse”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (891, 31, 17, 'Levantou-se, pois, Jacó, e fez montar seus filhos e suas mulheres nos camelos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (892, 31, 18, 'Levou todos os seus reba­nhos, todos os bens que tinha ajuntado, o rebanho que lhe pertencia, adquirido em Padã-Aram, e partiu para junto de seu pai Isaac, na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (893, 31, 19, 'Raquel, aproveitando um momento em que seu pai fora tosquiar suas ovelhas, roubou os terafim de seu pai;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (894, 31, 20, 'e Jacó enganou Labão, o arameu, ocultando-lhe sua fuga.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (895, 31, 21, 'Fugindo, pois, com tudo o que era seu, atravessou o rio e dirigiu-se para a montanha de Galaad.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (896, 31, 22, 'Três dias depois, soube Labão da fuga de Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (897, 31, 23, 'E, tomando consigo seus irmãos, perseguiu-o durante sete dias de marcha, e alcançou-o na montanha de Galaad.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (898, 31, 24, 'Deus, porém, apareceu em um sonho noturno a Labão, o arameu, e disse-lhe: “Guarda-te de dizer algo a Jacó”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (899, 31, 25, 'Labão alcançou, pois, Jacó. Este havia levantado sua tenda na montanha, enquanto Labão e seus irmãos tinham plantado a sua na montanha de Galaad.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (900, 31, 26, 'Labão disse a Jacó: “Que fizeste? Tu me enganaste, e conduziste minhas filhas como prisioneiras de guerra!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (901, 31, 27, 'Por que fugiste dessa forma, e me lograste em lugar de me avisar? Eu te teria despedido com manifestações de júbilo e com cânticos, ao som do tamborim e da harpa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (902, 31, 28, 'Não me deixaste beijar meus filhos e minhas filhas! Procedeste como um insensato.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (903, 31, 29, 'Eu poderia agora fazer-vos mal, mas o Deus de teu pai disse-me na última noite: ‘Guarda-te de dizer algo a Jacó’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (904, 31, 30, 'E, se partistes somente porque tinhas saudade da casa paterna, então por que roubaste os meus deuses?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (905, 31, 31, 'Jacó respondeu-lhe: “Tive medo, ao pensar que poderias tirar-me tuas fi­lhas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (906, 31, 32, 'quanto aos teus deuses, porém, seja morto aquele que os tiver consigo! Examina o que está comigo, em presença de nossos parentes, e retoma o que é teu”. Ora, Jacó ignorava o roubo de Raquel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (907, 31, 33, 'Labão entrou na tenda de Jacó, na de Lia e na das duas escravas, mas nada encontrou. Saindo da tenda de Lia, entrou na de Raquel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (908, 31, 34, 'Esta havia tomado os terafim e, colocando-os na sela do camelo, sentou-se em cima. Labão revistou toda a tenda, sem nada encontrar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (909, 31, 35, 'Raquel disse ao seu pai: “Não se irrite o meu senhor, se não posso levantar-me em sua presença, pois acho-me agora com a indisposição que costuma vir às mulhe­res”. Revistou, pois, mas não encontrou os terafim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (910, 31, 36, 'Jacó encolerizou-se então contra Labão, e acabrunhou-o de censuras: “Qual é o meu crime? – disse-lhe ele. – Qual é o meu pecado, para que te irrites desse modo contra mim?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (911, 31, 37, 'Revistaste todas as minhas bagagens: que encontraste do que é teu? Mostra-me aqui em presença de meus parentes e dos teus, e sejam eles juízes entre nós dois.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (912, 31, 38, 'Há vinte anos que estou em tua casa: tuas ovelhas e tuas cabras não abortaram, não comi os carneiros do teu rebanho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (913, 31, 39, 'Nunca te trouxe os animais estraçalhados pelas feras. Eu os repunha, pois tu o exigias, quer fossem roubados de dia, quer de noite.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (914, 31, 40, 'Eu era queimado de dia pelo calor, e de noite pelo frio, e o sono fugia dos meus olhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (915, 31, 41, 'Eis já vinte anos que estou em tua casa; servi-te catorze anos por tuas duas filhas, seis anos pelos teus rebanhos, e dez vezes modificaste o meu salário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (916, 31, 42, 'Se o Deus de meu pai, o Deus de Abraão, o Deus terrível de Isaac não se tivesse posto de meu lado, tu me terias hoje despedido com as mãos vazias. Deus viu minhas penas e meus trabalhos, e na última noite ele pronunciou-se”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (917, 31, 43, 'Labão respondeu a Jacó: “Estas filhas são minhas filhas, estes filhos, meus filhos, e estes rebanhos, meus rebanhos: tudo o que vês é meu. Que farei eu agora contra minhas filhas, ou contra os filhos que elas deram ao mundo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (918, 31, 44, 'Vamos, façamos juntos um pacto que nos sirva de tes­temunho a nós dois”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (919, 31, 45, 'Jacó tomou uma pedra e erigiu-a em estela,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (920, 31, 46, 'e disse aos seus parentes: “Trazei pedras”. E, tendo juntado muitas, fizeram um monte, sobre o qual comeram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (921, 31, 47, 'Labão chamou-o Yegar-Saaduta, e Jacó, Galaad.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (922, 31, 48, 'Labão disse: “Este monte é hoje testemunha entre mim e ti”; por isso, foi lhe dado o nome de Galaad,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (923, 31, 49, 'e também Masfa, porque Labão disse ainda: “Que o Senhor nos vigie a nós ambos, quando nós nos tivermos despedido um do outro.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (924, 31, 50, 'Se maltratares minhas fi­lhas, e se tomares outras mu­lheres além delas, não é um homem que estará conosco. Mas toma cuidado, pois é Deus que será testemunha entre nós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (925, 31, 51, 'Labão disse ainda a Jacó: “Vês este monte de pedras e esta estela que levantei entre mim e ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (926, 31, 52, 'Este monte é testemunho, e igualmente esta estela, de que eu não ultrapassarei este monte para o teu lado, e que tu não ultrapassarás este monte e esta estela para o meu lado para nos fazer mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (927, 31, 53, 'O Deus de Abraão, o Deus de Nacor, o Deus de seus pais seja juiz entre nós!”. E Jacó jurou pelo Deus terrível de Isaac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (928, 31, 54, 'Ofereceu um sacrifício sobre a montanha e convidou seus parentes para comer. Come­ram e passaram a noite na montanha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (929, 32, 1, 'No dia seguinte, pela ma­nhã, Labão beijou seus fi­lhos e suas filhas; abençoou-os e retomou o caminho de sua casa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (930, 32, 2, 'Jacó prosseguiu o seu caminho e encontrou uns anjos de Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (931, 32, 3, 'Ao vê-los, exclamou: “É aqui o acampamento de Deus!”. Por isso, deu àquele lugar o nome de Maanaim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (932, 32, 4, 'Despachou diante de si mensagei­ros a seu irmão Esaú, na terra de Seir, nos campos de Edom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (933, 32, 5, 'E deu-lhes esta ordem: “Eis o que direis ao meu senhor Esaú: Assim fala o teu servo Jacó: Habitei em casa de Labão onde estive até o dia de hoje.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (934, 32, 6, 'Possuo bois, jumentos, ovelhas, servos e servas, e mando agora anunciá-lo ao meu senhor para encontrar graça diante dele”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (935, 32, 7, 'Os mensageiros voltaram a Jacó, dizendo: “Fomos ter com Esaú: ele vem ao teu encontro com quatrocentos homens”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (936, 32, 8, 'Jacó foi tomado de pavor e de angústia. Dividiu em dois grupos a gente que estava com ele, assim como as ovelhas, os bois e os camelos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (937, 32, 9, '“Se Esaú – disse ele consigo – atacar um dos grupos e o destruir, ao menos o outro se salvará.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (938, 32, 10, 'Depois Jacó disse: “Deus de meu pai Abraão, Deus de meu pai Isaac, Senhor que me disses­ses: Volta para a tua terra, para o meio de tua parentela, e eu te beneficiarei,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (939, 32, 11, 'eu sou indigno de todos os favores e de toda a fidelidade que tendes testemunhado ao vosso servo. Só tinha o meu bastão quando atravessei este Jordão, e eis que possuo agora dois acampamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (940, 32, 12, 'Salvai-me, eu vos peço, das mãos de meu irmão Esaú, pois temo que ele me venha atacar, sem poupar nem mãe nem filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (941, 32, 13, 'Entretanto, vós me dissestes: Eu te beneficiarei e tornarei tua posteridade inumerável como os grãos de areia do mar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (942, 32, 14, 'Jacó passou a noite naquele lugar. Escolheu entre os bens que possuía um presente para o seu irmão Esaú:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (943, 32, 15, 'duzentas cabras, vinte bodes, duzentas ovelhas, vinte carneiros,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (944, 32, 16, 'trinta came­las com suas crias, quarenta vacas, dez touros, vinte jumentas e dez jumentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (945, 32, 17, 'Entregou-os aos servos, cada rebanho à parte, e disse-lhes: “Ide adiante de mim, e haja uma distância entre cada rebanho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (946, 32, 18, 'E deu esta ordem ao primeiro: “Quando meu irmão Esaú te encontrar e te perguntar quem és, aonde vais e a quem pertence o rebanho que conduzes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (947, 32, 19, 'responderás: Pertence ao teu servo Jacó; é um presente que ele manda ao meu senhor Esaú; ele mesmo vem atrás de nós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (948, 32, 20, 'Deu a mesma ordem ao segundo, ao terceiro e a todos os que conduziam os reba­nhos: “Quando encontrardes Esaú – disse ele – vós lhe direis a mesma coisa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (949, 32, 21, 'E direis que seu servo Jacó vos segue”. “Eu o aplacarei – pensou ele – com este presente que me precede; e depois o verei pessoalmente; talvez me fará ele bom acolhimento.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (950, 32, 22, 'Foi, pois, o presente adiante dele, e ele ficou aquela noite no acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (951, 32, 23, 'Naquela mesma noite, ele se levantou com suas duas mulheres, suas duas servas e seus onze filhos e passou o vau do Jaboc.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (952, 32, 24, 'Tomou-os, e os fez passar a torrente com tudo o que lhe pertencia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (953, 32, 25, 'Jacó ficou só; e alguém lutava com ele até o romper da aurora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (954, 32, 26, 'Vendo que não podia vencê-lo, tocou-lhe aquele homem na articulação da coxa e esta deslocou-se, enquanto Jacó lutava com ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (955, 32, 27, 'E disse-lhe: “Deixa-me partir, porque a aurora se levanta”. “Não te deixarei partir – respondeu Jacó – antes que me tenhas abençoado.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (956, 32, 28, 'Ele perguntou-lhe: “Qual é o teu nome?”. “Jacó.”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (957, 32, 29, '“Teu nome não será mais Jacó – tornou ele – mas Israel, porque lutaste com Deus e com os homens, e venceste.” Jacó pediu-lhe:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (958, 32, 30, '“Peço-te que me digas qual é o teu nome”. “Por que me perguntas o meu nome?” – respondeu ele. E abençoou-o no mesmo lugar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (959, 32, 31, 'Jacó chamou àquele lugar Fa­nuel, “porque – disse ele – eu vi a Deus face a face, e minha vida foi poupada”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (960, 32, 32, 'O sol levantava-se no horizonte, quando ele atravessou Fanuel. E coxeava de uma perna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (961, 32, 33, 'É por isso que os israe­litas, ain­da hoje, não comem o nervo da articulação da coxa, porque aquele homem tinha tocado nesse nervo da articulação da coxa de Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (962, 33, 1, 'Jacó, levantando os olhos, viu Esaú que avançava com quatrocentos homens. Repartiu então os filhos entre Lia, Raquel e as duas servas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (963, 33, 2, 'Colocou as servas com seus filhos na frente, depois Lia com os seus e, por último, Raquel com José.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (964, 33, 3, 'E ele, passando adiante, prostrou-se até a terra sete vezes antes de se aproximar do seu irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (965, 33, 4, 'Esaú correu-lhe ao encontro e beijou-o; ele atirou-se ao seu pescoço e beijou-o; e puseram-se a chorar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (966, 33, 5, 'Levantando os olhos, Esaú viu as mulheres e as crianças: “Quem são estes que tens contigo?” – perguntou ele. “São – respondeu Jacó – os filhos que aprouve a Deus dar ao teu servo.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (967, 33, 6, 'Aproximaram-se então as servas com seus filhos e prostraram-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (968, 33, 7, 'Lia com seus filhos adiantaram-se por sua vez e prostraram-se, e, enfim, José e Raquel prostraram também.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (969, 33, 8, 'Esaú disse: “Que significa todo esse acampamento que encontrei?”. “É – disse Jacó – para ganhar o favor de meu senhor.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (970, 33, 9, 'Esaú disse-lhe: “Possuo muitos bens, meu irmão, guarda o que te pertence”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (971, 33, 10, '“Oh, suplico-te – replicou Jacó – se ganhei teu favor, aceita este presente de minhas mãos; porque te contemplei como se contempla Deus, e me fizeste bom aco­lhimento.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (972, 33, 11, 'Aceita o presente que te ofereço; pois Deus cumulou-me de seus favores, e nada me falta.” E tanto insistiu que Esaú aceitou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (973, 33, 12, 'Esaú disse: “Partamos, ponhamo-nos a caminho; eu te precederei”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (974, 33, 13, 'Jacó disse-lhe: “Tu vês, meu senhor, que os meninos são delicados; e tenho de cuidar das ovelhas e vacas que amamentam; se os fizer caminhar ainda um só dia, morrerá todo o rebanho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (975, 33, 14, 'Que o meu se­nhor vá, pois, adiante de seu servo; eu seguirei devagar, ao passo do rebanho que vai adiante de mim, e ao passo dos meninos, até que chegue à casa de meu senhor em Seir”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (976, 33, 15, '“Permita-me ao menos – disse-lhe Esaú – deixar-te uma parte de meus homens.” “Não é necessário – disse Jacó – basta-me ter achado graça aos olhos do meu senhor!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (977, 33, 16, 'No mesmo dia, Esaú retomou o caminho de Seir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (978, 33, 17, 'Jacó partiu para Sucot, onde, tendo edificado uma casa, cons­truiu também cabanas para o seu rebanho. Daí o nome de Sucot dado a esse lugar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (979, 33, 18, 'De volta de Padã-Aram, Jacó chegou sem contratempos à cidade de Siquém, na terra de Canaã. E acampou diante da cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (980, 33, 19, 'Comprou por cem moedas de prata aos filhos de Hemor, pai de Siquém, o pedaço de terra onde havia armado sua tenda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (981, 33, 20, 'Levantou ali um altar, ao qual chamou El, Deus de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (982, 34, 1, 'Dina, a filha que Lia tinha dado a Jacó, saiu para ver as filhas da região.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (983, 34, 2, 'Tendo-a visto Siquém, filho de Hemor, o heveu, príncipe daquela terra, raptou-a e dormiu com ela, violentando-a.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (984, 34, 3, 'Seu coração prendeu-se a Dina, filha de Jacó: ele amou a jovem, e soube falar-lhe ao coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (985, 34, 4, 'E disse então ao seu pai Hemor: “Dá-me esta jovem por mulher”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (986, 34, 5, 'Ora, Jacó soube do ultraje que ele tinha feito à sua filha, mas, como seus filhos estivessem no campo com o rebanho, não disse nada até que voltassem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (987, 34, 6, 'Hemor, pai de Siquém, veio ter com Jacó para lhe falar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (988, 34, 7, 'Quando os filhos de Jacó, voltando do campo, souberam o que se tinha passado, indignaram-se muito, porque Siquém se tornara culpado de uma grande infâmia contra Israel, dormindo com a filha de Jacó, coisa que não devia fazer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (989, 34, 8, 'Hemor disse-lhes então: “Meu filho Siquém está enamorado de vossa filha; dai-a por mulher, eu vos peço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (990, 34, 9, 'Aliai-vos conosco: dai-nos vossas filhas e des­posai as nossas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (991, 34, 10, 'Habitai no meio de nós, pois a terra estará à vossa disposição; podereis estabelecer-vos e negociar nela, e adquirir propriedades”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (992, 34, 11, 'De seu lado, Siquém disse ao pai e aos outros irmãos de Dina: “Ache eu graça aos vossos olhos e vos darei o que pedirdes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (993, 34, 12, 'Seja qual for o preço de compra e os presentes que exigirdes, o que me fixar­des, isto eu darei, contanto que me deis a jovem por mulher”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (994, 34, 13, 'Os filhos de Jacó deram a Siquém e a Hemor uma resposta dolosa, porque Siquém havia ultrajado sua irmã Dina e lhes disseram:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (995, 34, 14, '“Dar nossa irmã a um incircunciso – disseram eles – é uma coisa que não podemos fazer, porque isso seria desonroso para nós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (996, 34, 15, 'Só concordaremos com o vosso desejo com a condição de que vos torneis como nós, e que todos vossos varões sejam circuncidados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (997, 34, 16, 'Então vos daremos nossas filhas e desposaremos as vossas, habitaremos convosco e formaremos todos um só povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (998, 34, 17, 'Mas se não nos quiserdes ouvir e não vos deixardes circuncidar, tomaremos nossa filha e nos retiraremos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (999, 34, 18, 'O seu oferecimento agradou a Hemor e ao seu filho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1000, 34, 19, 'O jovem não tardou em fazer o que se lhe pedia, porque estava enamorado da filha de Jacó. Era o homem mais considerado de sua família.');
