INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1001, 34, 20, 'Hemor e seu filho foram à porta da cidade e disseram a seus concidadãos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1002, 34, 21, '“Estes homens são pacíficos conosco; fiquem eles na terra e possam aí circular. A região é bastante espaçosa para eles, tanto para a direita como para a esquerda. Des­posaremos suas filhas e eles desposarão as nossas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1003, 34, 22, 'Mas eles só consentem em ficar conosco, de modo a fazermos todos um só povo, com a condição de que todos os nossos varões sejam circuncidados como o são eles mesmos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1004, 34, 23, 'Com isso, os seus rebanhos, os seus bens e todos os seus animais, tudo não será nosso? Aceitemos, pois, suas condições, a fim de que se estabeleçam entre nós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1005, 34, 24, 'Todos os que passavam pela porta da cidade deixaram-se convencer por Hemor e Siquém, seu filho, e todos os varões foram circuncidados.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1006, 34, 25, 'No terceiro dia, estando todos ainda doentes, os dois filhos de Jacó, Simeão e Levi, irmãos de Dina, tomaram cada um sua espada, penetraram na cidade, que de nada desconfiava, e mataram todos os varões.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1007, 34, 26, 'Passaram a fio de espada também Hemor e Siquém, seu filho; tiraram Dina da casa de Siquém e se foram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1008, 34, 27, 'Os filhos de Jacó caíram impetuosamente sobre os mortos e assolaram a cidade, porque haviam ultrajado sua irmã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1009, 34, 28, 'Tomaram suas ovelhas, seus bois, seus jumentos e tudo o que havia na cidade como nos campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1010, 34, 29, 'E levaram como espólio todos os seus bens, seus filhos, suas mu­lheres e tudo o que se encontrava em suas casas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1011, 34, 30, 'Jacó disse a Simeão e a Levi: “Vós me lançastes na confusão e me tornastes odioso aos habitantes desta terra, aos cananeus e aos ferezeus. Só tenho comigo alguns homens e, quando toda essa gente se congregar contra mim para me ferir, perecerei com minha família”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1012, 34, 31, 'Eles res­ponderam: “Porventura, devíamos deixar tratar nossa irmã como uma prostituta?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1013, 35, 1, 'Deus disse a Jacó: “Vamos, sobe a Betel e fica ali, e levanta um altar nesse lugar ao Deus que se manifestou a ti, quando fugias diante de teu irmão Esaú”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1014, 35, 2, 'Jacó disse à sua família e à sua gente: “Tirai do meio de vós os deuses estrangeiros, purificai-vos e mudai vossos vestidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1015, 35, 3, 'Vamos subir a Betel, onde levantarei um altar ao Deus que me ouviu no dia de minha aflição, e que esteve comigo durante minha viagem”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1016, 35, 4, 'Entregaram a Jacó todos os deuses estrangeiros que tinham, assim como os brincos que traziam nas orelhas, e Jacó enterrou-os debaixo de um tere­binto, perto de Siquém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1017, 35, 5, 'Partindo eles dali, Deus semeou o pânico nas cidades circunvizi­nhas, de sorte que não perseguiram os filhos de Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1018, 35, 6, 'Chegou, portanto, Jacó com toda a sua gente à Luza, na terra de Canaã, hoje Betel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1019, 35, 7, 'Levantou ali um altar e deu a esse lugar o nome de El-Betel, porque foi ali que Deus lhe aparecera, quando fugia de seu irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1020, 35, 8, 'Foi então que morreu Débora, ama de Rebeca. E foi ali sepultada, ao pé de Betel, debaixo de um carvalho, ao qual se chamou Carvalho do Pranto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1021, 35, 9, 'Quando Jacó voltou de Padã-­Aram, Deus apareceu-lhe novamente e o abençoou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1022, 35, 10, '“Teu nome – disse-lhe ele – é Jacó. Tu não te chamarás mais assim, mas Israel.” E chamou-o Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1023, 35, 11, 'Deus disse-lhe: “Eu sou o Deus Todo-poderoso. Sê fecundo e multiplica-te. De ti nascerão um povo e uma assembleia de povos; e de teus rins sairão reis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1024, 35, 12, 'A terra que dei a Abraão e a Isaac, darei a ti e à tua posteridade”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1025, 35, 13, 'Depois, Deus retirou-se de junto dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1026, 35, 14, 'No mesmo lugar onde Deus lhe falou, Jacó erigiu uma estela sobre a qual fez uma libação e derramou óleo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1027, 35, 15, 'E deu o nome de Betel ao lugar onde Deus lhe tinha falado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1028, 35, 16, 'E partiram de Betel. Quando estavam a pouca distância de Éfrata, Raquel deu à luz, e o seu parto foi penoso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1029, 35, 17, 'Durante as dores do parto, a parteira disse-lhe: “Não temas, porque ainda terás este filho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1030, 35, 18, 'E, estando prestes a render a alma – porque estava já agonizante – ela chamou o filho de Benoni; o seu pai, porém, chamou-o Benjamim.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1031, 35, 19, 'Raquel expirou e foi sepultada no caminho de Éfrata, hoje Belém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1032, 35, 20, 'Jacó erigiu uma estela sobre seu túmulo; é a estela do túmulo de Raquel, que existe ainda hoje.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1033, 35, 21, 'Israel partiu e plantou sua tenda além de Migdal-Eder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1034, 35, 22, 'Foi durante sua estada nessa região que Rúben foi dormir com Bala, concubina de seu pai, e Israel soube.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1035, 35, 23, 'Os filhos de Jacó foram em número de doze. Filhos de Lia: Rúben, primogênito de Jacó, Simeão, Levi, Judá, Issacar e Zabulon.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1036, 35, 24, 'Filhos de Raquel: José e Benjamim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1037, 35, 25, 'Filhos de Bala, escrava de Raquel: Dã e Neftali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1038, 35, 26, 'Filhos de Zelfa, escrava de Lia: Gad e Aser. Tais são os filhos nascidos a Jacó em Padã-Aram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1039, 35, 27, 'Jacó foi para junto de seu pai Isaac em Mambré, em Cariat-Arbe, hoje Hebron, onde tinham habitado Abraão e Isaac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1040, 35, 28, 'E todos os dias da vida de Isaac foram cento e oitenta anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1041, 35, 29, 'E morreu. A morte reuniu-o aos seus, velho e saciado de dias. Esaú e Jacó, seus filhos, sepultaram-no.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1042, 36, 1, 'Eis a descendência de Esaú, também chamado Edom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1043, 36, 2, 'Esaú tomou suas mulheres entre as filhas de Canaã: Ada, filha de Elon, o hiteu; Ooli­bama, filha de Ana, filha de Sebeon, o heveu;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1044, 36, 3, 'e Basemat, filha de Ismael, irmã de Nabaiot.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1045, 36, 4, 'Ada deu a Esaú, Elifaz; Base­mat deu à luz Rauel, e Ooliba­ma deu à luz Jeús, Jalam e Coré.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1046, 36, 5, 'Tais são os filhos nascidos a Esaú na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1047, 36, 6, 'Esaú tomou suas mulheres, seus filhos e suas filhas, assim como todo o seu pessoal, seus rebanhos, seus animais e todos os bens que tinha adquirido na terra de Canaã, e mudou-se para longe de seu irmão Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1048, 36, 7, 'Seus bens eram, com efeito, numerosos demais para que pudessem morar juntos, e a terra em que habitavam não lhes bastava, por causa dos seus muitos rebanhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1049, 36, 8, 'Esaú estabeleceu-se na montanha de Seir. Esaú chamava-se também Edom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1050, 36, 9, 'Eis a descendência de Esaú, pai de Edom, na montanha de Seir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1051, 36, 10, 'Estes são os nomes dos filhos de Esaú: Elifaz, filho de Ada, mulher de Esaú; Rauel, filho de Basemat, mu­lher de Esaú.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1052, 36, 11, 'Os filhos de Elifaz foram: Temã, Omar, Sefo, Gatam e Cenez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1053, 36, 12, 'Elifaz, filho de Esaú, tomou uma concubina, Tamna que lhe deu à luz Amalec. Estes são os filhos de Ada, mulher de Esaú.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1054, 36, 13, 'Eis os filhos de Rauel: Naat, Zara, Sama e Meza. São estes os filhos de Basemat, mulher de Esaú.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1055, 36, 14, 'Eis os filhos de Oolibama, filha de Ana, filha de Sebeon, mulher de Esaú: ela deu à luz Esaú, Jeús, Jalam e Coré.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1056, 36, 15, 'Eis os chefes das tribos dos filhos de Esaú. Filhos de Elifaz, primogênito de Esaú: o chefe Temã, o chefe Omar, o chefe Sefo, o chefe Cenez, o chefe Coré,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1057, 36, 16, 'o chefe Gatam, o chefe Amalec. Estes são os chefes saídos de Elifaz, na terra de Edom; tais são os filhos de Ada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1058, 36, 17, 'Filhos de Rauel, filho de Esaú: o chefe Naat, o chefe Zara, o chefe Sama e o chefe Meza. Tais são os chefes saídos de Rauel, na terra de Edom; estes são os filhos de Basemat, mulher de Esaú.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1059, 36, 18, 'Filhos de Oolibama, mulher de Esaú: o chefe Jeús, o chefe Jalam e o chefe Coré. Estes são os chefes saídos de Oolibama, filha de Ana e mulher de Esaú.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1060, 36, 19, 'Tais são os filhos de Esaú, e estes são os seus chefes; isto é, de Edom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1061, 36, 20, 'Eis os filhos de Seir, o horreu, que habitava naquela terra: Lotã, Sobal, Sebeon, Ana,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1062, 36, 21, 'Dison, Eser e Disã. Tais são os chefes dos horreus, filhos de Seir, na terra de Edom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1063, 36, 22, 'Os filhos de Lotã foram Hori e Hemã; a irmã de Lotã era Tamna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1064, 36, 23, 'Eis os filhos de Sobal: Alvã, Manaat, Ebal, Sefo e Onam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1065, 36, 24, 'Eis os filhos de Sebeon: Aia e Ana. Foi esta Ana que encontrou no deserto as fontes de água quente, quando pastoreava os jumentos de Sebeon, seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1066, 36, 25, 'Eis os filhos de Ana: Dison e Oolibama, filha de Ana.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1067, 36, 26, 'Eis os fi­lhos de Dison: Hamdã, Esebã, Jetraã e Caraã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1068, 36, 27, 'Eis os filhos de Eser: Balaã, Zavã e Acã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1069, 36, 28, 'Eis os filhos de Disã: Hus e Aram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1070, 36, 29, 'Eis os chefes dos horreus: o chefe Lotã, o chefe Sobal, o chefe Sebeon, o chefe Aná,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1071, 36, 30, 'o chefe Dison, o chefe Eser, o chefe Disã. Esses são os chefes dos horreus, que governaram na terra de Seir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1072, 36, 31, 'Estes são os reis que reinaram na terra de Edom, antes que os fi­lhos de Israel tivessem rei:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1073, 36, 32, 'Bela, filho de Beor, reinou em Edom; sua cidade chamava-se Denaba.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1074, 36, 33, 'Morrendo Bela, Jobab, filho de Zara, de Bosra, reinou em seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1075, 36, 34, 'Morto Jobab, Husam, da terra dos temanitas, reinou em seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1076, 36, 35, 'Morto Husam, Adad, filho de Badad, reinou em seu lugar; ele derrotou Madiã, nas terras de Moab; sua cidade chamava-se Avit.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1077, 36, 36, 'Morto Adad, Semla, de Masreca, reinou em seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1078, 36, 37, 'Morto Semla, reinou em seu lugar, Saul, de Rehobot, que está perto do rio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1079, 36, 38, 'Com a morte de Saul, Baalanã, filho de Acor, reinou em seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1080, 36, 39, 'Com a morte de Baalanã, filho de Acor, Hadar reinou em seu lugar; sua cidade chamava-se Fau; sua mulher chamava-se Meetabel, filha de Matred, filha de Mezaab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1081, 36, 40, 'Eis os nomes dos chefes saídos de Esaú, segundo suas tribos, seus territórios e seus nomes: o chefe Tamna, o chefe Alva, o chefe Jetet,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1082, 36, 41, 'o chefe Oolibama, o chefe Ela, o chefe Finon,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1083, 36, 42, 'o chefe Cenez, o chefe Temã, o chefe Mabsar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1084, 36, 43, 'o chefe Magdiel, o chefe Iram. Esses são os chefes de Edom, segundo suas residências na terra que ocupavam. Eis aí Esaú, pai de Edom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1085, 37, 1, 'Jacó habitou na região onde seu pai havia morado, na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1086, 37, 2, 'Eis a história da descendência de Jacó: José, ainda jovem, com a idade de dezessete anos, apascentava o rebanho com seus irmãos, os filhos de Bala e os filhos de Zelfa, mulheres de seu pai; e ele contou ao seu pai as más conversas dos irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1087, 37, 3, 'Israel amava José mais do que todos os outros filhos, porque ele era o filho de sua velhice; e mandara-lhe fazer uma túnica de várias cores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1088, 37, 4, 'Seus irmãos, vendo que seu pai o preferia a eles, começaram a odiá-lo e não podiam mais tratá-lo com bons modos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1089, 37, 5, 'Ora, José teve um sonho, e o contou aos seus irmãos, que o detestaram ainda mais. José disse-lhe:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1090, 37, 6, '“Ouvi o sonho que tive:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1091, 37, 7, 'estávamos ligando feixes no campo, e eis que o meu feixe se levantou e se pôs de pé, enquanto os vossos o cercavam e se prostravam diante dele”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1092, 37, 8, 'Seus irmãos disseram-lhe: “Quererias, porven­tura, reinar sobre nós e tornar-te nosso senhor?”. E odiaram-no ainda mais por causa de seus sonhos e de suas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1093, 37, 9, 'José teve ainda outro sonho, que contou aos seus irmãos. “Tive – disse ele – ainda um sonho: o sol, a lua e onze estrelas prostravam-se diante de mim.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1094, 37, 10, 'Ele contou isso ao seu pai e aos seus irmãos, mas foi repreendido por seu pai: “Que significa – disse-lhe ele – este sonho que tiveste? Viremos, acaso, eu, tua mãe e teus irmãos, a nos prostrar por terra diante de ti?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1095, 37, 11, 'Seus irmãos ficaram, pois, com inveja dele, mas seu pai guardou a lembrança desse acontecimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1096, 37, 12, 'Os irmãos de José foram apascentar os rebanhos de seu pai em Siquém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1097, 37, 13, 'Israel disse a José: “Teus irmãos guardam os rebanhos em Siquém. Vem: vou mandar-te a eles”. “Eis-me aqui” – res­pondeu José.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1098, 37, 14, '“Vai, pois, ver se tudo corre bem a teus irmãos e ao rebanho, e traze-me notícias deles.” Enviou-o do vale de Hebron, e José foi a Siquém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1099, 37, 15, 'Um homem encontrou-o vagando pelo campo: “Que buscas?” – perguntou ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1100, 37, 16, '“Busco meus irmãos – respondeu ele. Dize-me onde apascentam os rebanhos.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1101, 37, 17, 'E o homem respondeu: “Partiram daqui e ouvi-os dizer: ‘Vamos para Dotain’.” Partiu então José em busca dos seus irmãos e encontrou-os em Dotain.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1102, 37, 18, 'Eles o viram de longe. Antes que José se aproximasse, combinaram entre si como o haveriam de matar;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1103, 37, 19, 'e disseram uns aos outros: “Eis o sonhador que chega.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1104, 37, 20, 'Vamos, matemo-lo e atiremo-lo numa cisterna; diremos depois que uma fera o devorou; e então veremos de que lhe aproveitaram os seus sonhos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1105, 37, 21, 'Ouvindo-o, porém, Rúben, quis livrá-lo de suas mãos: “Não lhe tiremos a vida – disse ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1106, 37, 22, 'Não derrameis sangue. Jogai-o naquela cisterna, no deserto, mas não levanteis vossa mão contra ele”. Pois Rúben pensava livrá-lo de suas mãos para o reconduzir ao pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1107, 37, 23, 'Quando José se aproximou de seus irmãos, eles o despojaram de sua túnica, daquela bela túnica de várias cores que trazia,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1108, 37, 24, 'e jogaram-no numa cisterna velha, que não tinha água.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1109, 37, 25, 'E, sentando-se para comer, eis que, levantando os olhos, viram surgir no horizonte uma caravana de ismaelitas vinda de Galaad. Seus camelos estavam carregados de resina, de bálsamo e de ládano, que transportavam para o Egito.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1110, 37, 26, 'Então Judá disse aos seus irmãos: “Que nos aproveita matar nosso irmão e ocultar o seu sangue?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1111, 37, 27, 'Vinde e vendamo-lo aos ismaelitas. Não levantemos nossas mãos contra ele, pois, afinal, é nosso irmão, nossa carne”. Seus irmãos concordaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1112, 37, 28, 'E, quando passaram os negociantes madianitas, tiraram José da cisterna e venderam-no por vinte moedas de prata aos ismae­litas, que o levaram para o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1113, 37, 29, 'Rúben voltou à cisterna, e eis que José já não estava ali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1114, 37, 30, 'Rasgou então suas vestes e voltou para junto dos seus irmãos: “O menino desapareceu – disse ele. E eu, para onde irei?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1115, 37, 31, 'Tomaram então a túnica de José, mataram um cabrito e a mergulharam no seu sangue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1116, 37, 32, 'E mandaram-na levar ao seu pai com esta mensagem: “Eis o que encontramos: vê se não é, porventura, a túnica do teu filho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1117, 37, 33, 'Jacó reconheceu-a e exclamou: “É a túnica de meu filho! Uma fera o devorou! José foi estraçalhado!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1118, 37, 34, 'E, rasgando as vestes, cobriu-se de um saco, e chorou o seu filho por muito tempo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1119, 37, 35, 'Todos os seus filhos e filhas vieram consolá-lo, mas ele não aceitou nenhuma condolência: “É chorando – disse ele – que descerei para junto de meu filho na habitação dos mortos”. Foi assim que o seu pai o chorou.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1120, 37, 36, 'Os madianitas venderam-no a Putifar, no Egito, eunuco do faraó e chefe da guarda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1121, 38, 1, 'Naquele tempo, Judá, apartando-se dos seus irmãos, foi para a casa de um homem de Odolam, chamado Hira.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1122, 38, 2, 'Judá viu ali a filha de um cana­neu, de nome Sué, e desposou-a, unindo-se a ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1123, 38, 3, 'Ela concebeu e deu à luz um filho, ao qual chamou Her.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1124, 38, 4, 'Concebeu novamente e deu ao mundo um filho, e deu-lhe o nome de Onã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1125, 38, 5, 'E teve ainda um filho, que chamou Sela. Judá estava em Casib, na ocasião desse nascimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1126, 38, 6, 'Judá escolheu para Her, seu primogênito, uma mulher chamada Tamar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1127, 38, 7, 'Her, porém, o primogênito de Judá, era mau aos olhos do Senhor, e o Senhor o feriu de morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1128, 38, 8, 'Então Judá disse a Onã: “Vai, toma a mulher de teu irmão, cumpre teu dever de levirato e suscita uma posteridade a teu irmão”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1129, 38, 9, 'Mas Onã, que sabia que essa posteridade não seria dele, maculava-se por terra cada vez que se unia à mulher do seu irmão, para não dar a ele posteridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1130, 38, 10, 'Seu comportamento desagradou ao Senhor, que o feriu de morte também.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1131, 38, 11, 'E Judá disse a Tamar, sua nora: “Conserva-te viúva em casa de teu pai até que meu filho Sela se torne adulto”. “Não é bom – pensava ele – que também ele morra como seus irmãos.” E Tamar voltou a habitar na casa paterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1132, 38, 12, 'Muito tempo depois, morreu a filha de Sué, mulher de Judá. Passado o luto, subiu Judá a Tamna para a tosquia de suas ovelhas, com seu amigo Hira, o odolamita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1133, 38, 13, 'E foi noticiado a Tamar: “Eis que o teu sogro sobe a Tamna para a tosquia de suas ovelhas.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1134, 38, 14, 'Depôs ela então os seus vestidos de viúva, cobriu-se de um véu, e, assim disfarçada, assentou-se à entrada de Enaim, que se encontra no cami­nho de Tamna, pois via que Sela tinha crescido e não lha tinham dado por mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1135, 38, 15, 'Judá, vendo-a, julgou tratar-se de uma prostituta, porque tinha o rosto coberto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1136, 38, 16, 'E, chegando-se a ela no caminho, disse: “Queres juntar-te comigo?”. (Ignorava ele que se tratava de sua nora.) Ela respondeu: “O que me darás para juntar-me contigo?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1137, 38, 17, '“Mandarei a ti um cabrito do meu rebanho.” “Está bem; mas dá-me então um penhor, até que o tenhas enviado.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1138, 38, 18, '“Que penhor queres que eu te dê?” “Teu anel, teu cordão e o bastão que tens na mão.” Ele os entregou; em seguida, aproximou-se dela e ela concebeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1139, 38, 19, 'E levantando-se, partiu; tirou o seu véu e retomou seus vestidos de viúva.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1140, 38, 20, 'E Judá mandou-lhe o cabrito por seu amigo, o odolamita, para retirar o penhor das mãos daquela mulher, mas ele, não a encontran­do,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1141, 38, 21, 'perguntou aos habitantes do lugar: “Onde está aquela prostituta que estava em Enaim, à beira do caminho?”. Responderam-lhe: “Não há prostituta nesse lugar!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1142, 38, 22, 'Ele voltou para junto de Judá: “Não a encontrei – disse ele – e os moradores daquele lugar disseram-me que não havia nenhuma prostituta ali”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1143, 38, 23, '“Guarde ela o meu pe­nhor – respondeu Judá – não nos tornemos ridículos! Eu mandei o cabrito; tu, porém, não a encontraste.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1144, 38, 24, 'Mais ou menos três meses depois, vieram dizer a Judá: “Tamar, tua nora, conduziu-se mal: vê-se que está grávida”. Judá respondeu: “Tirai-a para fora, que ela seja queimada!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1145, 38, 25, 'E, enquanto era conduzida, ela mandou dizer ao seu sogro: “Concebi do homem a quem pertence isto: examine bem – ajuntou ela – de quem são este anel, este cordão e este bastão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1146, 38, 26, 'Judá, reconhecendo-os, exclamou: “Ela é mais justa do que eu; pois que não a dei ao meu filho Sela”. E não a conheceu mais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1147, 38, 27, 'E, na ocasião de dar à luz, eis que ela trazia dois gêmeos no seu ventre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1148, 38, 28, 'No parto, saindo a mão, a parteira tomou-a e atou nela um fio vermelho, dizendo: “Este é o que saiu primeiro!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1149, 38, 29, 'Mas, como ele retirasse a mão, saiu o seu irmão. “Que brecha fizeste! – exclamou a parteira: Que a brecha esteja sobre ti!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1150, 38, 30, 'E chamou-se-lhe Farés. Em seguida, veio o seu irmão, com o fio vermelho atado na mão. Deu-se­­-lhe o nome de Zara.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1151, 39, 1, 'José foi conduzido ao Egito, e Putifar, um oficial egípcio do faraó, chefe da guarda, comprou-o aos ismaeli­tas que o levavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1152, 39, 2, 'O Senhor estava com José, e tudo lhe prosperava. Morava na casa do seu senhor, o egípcio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1153, 39, 3, 'Seu se­nhor viu que o Senhor estava com ele e lhe fazia prosperar tudo o que empreendia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1154, 39, 4, 'José conquistou a simpatia do seu senhor, que o empregou a seu serviço, pondo-o à testa de sua casa e confiando-lhe todos os seus bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1155, 39, 5, 'Desde o momento em que José tomou o governo de sua casa e de todos os seus bens, o Senhor abençoou a casa do egípcio, por causa de José: a bênção do Senhor desceu sobre tudo o que lhe pertencia, na casa como nos campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1156, 39, 6, 'Ele entregou todos os seus negócios a José, sem mais se preocupar de coisa alguma, exceto do que se alimentava. Ora, José era belo de corpo e de rosto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1157, 39, 7, 'E aconteceu, depois de tudo isso, que a mulher de seu senhor lançou seus olhos em José e disse-lhe: “Dorme comigo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1158, 39, 8, 'Mas ele recusou: “Meu senhor – disse-lhe ele – não me pede conta alguma do que se faz na casa, e confiou-me todos os seus bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1159, 39, 9, 'Não há maior do que eu nesta casa; ele nada me interdisse, exceto tu, que és sua mulher. Como poderia eu cometer um tão grande crime e pecar contra Deus?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1160, 39, 10, 'Em vão se esforçava ela todos os dias, falando a José; ele não consentia em dormir com ela e unir-se a ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1161, 39, 11, 'Certo dia, tendo ele entrado na ca­sa para fazer seus serviços, e não se encontrando ali ninguém da casa,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1162, 39, 12, 'ela segurou-o pelo manto, dizendo: “Dorme comigo!”. Mas José, largando-lhe o manto nas mãos, fugiu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1163, 39, 13, 'Vendo a mulher que ele lhe tinha deixado o manto nas mãos e fugido,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1164, 39, 14, 'chamou a gente de sua casa e disse-lhes: “Vede: trouxeram-nos este hebreu para a casa a fim de que ele abuse de nós. Este homem veio-me procurar para dormir comigo, mas eu gritei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1165, 39, 15, 'E vendo que eu me punha a gritar, deixou seu manto ao meu lado e fugiu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1166, 39, 16, 'E guardou junto de si as vestes de José até a volta de seu senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1167, 39, 17, 'E fez-lhe a mesma narrativa: “O escravo hebreu – disse ela – que nos trouxeste, veio à minha procura para abusar de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1168, 39, 18, 'Mas, pondo-me a gritar, deixou o seu manto ao meu lado e fugiu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1169, 39, 19, 'Ao ouvir isso de sua mulher, contando-lhe como se tinha comportado com ela o seu servo, ele enfureceu-se,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1170, 39, 20, 'e lançou José na prisão, onde se encontravam detidos os prisioneiros do rei. E José foi encarcerado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1171, 39, 21, 'O Senhor estava com ele. Mostrou-lhe sua bondade e fez que ele conquistasse a simpatia do chefe da prisão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1172, 39, 22, 'Este confiou a José todos os presos que ali se encontravam, e nada se fazia sem sua ordem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1173, 39, 23, 'O chefe da prisão não fiscalizava nada do que fazia José, porque o Senhor estava com ele e fazia-lhe prosperar tudo o que empreendia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1174, 40, 1, 'Depois disso, aconte­ceu que o copeiro e o padeiro do rei do Egito ofenderam o seu senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1175, 40, 2, 'O faraó, encolerizado contra os seus dois oficiais, o copeiro-mor e o padeiro-mor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1176, 40, 3, 'mandou-os encarcerar na casa do chefe da guarda, na prisão onde se encontrava detido José.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1177, 40, 4, 'O chefe da guarda associou-lhes José para os servir. Havia já certo tempo que estavam detidos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1178, 40, 5, 'quando os dois prisioneiros, o copeiro e o padeiro do rei do Egito, tiveram um sonho numa mesma noite, cada um o seu, com seu sentido particular.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1179, 40, 6, 'Quando José voltou junto deles, no dia seguinte pela manhã, viu que estavam tristes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1180, 40, 7, 'Perguntou então aos oficiais do faraó, detidos com ele na casa do seu senhor: “Por que tendes hoje um ar sombrio?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1181, 40, 8, '“Tivemos um sonho – responderam –; e não há ninguém para no-los interpretar.” “Porventura, não pertence a Deus – repli­cou José – a interpretação dos sonhos? Rogo-vos que me conteis tais sonhos.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1182, 40, 9, 'E o copeiro-mor contou seu sonho a José: “Em meu sonho – disse ele – vi uma cepa que estava diante de mim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1183, 40, 10, 'e nessa cepa três varas, que pareciam brotar; saiu uma flor e seus cachos deram uvas maduras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1184, 40, 11, 'Eu tinha na mão a taça do faraó; tomei as uvas e espre­mi as­ na taça, que entreguei na mão do faraó”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1185, 40, 12, 'José disse-lhe: “Eis o significado do teu sonho: as três varas são três dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1186, 40, 13, 'Dentro de três dias, o faraó te reabilitará em tuas funções. Apresentarás ao faraó sua taça, como o fazias antes, quando eras seu copeiro.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1187, 40, 14, 'Quando fores feliz, lembra-te de mim e faze-me o favor de recomendar-me ao faraó, para que ele me tire desta prisão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1188, 40, 15, 'Porque é por um rapto que fui tirado da terra dos hebreus, e aqui, igualmente, eu nada fiz para merecer a prisão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1189, 40, 16, 'O padeiro-mor, vendo que José tinha dado uma boa interpretação, disse-lhe: “Eu também, em meu sonho, levava sobre minha cabeça três cestas de pão branco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1190, 40, 17, 'Na de cima, havia toda a sorte de manjares para o faraó; mas as aves do céu comiam-nos na cesta que estava sobre minha cabeça”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1191, 40, 18, '“Eis – disse José – o que isto significa: as três cestas são três dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1192, 40, 19, 'Dentro de três dias, o faraó levantará a tua cabeça: ele te suspenderá numa forca, e as aves devorarão a tua carne.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1193, 40, 20, 'No terceiro dia, celebrava-se o aniversário do faraó, e ele ofereceu um banquete a todo o seu pessoal. Ele levantou a cabeça do copeiro-mor e do padeiro-mor, no meio de todos os seus servos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1194, 40, 21, 'restabeleceu no seu cargo o copeiro-mor, que apresentou novamente a taça ao faraó,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1195, 40, 22, 'e mandou suspender no patíbulo o padeiro-mor, como na interpretação que José lhes havia dado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1196, 40, 23, 'Mas o copeiro-mor não pensou mais em José; esqueceu-o.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1197, 41, 1, 'Dois anos depois, o faraó teve um sonho: encontrava-se ele per­to do Nilo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1198, 41, 2, 'de onde saíram sete vacas belas e gordas, que se puseram a pastar no capin­zal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1199, 41, 3, 'Mas, eis que saíram em seguida do mesmo Nilo sete outras vacas, feias e magras, que vieram e se puseram ao lado das outras na margem do rio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1200, 41, 4, 'As vacas feias e magras devoraram as sete vacas belas e gordas. E o faraó despertou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1201, 41, 5, 'Ador­­meceu de novo e teve outro sonho: sete es­pigas grossas e belas saíam de uma mes­ma haste.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1202, 41, 6, 'Mas eis que em seguida germinaram sete outras espigas, magras e ressequidas pelo vento do oriente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1203, 41, 7, 'E as espigas magras devoraram as sete espigas grossas e cheias. E o faraó despertou: era um sonho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1204, 41, 8, 'Chegada a manhã, o faraó, com o espírito preocupado, mandou chamar todos os mágicos e sábios do Egito. Contou-lhes seus sonhos, mas nenhum deles soube explicá-los.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1205, 41, 9, 'Então o copeiro-mor disse-lhe: “Vou confessar a minha falta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1206, 41, 10, 'Um dia, tendo-se o faraó irado contra os seus servos, mandou-me encarcerar na casa do chefe da guarda, com o padeiro-mor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1207, 41, 11, 'Eis que uma noite tivemos nós dois um sonho, cada um o seu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1208, 41, 12, 'Ora, estava lá conosco um jovem hebreu, escravo do chefe da guarda. Contamos-lhe nossos so­nhos, e ele no-los interpretou, a cada um o seu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1209, 41, 13, 'E os acontecimentos confirmaram sua interpretação: eu fui restabelecido no meu cargo, e o outro foi pendurado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1210, 41, 14, 'O faraó mandou chamar José, o qual foi, imediatamente, tirado do cárcere. Ele barbeou-se, trocou de roupas e apresentou-se diante do faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1211, 41, 15, 'Este disse-lhe: “Tive um sonho que ninguém pôde interpretar. Mas ouvi dizer de ti, que basta contar-te um sonho para que tu o expliques”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1212, 41, 16, '“Não sou eu – respondeu José – mas é Deus quem dará ao faraó uma explicação favorável.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1213, 41, 17, 'O faraó disse então a José: “Em meu sonho, eu estava à margem do Nilo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1214, 41, 18, 'e eis que do Nilo saíram sete vacas gordas e belas, que se puseram a pastar no capinzal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1215, 41, 19, 'E saíram em seguida sete outras vacas magras, feias e disformes, como jamais vi em todo o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1216, 41, 20, 'As vacas magras e feias devoraram as sete primeiras, as gordas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1217, 41, 21, 'que entraram em seu ventre como se nada fossem, pois ficaram tão macilentas e feias como antes. Nessa altura, despertei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1218, 41, 22, 'E tive outro sonho: vi elevar-se de uma mesma haste sete espigas cheias e belas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1219, 41, 23, 'Mas eis que sete outras espigas medíocres, finas e queimadas pelo vento do oriente, germinaram em seguida;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1220, 41, 24, 'e as espigas magras engoliram as sete belas espigas. Em vão contei tudo isso aos mágicos; nenhum deles pôde dar-me a explicação”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1221, 41, 25, 'José disse ao faraó: “O (duplo) sonho do faraó reduz-se a um só. Deus revelou ao faraó o que ele vai fazer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1222, 41, 26, 'As sete belas vacas são sete anos, e as sete belas espigas, igualmente, sete anos; o sonho é um só.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1223, 41, 27, 'As sete vacas magras e feias que saíram em seguida são também sete anos; e as sete espigas vazias e queimadas pelo vento do oriente serão sete anos de miséria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1224, 41, 28, 'É como eu disse ao faraó: Deus lhe revela o que vai fazer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1225, 41, 29, 'Haverá sete anos de grande abundância para todo o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1226, 41, 30, 'Virão em seguida sete anos de miséria que farão esquecer toda a abundância no Egito. A fome devastará o país.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1227, 41, 31, 'E a abundância do país não será mais notada, por causa da fome que se seguirá, porque será violenta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1228, 41, 32, 'Se o sonho se repetiu duas vezes ao faraó, é que a coisa está bem decretada da parte de Deus, que vai apressar-se em executá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1229, 41, 33, 'Agora, pois, escolha o rei um homem sábio e prudente para pô-lo à testa do país.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1230, 41, 34, 'Nomeie também o faraó administradores no país, que recolham a quinta parte das colheitas do Egito, durante os sete anos de abundância.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1231, 41, 35, 'Eles ajuntarão todos os produtos destes bons anos que vêm, e armazenarão o trigo nas cidades, à disposição do faraó como provisões a conservar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1232, 41, 36, 'Esses mantimentos formarão para o país uma reserva em previsão dos sete anos de fome que assolarão o Egito. Dessa forma, o país não será arruinado pela fome”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1233, 41, 37, 'Essas palavras agradaram o faraó e toda a sua gente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1234, 41, 38, '“Poderíamos – disse-lhes ele – encontrar um homem que tenha, tanto como este, o espírito de Deus?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1235, 41, 39, 'E disse em seguida a José: “Pois que Deus te revelou tudo isso, não haverá ninguém tão prudente e tão sábio como tu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1236, 41, 40, 'Tu mesmo serás posto à frente de toda a minha casa, e todo o meu povo obedecerá à tua palavra: só o trono me fará maior do que tu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1237, 41, 41, '“Vês – disse-lhe ainda – eis que te ponho à testa de todo o Egito.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1238, 41, 42, 'E o faraó, tirando o anel de sua mão, pôs na mão de José; e o fez revestir-se de vestes de linho fino e meteu-lhe ao pescoço um colar de ouro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1239, 41, 43, 'E, fazendo-o montar no segundo dos seus carros, mandou que se clamasse diante dele: “Ajoelhai-vos!”. É assim que ele foi posto à frente de todo o Egito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1240, 41, 44, 'e o faraó disse-lhe: “Sou eu o faraó: sem tua permissão não se moverá a mão nem o pé em toda a terra do Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1241, 41, 45, 'O faraó chamou a José Safenat Fanec, e deu-lhe por mulher Asenet, filha de Putifar, sacerdote de On.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1242, 41, 46, 'José tinha trinta anos quando se apresentou diante do faraó, rei do Egito. Ele retirou-se da casa do faraó e percorreu todo o país.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1243, 41, 47, 'A terra produziu abundantemente durante os sete anos de fertilidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1244, 41, 48, 'José ajuntou todo o produto destes sete anos no Egito e os pôs em reserva nas cidades, e os mantimentos dos campos que estavam ao redor de cada cidade, guardou-os na mesma cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1245, 41, 49, 'José ajuntou trigo como a areia do mar, em tal quantidade que se não podia contar, pois que ela excedia a toda a medida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1246, 41, 50, 'Antes que viesse o ano de fome, nasceram a José dois filhos, que lhe deu Asenet, filha de Putifar, sacerdote de On.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1247, 41, 51, 'José chamou ao primeiro Manassés, “porque, dizia ele, Deus fez-me esquecer de todo o meu trabalho e de toda a minha família”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1248, 41, 52, 'Chamou ao segundo Efraim, “porque – disse ele – Deus tornou-me fecundo na terra de minha aflição”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1249, 41, 53, 'Tendo acabado os sete anos de abundância que houve no Egito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1250, 41, 54, 'os sete anos de miséria começaram, assim como o tinha predito José. A fome assolou todos os países, mas havia pão em toda a terra do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1251, 41, 55, 'Em seguida, houve fome também no Egito, e o povo clamou ao faraó pedindo pão. Este disse a todos os egípcios: “Ide a José, e fazei o que ele vos disser”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1252, 41, 56, 'Como a fome assolasse toda a terra, José abriu todos os celeiros e vendeu víveres aos egípcios. Mas a penúria cresceu no Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1253, 41, 57, 'E de toda a terra vinha-se ao Egito comprar trigo a José, porque a fome era violenta em toda a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1254, 42, 1, 'Jacó, sabendo que havia trigo no Egito, disse aos seus filhos: “Por que estais olhan­do uns para os outros?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1255, 42, 2, 'Eu soube que há trigo no Egito. Descei lá e comprai-o para nós; podere­mos assim viver e escaparemos à morte”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1256, 42, 3, 'E os dez irmãos de José desceram ao Egito para comprar trigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1257, 42, 4, 'Jacó não deixou partir com seus irmãos Benjamim, irmão de José, “com medo – pensava ele – de que lhe acontecesse alguma desgraça”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1258, 42, 5, 'Os filhos de Israel chegaram, pois, no meio de uma multidão de outros para comprar víveres, porque a fome reinava na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1259, 42, 6, 'José era o governador de toda a região, e era ele quem vendia o trigo a todo o mundo. Desde sua chegada, os irmãos de José prostraram-se diante dele com o rosto por terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1260, 42, 7, 'José reconheceu-os imedia­tamente, mas, comportando-se com eles como um estrangeiro, disse-lhes com rudeza: “Donde vindes?”. “Da terra de Canaã – responderam eles – para comprar víveres.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1261, 42, 8, 'José reconheceu os seus irmãos, mas eles não o reconheceram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1262, 42, 9, 'E lembrava-se dos sonhos que tivera outrora a respeito deles; disse-lhes: “Vós sois espiões: viestes explorar os pontos fracos do país”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1263, 42, 10, '“Não, meu senhor – responderam – teus servos vieram comprar víveres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1264, 42, 11, 'Somos todos filhos do mesmo pai, somos gente honesta; teus servos não são espiões.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1265, 42, 12, '“Não é verdade –, disse-lhes ele – viestes explorar os pontos fracos do país.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1266, 42, 13, 'Eles responderam: “Somos doze irmãos, filhos do mesmo pai, na terra de Canaã. O mais novo está agora em casa de nosso pai, o outro já não existe”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1267, 42, 14, 'José disse-lhes: “É bem como eu disse: sois espiões.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1268, 42, 15, 'Sereis, aliás, postos à prova: pela vida do faraó, não saireis daqui antes que tenha vindo vosso irmão mais novo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1269, 42, 16, 'Mandai um de vós buscá-lo; enquanto isso, ficareis prisioneiros. Vossas palavras serão assim provadas, e veremos se disses­tes a verdade. Do contrário, pela vida do faraó, sois espiões!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1270, 42, 17, 'E mandou metê-los na prisão durante três dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1271, 42, 18, 'No terceiro dia, José disse-lhes: “Fazei isto, e vivereis, porque sou cheio do temor a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1272, 42, 19, 'Se sois gente de bem, que um dentre vós fique detido em prisão; e os outros partam levando o trigo para alimentar vossas famílias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1273, 42, 20, 'Trazei-me então vosso irmão mais novo, para que eu possa verificar a verdade de vossas palavras, e não morrereis”. Foi o que fizeram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1274, 42, 21, 'Disseram uns aos outros: “Em verdade, somos culpados pelo crime cometido contra o nosso irmão, porque víamos a angústia de sua alma quando ele nos suplicava, e não o escutamos! Eis por que veio sobre nós esta desgraça!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1275, 42, 22, '“Não vos tinha eu dito, disse-lhes Rúben, para não pecardes contra o menino? Não quisestes ouvir-me, e eis agora que nos é reclamado o seu sangue!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1276, 42, 23, 'Ora, não sabiam que José os compreendia, porque lhes tinha falado por meio de um intér­prete.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1277, 42, 24, 'E José afastou-se deles para chorar. Voltou em seguida e falou-lhes; e escolheu Simeão, ao qual mandou prender na presença deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1278, 42, 25, 'José ordenou depois que se enchessem de trigo os seus sacos, e que se pusesse o dinheiro de cada um em seu saco de viagem, e também que se lhes dessem provisões para o caminho: assim foi feito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1279, 42, 26, 'Eles carregaram o trigo sobre os seus jumentos e partiram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1280, 42, 27, 'Na estalagem, abrindo um deles o seu saco para dar de comer ao seu jumento, viu que o seu dinheiro estava na boca do saco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1281, 42, 28, '“Devolve­ram-me o meu dinheiro – disse ele aos seus irmãos –; ei-lo aqui no meu saco!” Desfaleceu-se-lhes o coração, e, tomados de espanto, disseram uns aos outros: “Que é isto que Deus nos fez?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1282, 42, 29, 'Voltaram para junto de Jacó, seu pai, na terra de Canaã, e contaram-lhe nestes termos tudo o que lhes tinha acontecido:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1283, 42, 30, '“O homem que governa o país nos falou asperamente e nos tomou por espiões.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1284, 42, 31, 'Dissemos-lhe que éramos gente honesta, e não espiões;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1285, 42, 32, 'que éramos doze irmãos, filhos do mesmo pai, dos quais um já não existia mais, e o mais novo estava no momento com nosso pai, na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1286, 42, 33, 'O governador do país disse-nos: por isso reconhecerei se sois gente de bem: deixai junto de mim um de vossos irmãos, levai o trigo que precisais para alimentar vossas famílias, e parti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1287, 42, 34, 'Vós me conduzireis então vosso irmão mais novo: assim saberei que não sois espiões, mas gente honesta. Eu vos devolverei então vosso irmão, e podereis negociar no país”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1288, 42, 35, 'E, esvaziando os seus sacos, eis que o pacote de dinheiro de cada um se encontrava em seu saco. Quando eles e seu pai viram seu dinheiro, tiveram medo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1289, 42, 36, 'Jacó disse-lhes: “Vós me tirais os meus filhos! José já não existe, Simeão tampouco, e quereis me tomar ainda Benjamim! Tudo vem cair sobre mim!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1290, 42, 37, 'Rúben disse-lhe: “Tira a vida aos meus dois filhos, se eu não te reconduzir Benja­mim! Confia-o a mim: eu o trarei de volta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1291, 42, 38, '“Meu filho – tornou Jacó – não descerá convosco, porque seu irmão morreu, e só resta ele. Se lhe acontecesse um acidente nesta viagem que ides fazer, faríeis descer os meus cabelos brancos à habitação dos mortos, sob o peso da dor.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1292, 43, 1, 'A fome pesava sobre o país.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1293, 43, 2, 'E tendo acabado o trigo trazido do Egito, o pai disse aos seus filhos: “Voltai e comprai-nos um pouco de víveres”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1294, 43, 3, 'Ju­dá respondeu-lhe: “Aquele homem nos declarou formalmente que não voltássemos à sua presença sem levar conosco nosso irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1295, 43, 4, 'Se mandas nosso irmão conosco, desceremos para comprar víveres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1296, 43, 5, 'Mas, se o não deixas ir, não desceremos, porque ele nos disse: Não sereis admitidos em minha presença se vosso irmão não estiver convosco”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1297, 43, 6, 'Israel disse: “Por que me fizestes este mal, dando-lhe a conhecer que tínheis ainda um irmão?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1298, 43, 7, '“Aquele homem – responderam eles – perguntou por nós e por nossa família, e quis saber se nosso pai vivia ain­da, se tínhamos outro irmão; e respondemos às suas perguntas. Podíamos, porventura, adivinhar que ele nos ia mandar levar a ele o nosso irmão?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1299, 43, 8, 'E Judá disse a Israel,­ seu pai: “Deixa partir o menino comigo, e nos poremos a caminho para essa viagem. Desse modo poderemos viver, e escaparemos à morte, nós, tu e nossos filhinhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1300, 43, 9, 'Eu respondo por ele: é de mim que tu o reclamarás. Se eu não o recon­duzir e não o recolocar diante de ti, serei eternamente culpado diante de ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1301, 43, 10, 'Se não tivéssemos demorado tanto, certamente já pela segunda vez estaríamos de volta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1302, 43, 11, '“Se assim é – disse-lhes Israel, seu pai – tomai em vossas bagagens os melhores produtos da terra, e levai-os como presente a esse homem: um pouco de bálsamo, um pouco de mel, resina, ládano, nozes de pistácia e amêndoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1303, 43, 12, 'Levai também convosco o dinheiro em dobro para restituir a soma que encontras­­tes na boca dos sacos, certamente por engano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1304, 43, 13, 'Tomai vosso irmão, parti e ide ter com esse homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1305, 43, 14, 'Que o Deus Todo-poderoso vos faça ganhar os favores desse homem, a fim de que ele deixe voltar vosso irmão, juntamente com Benjamim. Quanto a mim, se devo ser privado de meus filhos, paciência, que eu seja privado deles!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1306, 43, 15, 'Tomaram, pois, consigo o presente e uma soma dobrada de dinheiro, assim como Benjamim, e partiram para o Egito. E apresentaram-se a José.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1307, 43, 16, 'José, vendo-os e com eles Benja­mim, disse ao seu intendente: “Faze entrar estes homens na casa, mata um animal, e prepara-o, pois comerão comigo ao meio-dia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1308, 43, 17, 'Fez o intendente como José tinha dito: introduziu-os na casa de José.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1309, 43, 18, 'Vendo isso, ficaram amedrontados: “É – diziam eles – por causa do dinheiro, encontrado da outra vez nos nossos sacos, que nos conduzem aqui. Vão-nos assaltar, cair sobre nós, escravizar-nos e apoderar-se de nossos jumentos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1310, 43, 19, 'Então, aproximando-se do intendente da casa de José, falaram-lhe à entrada da casa:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1311, 43, 20, '“Desculpa, meu senhor – disseram eles – viemos já uma vez comprar víveres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1312, 43, 21, 'Quando chegamos à estalagem e abrimos nossos sacos, o dinheiro de cada um se encontrava na boca de seu saco: era o peso exato do dinheiro. Tornamos a trazê-lo conosco;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1313, 43, 22, 'e trazemos, ao mesmo tempo, outra quantia para comprar víveres. Não sabemos quem tenha metido nosso dinheiro em nossos sacos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1314, 43, 23, '“Ficai tranquilos – respondeu-lhes ele – nada temais. É o vosso Deus, o Deus de vossos pais, quem vos pôs um tesouro em vossos sacos; o vosso di­nheiro me foi entregue.” Depois trouxe-lhes Simeão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1315, 43, 24, 'Fê-los em seguida entrar na casa de José, deu-lhes água para lavarem os pés e forragem para os seus jumentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1316, 43, 25, 'E, enquanto esperavam por José, que devia voltar ao meio-dia, preparavam o seu presente, pois foi-lhes anunciado que comeriam em casa dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1317, 43, 26, 'Logo que José entrou em casa, ofereceram-lhe os presentes que tinham trazido, prostrando-se diante dele até a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1318, 43, 27, 'Ele perguntou pela saúde deles e ajuntou: “Vosso velho pai, do qual me falastes, vai bem? Ainda vive?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1319, 43, 28, '“Teu servo, nosso pai, está passando bem; e vive ainda” – res­ponderam-lhe inclinando-se até o solo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1320, 43, 29, 'Então, levantando os olhos,­ José viu Benjamim, seu irmão, filho de sua mãe. “É este – disse ele – vosso irmão mais novo do qual me falastes?” E ajuntou: “Que Deus te faça misericórdia, meu filho!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1321, 43, 30, 'E retirou-se precipitadamente, porque suas entranhas se tinham comovido por causa de seu irmão, e tinha vontade de chorar; entrou em seu quarto e deu livre curso às lágrimas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1322, 43, 31, 'Depois de ter lavado o rosto saiu e, procurando dominar-se, disse: “Servi a mesa”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1323, 43, 32, 'Foi-lhe servido à parte, seus irmãos também à parte, e igualmente à parte os egípcios, seus comensais, porque lhes é proibido comer com hebreus, por ser abominável para eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1324, 43, 33, 'Os irmãos de José foram colocados diante dele, desde o mais velho até o mais novo, segundo sua idade, o que lhes fez olhar uns para os outros assombrados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1325, 43, 34, 'José mandou que se lhes trouxessem porções de sua própria mesa, e a parte de Benjamim foi cinco vezes maior que a dos outros. Eles beberam e alegraram-se com ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1326, 44, 1, 'José deu esta ordem ao intendente de sua casa: “Enche de víveres os sacos destes homens tanto quanto possam conter, e põe o dinheiro de cada um na boca do saco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1327, 44, 2, 'Porás minha taça de prata na boca do saco do mais novo, com o preço do seu trigo”. E fez o inten­den­te como José lhe mandara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1328, 44, 3, 'De manhã, ao romper do dia, foram despedidos com seus jumentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1329, 44, 4, 'Dei­xaram a cidade, mas, não tendo ido ainda muito longe, José disse ao seu intendente: “Levanta-te e persegue estes homens e, quando os tiveres alcançado, lhes dirás: Por que pagastes o bem com o mal?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1330, 44, 5, 'A taça que roubastes é aquela em que bebe o meu senhor e da qual se serve para suas adivinhações. Fizestes muito mal”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1331, 44, 6, 'O intendente, tendo-os alcançado, falou-lhes desse modo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1332, 44, 7, 'Eles responderam-lhe: “Por que fala assim o meu senhor? Longe de teus servos a ideia de fazerem semelhante coisa!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1333, 44, 8, 'Nós te trouxe­mos de Canaã o dinheiro que tínhamos encontrado na boca dos sacos. Por que, pois, haveríamos de roubar prata ou ouro na casa de teu senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1334, 44, 9, 'Que aquele dos teus servos com quem for encontrada a taça morra, e, ao mesmo tempo, nós nos tornemos escravos do meu senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1335, 44, 10, '“Está bem! – disse-lhes ele. Seja como dissestes! Aquele com quem for encontrada a taça será meu escravo. Vós outros sereis livres.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1336, 44, 11, 'E, imediatamente, pôs cada um o seu saco por terra e o abriu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1337, 44, 12, 'O intendente revistou-os começando pelo mais velho e acabando pelo mais novo; e a taça foi encontrada no saco de Benjamim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1338, 44, 13, 'Eles rasgaram suas vestes e, tendo cada um carregado de novo o seu jumento, voltaram para a cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1339, 44, 14, 'Judá e seus irmãos entraram em casa de José, que estava ainda em sua casa, e prostraram-se por terra diante dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1340, 44, 15, 'José disse-lhes: “Que é isso que fizestes? Não sabíeis que sou um homem dotado da faculdade de adivinhar?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1341, 44, 16, 'Judá respondeu: “Que podemos dizer a meu senhor? Que falaremos? Como nos justificar? Deus descobriu o crime de teus servos. Somos os escravos do meu senhor, nós e aquele junto de quem foi encontrada a taça”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1342, 44, 17, '“Longe de mim – replicou José – o pensamento de agir dessa forma! Mas aquele em poder de quem foi encontrada a taça, esse será o meu escravo. Vós outros, voltai em paz para junto de vosso pai.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1343, 44, 18, 'Então Judá adiantou-se e disse a José: “Rogo-te, meu se­nhor, que permitas ao teu servo dizer uma palavra aos ouvidos do meu senhor, e não se acenda a tua ira contra o teu servo, porque tu és como o próprio faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1344, 44, 19, 'Meu senhor havia perguntado aos seus servos: ‘Tendes ainda vosso pai? Tendes um irmão?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1345, 44, 20, 'E nós havíamos respondido ao meu senhor que tínhamos um velho pai e um irmão­zinho, filho de sua velhice, do qual o irmão havia morrido; e que, como ele foi deixado o único de sua mãe, seu pai o amava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1346, 44, 21, 'Disseste aos teus servos: ‘Tra­zei-o para junto de mim, a fim de que o veja com meus olhos’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1347, 44, 22, 'Havíamos respondido ao meu senhor que o menino não podia abandonar o seu pai, pois, se o fizesse, seu pai morreria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1348, 44, 23, 'E disseste aos teus servos: ‘Se vosso irmãozinho não vier convosco, não sereis admitidos na minha presença’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1349, 44, 24, 'Quando voltamos para a casa do teu servo, nosso pai, referimos-lhe as palavras do meu senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1350, 44, 25, 'E, quando nosso pai nos mandou voltar para comprar alguns víveres,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1351, 44, 26, 'respondemos-lhe: ‘Não podemos descer. Mas, se nosso irmão mais novo nos acompanhar, o faremos, pois que não seremos admitidos na presença do governador, se nosso irmão não for conosco’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1352, 44, 27, 'Teu servo, nosso pai, nos replicou: ‘Sabeis que minha mulher me deu dois filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1353, 44, 28, 'Um desapareceu de minha casa, e eu disse: Certamente foi devorado. E não mais o revi até hoje.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1354, 44, 29, 'Se me tirais ainda este, e lhe acontecer alguma desgraça, fareis descer os meus cabelos brancos à habitação dos mortos, sob o peso da dor’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1355, 44, 30, 'Se agora volto para junto de teu servo, meu pai, sem levar conosco o menino, ele, cuja vida está ligada à do menino,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1356, 44, 31, 'desde que notar que ele não está conosco, morrerá. E teus servos terão feito descer à habitação dos mortos, sob o peso da aflição, os cabelos brancos do teu servo, nosso pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1357, 44, 32, 'Ora, o teu servo respondeu pelo menino junto de meu pai; e disse-lhe que, se ele não o reconduzisse, seria eternamente culpado para com seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1358, 44, 33, 'Rogo-te, pois: aceita que teu servo fique escravo de meu se­nhor em lugar do menino, para que este possa voltar com seus irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1359, 44, 34, 'Como poderia eu apresentar-me diante do meu pai, se o menino não for comigo? Oh, eu não poderia suportar a dor que sobreviria a meu pai!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1360, 45, 1, 'Então José, já não se podendo conter diante de todos os assistentes, exclamou: “Fazei sair todo o mundo”. Desse modo, ninguém ficou com ele, quando se deu a conhecer aos seus irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1361, 45, 2, 'Pôs-se a chorar tão alto que os egípcios da casa do faraó o ouviram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1362, 45, 3, 'E disse aos seus irmãos: “Eu sou José! Meu pai vive ainda?” Mas não lhe puderam res­ponder, porque estavam pasmados de se encontrarem diante dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1363, 45, 4, '“Aproximai-vos” – disse-lhes ele –; e eles aproximaram-se. E ele disse-lhes: “Eu sou José, vosso irmão, que vendestes para o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1364, 45, 5, 'Mas agora não vos entristeçais, nem tenhais remorsos de me terdes vendido para ser conduzido aqui. É para vos conservar a vida que Deus me enviou adiante de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1365, 45, 6, 'Porque eis já dois anos que a fome assola a terra, e haverá ainda cinco anos sem semeadura nem colheita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1366, 45, 7, 'Deus enviou-me adiante de vós para que subsista um resto de vossa raça na terra, e para vos conservar a vida por uma grande libertação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1367, 45, 8, 'Não sois vós, pois, que me haveis mandado para aqui, mas Deus mesmo. Ele tornou-me como o pai do faraó, chefe de toda a sua casa e governador de todo o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1368, 45, 9, 'Apressai-vos em voltar para junto de meu pai e dizei-lhe: ‘Eis o que diz o teu filho José: Deus elevou-me ao cargo de chefe de todo o Egito. Vem para junto de mim sem demora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1369, 45, 10, 'Habitarás na terra de Gessen, bem perto de mim, com teus filhos, teus netos, tuas ovelhas, teus bois e tudo o que te pertence.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1370, 45, 11, 'Eu te sustentarei, pois haverá ainda cinco anos de fome; e assim não cairás na miséria, nem tu, nem tua casa, nem nada do que te pertence’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1371, 45, 12, 'Vereis com vossos olhos, e meu irmão Benjamim também, que sou bem eu quem vos fala.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1372, 45, 13, 'Contai ao meu pai as honras que recebo no Egito, e tudo o que vistes, e depois apressai-o para que venha para cá”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1373, 45, 14, 'Então ele jogou-se ao pescoço de seu irmão Benjamim e pôs-se a chorar; Benjamim também chorou sobre os seus ombros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1374, 45, 15, 'Beijou em seguida todos os seus irmãos, enquanto os abraçava. Depois os irmãos conversaram com ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1375, 45, 16, 'A notícia da chegada dos irmãos de José espalhou-se logo na casa do faraó, e foi bem acolhida pelo faraó e por todo o seu pessoal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1376, 45, 17, 'Ele disse a José: “Dize a teus irmãos: ‘Eis o que ides fazer: carregai vossos animais e voltai à terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1377, 45, 18, 'Tomai vosso pai e vossas famílias e vinde para junto de mim. Daremos a vós o que há de melhor no Egito e vos alimentarei com a gordura da terra’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1378, 45, 19, 'Encarrego-te de dizer-lhes: Eis o que ides fazer: Tomai carros no Egito para vossos filhos e vossas mulheres, trazei vosso pai e vinde!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1379, 45, 20, 'Não façais caso do que tereis de deixar, porque o que há de melhor em todo o Egito é vosso”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1380, 45, 21, 'Assim fizeram os filhos de Israel. Seguindo a ordem do faraó, José deu-lhes carros e provisões para o caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1381, 45, 22, 'Deu-lhes também a todos mudas de roupas; a Benjamim, porém, deu trezentas moedas de prata e cinco mudas de roupas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1382, 45, 23, 'Mandou, igualmente, ao seu pai dez jumentos carregados dos me­lhores produtos do Egito e dez jumentas carregadas de trigo, pão e provisões para sua viagem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1383, 45, 24, 'E, despedindo seus irmãos que partiam, disse-lhes: “Não vos dei­xeis abalar pelo caminho”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1384, 45, 25, 'Partiram do Egito e chegaram junto de Jacó, seu pai, na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1385, 45, 26, 'E anunciaram-lhe a boa-nova: “José vive ainda – disseram-lhe eles –; e é mesmo ele quem governa todo o Egito”. Mas o coração de Jacó permaneceu frio, porque não acreditava no que ouvia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1386, 45, 27, 'Entretanto, quando lhe disseram todas as palavras que José lhes havia dito, e viu os carros que José tinha enviado para o transportar, seu espírito se reanimou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1387, 45, 28, '“Basta! – disse ele –; José, meu filho, vive ainda. Irei e o verei antes de morrer.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1388, 46, 1, 'Israel partiu com tudo o que lhe pertencia. Chegou a Bersabeia, onde ofereceu sacrifícios ao Deus de seu pai Isaac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1389, 46, 2, 'Em uma visão noturna, Deus disse-lhe: “Jacó! Jacó!”. “Eis-me aqui” – res­pondeu ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1390, 46, 3, 'E Deus disse: “Eu sou Deus, o Deus de teu pai. Não temas descer ao Egito, porque ali farei de ti uma grande nação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1391, 46, 4, 'Descerei contigo ao Egito, e eu mesmo te farei de novo subir de lá. José te fechará os olhos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1392, 46, 5, 'E Jacó deixou Bersabeia. Os filhos de Israel levaram seu pai, assim como seus filhos e suas mulheres, nos carros que o faraó tinha enviado para os transportar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1393, 46, 6, 'Tomaram também seus rebanhos e os bens que tinham adquirido na terra de Canaã,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1394, 46, 7, 'e Jacó, com toda a sua família, partiu para o Egito. Levou os seus filhos e seus netos, suas filhas e suas netas, enfim, toda a sua família.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1395, 46, 8, 'Eis os nomes dos filhos de Israel que foram para o Egito: Jacó e seus filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1396, 46, 9, 'O primogênito de Jacó: Rúben. Os filhos de Rúben: Henoc, Falu, Hesron e Carmi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1397, 46, 10, 'Os filhos de Simeão: Jamuel, Jamin, Aod, Jaquin, Soar e Saul, filho da cananeia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1398, 46, 11, 'Os filhos de Levi: Gérson, Caat e Merari.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1399, 46, 12, 'Os filhos de Judá: Her, Onã, Sela, Farés e Zara. Her e Onã, porém, morreram em Canaã. Os filhos de Farés: Hesron e Hamul.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1400, 46, 13, 'Os filhos de Issacar: Tola, Fua, Jasub e Semron.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1401, 46, 14, 'Os filhos de Zabulon: Sared, Elon e Jaelel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1402, 46, 15, 'São estes os filhos que Lia deu a Jacó em Padã-Aram, assim como sua filha Dina. Total de seus filhos e filhas: trinta e três pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1403, 46, 16, 'Os filhos de Gad: Safon, Hagi, Suni, Esebon, Eri, Arodi e Areli.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1404, 46, 17, 'Os filhos de Aser: Jamne, Jesua, Jessui e Beria, assim como sua irmã Sara. Os filhos de Beria: Héber e Melquiel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1405, 46, 18, 'São estes os filhos que Zelfa, dada por Labão à sua filha Lia, deu à luz a Jacó: dezesseis pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1406, 46, 19, 'Filhos de Raquel, mulher de Jacó: José e Benjamim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1407, 46, 20, 'No Egito, José tivera de Asenet, filha de Putifar, sacerdote de On, Manassés e Efraim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1408, 46, 21, 'Os filhos de Benjamim: Bela, Bocor, Asbel, Gera, Naamã, Equi, Ros, Mofim, Ofim e Ared.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1409, 46, 22, 'São estes os filhos que Raquel deu a Jacó. Total: catorze pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1410, 46, 23, 'Filho de Dã: Husim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1411, 46, 24, 'Filhos de Neftali: Jasiee, Guni, Jeser e Salém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1412, 46, 25, 'São estes os filhos que Bala, dada por Labão à sua filha Raquel, deu à luz a Jacó. Total: sete pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1413, 46, 26, 'O total das pessoas saídas de Jacó, que vieram com ele para o Egito, sem contar as mulheres de seus filhos, era de setenta ao todo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1414, 46, 27, 'José teve dois filhos nascidos no Egito. O total das pessoas da família de Jacó que foram para o Egito era de setenta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1415, 46, 28, 'Jacó tinha enviado Judá adian­te dele para informar a José de sua chegada a Gessen. Quando chegaram a Gessen,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1416, 46, 29, 'José mandou preparar o seu carro e montou para ir ao encontro de seu pai em Gessen. E, logo que o viu, lançou-se nos seus braços e chorou longo tempo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1417, 46, 30, '“Agora posso morrer – disse-lhe Israel – porque vi o teu rosto, e vives ainda!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1418, 46, 31, 'José disse aos seus irmãos: “Vou avisar o faraó, dizendo: ‘Meus irmãos e a família de meu pai, que estavam em Canaã, vieram para junto de mim;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1419, 46, 32, 'os homens são pastores, criadores de animais, e trouxe­ram suas ovelhas e seus bois com tudo o que lhes pertence’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1420, 46, 33, 'Quando o faraó mandar chamar-vos e vos perguntar qual é vossa profissão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1421, 46, 34, 'lhes respondereis: ‘Teus servos foram sempre criado­res de animais, desde sua juventude até o presente, e nossos pais também’. Desse modo podereis ficar na terra de Gessen, porque os egípcios têm aversão aos pastores”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1422, 47, 1, 'José foi, pois, informar o faraó: “Meu pai – disse ele – e meus irmãos chegaram da terra de Canaã com suas ovelhas, seus bois e tudo o que lhes pertence. Eles estão na terra de Gessen”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1423, 47, 2, 'José levara cinco de seus irmãos, que apresentou ao faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1424, 47, 3, 'Este disse-lhes: “Qual é vossa profissão?”. Responderam: “Teus servos são pastores, como sempre o foram nossos pais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1425, 47, 4, 'Viemos – ajuntaram eles – para morar no país porque não há mais pastagem para os rebanhos de teus servos, sendo muito grande a fome na terra de Canaã. Permite, pois, aos teus servos habitarem na terra de Gessen”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1426, 47, 5, 'O faraó disse a José: “Teu pai e teus irmãos vieram para junto de ti; a terra do Egito está à tua disposição: instala-os na me­lhor parte do país.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1427, 47, 6, 'Que eles habitem na terra de Gessen; e, se conheces entre eles alguns que sejam capazes, os porás à fren­te dos rebanhos que me pertencem”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1428, 47, 7, 'José fez então vir Jacó, seu pai, e o apresentou ao faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1429, 47, 8, 'Jacó abençoou o faraó. Este disse-lhe: “Que idade tens?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1430, 47, 9, 'Jacó respondeu-lhe: “O número dos anos de minha peregrinação é de cento e trinta anos. Curtos e maus foram os anos de minha vida, e não atingiriam o número dos que viveram meus pais durante sua peregrinação”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1431, 47, 10, 'E, depois de ter abençoado o faraó, Jacó despediu-se dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1432, 47, 11, 'José instalou seu pai e seus irmãos em uma propriedade do país do Egito, na melhor parte da região, a terra de Ramsés, como o tinha ordenado o faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1433, 47, 12, 'E José forneceu víveres a seu pai, a seus irmãos e a toda sua família, proporcionalmente ao número dos filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1434, 47, 13, 'E faltou pão em toda a terra, porque a fome era tão violenta que a terra do Egito e a terra de Canaã estavam esgotadas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1435, 47, 14, 'José tinha ajuntado todo o dinheiro que se encontrava no Egito e em Canaã, como preço do trigo que compravam, e o tinha depositado no tesouro do faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1436, 47, 15, 'Quando havia acabado todo o di­nheiro do Egito e de Canaã, todos os egípcios vieram dizer a José: “Dá-nos pão. Por que morreremos na tua presença por falta de dinheiro?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1437, 47, 16, 'José respondeu: “Trazei vossos animais, se não tendes dinheiro, e vos darei pão em troca”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1438, 47, 17, 'Trouxeram, pois, seus animais a José, o qual lhes deu pão em troca dos cavalos, dos rebanhos de ovelhas, dos bois e dos jumentos. Dessa forma, naquele ano, fornecera-lhes pão em troca de todos os seus rebanhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1439, 47, 18, 'E aquele ano passou. No ano seguinte, voltaram a ele e disseram-lhe: “Não podemos ocultar do meu senhor que o dinhei­ro, tendo-se esgotado, e nossos animais, tendo já passado para as mãos de meu senhor, não nos restam agora senão nossos corpos e nossas terras para oferecer ao meu senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1440, 47, 19, 'Por que perecermos diante de teus olhos, nós e nossas terras? Compra-nos a nós e a nossas terras em troca de pão, e nós e nossas terras seremos escravos do faraó. Dá-nos sementes, para que vivamos e não morramos, e não seja desolado o nosso solo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1441, 47, 20, 'José adquiriu, assim, para o faraó, todas as terras do Egito, porque cada egípcio vendia o seu campo, obrigado pela fome; e o país tornou-se propriedade do faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1442, 47, 21, 'De um extremo a outro do território, ele reduziu a população à servidão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1443, 47, 22, 'As terras dos sacerdotes foram as únicas que não comprou, porque estes recebiam do faraó uma ração determinada para o seu sustento. Por isso, não venderam suas propriedades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1444, 47, 23, 'José disse ao povo: “Eu vos comprei hoje, vós e vossas terras, para o faraó.­ Aqui tendes sementes: semeai vossos campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1445, 47, 24, 'No tempo da colheita, dareis a quinta parte ao faraó: as outras quatro partes vos servirão para semente do campo e para vosso alimento com vossos filhos e os que moram convosco”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1446, 47, 25, 'Eles responderam: “Tu nos salvaste a vida. Tenhamos graça aos olhos de meu senhor e seremos de bom grado escravos do faraó”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1447, 47, 26, 'José instituiu assim uma lei que ainda hoje está em vigor, em virtude da qual uma quinta parte da colheita pertence ao faraó. Somente as terras dos sacerdotes não se tornaram sua propriedade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1448, 47, 27, 'Israel estabeleceu-se, pois, no Egi­to, na terra de Gessen. Adqui­riram ali propriedades, foram fecundos e multiplicaram-se grandemente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1449, 47, 28, 'Jacó viveu ainda dezes­sete anos no Egito. A duração de sua vida foi de cento e quarenta e sete anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1450, 47, 29, 'E, aproximando-se do seu termo os dias de Israel, chamou o seu filho José e disse-lhe: “Se achei graça diante de teus olhos, põe, rogo-te, tua mão debaixo de minha coxa e promete-me, com toda a bondade e fidelidade, que não me enterrarás no Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1451, 47, 30, 'Quando eu me tiver deitado com meus pais, me levarás para fora do Egito e me enterrarás junto deles em seu túmulo”. José respondeu: “Farei como dizes”. “Jura-me” – replicou Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1452, 47, 31, 'José jurou-lhe e Israel prostrou-se sobre a cabeceira de sua cama.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1453, 48, 1, 'Depois disso, vieram anunciar a José: “Teu pai está doente”. Tomou então com ele seus dois filhos, Manassés e Efraim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1454, 48, 2, 'Jacó foi avisado disso: “Eis – disseram-lhe – que o teu filho José vem te ver”. Israel, reunindo suas forças, assentou-se no seu leito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1455, 48, 3, 'E disse a José: “O Deus Todo-poderoso apareceu-me em Luza, na terra de Canaã, e abençoou-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1456, 48, 4, 'Disse-me: ‘Eu te tornarei fecundo e te multiplicarei até fazer de ti uma assembleia de povos, e darei esta terra à tua posteridade em possessão eterna’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1457, 48, 5, 'Agora, os dois filhos que te nasceram no Egito antes que eu viesse para junto de ti, são meus filhos: Efraim e Manassés são meus, com o mesmo título que Rúben e Simeão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1458, 48, 6, 'Os filhos, porém, que tiveste depois deles, são teus: é conforme o nome de seus irmãos que eles terão parte na repartição da heran­ça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1459, 48, 7, 'Quando eu voltava de Padã-Aram, tua mãe Raquel morreu em caminho, perto de mim, na terra de Canaã, a alguma distância de Éfrata; foi ali que a enterrei, no caminho de Éfrata, hoje Belém”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1460, 48, 8, 'Israel viu os filhos de José e disse: “Quem são estes?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1461, 48, 9, '“São – respondeu José – os filhos que Deus me deu aqui.” “Faze-os aproximarem-se, para que eu os abençoe.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1462, 48, 10, 'Os olhos de Israel tinham-se enfraquecido tanto pela idade, que já não podia ver. José fê-los aproximarem-se dele e Israel, tomando-os em seus braços, beijou-os.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1463, 48, 11, 'Depois disse a José: “Não esperava mais rever-te, e eis que Deus me fez ver teus filhos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1464, 48, 12, 'José tirou-os dos joelhos de seu pai e prostrou-se com o rosto por terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1465, 48, 13, 'Tomou depois os dois, Efraim pela mão direita, para colocá-lo à esquerda de Israel, e Manassés pela mão esquerda, para colocá-lo à direita de Israel, e fê-los aproximarem-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1466, 48, 14, 'Mas Israel estendeu a mão direita e pô-la sobre a cabeça de Efraim, o caçula, e a mão esquerda sobre a cabeça de Manassés. Cruzou assim as mãos porque Manassés era o primogênito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1467, 48, 15, 'Israel abençoou José, dizendo: “O Deus em cujo caminho andaram meus pais Abraão e Isaac, o Deus que tem sido o meu pastor durante toda a minha vida até este dia,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1468, 48, 16, 'o anjo que me guardou de todo o mal, abençoe estes meninos! Seja perpetuado neles o meu nome e o de meus pais Abraão e Isaac, e multipliquem-se abundantemente nesta terra!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1469, 48, 17, 'Vendo José que seu pai tinha colocado a mão direita sobre a cabeça de Efraim, contrariou-se e tomou a mão de seu pai para removê-la da cabeça de Efraim para a cabeça de Manassés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1470, 48, 18, 'E disse-lhe: “Não assim, meu pai; é este aqui o primogênito; põe tua mão direita sobre sua cabeça”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1471, 48, 19, 'Seu pai, porém, recusou: “Eu sei, meu filho – disse ele – eu sei. Ele também se tornará um povo e será grande; mas seu irmão mais novo crescerá mais do que ele e sua posteridade se tornará uma multidão de nações”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1472, 48, 20, 'Abençoou-os, pois, naquele dia, e disse: “Israel vos nomeará em suas bênçãos; se dirá: Deus te torne semelhante a Efraim e a Manassés”. Foi assim que ele pôs Efraim na frente de Manassés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1473, 48, 21, 'Israel disse a José: “Vou morrer. Mas Deus estará convosco e vos reconduzirá à terra de vossos pais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1474, 48, 22, 'Dou-te a mais que teus irmãos, uma porção que conquistei aos amorreus com minha espada e meu arco”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1475, 49, 1, 'Jacó chamou seus filhos e lhes disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1476, 49, 2, 'Ajuntai-vos e ouvi, filhos de Jacó. Escutai Israel, vosso pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1477, 49, 3, 'Rúben, tu és o meu primogênito, minha força, primícias do meu vigor. Notável em dignidade e notável em poder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1478, 49, 4, 'Transbordante como a água, não terás o primeiro lugar, porque subiste ao leito de teu pai, e desse modo maculaste o meu leito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1479, 49, 5, 'Simeão e Levi são irmãos; suas espadas são instrumentos de violência.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1480, 49, 6, 'Minha alma não participe de suas maquinações, meu coração jamais se associe às suas reuniões! Porque em sua cólera mataram homens e em seu furor enervaram touros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1481, 49, 7, 'Maldita cólera que os levou à violência, maldito furor que os induziu à crueldade! Hei de dispersá-los em Jacó, hei de espalhá-los em Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1482, 49, 8, 'Judá, teus irmãos te louvarão. Pegarás pela nuca os inimigos; os filhos de teu pai se prostrarão em tua presença.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1483, 49, 9, 'Filhote de leão, Judá: voltas trazendo a caça, meu filho. Dobra-se, deita-se como um leão; como uma leoa: quem o despertará?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1484, 49, 10, 'Não se apartará o cetro de Judá, nem o bastão de comando dentre seus pés, até que venha aquele a quem pertence por direito, e a quem devem obediência os povos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1485, 49, 11, 'Amarra à videira o jumentinho, à cepa o filho da jumenta. Lava com o vinho suas vestes, com o sangue das uvas o seu manto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1486, 49, 12, 'O vinho aumenta o brilho de seus olhos, seus dentes são brancos como o leite.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1487, 49, 13, 'Zabulon habita à beira do mar, no litoral, onde aportam os navios, e seu flanco se estende até Sidônia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1488, 49, 14, 'Issacar é um jumento forte, deitado nos currais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1489, 49, 15, 'Vê que é bom o descanso e a terra agradável: curva os ombros sob a carga, sujeita-se ao tributo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1490, 49, 16, 'Dã julgará seu povo, como uma das tribos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1491, 49, 17, 'Dã será uma serpente no caminho, uma cobra na estrada, que morde a pata do cavalo e derruba o cavaleiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1492, 49, 18, 'Espero em vosso socorro, Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1493, 49, 19, 'Gad será saqueado por quadrilhas de assaltantes, mas também os assaltará e perseguirá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1494, 49, 20, 'Aser tem um pão saboroso, que constitui as delícias dos reis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1495, 49, 21, 'Neftali é uma gazela solta, que tem lindos filhotes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1496, 49, 22, 'José é broto de uma árvore fértil, broto de uma árvore fértil junto à nascente: seus ramos crescem acima do muro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1497, 49, 23, 'Provocam-no, atiram contra ele, atacam-no os flecheiros,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1498, 49, 24, 'mas, seu arco permanece firme, seus braços e mãos desembaraçados pelas mãos do Poderoso de Jacó, pelo nome do Pastor, que é a pedra de Israel,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1499, 49, 25, 'graças ao Deus de teu pai, que te ajuda, graças ao Todo-poderoso, que te abençoa com as bênçãos do céu altíssimo, com as bênçãos do profundo abismo, com as bênçãos dos peitos e do seio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1500, 49, 26, 'As bênçãos de teu pai sobrepujam as bênçãos das antigas montanhas, as aspirações das colinas eternas. Que elas desçam sobre a cabeça de José, sobre a fronte do príncipe de seus irmãos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1501, 49, 27, 'Benjamim, lobo voraz, de manhã devora a presa e à tarde reparte o despojo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1502, 49, 28, 'São esses todos que formam as doze tribos de Israel. Foi isso que lhes disse seu pai ao abençoá-los. A cada um deu uma bênção particular.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1503, 49, 29, 'Em seguida, fez-lhes esta recomendação: “Eis que vou ser reunido aos meus. Enterrai-me junto de meus pais na caverna da terra de Efron, o hiteu,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1504, 49, 30, 'na caverna da terra de Macpela, defronte de Mambré, na terra de Canaã, essa caverna que Abraão havia comprado a Efron, o hi­teu, ao mesmo tempo que a terra, para ter a propriedade de uma sepultura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1505, 49, 31, 'Foi ali que enterraram Abraão e Sara, sua mu­lher; foi ali que enterraram Isaac e Rebeca, sua mulher; e foi ali que enterrei Lia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1506, 49, 32, 'Essa propriedade, bem como a caverna que nela se encontra, foram compradas aos filhos de Het.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1507, 49, 33, 'E, tendo Jacó dado aos seus filhos esta última recomendação, recolheu os pés em sua cama, e expirou. E foi reunido aos seus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1508, 50, 1, 'José lançou-se então sobre o rosto de seu pai e o beijou cho­rando.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1509, 50, 2, 'Ordenou depois aos médicos que o serviam que embalsamassem seu pai; e os médicos embalsamaram Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1510, 50, 3, 'Gastaram nisso quarenta dias, que é o tempo necessário ao embalsamamento. Os egípcios choraram-no durante setenta dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1511, 50, 4, 'Passado o tempo do pranto, José disse à casa do faraó: “Se achei graça aos vossos olhos, dizei de minha parte ao faraó');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1512, 50, 5, 'que meu pai me fez jurar-lhe: ‘Eu vou morrer – disse-me ele –; tu me enterrarás no túmulo que adquiri na terra de Canaã’. Permite-me, pois, subir e enterrar meu pai; depois voltarei”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1513, 50, 6, 'O faraó respondeu: “Vai sepultar teu pai como ele te fez jurar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1514, 50, 7, 'José partiu para sepultar seu pai. Todos os servos do faraó, os anciãos de sua casa e todos os anciãos do Egito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1515, 50, 8, 'toda a casa de José, seus irmãos e a casa de seu pai o seguiram. Deixaram na terra de Gessen somente seus filhinhos, suas ovelhas e seus bois.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1516, 50, 9, 'Carros e cavaleiros acompanhavam-no, de sorte que a caravana era muito grande.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1517, 50, 10, 'Chegando à eira de Atad, além do Jordão, fizeram uma grande e solene lamentação, e José celebrou, em honra de seu pai, um pranto de sete dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1518, 50, 11, 'Vendo esse pranto na eira de Atad, o povo daquela terra disse: “Grande pranto é esse dos egípcios!”. Daí o nome de Abel-Misraim dado a esse lugar, que está situado além do Jordão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1519, 50, 12, 'Os filhos de Jacó fizeram, pois, o que ele lhes tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1520, 50, 13, 'Levaram-no para Canaã e enterraram-no na caverna da terra de Macpela, que Abraão tinha comprado, juntamente com a propriedade de Efron, o hiteu, defronte de Mambré, para ter a propriedade de uma sepultura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1521, 50, 14, 'Depois do enterro, José voltou para o Egito com seus irmãos e todos os que o tinham acompanhado nos funerais de seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1522, 50, 15, 'Os irmãos de José, vendo que seu pai morrera, disseram entre si: “Será que José nos tomará em aversão e irá vingar-se de todo o mal que lhe fizemos?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1523, 50, 16, 'Mandaram, pois, dizer-lhe: “Antes de morrer, teu pai recomendou-nos');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1524, 50, 17, 'que te pedíssemos perdão do crime que teus irmãos cometeram, de seu pecado, de todo o mal que te fizeram. Perdoa, pois, agora esse crime àqueles que servem o Deus de teu pai”. Ouvindo isso, José chorou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1525, 50, 18, 'Seus irmãos vieram jogar-se aos seus pés, dizendo: “Somos teus escravos!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1526, 50, 19, 'José disse-lhes: “Não temais: posso eu pôr-me no lugar de Deus?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1527, 50, 20, 'Vossa intenção era de fazer-me mal, mas Deus tirou daí um bem; era para fazer, como acontece hoje, com que se conservasse a vida a um grande povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1528, 50, 21, 'Não temais, pois: eu vos sustentarei a vós e a vossos filhos”. Essas palavras, que lhes foram direto ao coração, reconfortaram-nos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1529, 50, 22, 'José habitou no Egito, e também a família de seu pai. Viveu cento e dez anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1530, 50, 23, 'Viu os descendentes de Efraim até a terceira geração. Igualmente, os filhos de Maquir, filho de Manassés, vieram à luz sobre os joelhos de José.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1531, 50, 24, 'José disse a seus irmãos: “Vou morrer; mas Deus vos visitará seguramente e vos fará subir desta terra para a terra que jurou dar a Abraão, Isaac e a Jacó”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1532, 50, 25, 'E José fez que os filhos de Israel jurassem: “Quando Deus vos visitar – disse ele – levareis daqui os meus ossos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1533, 50, 26, 'José morreu com a idade de cento e dez anos. Foi embalsamado e depositado num sarcófago no Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1534, 51, 1, 'Eis os nomes dos filhos de Israel que vieram para o Egito com Jacó, cada um com sua família:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1535, 51, 2, 'Rúben, Simeão, Levi, Judá,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1536, 51, 3, 'Issacar, Zabulon, Benjamim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1537, 51, 4, 'Dã, Neftali, Gad e Aser.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1538, 51, 5, 'Todas as pes­soas saídas de Jacó eram em número de setenta. José estava já no Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1539, 51, 6, 'E, morto José, assim como todos os seus irmãos e toda aquela geração,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1540, 51, 7, 'os israelitas foram fecundos e multiplicaram-se; tornaram-se tão numerosos e tão fortes, que a terra ficou repleta deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1541, 51, 8, 'Entretanto, subiu ao trono do Egito um novo rei, que não tinha conhecido José.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1542, 51, 9, 'Ele disse ao seu povo: ‘‘Vede: os israelitas tornaram-se numerosos e fortes demais para nós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1543, 51, 10, 'Vamos! É preciso tomar precaução contra eles e impedir que se multipliquem, para não acontecer que, sobrevindo uma guerra, se unam com os nossos inimigos e combatam contra nós, e se retirem do país”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1544, 51, 11, 'Estabeleceu, pois, sobre eles, feitores para acabrunhá-los com trabalhos penosos: eles cons­truíram para o faraó as cidades de Pitom e Ramsés, que deviam servir de entreposto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1545, 51, 12, 'Quanto mais os oprimiam, porém, tanto mais eles se multiplicavam e se espalhavam, a ponto de os egípcios os aborrecerem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1546, 51, 13, 'Impunham-lhes a mais dura servidão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1547, 51, 14, 'e amarguravam-lhes a vida com duros traba­lhos na argamassa e na fabricação de tijolos, bem como com toda a sorte de trabalhos nos campos e todas as tarefas que se lhes impunham tiranicamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1548, 51, 15, 'O rei do Egito dirigiu-se, igualmente, às parteiras dos hebreus (uma se chamava Sefra e a outra, Fua),');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1549, 51, 16, 'e disse-lhes: ‘‘Quando assistirdes às mulheres dos hebreus, e as virdes sobre o leito, se for um filho, matai-o; mas se for uma filha, dei­xai-a viver’’.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1550, 51, 17, 'Mas as parteiras temiam a Deus, e não executaram as ordens do rei do Egito, deixando viver os meninos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1551, 51, 18, 'O rei mandou-as chamar então e disse-lhes: ‘‘Por que agistes assim, e deixastes viver os meninos?’’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1552, 51, 19, '‘‘Porque – responderam elas ao faraó – as mulheres dos hebreus não são como as dos egípcios: elas são vigorosas, e já dão à luz antes que chegue a parteira.’’');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1553, 51, 20, 'Deus beneficiou as parteiras: o povo continuou a multiplicar-se e a espa­lhar-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1554, 51, 21, 'Porque elas haviam temido a Deus, ele fez prosperar suas famílias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1555, 51, 22, 'Então o faraó deu esta ordem a todo o seu povo: ‘‘Todo menino que nascer, vós o atirareis ao Nilo. Deixareis, porém, viver todas as meninas’’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1556, 52, 1, 'Um homem da casa de Levi tinha tomado por mulher uma filha de Levi,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1557, 52, 2, 'que concebeu, e deu à luz um filho. Vendo que era formoso, escondeu-o durante três meses.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1558, 52, 3, 'Mas, não podendo guardá-lo oculto por mais tempo, tomou uma cesta de junco, untou-a de betume e piche, colocou dentro o menino e depô-la à beira do rio, no meio dos caniços.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1559, 52, 4, 'A irmã do menino ficou parada a alguma distância para ver o que lhe havia de acontecer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1560, 52, 5, 'Ora, a filha do faraó desceu ao rio para se banhar, enquanto suas criadas passea­vam à beira do rio. Ela viu a cesta no meio dos juncos e mandou uma de suas criadas buscá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1561, 52, 6, 'Abriu-a e viu dentro o menino que chorava. E compadeceu-se: “É um filho dos hebreus” – disse ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1562, 52, 7, 'Veio então a irmã do menino e disse à filha do faraó: “Queres que vá procurar entre as mulhe­res dos hebreus uma ama de leite para amamentar o menino?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1563, 52, 8, '“Sim” – disse a filha do faraó –. E a moça correu a buscar a mãe do menino.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1564, 52, 9, '“Toma este menino – disse-lhe a filha do faraó – amamenta-o; te darei o teu salário.” A mulher tomou o menino e o amamentou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1565, 52, 10, 'Quando o menino cresceu, ela o conduziu à filha do faraó, que o adotou como seu filho e deu-lhe o nome de Moisés, “porque – disse ela – eu o salvei das águas”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1566, 52, 11, 'Moisés cresceu. Um dia em que saíra por acaso para ir ter com os seus irmãos, foi testemunha de seus duros trabalhos, e viu um egípcio ferindo um hebreu dentre seus irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1567, 52, 12, 'Moisés, voltando-se para um e outro lado e vendo que não havia ali ninguém, matou o egípcio e ocultou-o na areia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1568, 52, 13, 'Saindo de novo no dia seguinte, viu dois hebreus que estavam brigan­do. E disse ao culpado: “Por que feres o teu companheiro?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1569, 52, 14, 'Mas o homem respondeu-lhe: “Quem te constituiu chefe e juiz sobre nós? Queres, porventura, matar-me como mataste o egípcio?”. Moisés teve medo e pensou: “Certamente a coisa já é conhecida”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1570, 52, 15, 'O faraó, sabendo do ocorrido, procurou matar Moisés, mas este fugiu para longe do faraó. Retirou-se para a terra de Madiã, e sentou-se junto de um poço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1571, 52, 16, 'Ora, as sete filhas do sacerdote de Madiã vieram tirar água do poço e encher as gamelas para dar de beber às ovelhas de seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1572, 52, 17, 'Sobrevindo então alguns pastores, as expulsavam. Moisés, porém, tomou sua defesa e deu de beber ao seu rebanho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1573, 52, 18, 'E, voltando elas para junto de Raguel, seu pai, este disse-lhes: “Por que voltais hoje tão cedo?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1574, 52, 19, 'Elas responderam: “Um egípcio nos protegeu contra alguns pastores e, além disso, tirou água ele mesmo e deu de beber aos animais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1575, 52, 20, '“Onde está ele? – perguntou às suas filhas –. Por que o deixastes partir? Chamai-o para que coma alguma coisa.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1576, 52, 21, 'Moisés aceitou ficar em casa desse homem, o qual lhe deu por mulher sua filha Séfora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1577, 52, 22, 'Ela teve um filho, que Moisés chamou de Gérson, “porque – disse ele – sou apenas um hóspede em terra estrangeira”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1578, 52, 23, 'Muito tempo depois, morreu o rei do Egito. Os israelitas, que gemiam ainda sob o peso da servidão, clamaram, e, do fundo de sua escravidão, subiu o seu clamor até Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1579, 52, 24, 'Deus ouviu seus gemidos e lembrou-se de sua aliança com Abraão, Isaac e Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1580, 52, 25, 'Olhou para os israelitas e reconheceu-os.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1581, 53, 1, 'Moisés apascentava o rebanho de Jetro, seu sogro, sacerdote de Madiã. Um dia em que conduzira o rebanho para além do deserto, chegou até a montanha de Deus, Horeb.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1582, 53, 2, 'O anjo do Senhor apareceu-lhe numa chama (que saía) do meio a uma sarça. Moisés olhava: a sarça ardia, mas não se consumia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1583, 53, 3, '“Vou me aproximar – disse ele consigo – para contemplar esse extraordinário espetáculo, e saber por que a sarça não se consome.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1584, 53, 4, 'Vendo o Senhor que ele se aproximou para ver, chamou-o do meio da sarça: “Moisés, Moisés!”. “Eis-me aqui!” – respondeu ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1585, 53, 5, 'E Deus: “Não te aproximes daqui. Tira as sandálias dos teus pés, porque o lugar em que te encontras é uma terra santa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1586, 53, 6, 'Eu sou – ajuntou ele – o Deus de teu pai, o Deus de Abraão, o Deus de Isaac e o Deus de Jacó”. Moisés escondeu o rosto, e não ousava olhar para Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1587, 53, 7, 'O Senhor disse: “Eu vi, eu vi a aflição de meu povo que está no Egito, e ouvi os seus clamores por causa de seus opressores. Sim, eu conheço seus sofrimentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1588, 53, 8, 'E desci para livrá-lo da mão dos egípcios e para fazê-lo subir do Egito para uma terra fértil e espaçosa, uma terra que mana leite e mel, lá onde habitam os cananeus, os hiteus, os amorreus, os ferezeus, os heveus e os jebuseus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1589, 53, 9, 'Agora, eis que os clamores dos israelitas chegaram até mim, e vi a opressão que lhes fazem os egípcios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1590, 53, 10, 'Vai, eu te envio ao faraó para tirar do Egito os israelitas, meu povo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1591, 53, 11, 'Moisés disse a Deus: “Quem sou eu para ir ter com o faraó e tirar do Egito os israelitas?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1592, 53, 12, '“Eu estarei contigo – res­pondeu Deus –; e eis aqui um sinal de que sou eu que te envio: quando tiveres tirado o povo do Egito, servireis a Deus sobre esta montanha.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1593, 53, 13, 'Moisés disse a Deus: “Quando eu for para junto dos israelitas e lhes disser que o Deus de seus pais me enviou a eles, que lhes responderei se me perguntarem qual é o seu nome?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1594, 53, 14, 'Deus respondeu a Moisés: “Eu sou aquele que sou”. E ajuntou: “Eis como responderás aos israelitas: (Aquele que se chama) ‘Eu sou’ envia-me junto de vós”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1595, 53, 15, 'Deus disse ainda a Moisés: “Assim falarás aos israelitas: É Javé, o Deus de vossos pais, o Deus de Abraão, o Deus de Isaac e o Deus de Jacó, quem me envia para junto de vós. Esse é o meu nome para sempre, e é assim que me chamarão de geração em geração”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1596, 53, 16, '“Vai, reúne os anciãos de Israel e dize-lhes: O Senhor, o Deus de vossos pais, o Deus de Abraão, de Isaac e de Jacó apareceu-me. E disse-me: eu vos visitei, e vi o que se vos faz no Egito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1597, 53, 17, 'e disse: Eu vos tirarei do Egito onde sois oprimidos, para fazer-vos subir para a terra dos cananeus, dos hiteus, dos amorreus, dos ferezeus, dos heveus e dos jebuseus, terra que mana leite e mel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1598, 53, 18, 'Eles ouvirão a tua voz. Irás então com os anciãos de Israel à presença do rei do Egito e lhe direis: O Senhor, o Deus dos hebreus, nos apareceu. Deixa-nos, pois, ir para o deserto, a três dias de caminho, para oferecer sacrifícios ao Senhor, nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1599, 53, 19, 'Eu sei que o rei do Egito não vos deixará partir, se ele não for obrigado pela força.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1600, 53, 20, 'Mas estenderei a mão e ferirei o Egito com toda sorte de prodígios que farei no meio deles. Depois disso, o faraó vos deixará partir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1601, 53, 21, 'Farei com que esse povo ganhe as boas graças dos egípcios, e, quando partirdes, não ireis com as mãos vazias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1602, 53, 22, 'Cada mulher pedirá à sua vizinha e àquela que mora em sua casa objetos de prata e de ouro, e vestidos que poreis sobre vossos filhos e sobre vossas filhas. Assim despojareis os egípcios.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1603, 54, 1, 'Moisés respondeu: “Eles não me crerão, nem me ouvirão, e vão dizer que o Senhor não me apareceu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1604, 54, 2, 'O Se­nhor disse-lhe: “O que tens na mão?”. “Uma vara.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1605, 54, 3, '“Joga-a por terra.” Ele jogou-a por terra; e a vara transformou-se numa serpente, de modo que Moisés recuou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1606, 54, 4, 'O Senhor disse-lhe: “Estende tua mão e toma-a pela cauda – ele estendeu a mão e tomou-a, e a serpente tornou-se de novo uma vara em sua mão –;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1607, 54, 5, 'é para que creiam que o Senhor, o Deus de seus pais, o Deus de Abraão, o Deus de Isaac e o Deus de Jacó, realmente te apareceu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1608, 54, 6, 'O Senhor continuou: “Mete a tua mão no teu seio”. Ele meteu a mão em seu seio e, quando a retirou, sua mão estava le­prosa, tão branca como a neve.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1609, 54, 7, 'O Senhor disse-lhe: “Mete de novo a mão em teu seio”. Ele meteu de novo a mão em seu seio e, retirando-a, eis que ela se tornara como o restante de sua carne.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1610, 54, 8, '“Se não te crerem, nem obedecerem à voz do primeiro prodígio, crerão à voz do segundo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1611, 54, 9, 'Se ainda permanecerem incrédulos diante desses dois prodígios, nem te ouvirem, tomarás da água do Nilo e a derramarás por terra; a água tirada do rio se tornará sangue sobre a terra.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1612, 54, 10, 'Moisés disse ao Senhor: “Ah! Se­nhor! Eu não tenho o dom da palavra; nunca o tive, nem mesmo depois que falastes ao vosso servo; tenho a boca e a língua pesadas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1613, 54, 11, 'O Senhor disse-lhe: “Quem deu uma boca ao homem? Quem o faz mudo ou surdo, o faz ver ou o faz cego? Não sou eu, o Senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1614, 54, 12, 'Vai, pois, eu estarei contigo quando falares, e te ensinarei o que terás de dizer”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1615, 54, 13, '“Ah! Senhor! – disse Moisés – mandai quem quiserdes!”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1616, 54, 14, 'Então o Senhor irritou-se contra Moisés: “Não tens Aarão – disse ele – teu irmão, o levita? Eu sei que ele fala bem. Ei-lo justamente que vem ao teu encontro e, vendo-te, se alegrará o seu coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1617, 54, 15, 'Tu lhe falarás, lhe porás as palavras na boca. E, quando falardes, eu estarei contigo e com ele, e vos ensinarei o que tereis a fazer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1618, 54, 16, 'É ele quem falará ao povo em teu lugar: ele te servirá de boca e tu lhe servirás de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1619, 54, 17, 'Toma em tua mão esta vara, com a qual operarás prodígios”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1620, 54, 18, 'Moisés partiu. De volta para junto de Jetro, seu sogro, disse-lhe: “Rogo-te que me deixes partir, e voltar para junto de meus irmãos no Egito; vou ver se ainda vivem”. Jetro disse a Moisés: “Vai em paz”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1621, 54, 19, 'O Senhor disse a Moisés em Madiã: “Vai, volta ao Egito, porque todos aqueles que atentavam contra a tua vida estão mortos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1622, 54, 20, 'Moisés tomou consigo sua mulher e seus filhos, fê-los montar em jumentos e voltou para o Egito. Levava na mão a vara de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1623, 54, 21, 'O Senhor disse a Moisés: “Agora que voltas ao Egito, cuida para que todos os prodígios, que te concedi o poder de operar, tu os faças na presença do faraó. Mas endurecerei o seu coração e ele não deixará partir o povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1624, 54, 22, 'Tu lhe dirás: Assim fala o Senhor: Israel é meu filho primogênito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1625, 54, 23, 'Eu te digo: Dei­xa ir o meu filho, para que ele me preste um culto. Se te recusas a deixá-lo partir, farei perecer teu filho primogênito”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1626, 54, 24, 'Estando Moisés a caminho, numa estalagem, o Senhor atacou Moisés, procurando matá-lo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1627, 54, 25, 'Sefra tomou então uma pedra afiada, cortou o prepúcio de seu filho e atirou-o aos pés de Moisés, dizendo: “Tu me és um esposo de sangue!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1628, 54, 26, 'Assim o Senhor o dei­xou. Sefra havia dito: “esposo de sangue”, por causa da circuncisão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1629, 54, 27, 'O Senhor disse a Aarão: “Vai ao encontro de Moisés no deserto”. Aarão foi e, encontrando seu irmão na montanha de Deus, beijou-o.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1630, 54, 28, 'Moisés contou-lhe tudo o que lhe tinha dito o Senhor ao enviá-lo, e todos os prodígios que lhe tinha ordenado fazer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1631, 54, 29, 'Moisés e Aarão continuaram seu caminho e reuniram todos os anciãos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1632, 54, 30, 'Aarão repetiu todas as palavras que o Senhor tinha dito a Moisés, e este fez os prodígios em presença do povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1633, 54, 31, 'O povo acreditou. E, tendo ouvido que o Senhor viera visitar os filhos de Israel, e que vira sua aflição, inclinaram-se e prostraram-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1634, 55, 1, 'Depois disso, Moisés e Aarão dirigiram-se ao faraó e disseram-lhe: “Assim fala o Senhor, o Deus de Israel: Deixa ir o meu povo, para que me faça uma festa no deserto”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1635, 55, 2, 'O faraó respondeu: “Quem é esse Senhor, para que eu lhe deva obedecer, deixando partir Israel? Não conheço o Senhor, e não deixarei partir Israel”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1636, 55, 3, 'Eles prosseguiram: “O Deus dos hebreus nos apareceu. Deixa-nos ir ao deserto, a três dias de caminho, para oferecer sa­crifícios ao Senhor, para que não nos fira ele pela peste ou pela espada”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1637, 55, 4, 'O rei do Egito disse-lhes: “Moisés e Aarão, por que quereis desviar o povo do seu trabalho? Ide às vossas ocupações”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1638, 55, 5, 'E ajuntou: “O povo é, atualmente, numeroso, e vós o faríeis interromper seus trabalhos!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1639, 55, 6, 'Naquele mesmo dia, deu o faraó ao inspetor do povo e aos vigias esta ordem:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1640, 55, 7, '“Não fornecereis mais, como dantes, a palha ao povo para fazer os tijolos: irão eles mesmos procurá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1641, 55, 8, 'Entretanto, exigi deles a mesma quantidade de tijolos que antes, sem nada diminuir. São uns preguiçosos. É por isso que clamam: queremos ir oferecer sacrifícios ao nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1642, 55, 9, 'Que sejam sobrecarregados de trabalhos; ocupem-se eles de suas tarefas e não deem ouvidos às mentiras que se lhes contam!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1643, 55, 10, 'Os inspetores e os vigias do povo foram dizer-lhes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1644, 55, 11, '“O faraó manda-vos dizer que já não vos fornecerá palha; e que vós mesmos devereis procurá-la onde houver, mas nada se diminuirá de vosso trabalho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1645, 55, 12, 'Espalhou-se, pois, o povo por todo o Egito para ajuntar restolho e transformá-lo em palha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1646, 55, 13, 'Os inspetores instavam com eles, dizendo: “Aprontai vossa tarefa diária, como quando se vos fornecia palha”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1647, 55, 14, 'Açoitavam até os vigias israelitas que os inspetores do faraó tinham estabelecido sobre eles. Diziam-lhes: “Por que não terminastes, ontem e hoje, como antes, o que se vos havia fixado de tijolos a fazer?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1648, 55, 15, 'Os vigias israe­litas foram queixar-se ao faraó: “Por que – perguntaram eles – procedes desse modo com os teus servos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1649, 55, 16, 'Não se nos fornece mais a palha e se nos diz: fazei tijolos. E chegam até a nos açoitar (como se) teu povo estivesse em falta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1650, 55, 17, 'O faraó respondeu: “Vós sois uns preguiçosos, sim, uns preguiçosos! É por isso que dizeis: queremos ir oferecer sacrifícios ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1651, 55, 18, 'E agora, ao trabalho! Não se vos fornecerá a palha, mas deveis entregar a mesma quantidade de tijolos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1652, 55, 19, 'Os vigias israelitas, aos quais di­ziam: “Nada diminuireis da entrega diária de tijolos”, viram-se em um cruel embaraço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1653, 55, 20, 'Saindo da casa do faraó, encontraram Moisés e Aarão que os esperavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1654, 55, 21, 'Disseram-lhes: “Que o Senhor vos veja e vos julgue, vós, que atraístes sobre nós a aversão do faraó e de sua gente, e pusestes em suas mãos a espada para nos matar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1655, 55, 22, 'Moisés voltou-se de novo para o Senhor: “Ó Senhor – disse-lhe ele – por que fizestes mal a este povo? Por que me enviastes?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1656, 55, 23, 'Desde que fui falar ao faraó em vosso nome, ele maltrata o povo, e vós não o livrastes de maneira alguma”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1657, 56, 1, 'O Senhor respondeu: “Verás o que vou fazer ao faraó: forçado por mão poderosa, ele os deixará partir; forçado por mão poderosa, ele os expulsará de sua terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1658, 56, 2, 'Deus disse a Moisés: “Eu sou o Se­nhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1659, 56, 3, 'Apareci a Abraão, a Isaac e a Jacó como o Deus Todo-poderoso, mas não me dei a conhe­cer a eles pelo meu nome, Javé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1660, 56, 4, 'Eu me comprometi com eles a lhes dar a terra de Canaã, a terra onde levaram uma vida errante e habitaram como estrangeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1661, 56, 5, 'Ouvi o clamor dos israelitas oprimidos pelos egípcios, e lembrei-me de minha aliança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1662, 56, 6, 'Por isso, dize aos israe­litas: Eu sou o Senhor; vou libertar-vos do jugo dos egípcios e livrar-vos de sua servidão. Estenderei o braço para essa libertação e manifestarei uma terrível justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1663, 56, 7, 'Eu vos tomarei para meu povo e serei o vosso Deus, e sabereis que eu sou o Senhor, vosso Deus, que vos terei libertado do jugo dos egípcios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1664, 56, 8, 'Eu vos introduzirei na terra que jurei dar a Abraão, a Isaac e a Jacó: e vos darei a pos­ses­são dessa terra, eu, o Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1665, 56, 9, 'Moisés repetiu essas palavras aos israelitas, mas estes não o ouviram, tão grande era o abatimento de sua alma e penosa a sua servidão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1666, 56, 10, 'O Senhor disse então a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1667, 56, 11, '“Vai pedir ao faraó, rei do Egito, que deixe sair de sua terra os israelitas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1668, 56, 12, 'Moisés respondeu ao Senhor: “Os israelitas não me ouviram; como me ouvirá o faraó, a mim que não tenho o dom da palavra?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1669, 56, 13, 'O Senhor falou a Moisés e a Aarão, e deu-lhes a ordem de irem ter com o faraó, rei do Egito, a fim de tirarem da terra do Egito os filhos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1670, 56, 14, 'Eis os chefes das famílias dos israelitas: filhos de Rúben, primogênito de Israel: Henoc, Falu, Hesron e Carmi. Estas são as famílias de Rúben.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1671, 56, 15, 'Filhos de Simeão: Jamuel, Jamin, Aod, Jaquin, Soar e Saul, filho da cananeia. Estas são as famílias de Simeão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1672, 56, 16, 'Eis os nomes dos filhos de Levi, por ordem de gerações: Gérson, Caat e Merari. A duração da vida de Levi foi de cento e trinta e sete anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1673, 56, 17, 'Filhos de Gérson: Lobni e Semei, e suas famílias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1674, 56, 18, 'Filhos de Caat: Amram, Isaar, Hebron e Oziel. A duração de vida de Caat foi de cento e trinta e três anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1675, 56, 19, 'Filhos de Merari: Mooli e Musi. Tais são as famílias de Levi por ordem de gerações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1676, 56, 20, 'Amram desposou Jocabed, sua tia, que lhe deu Aarão e Moisés. A duração de vida de Amram foi de cento e trinta e sete anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1677, 56, 21, 'Filhos de Isaar: Coré, Nefeg e Zecri.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1678, 56, 22, 'Filhos de Oziel: Misael, Elisafã e Setri.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1679, 56, 23, 'Aarão desposou Isabel, filha de Aminadab, irmã de Naasson; ela lhe deu Nadab, Abiú, Eleazar e Itamar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1680, 56, 24, 'Filhos de Coré: Aser, Elcana e Abiasaf; estas são as famílias dos coreítas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1681, 56, 25, 'Eleazar, filho de Aarão, desposou uma das filhas de Futiel, que lhe deu Fineias. Tais são os chefes das famílias dos levitas, com suas famílias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1682, 56, 26, 'Estes são Aarão e Moisés, a quem o Senhor disse: “Fazei sair do Egito os israelitas, segundo os seus exércitos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1683, 56, 27, 'Foram eles que falaram ao faraó, rei do Egito, para tirar do Egito os israelitas. São estes Moisés e Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1684, 56, 28, 'Quando o Senhor falou a Moisés no Egito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1685, 56, 29, 'ele o fez nestes termos: “Eu sou o Senhor. Repete ao faraó, rei do Egito, tudo o que te digo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1686, 56, 30, 'E Moisés respondeu-lhe: “Eu não tenho o dom da palavra; como me ouvirá o faraó?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1687, 57, 1, 'O Senhor disse a Moisés: “Vê, eu vou fazer de ti um deus para o faraó, e teu irmão Aarão será teu profeta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1688, 57, 2, 'Dirás tudo o que eu te mandar, e teu irmão Aarão falará ao rei para que ele deixe sair de sua terra os israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1689, 57, 3, 'Mas eu endurecerei o coração do faraó, e multiplicarei meus sinais e meus prodígios no Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1690, 57, 4, 'Ele não vos ouvirá. Então estenderei minha mão sobre o Egito e farei sair dele os meus exércitos, meu povo, os israelitas, com uma grandiosa manifestação de justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1691, 57, 5, 'Os egípcios saberão que eu sou o Senhor, quando eu estender a mão sobre o Egito e fizer sair dele os israelitas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1692, 57, 6, 'Moisés e Aarão fizeram o que o Senhor tinha ordenado, e obedeceram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1693, 57, 7, 'Moisés tinha oitenta anos e Aarão oitenta e três, quando falaram ao faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1694, 57, 8, 'O Senhor disse a Moisés e a Aarão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1695, 57, 9, '“Se o faraó vos pedir um prodígio, tu dirás a Aarão: toma tua vara e joga-a diante do faraó; ela se tornará uma serpente”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1696, 57, 10, 'Tendo Moisés e Aarão chegado à presença do faraó, fizeram o que o Senhor tinha ordenado. Aarão jogou sua vara diante do rei e de sua gente, e ela se tornou uma serpente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1697, 57, 11, 'Mas o faraó, mandando vir os sábios, os encantadores e os mágicos, estes fizeram o mesmo com os seus encantamentos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1698, 57, 12, 'jogaram cada um suas varas, que se transformaram em serpentes. Mas a vara de Aarão engoliu as deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1699, 57, 13, 'Entretanto, como o Senhor o havia anunciado, endureceu-se o coração do faraó e ele não quis ouvi-los.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1700, 57, 14, 'O Senhor disse a Moisés: “O faraó endureceu o coração: ele se obstina em não querer deixar partir o povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1701, 57, 15, 'Vai procurá-lo amanhã cedo, no momento em que ele sair para ir à margem do rio; os esperarão à beira do Nilo, tomarás na mão a vara que se mudou em serpente,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1702, 57, 16, 'e lhes dirás: O Senhor, o Deus dos hebreus, mandou-me a ti para dizer-te: Deixa ir o meu povo, para que me preste culto no deserto. Até agora não me escutaste.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1703, 57, 17, 'Eis o que diz o Senhor: nisto reconhe­cerás que eu sou o Senhor: vou ferir as águas do Nilo com a vara que tenho na mão e elas se mudarão em sangue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1704, 57, 18, 'Os peixes do Nilo morrerão, o rio se tornará tão poluído que os egípcios terão nojo de beber suas águas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1705, 57, 19, 'O Senhor disse a Moisés: “Dize a Aarão: toma a tua vara e estende a mão sobre as águas do Egito, sobre os seus rios e seus canais, sobre seus lagos e seus reservatórios, para que essas águas se tornem sangue. Haverá sangue em todo o Egito, assim nos recipientes de madeira como nos de pedra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1706, 57, 20, 'Moisés e Aarão obedeceram à ordem do Senhor. Sob os olhos do faraó e de sua gente, Aarão levantou sua vara e feriu a água do Nilo, que se mudou toda em sangue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1707, 57, 21, 'Morreram os peixes do Nilo, e o rio tornou-se tão poluído que os egípcios não podiam beber de suas águas. Houve sangue em todo o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1708, 57, 22, 'Mas os mágicos do Egito fizeram outro tanto com seus encantamentos; o coração do faraó permaneceu endurecido e, como o Senhor havia predito, ele não ouviu Moisés e Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1709, 57, 23, 'Voltou e entrou em sua casa sem mais se cuidar do acontecido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1710, 57, 24, 'Todos os egípcios cavaram o solo nas proxi­midades do Nilo procurando água potável, porque não se podia beber a água do rio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1711, 57, 25, 'Sete dias se passaram depois que o Senhor feriu o Nilo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1712, 57, 26, 'O Senhor disse a Moisés: “Vai procurar o faraó e dize-lhe: Deixa ir o meu povo, para que ele me preste um culto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1713, 57, 27, 'Se recusas, infestarei de rãs todo o teu território.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1714, 57, 28, 'O Nilo ferverá de rãs que subirão para invadir tua habitação, teu quarto, teu leito, as casas de teu povo, os teus fornos e tuas amassadeiras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1715, 57, 29, 'As rãs subirão sobre ti, sobre teu povo e sobre todos os teus servos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1716, 58, 1, 'O Senhor disse a Moisés: “Dize a Aarão: Estende a tua mão com a tua vara sobre os rios, os canais e os lagos, e faze subir as rãs sobre a terra do Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1717, 58, 2, 'Aarão levantou a mão sobre as águas do Egito, e as rãs subiram e cobriram a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1718, 58, 3, 'Os mágicos, porém, fizeram outro tanto com seus encantamentos: fizeram subir as rãs sobre a terra do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1719, 58, 4, 'O faraó mandou chamar Moisés e Aarão: “Intercedei – disse-lhes ele – junto do Senhor, a fim de que afaste as rãs de mim e de meu povo, e deixarei partir o vosso povo para que ofereça sacrifícios ao Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1720, 58, 5, 'Moisés respondeu-lhe: “Digna-te dizer-me quando é que devo interceder por ti, por teus servos e por teu povo, para que o Senhor afaste as rãs de tua pessoa e de tuas casas, de sorte que fiquem somente no rio”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1721, 58, 6, '“Seja amanhã” – disse ele. Moisés replicou: “Será feito segundo o teu desejo, para que saibas que não há ninguém como o Senhor, nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1722, 58, 7, 'As rãs se afastarão de tua pessoa, de tuas habitações, de teus servos e de teu povo; e ficarão somente no Nilo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1723, 58, 8, 'Moisés e Aarão saíram da casa do rei e Moisés invocou o Se­nhor a respeito das rãs que enviara contra o faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1724, 58, 9, 'Fez o Senhor o que pedia Moisés: morreram as rãs nas casas, nas praças e nos campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1725, 58, 10, 'Ajuntaram-nas em montões e o país ficou poluído com isso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1726, 58, 11, 'Mas, vendo o faraó que havia descanso, endureceu o coração; e, como o Senhor havia predito, não ouviu Moisés e Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1727, 58, 12, 'O Senhor disse a Moisés: “Dize a Aarão: Levanta a tua vara e fere o pó da terra para que se transforme em mosquitos em todo o Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1728, 58, 13, 'Fizeram assim: Aarão estendeu a mão com sua vara, e feriu o pó da terra: houve mosquitos sobre os homens e os animais. Toda a poeira da terra se transformou em mosquitos em todo o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1729, 58, 14, 'Os mágicos, usando de seus encantamentos, tentaram produzir mosquitos, mas não o puderam. Os mosquitos ficavam sobre os homens e os animais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1730, 58, 15, 'Então os mágicos disseram ao faraó: “Isso é o dedo de Deus”. Mas o coração do faraó permaneceu endurecido e, como o Senhor havia predito, não ouviu Moisés e Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1731, 58, 16, 'O Senhor disse a Moisés: “Irás amanhã de manhã apresentar-te diante do faraó, quando ele sair para ir à margem do rio, e lhe dirás: Eis o que diz o Senhor: Deixa partir o meu povo, para me prestar culto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1732, 58, 17, 'Se recusares, mandarei moscas sobre tua pessoa, tua gente, teu povo, tuas casas: as casas dos egípcios serão todas invadidas por elas, bem como a terra em que moram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1733, 58, 18, 'Farei, porém, uma exceção naquele dia para a terra de Gessen, onde habita o meu povo. Ali não haverá moscas, para que saibas que eu, o Senhor, estou no meio da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1734, 58, 19, 'Farei, pois, uma distinção entre o meu povo e o teu. Amanhã terá lugar esse prodígio”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1735, 58, 20, 'Assim fez o Senhor: surgiu na casa do faraó, e na de sua gente, uma multidão de moscas e todo o Egito foi devastado pelas moscas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1736, 58, 21, 'Mandou então o faraó chamar Moisés e Aarão: “Ide – disse-lhes ele – oferecer sacrifícios ao vosso Deus, (mas) no país”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1737, 58, 22, 'Moisés respondeu: “Não convém que seja assim: os sacrifícios que oferecemos ao Senhor, nosso Deus, seriam uma abominação para os egípcios. Se oferecermos, sob os seus olhos, sacrifícios que lhes são abomináveis, não nos apedrejarão eles?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1738, 58, 23, 'Havemos de ir ao deserto, a três dias de caminho, e oferecere­mos (lá) sacrifícios ao Senhor, nosso Deus, conforme ele nos ordenou”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1739, 58, 24, '“Consinto – replicou o faraó – em vos deixar partir: ofere­cereis sacrifícios ao Senhor, vosso Deus, no deserto; somente não ireis muito longe. Rogai por mim.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1740, 58, 25, 'Moisés respondeu: “Logo que eu sair de tua casa, intercederei junto ao Senhor, e amanhã as moscas se afastarão do faraó, de seus servos e de seu povo. Somente não continue o faraó a nos enganar, recusando-se deixar ir o povo para oferecer sacrifícios ao Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1741, 58, 26, 'Moisés saiu da casa do faraó. Rogou ao Senhor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1742, 58, 27, 'e fez o Senhor o que lhe era pedido: as moscas afastaram-se do faraó, de sua gente, de seu povo e não restou uma sequer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1743, 58, 28, 'Mas ainda esta vez endureceu o faraó o seu coração, e não deixou ir o povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1744, 59, 1, 'O Senhor disse a Moisés: “Vai procurar o faraó e dize-lhe: Eis o que diz o Senhor, Deus dos hebreus: Deixa ir o meu povo, para que ele me preste um culto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1745, 59, 2, 'Se recusas, se persistes em retê-lo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1746, 59, 3, 'a mão do Senhor vai pesar sobre os teus animais que estão nos campos, sobre os cavalos, os jumentos, os camelos, os bois e as ovelhas: haverá uma peste terrível.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1747, 59, 4, 'Entretanto, o Senhor fará uma distinção entre os animais dos israelitas e os dos egípcios, e nada perecerá de tudo o que pertence aos israelitas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1748, 59, 5, 'O Senhor fixou o prazo nestes termos: “Amanhã, o Senhor fará isso na terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1749, 59, 6, 'No dia seguinte, o Senhor cumpriu sua palavra: todos os animais dos egípcios pereceram, mas não morreu um animal sequer dos reba­nhos dos israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1750, 59, 7, 'Tendo o faraó mandado examinar, verificou que não morrera nem um só animal dos rebanhos dos israelitas. Mas o coração do faraó ficou endurecido, e ele não deixou ir o povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1751, 59, 8, 'O Senhor disse a Moisés e a Aarão: “Tomai vossas duas mãos cheias de cinza do forno, e Moisés a lance para o céu dian­te dos olhos­ do faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1752, 59, 9, 'Ela se tornará uma poeira que se espalhará por todo o Egito, e haverá em todo o Egito, sobre os homens e sobre os animais, tumores que se arrebentarão em úlceras”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1753, 59, 10, 'Tomaram, pois, da cinza do forno e apresentaram-se diante do faraó. Moisés atirou-a para o céu e produziram-se, sobre os homens e sobre os animais, tumores que se arrebentaram em úlceras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1754, 59, 11, 'Os mágicos não puderam aparecer diante de Moisés por causa das úlceras, porque foram atingidos como todos os egípcios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1755, 59, 12, 'O Senhor endureceu o coração do faraó, que, como o Senhor havia predito, não ouviu Moisés e Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1756, 59, 13, 'O Senhor disse a Moisés: “Tu te apresentarás amanhã cedo dian­te do faraó, e lhe dirás: Eis o que diz o Senhor, Deus dos hebreus: Deixa partir meu povo para que me preste um culto,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1757, 59, 14, 'porque desta vez vou descarregar todos os meus flagelos sobre tua pessoa, tua gente e teu povo, a fim de que saibas que não há ninguém semelhante a mim em toda a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1758, 59, 15, 'Eu poderia, num instante, estendendo a minha mão, ferir-te de peste, tu e o teu povo; e tu já terias desaparecido da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1759, 59, 16, 'Mas, se te deixo incólume, é para que vejas o meu poder, e que o meu nome seja glorificado por toda a terra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1760, 59, 17, 'Se te obstinas em impedir a partida de meu povo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1761, 59, 18, 'amanhã, a esta mesma hora, farei cair uma chuva de pedras tão violenta como nunca houve outra igual no Egito, desde sua origem até o dia de hoje.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1762, 59, 19, 'Mete, pois, ao abrigo, teus animais e tudo o que tens nos campos, porque todos os homens e todos os animais, que se encontrarem fora de casa nos campos, serão atingidos pela saraiva e morrerão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1763, 59, 20, 'Aqueles dentre a gente do faraó, que temem a palavra do Senhor, porão seus servos e seus rebanhos ao abrigo nas casas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1764, 59, 21, 'Mas os que não fazem caso da palavra do Senhor, deixarão nos campos seus escravos e seus rebanhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1765, 59, 22, 'O Senhor disse a Moisés: “Estende a mão para o céu, para que caia uma chuva de granizo em todo o Egito sobre os homens, os animais e sobre toda a erva dos campos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1766, 59, 23, 'Moisés estendeu sua vara para o céu, e o Senhor enviou trovões e chuva de pedras, e o fogo do céu caiu sobre a terra. O Senhor fez chover granizo sobre o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1767, 59, 24, 'Caiu granizo misturado com fogo; e caiu com tanta violência como nunca houve semelhante em todo o Egito, desde que veio a ser uma nação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1768, 59, 25, 'Em todo o Egito a chuva de pedras feriu tudo o que estava nos campos, homens e animais, e feriu toda a erva dos campos e quebrou todas as árvores dos campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1769, 59, 26, 'Só a terra de Gessen, onde se encontravam os israelitas, foi poupada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1770, 59, 27, 'O rei mandou chamar Moisés e Aarão e disse-lhes: “Desta vez eu pequei. O Senhor é justo; eu e meu povo fomos culpados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1771, 59, 28, 'Rogai ao Senhor para que cessem os trovões e o granizo. Eu vos deixarei ir, e não vos reterei mais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1772, 59, 29, 'Moisés disse-lhe: “Logo que eu tiver saído da cidade, levantarei minhas mãos para o Senhor: os trovões cessarão e não haverá mais granizo, para que saibas que a terra pertence ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1773, 59, 30, 'Mas sei que tu e tua gente não temeis ainda o Senhor Deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1774, 59, 31, 'O linho e a cevada foram destruídos, porque o centeio estava espigando e o linho estava em flor;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1775, 59, 32, 'o trigo, porém, e o centeio se salvaram, porque são tardios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1776, 59, 33, 'Moisés partiu da casa do faraó e deixou a cidade. E levantou as mãos para o Senhor: cessaram os trovões e o granizo, e parou de chover sobre a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1777, 59, 34, 'Vendo o faraó que cessara a chuva, assim como o granizo e os trovões, continuou a pecar e endureceu seu coração, ele e sua gente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1778, 59, 35, 'E, tendo-se obstinado o coração do faraó, não deixou partir os israelitas, assim como o Senhor havia predito pela voz de Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1779, 60, 1, 'O Senhor disse a Moisés: “Vai procurar o faraó, porque lhe endureci o coração e o de sua gente para manifestar os meus prodígios no meio deles,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1780, 60, 2, 'para que contes aos teus filhos e aos teus netos as maravilhas que fiz no Egito e os prodígios que operei no meio deles, e para que saibais que eu sou o Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1781, 60, 3, 'Moisés e Aarão foram procurar o rei e disseram-lhe: “Eis o que diz o Senhor, Deus dos hebreus: Até quando recusarás humilhar-te dian­te de mim? Deixa ir o meu povo para que ele me preste o seu culto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1782, 60, 4, 'Se recusares, farei vir amanhã gafanhotos sobre o teu território.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1783, 60, 5, 'Cobrirão a superfície da terra de tal modo que se não poderá mais ver o solo. Devorarão o resto das colheitas que escapou ao granizo, e devorarão todas as árvores de vossos campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1784, 60, 6, 'Encherão tuas casas, as casas de todos os teus servos e a de todos os egípcios. Será uma calamidade tão grande como nunca viram teus pais nem os pais de teus pais, desde sua chegada ao país até o dia de hoje”. Voltou-se, pois, Moisés e retirou-se da casa do faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1785, 60, 7, 'Os servos do faraó disseram-lhe: “Até quando nos servirá de laço este homem? Deixa partir essa gente para que preste seu culto ao Senhor, seu Deus. Não compreendeste ainda que o Egito vai ser arruinado?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1786, 60, 8, 'Mandaram então vir Moisés e Aarão à presença do rei que lhes disse: “Ide fazer vossas devoções ao Senhor, vosso Deus. Quem são os que hão de partir?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1787, 60, 9, '“Iremos – respondeu Moisés – com nossos jovens e nossos velhos, nossos filhos e nossas filhas. Iremos com nossas ovelhas e nossos bois, porque temos de celebrar uma festa em honra do Senhor.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1788, 60, 10, 'O faraó replicou: “O Senhor esteja convosco, do mesmo modo como vos deixarei partir com vossos filhos! Tomai cuidado, porque tendes más intenções.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1789, 60, 11, 'Não há de ser assim. Ide vós, os homens, e prestai o vosso culto ao Senhor, pois é isso o que desejais”. E foram expulsos da presença do faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1790, 60, 12, 'O Senhor disse a Moisés: “Estende tua mão sobre o Egito para que venham gafanhotos sobre ele, e invadam o Egito, e devorem toda a erva da terra, tudo o que o granizo deixou”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1791, 60, 13, 'Moisés estendeu sua vara sobre o Egito, e o Senhor fez soprar sobre o país, todo aquele dia e toda aquela noite, um vento do oriente. E, chegando a manhã, o vento do oriente tinha trazido os gafanhotos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1792, 60, 14, 'Espalharam-se eles sobre todo o Egito, e invadiram todo o território egípcio em tão grande quantidade como nunca houve nem haverá jamais invasão semelhante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1793, 60, 15, 'Eles cobriram toda a superfície do solo em todo o país, de modo que a terra se escureceu. Devoraram toda a vegetação da terra e todos os frutos das árvores que tinha poupado o granizo. Nada de verde ficou nas árvores, nem nas plantas do campo, em toda a extensão do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1794, 60, 16, 'O rei mandou imediatamente chamar Moisés e Aarão e disse-lhes: “Pequei contra o Senhor, vosso Deus, e contra vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1795, 60, 17, 'Mas perdoa ainda esta vez o meu pecado, e roga ao Senhor, vosso Deus, que afaste ao menos de mim este flagelo mortal”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1796, 60, 18, 'Moisés saiu da casa do faraó e intercedeu junto ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1797, 60, 19, 'O Senhor fez soprar do ocidente um vento fortís­simo que levou os gafanhotos e os precipitou no mar Vermelho, sem que ficasse um só em todo o território do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1798, 60, 20, 'Mas o Senhor endureceu o coração do faraó, que não deixou partir os israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1799, 60, 21, 'O Senhor disse a Moisés: “Estende a mão para o céu, e que se formem sobre todo o Egito trevas (tão espessas) que se possam apalpar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1800, 60, 22, 'Moisés estendeu a mão para o céu, e durante três dias espessas trevas cobriram todo o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1801, 60, 23, 'Durante esses três dias, não se via um ao outro, e ninguém se levantou do lugar onde estava. Ao passo que todos os israelitas tinham luz nos lugares onde habitavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1802, 60, 24, 'O faraó mandou chamar Moisés e disse-lhe: “Ide fazer vossas devoções ao Senhor. Somente vossas ovelhas e vossos bois ficarão neste lugar; podeis levar convosco vossos filhinhos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1803, 60, 25, 'Moisés respondeu: “Tu mesmo nos porás nas mãos o que precisamos para oferecermos sacrifícios e holocaustos ao Senhor, nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1804, 60, 26, 'Além disso, nossos animais virão conosco; nem uma unha ficará, porque é deles que devemos tomar o que precisamos para fazer nosso culto ao Senhor, nosso Deus. Enquanto não tivermos chegado lá, não sabemos de que nos serviremos para prestar nosso culto ao Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1805, 60, 27, 'Mas o Senhor endureceu o coração do faraó, que não quis deixá-los partir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1806, 60, 28, 'O faraó disse a Moisés: “Fora de minha casa! Guarda-te de me rever, porque no dia em que vires o meu rosto morrerás!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1807, 60, 29, '“Tu o disseste – replicou Moisés – já não verei o teu rosto.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1808, 61, 1, 'O Senhor disse a Moisés: “Mandarei ainda outra praga sobre o faraó e sobre o Egito, e em consequência dela vos deixará partir daqui. Quando vos deixar partir, será definitivamente, será mesmo expulsando-vos daqui.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1809, 61, 2, 'Dirás ao povo que cada homem peça ao seu vizinho, cada mulher à sua vizinha, objetos de prata e de ouro”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1810, 61, 3, 'O Se­nhor fez que o povo ganhasse o favor dos egípcios. Moisés mesmo era muito considerado no Egito pelos servos do faraó e por todo o povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1811, 61, 4, 'Moisés disse: “Eis o que diz o Senhor: Pela meia-noite passarei através do Egito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1812, 61, 5, 'e morrerá todo primogênito na terra do Egito, desde o primogênito do faraó, que deveria assentar-se no seu trono, até o primogênito do escravo que faz girar a mó, assim como todo primogênito dos animais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1813, 61, 6, 'Haverá em toda a terra do Egito um clamor tal como nunca houve nem haverá jamais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1814, 61, 7, 'Quanto aos israelitas, porém, desde os homens até os animais, ninguém, nem mesmo um cão moverá a sua língua. Sabereis assim como o Senhor fez distinção entre os egípcios e os israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1815, 61, 8, 'Então todos esses teus servos virão procurar-me e se prostrarão diante de mim, dizendo: ‘Vai-te, tu e todo o povo que te acompanha!’. E depois disso partirei”. Moisés, grandemente irado, saiu da casa do faraó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1816, 61, 9, 'O Senhor disse a Moisés: “O faraó não vos ouvirá, a fim de que meus prodígios se multipliquem no Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1817, 61, 10, 'Moisés e Aarão tinham operado todos esses prodígios em presença do faraó. Mas o Senhor endureceu o coração do faraó, que não permitiu aos israelitas partirem de sua terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1818, 62, 1, 'O Senhor disse a Moisés e a Aarão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1819, 62, 2, '“Este mês será para vós o prin­cípio dos meses: vós o tereis como o primeiro mês do ano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1820, 62, 3, 'Dizei a toda a assembleia de Israel: no décimo dia deste mês cada um de vós tome um cordeiro por família, um cordeiro por casa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1821, 62, 4, 'Se a família for pequena demais para um cordeiro, então o tomará em comum com seu vizinho mais próximo, segundo o número das pessoas, calculando-se o que cada um pode comer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1822, 62, 5, 'O animal será sem defeito, macho, de um ano; podereis tomar tanto um cordeiro como um cabrito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1823, 62, 6, 'E o guardareis até o décimo quarto dia deste mês; então toda a assembleia de Israel o imolará no crepúsculo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1824, 62, 7, 'Tomarão do seu sangue e o porão sobre as duas ombreiras e sobre a moldura da porta das casas em que o comerem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1825, 62, 8, 'Naquela noite comerão a carne assada no fogo com pães sem fermento e ervas amargas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1826, 62, 9, 'Nada comereis dele que seja cru, ou cozido, mas será assado no fogo completamente com a cabeça, as pernas e as entranhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1827, 62, 10, 'Nada deixareis dele até pela manhã; se sobrar alguma coisa, a queimareis no fogo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1828, 62, 11, 'Eis a maneira como o comereis: tereis cingidos os vossos rins, vossas sandálias nos pés e vosso cajado na mão. Vós comereis apressadamente: é a Páscoa do Senhor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1829, 62, 12, 'Naquela noite, passarei através do Egito, e ferirei os primogênitos no Egito, tanto os dos homens como os dos animais, e exercerei minha justiça contra todos os deuses do Egito. Eu sou o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1830, 62, 13, 'O sangue sobre as casas em que habitais vos servirá de sinal (de proteção): vendo o sangue, passarei adiante, e não sereis atingidos pelo flagelo destruidor, quando eu ferir o Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1831, 62, 14, 'Conservareis a memória daquele dia, celebrando-o com uma festa em honra do Senhor: fareis isso de geração em geração, pois é uma institui­ção perpétua.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1832, 62, 15, 'Comereis pão sem fermento durante sete dias. Logo ao primeiro dia tirareis de vossas casas o fermento, pois todo o que comer pão fermentado, desde o primeiro dia até o sétimo, será cortado de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1833, 62, 16, 'No primeiro dia, assim como no sétimo, tereis uma santa assembleia. Durante esses dias não se fará trabalho algum, exceto a preparação da comida para todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1834, 62, 17, 'Guardareis (a festa) dos Ázimos, porque foi naquele dia que tirei do Egito vossos exércitos. Guardareis aquele dia de geração em geração: é uma instituição perpétua.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1835, 62, 18, 'No primeiro mês, desde a tarde do décimo quarto dia do mês até a tarde do vigésimo primeiro, comereis pães sem fermento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1836, 62, 19, 'Durante sete dias não haverá fermento em vossas casas: se alguém comer pão fermentado, será cortado da assembleia de Israel, quer se trate de estrangeiro ou natural do país.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1837, 62, 20, 'Não comereis pão fermentado: em todas as vossas casas comereis ázimos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1838, 62, 21, 'Moisés convocou todos os anciãos de Israel e disse-lhes: “Ide e escolhei um cordeiro por família, e imolai a Páscoa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1839, 62, 22, 'Depois disso, tomareis um feixe de hissopo, o ensopareis no sangue que estiver na bacia e aspergireis com esse sangue a moldura e as duas ombreiras da porta. Nenhum de vós transporá o limiar de sua casa até pela manhã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1840, 62, 23, 'Quando o Senhor passar para ferir o Egito, vendo o sangue sobre a moldura e sobre as duas ombreiras da porta, passará adiante e não permitirá ao destruidor entrar em vossas casas para ferir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1841, 62, 24, 'Observareis esse costume como uma instituição perpétua para vós e vossos filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1842, 62, 25, 'Quando tiverdes penetrado na terra que o Senhor vos dará, como prometeu, observareis esse rito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1843, 62, 26, 'E quando vossos filhos vos disserem: ‘Que significa esse rito?’ – respondereis:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1844, 62, 27, '‘É o sacrifício da Páscoa, em honra do Senhor que, ferindo os egípcios, passou por cima das casas dos israelitas no Egito e preservou nossas casas’.” O povo inclinou-se e prostrou-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1845, 62, 28, 'Em seguida, retiraram-se os israelitas para fazerem o que o Senhor tinha ordenado a Moisés e a Aarão. Assim o fizeram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1846, 62, 29, 'Pelo meio da noite, o Senhor feriu todos os primogênitos no Egito, desde o primogênito do faraó, que devia assentar-se no trono, até o primogênito do cativo que estava no cárcere, e todos os primogênitos dos animais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1847, 62, 30, 'O faraó levantou-se durante a noite, assim como todos os seus servos e todos os egípcios e fez-se um grande clamor no Egito, porque não havia casa em que não houvesse um morto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1848, 62, 31, 'Naquela mesma noite, o rei mandou chamar Moisés e Aarão e disse-lhes: “Ide! Saí do meio do meu povo, vós e os israelitas. Ide prestar um culto ao Senhor, como o dissestes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1849, 62, 32, 'Tomai vossas ovelhas e vossos bois, como o pedistes. Ide e abençoai-me”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1850, 62, 33, 'Os egípcios instavam com o povo para que saísse quanto antes do país. “Vamos morrer todos” – diziam eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1851, 62, 34, 'O povo tomou a sua massa antes que fosse levedada; cada um carregava em seus ombros a cesta embrulhada em seu manto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1852, 62, 35, 'Os israelitas, segundo a ordem de Moisés, tinham pedido aos egípcios objetos de prata, objetos de ouro e vestes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1853, 62, 36, 'O Senhor lhes fizera ganhar o favor dos egípcios, que atenderam ao seu pedido. Foi assim que despojaram os egípcios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1854, 62, 37, 'Os israelitas partiram de Ramsés para Sucot, em número de seiscentos mil homens, aproximadamente, sem contar os meninos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1855, 62, 38, 'Além disso, acompanhava-os uma numerosa multidão, bem como rebanhos consideráveis de ovelhas e de bois.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1856, 62, 39, 'Cozeram bolos ázimos da massa que levaram do Egito, pois esta não se tinha fermentado, porque tinham sido lançados fora do país e não puderam deter-se nem fazer provisões.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1857, 62, 40, 'A permanência dos israelitas no Egito durara quatrocentos e trinta anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1858, 62, 41, 'Exatamente no fim desses quatrocentos e trinta anos, todos os exércitos do Senhor saíram do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1859, 62, 42, 'Foi uma noite de vigília para o Senhor, a fim de tirá-los do Egito: essa mesma noite é uma vigília a ser celebrada de geração em geração por todos os israelitas, em honra do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1860, 62, 43, 'O Senhor disse a Moisés e a Aarão: “Eis a regra relativa à Páscoa: nenhum estrangeiro comerá dela;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1861, 62, 44, 'todo escravo adquirido a preço de dinheiro, e que tiver sido circuncidado, comerá dela,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1862, 62, 45, 'mas nem o estrangeiro nem o mercenário come­rão dela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1863, 62, 46, 'O cordeiro será comido em uma mesma casa: tu não levarás nada de sua carne para fora da casa e não lhe quebrarás osso algum.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1864, 62, 47, 'Toda a assembleia de Israel celebrará a Páscoa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1865, 62, 48, 'Se um estrangeiro, habitando em tua casa, quiser celebrar a Páscoa em honra do Senhor, que primeiro seja circuncidado todo varão de sua casa e somente depois poderá fazê-lo e será tratado com a mesma igualdade que o natural do país; mas nenhum incircunciso comerá a Páscoa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1866, 62, 49, 'Haverá uma mesma lei para o natural e o estrangeiro que pere­grina entre vós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1867, 62, 50, 'Todos os israelitas fizeram o que o Senhor havia ordenado a Moisés e a Aarão. Obedeceram-lhes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1868, 62, 51, 'Naquele mesmo dia, o Se­nhor fez sair do Egito os israelitas, como fileiras de um exército.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1869, 63, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1870, 63, 2, '“Tu me consagrarás todo primogênito entre os israelitas, tanto homem como animal: ele será meu”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1871, 63, 3, 'Moisés disse ao povo: “Conservareis a memória deste dia, em que saístes do Egito, da casa da servidão, porque foi pelo poder de sua mão que o Senhor vos fez sair deste lugar; não comereis pão fermentado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1872, 63, 4, 'Vós saís hoje do Egito, no mês das espigas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1873, 63, 5, 'Assim, pois, quando o Senhor te houver introduzido na terra dos cananeus, dos hiteus, dos amorreus, dos heveus e dos jebuseus, que jurou a teus pais te dar, terra que mana leite e mel, observarás este rito neste mesmo mês.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1874, 63, 6, 'Durante sete dias, comerás pães sem fermento e, no sétimo dia, haverá uma festa em honra do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1875, 63, 7, 'Serão comidos pães sem fermento durante sete dias. Não se verão em tua casa, em toda a extensão do território, nem pães fermentados nem fermento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1876, 63, 8, 'Explicarás então a teu filho: isso é em memória do que o Senhor fez por mim, quando saí do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1877, 63, 9, 'Será isso para ti como um sinal sobre tua mão, como uma marca entre os teus olhos, a fim de que tenhas na boca a lei do Senhor, porque foi graças à sua poderosa mão que o Senhor te fez sair do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1878, 63, 10, 'Observarás a cada ano essa prescrição no tempo prescrito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1879, 63, 11, '“Quando o Senhor te houver introduzido na terra dos cananeus, como ele jurou a ti e a teus pais, e te houver dado essa terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1880, 63, 12, 'consagrarás ao Senhor todo primogênito; mesmo os primogênitos de teus animais, os machos, serão do Se­nhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1881, 63, 13, 'Entretanto, resgatarás com um cordeiro todo primogênito do jumento; do contrário, lhe quebrarás a nuca. Todo primogênito dos homens entre teus filhos, o resgatarás igualmente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1882, 63, 14, 'E, quando teu filho te perguntar um dia o que isso significa, lhe dirás: é que o Senhor nos tirou do Egito com sua mão poderosa, da casa da servidão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1883, 63, 15, 'E, como o faraó se obstinasse em não nos deixar partir, o Senhor matou todos os primogênitos do Egito, desde os primogênitos dos homens até os dos animais. Eis por que sacrifico ao Senhor todos os primogênitos machos dos animais, e devo resgatar todo primogênito entre meus filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1884, 63, 16, 'Isso será como um sinal sobre tua mão e como uma marca entre teus olhos, porque foi pelo poder de sua mão que o Senhor nos tirou do Egito.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1885, 63, 17, 'Tendo o faraó deixado partir o povo, Deus não o conduziu pelo caminho da terra dos filisteus, que é, no entanto, o mais curto, pois disse: “Talvez o povo possa arrepender-se, no momento em que tiver de enfrentar um combate e voltar para o Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1886, 63, 18, 'Por isso, Deus fez com que o povo desse uma volta pelo deserto, para o lado do mar Vermelho. Os israelitas partiram do Egito em boa ordem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1887, 63, 19, 'Moisés levou os ossos de José, porque este fizera os filhos de Israel jurarem: “Quando Deus vos visitar, levareis daqui os meus ossos convosco”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1888, 63, 20, 'Tendo partido de Sucot, acamparam em Etam, na extremidade do deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1889, 63, 21, 'O Senhor ia adiante deles: de dia numa coluna de nuvens para guiá-los pelo caminho; e de noite numa coluna de fogo para alumiá-los; de sorte que podiam marchar de dia e de noite.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1890, 63, 22, 'Nunca a coluna de nuvens deixou de preceder o povo durante o dia, nem a coluna de fogo durante a noite.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1891, 64, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1892, 64, 2, '“Dize aos israelitas que mudem de direção e venham acampar diante de Piairot, entre Magdol e o mar, defronte de Baal Sefon: acampareis defronte desse lugar, perto do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1893, 64, 3, 'O faraó vai pensar: os israelitas perderam-se no país, e o deserto fechou-lhes a passagem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1894, 64, 4, 'Endurecerei o coração do faraó, e ele os perseguirá; mas eu triunfarei gloriosamente sobre o faraó e sobre todo o seu exército, e os egípcios saberão que eu sou o Senhor”. Os israelitas obedeceram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1895, 64, 5, 'Quando se anunciou ao rei do Egito que o povo tinha fugido, o coração do faraó e de seus servos voltou-se contra o povo: “Que fizemos – disseram eles – deixando partir Israel e renunciando assim ao seu serviço!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1896, 64, 6, 'O faraó mandou preparar seu carro e levou com ele suas tropas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1897, 64, 7, 'Escolheu seiscentos carros dos melhores e todos os carros egípcios com homens de guerra em cada um deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1898, 64, 8, 'O Senhor endureceu o coração do faraó, rei do Egito, e este se pôs a perseguir os filhos de Israel. Eles haviam partido de cabeça erguida.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1899, 64, 9, 'Puseram-se os egípcios a persegui-los e alcançaram-nos em seu acampamento à beira do mar: todos os cavalos dos carros do faraó, seus cavaleiros e seu exército alcançaram-nos perto de Piairot, defronte de Baal Sefon.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1900, 64, 10, 'Aproximando-se o faraó, os israelitas, ao levantarem os olhos, viram os egípcios que vinham ao seu encalço. Foram tomados de espanto e invocaram o Senhor, clamando em alta voz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1901, 64, 11, 'E disseram a Moisés: “Não havia, porventura, túmulos no Egito, para que nos conduzisses a morrer no deserto? Por que nos fizeste isso, tirando-nos do Egito?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1902, 64, 12, 'Não te dizíamos no Egito: deixa-nos servir os egípcios! É melhor ser escravos dos egípcios do que morrer no deserto”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1903, 64, 13, 'Moisés respondeu ao povo: “Não temais! Tende ânimo, e vereis a liber­tação que o Senhor vai operar hoje em vosso favor. Os egípcios que hoje vedes, não os tornareis a ver jamais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1904, 64, 14, 'O Senhor combaterá por vós; quanto a vós, nada tereis a fazer”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1905, 64, 15, 'O Senhor disse a Moisés: “Por que clamas a mim? Dize aos filhos de Israel que se ponham a caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1906, 64, 16, 'E tu, levanta a tua vara, estende a mão sobre o mar e fere-o, para que os israelitas possam atravessá-lo a pé enxuto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1907, 64, 17, 'Vou endurecer o coração dos egípcios, para que se ponham ao teu encalço, e triunfarei gloriosamente sobre o faraó e sobre todo o seu exército, seus carros e seus cavaleiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1908, 64, 18, 'Os egípcios saberão que eu sou o Senhor quando tiver alcançado esse glorioso triunfo sobre o faraó, seus carros e seus cavaleiros”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1909, 64, 19, 'O anjo de Deus, que marchava à frente do exército dos israelitas, mudou de lugar e passou para trás; a coluna de nuvens que os precedia pôs-se detrás deles,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1910, 64, 20, 'entre o acampamento dos egípcios e o de Israel. Era obscura, e alumiava a noite. E não puderam aproximar-se um do outro, durante a noite inteira.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1911, 64, 21, 'Moisés estendeu a mão sobre o mar. O Senhor fê-lo recuar com um vento impetuoso vindo do oriente, que soprou toda a noite. E pôs o mar a seco. As águas dividiram-se');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1912, 64, 22, 'e os israelitas desceram a pé enxuto no meio do mar, enquanto as águas formavam uma muralha à direita e à esquerda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1913, 64, 23, 'Os egípcios os perseguiram: todos os cavalos do faraó, seus carros e seus cavaleiros internaram-se após eles no leito do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1914, 64, 24, 'À vigília da manhã, o Senhor, do alto da coluna de fogo e da de nuvens, olhou para o acampamento dos egípcios e semeou o pânico no meio deles.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1915, 64, 25, 'Embaraçou-lhes as rodas dos carros de tal sorte que, só dificilmente, conseguiam avançar. Disseram então os egípcios: “Fujamos diante de Israel, porque o Senhor combate por eles contra o Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1916, 64, 26, 'O Senhor disse a Moisés: “Estende tua mão sobre o mar, e as águas se voltarão sobre os egípcios, seus carros e seus cavaleiros”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1917, 64, 27, 'Moisés estendeu a mão sobre o mar, e este, ao romper da manhã, voltou ao seu nível habitual. Os egípcios que fugiam foram de encontro a ele, e o Senhor derribou os egípcios no meio do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1918, 64, 28, 'As águas voltaram e cobriram os carros, os cavaleiros e todo o exército do faraó que havia descido no mar ao encalço dos israelitas. Não ficou um sequer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1919, 64, 29, 'Mas os israelitas tinham andado a pé enxuto no leito do mar, enquanto as águas formavam uma muralha à direita e à esquerda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1920, 64, 30, 'Foi assim que naquele dia o Senhor livrou Israel da mão dos egípcios. E Israel viu os cadáveres dos egípcios na praia do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1921, 64, 31, 'Viu Israel o grande poder que o Senhor tinha exercido contra os egípcios. Por isso, o povo temeu o Senhor e confiou nele e em seu servo Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1922, 65, 1, 'Então Moisés e os israelitas entoaram em honra do Senhor o seguinte cântico: “Cantarei ao Senhor, porque ele manifestou sua glória. Precipitou no mar cavalos e cavaleiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1923, 65, 2, 'O Senhor é a minha força e o objeto do meu cântico; foi ele quem me salvou. Ele é o meu Deus – eu o celebrarei; o Deus de meu pai – eu o exaltarei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1924, 65, 3, 'O Senhor é o herói dos combates, seu nome é Javé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1925, 65, 4, 'Lançou no mar os carros do faraó e o seu exército; a elite de seus combatentes afogou-se no mar Vermelho;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1926, 65, 5, 'o abismo os cobriu; afundaram-se nas águas como pedra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1927, 65, 6, 'A vossa (mão) direita, ó Senhor, manifestou sua força. Vossa direita aniquilou o inimigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1928, 65, 7, 'Por vossa soberana majestade derrotais vossos adversários; desencadeais vossa cólera, e ela os consome como palha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1929, 65, 8, 'Ao sopro de vosso furor amontoaram-se as águas; levantaram-se as ondas como muralha, solidificaram-se as vagas no coração do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1930, 65, 9, 'Dizia o inimigo: perseguirei, alcançarei, repartirei o despojo, satisfarei meu desejo de vingança, desembainharei a espada, minha mão os destruirá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1931, 65, 10, 'Ao sopro de vosso hálito o mar os sepultou; submergiram como chumbo na vastidão das águas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1932, 65, 11, 'Quem entre os deuses é semelhante a vós, Senhor? Quem é semelhante a vós, glorioso por vossa santidade, temível por vossos feitos dignos de louvor, e que operais prodígios?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1933, 65, 12, 'Apenas estendestes a mão, e a terra os tragou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1934, 65, 13, 'Conduzistes com bondade esse povo, que libertastes; e com vosso poder o guiastes à vossa morada santa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1935, 65, 14, 'Ao ouvir isso, estremeceram os povos. Um pavor imenso apoderou-se dos filisteus;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1936, 65, 15, 'os chefes de Edom ficaram aterrados; a angústia tomou conta dos valentes de Moab; tremeram de medo todos os habitantes de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1937, 65, 16, 'Caíram sobre eles o terror e a angústia, o poder do vosso braço os petrificou, até que tivesse passado o vosso povo, Senhor, até que tivesse passado o povo que adquiristes para vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1938, 65, 17, 'Vós o conduzireis e o plantareis na montanha que vos pertence, no lugar que preparastes para vossa habitação, Senhor, no santuário, Senhor, que vossas mãos fundaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1939, 65, 18, 'O Senhor é rei para sempre, sem fim!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1940, 65, 19, 'Os cavalos do faraó, com efeito, entraram no mar com seus carros e seus cavaleiros, e o Senhor os envolveu nas águas, enquanto os israelitas passaram a pé enxuto o leito do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1941, 65, 20, 'A profetisa Maria, irmã de Aarão, tomou seu tamborim na mão, e todas as mulheres seguiram-na dançando com tamborins.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1942, 65, 21, 'Maria as acompanhava entoando: “Cantai ao Senhor, porque fez brilhar a sua glória, precipitou no mar cavalos e cavaleiros!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1943, 65, 22, 'Moisés fez partir os israelitas do mar Vermelho e os dirigiu para o deserto de Sur. Caminharam três dias no deserto, sem encontrar água.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1944, 65, 23, 'Chegaram a Mara, onde não puderam beber de sua água, porque era amarga, de onde o nome de Mara que deram a esse lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1945, 65, 24, 'Então o povo murmurou contra Moisés: “Que havemos de beber?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1946, 65, 25, 'Moisés clamou ao Senhor, e o Senhor indicou-lhe um madeiro que ele jogou na água. E esta tornou-se doce. Foi nesse lugar que o Senhor deu ao povo preceitos e leis, e ali o provou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1947, 65, 26, 'Disse-lhe: “Se ouvires a voz do Senhor, teu Deus, e fizeres o que é reto aos seus olhos, se inclinares os ouvidos às suas ordens e observares todas as suas leis, não mandarei sobre ti nenhum dos males com que acabrunhei o Egito, porque eu sou o Senhor que te cura”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1948, 65, 27, 'E chegaram a Elim, onde havia doze fontes de água e setenta palmeiras; e ali acamparam junto das águas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1949, 66, 1, 'Toda a assembleia dos israelitas partiu de Elim e foi para o deserto de Sin, situado entre Elim e o Sinai. Era o décimo quinto dia do segundo mês após sua saída do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1950, 66, 2, 'Toda a assembleia dos israelitas pôs-se a murmurar contra Moisés e Aarão no deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1951, 66, 3, 'Disseram-lhes: “Oxalá tivéssemos sido mortos pela mão do Senhor no Egito, quando nos assentávamos diante das panelas­ de carne e tínhamos pão em abundância! Vós nos conduzistes a este deserto, para matardes de fome toda esta multidão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1952, 66, 4, 'O Senhor disse a Moisés: “Vou fazer chover pão do alto do céu. Sairá o povo e colherá diariamente a porção de cada dia. Eu o porei desse modo à prova, para ver se andará ou não segundo minhas ordens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1953, 66, 5, 'No sexto dia, quando prepararem o que tiverem ajuntado haverá o dobro do que recolhem cada dia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1954, 66, 6, 'Moisés e Aarão disseram a todos os israelitas: “Esta tarde, sabereis que foi o Senhor quem vos tirou do Egito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1955, 66, 7, 'e amanhã pela manhã vereis a sua glória, porque ele ouviu as vossas murmurações contra ele. Nós, porém, quem somos para que murmureis contra nós?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1956, 66, 8, 'Moisés disse: “Isso acontecerá quando o Senhor vos der, esta tarde, carne para comerdes e, amanhã pela manhã, pão em abundância, porque ele ouviu as murmurações que proferistes contra ele. Nós, porém, quem somos? Não é contra nós que murmurastes, mas contra o Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1957, 66, 9, 'Moisés disse a Aarão: “Dize a toda a assembleia dos israelitas: apresentai-vos diante do Senhor, porque ele ouviu vossas murmurações”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1958, 66, 10, 'Enquanto Aarão falava a toda a assembleia dos israelitas, olharam para o deserto e eis que apareceu na nuvem a glória do Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1959, 66, 11, 'E o Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1960, 66, 12, '“Ouvi as murmurações dos israelitas. Dize-lhes: Esta tarde, antes que escureça, comereis carne e, amanhã de manhã, vos fartareis de pão; e sabereis que sou o Senhor, vosso Deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1961, 66, 13, 'À tarde, com efeito, subiram codornizes (do horizonte) e cobriram o acampamento; e, no dia seguinte pela manhã, havia uma camada de orvalho em torno de todo o acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1962, 66, 14, 'E, tendo evaporado esse orvalho, eis que sobre a superfície do deserto estava uma coisa miúda, granulosa, miúda como a geada sobre a terra!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1963, 66, 15, 'Vendo isso, disseram os filhos de Israel uns aos outros: “Que é isso?”, pois não sabiam o que era. Moisés disse-lhes: “Este é o pão que o Senhor vos manda para comer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1964, 66, 16, 'Eis que vos ordena o Senhor: Ajunte cada um o quanto lhe for necessário para comer; para aqueles que estão em sua tenda, um gomor por cabeça, segundo o número das pessoas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1965, 66, 17, 'Assim fizeram os israelitas: ajuntaram uns mais, outros menos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1966, 66, 18, 'Mas, quando se media com o gomor, aconteceu que o que tinha ajuntado muito não tinha demais e, ao que tinha ajuntado pouco, não lhe faltava: cada um havia recolhido segundo a sua necessidade.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1967, 66, 19, 'Moisés disse-lhes: “Ninguém reserve dele para o dia seguinte”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1968, 66, 20, 'Alguns não o ouviram e guardaram dele até pela manhã; mas criou vermes e cheirou mal. Moisés irritou-se contra eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1969, 66, 21, 'Todas as manhãs fizeram a sua provisão, cada um segundo suas necessidades. E, quando vinha o calor do sol, derretia-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1970, 66, 22, 'No sexto dia, reco­lheram uma dupla quantidade de alimento, dois gomores para cada um. Vieram todos os chefes da assembleia e contaram-no a Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1971, 66, 23, 'Este lhes disse: “É isso o que o Senhor ordenou. Amanhã é um dia de repouso, o sábado consagrado ao Senhor. Por isso, o que tendes a cozer no forno, cozei-o, e o que tendes a cozer em água, cozei-o; e o que sobrar, ponde-o de lado até pela manhã”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1972, 66, 24, 'Guardaram-no até o dia seguinte, segundo a ordem de Moisés; e não cheirou mal, nem se acharam vermes nele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1973, 66, 25, '“Comei-o hoje – disse Moisés – porque é o dia do sábado do Senhor; hoje não o achareis no campo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1974, 66, 26, 'Durante seis dias o ajuntareis; mas o sétimo é o sábado: nele não haverá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1975, 66, 27, '(No sétimo dia alguns saíram para fazer sua provisão, mas nada encontraram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1976, 66, 28, 'Então o Senhor disse a Moisés: ‘Até quando vos recusareis a observar meus mandamentos e minhas leis?’.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1977, 66, 29, 'Considerai que, se o Senhor vos deu o sábado, vos dá ele no sexto dia alimento para dois dias. Fique cada um onde está, e ninguém saia de sua habitação no sétimo dia.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1978, 66, 30, 'Assim o povo repousou no sétimo dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1979, 66, 31, 'Os israelitas deram a esse alimento o nome de maná. Assemelhava-se à semente de coentro: era branco e tinha o sabor de uma torta de mel.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1980, 66, 32, 'Moisés disse: “Eis o que ordena o Senhor: que se encha dele um gomor para ser conservado para vossas gerações futuras, a fim de que vejam o pão com que vos sustentei no deserto, depois de vos ter tirado do Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1981, 66, 33, 'E Moisés disse a Aarão: “Toma uma vasilha e põe nela a quantia de um gomor de maná, e deposita-o diante do Senhor, a fim de conservá-lo para vossos descendentes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1982, 66, 34, 'Aarão, segundo a ordem do Senhor a Moisés, depositou-o diante do Testemunho para ser conservado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1983, 66, 35, 'Os israelitas comeram o maná durante quarenta anos, até a sua chegada a uma terra habi­tada. Comeram o maná até que chegaram aos confins da terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1984, 66, 36, '(O gomor é a décima parte do efá.)*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1985, 67, 1, 'Segundo uma ordem do Senhor, toda a assembleia dos israelitas partiu, por etapas, do deserto de Sin. Acamparam em Rafidim, onde não havia água para o povo beber.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1986, 67, 2, 'E vieram então discutir com Moisés: “Dá-nos água para beber” – disseram eles. Moisés respondeu-lhes: “Por que procurais contendas comigo? Por que provocais o Senhor?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1987, 67, 3, 'Entretanto, o povo que ali estava privado de água e devorado pela sede, murmurava contra Moisés: “Por que nos fizeste sair do Egito? Para nos fazer morrer de sede com nossos filhinhos e nossos reba­nhos?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1988, 67, 4, 'Então dirigiu Moisés esta prece ao Senhor: “Que farei a este povo? Mais um pouco e irão apedrejar-me”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1989, 67, 5, 'O Senhor respondeu a Moisés: “Passa adiante do povo, e leva contigo alguns dos anciãos de Israel; toma na mão tua vara, com que feriste o Nilo, e vai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1990, 67, 6, 'Eis que estarei ali diante de ti. Sobre o rochedo do monte Horeb ferirás o rochedo e a água jorrará dele: assim o povo poderá beber”. Isso fez Moisés em presença dos anciãos de Israel.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1991, 67, 7, 'Chamaram esse lugar Massa e Meriba, por causa da contenda que os israelitas tiveram com ele, e porque tinham provocado o Senhor, dizendo: “O Senhor está ou não no meio de nós?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1992, 67, 8, 'Amalec veio atacar Israel em Rafidim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1993, 67, 9, 'Moisés disse a Josué: “Escolhe-nos homens e vai combater Amalec. Amanhã estarei no alto da colina com a vara de Deus na mão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1994, 67, 10, 'Josué obedeceu Moisés e foi combater Amalec, enquanto Moisés, Aarão e Hur subiam ao alto da colina.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1995, 67, 11, 'E, quando Moisés tinha a mão levantada, Israel vencia, mas logo que a abaixava, Amalec triunfava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1996, 67, 12, 'Mas como se fatigassem os braços de Moisés, puseram-lhe uma pedra por baixo e ele assentou-se nela, enquanto Aarão e Hur lhe sustentavam as mãos de cada lado: suas mãos puderam assim conservar-se levantadas até o pôr do sol,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1997, 67, 13, 'e Josué derrotou Amalec e seu povo a fio da espada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1998, 67, 14, 'O Senhor disse a Moisés: “Escreve isto para lembrar, e dize a Josué que eu apagarei a memória de Amalec de debaixo do céu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (1999, 67, 15, 'Moisés construiu um altar que chamou de “O Senhor é minha bandeira”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (2000, 67, 16, '“Já que a mão – disse ele – foi levantada contra o trono do Senhor, o Senhor está em guerra perpétua contra Amalec.”');
