INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3885, 124, 35, 'e ainda dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano, para o sacrifício pacífico. Essa foi a oferta de Elisur, filho de Sedeur.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3886, 124, 36, 'No quinto dia, o príncipe dos filhos de Simeão, Salamiel, filho de Surisadai,');
