INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3892, 124, 42, 'No sexto dia, o príncipe dos filhos de Gad, Eliasaf, filho de Reuel,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3893, 124, 43, 'ofereceu um prato de prata pesando cento e trinta siclos, uma bacia de prata pesando setenta siclos, segundo o siclo do santuá­rio, ambos cheios de flor de farinha amassada com óleo, para a oblação;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3894, 124, 44, 'uma taça de ouro pesando dez siclos, cheia de perfume; 45 um novilho, um carneiro e um cordeiro de um ano para o holocausto;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3895, 124, 45, 'um bode para o sacrifício pelo pecado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3896, 124, 46, 'e ainda dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano, para o sacrifício pacífico. Essa foi a oferta de Eliasaf, filho de Reuel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3897, 124, 47, 'No sétimo dia, o príncipe dos filhos de Efraim, Elisama, filho de Amiud,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3898, 124, 48, 'ofereceu um prato de prata pesando cento e trinta siclos, uma bacia de prata pesando setenta siclos, segundo o siclo do santuário, ambos cheios de flor de farinha amassada com azeite, para a oblação;');
