INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3900, 124, 50, 'um novilho, um carneiro e um cordeiro de um ano para o holocausto;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3901, 124, 51, 'um bode para o sacrifício pelo pecado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3902, 124, 52, 'e ainda dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano para o sacrifício pacífico. Essa foi a oferta de Elisama, filho de Amiud.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3903, 124, 53, 'No oitavo dia, o príncipe dos filhos de Manassés, Gamaliel, filho de Fadassur,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3904, 124, 54, 'ofereceu um prato de prata pesando cento e trinta siclos, uma bacia de prata pesando setenta siclos, segundo o siclo do santuário, ambos cheios de flor de farinha amassada com azeite, para a obla­ção;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3905, 124, 55, 'uma taça de ouro pesando dez siclos, cheia de perfume;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3906, 124, 56, 'um novilho, um carneiro e um cordeiro de um ano para o holocausto;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3907, 124, 57, 'um bode para o sacrifício pelo pecado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3908, 124, 58, 'e ainda dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano para o sacrifício pacífico. Essa foi a oferta de Gamaliel, filho de Fadassur.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3909, 124, 59, 'No nono dia, o príncipe dos filhos de Benjamim, Abidã, filho de Gedeão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3910, 124, 60, 'ofereceu um prato de prata pesando cento e trinta siclos, uma bacia de prata pesando setenta siclos, segundo o siclo do santuário, ambos cheios de flor de farinha amassada com óleo, para a oblação;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3911, 124, 61, 'uma taça de ouro pesando dez siclos, cheia de perfume;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3912, 124, 62, 'um novilho, um carneiro e um cordeiro de um ano para o holocausto;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3913, 124, 63, 'um bode para o sacrifício pelo pecado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3914, 124, 64, 'e ainda dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano, para o sacrifício pacífico. Essa foi a oferta de Abidã, filho de Gedeão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3915, 124, 65, 'No décimo dia, o príncipe dos filhos de Dã, Aiezer, filho de Amisadai,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3916, 124, 66, 'ofereceu um prato de prata pesando cento e trinta siclos, uma bacia de prata pesando setenta siclos, segundo o siclo do santuário, ambos cheios de flor de farinha amassada com óleo, para a oblação;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3917, 124, 67, 'uma taça de ouro pesando dez ciclos, cheia de perfume;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3918, 124, 68, 'um novilho, um carneiro e um cordeiro de um ano para o holocausto;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3919, 124, 69, 'um bode para o sacrifício pelo pecado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3920, 124, 70, 'e ainda dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano, para o sacrifício pacífico. Essa foi a oferta de Aiezer, filho de Amisadai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3921, 124, 71, 'No décimo primeiro dia, o príncipe dos filhos de Aser, Fegiel, filho de Ocrã,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3922, 124, 72, 'ofereceu um prato de prata pesando cento e trinta siclos, uma bacia de prata pesando setenta siclos, segundo o siclo do santuário, ambos cheios de flor de farinha amassada com óleo, para a oblação;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3923, 124, 73, 'uma taça de ouro pesando dez siclos, cheia de perfume;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3924, 124, 74, 'um novilho, um carneiro e um cordeiro de um ano para o holocausto;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3925, 124, 75, 'um bode para o sacrifício pelo pecado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3926, 124, 76, 'e ainda dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano, para o sacrifício pacífico. Essa foi a oferta de Fegiel, filho de Ocrã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3927, 124, 77, 'No décimo segundo dia, o príncipe dos filhos de Neftali, Aíra, filho de Enã,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3928, 124, 78, 'ofereceu um prato de prata pesando cento e trinta siclos, uma bacia de prata pesando setenta siclos, segundo o siclo do santuário, ambos cheios de flor de farinha amassada com óleo, para a oblação;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3929, 124, 79, 'uma taça de ouro pesando dez siclos, cheia de perfume;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3930, 124, 80, 'um novilho, um carneiro e um cordeiro de um ano para o holocausto;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3931, 124, 81, 'um bode para o sacrifício pelo pecado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3932, 124, 82, 'e ainda dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano, para o sacrifício pacífico. Essa foi a oferta de Aíra, filho de Enã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3933, 124, 83, 'Estes foram os presentes que os príncipes de Israel ofereceram para a dedicação do altar no dia em que foi ungido: doze pratos de prata, doze bacias de prata e doze taças de ouro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3934, 124, 84, 'Cada prato de prata pesava cento e trinta siclos, e cada bacia, setenta siclos; o peso total da prata desses objetos era de dois mil e quatrocentos siclos, segundo o siclo do santuário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3935, 124, 85, 'As doze taças de ouro para o perfume pesavam cada uma dez siclos, segundo o siclo do santuário; o peso total de ouro das taças era de cento e vinte si­clos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3936, 124, 86, 'O total dos animais para o holo­causto era de doze novilhos, doze carneiros, doze cordeiros de um ano com suas oblações e doze bodes em sacrifício pelo pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3937, 124, 87, 'O total de animais para o sacrifício pacífico era de vinte e quatro bois, sessenta carneiros, sessenta bodes e sessenta cordeiros de um ano. Esses foram os presentes oferecidos para a dedicação do altar depois de ungido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3938, 124, 88, 'Quando Moisés entrava na tenda de reunião para falar com o Senhor, ouvia a voz que lhe falava de cima do propiciatório colocado sobre a arca do testemunho, entre os dois querubins. E falava com o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3939, 125, 1, 'O Senhor disse a Moisés: “Dize a Aarão o seguinte:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3940, 125, 2, 'Quando colocares as lâmpadas no candelabro, faça de tal modo que as sete lâmpadas projetem sua luz para a frente do mesmo candelabro”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3941, 125, 3, 'Aarão assim fez, e colocou as lâmpadas na parte dianteira do candelabro, como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3942, 125, 4, 'O candelabro era feito de ouro batido: seu pé, suas flores, tudo era de ouro batido; e foi feito segundo o modelo que o Senhor tinha mostrado a Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3943, 125, 5, 'O Senhor disse a Moisés o seguinte:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3944, 125, 6, '“Toma os levitas do meio dos israelitas e purifica-os.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3945, 125, 7, 'Eis como farás para puri­ficá-los: asperge-os com a água da expiação e eles passem uma navalha sobre todo o corpo, lavem as suas vestes e purifiquem-se a si mesmos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3946, 125, 8, 'Tomem então um touro com a sua oblação de flor de farinha amassada com óleo; e tomarás tu um segundo touro em sacrifício pelo pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3947, 125, 9, 'Farás aproximarem-se os levitas diante da tenda de reunião, e convocarás toda a assembleia dos israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3948, 125, 10, 'Mandarás os levitas que se aproximem diante do Senhor, e os israelitas porão suas mãos sobre eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3949, 125, 11, 'Aarão oferecerá os levitas ao Senhor, como oferta agitada, em nome dos israelitas, a fim de que eles sejam destinados ao serviço do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3950, 125, 12, 'Os levitas porão suas mãos sobre as cabeças dos touros, dos quais oferecerá um em sacrifício pelo pecado, e o outro em holocausto ao Senhor para fazer a expiação em favor dos levitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3951, 125, 13, 'Mandarás que os levitas se conservem de pé diante de Aarão e seus filhos, e os apresentarás em oferta agitada ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3952, 125, 14, 'Separarás desse modo os levitas do meio dos israelitas, e eles serão meus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3953, 125, 15, 'Depois disso, virão para a tenda de reunião para me servirem. Assim os purificarás e os apresentarás em oferta agitada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3954, 125, 16, 'Porque eles me são inteiramente reservados entre os israelitas; eu os tomei para mim em lugar de todo primogênito, daqueles que nascem primeiro entre os filhos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3955, 125, 17, 'Porque todo primo­gênito entre os israelitas, homem ou animal, é meu, eu os consagrei a mim no dia em que feri os primogênitos no Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3956, 125, 18, 'Eu tomei os levitas em lugar de todos os primogênitos dos israelitas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3957, 125, 19, 'e, tirados do meio do povo, dei-os inteiramente a Aarão e seus filhos para fazerem o serviço dos israelitas na tenda de reunião, e para fazerem a expiação em favor dos israelitas, de sorte que estes últimos não sejam feridos por nenhuma praga quando se aproximarem do santuário”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3958, 125, 20, 'Moisés, Aarão e toda a assembleia dos israelitas fizeram, pois, acerca dos levitas, tudo o que o Senhor tinha ordenado a Moisés a seu respeito. Assim fizeram os filhos de Israel:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3959, 125, 21, 'purificaram-se e lavaram suas vestes. Aarão apresentou-os em oferta agitada diante do Senhor, e fez a expiação por eles, a fim de purificá-los.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3960, 125, 22, 'E vieram em seguida os levitas para a tenda de reunião para fazer o seu serviço, em presença de Aarão e seus filhos. Como o Senhor tinha ordenado a Moisés acerca dos levitas, assim se fez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3961, 125, 23, 'O Senhor disse a Moisés o seguinte:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3962, 125, 24, '“Esta é a lei relativa aos levitas: desde os vinte e cinco anos para cima, o levita será admitido ao serviço na tenda de reunião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3963, 125, 25, 'A partir dos cinquenta anos, renunciará às suas funções e cessará de servir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3964, 125, 26, 'Ajudará seus irmãos na tenda de reunião, zelando pelo que lhe foi confiado; mas não exercerá mais as suas funções. Desse modo disporás os levitas nos seus encargos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3965, 126, 1, 'No primeiro mês, do segundo ano após a saída do Egito, o Senhor falou a Moisés, no deserto do Sinai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3966, 126, 2, 'Disse-lhe: “Celebrem os israelitas a Páscoa no tempo fixado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3967, 126, 3, 'Vós a celebrareis no décimo quarto dia deste mês, conforme foi marcado, entre as duas tardes, e fareis essa festa segundo todas as leis e prescrições que lhes são próprias”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3968, 126, 4, 'Moisés mandou que os israelitas celebrassem a Páscoa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3969, 126, 5, 'Celebraram-na no décimo quarto dia do primeiro mês, entre as duas tardes, no deserto do Sinai. Os israelitas fizeram tudo o que o Senhor tinha ordenado a Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3970, 126, 6, 'Ora, alguns homens, não podendo fazer a Páscoa naquele dia, pois se tinham manchado tocando em um cadáver, apresentaram-se a Moisés e Aarão naquele dia,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3971, 126, 7, 'e disseram-lhe: “Estamos impuros, porque tocamos em um cadáver; vamos por isso ser privados de apresentar com os outros israelitas a oferta do Senhor no dia fixado?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3972, 126, 8, '“Esperai” – respondeu Moisés – “vou consultar o Senhor, para saber o que ordena a vosso respeito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3973, 126, 9, 'Então o Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3974, 126, 10, '“Dize aos israelitas o seguinte: Se um de vós ou de vossos descendentes estiver impuro por haver tocado em um morto, ou se achar em viagem longe de vós, não deixará de celebrar a Páscoa em honra do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3975, 126, 11, 'Eles a celebrarão aos catorze dias do segundo mês, entre as duas tardes. Deverão comê-la com pães sem fermento e ervas amargas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3976, 126, 12, 'Nada deixarão dela para o dia seguinte, não quebrarão nenhum de seus ossos, e farão essa festa segundo todas as prescrições relativas à Páscoa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3977, 126, 13, 'Mas se alguém, estando puro, não se encontrar em viagem e, todavia, não fizer a Páscoa, será cortado do seu povo, porque não apresentou a oferta do Senhor no tempo estabelecido; este levará a pena do seu pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3978, 126, 14, 'Se o estrangeiro que mora no meio de vós fizer a Páscoa do Senhor, terá de se conformar às leis e às prescrições relativas à Páscoa. Haverá uma só lei, que será a mesma para vós, para o estrangeiro e para o natural”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3979, 126, 15, 'No dia em que o tabernáculo foi levantado, a nuvem o cobriu. E sobre o tabernáculo, isto é, na tenda do testemunho, desde a tarde até pela manhã, apareceu como uma espécie de fogo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3980, 126, 16, 'Assim acontecia continuamente: a nuvem cobria o tabernáculo e à noite assemelhava-se ao fogo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3981, 126, 17, 'Quando se levantava a nuvem sobre a tenda, os israelitas punham-se em marcha; no lugar onde a nuvem parava, aí acampavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3982, 126, 18, 'À ordem do Senhor levantavam o acampamento, e à sua ordem o assentavam de novo; e ficavam no acampamento enquanto a nuvem permanecesse sobre o tabernáculo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3983, 126, 19, 'Mesmo quando ela se detinha muito tempo sobre ele, os israelitas não partiam, e aguardavam a ordem do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3984, 126, 20, 'E se acontecia de a nuvem ficar poucos dias sobre o tabernáculo, então, à ordem do Senhor, permaneciam acampados e, à ordem do Senhor, se punham em marcha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3985, 126, 21, 'Se a nuvem se detinha desde a tarde até pela manhã, e chegada a manhã se levantava, eles partiam; se depois de um dia e uma noite a nuvem se levantava, desmanchavam o acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3986, 126, 22, 'Mas se a nuvem se detinha sobre o tabernáculo vários dias, um mês ou mesmo um ano, os israelitas permaneciam acampados e não partiam; só partiam quando se levantava a nuvem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3987, 126, 23, 'Levantavam e desmanchavam o acampamento segundo a ordem do Senhor. E observavam o mandamento do Senhor, como este lhes tinha ordenado por Moisés.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3988, 127, 1, 'O Senhor disse a Moisés o seguinte:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3989, 127, 2, '“Faze para ti duas trombetas de prata: faze-as de prata batida. Elas te servirão para convocar a assembleia e para dar o sinal de levantar o acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3990, 127, 3, 'Quando elas soarem, toda a assembleia se reunirá junto de ti, à entrada da tenda de reunião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3991, 127, 4, 'Se se tocar uma só, virão e se juntarão a ti os príncipes, os chefes de milhares em Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3992, 127, 5, 'Quando tocardes com força, partirão aqueles que estão acampados ao oriente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3993, 127, 6, 'E quando tocardes com força uma segunda vez, partirão aqueles que estão acampados ao meio-dia; o sinal para a sua partida será um toque de alarme.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3994, 127, 7, 'Para convocar a assembleia tocareis também, mas não com o toque de alarme.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3995, 127, 8, 'São os filhos de Aarão, os sacerdotes, que tocarão as trombetas. É uma lei perpétua para vós e vossos descendentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3996, 127, 9, 'Quando na vossa terra sairdes à guerra contra inimigos que vos atacarem, tocareis com força as trombetas, e o Senhor, vosso Deus, se lembrará de vós, e sereis livres dos vossos inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3997, 127, 10, 'Nos vossos dias de alegria, vossas festas e vossas luas novas, tocareis as trombetas, oferecendo os holocaustos e os sacrifícios pacíficos, e elas vos lembrarão a memória de vosso Deus. Eu sou o Senhor, vosso Deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3998, 127, 11, 'No vigésimo dia do segundo mês do segundo ano, levantou-se a nuvem do tabernáculo do testemunho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (3999, 127, 12, 'Os israe­litas puseram-se em marcha e partiram do deserto do Sinai, e a nuvem parou no deserto de Farã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4000, 127, 13, 'Partiram, pois, pela primeira vez, conforme a ordem do Senhor transmitida por Moisés.');
