INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4001, 127, 14, 'A bandeira do acampamento dos filhos de Judá partiu em primeiro lugar, seguida de suas tropas; e a tropa de Judá era comandada por Naas­son, filho de Abinadab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4002, 127, 15, 'A tropa da tribo dos filhos de Issacar era comandada por Natanael, filho de Suar; 16 e a tropa da tribo dos filhos de Zabulon era comandada por Eliab, filho de Helon.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4003, 127, 16, 'O tabernáculo foi desmontado, e os filhos de Gérson e de Merari partiram, transportando-o.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4004, 127, 17, 'Depois partiu a bandeira do acampamento de Rúben, seguida de suas tropas, e seu comandante era Elisur, filho de Sedeur.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4005, 127, 18, 'A tropa da tribo dos filhos de Simeão era comandada por Salamiel, filho de Surisadai;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4006, 127, 19, 'e a tropa da tribo dos filhos de Gad era comandada por Eliasaf, filho de Reuel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4007, 127, 20, 'Os caatitas partiram em seguida, levando os objetos sagrados. E, antes que chegassem, era montado o tabernáculo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4008, 127, 21, 'A bandeira do acampamento dos filhos de Efraim partiu, seguida de suas tropas; e a tropa de Efraim era comandada por Elisama, filho de Amiud.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4009, 127, 22, 'A tropa da tribo dos filhos de Manassés era comandada por Gamaliel, filho de Fadassur;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4010, 127, 23, 'e a tropa da tribo de Benjamim era comandada por Abidã, filho de Gedeão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4011, 127, 24, 'A bandeira do acampamento dos filhos de Dã, que formavam a retaguarda de todos os acampamentos, partiu, seguida de suas tropas. A tropa de Dã era comandada por Aiezer, filho de Amisadai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4012, 127, 25, 'A tropa da tribo dos filhos de Aser era comandada por Fegiel, filho de Ocrã;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4013, 127, 26, 'e a tropa dos filhos de Neftali era comandada por Aíra, filho de Enã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4014, 127, 27, 'Essa foi a ordem de marcha dos israelitas, divididos em tropas, quando levantaram acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4015, 127, 28, 'Moisés disse a Hobab, filho de Raguel, o madianita, seu sogro: “Nós partimos para o lugar que o Senhor nos prometeu dar. Vem conosco, e te haveremos de tratar bem, porque o Senhor prometeu fazer bem a Israel”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4016, 127, 29, 'Hobab, porém, respondeu-lhe: “Não irei contigo, mas voltarei para a minha terra e para junto de minha família”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4017, 127, 30, 'Moisés replicou: “Rogo-te que não te separes de nós. Conheces os lugares onde podemos acampar no deserto e nos servirás de guia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4018, 127, 31, 'Se vieres conosco, dividiremos contigo os bens que o Senhor nos der”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4019, 127, 32, 'Partiram da montanha do Senhor e caminharam três dias. Durante esses três dias de marcha, a arca da aliança do Senhor os precedia, para lhes escolher um lugar de repouso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4020, 127, 33, 'A nuvem do Senhor estava sobre eles de dia, quando partiam do acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4021, 127, 34, 'Quando a arca se levantava, Moisés dizia: “Levantai-vos, Senhor, e sejam dispersos os vossos inimigos! Fujam de vossa face os que vos aborrecem!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4022, 127, 35, 'Quando, porém, se detinha, dizia: “Voltai, Senhor, para a imensa multidão de Israel!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4023, 128, 1, 'O povo pôs-se a murmurar amargamente aos ouvidos do Senhor. O Senhor, ouvindo isso, irou-se: o fogo do Senhor acendeu-se entre eles e devorou a extremidade do acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4024, 128, 2, 'O povo clamou a Moisés; Moisés orou ao Senhor e o fogo extinguiu-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4025, 128, 3, 'Deu-se àquele lugar o nome de Tabeera, porque o fogo do Senhor se tinha acendido no meio deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4026, 128, 4, 'A população que estava no meio de Israel foi atacada por um desejo desor­denado, de modo que até os israelitas recomeçaram a gemer: “Quem nos dará carne para comer?” – diziam eles –.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4027, 128, 5, '“Lembramo-nos dos peixes que comíamos de graça no Egito, dos pepinos, melões, verduras, cebolas e alhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4028, 128, 6, 'Agora nossa alma está seca. Não há mais nada, e só vemos maná diante de nossos olhos.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4029, 128, 7, 'O maná assemelhava-se ao grão de coen­tro e parecia-se com o bdélio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4030, 128, 8, 'O povo dispersava-se para colhê-lo; moía-o com a mó ou esmagava-o num pilão, cozia-o numa panela e fazia bolos com ele, os quais tinham o sabor de um bolo amassado com óleo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4031, 128, 9, 'Enquanto de noite caía o orvalho no campo, caía também com ele o maná.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4032, 128, 10, 'Ouviu Moisés o povo que chorava, agrupado por famílias, cada uma à entrada de sua tenda. A cólera do Senhor acendeu-se com violência. Moisés entristeceu-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4033, 128, 11, 'E disse ao Senhor: “Por que afligis vosso servo? Por que não acho eu favor a vossos olhos, vós que me impusestes a carga de todo esse povo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4034, 128, 12, 'Porventura fui eu que concebi esse povo? Ou acaso fui eu que o dei à luz, para me dizerdes: leva-o em teu seio como a ama costuma levar o bebê, para a terra que, com juramento, prometi aos seus pais?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4035, 128, 13, 'Onde encontrarei carne para dar a todo esse povo que vem chorar perto de mim, dizendo: ‘Dá-nos carne para comer?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4036, 128, 14, 'Eu sozinho não posso suportar todo esse povo; ele é pesado demais para mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4037, 128, 15, 'Em lugar de tratar-me assim, rogo-vos que antes me façais morrer, se achei agrado a vossos olhos, a fim de que eu não veja a minha infelicidade!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4038, 128, 16, 'O Senhor respondeu a Moisés: “Junta-me setenta homens entre os an­ciãos de Israel, que sabes serem os anciãos do povo e tenham autoridade sobre ele. Conduze-os à tenda de reunião, onde estarão contigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4039, 128, 17, 'Então descerei e ali falarei contigo. Tomarei do espírito que está em ti e o derramarei sobre eles, para que possam levar contigo a carga do povo e não estejas mais sozinho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4040, 128, 18, 'Dirás ao povo: Santificai-vos para amanhã, e tereis carne para comer, pois chorastes aos ouvidos do Eterno, dizendo: Quem nos dará carne para comer? Estávamos tão bem no Egito!... O Senhor vos dará carne, e comereis.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4041, 128, 19, 'E comereis não só um dia, nem dois, nem cinco, nem dez, nem vinte,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4042, 128, 20, 'mas durante um mês inteiro, até que ela vos saia pelas narinas e vos cause nojo: porque rejeitastes o Senhor que está no meio de vós e dissestes-lhe chorando: Por que saímos nós do Egito?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4043, 128, 21, 'Moisés disse: “Este povo, no meio do qual estou, conta seiscentos mil homens de pé, e dizeis que lhes dareis carne para que comam um mês inteiro!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4044, 128, 22, 'Porventura se matará tanta quantidade de ovelhas e bois até que tenham bastante? Ou juntarão todos os peixes do mar para fartá-los?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4045, 128, 23, 'O Senhor respondeu a Moisés: “Acaso será impotente a mão do Senhor? Verás sem demora se se fará ou não o que eu te disse”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4046, 128, 24, 'Moisés saiu e referiu ao povo as palavras do Senhor. Reuniu setenta homens dos anciãos do povo e os colocou em volta da tenda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4047, 128, 25, 'O Senhor desceu na nuvem e falou a Moisés. Tomou uma parte do espírito que o animava e a pôs sobre os setenta anciãos. Apenas repousara o espírito sobre eles, começaram a profetizar; mas não continuaram.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4048, 128, 26, 'Dois homens tinham ficado no acampamento: um chamava-se Eldad e o outro, Medad, e o espírito repousou também sobre eles, pois tinham sido alistados, mas não tinham ido à tenda; e profetizaram no acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4049, 128, 27, 'Um jovem correu a dar notícias a Moisés: “Eldad e Medad – disse ele – profetizam no acampamento”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4050, 128, 28, 'Então Josué, filho de Nun, servo de Moisés desde a sua juventude, tomou a palavra: “Moisés – disse ele – meu senhor, impede-os”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4051, 128, 29, 'Moisés, porém, respondeu: “Por que és tão zeloso por mim? Prouvera a Deus que todo o povo do Senhor profetizasse, e que o Senhor lhe desse o seu espírito!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4052, 128, 30, 'E Moisés retirou-se do acampamento com os anciãos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4053, 128, 31, 'Um vento mandado pelo Senhor, vindo das bandas do mar, trouxe consigo codornizes e derramou-as sobre o acampamento, em uma extensão de cerca de um dia de caminho para ambos os lados em volta do acampamento; e cobriam o solo, cerca de dois côvados de alto sobre a superfície da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4054, 128, 32, 'Levantou-se então o povo, e ajuntou durante todo aquele dia, toda a noite e todo o dia seguinte tantas codornizes, que aquele que menos ajuntou conseguiu encher dez homeres. E estenderam-nas, para si mesmos, em toda a volta do acampamento.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4055, 128, 33, 'Ainda a carne estava nos seus dentes, e ainda não estava mastigada, quando a cólera do Senhor se inflamou contra o povo e o Senhor feriu o povo com um grande flagelo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4056, 128, 34, 'Chamou-se àquele lugar Quibrot-Hataava (sepulcros da concupiscência, porque ali sepultou-se o povo que se deixara dominar pelo desejo desordenado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4057, 128, 35, 'De Quibrot-Hataava, partiu o povo para Haserot, onde se deteve.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4058, 129, 1, 'Maria e Aarão criticaram Moisés por causa da mulher etíope que ele desposara. (Moisés tinha, com efeito, tomado uma mulher etíope.)*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4059, 129, 2, '“Porventura é só por Moisés – diziam eles – que o Senhor fala? Não fala ele também por nós?” E o Senhor ouviu isso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4060, 129, 3, 'Ora, Moisés era um homem muito paciente, o mais pacien­te da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4061, 129, 4, 'Logo falou o Senhor a Moisés, a Aarão e a Maria: “Ide todos os três à tenda de reunião”. E eles foram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4062, 129, 5, 'O Senhor desceu na coluna de nuvem e parou à entrada da tenda. Chamou Aarão e Maria, e eles aproximaram-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4063, 129, 6, '“Ouvi bem – disse ele – o que vou dizer: Se há entre vós um profeta, eu lhe aparecerei em visão; eu, o Senhor, é em sonho que lhe falarei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4064, 129, 7, 'Mas não é assim a respeito de meu servo Moisés, que é fiel em toda a minha casa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4065, 129, 8, 'A ele eu lhe falo face a face, manifesto-me a ele sem enigmas, e ele contempla o rosto do Senhor. Por que vos atrevestes, pois, a falar contra o meu servo Moisés?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4066, 129, 9, 'A cólera do Senhor se acendeu contra eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4067, 129, 10, 'O Senhor partiu, e a nuvem retirou-se de sobre a tenda. No mesmo instante, Maria foi ferida por uma lepra branca como a neve. Aarão, olhando para ela, viu-a coberta de lepra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4068, 129, 11, 'Aarão disse então a Moisés: “Rogo-te, meu senhor, não nos faças levar o peso desse pecado que cometemos num momento de loucura, e do qual somos culpados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4069, 129, 12, 'Que ela não fique como um aborto que sai do ventre de sua mãe, com a carne já meio consumida!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4070, 129, 13, 'Moisés orou ao Senhor: “Ó Deus – disse ele – rogo-vos que a cureis”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4071, 129, 14, 'O Senhor disse a Moisés: “Se seu pai lhe tivesse cuspido no rosto, não estaria ela coberta de vergonha durante sete dias? Que ela seja excluída do acampamento durante sete dias; depois será reintegrada”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4072, 129, 15, 'Maria foi, pois, excluí­da do acampamento durante sete dias e o povo não se moveu daquele lugar, enquanto ela não foi reintegrada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4073, 129, 16, 'Depois disso, o povo partiu de Ha­serot, e acampou no deserto de Farã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4074, 130, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4075, 130, 2, '“Envia homens para explorar a terra de Canaã, que eu hei de dar aos filhos de Is­rael. Enviarás um homem de cada tribo patriarcal, tomados todos entre os príncipes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4076, 130, 3, 'Enviou-os Moisés do deserto de Farã segundo as ordens do Senhor; todos esses homens eram príncipes em Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4077, 130, 4, 'Eis os seus nomes: da tribo de Rúben, Samua, filho de Zacur;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4078, 130, 5, 'da tribo de Simeão, Safat, filho de Huri;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4079, 130, 6, 'da tribo de Judá, Caleb, filho de Jefoné;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4080, 130, 7, 'da tribo de Issacar, Igal, filho de José;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4081, 130, 8, 'da tribo de Efraim, Oseias, filho de Nun;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4082, 130, 9, 'da tribo de Benjamim, Falti, filho de Rafu;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4083, 130, 10, 'da tribo de Zabulon, Gediel, filho de Sodi;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4084, 130, 11, 'da tribo de José, na tribo de Manassés, Gadi, filho de Susi;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4085, 130, 12, 'da tribo de Dã, Amiel, filho de Gemali;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4086, 130, 13, 'da tribo de Aser, Setur, filho de Miguel;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4087, 130, 14, 'da tribo de Neftali, Naabi, filho de Vapsi;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4088, 130, 15, 'da tribo de Gad, Guel, filho de Maqui.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4089, 130, 16, 'Esses são os nomes dos homens que Moisés enviou como exploradores a Canaã. Moisés deu a Oseias, filho de Nun, o nome de Josué.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4090, 130, 17, 'Enviando-os a explorar a terra de Canaã, Moisés disse-lhes: “Ide pelo Negueb e subi a montanha.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4091, 130, 18, 'Examinai que terra é essa, e o povo que a habita, se é forte ou fraco, pequeno ou numeroso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4092, 130, 19, 'Vede como é a terra onde habita, se é boa ou má, e como são as suas cidades, se muradas ou sem muros;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4093, 130, 20, 'examinai igualmente se o terreno é fértil ou estéril, e se há árvores ou não. Coragem! E trazei-nos dos frutos da terra”. Era então a época das primeiras uvas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4094, 130, 21, 'Partiram, pois, e exploraram a terra desde o deserto de Sin até Roob, no caminho de Emat.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4095, 130, 22, 'Subiram ao Negueb e foram a Hebron, onde se encontravam Aimã, Sesai e Tolmai, filhos de Enac. Hebron fora construída sete anos antes de Tânis, no Egito.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4096, 130, 23, 'Chegaram ao vale de Escol, onde cortaram um ramo de vide com um cacho de uvas, que dois homens levaram numa vara; tomaram também consigo romãs e figos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4097, 130, 24, 'Chamou-se a esse lugar vale de Escol, por causa do cacho que nele haviam cortado os israelitas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4098, 130, 25, 'Tendo voltado os exploradores, passados quarenta dias,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4099, 130, 26, 'foram ter com Moisés e Aarão e toda a assembleia dos israelitas em Cades, no deserto de Farã. Diante deles e de toda a multidão relataram a sua expedição e mostraram os frutos da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4100, 130, 27, 'Eis como narraram a Moisés a sua exploração: “Fomos à terra aonde nos envias­te. É verdadeiramente uma terra onde corre leite e mel, como se pode ver por esses frutos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4101, 130, 28, 'Mas os habitantes dessa terra são robustos, suas cidades grandes e bem muradas; vimos ali até mesmo filhos de Enac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4102, 130, 29, 'Os amalecitas habitam na terra do Negueb; os hiteus, os jebuseus e os amorreus habitam nas montanhas, e os cananeus habitam junto ao mar e ao longo do Jordão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4103, 130, 30, 'Caleb fez calar o povo que começava a murmurar contra Moisés, e disse: “Vamos e apoderemo-nos da terra, porque podemos conquistá-la”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4104, 130, 31, 'Mas os outros, que tinham ido com ele, diziam: “Não somos capazes de atacar esse povo; é mais forte do que nós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4105, 130, 32, 'E diante dos filhos de Israel depreciaram a terra que tinham explorado: “A terra – disseram eles – que exploramos devora os seus habitantes: os homens que vimos ali são de uma grande estatura;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4106, 130, 33, 'vimos até mesmo gigantes, filhos de Enac, da raça dos gigantes; parecíamos gafanhotos comparados com eles”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4107, 131, 1, 'Toda a assembleia pôs-se a gritar e chorou aquela noite.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4108, 131, 2, 'Todos os israe­litas murmuraram contra Moisés e Aarão, dizendo: “Oxalá tivéssemos morrido no Egito ou neste deserto!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4109, 131, 3, 'Por que nos conduziu o Senhor a esta terra para morrermos pela espada? Nossas mulheres e nossos filhos serão a presa do inimigo. Não seria melhor que voltássemos para o Egito?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4110, 131, 4, 'E diziam uns para os outros: “Escolhamos um chefe e voltemos para o Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4111, 131, 5, 'Moisés e Aarão caíram com o rosto por terra diante de toda a assembleia dos israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4112, 131, 6, 'Josué, filho de Nun, e Caleb, filho de Jefoné, que tinham explorado a terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4113, 131, 7, 'rasgaram as suas vestes e disseram a toda a assembleia dos israelitas: “A terra que percorremos é muito boa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4114, 131, 8, 'Se o Senhor nos for propício, nos introduzirá nela e no-la dará. É uma terra onde corre leite e mel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4115, 131, 9, 'Somente não vos revolteis contra o Senhor, e não tenhais medo do povo dessa terra: o devoraremos como pão. Não há mais salvação para eles, porque o Senhor está conosco. Não tenhais medo deles”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4116, 131, 10, 'Toda a assembleia estava a ponto de apedrejá-los, quando a glória do Senhor apareceu sobre a tenda de reunião a todos os israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4117, 131, 11, 'O Senhor disse a Moisés: “Até quando me desprezará esse povo? Até quando não acreditará em mim, apesar de todos os prodígios que fiz no meio dele?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4118, 131, 12, 'Vou destruí-lo, ferindo-o de peste, mas farei de ti uma nação maior e mais poderosa do que ele”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4119, 131, 13, 'Moisés disse ao Senhor: “Os egípcios viram que, por vosso poder, tirastes este povo do meio deles e o disseram aos habitantes dessa terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4120, 131, 14, 'Todo mundo sabe, ó Senhor, que estais no meio desse povo, e sois visto face a face, ó Senhor, que vossa nuvem está sobre eles e marchais diante deles de dia numa coluna de nuvem, e de noite numa coluna de fogo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4121, 131, 15, 'Se fizerdes morrer todo esse povo, as nações que ouviram falar de vós dirão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4122, 131, 16, 'O Senhor foi incapaz de introduzir o povo na terra que lhe havia jurado dar, e exterminou-o no deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4123, 131, 17, 'Agora, pois, rogo-vos que o poder do Senhor se manifeste em toda a sua grandeza, como o dissestes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4124, 131, 18, 'O Senhor é lento para a cólera e rico em bondade; ele perdoa a iniquidade e o pecado, mas não tem por inocente o culpado, e castiga a iniquidade dos pais nos filhos até a terceira e a quarta geração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4125, 131, 19, 'Perdoai o pecado desse povo segundo a vossa grande misericórdia, como já o tendes feito desde o Egito até aqui”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4126, 131, 20, 'O Senhor respondeu: “Eu perdoo, conforme o teu pedido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4127, 131, 21, 'Mas, pela minha vida e pela minha glória que enche toda a terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4128, 131, 22, 'nenhum dos homens que viram a minha glória e os prodígios que fiz no Egito e no deserto, que me provocaram já dez vezes e não me ouviram,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4129, 131, 23, 'verá a terra que prometi com juramento aos seus pais. Nenhum daqueles que me desprezaram a verá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4130, 131, 24, 'Quanto ao meu servo Caleb, porém, que animado de outro espírito me obedeceu fielmente, eu o introduzirei na terra que ele percorreu, e a sua posteridade a possuirá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4131, 131, 25, 'Visto que os amalecitas e os cananeus habitam no vale, voltai amanhã e parti para o deserto em direção ao mar Vermelho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4132, 131, 26, 'O Senhor disse a Moisés e a Aarão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4133, 131, 27, '“Até quando sofrerei eu essa assembleia revoltada que murmura contra mim? Ouvi as murmurações que os israelitas proferem contra mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4134, 131, 28, 'Dize-lhes: Juro por mim mesmo – diz o Senhor – eu vos tratarei como vos ouvi dizer.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4135, 131, 29, 'Vossos cadáveres cairão nesse deserto. Todos vós que fostes recenseados da idade de vinte anos para cima, e que murmurastes contra mim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4136, 131, 30, 'não entrareis na terra onde jurei estabelecer-vos, exceto Caleb, filho de Jefoné, e Josué, filho de Nun.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4137, 131, 31, 'Todavia, introduzirei nela os vossos filhinhos, dos quais dizíeis que seriam a presa do inimigo, e eles conhecerão a terra que desprezastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4138, 131, 32, 'Quanto a vós, os vossos cadáveres ficarão nesse deserto,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4139, 131, 33, 'onde os vossos filhos guardarão os seus rebanhos durante quarenta anos, pagando a pena de vossas infidelidades, até que vossos cadáveres apodreçam no deserto.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4140, 131, 34, 'Explorastes a terra em quarenta dias; tantos anos quantos foram esses dias paga­reis a pena de vossas iniquidades, ou seja, durante quarenta anos, e vereis o que significa ser objeto de minha vingança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4141, 131, 35, 'Eu, o Senhor, o disse. Eis como hei de tra­tar essa assembleia rebelde que se revoltou contra mim. Eles serão consumidos e mortos nesse deserto!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4142, 131, 36, 'Os homens que Moisés tinha envia­­do a explorar a terra e que, depois de terem voltado, tinham feito murmurar contra ele toda a assembleia,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4143, 131, 37, 'depreciando a terra, morreram feridos por uma praga, diante do Senhor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4144, 131, 38, 'Somente Josué, filho de Nun, e Caleb, filho de Jefoné, sobreviveram entre todos os que tinham ex­plo­rado a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4145, 131, 39, 'Moisés referiu tudo isso aos filhos de Israel e o povo ficou profundamente desolado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4146, 131, 40, 'Levantaram-se de madrugada e se puseram a caminho para o cimo do monte, dizendo: “Estamos prontos a subir para o lugar de que falou o Senhor, porque pecamos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4147, 131, 41, 'Moisés disse-lhes: “Por que transgredis a ordem do Senhor? Isso não será bem sucedido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4148, 131, 42, 'Não su­bais; sereis derrotados por vossos inimigos, pois o Senhor não está no meio de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4149, 131, 43, 'Os amalecitas e os cananeus estão diante de vós, e sucumbireis sob a sua espada, porque vos desviastes do Senhor. O Senhor não estará convosco”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4150, 131, 44, 'Eles obstinaram-se em querer subir até o cimo do monte; a arca da aliança do Senhor, porém, e Moisés, não saíram do acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4151, 131, 45, 'Então os amalecitas e os cananeus, que habitavam nessa montanha, desceram e, tendo-os batido e retalhado, perseguiram-nos até Horma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4152, 132, 1, 'O Senhor disse a Moisés: “Dize aos israelitas o seguinte:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4153, 132, 2, 'Quando entrardes na terra de vossa habitação, que eu vos hei de dar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4154, 132, 3, 'e oferecerdes ao Senhor algum sacrifício pelo fogo, seja holocausto, seja um simples sacrifício, quer em cumprimento de um voto, quer como oferta espontânea, ou por ocasião de uma festa, para apresentar uma oferta de agradável odor ao Senhor, com vossos bois ou vossas ovelhas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4155, 132, 4, 'aquele que fizer essa oferta apresentará ao Senhor em oblação um décimo de flor de farinha amassada com um quarto de hin de óleo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4156, 132, 5, 'E, para a libação, acrescentará um quarto de hin de vinho ao holocausto ou ao sacrifício de cada cordeiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4157, 132, 6, 'Para um carneiro oferecerás dois décimos de flor de farinha amassada com um terço de hin de óleo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4158, 132, 7, 'ajuntando uma libação de um terço de hin de vinho, como oferta de agradável odor ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4159, 132, 8, 'Quando ofereceres um touro em holo­causto ou em sacrifício, para o cumprimento de um voto ou em sacrifício pacífico ao Senhor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4160, 132, 9, 'darás com o touro uma oblação de três décimos de flor de farinha amassada com meio hin de óleo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4161, 132, 10, 'ajuntando uma libação de meio-hin de vinho; isso é um sacrifício feito pelo fogo, de agradável odor ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4162, 132, 11, 'O mesmo se fará para cada boi, cada carneiro, cordeiro ou cabrito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4163, 132, 12, 'Assim fareis para cada um desses sacrifícios, seja qual for o número das vítimas que oferecerdes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4164, 132, 13, 'Todos os nativos procederão do mesmo modo, quando oferecerem um sacrifício pelo fogo de agradável odor ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4165, 132, 14, 'Se um estrangeiro que habita no meio de vós, ou qualquer outro homem que venha mais tarde a se estabelecer entre vós, oferecer um sacrifício pelo fogo de agradável odor ao Senhor, fará o mesmo que vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4166, 132, 15, 'Só haverá uma lei, a mesma para vós, para a assembleia e para o estrangeiro que habita no meio de vós. Esta é uma lei perpétua para vossos descendentes: diante do Senhor será a mesma coisa tanto para vós como para o estrangeiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4167, 132, 16, 'Haverá uma só lei e uma só regra para vós e para o estrangeiro que habita no meio de vós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4168, 132, 17, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4169, 132, 18, '“Dize aos israelitas o seguinte:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4170, 132, 19, 'Quando che­gardes à terra para onde vos levo, e comer­des o pão daquela terra, reservareis uma oferta para o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4171, 132, 20, 'Essa oferta será um bolo feito das primícias de vossa farinha: vós a separareis como se separa a oferta da eira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4172, 132, 21, 'Como primícias de vossa farinha, vós e vossos descendentes separareis uma oferta para o Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4173, 132, 22, '“Se pecardes involuntariamente, deixando de observar um desses mandamentos que o Senhor deu a Moisés, –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4174, 132, 23, 'tudo o que por ele vos ordenou desde o dia em que o Senhor vos deu os seus mandamentos, e daí por diante em vossas gerações futuras –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4175, 132, 24, 'se alguém pecar involuntariamente, e a assembleia não o tiver notado, toda a assembleia oferecerá em holocausto de agradável odor ao Senhor um novilho, com sua oblação e sua libação, segundo o rito prescrito, bem como um bode em sacrifício pelo pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4176, 132, 25, 'O sacerdote fará a expiação por toda a assembleia dos israelitas, e lhes será perdoado, porque é um pecado involuntário, e apresentaram sua oferta ao Senhor, um sacrifício feito pelo fogo e seu sacrifício pelo pecado para reparar o seu erro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4177, 132, 26, 'Será perdoado a toda a assembleia dos filhos de Israel, e ao estrangeiro que mora no meio deles, porque é uma culpa que todo o povo cometeu involuntariamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4178, 132, 27, 'Se for uma só pessoa que pecou involuntariamente, oferecerá uma cabra de um ano em sacrifício pelo pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4179, 132, 28, 'O sacerdote fará a expiação diante do Senhor por essa pessoa que pecou involun­tariamente; feita a expiação, será perdoada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4180, 132, 29, 'Tereis uma só lei para aquele que pecar involuntariamente, quer sejam israelitas, quer sejam estrangeiros que habitem no meio deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4181, 132, 30, 'Aquele, porém, que pecar conscientemente, ultraja o Senhor; ele será cortado do meio de seu povo,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4182, 132, 31, 'porque desprezou a palavra do Senhor e violou o seu preceito; será cortado e levará o peso de sua iniquidade”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4183, 132, 32, 'Ora, aconteceu que, estando os israe­litas no deserto, encontraram um homem ajuntando lenha num dia de sábado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4184, 132, 33, 'Os que o acharam apanhando lenha, levaram-no a Moisés e a Aarão, dian­te de toda a assembleia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4185, 132, 34, 'Eles colocaram-no em guarda, pois não estava ainda determinado o que se lhe devia fazer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4186, 132, 35, 'O Senhor disse a Moisés: “Que esse homem seja punido de morte, e a assembleia o apedreje fora do acampamento”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4187, 132, 36, 'Levaram-no para fora do acampamento e toda a assembleia o apedrejou, e ele morreu, como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4188, 132, 37, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4189, 132, 38, '“Dize aos israelitas que façam para eles e seus descendentes borlas nas extremidades de suas vestes, pondo na borla de cada canto um cordão de púrpura violeta.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4190, 132, 39, 'Fareis essas borlas para que, vendo-as, vos recordeis de todos os mandamentos do Senhor, e os pratiqueis, e não vos dei­xeis levar pelos apetites de vosso coração e de vossos olhos que vos arrastam à infidelidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4191, 132, 40, 'Desse modo, vós vos lembrareis de todos os meus mandamentos, e os praticareis, e sereis consagrados ao vosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4192, 132, 41, 'Eu sou o Senhor, vosso Deus, que vos tirei do Egito para ser o vosso Deus. Eu sou o Senhor, vosso Deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4193, 133, 1, 'Coré, filho de Isaar, filho de Caat, filho de Levi, Datã e Abiram, filhos de Eliab, e On, filho de Felet, todos filhos de Rúben,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4194, 133, 2, 'levantaram-se contra Moisés, juntamente com outros duzentos e cinquenta israelitas, príncipes da assembleia, membros do conselho e homens notáveis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4195, 133, 3, 'Dirigiram-se, pois, em grupo a Moisés e a Aarão, dizendo-lhes: “Basta! Toda a assembleia é santa, todos o são, e o Senhor está no meio deles. Por que vos colocais acima da assembleia do Senhor?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4196, 133, 4, 'Ouvindo isso, Moisés lançou-se com o rosto por terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4197, 133, 5, 'e disse a Coré e aos seus cúmplices: “Amanhã cedo o Senhor fará conhecer quem é dele e quem é santo, e o fará aproximar de si; fará aproximar de si aquele que ele escolher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4198, 133, 6, 'Eis o que tendes a fazer: cada um tome o seu turíbulo, tu, Coré, e todos os teus sequazes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4199, 133, 7, 'Amanhã poreis fogo em vossos turíbulos e queimareis neles o incenso diante do Senhor. O homem que o Senhor escolher, esse é santo. Isso já é demais, ó filhos de Levi!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4200, 133, 8, 'Disse mais a Coré: “Ouvi, agora, ó filhos de Levi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4201, 133, 9, 'Não vos basta que o Deus de Israel vos tenha separado da assembleia de Israel, e vos tenha trazido para junto de si, para o serviço do tabernáculo do Senhor e para estardes a serviço da assembleia?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4202, 133, 10, 'Fez-te aproximar dele, tu e todos os teus irmãos, os levitas, e ainda disputais o sacerdócio!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4203, 133, 11, 'E é por isso que vos amotinais contra o Senhor, tu e todo o teu grupo! E quem é Aarão para murmurardes contra ele?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4204, 133, 12, 'Moisés convocou Datã e Abiram, filhos de Eliab. Mas eles responderam: “Não iremos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4205, 133, 13, 'Porventura não te basta ter-nos tirado de uma terra onde corria leite e mel, para nos fazeres morrer no deserto, e ainda queres tornar-te nosso senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4206, 133, 14, 'Na verdade, não nos conduziste a uma terra onde corre leite e mel; não nos deste em herança nem campos nem vinhas. Pensas que taparás os olhos de toda essa gente? Nós não iremos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4207, 133, 15, 'Moisés, muito irado, disse ao Senhor: “Não olheis para a sua oblação. Vós sabeis que nunca recebi deles nem mesmo um asno, e a nenhum deles fiz o menor mal”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4208, 133, 16, 'Moisés disse a Coré: “Tu e todos os teus sequazes, apresentai-vos amanhã diante do Senhor, com Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4209, 133, 17, 'Tomai cada qual vosso turíbulo, ponde incenso nele e apresentai cada qual vosso turíbulo diante do Senhor: isto é, duzentos e cinquenta turíbulos. Tu e Aarão tomareis também o vosso turíbulo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4210, 133, 18, 'Tomaram, pois, cada um o seu turíbulo, puseram-lhe fogo e deitaram por cima o incenso; e conservaram-se de pé com Moisés e Aarão à entrada da tenda de reunião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4211, 133, 19, 'Coré tinha reunido perto de si toda a assembleia à entrada da tenda de reunião. E eis que a glória do Senhor apareceu a toda a assembleia,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4212, 133, 20, 'e o Senhor falou a Moisés e a Aarão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4213, 133, 21, '“Retirai-vos do meio dessa assembleia, e eu os consumirei neste instante”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4214, 133, 22, 'Eles prostraram-se com o rosto por terra, e disseram: “Ó Deus, Deus dos espíritos de toda a carne, um só homem pecou, e tu te iras contra toda a assembleia?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4215, 133, 23, 'O Senhor respondeu a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4216, 133, 24, '“Manda ao povo: apartai-vos de junto das tendas de Coré, de Datã e de Abiram”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4217, 133, 25, 'Moisés levantou-se e, seguido dos anciãos, dirigiu-se aonde estavam Datã e Abiram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4218, 133, 26, '“Afastai-vos – disse ele à assembleia – das tendas desses homens perversos, e não toqueis em coisa alguma que lhes pertença, para que não morrais, envolvidos em todos os seus pecados”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4219, 133, 27, 'Afastando-se o povo de junto das tendas de Coré, Datã e Abiram, saíram estes últimos com suas mulheres, seus filhos e seus filhinhos e pararam à entrada de suas tendas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4220, 133, 28, 'Moisés disse então: “Nisto conhecereis que o Senhor me enviou a fazer todas estas obras e que nada faço por mim mesmo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4221, 133, 29, 'Se estes morrerem com a morte ordinária dos homens e se a sua sorte for como a de todos, o Senhor não me enviou;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4222, 133, 30, 'mas se o Senhor fizer um novo prodígio e o solo abrindo a sua boca, os engolir com tudo o que lhes pertence, de sorte que desçam vivos à habitação dos mortos, então sabereis que estes homens desprezaram o Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4223, 133, 31, 'Apenas acabou ele de falar, fendeu-se a terra debaixo de seus pés');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4224, 133, 32, 'e, abrindo sua boca, os devorou com toda a sua família, todos os seus bens e todos os homens de Coré.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4225, 133, 33, 'Desceram vivos à morada dos mortos, eles e tudo o que possuíam; cobriu-os a terra, e desapareceram da assembleia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4226, 133, 34, 'Todo o Israel que estava ao redor deles, ouvindo o grito que soltaram, fugiu, dizendo: “Cuidemos que a terra não nos engula também a nós!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4227, 133, 35, 'Saiu um fogo de junto do Senhor e devorou os duzentos e cinquenta homens que ofereciam o incenso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4228, 134, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4229, 134, 2, '“Dize a Eleazar, filho do sacerdote Aa­rão, que tire os turíbulos que estão no meio do incêndio e espalhe ao longe o fogo, pois são objetos consagrados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4230, 134, 3, 'Com os turíbulos desses homens que pecaram e perderam a vida, façam-se lâminas para cobrir o altar, porque foram apresentados ao Senhor e estão santificados. Ficarão como um sinal para os israelitas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4231, 134, 4, 'O sacerdote Eleazar tirou, pois, os turíbulos de bronze que os homens consumidos pelo fogo tinham apresentado ao Senhor, e fez deles lâminas para cobrir o altar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4232, 134, 5, 'Isso devia servir de memorial para os israelitas, a fim de que nenhum estranho à linhagem de Aarão se aproximasse para oferecer incenso ao Senhor, temendo lhe acontecesse o mesmo que a Coré e a seus homens, como o Senhor tinha declarado pela boca de Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4233, 134, 6, 'Ora, no dia seguinte, toda a comunidade dos israelitas murmurou contra Moisés e Aarão: “Matastes o povo do Senhor” – diziam eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4234, 134, 7, 'E, crescendo o tumulto, Moisés e Aarão voltaram-se para o lado da tenda de reunião e viram a nuvem que a cobria; e apareceu a glória do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4235, 134, 8, 'Eles foram e colocaram-se diante da tenda de reunião,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4236, 134, 9, 'e o Senhor falou a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4237, 134, 10, '“Afastai-vos do meio dessa assembleia, pois vou devorá-la num instante”. Prostraram-se por terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4238, 134, 11, 'e Moisés disse a Aarão: “Toma o turíbulo, põe-lhe fogo do altar, deita-lhe incenso por cima e vai depressa ao povo para fazer expiação por ele; porque acendeu-se a cólera do Senhor, e o flagelo começa”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4239, 134, 12, 'Aarão, obedecendo à palavra de Moisés, tomou o turíbulo e correu ao meio da assembleia, pois a praga começava já no meio do povo; deitou nele o incenso e fez a expiação pelo povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4240, 134, 13, 'Colocando-se de pé entre os mortos e os vivos, deteve o flagelo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4241, 134, 14, 'Com esse golpe morreram catorze mil e setecentos, além dos que tinham perecido na rebelião de Coré.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4242, 134, 15, 'Aarão voltou para junto de Moisés, à entrada da tenda de reunião, e o flagelo terminou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4243, 134, 16, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4244, 134, 17, '“Fala aos israelitas. Que eles te deem uma vara por tribo, ou seja, doze varas de todos os príncipes das doze casas patriarcais. Escreverás o nome de cada um na sua vara;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4245, 134, 18, 'na vara de Levi escreverás o nome de Aarão, porque haverá uma vara por tribo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4246, 134, 19, 'Colocarás as varas na tenda de reunião, diante do testemunho, no lugar onde me encontro convosco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4247, 134, 20, 'E eis que a vara de meu eleito florescerá e, desse modo, farei cessar diante de mim as murmurações dos filhos de Israel contra vós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4248, 134, 21, 'Moisés falou aos israelitas e todos os príncipes lhe deram a vara, um de cada tribo, ou seja, doze varas pelas doze tribos, entre as quais também a de Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4249, 134, 22, 'Moisés as pôs diante do Senhor na tenda do testemunho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4250, 134, 23, 'Voltando no dia seguinte, entrou no pavilhão e eis que tinha florescido a vara de Aarão, pela tribo de Levi: tinham aparecido botões, saído flores e amadurecido amêndoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4251, 134, 24, 'Moisés levou todas as varas de diante do Senhor aos israelitas. Eles viram o (prodígio) e receberam cada um a sua vara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4252, 134, 25, 'O Senhor disse então a Moisés: “Torna a levar a vara de Aarão para diante da tenda do testemunho, e seja ali conservada como um sinal para todos aqueles que quiserem revoltar-se, e assim possas pôr um termo às murmurações diante de mim, para que não morram”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4253, 134, 26, 'Moisés executou a ordem que o Senhor lhe tinha dado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4254, 134, 27, 'Os israelitas disseram a Moisés: “Nós pereceremos, estamos perdidos, sim, esta­mos todos perdidos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4255, 134, 28, 'Qualquer que se aproxime do tabernáculo do Senhor, morre. Acaso seremos todos exterminados?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4256, 135, 1, 'O Senhor disse a Aarão: “Tu, teus filhos e tua família contigo, leva­reis a responsabilidade dos pecados cometidos no santuário. Tu e teus filhos contigo levareis a responsabilidade dos pecados cometidos em vosso sacerdócio.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4257, 135, 2, 'Farás aproximarem-se do santuário contigo os teus irmãos, a tribo de Levi e a tribo de teu pai, para que se juntem a ti e te ajudem quando estiveres com teus filhos diante da tenda do testemunho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4258, 135, 3, 'Eles farão o serviço que te é devido e o serviço da tenda, mas não se aproximarão dos objetos sagrados, nem do altar, para que não morram, e vós juntamente com eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4259, 135, 4, 'Serão teus auxiliares e terão a seu cuidado a tenda de reunião para fazer todo o seu serviço. Nenhum estrangeiro se aproximará de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4260, 135, 5, 'Fareis o serviço do santuário e do altar, para que não venha de novo a cólera ferir os israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4261, 135, 6, 'Fui eu que escolhi os levitas, vossos irmãos, entre os israelitas. Dados ao Senhor, vos são de novo entregues para fazerem o serviço da tenda de reunião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4262, 135, 7, 'Tu, porém, e teus filhos contigo, exercereis o vosso sacerdócio no altar e atrás do véu: esse é o vosso serviço. O sacerdócio é um dom que eu vos faço; o estrangeiro que se aproximar será morto”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4263, 135, 8, 'O Senhor disse a Aarão: “Dou-te o que se reserva de tudo o que é separado para mim, dentre todas as coisas consagradas dos israelitas. Dou isso a ti e a teus filhos em virtude de uma lei perpétua, por causa da unção que recebeste.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4264, 135, 9, 'Eis o que receberás das coisas santíssimas que não são queimadas; todas as suas ofertas, oblações, sacrifícios pelo pecado, sacrifícios de reparação; todas essas coisas santíssimas serão para ti e teus filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4265, 135, 10, 'Tu as comerás em lugar santíssimo. Todo varão poderá comer delas; e serão para ti coisas sagradas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4266, 135, 11, 'Eis ainda o que será teu: o que é tomado dentre os dons, dentre toda oferta agitada dos israelitas. Eu o dou a ti, a teus filhos e a tuas filhas em virtude de uma lei perpétua. Todo membro de tua família que estiver puro comerá dessas coisas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4267, 135, 12, 'Dou-te também as primícias que os israelitas oferecerem ao Senhor: o melhor de seu óleo, de seu vinho e de seu trigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4268, 135, 13, 'Serão para ti as primícias dos produtos da terra que trouxerem ao Senhor. Todo membro de tua família que estiver puro poderá comer delas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4269, 135, 14, 'Tudo o que for votado ao interdito em Israel será teu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4270, 135, 15, 'Será teu igualmente todo primogênito de toda criatura, homem ou animal, que os israelitas oferecerem ao Senhor; ordenarás, não obstan­te, que se resgate o primogênito do homem, assim como os primogênitos dos animais impuros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4271, 135, 16, 'O seu resgate será feito logo que ele tiver um mês, segundo tua estimação, à razão de cinco siclos de prata (conforme o siclo do santuário, que vale vinte gueras).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4272, 135, 17, 'Mas não farás resgatar o primogênito da vaca, nem o da ovelha, nem o da cabra: estes são coisas sagradas. Derramarás o seu sangue sobre o altar e queimarás a sua gordura em sacrifício feito pelo fogo, de agradável odor ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4273, 135, 18, 'Sua carne será para ti da mesma forma que o peito agitado e a perna direita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4274, 135, 19, 'Tudo o que é tomado das coisas santas que os israelitas oferecem ao Senhor, dou-o a ti, a teus filhos e a tuas filhas em virtude de uma lei perpétua. Essa é uma aliança de sal, que vale perpetuamente diante do Senhor, para ti e para toda a tua posteridade contigo”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4275, 135, 20, 'O Senhor disse a Aarão: “Não possuirás nada na terra deles, e não terás parte alguma entre eles. Eu sou a tua parte e a tua herança no meio dos israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4276, 135, 21, 'Quanto aos levitas, dou-lhes como patrimônio todos os dízimos de Israel pelo serviço que prestam na tenda de reunião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4277, 135, 22, 'Os israelitas não se aproximarão mais da tenda de reunião, para que não caia sobre eles o peso de um pecado que lhes cause a morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4278, 135, 23, 'São os levitas que farão o trabalho na tenda de reunião e que levarão a responsabilidade de suas faltas: essa é uma lei perpétua para todos os vossos descendentes. Eles não terão herança no meio dos israelitas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4279, 135, 24, 'porque lhes dou como herança os dízimos que os israelitas tomarem para o Senhor. Eis por que declaro que eles não possuirão herança alguma no meio dos israelitas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4280, 135, 25, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4281, 135, 26, '“Dirás aos levitas: Quando receberdes dos israelitas o dízimo que vos dei de seus bens por vossa herança, tomareis dele uma oferta para o Senhor: o dízimo do dízimo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4282, 135, 27, 'Essa reserva será como o trigo tomado da eira e como o vinho tomado do lagar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4283, 135, 28, 'Desse modo, fareis também vós uma reserva devida ao Senhor de todos os dízimos que receberdes dos israelitas, e essa oferta reservada para o Senhor, vós a entregareis ao sacerdote Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4284, 135, 29, 'De todos os dons que receberdes, separareis uma parte para o Senhor, isto é, tomareis a porção consagrada do que houver de melhor em vossos dízimos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4285, 135, 30, 'Dize-lhes também: Quando tiverdes separado o melhor do dízimo, o resto será para os levitas como o produto da eira ou do lagar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4286, 135, 31, 'Podereis comê-lo com vossa família, porque é o vosso salário pelo serviço que prestais na tenda de reunião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4287, 135, 32, 'Não cometereis pecado algum por causa disso, e não profa­nareis as santas ofertas dos israelitas, quando tiverdes separado o melhor do dízimo, para que não morrais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4288, 136, 1, 'O Senhor disse a Moisés e a Aarão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4289, 136, 2, '“Eis a prescrição legal que o Senhor vos dá: Dize aos israelitas que te tragam uma vaca vermelha sem defeito, sem mancha e que não tenha ainda levado o jugo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4290, 136, 3, 'Entregareis ao sacerdote Elea­zar, o qual, levando-a para fora do acampamento, a imolará à vista de todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4291, 136, 4, 'O sacerdote Eleazar tomará o sangue do animal com o dedo e fará com ele sete aspersões para o lado da entrada da tenda de reunião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4292, 136, 5, 'A vaca será em seguida queimada à vista de todos. Serão queimados o couro, a carne, o sangue e os excre­mentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4293, 136, 6, 'O sacerdote tomará pau de cedro, hissopo e carmesim e os jogará na chama em que arde a vaca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4294, 136, 7, 'O sacerdote lavará suas vestes e a banhará em água. Depois disso, voltará ao acampamento e será impuro até a tarde.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4295, 136, 8, 'Aquele que tiver queimado a vaca lavará suas vestes e se banhará em água, e será impuro até a tarde.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4296, 136, 9, 'Um homem puro recolherá a cinza da vaca e a deporá em um lugar puro fora do acampamento, onde será guardada pela assembleia dos israelitas para a água lustral. Esse é um sacrifício pelo pecado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4297, 136, 10, 'Aquele que tiver recolhido a cinza da vaca lavará suas vestes e será impuro até a tarde. Essa será uma lei perpétua para os israelitas e para o estrangeiro que habita no meio deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4298, 136, 11, 'Quem tocar o cadáver de um homem qualquer será impuro sete dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4299, 136, 12, 'Deverá purificar-se com essa água ao terceiro e ao sétimo dia, e ficará puro; mas se ele não se purificar ao terceiro e ao sétimo dia, não ficará puro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4300, 136, 13, 'Todo que tiver tocado o cadáver de um homem qualquer, e não se purificar, manchará a casa do Senhor. Essa pessoa será eliminada de Israel. Não tendo corrido sobre ela a água lustral, ficará impura, e sua impureza permanecerá sobre ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4301, 136, 14, 'Esta é a lei: Tudo o que penetrar na tenda em que morrer um homem será impuro durante sete dias, e igualmente tudo o que ali se encontrar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4302, 136, 15, 'O vaso aberto, sem tampa, será também impuro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4303, 136, 16, 'Se alguém, em pleno campo, tocar em um homem morto pela espada, em um cadáver, em ossos humanos, ou em um sepulcro, será impuro durante sete dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4304, 136, 17, 'Para quem se tiver assim manchado, se tomará da cinza da vítima queimada pelo pecado, e se despejará por cima dela, dentro de um vaso, água viva.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4305, 136, 18, 'Em seguida, um homem puro, depois de ter molhado nela um hissopo, aspergirá com ele a tenda, todo o seu mobiliário, todas as pessoas que aí se encontram, bem como a pessoa que tocou nos ossos, ou no homem assassinado, ou no cadáver, ou no sepulcro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4306, 136, 19, 'O homem puro aspergirá o impuro ao terceiro e ao sétimo dia e o purificará no sétimo dia. Lavará as suas vestes e a si mes­mo, e à tarde será puro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4307, 136, 20, 'O homem impuro que não se purificar será eliminado da assembleia, porque ele mancha o santuário do Senhor. Não tendo corrido sobre ele a água lustral, ele permanece impuro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4308, 136, 21, 'Essa será para eles uma lei perpétua. Aquele que tiver feito a aspersão com a água lustral deverá lavar suas vestes. Todo o que tocar a água lustral será impuro até a tarde.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4309, 136, 22, 'Todo o que tocar o impuro será manchado, e a pessoa que o tocar ficará impura até a tarde”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4310, 137, 1, 'Toda a assembleia dos filhos de Israel chegou ao deserto de Sin no primeiro mês. O povo ficou em Cades; ali morreu Maria, que foi sepultada no mesmo lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4311, 137, 2, 'Como não houvesse água para a assembleia, o povo se ajuntou contra Moisés e Aarão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4312, 137, 3, 'procurou disputar com Moisés e gritou: “Oxalá tivéssemos perecido com nossos irmãos diante do Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4313, 137, 4, 'Por que conduziste a assembleia do Senhor a este deserto, para nos deixar morrer aqui com os nossos rebanhos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4314, 137, 5, 'Por que nos fizeste sair do Egito e nos trouxeste a este péssimo lugar, em que não se pode semear, e onde não há figueira, nem vinha, nem romãzeira, e tampouco há água para beber?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4315, 137, 6, 'Moisés e Aarão deixaram a assembleia e dirigiram-se à entrada da tenda de reunião, onde se prostraram com a face por terra. Apareceu-lhes a glória do Senhor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4316, 137, 7, 'e o Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4317, 137, 8, '“Toma a tua vara e convoca a assembleia, tu e teu irmão Aarão. Ordenareis ao rochedo, diante de todos, que dê as suas águas; farás brotar a água do rochedo e darás de beber à assembleia e aos seus rebanhos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4318, 137, 9, 'Tomou Moisés a vara que estava diante do Senhor, como ele lhe tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4319, 137, 10, 'Em seguida, tendo Moisés e Aarão convocado a assembleia diante do rochedo, disse-lhes Moisés: “Ouvi, rebeldes: Acaso faremos nós brotar água deste rochedo?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4320, 137, 11, 'Moisés levantou a mão e feriu o rochedo com a sua vara duas vezes; as águas jorraram em abundância, de sorte que beberam, o povo e os animais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4321, 137, 12, 'Em seguida, disse o Senhor a Moi­sés e Aarão: “Porque faltastes à confiança em mim para fazer brilhar a minha santidade aos olhos dos israelitas, não introduzireis esta assembleia na terra que lhe destino”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4322, 137, 13, 'Estas são as águas de Meriba, onde os israelitas se queixaram do Senhor, e onde este fez resplandecer a sua santidade.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4323, 137, 14, 'De Cades, Moisés enviou mensageiros ao rei de Edom: “Eis – disseram-lhe eles – as palavras que te dirige o teu irmão Israel: Tu sabes todos os males que temos passado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4324, 137, 15, 'Nossos pais tinham descido ao Egito, onde habitamos durante muito tempo. Os egípcios, porém, nos maltrataram, a nós e a nossos pais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4325, 137, 16, 'Clamamos ao Senhor, ele nos ouviu, e mandou-nos um anjo que nos tirou do Egito. Eis-nos agora aqui em Cades, cidade situada nos confins de teu território.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4326, 137, 17, 'Deixa-nos passar pela tua terra. Não atravessaremos os campos, nem as vinhas e não beberemos a água dos poços; mas seguiremos a estrada real sem nos desviar­mos nem para a direita nem para a esquerda, até que tenhamos passado o teu território”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4327, 137, 18, 'Edom respondeu: “Tu não passarás pela minha terra; do contrário, sairei ao teu encontro com a espada na mão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4328, 137, 19, 'Disseram-lhe os israelitas: “Tomaremos a estrada comum, e se bebermos de tua água, eu e os meus rebanhos, te pagarei o preço. Não há perigo algum; só queremos passar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4329, 137, 20, 'Edom replicou: “Tu não passarás”. E veio em massa ao encontro deles com as armas na mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4330, 137, 21, 'Recusando Edom a passagem através do seu território, Israel tomou outra direção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4331, 137, 22, 'Partiram de Cades. Toda a assembleia dos israelitas chegou ao monte Hor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4332, 137, 23, 'Nesse lugar, que está nas fronteiras da terra de Edom, o Senhor disse a Moisés e a Aarão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4333, 137, 24, '“Aarão vai ser reunido aos seus, porque ele não entrará na terra que destino aos filhos de Israel, visto terdes sido rebeldes à minha ordem nas águas de Meriba.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4334, 137, 25, 'Toma Aarão e seu filho Eleazar, e leva-os ao monte Hor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4335, 137, 26, 'Despojarás Aarão de suas vestes e revestirás com elas o seu filho Eleazar. Aarão será reunido aos seus, e aí morrerá”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4336, 137, 27, 'Moisés fez como ordenou o Senhor: Subiram ao monte Hor à vista da assembleia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4337, 137, 28, 'Despojando Aarão de suas vestes, Moisés revestiu com elas Eleazar, filho do sacerdote. Aarão morreu ali, no cimo do monte. Moisés e Eleazar desceram de novo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4338, 137, 29, 'e toda a assembleia, ao saber da morte de Aarão, chorou-o durante trinta dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4339, 138, 1, 'O rei cananeu Arad, que habitava no Negueb, soube que Israel avançava pelo caminho de Atarim; atacou-o e levou alguns deles prisioneiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4340, 138, 2, 'Então Israel fez ao Senhor este voto: se me entregardes nas mãos esse povo, votarei as suas cidades ao interdito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4341, 138, 3, 'O Senhor ouviu os rogos de Israel e entregou-lhe os cananeus, que foram votados ao interdito juntamente com as suas cidades. Deu-se a esse lugar o nome de Horma.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4342, 138, 4, 'Partiram do monte Hor em direção ao mar Vermelho, para contornar a terra de Edom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4343, 138, 5, 'Mas o povo perdeu a coragem no caminho, e começou a murmurar contra Deus e contra Moisés: “Por que – diziam eles – nos tirastes do Egito, para morrermos no deserto onde não há pão nem água? Estamos enjoados desse miserável alimento”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4344, 138, 6, 'Então o Senhor enviou contra o povo serpentes ardentes, que morderam e mataram muitos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4345, 138, 7, 'O povo veio a Moisés e disse-lhe: “Pecamos, murmurando contra o Senhor e contra ti. Roga ao Senhor que afaste de nós essas serpentes”. Moisés intercedeu pelo povo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4346, 138, 8, 'e o Senhor disse a Moisés: “Faze para ti uma serpente ardente e mete-a sobre um poste. Todo o que for mordido, olhando para ela, será salvo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4347, 138, 9, 'Moisés fez, pois, uma serpente de bronze, e fixou-a sobre um poste. Se alguém era mordido por uma serpente e olhava para a serpente de bronze, conservava a vida.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4348, 138, 10, 'Os filhos de Israel partiram e acam­param em Obot.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4349, 138, 11, 'Deixaram Obot e acamparam em Jeabarim, no deserto que está defronte de Moab, ao oriente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4350, 138, 12, 'Dali foram para o vale de Zared.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4351, 138, 13, 'Saindo de Zared, acamparam para além do Arnon, no deserto, nos limites do território dos amorreus. O Arnon, com efeito, serve de fronteira entre Moab e os amorreus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4352, 138, 14, 'É por isso que se diz no Livro das Guerras do Senhor: “Vaeb em Sufa, e as torrentes do Arnon,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4353, 138, 15, 'e o declive dos vales que se inclina para o sítio de Ar e se apoia na fronteira de Moab...”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4354, 138, 16, 'Partindo dali, ganharam Beer, que é o poço a respeito do qual o Senhor disse a Moisés: “Reúne o povo, para que eu lhe dê água”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4355, 138, 17, 'Então cantou Israel este cântico:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4356, 138, 18, '“Brota, ó poço! Cantai-lhe! Poço cavado pelos príncipes, aberto pelos nobres do povo com cetro e com bastões!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4357, 138, 19, 'Do deserto foram a Matana; de Mata­na a Naaliel; de Naaliel a Bamot;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4358, 138, 20, 'de Bamot ao vale que está nos campos de Moab, no cimo do Fasga, que domina o deserto.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4359, 138, 21, 'Israel mandou mensageiros a Seon, rei dos amorreus, para lhe dizer:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4360, 138, 22, '“Permite-nos passar pela tua terra. Não nos desviaremos nem para os campos, nem para as vinhas, e não beberemos a água dos poços; mas seguiremos a estrada real até que tenhamos atravessado tuas fronteiras”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4361, 138, 23, 'Seon, porém, não quis permitir que Israel passasse pelo seu território. Ao contrário, ajuntou suas tropas e partiu ao encontro de Israel no deserto. Veio a Jasa e combateu contra Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4362, 138, 24, 'Porém, Israel o feriu com o fio da espada e apoderou-se de toda a sua terra, desde o Arnon até o Jaboc, fronteira dos amonitas, porque essa fronteira era poderosa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4363, 138, 25, 'Israel tomou todas as cidades dos amorreus e estabeleceu-se em Hesebon e nas suas aldeias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4364, 138, 26, 'Hesebon era a cidade de Seon, rei dos amorreus, o qual tinha feito guerra ao rei precedente de Moab e tinha-lhe tomado toda a sua terra até o Arnon.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4365, 138, 27, 'Por isso, os poetas dizem: “Vinde a Hesebon! Vai ser reconstruída, vai ser fortificada a cidade de Seon!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4366, 138, 28, 'Porque um fogo saiu de Hesebon, uma chama, da cidade de Seon, e devorou Ar-Moab e os Baal das alturas do Arnon.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4367, 138, 29, 'Ai de ti, Moab! Estás perdido, povo de Camos! Entregaram seus filhos fugitivos e suas filhas cativas a Seon, rei dos amorreus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4368, 138, 30, 'Nós os crivamos de flechas; Hesebon está destruída até Dibon. Devastamos até Nofe, incendiamos até Mádaba”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4369, 138, 31, 'Israel estabeleceu-se na terra dos amorreus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4370, 138, 32, 'Moisés enviou exploradores a Jazer, e os israelitas tomaram-na junta­mente com suas aldeias, expulsando os amorreus que aí se encontravam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4371, 138, 33, 'Depois mudaram de direção e subiram pelo caminho de Basã. Og, rei de Basã, foi-lhes ao encontro com todo o seu povo, para combatê-los em Edrai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4372, 138, 34, '“Não o temas – disse o Senhor a Moisés – porque vou entregá-lo em tuas mãos, ele, o seu exército e a sua terra. Farão o mesmo que fizeste com Seon, rei dos amorreus, que morava em Hesebon.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4373, 138, 35, 'Feriram-no, pois, ele, seus filhos e todo o seu povo, de sorte que não ficou um sequer, e se apoderaram de sua terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4374, 139, 1, 'Partiram os filhos de Israel e acamparam nas planícies de Moab, além do Jordão, defronte de Jericó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4375, 139, 2, 'Balac, filho de Sefor, tinha visto tudo o que Israel tinha feito aos amorreus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4376, 139, 3, 'Moab teve grande medo desse povo, porque era muito numeroso e ficou aterrorizado diante dos israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4377, 139, 4, 'E Moab disse aos anciãos de Madiã: “Essa multidão vai devorar todos os nossos arredores como os bois devoram a erva do campo”. Balac, filho de Sefor, era então o rei de Moab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4378, 139, 5, 'Mandou, pois, mensageiros a Balaão, filho de Beor, em Petor, sobre o rio, na terra dos filhos de Amon, para que o chamassem e lhe dissessem: “Há aqui um povo que saiu do Egito, o qual cobre a face da terra, e estabeleceu-se diante de mim.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4379, 139, 6, 'Rogo-te que venhas e amaldiçoes esse povo, pois é muito mais poderoso do que eu. Talvez assim eu possa batê-lo e expulsá-lo de minha terra. Eu sei que será bendito o que abençoares e maldito o que amaldiçoares”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4380, 139, 7, 'Os anciãos de Moab e de Madiã partiram levando consigo o preço da adivinhação. Chegando junto de Balaão, referiram-lhe as palavras de Balac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4381, 139, 8, 'Balaão respondeu: “Passai a noite aqui, e vos darei a resposta que o Senhor me indicar”. Ficaram, pois, os chefes de Moab em casa de Balaão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4382, 139, 9, 'Deus veio a Balaão e disse-lhe: “Quem é essa gente que tens em tua casa?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4383, 139, 10, 'Balaão respondeu a Deus: “É Balac, filho de Sefor, rei de Moab, que me manda dizer:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4384, 139, 11, 'Há aqui um povo que saiu do Egito, o qual cobre a superfície da terra. Vem, pois, e amaldiçoa-o. Talvez assim possa eu batê-lo e expulsá-lo da terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4385, 139, 12, 'Disse Deus a Balaão: “Não irás com eles, e não amaldiçoarás esse povo, porque é bendito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4386, 139, 13, 'Levantando-se Balaão pela manhã, disse aos chefes enviados por Balac: “Voltai para a vossa terra, pois o Senhor me proibiu de ir convosco”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4387, 139, 14, 'Os chefes de Moab retomaram o caminho e voltaram para junto de Balac: “Balaão – disseram-lhe eles – recusou vir conosco”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4388, 139, 15, 'Balac mandou-lhe de novo outros chefes, mais numerosos e mais importantes que os primeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4389, 139, 16, 'Chegados junto a Balaão, disseram-lhe: “Eis a mensagem de Balac, filho de Sefor: Rogo-te que não recuses vir ter comigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4390, 139, 17, 'Eu te cumularei de honras e farei tudo o que me disseres. Vem amaldiçoar esse povo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4391, 139, 18, '“Ainda que o vosso senhor me desse a sua casa cheia de prata e de ouro – respondeu Balaão aos servos de Balac – eu não poderia transgredir a ordem do Senhor, meu Deus, nem pouco nem muito, no que quer que seja.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4392, 139, 19, 'Todavia, passai ainda esta noite aqui, para que eu saiba o que o Senhor me responderá ainda desta vez.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4393, 139, 20, 'Deus veio a Balaão durante a noite e disse-lhe: “Já que essa gente te veio chamar, levanta-te e vai com eles. Mas só farás o que eu te disser”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4394, 139, 21, 'Balaão levantou-se de manhã, selou sua jumenta e partiu com os chefes de Moab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4395, 139, 22, 'O Senhor irritou-se com sua partida, e o anjo do Senhor pôs-se-lhe no caminho como obstáculo. Balaão cavalgava em sua jumenta, acompanhado de seus dois servos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4396, 139, 23, 'A jumenta, vendo o anjo do Senhor postado no caminho, com uma espada desembainhada na mão, desviou-se e seguiu pelo campo; o adivinho a fustigava para fazê-la voltar ao caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4397, 139, 24, 'Então o anjo do Senhor pôs-se em um caminho estreito que passava por entre as vinhas, com um muro de cada lado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4398, 139, 25, 'Vendo-o, a jumenta coseu-se com o muro, ferindo contra ele o pé de Balaão, que a fustigou de novo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4399, 139, 26, 'O anjo do Senhor deteve-se de novo mais adiante em uma passagem estreita, onde não havia espaço para se desviar nem para a direita nem para a esquerda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4400, 139, 27, 'A jumenta, ao vê-lo, deitou-se debaixo de Balaão, o qual, encolerizado, a fustigava mais fortemente com seu bastão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4401, 139, 28, 'Então o Senhor abriu a boca da jumenta, que disse a Balaão: “Que te fiz eu? Por que me bateste já três vezes?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4402, 139, 29, '“Porque zombaste de mim – respondeu ele –. “Ah, se eu tivesse uma espada na mão! Já o teria matado!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4403, 139, 30, 'A jumenta replicou: “Acaso não sou eu a tua jumenta, a qual montaste até o dia de hoje? Tenho eu porventura o costume de proceder assim contigo?”. “Não” – respondeu ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4404, 139, 31, 'Então o Senhor abriu os olhos de Balaão, e ele viu o anjo do Senhor que estava no caminho com a espada desembainhada na mão. Inclinou-se e prostrou-se com a face por terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4405, 139, 32, '“Por que – disse-lhe o anjo do Senhor – feriste três vezes a tua jumenta? Eu vim opor-me a ti, porque segues um caminho que te leva ao precipício.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4406, 139, 33, 'Vendo-me, a tua jumenta desviou-se por três vezes diante de mim. Se ela não o tivesse feito, eu te haveria matado, e ela ficaria viva.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4407, 139, 34, 'Balaão disse ao anjo do Senhor: “Pequei. Eu não sabia que estavas postado no caminho para deter-me. Se minha viagem te desagrada, voltarei”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4408, 139, 35, '“Segue esses homens – respondeu-lhe o anjo do Senhor – mas cuida de só proferir as palavras que eu te disser.” E Balaão partiu com os chefes de Balac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4409, 139, 36, 'Quando Balac soube de sua chegada, subiu-lhe ao encontro até a cidade de Moab, na fronteira do Arnon, na extremidade daquela terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4410, 139, 37, 'e disse-lhe: “Mandei mensageiros chamar-te. Por que não vieste logo? Não posso eu tratar-te com honras?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4411, 139, 38, '“Eis-me aqui – respondeu Ba­laão – mas poderei eu agora dizer algo de mim mesmo? Só direi o que Deus me puser na boca, nada mais.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4412, 139, 39, 'E partiram os dois para Cariat-Husot.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4413, 139, 40, 'Balac imolou em sacrifício bois e ovelhas, dos quais mandou algumas porções a Balaão e aos chefes que o acompanhavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4414, 139, 41, 'No dia seguinte pela manhã, Balac tomou consigo o adivinho e levou-o a Bamot-Baal, de onde se podiam ver as últimas linhas do acampamento de Israel.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4415, 140, 1, 'Balaão disse ao rei: “Levanta-me aqui sete altares e prepara-me sete touros e sete carneiros”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4416, 140, 2, 'Balac fez o que o adivinho pediu, e ofereceram juntos um touro e um carneiro em cada altar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4417, 140, 3, '“Fica – disse Balaão a Balac – junto de teu holocausto, enquanto eu me afasto. Talvez o Senhor venha ao meu encontro, e te direi tudo o que ele me mandar.” Afastou-se Balaão e foi para um monte escalvado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4418, 140, 4, 'onde Deus se lhe apresentou. Balaão disse a Deus: “Levantei sete altares, e sobre cada altar ofereci um touro e um carneiro”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4419, 140, 5, 'O Senhor pôs então uma palavra na boca de Balaão e disse: “Volta para junto de Balac e dize-lhe isto e isto”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4420, 140, 6, 'Voltando para perto do rei, encontrou-o de pé junto do seu holocausto, com todos os chefes de Moab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4421, 140, 7, 'Balaão pronunciou o seguinte oráculo: “De Aram mandou-me vir Balac, das montanhas do Oriente, o rei de Moab: Vem! Por mim amaldiçoa Jacó! Vem votar Israel à perdição!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4422, 140, 8, 'Como poderei amaldiçoar a quem Deus não amaldiçoa? Como encolerizar-me, se o Senhor não se encolerizou?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4423, 140, 9, 'Do alto dos rochedos eu contemplo, estou vendo do cimo das colinas: um povo isolado, não contado entre as nações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4424, 140, 10, 'Quem poderia calcular o pó de Jacó? Quem poderia medir as nuvens de Israel? Que eu morra da morte dos justos, que o meu fim se assemelhe ao fim deles!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4425, 140, 11, 'Balac disse a Balaão: “Que me fizeste? Mandei-te chamar para amaldiçoares os meus inimigos; e eis que os abençoas!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4426, 140, 12, '“Porventura – respondeu o adivinho – não devo eu cuidar de só dizer o que o Senhor põe na minha boca?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4427, 140, 13, 'Balac disse-lhe então: “Vem comigo a outro lugar de onde poderás vê-los. Não verás somente a sua extremidade, mas todo o seu acampamento, e dali os amaldiçoarás”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4428, 140, 14, 'Conduziu-o ao campo de Sofim, no cimo do Fasga, onde levantou sete altares para serem oferecidos sobre cada qual um touro e um carneiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4429, 140, 15, 'Balaão disse-lhe: “Fica aqui junto de teu holocausto, enquanto vou ao encontro do Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4430, 140, 16, 'O Senhor apresentou-se a Balaão, pôs-lhe na boca uma palavra e disse: “Volta a Balac e dize-lhe isto e isto”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4431, 140, 17, 'Voltou o adivinho para junto do rei, o qual estava de pé ao lado do seu holocausto com os chefes de Moab. “Que disse o Senhor?” – perguntou-lhe Balac.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4432, 140, 18, 'E Balaão pronunciou o seguinte oráculo: “Levanta-te, Balac, e escuta; presta-me atenção, filho de Sefor:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4433, 140, 19, 'Deus não é homem para mentir, nem alguém para se arrepender. Alguma vez prometeu sem cumprir? Por acaso falou e não executou?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4434, 140, 20, 'Recebi ordem de abençoar; ele aben­çoou: nada posso mudar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4435, 140, 21, 'Não achou iniquidade em Jacó, nem perversidade em Israel. O Senhor, seu Deus, está com ele, nele é proclamado rei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4436, 140, 22, 'Deus os retirou do Egito e lhes deu o vigor do búfalo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4437, 140, 23, 'Não é preciso magia em Jacó, nem adivinhação em Israel: a seu tempo, se dirá a Jacó e a Israel o que Deus quer fazer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4438, 140, 24, 'Este povo levanta-se como leoa, firma-se como leão; não se deita sem ter devorado a presa e bebido o sangue de suas vítimas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4439, 140, 25, 'Balac disse a Balaão: “Se não os amaldiçoas, ao menos não os abençoes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4440, 140, 26, '“Não te disse eu – respondeu Balaão – que faria tudo o que o Senhor me dissesse?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4441, 140, 27, 'Balac replicou: “Vem: eu te conduzirei a outro lugar; talvez Deus se agrade que tu os amaldiçoes de lá”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4442, 140, 28, 'Balac levou o adivinho ao cimo do monte Fogor, que domina o deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4443, 140, 29, 'Balaão disse-lhe: “Constrói-me sete altares, e prepara-me sete touros e sete carneiros”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4444, 140, 30, 'Balac fez como ordenara Balaão, e ofereceu sobre cada altar um touro e um carneiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4445, 141, 1, 'Balaão, vendo que era do agrado do Senhor que abençoasse Israel, não foi como antes ao encontro de agouros. Voltou-se para o deserto');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4446, 141, 2, 'e, levantando os olhos, viu Israel acampado nas tendas segundo as suas tribos. O Espírito de Deus veio sobre ele,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4447, 141, 3, 'e pronunciou o oráculo seguinte: “Oráculo de Balaão, filho de Beor, oráculo do homem que tem o olho fechado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4448, 141, 4, 'oráculo daquele que ouve as palavras de Deus, desfruta a visão do Todo-poderoso, e se lhe abrem os olhos quando se prostra:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4449, 141, 5, 'Quão formosas tuas tendas, Jacó, tuas moradas, Israel!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4450, 141, 6, 'Elas se estendem como vales, como jardins à beira do rio, como aloés plantados pelo Senhor, como cedros junto das águas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4451, 141, 7, 'Jorram águas de seus jarros, suas sementeiras são copiosamente irrigadas. Seu rei é mais poderoso que Agag, de sublime realeza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4452, 141, 8, 'Deus os retirou do Egito, e lhes deu o vigor do búfalo. Devora os povos inimigos; quebra-lhes os ossos e criva-os de flechas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4453, 141, 9, 'Deita-se, descansa como um leão, como uma leoa: quem o despertará? Bendito seja quem te abençoar, maldito quem te amaldiçoar!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4454, 141, 10, 'Balac, encolerizado contra Balaão, bateu as mãos e disse-lhe: “Foi para amaldiçoar os meus inimigos que te chamei, e eis que já pela terceira vez os abençoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4455, 141, 11, 'Agora, vai-te depressa para a tua casa. Pensei em cumular-te de honras, mas o Senhor privou-te dessas honras”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4456, 141, 12, '“Pois não disse eu aos teus mensageiros – respondeu Balaão –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4457, 141, 13, 'mesmo que Balac me desse a sua casa cheia de prata e de ouro, eu não poderia transgredir a ordem do Senhor, nem fazer o que quer que seja por minha própria conta; somente diria o que o Senhor me ordenasse?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4458, 141, 14, 'Pois bem; volto para o meu povo. Vem, pois quero anunciar-te o que esse povo fará ao teu no decurso dos tempos.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4459, 141, 15, 'E Balaão pronunciou o oráculo seguinte: “Oráculo de Balaão, filho de Beor, oráculo do homem que tem o olho fechado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4460, 141, 16, 'oráculo daquele que ouve as palavras de Deus, conhece a ciência do Altíssimo, desfruta a visão do Todo-poderoso e se lhe abrem os olhos quando se prostra:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4461, 141, 17, 'eu o vejo, mas não é para agora, percebo-o, mas não de perto: um astro sai de Jacó, um cetro levanta-se de Israel, que fratura a cabeça de Moab, o crânio dessa raça guerreira. *');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4462, 141, 18, 'Edom é sua conquista, Seir, seu inimigo, é sua presa. Israel ostenta a sua força.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4463, 141, 19, 'De Jacó virá um dominador que há de exterminar os sobreviventes da cidade.”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4464, 141, 20, 'Ao ver Amalec, Balaão pronunciou este oráculo: “Amalec é a primeira das nações, mas seu fim será o extermínio”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4465, 141, 21, 'Depois, ao ver os quenitas, pronunciou o seguinte oráculo: “Sólida é a tua morada, teu ninho está posto na rocha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4466, 141, 22, 'Mas o quenita será aniquilado; Assur te levará ao cativeiro”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4467, 141, 23, 'E, por fim, acrescentou este oráculo: “Povos vivem ao norte. Navios hão de aportar das costas de Citim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4468, 141, 24, 'e oprimirão Assur, e oprimirão Héber, pois, também este perecerá para sempre”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4469, 141, 25, 'E, depois disso, Balaão partiu para a sua terra, enquanto Balac voltou pelo caminho por onde tinha vindo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4470, 142, 1, 'Habitando os israelitas em Setim, entregaram-se à libertinagem com as filhas de Moab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4471, 142, 2, 'Estas convidaram o povo aos sacrifícios de seus deuses, e o povo comeu e prostrou-se diante dos seus deuses.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4472, 142, 3, 'Israel juntou-se a Baal-Fegor, provocando assim contra ele a cólera do Senhor:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4473, 142, 4, '“Reúne – disse o Senhor a Moisés – todos os chefes do povo, e pendura os culpados em forcas diante de mim, de cara para o sol, a fim de que o fo­go de minha cólera se desvie de Israel”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4474, 142, 5, 'Moisés disse aos juízes de Israel: “Cada um de vós mate os seus que se tenham juntado a Baal-Fegor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4475, 142, 6, 'Entretanto, um dos filhos de Israel trouxe para junto de seus irmãos uma madianita, sob os olhos de Moisés e de toda a assembleia que chorava à entrada da tenda de reunião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4476, 142, 7, 'Vendo isso, Fineias, filho de Eleazar, filho do sacerdote Aarão, levantou-se no meio da assembleia, tomou uma lança,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4477, 142, 8, 'seguiu o israelita até a sua tenda, e ali transpassou-o juntamente com a mulher, ferindo-os no ventre. E deteve-se então o flagelo que se alastrava entre os israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4478, 142, 9, 'Morreram vinte e quatro mil homens com essa praga.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4479, 142, 10, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4480, 142, 11, '“Fineias, filho de Eleazar, filho do sacerdote Aarão, desviou minha cólera de sobre os israe­litas, dando provas entre eles do mesmo zelo que eu. Por isso, não os extingui em minha cólera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4481, 142, 12, 'Dize-lhe, pois, que lhe dou a minha aliança de paz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4482, 142, 13, 'Isso será para ele e seus descendentes o pacto de um sacerdócio eterno, porque se mostrou cheio de zelo pelo seu Deus e fez expiação pelos israelitas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4483, 142, 14, 'Chamava-se Zambri, filho de Salu, o israelita que foi morto com a madianita, o qual era chefe de uma família patriarcal da tribo de Simeão;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4484, 142, 15, 'o nome da madia­nita morta era Cozbi, filha de Sur, chefe de tribo, de família patriarcal em Madiã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4485, 142, 16, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4486, 142, 17, '“Atacai os madianitas e matai-os,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4487, 142, 18, 'porque eles vos atacaram primeiro, enganando-vos artificiosamente por meio do ídolo de Fogor e de Cozbi, sua irmã, filha de um chefe de Madiã, que foi massacrada no dia do flagelo que sobreveio por causa do sacrilégio de Fogor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4488, 143, 1, 'Depois desse flagelo, disse o Se-nhor a Moisés e a Eleazar, filho do sacerdote Aarão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4489, 143, 2, '“Fazei o recenseamento de toda a assembleia dos israelitas da idade de vinte anos para cima, família por família, todos os que estiverem em condições de pegar em armas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4490, 143, 3, 'Moisés e o sacerdote Eleazar disseram pois nas planícies de Moab, às margens do Jordão, perto de Jericó:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4491, 143, 4, '“Serão recenseados todos os que tiverem a idade de vinte anos para cima, como o Senhor ordenou a Moisés e aos israelitas ao saírem do Egito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4492, 143, 5, 'Rúben, primogênito de Israel. Filhos de Rúben: de Henoc, a família dos heno­quitas; de Falu, a família dos faluítas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4493, 143, 6, 'de Hesron, a família dos hesronitas; de Carmi, a família dos carmitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4494, 143, 7, 'Essas são as famílias dos rubenitas; seus recenseados foram em número de quarenta e três mil setecentos e trinta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4495, 143, 8, 'Filho de Falu, Eliab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4496, 143, 9, 'Filhos de Eliab, Namuel, Datã e Abiram. Estes são Datã e Abiram, aqueles membros do conselho que se tinham sublevado contra Moisés e Aarão, com os cúmplices de Coré em revolta contra o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4497, 143, 10, 'A terra, abrindo sua boca, engoliu-os com Coré, enquanto o seu grupo perecia pelo fogo que devorou os duzentos e cinquenta homens. Isso serviu de exemplo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4498, 143, 11, 'Os filhos de Coré, porém, não pereceram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4499, 143, 12, 'Filhos de Simeão, classificados por famílias: de Namuel, a família dos namue­litas; de Jamim, a família dos jaminitas; de Joaquim, a família dos joaquinitas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4500, 143, 13, 'de Zaré, a família dos zaritas; de Saul, a família dos saulitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4501, 143, 14, 'Tais são as famílias dos simeonitas: vinte dois mil e duzentos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4502, 143, 15, 'Filhos de Gad, classificados por famílias: de Sefon, a família dos sefonitas; de Agi, a família dos agitas; de Suni, a família dos sunitas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4503, 143, 16, 'de Ozni, a família dos oznitas; de Her, a família dos heritas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4504, 143, 17, 'de Arod, a família dos aroditas; de Ariel, a família dos arielitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4505, 143, 18, 'Essas são as famílias dos gaditas. Seus recenseados foram quarenta mil e quinhentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4506, 143, 19, 'Filhos de Judá: Her e Onã, que morreram na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4507, 143, 20, 'Eis os filhos de Judá, classificados por famílias: de Sela, a família dos selitas; de Farés, a família dos faresitas; de Zara, a família dos zaritas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4508, 143, 21, 'Os filhos de Farés foram: de Hesron, a família dos hesronitas; de Hamul, a família dos hamulitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4509, 143, 22, 'Tais são as famílias de Judá; seus recenseados foram setenta e seis mil e quinhentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4510, 143, 23, 'Filhos de Issacar, classificados por famílias: de Tola, a família dos tolaítas; de Fua, a família dos fuaítas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4511, 143, 24, 'de Jasub, a família dos jasubitas; de Semrã, a família dos semranitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4512, 143, 25, 'Essas são as famílias de Issacar; seus recenseados foram sessenta e quatro mil e trezentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4513, 143, 26, 'Filhos de Zabulon, classificados por famílias: de Sared, a família dos sareditas; de Elon, a família dos elonitas; de Jalel, a família dos jalelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4514, 143, 27, 'Essas são as famílias de Zabulon; seus recenseados foram sessenta mil e quinhentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4515, 143, 28, 'Filhos de José, classificados por famílias: Manassés e Efraim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4516, 143, 29, 'Filhos de Manassés: de Maquir, a família dos maquiritas. Maquir gerou Ga­laad; de Galaad, a família dos galaaditas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4517, 143, 30, 'Eis os filhos de Galaad: de Jezer, a família dos jezeritas; de Helec, a família dos helequitas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4518, 143, 31, 'de Asriel, a família dos asrie­litas; de Siquém, a família dos seque­mitas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4519, 143, 32, 'de Semida, a família dos semidaí­tas; de Héfer, a família dos héferitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4520, 143, 33, 'Sala­faad, filho de Héfer, não teve filhos, mas muitas filhas. Eis os nomes das filhas de Salafaad: Maala, Noa, Hegla, Melca e Tersa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4521, 143, 34, 'Essas são as famílias de Manassés; seus recenseados foram cinquenta e dois mil e setecentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4522, 143, 35, 'Eis os filhos de Efraim, classificados por famílias: de Sutala, a família dos sutalaítas; de Bequer, a família dos beque­ritas; de Teen, a família dos teenitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4523, 143, 36, 'Eis os filhos de Sutala: de Herã, a família dos heranitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4524, 143, 37, 'Essas são as famílias dos filhos de Efraim; seus recenseados foram trinta e dois mil e quinhentos. Esses são os filhos de José, classificados por famílias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4525, 143, 38, 'Filhos de Benjamim, classificados por famílias: de Bela, a família dos belaí­tas; de Asbel, a família dos asbelitas; de Airam, a família dos airamitas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4526, 143, 39, 'de Sufam, a família dos sufamitas; de Hufam, a família dos hufamitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4527, 143, 40, 'Os filhos de Bela foram Arad e Naamã; de Arad, a família dos hereditas; de Naamã, a família dos naamanitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4528, 143, 41, 'Tais são os filhos de Benja­mim, classificados por famílias; seus recenseados foram em número de quarenta e cinco mil e seiscentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4529, 143, 42, 'Eis os filhos de Dã, classificados por famílias: de Suam, a família dos suami­tas. Tais são as famílias de Dã, classificadas por famílias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4530, 143, 43, 'Total das famílias dos suamitas: seus recenseados foram sessenta e quatro mil e quatrocentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4531, 143, 44, 'Filhos de Aser, classificados por famílias: de Jemna, a família dos jemnaítas; de Jessui, a família dos jessuítas; de Beria, a família dos briaítas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4532, 143, 45, 'de Héber, a família dos heberitas; de Melquiel, a família dos melquielitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4533, 143, 46, 'O nome da filha de Aser era Sara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4534, 143, 47, 'Tais são as famílias dos filhos de Aser; seus recenseados foram cinquenta e tres mil e quatrocentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4535, 143, 48, 'Filhos de Neftali, classificados por famílias: de Jesiel, a família dos jesielitas; de Guni, a família dos gunitas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4536, 143, 49, 'de Je­ser, a família dos jeseritas; de Selém, a fa­mília dos selemitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4537, 143, 50, 'Essas são as famílias de Neftali, classificadas por famílias; seus recenseados foram quarenta e cinco mil e quatrocentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4538, 143, 51, 'Eis o total dos israelitas recensea­dos: seiscentos e um mil setecentos e trinta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4539, 143, 52, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4540, 143, 53, '“A terra será dividida entre estes, segundo o número de suas pessoas, para que eles a possuam como herança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4541, 143, 54, 'Aos mais numerosos darás uma parte maior, e aos que forem menos, uma menor; cada um receberá uma parte proporcional ao número dos recenseados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4542, 143, 55, 'Todavia, é a sorte que decidirá a divisão da terra. Eles receberão a sua parte segundo os nomes das tribos patriarcais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4543, 143, 56, 'A propriedade será dividida por sorte entre os grupos numerosos e os grupos menores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4544, 143, 57, 'Eis os levitas recenseados segundo suas famílias: de Gérson, a família dos gersonitas; de Caat, a família dos caatitas; de Merari, a família dos meraritas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4545, 143, 58, 'Eis as famílias de Levi: a família dos lobnitas, a dos hebronitas, a dos moolitas, a dos musitas, a dos coritas. (Caat gerou Amram,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4546, 143, 59, 'cuja mulher se chamava Jocabed, filha de Levi, nascida no Egito. Ela deu a Amram: Aarão, Moisés e Maria, sua irmã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4547, 143, 60, 'Aarão teve os filhos: Nadab, Abiú, Eleazar e Itamar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4548, 143, 61, 'Nadab e Abiú morreram quando apresentaram diante do Senhor um fogo estranho.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4549, 143, 62, 'Recenseados os levitas, todos os varões da idade de um mês para cima somaram vinte e três mil. Não foram contados no recenseamento dos is­raelitas, porque não se lhes havia destinado nenhum patrimônio no meio deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4550, 143, 63, 'Tal é o recenseamento dos israe­litas que fizeram Moisés e o sacerdote Eleazar nas planícies de Moab, às margens do Jordão, perto de Jericó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4551, 143, 64, 'Não se achou entre eles nenhum daqueles que tinham sido recenseados antes por Moi­sés e Aarão, no deserto do Sinai,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4552, 143, 65, 'porque o Senhor dissera deles: “Morrerão no deserto”. Não ficou nenhum deles, exceto Caleb, filho de Jefoné, e Josué, filho de Nun.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4553, 144, 1, 'Aproximaram-se então as filhas de Salafaad, filho de Héfer, filho de Galaad, filho de Maquir, filho de Manassés, filho de José. Seus nomes eram Maala, Noa, Hegla, Melca e Tersa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4554, 144, 2, 'Elas apresentaram-se diante de Moisés e do sacerdote Eleazar, e diante dos principais e de toda a assembleia, à entrada da tenda de reunião:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4555, 144, 3, '“Nosso pai – disseram elas – morreu no deserto, e não tomou parte na sedição excitada por Coré contra o Senhor; mas morreu por causa de seu próprio pecado. Ora, ele não teve filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4556, 144, 4, 'Por que razão há de desaparecer o nome de sua família, por não ter tido filho algum? Dá-nos uma propriedade entre os irmãos de nosso pai”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4557, 144, 5, 'Moisés levou a sua causa diante do Senhor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4558, 144, 6, 'que lhe disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4559, 144, 7, '“As filhas de Salafaad têm razão. Dá-lhes uma propriedade como herança entre os irmãos de seu pai, para que elas lhe sucedam na herança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4560, 144, 8, 'Dirás aos israelitas: Se um homem morrer sem deixar filhos, a herança passará à sua filha;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4561, 144, 9, 'se não tiver filhas, será dada aos seus irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4562, 144, 10, 'Se não tiver irmãos, a herança passará aos irmãos de seu pai,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4563, 144, 11, 'e se seu pai não tiver irmãos, será dada ao seu parente mais próximo em sua família, e este último se tornará seu possessor. Essa será para os filhos de Israel uma prescrição de direito, assim como o Senhor ordenou a Moisés”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4564, 144, 12, 'O Senhor disse a Moisés: “Sobe a esse monte Abarim e contempla a terra que eu hei de dar aos israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4565, 144, 13, 'Depois de a teres visto, serás reunido aos teus, como o teu irmão Aarão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4566, 144, 14, 'porque, no deserto de Sin, na contenda da assembleia, fostes rebeldes à minha ordem, não manifestando a minha santidade diante deles na questão das águas”. (Trata-se das águas de Meriba, em Cades, no deserto de Sin.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4567, 144, 15, 'Moisés disse ao Senhor:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4568, 144, 16, '“O Senhor Deus dos espíritos e de toda a carne escolha um homem que chefie a assembleia,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4569, 144, 17, 'que marche à sua frente e guie os seus passos, para que a assembleia do Senhor não seja como um rebanho sem pastor”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4570, 144, 18, 'O Senhor respondeu a Moisés: “Toma Josué, filho de Nun, no qual reside o Espírito, e impõe-lhe a mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4571, 144, 19, 'Apresenta-o ao sacerdote Eleazar e a toda a assembleia, e o empossarás sob os seus olhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4572, 144, 20, 'Tu o investirás de tua autoridade, a fim de que toda a assembleia dos israe­litas lhe obedeça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4573, 144, 21, 'Ele se apresentará ao sacerdote Eleazar, que consultará por ele o oráculo de Urim diante do Senhor; é segundo essa ordem que se conduzirão, ele e toda a assembleia dos israelitas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4574, 144, 22, 'Moisés fez como o Senhor tinha ordenado. Tomou Josué e apresentou-o ao sacerdote Eleazar, bem como a toda a assembleia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4575, 144, 23, 'Impôs-lhe as mãos e empossou-o assim como o Senhor tinha ordenado pela boca de Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4576, 145, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4577, 145, 2, '“Ordena o seguinte aos israelitas: Cuidareis de apresentar no devido tempo a minha oblação, o meu alimento, em sacrifícios de agradável odor consumidos pelo fogo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4578, 145, 3, 'Dize-lhes: Eis o sacrifício pelo fogo que oferecereis ao Senhor: um holocausto cotidiano e perpétuo de dois cordeiros de um ano, sem defeito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4579, 145, 4, 'Oferecerás um pela manhã e outro entre as duas tardes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4580, 145, 5, 'juntando, à guisa de oblação, um décimo de efá de flor de farinha amassada com um quarto de hin de óleo de olivas esma­ga­das.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4581, 145, 6, 'Esse é o holocausto perpétuo tal como foi feito no monte Sinai, um sacrifício pelo fogo de suave odor para o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4582, 145, 7, 'A libação será de um quarto de hin para cada cordeiro; é no santuário que farás ao Senhor a libação de vinho fermentado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4583, 145, 8, 'Oferecerás, entre as duas tardes, o segundo cordeiro; e farás a mesma oblação e a mesma libação como de manhã: este é um sacrifício pelo fogo, de suave odor para o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4584, 145, 9, 'No dia de sábado oferecereis dois cor­deiros de um ano sem defeito, com dois décimos de flor de farinha amassada com óleo, à guisa de oblação, e a libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4585, 145, 10, 'Esse é o holocausto de cada sábado, além do holocausto perpétuo com a sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4586, 145, 11, 'No início de cada mês oferecereis, em holo­causto ao Senhor, dois novilhos, um carneiro, sete cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4587, 145, 12, 'bem como, com cada touro, três décimos de flor de farinha amassada com óleo, à guisa de oblação; com o carneiro, uma oblação de dois décimos de flor de farinha amassada com óleo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4588, 145, 13, 'e com cada cordeiro, um décimo de flor de farinha amassada com óleo. Esse é um holocausto de suave odor consumido pelo fogo para o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4589, 145, 14, 'As respectivas libações serão de meio-hin de vinho por touro, um terço de hin por carneiro, e um quarto de hin pelo cordeiro. Esse será o holocausto do mês para cada mês do ano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4590, 145, 15, 'Além do holocausto perpétuo e sua liba­ção, será também oferecido ao Senhor um bode em sacrifício pelo pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4591, 145, 16, 'No décimo quarto dia do primeiro mês será a Páscoa do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4592, 145, 17, 'No décimo quinto desse mês começará a festa: durante sete dias só comerão pães sem fermento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4593, 145, 18, 'No primeiro dia haverá uma santa assembleia: não fareis nenhum trabalho servil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4594, 145, 19, 'Oferecereis em holocaus­to pelo fogo, ao Senhor, dois novilhos, um carneiro e sete cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4595, 145, 20, 'com flor de farinha amassada com óleo, à guisa de oblação: três décimos por touro, dois décimos para o carneiro');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4596, 145, 21, 'e um décimo para cada um dos sete cordeiros;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4597, 145, 22, 'além disso, um bode, em sacrifício pelo pecado, para fazer expiação por vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4598, 145, 23, 'Tudo isso, vós o fareis sem prejuízo do holocausto da manhã, que é perpétuo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4599, 145, 24, 'Assim fareis cada dia, durante sete dias: este é o alimento consumido pelo fogo, de suave odor para o Senhor. Isso se fará além do holocausto perpétuo e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4600, 145, 25, 'No sétimo dia, haverá de novo uma santa assembleia e a cessação de toda obra servil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4601, 145, 26, 'No dia das Primícias, quando apre­sentardes ao Senhor uma oblação de grão novo na vossa festa das Semanas, tereis uma santa assembleia e a suspensão de todo o trabalho servil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4602, 145, 27, 'Oferece­reis em holocausto de suave odor ao Senhor, dois novilhos, um carneiro e sete cordeiros de um ano,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4603, 145, 28, 'com flor de farinha amassada com óleo, à guisa de oblação: três décimos por touro, dois décimos para o carneiro');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4604, 145, 29, 'e um décimo para cada um dos sete cordeiros;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4605, 145, 30, 'além disso, um bode, para a expiação de vossos pecados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4606, 145, 31, 'Isso, sem prejuí­zo do holocausto perpétuo e de sua oblação. Os animais serão sem defeito e acompanhados de suas libações”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4607, 146, 1, '“No primeiro dia do sétimo mês tereis uma santa assembleia e a suspensão de todo o trabalho servil. Será para vós um dia de toques de trombetas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4608, 146, 2, 'Oferecereis, em holocausto de suave odor ao Senhor, um novilho, um carneiro e sete cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4609, 146, 3, 'com flor de farinha amassada com óleo, à guisa de oblação: três décimos pelo touro, dois décimos pelo carneiro');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4610, 146, 4, 'e um décimo para cada um dos sete cordeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4611, 146, 5, 'Além disso, um bode, em sacrifício pelo pecado, para fazer a expiação por vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4612, 146, 6, 'Tudo isso, sem prejuízo do holocausto do mês e sua oblação, do holocausto perpétuo e sua oblação, e das libações que devem acompanhar regularmente esses sacrifícios. Esses serão sacrifícios pelo fogo, de agradável odor ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4613, 146, 7, 'No dia dez desse sétimo mês, tereis uma santa assembleia, um jejum e a suspensão de todo o trabalho servil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4614, 146, 8, 'Ofere­cereis em holocausto de suave odor ao Senhor, um novilho, um carneiro e sete cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4615, 146, 9, 'com flor de farinha amassada com óleo, à guisa de oblação: três décimos para o touro, dois décimos para o carneiro');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4616, 146, 10, 'e um décimo para cada um dos sete cordeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4617, 146, 11, 'Além disso, um bode, em sacrifício pelo pecado, sem prejuízo do sacrifício para as expiações, do holocausto perpétuo com sua oblação, nem de suas libações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4618, 146, 12, 'No dia quinze do sétimo mês, tereis uma santa assembleia com a suspensão de toda obra servil. Celebrareis uma festa em honra do Senhor durante sete dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4619, 146, 13, 'Oferecereis em holocausto, em sacrifício de agradável odor ao Senhor: treze novilhos, dois carneiros e catorze cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4620, 146, 14, 'com flor de farinha amassada com óleo, à guisa de oblação: três décimos por touro, dois décimos por carneiro');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4621, 146, 15, 'e um décimo para cada um dos catorze cordeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4622, 146, 16, 'Além disso, um bode, em sacrifício pelo pecado, sem prejuízo do holocausto perpétuo, com sua oblação e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4623, 146, 17, 'No segundo dia, oferecereis doze novilhos, dois carneiros e catorze cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4624, 146, 18, 'com a oblação e as libações pelos touros, carneiros e cordeiros, proporcionalmente ao seu número, segundo a regra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4625, 146, 19, 'Além disso, um bode em sacrifício pelo pecado, sem prejuízo do holocausto perpétuo, com sua oblação e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4626, 146, 20, 'No terceiro dia, oferecereis onze novilhos, dois carneiros e catorze cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4627, 146, 21, 'com a oblação e as liba­ções pelos touros, carneiros e cordeiros, proporcionalmente ao seu número, segundo a regra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4628, 146, 22, 'Além disso, um bode, em sacrifício pelo pecado, sem prejuízo do holocausto perpétuo, com sua oblação e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4629, 146, 23, 'No quarto dia, oferecereis dez novilhos, dois carneiros e catorze cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4630, 146, 24, 'com a oblação e as libações pelos touros, carneiros e cordeiros, proporcionalmente ao seu número, segundo a regra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4631, 146, 25, 'Além disso, um bode em sacrifício pelo pecado, sem prejuízo do holocausto perpétuo, com sua oblação e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4632, 146, 26, 'No quinto dia, oferecereis nove novilhos, dois carneiros e catorze cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4633, 146, 27, 'com a obla­ção e as libações pelos touros, carneiros e cordeiros, proporcionalmente ao seu número, segundo a regra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4634, 146, 28, 'Além disso, um bode em sacrifício pelo pecado, sem prejuízo do holocausto perpétuo, com sua oblação e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4635, 146, 29, 'No sexto dia, oferecereis oito novilhos, dois carneiros e catorze cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4636, 146, 30, 'com a oblação e as libações pelos touros, carneiros e cordeiros, proporcionalmente ao seu número, segundo a regra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4637, 146, 31, 'Além disso, um bode, em sacrifício pelo pecado, sem prejuízo do holocausto perpétuo, com sua oblação e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4638, 146, 32, 'No sétimo dia, oferecereis sete novilhos, dois carneiros e catorze cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4639, 146, 33, 'com a obla­ção e as libações pelos touros, carneiros e cordeiros, proporcionalmente ao seu número, segundo a regra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4640, 146, 34, 'Além disso, um bode em sacrifício pelo pecado, sem prejuízo do holocausto perpétuo, com sua oblação e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4641, 146, 35, 'No oitavo dia, tereis uma solene assembleia e a cessação de todo o trabalho servil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4642, 146, 36, 'Oferecereis em holocausto, em sa­crifício consumido pelo fogo, de suave odor ao Senhor: um touro, um carneiro e sete cordeiros de um ano sem defeito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4643, 146, 37, 'com a oblação e as libações pelo touro, pelo carneiro e pelos cordeiros, proporcionalmente ao seu número, segundo a regra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4644, 146, 38, 'Além disso, um bode, em sacrifício pelo pecado, sem prejuízo do holocausto perpétuo, com sua oblação e sua libação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4645, 146, 39, 'Tais são os sacrifícios que oferece­reis ao Senhor em vossas solenidades, além de vossos votos e vossas ofertas espontâneas: holocaustos, oblações, liba­ções e sacrifícios pacíficos.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4646, 147, 1, 'Moisés referiu aos israelitas tudo o que o Senhor lhe tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4647, 147, 2, 'Moisés disse aos chefes das tribos israelitas: “Eis o que o Senhor ordenou:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4648, 147, 3, 'Se um homem fizer um voto ao Senhor ou se se comprometer com juramento a uma obrigação qualquer, não faltará à sua palavra, mas cumprirá toda obrigação que tiver tomado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4649, 147, 4, 'Se uma donzela, que se encontre ainda na casa de seu pai, fizer um voto ao Senhor, ou se se impuser uma obrigação,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4650, 147, 5, 'e seu pai, tendo conhecimento do voto que ela fez ou da obrigação que tomou, nada disse, todos os seus votos e suas obrigações serão válidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4651, 147, 6, 'Porém, se seu pai se opuser no dia em que ele tiver conhecimento disso, todos os seus votos e obrigações serão inválidos, e o Senhor o perdoará, porque seu pai se opôs.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4652, 147, 7, 'Se na ocasião de seu casamento ela estiver ligada por algum voto ou algum compromisso inconsiderado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4653, 147, 8, 'e seu marido, sabendo-o, não disser nada naquele dia, seus votos serão válidos, assim como os compromissos tomados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4654, 147, 9, 'Mas se o marido, no dia em que disso tiver conhecimento, desaprová-lo, serão nulos o seu voto e o compromisso tomados incon­side­ra­­damente, e o Senhor o perdoará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4655, 147, 10, 'O voto de uma viúva, ou de uma mulher repudiada, e toda obrigação que ela se impuser a si mesma, será válida para ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4656, 147, 11, 'A mulher que está em casa de seu marido, se se obrigar com voto, ou se se impuser uma obrigação, ou juramento,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4657, 147, 12, 'desde que o marido, ao sabê-lo, nada diga, nem se oponha, todos os seus votos serão válidos, bem como todo compromisso que ela tiver tomado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4658, 147, 13, 'Porém, se seu marido se opuser ao ser informado de seus votos, todos eles serão nulos e, igualmente, todos os compromissos que tiver tomado; serão sem valor, porque foram anulados por seu marido, e o Senhor o perdoará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4659, 147, 14, 'Seu marido pode ratificar ou anular todo voto ou todo juramento que ela tiver feito para se mortificar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4660, 147, 15, 'Se seu marido guardar silêncio até o dia seguinte, com isso ratifica todos os seus votos e todos os seus compromissos; e os ratifica porque nada disse no dia em que deles teve conhecimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4661, 147, 16, 'Se os anular depois do dia em que o soube, levará a responsabilidade da falta de sua mulher”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4662, 147, 17, 'Tais são as leis que o Senhor prescreveu a Moisés com relação a marido e mulher, pai e filha, quando esta é ainda jovem e vive na casa de seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4663, 148, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4664, 148, 2, '“Vinga os filhos de Israel do mal que lhes fizeram os madianitas; depois disso, serás reunido aos teus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4665, 148, 3, 'Moisés disse então ao povo: “Armem-se para a guerra alguns homens dentre vós: eles atacarão Madiã, para executarem sobre ele a vingança do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4666, 148, 4, 'Poreis em linha de combate mil homens de cada uma das tribos de Israel”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4667, 148, 5, 'Reuniram-se, pois, dentre as famílias de Israel, mil homens por tribo, ou seja, doze mil homens de pé, prontos para o combate.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4668, 148, 6, 'Moisés enviou-os ao combate; mil homens de cada tribo, com Fineias, filho do sacerdote Eleazar, que levou também os objetos sagrados e as trombetas para tocar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4669, 148, 7, 'Atacaram os madianitas, como o Senhor tinha ordenado a Moisés, e mataram todos os varões.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4670, 148, 8, 'Mataram também os reis de Madiã: Evi, Recém, Sur, Hur e Rebe, cinco reis de Madiã, e passaram a fio da espada Balaão, filho de Beor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4671, 148, 9, 'Levaram prisioneiras as mulheres dos madianitas com seus filhos, e pilharam todo o seu gado, seus rebanhos e todos os seus bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4672, 148, 10, 'Incendiaram todas as cidades que habitavam e todos os seus acampamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4673, 148, 11, 'Levaram consigo todo o espólio e todos os despojos, animais e pessoas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4674, 148, 12, 'e conduziram-nos a Moisés, ao sacerdote Eleazar e à assembleia dos israe­litas no acampamento que se encontrava nas planícies de Moab, perto do Jordão, em face de Jericó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4675, 148, 13, 'Moisés, o sacerdote Eleazar e todos os chefes da assembleia saíram-lhes ao encontro fora do acampamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4676, 148, 14, 'E Moisés, irado contra os generais do exército, os chefes de milhares e os chefes de centenas que voltavam da batalha, disse-lhes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4677, 148, 15, '“O que é isso? Deixastes com vida todas essas mulheres?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4678, 148, 16, 'Mas são justamente elas que, instigadas por Balaão, levaram os israelitas a serem infiéis ao Senhor na questão de Fegor, a qual foi também a causa do flagelo que feriu a assembleia do Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4679, 148, 17, 'Ide! Matai todos os filhos varões e todas as mulheres que tiverem tido comércio com um homem;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4680, 148, 18, 'mas deixai vivas todas as jovens que não o fizeram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4681, 148, 19, 'E vós, acampai durante sete dias fora do acampamento. Todos os que tiverem matado um homem ou tocado em um morto, deverão purificar-se ao terceiro e ao sétimo dia, eles e seus prisioneiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4682, 148, 20, 'Purificai também toda veste, todo objeto de pele, todo tecido de pêlo de cabra e todo utensílio de madeira”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4683, 148, 21, 'O sacerdote Eleazar disse então aos guerreiros que tinham combatido: “Eis o preceito da lei que o Senhor impôs a Moi­sés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4684, 148, 22, 'o ouro, a prata, o bronze, o ferro, o estanho, o chumbo, tudo o que pode passar pelas chamas');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4685, 148, 23, 'será purificado no fogo; mas será também purificado pela água lustral. Tudo o que não suporta o fogo será purificado com a água.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4686, 148, 24, 'Lava­reis vossas vestes no sétimo dia, para serdes puros; depois disso, voltareis ao acampamento”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4687, 148, 25, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4688, 148, 26, '“Fazei o inventário de todo o espólio que foi tomado, homens e animais, tu, o sacerdote Eleazar e os chefes de família da assembleia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4689, 148, 27, 'Repartirás em seguida a presa em partes iguais entre os que pelejaram, e entre todo o resto da assembleia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4690, 148, 28, 'Da parte daqueles que pelejaram e foram à guerra, separarás um tributo para o Senhor, um de cada quinhentos homens, gado, jumentos ou ovelhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4691, 148, 29, 'To­ma-o da sua metade para entregar ao sacerdote Elea­zar, como oferta ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4692, 148, 30, 'Da metade que toca aos israelitas, tomarás um de cada cinquenta, homens, bois, jumentos, ovelhas e qualquer outro animal, e darás aos levitas, que têm a guarda da casa do Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4693, 148, 31, 'Moisés e o sacerdote Eleazar fizeram como o Senhor tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4694, 148, 32, 'Os despojos, o conjunto do espólio que tinha feito o exército era de seiscentos e setenta e cinco mil ovelhas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4695, 148, 33, 'setenta e dois mil bois');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4696, 148, 34, 'e sessenta e um mil jumentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4697, 148, 35, 'Havia também trinta e duas mil jovens que não tinham coabitado com homem algum.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4698, 148, 36, 'Foi dada a metade àqueles que tinham ido ao combate, isto é, trezentas e trinta e sete mil e quinhentas ovelhas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4699, 148, 37, 'das quais seiscentas e setenta e cinco para o tributo do Senhor;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4700, 148, 38, 'trinta e seis mil bois, dos quais setenta e dois para o tributo do Senhor;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4701, 148, 39, 'trinta mil e quinhentos jumentos, dos quais sessenta e um para o tributo do Senhor;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4702, 148, 40, 'dezesseis mil pessoas, das quais trinta e duas para o tributo do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4703, 148, 41, 'Moisés entregou ao sacerdote Eleazar o tributo tomado para o Senhor, como o Senhor lhe tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4704, 148, 42, 'Restava a metade destinada aos filhos de Israel, que Moisés havia separado da dos guerreiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4705, 148, 43, 'Essa parte da assembleia compreendia trezentas e trinta e sete mil e quinhentas ovelhas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4706, 148, 44, 'trinta e seis mil bois,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4707, 148, 45, 'trinta mil e quinhentos jumentos');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4708, 148, 46, 'e dezesseis mil pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4709, 148, 47, 'Dessa metade dos israelitas Moisés tomou um de cada cinquenta, homens e animais, e deu-os aos levitas, encarregados do serviço da casa do Senhor, assim como o Senhor lhe tinha ordenado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4710, 148, 48, 'Os comandantes das tropas do exército, os chefes de milhares e de centenas');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4711, 148, 49, 'aproximaram-se então de Moisés e disseram-lhe: “Teus servos fizeram a conta dos guerreiros que estiveram sob o nosso comando: não falta nem um sequer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4712, 148, 50, 'Trazemos, pois, como oferta ao Senhor, tudo o que cada um encontrou de objetos de ouro: correntinhas, braceletes, anéis, brincos e colares, para que se faça expiação por nós diante do Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4713, 148, 51, 'Moisés e o sacerdote Eleazar receberam deles esse ouro, toda a sorte de objetos artisticamente trabalhados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4714, 148, 52, 'O peso total do ouro, que foi assim separado e oferecido ao Senhor da parte dos chefes de milhares e de centenas, era de dezesseis mil setecentos e cinquenta siclos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4715, 148, 53, 'Os homens da tropa haviam pilhado cada um para si.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4716, 148, 54, 'Moisés e o sacerdote Eleazar, tendo recebido o ouro das mãos dos chefes de milhares e de centenas, levaram-no à tenda de reunião para que servisse de memorial diante do Senhor pelos israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4717, 149, 1, 'Os filhos de Rúben e os filhos de Gad tinham rebanhos em grande quantidade; e, vendo que a terra de Jazer e a terra de Galaad eram próprias para a criação dos animais,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4718, 149, 2, 'vieram procurar Moisés, o sacerdote Eleazar e os chefes da assembleia, dizendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4719, 149, 3, '“Atarot, Dibon, Jazer, Nemra, Hesebon, Eleale, Sa­bam, Nebo e Beon,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4720, 149, 4, 'terras que o Senhor feriu diante dos filhos de Israel, são uma terra própria para o pasto dos rebanhos; e teus servos têm muitos animais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4721, 149, 5, 'E ajuntaram: “Se achamos graça diante de ti, seja dada essa terra em possessão aos teus servos, para que não tenhamos de atravessar o Jordão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4722, 149, 6, 'Moisés respondeu-lhes: “Irão os vossos irmãos à guerra, e vós vos quedareis tranquilamente aqui?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4723, 149, 7, 'Por que quereis desanimar os filhos de Israel, para que não entrem na terra que lhes deu o Senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4724, 149, 8, 'Foi justamente isso que fizeram os vossos pais quando os enviei de Cades Barne para explorar a terra:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4725, 149, 9, 'foram até o vale de Escol, viram a terra, e depois tiraram aos israelitas o desejo de entrar na terra que o Senhor lhes tinha dado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4726, 149, 10, 'Por isso, naquele dia a cólera do Senhor se inflamou de tal modo que ele fez este juramento:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4727, 149, 11, 'Os homens que subiram do Egito, da idade de vinte anos para cima, não verão jamais a terra que jurei dar a Abraão, a Isaac e a Jacó, porque não me seguiram com fidelidade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4728, 149, 12, 'exceto somente Caleb, filho de Jefoné, o cenezeu, e Josué, filho de Nun, que obedeceram sempre ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4729, 149, 13, 'E o Senhor, irado contra Israel, fê-lo errar pelo deserto durante quarenta anos, até que se extinguisse toda a geração que tinha feito o mal aos olhos do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4730, 149, 14, 'E agora, eis que tomais a sucessão de vossos pais, raça de pecadores, para aumentar ainda mais a cólera do Senhor contra Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4731, 149, 15, 'Se lhe recusais obedecer, ele continuará a vos deixar no deserto, e sereis a causa da ruína de todo o povo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4732, 149, 16, 'Mas eles, aproximando-se, disseram a Moisés: “Faremos aqui currais para os nossos rebanhos e construiremos cidades para os nossos filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4733, 149, 17, 'Nós, porém, nos equiparemos para marchar sem demora diante dos israelitas, até os introduzirmos na terra que lhes cabe, enquanto nossos filhos ficarão nas cidades fortes, ao abrigo dos habitantes da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4734, 149, 18, 'Não voltaremos às nossas casas antes que os israelitas tenham tomado posse cada um de sua porção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4735, 149, 19, 'Nada queremos do lado de lá do Jordão, junto deles, pois que já temos a nossa porção deste lado, ao oriente”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4736, 149, 20, 'Moisés disse-lhes: “Se fizerdes isso, e vos equipardes para combater diante do Senhor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4737, 149, 21, 'se todo homem apto para a guerra entre vós passar armado o Jordão diante do Senhor, até que o Senhor expulse seus inimigos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4738, 149, 22, 'e só voltardes para as vossas casas quando a terra estiver submetida dian­te do Senhor, então sereis irrepreensíveis aos olhos do Senhor e aos olhos de Israel, e possuireis esta terra que desejais diante do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4739, 149, 23, 'Mas se procederdes de outra forma, pecareis diante do Senhor; e sabeis que o vosso pecado cairá sobre vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4740, 149, 24, 'Construí, pois, cidades para os vossos filhos e fazei currais para os vossos rebanhos, mas cumpri vossa promessa”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4741, 149, 25, 'Os filhos de Gad e os filhos de Rúben responderam a Moisés: “Teus servos farão o que o meu senhor ordenar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4742, 149, 26, 'Nossos filhos, nossas mulheres, nossos rebanhos e nossos animais ficarão nas cidades de Galaad;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4743, 149, 27, 'e teus servos equipados para a guerra marcharão ao combate diante do Senhor, segundo a ordem do meu senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4744, 149, 28, 'Então Moisés deu ordens a respeito deles ao sacerdote Eleazar, a Josué, filho de Nun, e aos chefes das famílias das tribos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4745, 149, 29, 'Disse ele: “Se os filhos de Gad e os filhos de Rúben passarem convosco o Jordão equipados para o combate diante do Senhor, e a terra vos for sujeita, lhes deixareis em possessão a terra de Galaad.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4746, 149, 30, 'Mas, se não passarem armados convosco, deverão estabelecer-se no meio de vós, na terra de Canaã”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4747, 149, 31, 'Os filhos de Gad e os filhos de Rúben replicaram: “Faremos o que o Senhor disse aos teus servos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4748, 149, 32, 'Iremos armados diante do Senhor para a terra de Canaã, e nossa parte de terra será deste lado do Jordão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4749, 149, 33, 'Então Moisés deu aos filhos de Gad, aos filhos de Rúben e à meia tribo de Ma­nas­sés, filho de José, o reino de Seon, rei dos amorreus, e o de Og, rei de Basã: a ter­ra, com suas cidades e seus distritos, e as cidades da terra circunvizinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4750, 149, 34, 'Os filhos de Gad construíram Dibon, Atarot, Aroer,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4751, 149, 35, 'Atrot-Sofã, Jazer, Jegbaa,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4752, 149, 36, 'Bet-Nemra e Bet-Arã, cidades fortes, e fizeram currais para os rebanhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4753, 149, 37, 'Os filhos de Rúben construíram Hesebon, Eleale, Cariataim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4754, 149, 38, 'Nebo e Baal-Meon, mudando-lhes os nomes, e Sábama; e deram nomes às cidades que edificaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4755, 149, 39, 'Os filhos de Maquir, filho de Manas­sés, foram a Galaad e tomaram-na em possessão, depois de terem expulsado os amorreus que ali habitavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4756, 149, 40, 'Moisés deu Galaad a Maquir, filho de Manassés, o qual se estabeleceu ali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4757, 149, 41, 'Jair, filho de Manassés, foi e ocupou suas aldeias, às quais deu o nome de aldeias de Jair.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4758, 149, 42, 'Nob marchou contra Canat, e apoderou-se dela, bem como das aldeias dependentes, dando-lhe em seguida o nome de Nob, seu próprio nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4759, 150, 1, 'Eis as etapas que fizeram os israe­litas desde a sua partida do Egito em tropas organizadas sob as ordens de Moisés e Aarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4760, 150, 2, 'Moisés, por or­dem do Senhor, tomou nota de suas marchas por etapas. São as seguintes essas marchas em etapas:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4761, 150, 3, 'No décimo quinto dia do primeiro mês partiram de Ramsés. Isso foi no dia seguinte à Páscoa; partiram com a mão levantada, à vista de todos os egípcios');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4762, 150, 4, 'que estavam enterrando aqueles que o Senhor tinha ferido dentre eles, todos os seus primogê­nitos. Também contra os seus deuses o Senhor tinha exercido o seu juízo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4763, 150, 5, 'Partidos de Ramsés, os israelitas se detiveram em Sucot,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4764, 150, 6, 'de onde partiram, indo acampar em Etam, situado na extremidade do deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4765, 150, 7, 'Dali, voltaram a Piairot, defronte de Baal Sefon, e acamparam diante de Magdol.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4766, 150, 8, 'Deixando Piairot, passaram pelo meio do mar para o deserto. Após três dias de marcha na solidão de Etam, detiveram-se em Mara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4767, 150, 9, 'Partindo de Mara, ganharam Elim, onde havia doze fontes e setenta palmeiras; e acamparam ali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4768, 150, 10, 'Saindo de Elim, foram acampar junto do mar Vermelho,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4769, 150, 11, 'de onde partiram e acamparam no deserto de Sin.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4770, 150, 12, 'Tendo partido do deserto de Sin, acamparam em Dafca,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4771, 150, 13, 'de onde foram para Alus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4772, 150, 14, 'Dali, partiram e acamparam em Rafidim, onde o povo não encontrou água para beber.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4773, 150, 15, 'Partidos de Rafidim, acamparam no deserto do Sinai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4774, 150, 16, 'Saindo do deserto do Sinai, foram acampar em Kibrot-Hataava,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4775, 150, 17, 'de onde foram acampar em Haserot.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4776, 150, 18, 'De lá, acamparam em Retma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4777, 150, 19, 'De Retma foram a Remon­-Farés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4778, 150, 20, 'De Remon-Farés a Lebna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4779, 150, 21, 'De Lebna a Ressa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4780, 150, 22, 'De Ressa a Ceelata.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4781, 150, 23, 'Deixaram Ceelata e acamparam no monte Sefer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4782, 150, 24, 'Dali foram acampar em Arada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4783, 150, 25, 'De lá a Macelot.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4784, 150, 26, 'Dali a Taat.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4785, 150, 27, 'De Taat a Taré.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4786, 150, 28, 'De Taré a Metca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4787, 150, 29, 'De Metca a Hesmona.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4788, 150, 30, 'De Hesmona foram acampar em Moserot.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4789, 150, 31, 'De Moserot a Benê-Jacã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4790, 150, 32, 'Dali a Hor-Guidgad.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4791, 150, 33, 'Dali a Jotebata.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4792, 150, 34, 'Dali a Abrona.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4793, 150, 35, 'De lá a Asiongaber.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4794, 150, 36, 'De Asiongaber foram acampar no deserto de Sin, isto é, em Cades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4795, 150, 37, 'Deixando Cades, acamparam no monte Hor, na extremidade da terra de Edom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4796, 150, 38, 'O sacerdote Aarão subiu por ordem do Senhor ao monte Hor e ali morreu, no qua­dragésimo ano do êxodo dos israelitas do Egito, no primeiro dia do quinto mês.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4797, 150, 39, 'Aarão tinha cento e vinte e três anos quando expirou no monte Hor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4798, 150, 40, 'Foi então que o rei cananeu de Arad, que habitava no Negueb, na terra de Canaã, soube da chegada dos israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4799, 150, 41, 'Deixando o monte Hor acamparam em Salmona.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4800, 150, 42, 'De Salmona foram acampar em Funon.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4801, 150, 43, 'De Funon foram a Obot.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4802, 150, 44, 'De Obot, detiveram-se em Ijé-Aba­rim, na fronteira de Moab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4803, 150, 45, 'Dali foram acampar em Dibon-Gad.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4804, 150, 46, 'Dali a Almon-Diblataim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4805, 150, 47, 'Dali aos montes Abarim, em frente ao Nebo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4806, 150, 48, 'Partiram dos montes Aba­rim e foram acampar nas planícies de Moab, junto do Jordão, defronte de Jericó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4807, 150, 49, 'Seu acampamento nas planícies de Moab, perto do Jordão, ia desde Bet-Jesimot até Abel-Setim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4808, 150, 50, 'O Senhor disse a Moisés, nas planícies de Moab, junto do Jordão, defronte de Jericó:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4809, 150, 51, '“Dize aos israelitas: Quando tiverdes passado o Jordão e entrado na terra de Canaã,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4810, 150, 52, 'expulsareis de diante de vós todos os habitantes da terra, destruireis todas as suas pedras esculpidas, todas as suas estátuas fundidas e devastareis todos os seus lugares altos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4811, 150, 53, 'Tomareis posse da terra e habitai-a, porque vo-la dou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4812, 150, 54, 'Distribuí entre vossas famílias por sorte: aos que forem mais numerosos, uma porção maior, e uma menor, aos que forem menos. Cada um possuirá o que lhe couber por sorte. Fareis essa repartição segundo vossas tribos patriarcais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4813, 150, 55, 'Se vós, porém, não expulsardes de diante de vós os habitantes da terra, os que ficarem serão para vós como espinhos nos olhos e aguilhões nos flancos, e vos perseguirão na terra onde habitardes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4814, 150, 56, 'E tudo o que eu tinha pensado fazer a eles, o farei a vós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4815, 151, 1, 'O Senhor disse a Moisés: “Eis uma ordem para os israelitas:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4816, 151, 2, 'Quando entrardes na terra de Canaã, eis a terra que vos tocará como herança: a terra de Canaã, com estes limites:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4817, 151, 3, 'para o lado do meio-dia, vossa fronteira começará no deserto de Sin ao longo de Edom. Essa fronteira meridional partirá, ao oriente, da extremidade do mar Salgado');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4818, 151, 4, 'e irá para o lado do meio-dia pela subida de Acrabim. Passará por Sin e chegará até o sul de Cades Barne, de onde irá até Hatsar-Adar, estendendo-se para Asemon.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4819, 151, 5, 'De Asemon se dirigirá para a torrente do Egito, e terminará no mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4820, 151, 6, 'Vossa fronteira ocidental será o mar Grande, que fará o vosso limite ao ocidente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4821, 151, 7, 'Eis vossa fronteira setentrional: partindo do mar Grande, tereis por limite o monte Hor;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4822, 151, 8, 'desde o monte Hor marcareis até a entrada de Emat, terminando em Sedada;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4823, 151, 9, 'estenderá em seguida para Zefrona até Hatsar-Enã. Esse será o vosso limite setentrional.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4824, 151, 10, 'Para vossa fronteira oriental, marca­reis uma linha de Hasar-Enon a Sefana;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4825, 151, 11, 'descerá de Sefana a Rebla, ao oriente de Ain; depois, continuando, atingirá a praia oriental do mar de Genesaré,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4826, 151, 12, 'e enfim, descerá ao longo do Jordão, terminando no mar Salgado. Tal será a vossa terra em todo o perímetro de vossas fronteiras”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4827, 151, 13, 'Moisés ordenou aos israelitas o seguinte: “Esta será a terra que possuireis por sorte, e que o Senhor mandou que se desse às nove tribos e à meia tribo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4828, 151, 14, 'porque a tribo dos rubenitas, por suas famílias, assim como a tribo dos gaditas, por suas famílias, e a meia tribo de Manassés receberam já a sua porção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4829, 151, 15, 'Essas duas tribos e a meia tribo têm a sua herança além do Jordão, defronte de Jericó, para o levante”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4830, 151, 16, 'O Senhor disse a Moisés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4831, 151, 17, '“Eis os nomes dos homens que dividirão a terra entre vós: o sacerdote Eleazar, e Josué, filho de Nun.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4832, 151, 18, 'Tomareis, além disso, um príncipe de cada tribo para proceder à divisão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4833, 151, 19, 'Eis os nomes desses príncipes: da tribo de Judá, Caleb, filho de Jefoné;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4834, 151, 20, 'da tribo dos filhos de Simeão, Samuel, filho de Amiud;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4835, 151, 21, 'da tribo de Benjamim, Elidad, filho de Caselon;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4836, 151, 22, 'da tribo dos filhos de Dã, um príncipe, Boci, filho de Jogli;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4837, 151, 23, 'dos filhos de José, da tribo dos filhos de Manassés, um príncipe, Haniel, filho de Efod,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4838, 151, 24, 'e da tribo dos filhos de Efraim, um príncipe, Camuel, filho de Seftã;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4839, 151, 25, 'da tribo dos filhos de Zabulon, um príncipe, Elisafã, filho de Farnac;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4840, 151, 26, 'da tribo dos filhos de Issacar, um príncipe, Faltiel, filho de Ozã;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4841, 151, 27, 'da tribo dos filhos de Aser, um príncipe, Aiud, filho de Salomi;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4842, 151, 28, 'da tribo dos filhos de Neftali, um príncipe, Fedael, filho de Amiud.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4843, 151, 29, 'Tais são os que o Senhor designou para repartir entre os israelitas a terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4844, 152, 1, 'O Senhor disse a Moisés nas planícies de Moab, perto do Jordão, defronte de Jericó:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4845, 152, 2, '“Ordena aos filhos de Israel que de suas possessões deem aos levitas cidades para habitarem, bem como os subúrbios em volta dessas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4846, 152, 3, 'Terão as cidades para nelas habitarem, e os territórios circunvizinhos para a criação de seus gados, seus bens e seus outros animais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4847, 152, 4, 'O território circunvi­zinho das cidades que dareis aos levitas terá mil côvados de extensão em todos os sentidos, a partir do muro da cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4848, 152, 5, 'Medireis, pois, fora da cidade, dois mil côvados para o oriente, dois mil côvados para o sul, dois mil cô­vados para o ocidente e dois mil côvados para o norte, ficando a cidade no centro. Tais serão os territórios das cidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4849, 152, 6, 'Quanto às cidades que dareis aos levitas, seis serão cidades de refúgio destinadas ao asilo dos homicidas, e mais quarenta e duas cidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4850, 152, 7, 'O total das cidades que dareis aos levitas será, pois, de quarenta e oito, com as terras circunja­centes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4851, 152, 8, 'As cidades que se hão de dar das partes dos filhos de Israel, vós as tomareis em maior número dos que têm mais, em menor número dos que têm menos; cada uma das tribos cederá de seus territórios aos levitas na proporção da parte que lhe tocar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4852, 152, 9, 'O Senhor disse a Moisés: “Dize aos israelitas:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4853, 152, 10, 'Quando tiverdes passado o Jordão e entrado na terra de Canaã,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4854, 152, 11, 'escolhereis cidades de refúgio onde se possam retirar os homicidas que tiverem involuntariamente matado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4855, 152, 12, 'Elas vos servirão de asilo contra o vingador de sangue, de sorte que o homicida não seja morto antes de haver comparecido em juízo diante da assembleia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4856, 152, 13, 'Serão em número de seis as cidades que destinareis a esse fim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4857, 152, 14, 'Dareis três além do Jordão e três cidades na terra de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4858, 152, 15, 'Serão cidades de refúgio, e servirão aos israelitas, aos peregrinos e a qualquer outro que habite no meio de vós, para ali encontrar asilo quando houver matado alguém por descuido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4859, 152, 16, 'Se o homicida feriu com ferro, e o ferido morrer, é réu de homicídio, e morrerá também ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4860, 152, 17, 'Se foi com uma pedra atirada com a mão que o feriu, capaz de causar a morte, e realmente morrer o ferido, é réu de homicídio, e morrerá também ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4861, 152, 18, 'Se foi com um pau na mão, capaz de causar a morte, e esta venha de fato, é réu de homicídio; será punido de morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4862, 152, 19, 'O vingador de sangue o matará; logo que o encontrar, o matará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4863, 152, 20, 'Se um homem derrubar outro por ódio, ou lhe atirar qualquer coisa premeditadamente, causando-lhe a morte,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4864, 152, 21, 'ou se feri-lo com a mão por inimizade, e ele morrer, o que o feriu será punido de morte, porque é um assassino: o vingador de sangue o matará logo que o encontrar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4865, 152, 22, 'Mas se foi acidentalmente e sem ódio que o derrubou, ou lhe atirou qualquer objeto sem premeditação,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4866, 152, 23, 'ou se, sem ser seu inimigo nem procurar fazer-lhe mal, atingiu-o com uma pedra por descuido, podendo com isso causar-lhe a morte, e de fato ele morrer,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4867, 152, 24, 'então a assembleia julgará entre o homicida e o vingador de sangue de acordo com essas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4868, 152, 25, 'A assembleia livrará o homicida da mão do vingador de sangue e o recon­duzirá à cidade de refúgio onde se tinha abrigado. Permanecerá ali até a morte do sumo sacerdote que foi ungido com o santo óleo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4869, 152, 26, 'Mas, se o homicida se encontra fora dos limites da cidade de refúgio, para onde se tinha retirado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4870, 152, 27, 'e for morto pelo vingador de sangue ao encontrá-lo fora, este não será culpado de homicídio,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4871, 152, 28, 'porque o criminoso deveria permanecer na ci­dade de refúgio até a morte do sumo sacerdote. Somente depois que este morresse, poderia o homicida voltar para a terra onde ele tivesse a sua propriedade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4872, 152, 29, 'Isso vos servirá como prescrição de direito para vós e vossos descendentes, onde quer que habiteis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4873, 152, 30, 'Todo homem que matar outro será morto, ouvidas as testemunhas; mas uma só testemunha não bastará para condenar um homem à morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4874, 152, 31, 'Não aceitareis resgate pela vida de um homicida que merece a morte: deve morrer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4875, 152, 32, 'Tampouco aceitareis resgate pelo refugiado em uma cidade de refúgio, de maneira que ele volte a habitar na sua terra antes da morte do sumo sacerdote.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4876, 152, 33, 'Não manchareis a terra de vossa habitação, porque o sangue mancha a terra. O sangue derramado não poderá ser expiado pela terra senão com o sangue daquele que o tiver derramado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4877, 152, 34, 'Não manchareis a terra em que ides habitar, onde também eu habito, porque eu sou o Senhor, que habito no meio dos filhos de Israel”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4878, 153, 1, 'Os chefes de família dos filhos de Galaad, filho de Maquir, filho de Manassés, da raça de José, apresentaram-se diante de Moisés e dos principais chefes das famílias israelitas:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4879, 153, 2, '“O Senhor – disseram eles – ordenou ao meu senhor que desse, por sorte, a terra em herança aos israe­litas; e o Senhor ordenou ao meu senhor que desse às filhas de Salafaad, nosso irmão, a herança devida ao seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4880, 153, 3, 'Mas se homens de outra tribo as receberem por mulheres, a sua herança será retirada do patri­mônio de nossos pais e acrescentada ao da tribo na qual elas se casarem; e assim será diminuída a nossa herança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4881, 153, 4, 'Quando chegar o jubileu dos filhos de Israel, a sua herança será unida à da tribo a que pertencerem, e separada da de nossos pais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4882, 153, 5, 'Moisés, então, respondeu aos filhos de Israel, por ordem do Senhor: “Tem razão a tribo dos filhos de José.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4883, 153, 6, 'Eis a ordem do Senhor para as filhas de Salafaad: Casem com quem quiserem, contanto que seja com alguém de uma família da tribo paterna;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4884, 153, 7, 'desse modo, as possessões dos israelitas não passarão de uma tribo à outra, e cada israe­lita ficará na herança da tribo de seus pais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4885, 153, 8, 'Todas as mulheres que possuírem um patrimônio em uma tribo israelita, tomarão marido na tribo paterna, a fim de que cada israelita conserve o patrimônio de família.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4886, 153, 9, 'Herança alguma poderá passar de uma tribo à outra: deve cada tribo israelita permanecer no que é seu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4887, 153, 10, 'As filhas de Salafaad comportaram-se segundo a ordem do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4888, 153, 11, 'Maala, Tersa, Hegla, Melca e Noa, filhas de Salafaad, casaram-se com filhos de seus tios;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4889, 153, 12, 'casaram-se, pois, em famílias saídas de Manassés, filho de José, e a possessão que lhes tocava permaneceu na tribo de seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4890, 153, 13, 'Tais são as leis e as ordenações que o Senhor transmitiu aos israelitas por intermédio de Moisés, nas planícies de Moab, perto do Jordão, a caminho de Jericó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4891, 154, 1, 'Eis os discursos que Moisés dirigiu a todo o Israel, do outro lado do Jordão, no deserto, na planície que se estende defronte de Suf, entre Farã, Tofel, Labã, Haserot e Dizaab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4892, 154, 2, 'Desde Horeb até Cades Barne há uma distância de onze jornadas de marcha pelo caminho da montanha de Seir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4893, 154, 3, 'No quadragésimo ano, no primeiro dia do décimo primeiro mês, dian­te dos israelitas, Moisés pronunciou todos os discursos que o Senhor lhe tinha ordenado dizer,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4894, 154, 4, 'depois de ter derrotado Seon, rei dos amorreus, que habitava em Hesebon, e Og, rei de Basã, que morava em Astarot e Edrai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4895, 154, 5, 'Do outro lado do Jordão, na terra de Moab, Moisés começou a expor a lei, dizendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4896, 154, 6, '“O Senhor, nosso Deus, falou-nos nestes termos em Horeb: Tendes-vos demorado muito tempo neste monte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4897, 154, 7, 'Voltai e parti. Tomai o caminho do monte dos amorreus e das regiões vizinhas; ide às planícies, às montanhas, aos vales, ao Negueb, às costas do mar, à terra dos cananeus, ao Líbano e até o grande rio Eufrates.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4898, 154, 8, 'Eis que eu vos entrego esta terra. Ide e possuí a terra que jurei dar a vossos pais Abraão, Isaac e Jacó, a eles e à sua posteridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4899, 154, 9, 'Eu vos disse nessa mesma época: eu sozinho não posso tomar conta de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4900, 154, 10, 'O Senhor, vosso Deus, vos multiplicou de tal modo que sois hoje tão numerosos como as estrelas do céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4901, 154, 11, 'Que o Senhor, o Deus de vossos pais, vos multiplique mil vezes mais e vos abençoe como prometeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4902, 154, 12, 'Como poderia eu sozinho encarregar-me de vós e levar o fardo de vossas contendas?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4903, 154, 13, 'Escolhei, de cada uma de vossas tribos, homens sábios, prudentes e experimentados, que eu ponha à vossa frente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4904, 154, 14, 'Vós então me respondestes: ‘É uma boa coisa o que nos propões’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4905, 154, 15, 'Tomei, pois, dentre vós, homens sábios e experimentados que pus à vossa frente como chefes de milhares, de centenas, de cinquentenas e de dezenas, bem como escribas em vossas tribos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4906, 154, 16, 'Nesse mesmo tempo, dei esta ordem aos vossos juízes: ‘Dai audiência aos vossos irmãos e julgai com equidade as questões de cada um deles com o seu irmão ou com o estrangeiro que mora com ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4907, 154, 17, 'Não fareis distinção de pessoas em vossos julgamentos. Ouvireis o pequeno como o grande, sem temor de ninguém, porque o juízo é de Deus. Se uma questão vos parecer muito complicada, devereis apresentá-la a mim para que eu a ouça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4908, 154, 18, 'É assim que, naquele tem­po, vos ordenei tudo o que devíeis fazer’.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4909, 154, 19, '“Depois partimos de Horeb para atravessar esse vasto e terrível deserto que vistes, do monte dos amorreus, como nos havia ordenado o Senhor, nosso Deus. E chegamos a Cades Barne.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4910, 154, 20, 'Eu vos disse então: ‘Eis-vos chegados ao monte dos amorreus que o Senhor, nosso Deus, nos dá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4911, 154, 21, 'Vê: O Senhor, teu Deus, te entrega a terra. Subi e possuí-a, como o prometeu o Deus de teus pais. Não tenhas medo; não te assustes’.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4912, 154, 22, 'Vós vos aproximastes de mim e dissestes: ‘Enviemos homens adiante de nós, que explorem a terra e nos informem por que caminho devemos subir e para que cidades devemos ir’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4913, 154, 23, 'Vosso parecer agradou-me, e escolhi dentre vós doze homens, um de cada tribo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4914, 154, 24, 'Eles partiram, subiram as montanhas e chegaram ao vale de Escol, explorando a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4915, 154, 25, 'Tomaram consigo frutos da terra e no-los trouxeram dizendo: ‘A terra, que nos dá o Senhor, nosso Deus, é boa’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4916, 154, 26, 'Mas não quisestes subir a ela, e fostes rebeldes ao mandamento do Senhor, nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4917, 154, 27, 'E murmurastes em vossas tendas, dizendo: ‘O Senhor tem-nos ódio e, por isso, nos tirou da terra do Egito para entregar-nos ao extermínio pelas mãos dos amorreus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4918, 154, 28, 'Para onde iremos? Nossos irmãos fizeram-nos perder a coragem quando nos disseram ter visto um povo maior e de estatura mais alta que a nossa, e cidades grandes e fortificadas, cujos muros se elevavam até o céu, e vimos até mesmo filhos de Enacim por lá’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4919, 154, 29, 'Eu, porém, vos respondi: ‘Não vos assusteis nem tenhais medo deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4920, 154, 30, 'O Senhor, vosso Deus, que marcha diante de vós, combaterá ele mesmo em vosso lugar, como sempre o fez sob os vossos olhos, no Egito');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4921, 154, 31, 'e no deserto. No deserto, tu mesmo viste como o Senhor, teu Deus, te levou por todo caminho por onde andaste, como um homem costuma levar seu filho, até que chegásseis a esse lugar’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4922, 154, 32, 'E, apesar disso, não tivestes confiança no Senhor, vosso Deus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4923, 154, 33, 'o qual, procurando-vos um lugar onde acampar, marchava adiante de vós no caminho, de noite no fogo, para vos mostrar o caminho, e de dia na nuvem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4924, 154, 34, 'O Senhor, tendo ouvido o som de vossas palavras, encolerizou-se e fez este juramento:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4925, 154, 35, '‘Nenhum dos homens desta geração perversa verá a boa terra que eu, com juramento, prometi dar a vossos pais,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4926, 154, 36, 'exceto Caleb, filho de Jefoné. Este a verá, e eu darei a ele e a seus filhos o solo que ele pisou, porque cumpriu a vontade do Senhor’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4927, 154, 37, 'Até contra mim se irritou o Senhor por causa de vós: tu tampouco – disse-me ele – entrarás nessa terra!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4928, 154, 38, 'É Josué, filho de Nun, que ali entrará. Anima-o, pois é ele que introduzirá Israel na possessão da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4929, 154, 39, 'Vossos filhinhos, dos quais dissestes que seriam a presa do deserto, e vossos filhos, que hoje ainda não sabem distinguir o bem do mal, estes entrarão; a eles darei a terra e a possuirão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4930, 154, 40, 'Quanto a vós, voltai para trás e parti para o deserto na direção do mar Vermelho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4931, 154, 41, 'Vós respondestes-me: ‘Pecamos contra o Senhor. Vamos combater, como o Senhor, nosso Deus, nos ordenou’. E quando cada um de vós, tomando as suas armas, vos dispusestes inconsideradamente a marchar sobre o monte,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4932, 154, 42, 'o Senhor disse-me: ‘Dize-lhes: Não subais, não entreis em combate algum, porque não estou no meio de vós. Se o fizerdes, sereis vencidos por vossos inimigos’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4933, 154, 43, 'Em vão vos referi todas essas palavras: não me ouvistes e tivestes a presunção de subir ao monte, a despeito das ordens do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4934, 154, 44, 'Então os amorreus que habitavam nessa montanha saíram contra vós, perseguiram-vos como abelhas e retalharam-vos desde Seir até Horma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4935, 154, 45, 'Voltando, chorastes diante do Senhor, mas o Senhor não ouviu os vossos clamores, nem vos inclinou os seus ouvidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4936, 154, 46, 'Por isso é que ficastes tanto tempo em Cades, como o sabeis.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4937, 155, 1, '“Partindo dali, fomos para o deserto na direção do mar Vermelho, segundo a ordem do Senhor, e andamos muito tempo em torno do monte Seir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4938, 155, 2, 'O Senhor então me disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4939, 155, 3, '‘Basta de girar em volta deste monte; dirigi-vos para o norte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4940, 155, 4, 'Ordena ao povo: atravessareis o território de vossos irmãos, os filhos de Esaú, que habitam em Seir. Eles têm medo de vós;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4941, 155, 5, 'mas guardai-vos de entrar em luta contra eles, porque não vos darei nada de sua terra, nem mesmo a medida de um pé; é a Esaú que dei a propriedade das montanhas de Seir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4942, 155, 6, 'Comprarei deles a preço de dinheiro o necessário para alimentar-vos, e pagareis mesmo a água que beberdes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4943, 155, 7, 'porque o Senhor, teu Deus, te abençoou em todas as tuas empresas, e velou sobre ti durante a tua marcha através desse vasto deserto. Eis já quarenta anos que o Senhor, teu Deus, está contigo, e nada te faltou’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4944, 155, 8, 'Passamos, pois, ao longe de nossos irmãos, os filhos de Esaú, que habitam em Seir, evitando o caminho da planície, assim como Elat e Asiongaber. Voltamos e tomamos o caminho na direção do deserto de Moab.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4945, 155, 9, 'Então o Senhor me disse: ‘Não ataques os moabitas e não entres em guerra contra eles, porque não te darei nada de sua terra; foi aos filhos de Ló que dei Ar como herança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4946, 155, 10, 'Outrora habitavam os emim nessa terra. Era um povo grande, numeroso e de alta estatura, como os enacim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4947, 155, 11, 'Também eles eram considerados refaim, como os enacim; mas os moabitas chamavam-nos emim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4948, 155, 12, 'Em Seir habitavam também os horreus, os quais foram expulsos pelos filhos de Esaú. Mas os descendentes de Esaú, após os haverem exterminado, estabeleceram-se em seu lugar, como o fez Israel na terra que o Senhor lhe deu em possessão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4949, 155, 13, 'Vamos, pois! Passai a torrente de Zared. Passamos então a torrente de Zared’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4950, 155, 14, 'Durou trinta e oito anos essa nossa viagem de Cades Barne até a passagem da torrente de Zared. Entretanto, extinguiu-se do acampamento toda a geração dos ho­mens de guerra, assim como o Senhor o tinha jurado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4951, 155, 15, 'A mão do Senhor pesou sobre eles, e foram cortados do acampamento até a sua completa extinção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4952, 155, 16, 'Depois que todos esses homens de guerra desapareceram do meio de seu povo, levados pela morte,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4953, 155, 17, 'o Senhor disse-me:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4954, 155, 18, '‘Passarás hoje a fronteira de Moab, a região de Ar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4955, 155, 19, 'e te encontrarás em face dos amo­nitas. Não os ataques, nem lhes faças guerra, porque não te darei nada da sua terra; foi aos filhos de Ló que dei a possessão dessa terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4956, 155, 20, 'Também esta foi reputada terra dos refaim, chamados pelos amonitas de zanzomim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4957, 155, 21, 'povo grande, numeroso e de alta estatura como os enacim. Mas o Senhor exterminou-os dian­te dos amonitas, que os despojaram e habitaram em lugar deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4958, 155, 22, 'Foi o que o Senhor tinha feito pelos filhos de Esaú, que habitam em Seir, destruindo os hor­reus diante deles. Despojaram-nos e estabeleceram-se em seu lugar, onde estão ainda hoje.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4959, 155, 23, 'Da mesma sorte os heveus, que habitavam nas aldeias até Gaza, foram esmagados pelos caftorim, originários de Caftor, que se estabeleceram em seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4960, 155, 24, 'Vamos! Desarmai as tendas e passai a torrente do Arnon. Vê: entrego-te nas mãos Seon, rei de Hesebon, o amor­reu, com a sua terra. Começa a despojá-lo e faze-lhe guerra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4961, 155, 25, 'A partir de hoje, começarei a derramar o temor e o terror de teu nome entre os povos que habitam debaixo de todo o céu, de sorte que só ao ouvir o teu nome, eles tremerão e entrarão em pânico por causa de ti’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4962, 155, 26, 'Então enviei do deserto de Cademot mensageiros a Seon, rei de Hesebon, com palavras de paz, dizendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4963, 155, 27, '‘Deixa-me atravessar a tua terra. Seguirei pela estrada comum, e não me desviarei nem para a direita nem para a esquerda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4964, 155, 28, 'Tu me venderás por dinheiro o necessário para alimentar-me, e te pagarei mesmo a água que eu beber. Deixa-me somente passar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4965, 155, 29, '– como fizeram os filhos de Esaú que habitam em Seir, e os moabitas que habitam em Ar –, até que eu chegue ao Jordão e entre na terra que o Senhor, nosso Deus, nos dá’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4966, 155, 30, 'Mas Seon, rei de Hesebon, não consentiu que passássemos por sua terra; o Senhor, teu Deus, obcecara-lhe o espírito e endurecera-lhe o coração, a fim de entregá-lo em tuas mãos, como de fato aconteceu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4967, 155, 31, 'O Senhor disse-me então: ‘Vê, estou pronto a entregar-te imediatamente Seon com a sua terra. Empreende a conquista e ocupa-lhe o território’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4968, 155, 32, 'Seon saiu com todo o seu povo ao nosso encontro para pelejar contra nós em Jasa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4969, 155, 33, 'Mas o Senhor, nosso Deus, no-lo entregou e nós derrotamo-lo com os seus filhos e todo o seu povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4970, 155, 34, 'Tomamos-lhe então todas as suas cidades, que votamos ao interdito, com os homens, as mulheres e as crianças, sem deixar escapar ninguém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4971, 155, 35, 'Só nos reservamos os animais e o espólio das cidades conquistadas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4972, 155, 36, 'Desde Aroer, que está à margem da torrente do Arnon, e a cidade situada no vale, até Galaad, não houve lugar tão forte que nos pudesse resistir. O Senhor, nosso Deus, tudo nos entregou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4973, 155, 37, 'Somente não vos aproximastes da terra dos amonitas, nem de lugar algum situado às margens da torrente do Jaboc, nem das cidades da montanha, nem de nenhum dos lugares proibidos pelo Senhor, nosso Deus.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4974, 156, 1, '“Voltamo-nos, em seguida, para os lados de Basã, e Og, seu rei, saiu ao nosso encontro com todo o seu povo para nos combater em Edrai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4975, 156, 2, 'O Senhor disse-me: ‘Nada temas, porque eu o entreguei em tuas mãos, com todo o seu povo e sua terra. Trata-o como trataste Seon, rei dos amorreus, que habita em Hesebon’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4976, 156, 3, 'O Senhor, nosso Deus, entregou-nos também Og, rei de Basã, com todo o seu povo, e nós o derrotamos de tal sorte que nem um só dos seus escapou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4977, 156, 4, 'Tomamos então todas as suas cidades (não houve uma sequer que não caísse em nossas mãos), em número de sessenta, toda a região de Argob, o reino de Og, em Basã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4978, 156, 5, 'Todas essas cidades eram forti­ficadas, com altas muralhas, portas e ferrolhos, sem contar as numerosas cidades abertas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4979, 156, 6, 'Votamo-las ao interdito, como o tínhamos feito a Seon, rei de Hesebon, com os homens, as mulheres e as crianças.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4980, 156, 7, 'Mas reservamo-nos os animais e o espólio das cidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4981, 156, 8, 'Foi assim que tomamos naquele tempo, aos dois reis dos amorreus, o território que estava além do Jordão, desde a torrente do Arnon até a montanha do Hermon');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4982, 156, 9, '(os sidônios dão a Hermon o nome de Sarion e os amorreus, o de Sa­nir);');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4983, 156, 10, 'todas as cidades da planície, todo o Galaad e todo o Basã, até Salca e Edrai, cidades do reino de Og, em Basã;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4984, 156, 11, 'porque Og, rei de Basã, era o único que restava da raça dos refaim. Vê-se ainda o seu sarcófago, um sarcófago de basalto, em Rabá, cidade dos amonitas. Tem nove côvados de comprimento e quatro de largura, em côvados ordinários.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4985, 156, 12, 'Tomamos então posse dessa terra. Dei aos rubenitas e aos gaditas o território desde Aroer, que está no vale do Arnon, assim como a metade da montanha de Galaad, com suas cidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4986, 156, 13, 'Dei à meia tribo de Manassés o resto de Galaad e todo o Basã, reino de Og: toda a região de Argob, com todo o Basã; e o que se chama a terra dos refaim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4987, 156, 14, 'A Jair, filho de Manassés, coube toda a região de Argob até a fronteira dos ges­sureus e dos macateus, e ele deu o seu no­me às aldeias de Basã, chamadas ainda hoje aldeias de Jair.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4988, 156, 15, 'A Maquir dei Galaad.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4989, 156, 16, 'Dei aos rubenitas e aos gaditas a terra que se estende desde Galaad até a torrente do Arnon, servindo de limite o meio do vale, e depois até a torrente de Jaboc, fronteira dos amonitas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4990, 156, 17, 'e, enfim, a planície do Jordão, desde Genesaré até o mar da planície, o mar Salgado, ao pé das encostas do Fasga, para o oriente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4991, 156, 18, 'Naquele tempo, dei-vos esta ordem: ‘O Senhor, vosso Deus, deu-vos esta terra em herança. Vós, pois, homens valentes, tomareis vossas armas e marchareis à frente de vossos irmãos, os israelitas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4992, 156, 19, 'Somente vossas mulheres, com vossos filhos e vossos animais (sei que tendes muitos animais) ficarão nas cidades que vos dei,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4993, 156, 20, 'até que o Senhor tenha assegurado o descanso de vossos irmãos, como o vosso, e tenham por sua vez tomado posse da terra que o Senhor, vosso Deus, lhes dá do outro lado do Jordão. Então cada um voltará à possessão que lhe dei’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4994, 156, 21, 'Ao mesmo tempo, dei a Josué a seguinte ordem: ‘Viste com os teus olhos tudo o que o Senhor, vosso Deus, fez a esses dois reis’. Desse modo, tratará o Senhor todos os reinos que atravessares.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4995, 156, 22, 'Não os temas, porque é o Senhor, vosso Deus, quem combaterá por vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4996, 156, 23, 'Entrementes, roguei ao Senhor, dizendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4997, 156, 24, '‘Senhor Javé, começastes a mostrar ao vosso servo vossa grandeza e o poder de vossa mão. Qual é, nos céus ou na terra, o deus que pode igualar-se a vós em obras e grandes feitos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4998, 156, 25, 'Ah, se eu pudesse, também eu, passar e ver essa boa terra além do Jordão, essa bela montanha, e o Líbano!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (4999, 156, 26, 'Mas o Senhor irou-se contra mim por causa de vós, e não me ouviu. Disse-me: ‘Basta! Não me fales mais em tal coisa!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (5000, 156, 27, 'Sobe ao cimo do Fasga, lança teus olhos para o ocidente e para o norte, para o meio-dia e para o oriente, e contempla com os teus olhos a região; mas tu não passarás o Jordão.');
