INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14001, 503, 1, 'Eu havia feito um pacto com os meus olhos, para não desejar nunca olhar para uma virgem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14002, 503, 2, 'Que parte me daria Deus lá do alto, que sorte o Todo-poderoso me enviaria do céu?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14003, 503, 3, 'Acaso a infelicidade não está reservada ao injusto e o infortúnio ao iníquo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14004, 503, 4, 'Não conhece Deus os meus caminhos e não conta todos os meus passos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14005, 503, 5, 'Se caminhei com a mentira e meu pé correu atrás da fraude,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14006, 503, 6, 'que Deus me pese na balança da justiça e reconhecerá a minha integridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14007, 503, 7, 'Se meus passos se desviaram do caminho e meu coração seguiu meus olhos, e se às minhas mãos se apegou qualquer mácula,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14008, 503, 8, 'que semeie eu e outro o coma, e minhas plantações sejam desenraizadas!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14009, 503, 9, 'Se meu coração foi seduzido por uma mulher, se fiquei à espreita à porta de meu vizinho,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14010, 503, 10, 'que minha mulher gire a mó para um outro e que estranhos a possuam!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14011, 503, 11, 'Pois isso seria um crime, um delito digno de julgamento,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14012, 503, 12, 'um fogo que devoraria até o abismo e que teria arruinado todos os meus bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14013, 503, 13, 'Nunca violei o direito de meu escravo ou de minha serva, em suas discussões comigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14014, 503, 14, 'Que farei eu quando Deus se levantar? Quando me interrogar, que lhe responderei?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14015, 503, 15, 'Aquele que me criou no ventre, não o criou também a ele? Um mesmo criador nos formou!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14016, 503, 16, 'Acaso recusei aos pobres aquilo que desejavam e fiz desfalecer os olhos da viúva?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14017, 503, 17, 'Ou comi sozinho meu pedaço de pão, sem que o órfão tivesse a sua parte?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14018, 503, 18, 'Antes, desde minha infância cuidei-o como um pai e desde o ventre materno fui o seu guia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14019, 503, 19, 'Se vi perecer um homem por falta de roupa e um pobre que não tinha com que cobrir-se,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14020, 503, 20, 'sem que seus rins me tenham abençoado, aquecido como estava com a lã de minhas ovelhas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14021, 503, 21, 'se levantei a mão contra o órfão, quando me via apoiado pelos juízes,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14022, 503, 22, 'que meu ombro caia de minhas costas e meu braço seja arrancado de seu cotovelo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14023, 503, 23, 'Pois o terror de Deus me invadiu e diante de sua majestade não posso subsistir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14024, 503, 24, 'Nunca pus no ouro minha segurança e jamais disse ao ouro puro: ‘És minha esperança!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14025, 503, 25, 'Nunca me rejubilei por ser grande a minha riqueza, nem pelo fato de minha mão ter ajuntado muito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14026, 503, 26, 'Quando via o sol brilhar e a lua levantar-se em seu esplendor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14027, 503, 27, 'jamais meu coração deixou-se seduzir em segredo e minha mão não foi levada à boca para um beijo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14028, 503, 28, 'Isso seria um crime digno de castigo, pois eu teria renegado o Deus que está no alto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14029, 503, 29, 'Nunca me alegrei com a ruína de meu inimigo, nem exultei quando a infelicidade o feriu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14030, 503, 30, 'Não permiti que minha boca pecasse, reclamando sua morte por uma imprecação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14031, 503, 31, 'Jamais as pessoas de minha tenda me disseram: ‘Há alguém que não tenha ficado satisfeito da carne?’.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14032, 503, 32, 'O estrangeiro não passava a noite fora, eu abria a minha porta ao viajante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14033, 503, 33, 'Nunca dissimulei minha culpa aos homens, escondendo em meu peito minha iniquidade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14034, 503, 34, 'como se temesse a multidão e receasse o desprezo das famílias, a ponto de me manter quieto sem pôr o pé fora da porta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14035, 503, 35, 'Oh! Se eu tivesse alguém para me ouvir! Eis a minha assinatura: que o Todo-poderoso me responda! Que o meu adversário escreva também um memorial.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14036, 503, 36, 'Por certo eu o carregaria sobre meus ombros e cingiria minha fronte com ele como de uma coroa!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14037, 503, 37, 'Eu lhe prestaria contas de todos os meus passos e me apresentaria diante dele altivo como um príncipe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14038, 503, 38, 'Se minha terra clamou contra mim e seus sulcos derramaram lágrimas,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14039, 503, 39, 'se comi seus frutos sem pagar, se afligi os seus donos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14040, 503, 40, 'que em vez de trigo nasçam espinhos e joio em vez de cevada!”. Aqui terminam os discursos de Jó.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14041, 504, 1, 'Como Jó persistisse em conside-rar-se como um justo, estes três homens desistiram de lhe responder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14042, 504, 2, 'Então, se inflamou a cólera de Eliú, filho de Baraquel, de Buz, da família de Ram. Sua cólera inflamou-se contra Jó, por este pretender justificar-se perante Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14043, 504, 3, 'Inflamou-se também contra seus três amigos, por não terem achado resposta conveniente, dando assim culpa a Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14044, 504, 4, 'Como fossem mais velhos do que ele, Eliú esperou enquanto falavam com Jó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14045, 504, 5, 'Mas, quando viu que não tinham mais nada para responder, encolerizou-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14046, 504, 6, 'Então Eliú, filho de Baraquel, de Buz, tomou a palavra nestes termos: “Sou jovem em anos, e vós sois anciãos, por isso minha timidez me impediu de manifestar-vos o meu saber.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14047, 504, 7, 'Dizia comigo: ‘A idade vai falar, os muitos anos farão conhecer a sabedoria’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14048, 504, 8, 'Mas é o espírito de Deus no homem, e um sopro do Todo-poderoso que dá a inteligência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14049, 504, 9, 'Não são os mais velhos que são sábios, nem os anciãos que discernem o que é justo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14050, 504, 10, 'Por isso, é que digo: ‘Escutai-me, vou mostrar-vos o que sei’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14051, 504, 11, 'Esperei enquanto faláveis, prestei atenção em vossos raciocínios. Enquanto discutíeis,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14052, 504, 12, 'segui-vos atentamente. Mas ninguém refutou a Jó, nem respondeu aos seus argumentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14053, 504, 13, 'E não digais: ‘Encontramos a sabedoria; foi Deus e não um homem quem nos instrui’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14054, 504, 14, 'Não foi a mim que dirigiu seus discursos, mas encontrarei outras respostas diferentes das vossas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14055, 504, 15, 'Ei-los calados, já não dizem mais nada; faltam-lhes as palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14056, 504, 16, 'Esperei que se calassem e cessassem de responder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14057, 504, 17, 'É a minha vez de responder e vou também mostrar o que sei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14058, 504, 18, 'Pois estou cheio de palavras, o espírito que está em meu peito me oprime.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14059, 504, 19, 'Meu peito é como vinho arrolhado, como um barril pronto para estourar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14060, 504, 20, 'Tenho de falar, isso me aliviará. Abrirei meus lábios para responder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14061, 504, 21, 'Não farei acepção de ninguém, nem adularei este ou aquele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14062, 504, 22, 'Pois não sei bajular, do contrário, meu Criador logo me levaria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14063, 505, 1, 'E agora, Jó, ouve as minhas palavras e atende a todos os meus discursos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14064, 505, 2, 'Eis que abro a minha boca. Minha língua, sob o céu da boca, vai falar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14065, 505, 3, 'Minhas palavras brotam de um coração reto e meus lábios falarão francamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14066, 505, 4, 'O espírito de Deus me criou e o sopro do Todo-poderoso me deu a vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14067, 505, 5, 'Se puderes, responde-me. Toma posição e fica firme diante de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14068, 505, 6, 'Em face de Deus somos iguais. Como tu, eu também fui formado do barro!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14069, 505, 7, 'Assim, meu temor não te assustará e o peso de minhas palavras não te acabrunhará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14070, 505, 8, 'Pois, disseste aos meus ouvidos, e ouvi estas palavras:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14071, 505, 9, '‘Sou puro, sem pecado; sou limpo, não há culpa em mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14072, 505, 10, 'É ele que inventa pretextos contra mim e considera-me seu inimigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14073, 505, 11, 'Prendeu meus pés no cepo e vigiou todos os meus passos’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14074, 505, 12, 'Responderei que nisto foste injusto, pois Deus é maior do que o ser humano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14075, 505, 13, 'Por que o acusas de não dar nenhuma resposta a teus discursos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14076, 505, 14, 'Ora, Deus fala de uma maneira e de outra e não prestas atenção.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14077, 505, 15, 'Por meio dos sonhos, das visões noturnas, quando o sono profundo cai sobre os homens, enquanto dormem nos seus leitos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14078, 505, 16, 'então abre os ouvidos dos mortais e os assusta com suas aparições.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14079, 505, 17, 'Isso para desviá-lo do pecado e livrá-lo do orgulho,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14080, 505, 18, 'para salvar-lhe a alma da cova e sua vida, da seta mortífera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14081, 505, 19, 'Pela dor também é corrigido o homem em seu leito, quando todos os seus membros são agitados,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14082, 505, 20, 'quando recebe o alimento com desgosto e já não pode suportar as iguarias mais deliciosas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14083, 505, 21, 'Sua carne se consome aos olhares e seus membros emagrecidos se desvanecem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14084, 505, 22, 'Sua alma aproxima-se da sepultura e sua vida, daqueles que estão mortos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14085, 505, 23, 'Se perto dele se encontrar um anjo, um intercessor entre mil, para ensinar-lhe o que deve fazer,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14086, 505, 24, 'ter piedade dele e dizer: ‘Poupai-o de descer à cova, pois recebi o resgate de sua vida’.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14087, 505, 25, 'Sua carne retomará o vigor da mocidade e ele retornará aos dias de sua adolescência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14088, 505, 26, 'Ele rezará a Deus, que lhe será propício, contemplará com alegria sua face e restituirá ao homem sua justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14089, 505, 27, 'Cantará diante dos homens, dizendo: ‘Pequei, violei o direito, mas Deus não me tratou conforme meus erros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14090, 505, 28, 'Poupou minha alma de descer à cova e minha alma bem viva goza a luz!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14091, 505, 29, 'Eis o que Deus faz duas e três vezes com o ser humano,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14092, 505, 30, 'a fim de tirar-lhe a alma da cova e iluminá-la com a luz da vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14093, 505, 31, 'Presta atenção, Jó, escuta-me, cala a boca para que eu fale!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14094, 505, 32, 'Se tens alguma coisa para dizer, responde-me; fala, eu gostaria de te dar razão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14095, 505, 33, 'Se não, escuta-me, cala-te, e eu te ensinarei a sabedoria”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14096, 506, 1, 'Eliú retomou a palavra nestes termos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14097, 506, 2, '“Sábios, ouvi meu discurso; eruditos, prestai atenção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14098, 506, 3, 'Pois o ouvido discerne o valor das palavras como o paladar saboreia as iguarias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14099, 506, 4, 'Procuremos escolher o que é justo e conhecer entre nós o que é bom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14100, 506, 5, 'Jó disse: ‘Eu sou inocente, mas Deus recusa fazer-me justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14101, 506, 6, 'A despeito de meu direito, passo por mentiroso; minha ferida é incurável, sem que eu tenha pecado’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14102, 506, 7, 'Existe um homem como Jó, que bebe a blasfêmia como quem bebe água,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14103, 506, 8, 'que anda de par com os ímpios e caminha com os perversos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14104, 506, 9, 'Pois ele disse: ‘O homem não ganha nada em ser agradável a Deus’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14105, 506, 10, 'Ouvi-me, pois, homens sensatos: longe de Deus a injustiça, longe do Todo-poderoso a iniquidade!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14106, 506, 11, 'Ele trata o homem conforme seus atos e dá a cada um o que merece.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14107, 506, 12, 'Pois, Deus não é injusto e o Todo-poderoso não falseia o direito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14108, 506, 13, 'Quem lhe confiou a administração da terra? Quem lhe entregou o universo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14109, 506, 14, 'Se lhe retomasse o sopro, se lhe retirasse o alento,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14110, 506, 15, 'toda a carne expiraria no mesmo instante, e o homem voltaria ao pó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14111, 506, 16, 'Se tens inteligência, escuta isto, e dá ouvidos ao som de minhas palavras!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14112, 506, 17, 'Acaso um inimigo do direito poderia governar? Pode o Justo, o Poderoso cometer a iniquidade?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14113, 506, 18, 'Ele que disse a um rei: ‘Malvado!’. Ou aos príncipes: ‘Celerados!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14114, 506, 19, 'Ele não tem preferência pelos grandes, nem tem mais consideração pelos ricos do que pelos pobres, pois são todos obras de suas mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14115, 506, 20, 'Subitamente, perecem no meio da noite; os povos vacilam e passam, o poderoso desaparece, sem o socorro de mão alguma.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14116, 506, 21, 'Pois Deus olha para a conduta de cada um e observa todos os seus passos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14117, 506, 22, 'Não há obscuridade, nem trevas onde o iníquo possa esconder-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14118, 506, 23, 'Pois não precisa olhar duas vezes para um homem para citá-lo em justiça consigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14119, 506, 24, 'Abate os poderosos sem inquérito e põe outros em lugar deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14120, 506, 25, 'Pois conhece as suas obras, derruba-os à noite e são esmagados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14121, 506, 26, 'Fere-os como ímpios no lugar onde são vistos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14122, 506, 27, 'porque se afastaram dele e não quiseram conhecer nenhum de seus caminhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14123, 506, 28, 'Fizeram chegar até Deus o clamor do pobre e tornando-o atento ao grito do infeliz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14124, 506, 29, 'Se ele dá a paz, quem poderá censurá-lo? Se oculta sua face, quem poderá contemplá-lo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14125, 506, 30, 'Assim trata ele o povo e o indivíduo de maneira que o ímpio não venha a reinar, e já não seja uma armadilha para o povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14126, 506, 31, 'Se alguém diz a Deus: ‘Fui seduzido, não mais pecarei,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14127, 506, 32, 'ensina-me o que ignoro; se cometi o mal, não mais o farei!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14128, 506, 33, 'Julgas, então, que ele deve punir, já que rejeitaste suas ordens? És tu quem deves escolher, não eu; dize, pois, o que sabes.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14129, 506, 34, 'As pessoas sensatas me dirão, como qualquer homem sábio que me ouve:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14130, 506, 35, '‘Jó não falou conforme a razão, falta-lhe bom senso às palavras!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14131, 506, 36, 'Pois bem, que Jó seja provado até o fim, já que suas respostas são próprias de um ímpio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14132, 506, 37, 'Porque a seus pecados acrescenta a revolta. Entre nós, com zombaria, bate as mãos e multiplica as palavras contra Deus”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14133, 507, 1, 'Eliú retomou ainda a palavra nestes termos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14134, 507, 2, '“Imaginas ter razão em pretender justificar-te contra Deus?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14135, 507, 3, 'Quando dizes: ‘Para que me serve isto, qual é minha vantagem em não pecar?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14136, 507, 4, 'Pois vou responder-te, a ti e a teus amigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14137, 507, 5, 'Contempla os céus e observa as nuvens: vê como são mais altas do que tu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14138, 507, 6, 'Se pecas, que danos lhe causas? Se multiplicas tuas faltas, que mal lhe fazes?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14139, 507, 7, 'Se és justo, que vantagem lhe dás? Ou que recebe ele de tua mão?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14140, 507, 8, 'Tua maldade só prejudica o homem, teu semelhante; tua justiça só diz respeito a um ser humano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14141, 507, 9, 'Sob o peso da opressão, geme-se, e clama-se sob a mão dos poderosos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14142, 507, 10, 'Mas ninguém diz: ‘Onde está Deus, meu Criador, que inspira cantos de louvor em plena noite,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14143, 507, 11, 'que nos instrui mais do que aos animais selvagens e nos torna mais sábios do que as aves do céu?’.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14144, 507, 12, 'Clamam, mas não são ouvidos, por causa do orgulho dos maus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14145, 507, 13, 'Por certo, Deus não ouve palavras frívolas, e o Todo-poderoso não lhes presta atenção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14146, 507, 14, 'Quando dizes que ele não se ocupa de ti, que tua causa está diante dele e que esperas sua decisão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14147, 507, 15, 'que sua cólera não castiga e que ele ignora o pecado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14148, 507, 16, 'Jó abre a boca para palavras ociosas e derrama-se em discursos impertinentes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14149, 508, 1, 'Depois Eliú prosseguiu nestes termos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14150, 508, 2, '“Espera um pouco e te instruirei. Tenho ainda palavras em defesa de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14151, 508, 3, 'Vou buscar longe a minha ciência, para justificar aquele que me criou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14152, 508, 4, 'Pois minhas palavras não são certamente mentirosas e estás tratando com um homem de ciência sólida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14153, 508, 5, 'Deus é poderoso, mas não é arrogante, é poderoso por sua ciência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14154, 508, 6, 'Não deixa o ímpio viver, mas faz justiça aos oprimidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14155, 508, 7, 'Não tira seus olhos do justo e os faz assentar no trono com os reis, numa glória eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14156, 508, 8, 'Se forem presos em grilhões e atados com os laços da pobreza,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14157, 508, 9, 'ele lhes fará conhecer as suas obras e as faltas que cometeram por orgulho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14158, 508, 10, 'Abre-lhes os ouvidos para corrigi-los e diz-lhes que renunciem à iniquidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14159, 508, 11, 'Se escutarem e obedecerem, terminarão seus dias na felicidade e seus anos em delícias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14160, 508, 12, 'Mas se não o escutarem, morrerão de um golpe e expirarão por falta de sabedoria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14161, 508, 13, 'Os ímpios de coração são entregues à cólera e não clamam a Deus quando ele os aprisiona.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14162, 508, 14, 'Por isso morrem em plena mocidade e sua vida passa como a dos efeminados.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14163, 508, 15, 'Mas Deus salvará o pobre pela sua miséria e o instrui pelo sofrimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14164, 508, 16, 'A ti também ele retirará das fauces a angústia, numa larga liberdade e no repouso de uma mesa bem guarnecida.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14165, 508, 17, 'Mas tu te comportas como um malvado, com o risco de incorrer em sentença e penalidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14166, 508, 18, 'Toma cuidado para que a cólera não te inflija um castigo e que o tamanho do resgate não te perca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14167, 508, 19, 'Acaso levará ele em conta teu grito na aflição e todos os esforços do vigor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14168, 508, 20, 'Não suspires pela noite da morte, que arrebata os povos de seu lugar!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14169, 508, 21, 'Guarda-te de declinar para a iniquidade, e de preferir a injustiça ao sofrimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14170, 508, 22, 'Vê, Deus é sublime em seu poder! Que senhor lhe é comparável?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14171, 508, 23, 'Quem lhe fixou seus caminhos? Quem pode dizer-lhe: ‘Fizeste mal?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14172, 508, 24, 'Antes lembra-te de glorificar sua obra, que a humanidade celebra em seus cânticos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14173, 508, 25, 'Todos os homens a contemplam, mas cada um a considera de longe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14174, 508, 26, 'Deus é grande demais para que o possamos conhecer; o número de seus anos é incalculável.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14175, 508, 27, 'Atrai as gotinhas de água para transformá-las em chuva no nevoeiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14176, 508, 28, 'As nuvens espalham essas águas e as destilam sobre a multidão humana.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14177, 508, 29, 'Quem pode compreender como se expandem as nuvens e o estrépito que sai de sua tenda?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14178, 508, 30, 'Espalha à sua volta sua luz e encobre as profundezas do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14179, 508, 31, 'É por esse meio que governa os povos e fornece-lhes abundante alimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14180, 508, 32, 'Nas suas mãos esconde o raio e fixa-lhe o alvo a atingir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14181, 508, 33, 'O seu estrondo o anuncia e o rebanho também pressente aquele que se aproxima.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14182, 509, 1, 'Por isso, tremeu o meu coração e saltou fora de seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14183, 509, 2, 'Escutai, escutai o brado de sua voz e o estrondo que sai da sua boca!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14184, 509, 3, 'Enche dele toda a extensão do céu e seus relâmpagos atingem os confins da terra!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14185, 509, 4, 'Por detrás dele ruge uma voz e troveja com sua voz majestosa. Não retém mais seus raios quando se ouve sua voz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14186, 509, 5, 'Deus troveja com sua voz maravilhosa, faz prodígios que não compreendemos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14187, 509, 6, 'Diz à neve: ‘Cai sobre a terra!’. E às pancadas de chuva: ‘Sede fortes!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14188, 509, 7, 'Ele põe selos sobre as mãos dos homens, a fim de que todos os mortais reconheçam seu criador.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14189, 509, 8, 'A fera também entra em seu covil e encolhe-se em sua toca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14190, 509, 9, 'O furacão sai da câmara do sul e do norte chega o frio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14191, 509, 10, 'Ao sopro de Deus forma-se o gelo e a superfície das águas se congela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14192, 509, 11, 'Carrega as nuvens de vapor. As nuvens lançam por toda parte seus relâmpagos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14193, 509, 12, 'que vão em todos os sentidos sob sua direção, para realizar tudo quanto ele ordena na face da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14194, 509, 13, 'Ora é o castigo que eles trazem, ora seus benefícios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14195, 509, 14, 'Escuta isto, Jó! Para e considera as maravilhas de Deus!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14196, 509, 15, 'Sabes como Deus as opera e faz brilhar o relâmpago de sua nuvem?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14197, 509, 16, 'Conheces a lei do equilíbrio das nuvens e o milagre daquele cuja ciência é infinita?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14198, 509, 17, 'Por que são quentes as tuas vestes, quando repousa a terra ao sopro do meio-dia?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14199, 509, 18, 'Saberás, como ele, estender as nuvens e torná-las sólidas como um espelho de metal fundido?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14200, 509, 19, 'Dá-me a conhecer o que lhe diremos. Mergulhados em nossas trevas, só sabemos objetar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14201, 509, 20, 'Quem lhe repetirá o que digo? Acaso pedirá um homem a sua própria perdição?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14202, 509, 21, 'Agora já não se vê a luz, o sol brilha através das nuvens. Passa, porém, um vento e as varre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14203, 509, 22, 'A luz vem do norte. Deus está envolto numa majestade temível.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14204, 509, 23, 'Não podemos alcançar o Todo-poderoso. Ele é eminente em força e em equidade; grande na justiça, ele não tem a dar contas a ninguém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14205, 509, 24, 'Que os homens, pois, o reverenciem! Ele não olha aqueles que se julgam sábios!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14206, 510, 1, 'Então, do seio da tempestade, o Senhor deu a Jó esta resposta:*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14207, 510, 2, '“Quem é este que obscurece a Providência com discursos sem sentido?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14208, 510, 3, 'Cinge os teus rins como um valente! Vou interrogar-te e tu me responderás.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14209, 510, 4, 'Onde estavas, quando lancei os fundamentos da terra? Fala, se estiveres informado disso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14210, 510, 5, 'Quem lhe deu as medidas, já que o sabes? Ou quem sobre ela estendeu o cordel?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14211, 510, 6, 'Onde se assentam suas bases? Ou quem colocou nela a pedra angular,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14212, 510, 7, 'sob os alegres concertos dos astros da manhã e sob as aclamações de todos os filhos de Deus?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14213, 510, 8, 'Quem fechou com portas o mar, quando brotou do seio materno,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14214, 510, 9, 'quando lhe dei as nuvens por vestimenta e o enfaixava com névoas tenebrosas?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14215, 510, 10, 'Eu lhe tracei limites e lhe pus portas e ferrolhos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14216, 510, 11, 'dizendo: ‘Chegarás até aqui e não irás mais longe; aqui se deterá o orgulho de tuas ondas?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14217, 510, 12, 'Algum dia na vida deste ordens à manhã, ou indicaste à aurora o seu lugar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14218, 510, 13, 'para que ela alcançasse as extremidades da terra e dela sacudisse os ímpios?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14219, 510, 14, 'A terra se molda como a argila sob o sinete e toma cor como um vestido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14220, 510, 15, 'Aos ímpios, contudo, é recusada sua luz e se rompe o braço ameaçador.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14221, 510, 16, 'Acaso chegaste até as fontes do mar ou passaste até o fundo do abismo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14222, 510, 17, 'Apareceram-te, porventura, as portas da morte, ou viste a entrada da morada tenebrosa?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14223, 510, 18, 'Tens ideia da extensão da terra? Fala, se sabes tudo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14224, 510, 19, 'Onde está o caminho para a morada da luz? Quanto às trevas, onde é o seu lugar?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14225, 510, 20, 'Poderias alcançá-las em seu domínio e reconhecer as veredas de sua morada?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14226, 510, 21, 'Deverias sabê-lo, pois já tinhas nascido e são numerosos os teus dias!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14227, 510, 22, 'Entraste nos depósitos da neve ou visitaste os armazéns dos granizos');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14228, 510, 23, 'que reservo para os tempos de tormento, para os dias de luta e de batalha?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14229, 510, 24, 'Por que caminho se espalha o nevoeiro e se expande o vento do oriente sobre a terra?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14230, 510, 25, 'Quem abre um canal para o aguaceiro e uma rota para os relâmpagos dos trovões,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14231, 510, 26, 'para fazer chover sobre uma terra desabitada e sobre um deserto sem seres humanos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14232, 510, 27, 'para regar regiões vastas e desoladas, para nelas fazer germinar a erva verdejante?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14233, 510, 28, 'Terá a chuva um pai? Quem gera as gotas do orvalho?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14234, 510, 29, 'De que seio sai o gelo e quem engendra a geada do céu?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14235, 510, 30, 'As águas se endurecem como pedra e a superfície do abismo se congela!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14236, 510, 31, 'És tu que atas os laços das Plêiades ou desatas as correntes do Órion?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14237, 510, 32, 'És tu que fazes sair a seu tempo as constelações ou conduzes a Ursa com seus filhos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14238, 510, 33, 'Conheces as leis do céu e regulas sua influência sobre a terra?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14239, 510, 34, 'Levantarás a tua voz até as nuvens e o dilúvio te obedecerá?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14240, 510, 35, 'Tua ordem fará os relâmpagos surgirem e te dirão: ‘Aqui estamos?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14241, 510, 36, 'Quem pôs sabedoria nas nuvens e inteligência no meteoro?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14242, 510, 37, 'Quem pode enumerar com sabedoria as nuvens e inclinar as odres do céu,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14243, 510, 38, 'para que a poeira se transforme em massa compacta e os seus torrões se aglomerem?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14244, 510, 39, 'És tu que caças a presa para a leoa ou satisfazes a fome dos leõezinhos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14245, 510, 40, 'quando estão deitados em seus covis ou quando se emboscam nas covas?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14246, 510, 41, 'Quem prepara ao corvo o seu alimento, quando seus filhotes gritam a Deus, quando andam de um lado para outro por não terem o que comer?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14247, 511, 1, 'Sabes o tempo em que as cabras monteses dão cria nos rochedos? Observaste o parto das corças?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14248, 511, 2, 'Contaste os meses de sua gravidez e sabes o tempo de seu parto?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14249, 511, 3, 'Elas se agacham, dão cria e se livram de suas dores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14250, 511, 4, 'Seus filhotes tornam-se fortes e crescem nos campos, apartam-se delas e não voltam mais a elas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14251, 511, 5, 'Quem pôs o jumento selvagem em liberdade e quem rompeu os laços do asno veloz?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14252, 511, 6, 'Dei-lhe o deserto por morada e a planície salgada como lugar de habitação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14253, 511, 7, 'Ele se ri do tumulto da cidade e não escuta os gritos do tropeiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14254, 511, 8, 'Explora as montanhas da sua pastagem e nela anda buscando tudo o que é verde.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14255, 511, 9, 'Quererá servir-te o boi selvagem ou passará a noite em teu estábulo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14256, 511, 10, 'Podes prendê-lo com uma corda em seu pescoço ou fenderá ele atrás de ti os teus sulcos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14257, 511, 11, 'Fiarás nele porque sua força é grande e lhe deixarás a seu cuidado o teu trabalho?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14258, 511, 12, 'Confiarás nele para que te traga para a casa o que semeaste e que te encha a tua eira?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14259, 511, 13, 'O avestruz bate as asas alegremente, não tem asas nem penas de bondade?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14260, 511, 14, 'Abandona os seus ovos na terra e os deixa aquecer no solo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14261, 511, 15, 'esquecendo-se que um pé poderá esmagá-los ou que animais selvagens poderão pisá-los.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14262, 511, 16, 'É cruel com seus filhotes, como se não fossem seus e não se incomoda de ter sofrido em vão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14263, 511, 17, 'Pois Deus lhe negou sabedoria e não lhe concedeu inteligência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14264, 511, 18, 'Mas, quando alça voo, ri-se do cavalo e do cavaleiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14265, 511, 19, 'És tu que dás vigor ao cavalo e foste tu que enfeitaste seu pescoço com uma crina ondulante?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14266, 511, 20, 'Que o fazes saltar como um gafanhoto, relinchando terrivelmente?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14267, 511, 21, 'Orgulhoso de sua força, escava a terra com a pata e atira-se à frente das armas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14268, 511, 22, 'Ri-se do medo, nada o assusta e não recua diante da espada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14269, 511, 23, 'Sobre ele ressoam a aljava, o ferro brilhante da lança e o dardo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14270, 511, 24, 'Tremendo de impaciência, devora o espaço e o som da trombeta não o deixa no lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14271, 511, 25, 'Ao sinal do clarim, diz: ‘Vamos!’. De longe fareja a batalha, a voz troante dos chefes e o alarido dos guerreiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14272, 511, 26, 'É graças à tua sabedoria que o falcão alça voo e desdobra as suas asas para o sul?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14273, 511, 27, 'É por tua ordem que a águia levanta voo e faz seu ninho nas alturas?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14274, 511, 28, 'Ela habita nos rochedos e neles passa a noite, sobre a ponta rochosa e o cimo escarpado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14275, 511, 29, 'De lá espia sua presa, pois seus olhos penetram as distâncias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14276, 511, 30, 'Seus filhotes se alimentam de sangue e onde quer que haja cadáveres, ali está ela”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14277, 512, 1, 'O Senhor, dirigindo-se a Jó, lhe disse:*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14278, 512, 2, '“Aquele que disputa com o Todo-poderoso apresente suas críticas! Aquele que discute com Deus responda!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14279, 512, 3, 'Jó respondeu ao Senhor nestes termos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14280, 512, 4, '“Leviano como sou, que posso responder-te? Ponho a minha mão sobre a boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14281, 512, 5, 'Falei uma vez e não repetirei, duas vezes, e nada acrescentarei”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14282, 512, 6, 'Então, do meio da tempestade, o Senhor deu a Jó esta resposta:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14283, 512, 7, '“Cinge os teus rins como um valente; vou interrogar-te e tu me responderás.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14284, 512, 8, 'Queres reduzir a nada a minha justiça e condenar-me antes de ter razão?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14285, 512, 9, 'Acaso tens um braço semelhante ao de Deus e uma voz troante como a dele?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14286, 512, 10, 'Orna-te então de grandeza e majestade, reveste-te de esplendor e de glória!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14287, 512, 11, 'Espalha as ondas de tua cólera. Com um simples olhar, abate o arrogante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14288, 512, 12, 'Com um olhar, humilha o soberbo e esmaga os ímpios no mesmo lugar em que eles estão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14289, 512, 13, 'Enterra-os todos juntos debaixo da terra e amarra-lhes os rostos num lugar escondido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14290, 512, 14, 'Então, eu também te louvarei por triunfares pela força de tua mão direita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14291, 512, 15, 'Vê Beemot, que criei contigo, que nutre-se de erva como o boi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14292, 512, 16, 'Sua força reside nos rins e seu vigor nos músculos do ventre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14293, 512, 17, 'Levanta sua cauda como um cedro. Os nervos de suas coxas são entrelaçados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14294, 512, 18, 'Seus ossos são como tubos de bronze e sua carcaça como barras de ferro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14295, 512, 19, 'É obra-prima de Deus, foi criado como o soberano de seus companheiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14296, 512, 20, 'As montanhas fornecem-lhe a pastagem e todos os animais dos campos divertem-se em volta dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14297, 512, 21, 'Deita-se sob os lótus, no esconderijo dos caniços e dos brejos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14298, 512, 22, 'Os lótus cobrem-no com sua sombra e os salgueiros da margem o cercam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14299, 512, 23, 'Quando o rio transborda, ele não se assusta; mesmo que o Jordão levantasse até a sua boca, ele ficaria tranquilo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14300, 512, 24, 'Quem o seguraria pela frente e lhe furaria as ventas para nelas passar cordas?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14301, 512, 25, 'Poderás tu fisgar Leviatã com um anzol e amarrar-lhe a língua com uma corda?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14302, 512, 26, 'Serás capaz de passar-lhe um junco em suas ventas e de furar-lhe a mandíbula com um gancho?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14303, 512, 27, 'Ele te fará muitas súplicas e te dirigirá palavras ternas?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14304, 512, 28, 'Concluirá ele uma aliança contigo, a fim de que faças dele sempre teu escravo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14305, 512, 29, 'Brincarás com ele como se fosse um pássaro, ou o prenderás com a coleira, para divertir teus filhos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14306, 512, 30, 'Será ele vendido por uma sociedade de pescadores e dividido entre os negociantes?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14307, 512, 31, 'Poderás crivar-lhe a pele com dardos, ou a cabeça com arpões de pesca?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14308, 512, 32, 'Tenta pôr a mão sobre ele, sempre te lembrarás disso e não recomeçarás.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14309, 512, 33, 'Tua esperança será lograda: bastaria a sua vista para te arrasar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14310, 513, 1, 'Ninguém é bastante ousado para provocá-lo. Quem lhe resistiria face a face?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14311, 513, 2, 'Quem pôde afrontá-lo e sair com vida? Quem, debaixo de toda a extensão do céu?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14312, 513, 3, 'Não quero calar a glória de seus membros e falarei de seu vigor incomparável.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14313, 513, 4, 'Quem levantou a dianteira de sua couraça? Quem penetrou na dupla linha de sua dentadura?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14314, 513, 5, 'Quem lhe abriu os dois batentes da goela? Em torno dos seus dentes, só terror!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14315, 513, 6, 'Sua costa é um aglomerado de escudos, cujas juntas são estreitamente ligadas:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14316, 513, 7, 'uma encaixa na outra, nem sequer o ar passa por entre elas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14317, 513, 8, 'uma adere tão bem à outra, que são encaixadas sem se poderem desunir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14318, 513, 9, 'Seu espirro faz jorrar a luz e seus olhos são como as pálpebras da aurora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14319, 513, 10, 'De sua goela saem chamas e escapam centelhas ardentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14320, 513, 11, 'De suas ventas sai fumaça como de uma panela que ferve entre chamas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14321, 513, 12, 'Seu hálito queima como brasa e a chama jorra de sua goela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14322, 513, 13, 'Em seu pescoço reside sua força, diante dele salta o espanto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14323, 513, 14, 'As dobras de seus músculos são aderentes, esticadas sobre ele, elas são inabaláveis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14324, 513, 15, 'Firme como a pedra é seu coração, firme como a mó fixa de um moinho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14325, 513, 16, 'Quando se levanta, estremecem as ondas e os vagalhões do mar se afastam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14326, 513, 17, 'Se uma espada o atinge, ela não resiste, nem a lança, nem a flecha, nem o dardo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14327, 513, 18, 'O ferro para ele é como palha e o bronze, como madeira podre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14328, 513, 19, 'A flecha não o afugenta, as pedras de funda são palhinhas para ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14329, 513, 20, 'O martelo lhe parece um fiapo de palha e ri-se do assobio da espada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14330, 513, 21, 'Sob seu ventre há cacos pontiagudos, como uma grade de ferro que se arrasta sobre o lodo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14331, 513, 22, 'Faz ferver o abismo como uma caldeira e transforma o mar num queimador de perfumes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14332, 513, 23, 'Deixa atrás de si um sulco luminoso, como se o abismo tivesse cabeleira branca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14333, 513, 24, 'Não há nada igual a ele na terra, pois foi feito para não ter medo de nada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14334, 513, 25, 'Ele afronta tudo o que é elevado. Ele é o rei dos mais orgulhosos animais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14335, 514, 1, 'Jó respondeu ao Senhor nestes termos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14336, 514, 2, '“Sei que podes tudo e que nada te é impossível.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14337, 514, 3, '‘Quem é esse que obscurece assim a Providência com discursos ininteligíveis?’ É por isso que falei, sem compreendê-las, maravilhas que me superam e que não conheço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14338, 514, 4, '‘Escuta-me, deixa-me falar, vou interrogar-te e tu me responderás.’');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14339, 514, 5, 'Meus ouvidos ouviram falar de ti, mas agora meus próprios olhos te viram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14340, 514, 6, 'É por isso que me retrato e me arrependo, no pó e na cinza”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14341, 514, 7, 'Depois que o Senhor acabou de dirigir essas palavras a Jó, disse a Elifaz de Temã: “Estou irado contra ti e contra teus dois amigos, porque não falastes corretamente de mim, como Jó, meu servo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14342, 514, 8, 'Tomai, pois, sete touros e sete carneiros e vinde ter com meu servo Jó. Oferecei-os por vós em holocausto e meu servo Jó intercederá por vós. É em consideração a ele que não vos infligirei ignomínias por não terdes falado bem de mim, como Jó, meu servo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14343, 514, 9, 'Elifaz de Temã, Baldad de Suás e Sofar de Naamat foram-se então para fazer como o Senhor lhes tinha ordenado, e o Senhor tomou em consideração as orações de Jó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14344, 514, 10, 'Enquanto Jó rezava por seus amigos, o Senhor o restabeleceu de novo em seu primeiro estado e lhe tornou em dobro tudo quanto tinha possuído.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14345, 514, 11, 'Todos os seus irmãos, todas as suas irmãs, todos os seus amigos de antes vieram visitá-lo e sentaram-se com ele à mesa em sua casa. Tiveram muito dó dele e deram-lhe condolências a respeito de todas as infelicidades que o Senhor lhe enviara. E cada um deles ofereceu-lhe uma moeda de prata e um anel de ouro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14346, 514, 12, 'O Senhor abençoou os últimos tempos de Jó mais do que os primeiros. Teve Jó catorze mil ovelhas, seis mil camelos, mil juntas de bois e mil jumentas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14347, 514, 13, 'Teve ainda sete filhos e três filhas:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14348, 514, 14, 'chamou a primeira Pombinha, a segunda Cássia e a terceira Azeviche.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14349, 514, 15, 'Em toda aquela terra não poderiam ser encontradas mulheres mais belas do que as filhas de Jó. E seu pai lhes destinou uma parte da herança entre seus irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14350, 514, 16, 'Depois disso, Jó viveu ainda cento e quarenta anos e conheceu até a quarta geração dos filhos de seus filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14351, 514, 17, 'Depois, velho e cheio de dias, morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14352, 515, 1, 'Feliz o homem que não procede conforme o conselho dos ímpios, não trilha o caminho dos pecadores, nem se assenta entre os escarnecedores.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14353, 515, 2, 'Feliz aquele que se compraz no serviço do Senhor e medita sua lei dia e noite.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14354, 515, 3, 'Ele é como a árvore plantada na margem das águas correntes: dá fruto na época própria, sua folhagem não murchará jamais. Tudo o que empreende, prospera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14355, 515, 4, 'Os ímpios não são assim! Mas são como a palha que o vento leva.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14356, 515, 5, 'Por isso não suportarão o juízo, nem permanecerão os pecadores na assembleia dos justos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14357, 515, 6, 'Porque o Senhor vela pelo caminho dos justos, ao passo que o dos ímpios leva à perdição.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14358, 516, 1, 'Por que tumultuam as nações? Por que tramam os povos vãs conspirações?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14359, 516, 2, 'Erguem-se, juntos, os reis da terra, e os príncipes se unem para conspirar contra o Senhor e contra seu Cristo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14360, 516, 3, '“Quebremos seu jugo – disseram eles – e sacudamos para longe de nós as suas cadeias!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14361, 516, 4, 'Aquele, porém, que mora nos céus, se ri, o Senhor os reduz ao ridículo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14362, 516, 5, 'Dirigindo-se a eles em cólera, ele os aterra com o seu furor:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14363, 516, 6, '“Sou eu – diz – quem me sagrei um rei em Sião, minha montanha santa”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14364, 516, 7, 'Vou publicar o decreto do Senhor. Disse-me o Senhor: “Tu és meu filho, eu hoje te gerei.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14365, 516, 8, 'Pede-me; te darei por herança todas as nações; tu possuirás os confins do mundo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14366, 516, 9, 'Tu as governarás com cetro de ferro, tu as pulverizarás como um vaso de argila”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14367, 516, 10, 'Agora, ó reis, compreendei isso; instruí-vos, ó juízes da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14368, 516, 11, 'Servi ao Senhor com respeito e exultai em sua presença; prestai-lhe homenagem com tremor, para que não se irrite e não pereçais quando, em breve, se acender sua cólera. Felizes, entretanto, todos os que nele confiam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14369, 517, 1, 'Salmo de Davi, quando fugia de Absalão, seu filho.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14370, 517, 2, 'Senhor, como são numerosos os meus perseguidores! É uma turba que se dirige contra mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14371, 517, 3, 'Uma multidão inteira grita a meu respeito: “Não, não há mais salvação para ele em seu Deus!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14372, 517, 4, 'Mas vós sois, Senhor, para mim um escudo; vós sois minha glória, vós me levantais a cabeça.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14373, 517, 5, 'Apenas elevei a voz para o Senhor, ele me responde de sua montanha santa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14374, 517, 6, 'Eu, que me tinha deitado e adormecido, levanto-me, porque o Senhor me sustenta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14375, 517, 7, 'Nada temo diante desta multidão de povo, que de todos os lados se dirige contra mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14376, 517, 8, 'Levantai-vos, Senhor! Salvai-me, ó meu Deus! Feris no rosto todos os que me perseguem, quebrais os dentes dos pecadores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14377, 517, 9, 'Sim, Senhor, a salvação vem de vós. Desça a vossa bênção sobre vosso povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14378, 518, 1, 'Ao mestre de canto. Com instrumentos de corda. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14379, 518, 2, 'Quando vos invoco, respondei-me, ó Deus de minha justiça, vós que na hora da angústia me reconfortastes. Tende piedade de mim e ouvi minha oração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14380, 518, 3, 'Ó poderosos, até quando tereis o coração endurecido, no amor das vaidades e na busca da mentira?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14381, 518, 4, 'O Senhor escolheu como eleito uma pessoa admirável, o Senhor me ouviu quando o invoquei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14382, 518, 5, 'Tremei, mas sem pecar; refleti em vossos corações, quando estiverdes em vossos leitos, e calai.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14383, 518, 6, 'Oferecei vossos sacrifícios com sinceridade e esperai no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14384, 518, 7, 'Dizem muitos: “Quem nos fará ver a felicidade?”. Fazei brilhar sobre nós, Senhor, a luz de vossa face.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14385, 518, 8, 'Pusestes em meu coração mais alegria do que quando abundam o trigo e o vinho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14386, 518, 9, 'Apenas me deito, logo adormeço em paz, porque a segurança de meu repouso vem de vós só, Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14387, 519, 1, 'Ao mestre de canto. Com flautas. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14388, 519, 2, 'Senhor, ouvi minhas palavras, escutai meus gemidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14389, 519, 3, 'Atendei à voz de minha prece, ó meu rei, ó meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14390, 519, 4, 'É a vós que eu invoco, Senhor, desde a manhã; escutai a minha voz, porque, desde o raiar do dia, vos apresento minha súplica e espero.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14391, 519, 5, 'Pois vós não sois um Deus a quem agrade o mal, o mau não poderia morar junto de vós;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14392, 519, 6, 'os ímpios não podem resistir ao vosso olhar. Detestais a todos os que praticam o mal,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14393, 519, 7, 'fazeis perecer aqueles que mentem, o homem cruel e doloso vos é abominável, ó Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14394, 519, 8, 'Mas eu, graças à vossa grande bondade, entrarei em vossa casa. Irei prostrar-me em vosso santuário, com o respeito que vos é devido, Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14395, 519, 9, 'Conduzi-me pelas sendas da justiça, por causa de meus inimigos; aplainai, para mim, vosso caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14396, 519, 10, 'Porque em seus lábios não há sinceridade, seus corações só urdem projetos ardilosos. A garganta deles é como um sepulcro escancarado, com a língua distribuem lisonjas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14397, 519, 11, 'Deixai-os, Senhor, prender-se nos seus erros, que suas maquinações malogrem! Por causa do número de seus crimes, rejeitai-os, pois é contra vós que se revoltaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14398, 519, 12, 'Regozijam-se, pelo contrário, os que em vós confiam, permanecem para sempre na alegria. Protegei-os e triunfarão em vós os que amam vosso nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14399, 519, 13, 'Pois, vós, Senhor, abençoais o justo; vossa benevolência, como um escudo, o cobrirá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14400, 520, 1, 'Ao mestre de canto. Com instrumentos de corda. Em oitava. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14401, 520, 2, 'Senhor, em vossa cólera não me repreendais, em vosso furor não me castigueis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14402, 520, 3, 'Tende piedade de mim, Senhor, porque desfaleço; sarai-me, pois sinto abalados os meus ossos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14403, 520, 4, 'Minha alma está muito perturbada; vós, porém, Senhor, até quando?...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14404, 520, 5, 'Voltai, Senhor, livrai minha alma; salvai-me, pela vossa bondade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14405, 520, 6, 'Porque no seio da morte não há quem de vós se lembre; quem vos glorificará na habitação dos mortos?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14406, 520, 7, 'Eu me esgoto gemendo; todas as noites banho de pranto minha cama, com lágrimas inundo o meu leito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14407, 520, 8, 'De amargura meus olhos se turvam, esmorecem por causa dos que me oprimem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14408, 520, 9, 'Apartai-vos de mim, vós todos que praticais o mal, porque o Senhor atendeu às minhas lágrimas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14409, 520, 10, 'O Senhor escutou a minha oração, o Senhor acolheu a minha súplica.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14410, 520, 11, 'Que todos os meus inimigos sejam envergonhados e aterrados; recuem imediatamente, cobertos de confusão!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14411, 521, 1, 'Lamentação de Davi, que cantou em honra do Senhor, por causa de Cus, o benjaminita.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14412, 521, 2, 'Senhor, ó meu Deus, é em vós que eu busco meu refúgio; salvai-me de todos os que me perseguem e livrai-me,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14413, 521, 3, 'para que o inimigo não me arrebate como um leão, e me dilacere sem que ninguém me livre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14414, 521, 4, 'Senhor, ó meu Deus, se acaso fiz isso, se minhas mãos cometeram a iniquidade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14415, 521, 5, 'se fiz mal ao homem pacífico, se oprimi os que me perseguiam sem motivo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14416, 521, 6, 'que o inimigo me persiga e me apanhe, que ele me pise vivo ao solo e atire a minha honra ao pó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14417, 521, 7, 'Levantai-vos, Senhor, na vossa cólera; erguei-vos contra o furor dos que me oprimem, erguei-vos para me defender numa causa que tomastes a vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14418, 521, 8, 'Que a assembleia das nações vos circunde, presidi-a de um trono elevado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14419, 521, 9, 'O Senhor é o juiz dos povos. Fazei-me justiça, Senhor, segundo o meu justo direito, conforme minha integridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14420, 521, 10, 'Ponde fim à malícia dos ímpios e sustentai o direito, ó Deus de justiça, que sondais os corações e os rins.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14421, 521, 11, 'O meu escudo é Deus, ele salva os que têm o coração reto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14422, 521, 12, 'Deus é um juiz íntegro, um Deus perpetuamente vingador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14423, 521, 13, 'Se eles não se corrigem, ele afiará a espada, entesará o arco e os visará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14424, 521, 14, 'Contra os ímpios apresentará dardos mortíferos, lançará flechas inflamadas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14425, 521, 15, 'Eis que o mau está em dores de parto, concebe a malícia e dá à luz a mentira.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14426, 521, 16, 'Abre um fosso profundo, mas cai no abismo por ele mesmo cavado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14427, 521, 17, 'Sua malícia recairá em sua própria cabeça, e sua violência se voltará contra a sua fronte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14428, 521, 18, 'Eu, porém, glorificarei o Senhor por sua justiça, e salmodiarei o nome do Senhor, o Altíssimo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14429, 522, 1, 'Ao mestre de canto. Com a gitiena. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14430, 522, 2, 'Ó Senhor, nosso Deus, como é glorioso vosso nome em toda a terra! Vossa majestade se estende, triunfante, por cima de todos os céus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14431, 522, 3, 'Da boca das crianças e dos pequeninos sai um louvor que confunde vossos adversários, e reduz ao silêncio vossos inimigos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14432, 522, 4, 'Quando contemplo o firmamento, obra de vossos dedos, a lua e as estrelas que lá fixastes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14433, 522, 5, '‘‘Que é o homem – digo-me então –, para pensardes nele? Que são os filhos de Adão, para que vos ocupeis com eles?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14434, 522, 6, 'Entretanto, vós o fizestes quase igual aos anjos, de glória e honra o coroastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14435, 522, 7, 'Destes-lhe poder sobre as obras de vossas mãos, vós lhe submetestes todo o universo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14436, 522, 8, 'Rebanhos e gados, e até os animais bravios,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14437, 522, 9, 'pássaros do céu e peixes do mar, tudo o que se move nas águas do oceano”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14438, 522, 10, 'Ó Senhor, nosso Deus, como é glorioso vosso nome em toda a terra!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14439, 523, 1, 'Ao mestre de canto. Segundo a melodia “A morte para o filho”. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14440, 523, 2, 'Eu vos louvarei, Senhor, de todo o coração, todas as vossas maravilhas narrarei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14441, 523, 3, 'Em vós eu estremeço de alegria, cantarei vosso nome, ó Altíssimo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14442, 523, 4, 'Porque meus inimigos recuaram, fraquejaram, pereceram ante a vossa face.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14443, 523, 5, 'Pois tomastes a vós meu direito e minha causa; assentastes, ó justo Juiz, em vosso tribunal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14444, 523, 6, 'Com efeito, perseguistes as nações, destruístes o ímpio; apagastes, para sempre, o seu nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14445, 523, 7, 'Meus inimigos pereceram, consumou-se sua ruína eterna; demolistes suas cidades, sua própria lembrança se acabou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14446, 523, 8, 'O Senhor, porém, domina eternamente; num trono sólido, ele pronuncia seus julgamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14447, 523, 9, 'Ele mesmo julgará o universo com justiça, com equidade pronunciará sentença sobre os povos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14448, 523, 10, 'O Senhor torna-se refúgio para o oprimido, uma defesa oportuna para os tempos de perigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14449, 523, 11, 'Aqueles que conheceram vosso nome confiarão em vós, porque, Senhor, jamais abandonais quem vos procura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14450, 523, 12, 'Salmodiai ao Senhor, que habita em Sião; proclamai seus altos feitos entre os povos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14451, 523, 13, 'Porque, vingador do sangue derramado, ele se lembra deles e não esqueceu o clamor dos infelizes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14452, 523, 14, 'Tende piedade de mim, Senhor, vede a miséria a que me reduziram os inimigos; arrancai-me das portas da morte,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14453, 523, 15, 'para que nas portas da filha de Sião eu publique vossos louvores, e me regozije de vosso auxílio.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14454, 523, 16, 'Caíram as nações no fosso que cavaram; prenderam-se seus pés na armadilha que armaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14455, 523, 17, 'O Senhor se manifestou e fez justiça, capturando o ímpio em suas próprias redes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14456, 523, 18, 'Que os pecadores caiam na região dos mortos, todos esses povos que olvidaram a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14457, 523, 19, 'O pobre, porém, não ficará no eterno esquecimento; nem a esperança dos aflitos será frustrada para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14458, 523, 20, 'Levantai-vos, Senhor! Não seja o homem quem tenha a última palavra! Que diante de vós sejam julgadas as nações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14459, 523, 21, 'Enchei-as de pavor, Senhor, para que saibam que não passam de simples homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14460, 523, 22, '(l) Senhor, por que ficais tão longe? Por que vos ocultais nas horas de angústia?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14461, 523, 23, '(2) Enquanto o ímpio se enche de orgulho, é vexado o infeliz com as tribulações que aquele tramou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14462, 523, 24, '(3) O pecador se gloria até de sua cupidez, o cobiçoso blasfema e despreza a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14463, 523, 25, '(4) Em sua arrogância, o ímpio diz: “Não há castigo, Deus não existe”. É tudo e só o que ele pensa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14464, 523, 26, '(5) Em todos os tempos, próspero é o curso de sua vida; vossos juízos estão acima de seu alcance; quanto a seus adversários, os despreza a todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14465, 523, 27, '(6) Diz no coração: “Nada me abalará, jamais terei má sorte”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14466, 523, 28, '(7) De maledicência, astúcia e dolo sua boca está cheia;* em sua língua só existem palavras injuriosas e ofensivas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14467, 523, 29, '(8) Põe-se de emboscada na vizinhança dos povoados, mata o inocente em lugares ocultos; seus olhos vigiam o infeliz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14468, 523, 30, '(9) Como um leão no covil, espreita, no escuro; arma ciladas para surpreender o infeliz, colhe-o, na sua rede, e o arrebata.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14469, 523, 31, '(10) Curva-se, agacha-se no chão, e os infortunados caem em suas garras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14470, 523, 32, '(11) Depois diz em seu coração: “Deus depressa se esquecerá, ele voltará a cabeça, nunca vê nada”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14471, 523, 33, '(12) Levantai-vos, Senhor! Estendei a mão, e não vos esqueçais dos pobres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14472, 523, 34, '(13) Por que razão o ímpio despreza Deus e diz em seu coração “Não haverá castigo?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14473, 523, 35, '(14) Entretanto, vós vedes tudo: observais os que penam e sofrem, a fim de tomar a causa deles em vossas mãos. É a vós que se abandona o infortunado, sois vós o amparo do órfão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14474, 523, 36, '(15) Esmagai, pois, o braço do pecador perverso; persegui sua malícia, para que não subsista.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14475, 523, 37, '(16) O Senhor é rei eterno, as nações pagãs desaparecerão de seu domínio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14476, 523, 38, '(17) Senhor, ouvistes os desejos dos humildes, confortastes-lhes o coração e os atendestes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14477, 523, 39, '(18) Para que justiça seja feita ao órfão e ao oprimido, nem mais incuta terror o homem tirado do pó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14478, 524, 1, 'Ao mestre de canto. De Davi. É junto do Senhor que procuro refúgio. Por que dizer-me: “Foge, velozmente, para a montanha, como um pássaro;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14479, 524, 2, 'eis que os maus entesam seu arco e ajustam a flecha na corda, para ferir, de noite, os que têm o coração reto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14480, 524, 3, 'Quando os próprios fundamentos se abalam, que pode fazer ainda o justo?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14481, 524, 4, 'Entretanto, o Senhor habita em seu templo, o Senhor tem seu trono no céu. Sua vista está atenta, seus olhares observam os filhos dos homens.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14482, 524, 5, 'O Senhor sonda o justo como o ímpio, mas aquele que ama a injustiça, ele o aborrece.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14483, 524, 6, 'Sobre os ímpios ele fará cair uma chuva de fogo e de enxofre; um vento abrasador de procela será o seu quinhão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14484, 524, 7, 'Porque o Senhor é justo, ele ama a justiça; e os homens retos contemplarão a sua face.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14485, 525, 1, 'Ao mestre de canto. Uma oitava abaixo. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14486, 525, 2, 'Salvai-nos, Senhor, pois desaparecem os homens piedosos, e a lealdade se extingue entre os homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14487, 525, 3, 'Uns não têm para com os outros senão palavras mentirosas; adulação na boca, duplicidade no coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14488, 525, 4, 'Que o Senhor extirpe os lábios hipócritas e a língua insolente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14489, 525, 5, 'Aqueles que dizem: “Dominaremos pela nossa língua, nossos lábios trabalham para nós, quem nos será senhor?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14490, 525, 6, 'Responde, porém, o Senhor: “Por causa da aflição dos humildes e dos gemidos dos pobres, irei levantar-me para lhes dar a salvação que desejam”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14491, 525, 7, 'As palavras do Senhor são palavras sinceras, puras como a prata acrisolada, isenta de ganga, sete vezes depurada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14492, 525, 8, 'Vós, Senhor, haveis de nos guardar, defender-nos sempre dessa raça maléfica,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14493, 525, 9, 'porque os ímpios andam de todos os lados, enquanto a vileza se ergue entre os homens.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14494, 526, 1, 'Ao mestre de canto. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14495, 526, 2, 'Até quando, Senhor, de todo vos esquecereis de mim? Por quanto tempo ainda desviareis de mim os vossos olhares?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14496, 526, 3, 'Até quando aninharei a angústia na minha alma, e, dia após dia, a tristeza no coração?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14497, 526, 4, 'Até quando se levantará o meu inimigo contra mim? Olhai! Ouvi-me, Senhor, ó meu Deus!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14498, 526, 5, 'Iluminai meus olhos com vossa luz, para eu não adormecer na morte, para que meu inimigo não venha a dizer: “Venci-o”;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14499, 526, 6, 'e meus adversários não triunfem no momento de minha queda, eu que confiei em vossa misericórdia. Antes possa meu coração regozijar-se em vosso socorro! Então cantarei ao Senhor pelos benefícios que me concedeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14500, 527, 1, 'Ao mestre de canto. De Davi. Diz o insensato em seu coração: “Não há Deus”. Corromperam-se os homens, sua coduta é abominável, não há um só que faça o bem.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14501, 527, 2, 'O Senhor, do alto do céu, observa os filhos dos homens, para ver se, acaso, existe alguém sensato que busque a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14502, 527, 3, 'Mas todos eles se extraviaram e se perverteram; não há mais ninguém que faça o bem, nem um, nem mesmo um só.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14503, 527, 4, 'Não se emendarão esses obreiros do mal, que devoram meu povo como quem come pão? Eles que não invocam o Senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14504, 527, 5, 'Mas irão tremer de pavor, porque Deus está com a raça dos justos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14505, 527, 6, 'pretendeis frustrar os planos do humilde, mas o Senhor é seu refúgio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14506, 527, 7, 'Ah, que venha de Sião a salvação de Israel! Quando o Senhor tiver mudado a sorte de seu povo, Jacó exultará e Israel se alegrará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14507, 528, 1, 'Salmo de Davi. Senhor, quem há de morar em vosso tabernáculo? Quem habitará em vossa montanha santa?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14508, 528, 2, 'O que vive na inocência e pratica a justiça, o que pensa o que é reto no seu coração,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14509, 528, 3, 'cuja língua não calunia; o que não faz mal a seu próximo, e não ultraja seu semelhante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14510, 528, 4, 'O que tem por desprezível o malvado, mas sabe honrar os que temem a Deus; o que não retrata juramento mesmo com dano seu,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14511, 528, 5, 'não empresta dinheiro com usura, nem recebe presente para condenar o inocente. Aquele que assim proceder jamais será abalado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14512, 529, 1, 'Poema de Davi. Guardai-me, ó Deus, porque é em vós que procuro refúgio.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14513, 529, 2, 'Digo a Deus: “Sois o meu Senhor, fora de vós não há felicidade para mim”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14514, 529, 3, 'Quão admirável tornou Deus o meu afeto para com os santos que estão em sua terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14515, 529, 4, 'Numerosos são os sofrimentos que suportam aqueles que se entregam a estranhos deuses. Não hei de oferecer suas libações de sangue e meus lábios jamais pronunciarão o nome de seus ídolos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14516, 529, 5, 'Senhor, vós sois a minha parte de herança e meu cálice; vós tendes nas mãos o meu destino.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14517, 529, 6, 'O cordel mediu para mim um lote aprazível, muito me agrada a minha herança.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14518, 529, 7, 'Bendigo o Senhor porque me deu conselho, porque mesmo de noite o coração me exorta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14519, 529, 8, 'Ponho sempre o Senhor diante dos olhos, pois ele está à minha direita; não vacilarei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14520, 529, 9, 'Por isso, meu coração se alegra e minha alma exulta, até meu corpo descansará seguro,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14521, 529, 10, 'porque vós não abandonareis minha alma na habitação dos mortos, nem permitireis que vosso Santo conheça a corrupção.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14522, 529, 11, 'Vós me ensinareis o caminho da vida, há abundância de alegria junto de vós, e delícias eternas à vossa direita.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14523, 530, 1, 'Súplica de Davi. Ouvi, Senhor, uma causa justa! Atendei a meu clamor! Escutai minha prece, de lábios sem malícia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14524, 530, 2, 'Venha de vós o meu julgamento, e vossos olhos reconheçam que sou íntegro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14525, 530, 3, 'Podeis sondar meu coração, visitá-lo à noite, prová-lo pelo fogo, não encontrareis iniquidade em mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14526, 530, 4, 'Minha boca não pecou, como costumam os homens; conforme as palavras dos vossos lábios, segui os caminhos da lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14527, 530, 5, 'Meus passos se mantiveram firmes nas vossas sendas, meus pés não titubearam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14528, 530, 6, 'Eu vos invoco, pois me atendereis, Senhor; inclinai vossos ouvidos para mim, escutai minha voz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14529, 530, 7, 'Mostrai a vossa admirável misericórdia, vós que salvais dos adversários os que se acolhem à vossa direita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14530, 530, 8, 'Guardai-me como a pupila dos olhos, escondei-me à sombra de vossas asas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14531, 530, 9, 'longe dos pecadores, que me querem fazer violência. Meus inimigos me rodeiam com furor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14532, 530, 10, 'Seu coração endurecido se fecha à piedade; só têm na boca palavras arrogantes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14533, 530, 11, 'Eis que agora me cercam, espreitam para me prostrar por terra;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14534, 530, 12, 'qual leão que se atira ávido sobre a presa, e como o leãozinho no seu covil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14535, 530, 13, 'Levantai-vos, Senhor, correi-lhe ao encontro, derrubai-o; com vossa espada livrai-me do pecador,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14536, 530, 14, 'com vossa mão livrai-me dos homens, desses cuja única felicidade está nesta vida, que têm o ventre repleto de bens, cujos filhos vivem na abundância e deixam ainda aos seus filhos o que lhes sobra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14537, 530, 15, 'Mas eu, confiado na vossa justiça, contemplarei a vossa face; ao despertar, irei saciar-me com a visão de vosso ser.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14538, 531, 1, 'Ao mestre de canto. De Davi, servo do Senhor, que dirigiu as palavras deste cântico ao Senhor, no dia em que ficou livre de todos os seus inimigos e das mãos de Saul.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14539, 531, 2, 'Disse: Eu vos amo, Senhor, minha força!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14540, 531, 3, 'O Senhor é o meu rochedo, minha fortaleza e meu libertador. Meu Deus é a minha rocha, onde encontro o meu refúgio, meu escudo, força de minha salvação e minha cidadela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14541, 531, 4, 'Invoco o Senhor, digno de todo louvor, e fico livre dos meus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14542, 531, 5, 'Circundavam-me os vagalhões da morte, torrentes devastadoras me atemorizavam,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14543, 531, 6, 'enlaçavam-se as cadeias da habitação dos mortos, a própria morte me prendia em suas redes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14544, 531, 7, 'Na minha angústia, invoquei o Senhor, gritei para meu Deus: do seu templo ele ouviu a minha voz, e o meu clamor em sua presença chegou aos seus ouvidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14545, 531, 8, 'A terra vacilou e tremeu, os fundamentos das montanhas fremiram, abalaram-se, porque Deus se abrasou em cólera:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14546, 531, 9, 'suas narinas exalavam fumaça; sua boca, fogo devorador, brasas incandescentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14547, 531, 10, 'Ele inclinou os céus e desceu, calcando aos pés escuras nuvens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14548, 531, 11, 'Cavalgou sobre um querubim e voou, planando nas asas do vento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14549, 531, 12, 'Envolveu-se nas trevas como se fossem véu, fez para si uma tenda das águas tenebrosas, densas nuvens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14550, 531, 13, 'Do esplendor de sua presença suas nuvens avançaram: saraiva e centelhas de fogo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14551, 531, 14, 'Do céu trovejou o Senhor, o Altíssimo fez ressoar sua voz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14552, 531, 15, 'Lançou setas e dispersou os inimigos, fulminou relâmpagos e os desbaratou.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14553, 531, 16, 'E apareceu descoberto o leito do mar, ficaram à vista os fundamentos da terra, ante a vossa ameaçadora voz, ó Senhor, ante o furacão de vossa cólera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14554, 531, 17, 'Do alto estendeu a sua mão e me pegou, e retirou-me das águas profundas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14555, 531, 18, 'livrou-me de inimigo poderoso, dos meus adversários mais fortes do que eu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14556, 531, 19, 'Investiram contra mim no dia do meu infortúnio, mas o Senhor foi o meu arrimo;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14557, 531, 20, 'pôs-me a salvo e livrou-me, porque me ama.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14558, 531, 21, 'O Senhor me tratou segundo a minha inocência, retribuiu-me segundo a pureza de minhas mãos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14559, 531, 22, 'porque guardei os caminhos do Senhor e não pequei separando-me do meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14560, 531, 23, 'Tenho diante dos olhos todos os seus preceitos e não me desvio de suas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14561, 531, 24, 'Ando irrepreensivelmente diante dele, guardando-me do meu pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14562, 531, 25, 'O Senhor retribuiu-me segundo a minha justiça, segundo a pureza de minhas mãos diante dos seus olhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14563, 531, 26, 'Com quem é bondoso vos mostrais bondoso, com o homem íntegro vos mostrais íntegro;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14564, 531, 27, 'puro com quem é puro; prudente com quem é astuto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14565, 531, 28, 'Os humildes salvais, os semblantes soberbos humilhais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14566, 531, 29, 'Senhor, sois vós que fazeis brilhar o meu farol, sois vós que dissipais as minhas trevas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14567, 531, 30, 'Convosco afrontarei batalhões, com meu Deus escalarei muralhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14568, 531, 31, 'Os caminhos de Deus são perfeitos, a palavra do Senhor é pura. Ele é o escudo de todos os que nele se refugiam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14569, 531, 32, 'Pois quem é Deus senão o Senhor? Quem é o rochedo, senão o nosso Deus?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14570, 531, 33, 'É Deus quem me cinge de coragem e aplana o meu caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14571, 531, 34, 'Torna os meus pés velozes como os das gazelas e me instala nas alturas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14572, 531, 35, 'Adestra minhas mãos para o combate e meus braços para o tiro de arco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14573, 531, 36, 'Vós me dais o escudo que me salva. Vossa destra me sustém, e vossa bondade me engrandece.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14574, 531, 37, 'Alargais o caminho a meus passos, para meus pés não resvalarem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14575, 531, 38, 'Dou caça aos inimigos e os alcanço, e não volto sem que os tenha aniquilado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14576, 531, 39, 'De tal sorte os despedaço, que não mais poderão levantar-se: eles ficam caídos a meus pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14577, 531, 40, 'Vós me cingis de coragem para a luta e ante mim dobrais os meus adversários.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14578, 531, 41, 'Afugentais da minha presença os meus inimigos e reduzis ao silêncio os que me aborrecem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14579, 531, 42, 'Gritam por socorro, mas não há quem os salve; clamam ao Senhor, mas não responde...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14580, 531, 43, 'Eu os disperso como o pó que o vento leva, e os esmago como o barro das estradas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14581, 531, 44, 'Vós me livrais das revoltas do povo e me colocais à frente das nações; povos que eu desconhecia se tornaram meus servos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14582, 531, 45, 'Gente estranha me serve abnegadamente e me obedece à primeira intimação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14583, 531, 46, 'Gente estranha desfalece e sai tremendo de seus esconderijos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14584, 531, 47, 'Viva o Senhor e bendito seja o meu rochedo! Exaltado seja Deus, que me salva!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14585, 531, 48, 'Deus, que me proporciona a vingança e avassala nações a meus pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14586, 531, 49, 'Sois vós que me libertais dos meus inimigos, me exaltais acima dos meus adversários e me salvais do homem violento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14587, 531, 50, 'Por isso vos louvarei, ó Senhor, entre as nações e celebrarei o vosso nome.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14588, 531, 51, 'Ele prepara grandes vitórias a seu rei e faz misericórdia a seu ungido, a Davi e a sua descendência para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14589, 532, 1, 'Ao mestre de canto. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14590, 532, 2, 'Narram os céus a glória de Deus, e o firmamento anuncia a obra de suas mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14591, 532, 3, 'O dia ao outro transmite essa mensagem, e uma noite à outra a repete.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14592, 532, 4, 'Não é uma língua nem são palavras, cujo sentido não se perceba,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14593, 532, 5, 'porque por toda a terra se espalha o seu ruído, e até os confins do mundo a sua voz; aí armou Deus para o sol uma tenda.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14594, 532, 6, 'E este, qual esposo que sai do seu tálamo, exulta, como um gigante, a percorrer seu caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14595, 532, 7, 'Sai de um extremo do céu, e no outro termina o seu curso; nada se furta ao seu calor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14596, 532, 8, 'A Lei do Senhor é perfeita, reconforta a alma; a ordem do Senhor é segura, instrui o simples.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14597, 532, 9, 'Os preceitos do Senhor são retos, deleitam o coração; o mandamento do Senhor é luminoso, esclarece os olhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14598, 532, 10, 'O temor do Senhor é puro, subsiste eternamente; os juízos do Senhor são verdadeiros, todos igualmente justos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14599, 532, 11, 'Mais desejáveis que o ouro, que uma barra de ouro fino; mais doces que o mel, que o puro mel dos favos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14600, 532, 12, 'Ainda que vosso servo neles atente, guardando-os com todo o cuidado;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14601, 532, 13, 'quem pode, entretanto, ver as próprias faltas? Purificai-me das que me são ocultas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14602, 532, 14, 'Preservai, também, vosso servo do orgulho; não domine ele sobre mim, então serei íntegro e limpo de falta grave.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14603, 532, 15, 'Aceitai as palavras de meus lábios e os pensamentos de meu coração, na vossa presença, Senhor, minha rocha e meu redentor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14604, 533, 1, 'Ao mestre de canto. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14605, 533, 2, 'Que o Senhor te escute no dia da provação, e te proteja o nome do Deus de Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14606, 533, 3, 'Do seu santuário ele te socorra, e de Sião ele te sustente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14607, 533, 4, 'Lembre-se de tuas ofertas, e aceite os teus sacrifícios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14608, 533, 5, 'Conceda-te o que teu coração anseia, e realize todos os teus desejos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14609, 533, 6, 'Possamos nós alegrar-nos com tua vitória e levantar as bandeiras em nome de nosso Deus. Sim, que o Senhor realize todos os teus pedidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14610, 533, 7, 'Já sei que o Senhor reservou a vitória para seu ungido, e o ouviu do alto de seu santuário pelo poder de seu braço vencedor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14611, 533, 8, 'Uns põem sua força nos carros, outros, nos cavalos. Nós, porém, a temos em nome do Senhor, nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14612, 533, 9, 'Eles fraquejaram e foram vencidos, mas nós, de pé, continuamos firmes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14613, 533, 10, 'Senhor, dai a vitória ao rei, e ouvi-nos no dia em que vos invocarmos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14614, 534, 1, 'Ao mestre de canto. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14615, 534, 2, 'Senhor, alegra-se o rei com o vosso poder, e muito exulta com o vosso auxílio!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14616, 534, 3, 'Realizastes os anseios de seu coração, não rejeitastes a prece de seus lábios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14617, 534, 4, 'Com preciosas bênçãos fostes-lhe ao encontro, pusestes-lhe na cabeça coroa de puríssimo ouro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14618, 534, 5, 'Ele vos pediu a vida, vós lha concedestes, uma vida cujos dias serão eternos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14619, 534, 6, 'Grande é a sua glória, devida à vossa proteção; vós o cobristes de majestade e esplendor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14620, 534, 7, 'Sim, fizestes dele o objeto de vossas eternas bênçãos, de alegria o cobristes com a vossa presença,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14621, 534, 8, 'pois o rei confiou no Senhor. Graças ao Altíssimo não será abalado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14622, 534, 9, 'Que tua mão, ó rei, apanhe teus inimigos, que tua mão atinja os que te odeiam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14623, 534, 10, 'Tu os tornarás como fornalha ardente, quando apareceres diante deles. Que o Senhor em sua cólera os consuma, e que o fogo os devore.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14624, 534, 11, 'Faze desaparecer da terra a posteridade deles e a sua descendência dentre os filhos dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14625, 534, 12, 'Se intentarem fazer-te mal, tramando algum plano, não o conseguirão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14626, 534, 13, 'porque os porás em fuga, dirigindo teu arco contra a face deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14627, 534, 14, 'Erguei-vos, Senhor, em vossa potência! Cantaremos e celebraremos o vosso poder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14628, 535, 1, 'Ao mestre de canto. Segundo a melodia “A corça da aurora”. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14629, 535, 2, 'Meu Deus, meu Deus, por que me abandonastes? E permaneceis longe de minhas súplicas e de meus gemidos?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14630, 535, 3, 'Meu Deus, clamo de dia e não me respondeis; imploro de noite e não me atendeis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14631, 535, 4, 'Entretanto, vós habitais em vosso santuário, vós que sois a glória de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14632, 535, 5, 'Nossos pais puseram sua confiança em vós, esperaram em vós e os livrastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14633, 535, 6, 'A vós clamaram e foram salvos; confiaram em vós e não foram confundidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14634, 535, 7, 'Eu, porém, sou um verme, não sou homem, o opróbrio de todos e a abjeção da plebe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14635, 535, 8, 'Todos os que me vêem zombam de mim; dizem, meneando a cabeça:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14636, 535, 9, '“Esperou no Senhor, pois que ele o livre, que o salve, se o ama”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14637, 535, 10, 'Sim, fostes vós que me tirastes das entranhas de minha mãe e, seguro, me fizestes repousar em seu seio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14638, 535, 11, 'Eu vos fui entregue desde o meu nascer, desde o ventre de minha mãe vós sois o meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14639, 535, 12, 'Não fiqueis longe de mim, pois estou atribulado; vinde para perto de mim, porque não há quem me ajude.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14640, 535, 13, 'Cercam-me touros numerosos, rodeiam-me touros de Basã;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14641, 535, 14, 'contra mim eles abrem suas fauces, como o leão que ruge e arrebata.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14642, 535, 15, 'Derramo-me como água, todos os meus ossos se desconjuntam; meu coração tornou-se como cera e derrete-se nas minhas entranhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14643, 535, 16, 'Minha garganta está seca qual barro cozido, pega-se no paladar a minha língua: vós me reduzistes ao pó da morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14644, 535, 17, 'Sim, rodeia-me uma malta de cães, cerca-me um bando de malfeitores. Traspassaram minhas mãos e meus pés:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14645, 535, 18, 'poderia contar todos os meus ossos. Eles me olham e me observam com alegria,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14646, 535, 19, 'repartem entre si as minhas vestes, e lançam sorte sobre a minha túnica.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14647, 535, 20, 'Porém, vós, Senhor, não vos afasteis de mim; ó meu auxílio, bem depressa me ajudai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14648, 535, 21, 'Livrai da espada a minha alma, e das garras dos cães a minha vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14649, 535, 22, 'Salvai-me a mim, mísero, das fauces do leão e dos chifres dos búfalos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14650, 535, 23, 'Então, anunciarei vosso nome a meus irmãos, e vos louvarei no meio da assembleia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14651, 535, 24, '“Vós que temeis o Senhor, louvai-o; vós todos, descendentes de Jacó, aclamai-o; temei-o, todos vós, estirpe de Israel,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14652, 535, 25, 'porque ele não rejeitou nem desprezou a miséria do infeliz, nem dele desviou a sua face, mas o ouviu, quando lhe suplicava.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14653, 535, 26, 'De vós procede o meu louvor na grande assembleia, cumprirei meus votos na presença dos que vos temem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14654, 535, 27, 'Os pobres comerão e serão saciados; louvarão o Senhor aqueles que o procuram: “Vivam para sempre os nossos corações”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14655, 535, 28, 'Hão de se lembrar do Senhor e a ele se converter todos os povos da terra; e diante dele se prostrarão todas as famílias das nações,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14656, 535, 29, 'porque a realeza pertence ao Senhor e ele impera sobre as nações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14657, 535, 30, 'Todos os que dormem no seio da terra o adorarão; diante dele se prostrarão os que retornam ao pó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14658, 535, 31, 'Para ele viverá a minha alma, há de servi-lo minha descendência. Ela falará do Senhor às gerações futuras e proclamará sua justiça ao povo que vai nascer: “Eis o que fez o Senhor”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14659, 536, 1, 'Salmo de Davi. O Senhor é meu pastor, nada me faltará.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14660, 536, 2, 'Em verdes prados ele me faz repousar. Conduz-me junto às águas refrescantes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14661, 536, 3, 'restaura as forças de minha alma. Pelos caminhos retos ele me leva, por amor do seu nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14662, 536, 4, 'Ainda que eu atravesse o vale escuro, nada temerei, pois estais comigo. Vosso bordão e vosso báculo são o meu amparo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14663, 536, 5, 'Preparais para mim a mesa à vista de meus inimigos. Derramais o perfume sobre minha cabeça, e transborda minha taça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14664, 536, 6, 'A vossa bondade e misericórdia hão de seguir-me por todos os dias de minha vida. E habitarei na casa do Senhor por longos dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14665, 537, 1, 'Salmo de Davi. Do Senhor é a terra e tudo o que ela contém, a órbita terrestre e todos os que nela habitam,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14666, 537, 2, 'pois ele mesmo a assentou sobre as águas do mar e sobre as águas dos rios a consolidou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14667, 537, 3, 'Quem será digno de subir ao monte do Senhor? Ou de permanecer no seu lugar santo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14668, 537, 4, 'O que tem as mãos limpas e o coração puro, cujo espírito não busca as vaidades nem perjura para enganar seu próximo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14669, 537, 5, 'Este terá a bênção do Senhor, e a recompensa de Deus, seu Salvador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14670, 537, 6, 'Tal é a geração dos que o procuram, dos que buscam a face do Deus de Jacó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14671, 537, 7, 'Levantai, ó portas, os vossos dintéis! Levantai-vos, ó pórticos antigos, para que entre o rei da glória!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14672, 537, 8, '“Quem é este rei da glória?” É o Senhor forte e poderoso, o Senhor poderoso na batalha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14673, 537, 9, 'Levantai, ó portas, os vossos dintéis! Levantai-vos, ó pórticos antigos, para que entre o rei da glória!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14674, 537, 10, '“Quem é este rei da glória?” É o Senhor dos exércitos! É ele o rei da glória.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14675, 538, 1, 'De Davi. Para vós, Senhor, elevo a minha alma.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14676, 538, 2, 'Meu Deus, em vós confio: não seja eu decepcionado! Não escarneçam de mim meus inimigos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14677, 538, 3, 'Não, nenhum daqueles que esperam em vós será confundido, mas os pérfidos serão cobertos de vergonha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14678, 538, 4, 'Senhor, mostrai-me os vossos caminhos, e ensinai-me as vossas veredas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14679, 538, 5, 'Dirigi-me na vossa verdade e ensinai-me, porque sois o Deus de minha salvação e em vós eu espero sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14680, 538, 6, 'Lembrai-vos, Senhor, de vossas misericórdias e de vossas bondades, que são eternas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14681, 538, 7, 'Não vos lembreis dos pecados de minha juventude e dos meus delitos; em nome de vossa misericórdia, lembrai-vos de mim, por causa de vossa bondade, Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14682, 538, 8, 'O Senhor é bom e reto, por isso reconduz os extraviados ao caminho reto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14683, 538, 9, 'Dirige os humildes na justiça, e lhes ensina a sua via.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14684, 538, 10, 'Todos os caminhos do Senhor são graça e fidelidade, para aqueles que guardam sua aliança e seus preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14685, 538, 11, 'Por amor de vosso nome, Senhor, perdoai meu pecado, por maior que seja.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14686, 538, 12, 'Que advém ao homem que teme o Senhor? Deus lhe ensina o caminho que deve escolher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14687, 538, 13, 'Viverá na felicidade, e sua posteridade possuirá a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14688, 538, 14, 'O Senhor se torna íntimo dos que o temem, e lhes manifesta a sua aliança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14689, 538, 15, 'Meus olhos estão sempre fixos no Senhor, porque ele livrará do laço os meus pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14690, 538, 16, 'Olhai-me e tende piedade de mim, porque estou só e na miséria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14691, 538, 17, 'Aliviai as angústias do meu coração, e livrai-me das aflições.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14692, 538, 18, 'Vede minha miséria e meu sofrimento, e perdoai-me todas as faltas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14693, 538, 19, 'Vede meus inimigos: são muitos, e com ódio implacável me perseguem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14694, 538, 20, 'Defendei minha alma e livrai-me: não seja confundido eu que em vós me acolhi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14695, 538, 21, 'Protejam-me a inocência e a integridade, porque espero em vós, Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14696, 538, 22, 'Ó Deus, livrai Israel de todas as suas angústias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14697, 539, 1, 'De Davi. Fazei-me justiça, Senhor, pois tenho andado retamente e, confiando em vós, não vacilei.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14698, 539, 2, 'Sondai-me, Senhor, e provai-me; escrutai meus rins e meu coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14699, 539, 3, 'Tenho sempre diante dos olhos vossa bondade, e caminho na vossa verdade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14700, 539, 4, 'Entre os homens iníquos não me assento, nem me associo aos trapaceiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14701, 539, 5, 'Detesto a companhia dos malfeitores, com os ímpios não me junto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14702, 539, 6, 'Na inocência lavo as minhas mãos, e conservo-me junto de vosso altar, Senhor,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14703, 539, 7, 'para publicamente anunciar vossos louvores, e proclamar todas as vossas maravilhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14704, 539, 8, 'Senhor, amo a habitação de vossa casa, e o tabernáculo onde reside a vossa glória.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14705, 539, 9, 'Não leveis a minha alma com a dos pecadores, nem me tireis a vida com a dos sanguinários,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14706, 539, 10, 'cujas mãos são criminosas, e cuja destra está cheia de subornos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14707, 539, 11, 'Eu, porém, procedo com retidão. Livrai-me e sede-me propício.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14708, 539, 12, 'Meu pé está firme no caminho reto; nas assembleias, bendirei o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14709, 540, 1, 'De Davi. O Senhor é minha luz e minha salvação, a quem temerei? O Senhor é o protetor de minha vida, de quem terei medo?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14710, 540, 2, 'Quando os malvados me atacam para me devorar vivo, são eles, meus adversários e inimigos, que resvalam e caem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14711, 540, 3, 'Se todo um exército se acampar contra mim, não temerá meu coração. Se se travar contra mim uma batalha, mesmo assim terei confiança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14712, 540, 4, 'Uma só coisa peço ao Senhor e a peço incessantemente: é habitar na casa do Senhor todos os dias de minha vida, para admirar aí a beleza do Senhor e contemplar o seu santuário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14713, 540, 5, 'Assim, no dia mau ele me esconderá na sua tenda, irá ocultar-me no recôndito de seu tabernáculo, sobre um rochedo me erguerá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14714, 540, 6, 'Mas desde agora ele levanta a minha cabeça acima dos inimigos que me cercam; e oferecerei no tabernáculo sacrifícios de regozijo, com cantos e louvores ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14715, 540, 7, 'Escutai, Senhor, a voz de minha oração, tende piedade de mim e ouvi-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14716, 540, 8, 'Fala-vos meu coração, minha face vos busca; a vossa face, ó Senhor, eu a procuro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14717, 540, 9, 'Não escondais de mim vosso semblante, não afasteis com ira o vosso servo. Vós sois o meu amparo, não me rejeiteis. Nem me abandoneis, ó Deus, meu Salvador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14718, 540, 10, 'Se meu pai e minha mãe me abandonarem, o Senhor me acolherá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14719, 540, 11, 'Ensinai-me, Senhor, vosso caminho; por causa dos adversários, guiai-me pela senda reta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14720, 540, 12, 'Não me abandoneis à mercê dos inimigos, contra mim se ergueram violentos e falsos testemunhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14721, 540, 13, 'Sei que verei os benefícios do Senhor na terra dos vivos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14722, 540, 14, 'Espera no Senhor e sê forte! Fortifique-se o teu coração e espera no Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14723, 541, 1, 'De Davi. É para vós, Senhor, que ergo meu clamor. Ó meu apoio, não fiqueis surdo à minha voz; não suceda que, vós não me ouvindo, eu me vá unir aos que desceram para o túmulo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14724, 541, 2, 'Ouvi a voz de minha súplica quando clamo, quando levanto as mãos para o vosso templo santo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14725, 541, 3, 'Não me deixeis perecer com os pecadores e com os que praticam a iniquidade, que dizem ao próximo palavras de paz, mas guardam a maldade no coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14726, 541, 4, 'Tratai-os de acordo com as suas ações, e conforme a malícia de seus crimes. Retribuí-lhes segundo a obra de suas mãos; dai-lhes o que merecem,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14727, 541, 5, 'pois não atendem às ações do Senhor nem às obras de suas mãos. Que ele os abata e não os levante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14728, 541, 6, 'Bendito seja o Senhor, que ouviu a voz de minha súplica; nele confiou meu coração e fui socorrido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14729, 541, 7, 'O Senhor é a minha força e o meu escudo! Por isso meu coração exulta e o louvo com meu cântico.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14730, 541, 8, 'O Senhor é a força do seu povo, uma fortaleza de salvação para o que lhe é consagrado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14731, 541, 9, 'Salvai, Senhor, vosso povo e abençoai a vossa herança; sede seu pastor, levai-o nos braços eternamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14732, 542, 1, 'Salmo de Davi. Tributai ao Senhor, ó filhos de Deus, tributai ao Senhor glória e poder!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14733, 542, 2, 'Rendei-lhe a glória devida ao seu nome; adorai o Senhor com ornamentos sagrados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14734, 542, 3, 'Ouve-se a voz do Senhor sobre as águas! O Deus de grandeza retumbou: o Senhor trovejou sobre as águas imensas!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14735, 542, 4, 'A voz do Senhor faz-se ouvir com poder! A voz do Senhor faz-se ouvir com majestade!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14736, 542, 5, 'Fendem-se os cedros à voz do Senhor, quebra o Senhor os cedros do Líbano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14737, 542, 6, 'Faz saltar o Líbano como um novilho, e o Sarion como um búfalo novo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14738, 542, 7, 'A voz do Senhor despede relâmpagos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14739, 542, 8, 'a voz do Senhor abala o deserto. O Senhor faz tremer o deserto de Cades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14740, 542, 9, 'A voz do Senhor retorce os carvalhos, desnuda as florestas. E em seu templo todos bradam: “glória”!.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14741, 542, 10, 'O Senhor preside ao dilúvio, o Senhor domina como rei para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14742, 542, 11, 'O Senhor há de dar fortaleza ao seu povo! O Senhor abençoará o seu povo, dando-lhe a paz!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14743, 543, 1, 'Salmo. Cântico para a dedicação da casa de Deus. De Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14744, 543, 2, 'Eu vos exaltarei, Senhor, porque me livrastes, não permitistes que exultassem sobre mim meus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14745, 543, 3, 'Senhor, meu Deus, clamei a vós e fui curado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14746, 543, 4, 'Senhor, minha alma foi tirada por vós da habitação dos mortos; dentre os que descem para o túmulo, vós me salvastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14747, 543, 5, 'Ó vós, fiéis do Senhor, cantai sua glória, dai graças ao seu santo nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14748, 543, 6, 'Porque a sua indignação dura apenas um momento, enquanto sua benevolência é para toda a vida. Pela tarde, vem o pranto, mas, de manhã, volta a alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14749, 543, 7, 'Eu, porém, disse, seguro de mim: “Não serei jamais abalado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14750, 543, 8, 'Senhor, foi por favor que me destes honra e poder, mas quando escondestes vossa face fiquei aterrado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14751, 543, 9, 'A vós, Senhor, eu clamo, e imploro a misericórdia de meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14752, 543, 10, '“Que proveito vos resultará de retomar-me a vida, de minha descida ao túmulo? Porventura vos louvará o meu pó? Apregoará ele a vossa fidelidade?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14753, 543, 11, 'Ouvi-me, Senhor, e tende piedade de mim; Senhor, vinde em minha ajuda.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14754, 543, 12, 'Vós convertestes o meu pranto em prazer, tirastes minhas vestes de penitência e me cingistes de alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14755, 543, 13, 'Assim, minha alma vos louvará sem calar jamais. Senhor, meu Deus, eu vos bendirei eternamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14756, 544, 1, 'Ao mestre de canto. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14757, 544, 2, 'Junto de vós, Senhor, me refugio. Não seja eu confundido para sempre; por vossa justiça, livrai-me!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14758, 544, 3, 'Inclinai para mim vossos ouvidos, apressai-vos em me libertar. Sede para mim uma rocha de refúgio, uma fortaleza bem armada para me salvar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14759, 544, 4, 'Pois só vós sois minha rocha e fortaleza: haveis de me guiar e dirigir, por amor de vosso nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14760, 544, 5, 'Vós me livrareis das ciladas que me armaram, porque sois minha defesa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14761, 544, 6, 'Em vossas mãos entrego meu espírito; livrai-me, ó Senhor, Deus fiel.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14762, 544, 7, 'Detestais os que adoram ídolos vãos. Eu, porém, confio no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14763, 544, 8, 'Exultarei e me alegrarei pela vossa compaixão, porque olhastes para minha miséria e ajudastes minha alma angustiada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14764, 544, 9, 'Não me entregastes às mãos do inimigo, mas alargastes o caminho sob meus pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14765, 544, 10, 'Tende piedade de mim, Senhor, porque vivo atribulado, de tristeza definham meus olhos, minha alma e minhas entranhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14766, 544, 11, 'Realmente, minha vida se consome em amargura, e meus anos em gemidos. Minhas forças se esgotaram na aflição, mirraram-se os meus ossos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14767, 544, 12, 'Tornei-me objeto de opróbrio para todos os inimigos, zombaria dos vizinhos e pavor dos conhecidos. Fogem de mim os que me vêem na rua.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14768, 544, 13, 'Fui esquecido dos corações como um morto, fiquei rejeitado como um vaso partido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14769, 544, 14, 'Sim, eu ouvi o vozerio da multidão; em toda parte, o terror! Conspirando contra mim, tramam como me tirar a vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14770, 544, 15, 'Mas eu, Senhor, em vós confio. Digo: “Sois vós o meu Deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14771, 544, 16, 'Meu destino está nas vossas mãos. Livrai-me do poder de meus inimigos e perseguidores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14772, 544, 17, 'Mostrai semblante sereno ao vosso servo, salvai-me pela vossa misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14773, 544, 18, 'Senhor, não fique eu envergonhado, porque vos invoquei: Confundidos sejam os ímpios e, mudos, lançados na região dos mortos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14774, 544, 19, 'Fazei calar os lábios mentirosos que falam contra o justo com insolência, desprezo e arrogância.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14775, 544, 20, 'Quão grande é, Senhor, vossa bondade, que reservastes para os que vos temem e com que tratais aos que se refugiam em vós, aos olhos de todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14776, 544, 21, 'Sob a proteção de vossa face os defendeis contra as conspirações dos homens. Vós os ocultais em vossa tenda contra as línguas maldizentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14777, 544, 22, 'Bendito seja o Senhor, que usou de maravilhosa bondade, abrigando-me em cidade fortificada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14778, 544, 23, 'Eu, porém, tinha dito no meu temor: “Fui rejeitado de vossa presença”. Mas ouvistes antes o brado de minhas súplicas, quando clamava a vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14779, 544, 24, 'Amai o Senhor todos os seus servos! Ele protege os que lhe são fiéis. Sabe, porém, retribuir, castigando com rigor os que procedem com soberba.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14780, 544, 25, 'Animai-vos e sede fortes de coração todos vós, que esperais no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14781, 545, 1, 'De Davi. Hino. Feliz aquele cuja iniquidade foi perdoada, cujo pecado foi absolvido.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14782, 545, 2, 'Feliz o homem a quem o Senhor não argúi de falta, e em cujo coração não há dolo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14783, 545, 3, 'Enquanto me conservei calado, meus ossos se mirraram, entre contínuos gemidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14784, 545, 4, 'Pois, dia e noite, vossa mão pesava sobre mim; minhas forças se esgotavam as forças como nos ardores do verão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14785, 545, 5, 'Então eu vos confessei o meu pecado, e não mais dissimulei a minha culpa. Disse: “Sim, vou confessar ao Senhor a minha iniquidade”. E vós perdoastes a pena do meu pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14786, 545, 6, 'Assim também todo fiel recorrerá a vós, no momento da necessidade. Quando transbordarem muitas águas, elas não chegarão até ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14787, 545, 7, 'Vós sois meu asilo, das angústias me preservareis e me envolvereis na alegria de minha salvação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14788, 545, 8, '“Vou te ensinar – dizeis –, vou te mostrar o caminho que deves seguir; vou te instruir, fitando em ti os meus olhos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14789, 545, 9, 'não queiras ser sem inteligência como o cavalo, como o muar, que só ao freio e à rédea submetem seus ímpetos; de outro modo não se chegam a ti.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14790, 545, 10, 'São muitos os sofrimentos do ímpio. Mas quem espera no Senhor, sua misericórdia o envolve.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14791, 545, 11, 'Ó justos, alegrai-vos e regozijai-vos no Senhor. Exultai todos vós, retos de coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14792, 546, 1, 'Exultai no Senhor, ó justos, pois aos retos convém o louvor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14793, 546, 2, 'Celebrai o Senhor com a cítara, entoai-lhe hinos na harpa de dez cordas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14794, 546, 3, 'Cantai-lhe um cântico novo, acompanhado de instrumentos de música,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14795, 546, 4, 'porque a palavra do Senhor é reta, em todas as suas obras resplandece a fidelidade:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14796, 546, 5, 'ele ama a justiça e o direito, da bondade do Senhor está cheia a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14797, 546, 6, 'Pela palavra do Senhor foram feitos os céus, e pelo sopro de sua boca todo o seu exército.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14798, 546, 7, 'Ele junta as águas do mar como num cantil, e em reservatórios encerra as ondas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14799, 546, 8, 'Tema ao Senhor toda a terra; reverenciem-no todos os habitantes do globo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14800, 546, 9, 'Porque ele disse e tudo foi feito, ele ordenou e tudo existiu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14801, 546, 10, 'O Senhor desfaz os planos das nações pagãs, reduz a nada os projetos dos povos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14802, 546, 11, 'Só os desígnios do Senhor permanecem eternamente e os pensamentos de seu coração por todas as gerações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14803, 546, 12, 'Feliz a nação que tem o Senhor por seu Deus, e o povo que ele escolheu para sua herança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14804, 546, 13, 'O Senhor olha do céu, vê todos os filhos dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14805, 546, 14, 'Do alto de sua morada observa todos os habitantes da terra;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14806, 546, 15, 'ele, que formou o coração de cada um, está atento a cada uma de suas ações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14807, 546, 16, 'Não vence o rei pelo numeroso exército, nem se livra o guerreiro pela grande força.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14808, 546, 17, 'O cavalo não é penhor de vitória, nem salva pela sua resistência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14809, 546, 18, 'Eis os olhos do Senhor pousados sobre os que o temem, sobre os que esperam na sua bondade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14810, 546, 19, 'a fim de livrar-lhes a alma da morte e nutri-los no tempo da fome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14811, 546, 20, 'Nossa alma espera no Senhor, porque ele é nosso amparo e nosso escudo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14812, 546, 21, 'Nele, pois, se alegra o nosso coração, em seu santo nome confiamos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14813, 546, 22, 'Seja-nos manifestada, Senhor, a vossa misericórdia, como a esperamos de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14814, 547, 1, 'De Davi. Quando simulou alienação na presença de Abimelec e, despedido por ele, partiu.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14815, 547, 2, 'Bendirei continuamente o Senhor, seu louvor não deixará meus lábios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14816, 547, 3, 'Glorie-se a minha alma no Senhor; ouçam-me os humildes, e se alegrem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14817, 547, 4, 'Glorificai comigo o Senhor, juntos exaltemos o seu nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14818, 547, 5, 'Procurei o Senhor e ele me atendeu, livrou-me de todos os temores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14819, 547, 6, 'Olhai para ele a fim de vos alegrardes, e não se cobrir de vergonha o vosso rosto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14820, 547, 7, 'Vede, este miserável clamou e o Senhor o ouviu, de todas as angústias o livrou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14821, 547, 8, 'O anjo do Senhor acampa em redor dos que o temem e os salva.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14822, 547, 9, 'Provai e vede como o Senhor é bom, feliz o homem que se refugia junto dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14823, 547, 10, 'Reverenciai o Senhor, vós, seus fiéis, porque nada falta àqueles que o temem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14824, 547, 11, 'Os poderosos empobrecem e passam fome, mas aos que buscam o Senhor nada lhes falta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14825, 547, 12, 'Vinde, meus filhos, ouvi-me: eu vos ensinarei o temor do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14826, 547, 13, 'Qual é o homem que ama a vida e deseja longos dias para gozar de felicidade?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14827, 547, 14, 'Guarda tua língua do mal, e teus lábios das palavras enganosas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14828, 547, 15, 'Aparta-te do mal e faze o bem, busca a paz e vai ao seu encalço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14829, 547, 16, 'Os olhos do Senhor estão voltados para os justos, e seus ouvidos atentos aos seus clamores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14830, 547, 17, 'O Senhor volta a sua face irritada contra os que fazem o mal, para apagar da terra a lembrança deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14831, 547, 18, 'Apenas clamaram os justos, o Senhor os atendeu e os livrou de todas as suas angústias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14832, 547, 19, 'O Senhor está perto dos contritos de coração, e salva os que têm o espírito abatido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14833, 547, 20, 'São numerosas as tribulações do justo, mas de todas o livra o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14834, 547, 21, 'Ele protege cada um de seus ossos: nem um só deles será quebrado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14835, 547, 22, 'A malícia do ímpio o leva à morte, e os que odeiam o justo serão castigados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14836, 547, 23, 'O Senhor livra a alma de seus servos; não será punido quem a ele se acolhe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14837, 548, 1, 'De Davi. Lutai, Senhor, contra os que me atacam; combatei meus adversários.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14838, 548, 2, 'Empunhai o broquel e o escudo, e erguei-vos em meu socorro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14839, 548, 3, 'Brandi a lança e sustai meus perseguidores. Dizei à minha alma: “Eu sou a tua salvação”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14840, 548, 4, 'Sejam confundidos e envergonhados os que odeiam a minha vida, recuem humilhados os que tramam minha desgraça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14841, 548, 5, 'Sejam como a palha levada pelo vento, quando o anjo do Senhor vier acossá-los.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14842, 548, 6, 'Torne-se tenebroso e escorregadio o seu caminho, quando o anjo do Senhor vier persegui-los,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14843, 548, 7, 'porquanto sem razão me armaram laços; para me perder, cavaram um fosso sem motivo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14844, 548, 8, 'Venha sobre eles de improviso a ruína; apanhe-os a rede por eles mesmos preparada, caiam eles próprios na cova que abriram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14845, 548, 9, 'Então a minha alma exultará no Senhor, e se alegrará pelo seu auxílio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14846, 548, 10, 'Todas as minhas potências dirão: “Senhor, quem é semelhante a vós? Vós que livrais o desvalido do opressor, o mísero e o pobre de quem os despoja”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14847, 548, 11, 'Surgiram apaixonadas testemunhas, interrogaram-me sobre faltas que ignoro,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14848, 548, 12, 'pagaram-me o bem com o mal. Oh, desolação para a minha alma!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14849, 548, 13, 'Contudo, quando eles adoeciam, eu me revestia de saco, extenuava-me em jejuns e rezava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14850, 548, 14, 'Andava triste, como se tivesse perdido um amigo, um irmão; abatido, vergava-me como quem chora por sua mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14851, 548, 15, 'Quando tropecei, eles se reuniram para se alegrar; eles me dilaceraram sem parar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14852, 548, 16, 'Puseram-me à prova, escarneceram de mim, rangeram os dentes contra mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14853, 548, 17, 'Senhor, até quando assistireis impassível a este espetáculo? Arrancai desses leões a minha vida, livrai-me a alma de seus rugidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14854, 548, 18, 'Vou render-vos graças publicamente, eu vos louvarei na presença da multidão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14855, 548, 19, 'Não se regozijem de mim meus pérfidos inimigos, nem tramem com os olhos os que me odeiam sem motivo,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14856, 548, 20, 'pois nunca têm palavras de paz: e armam ciladas contra a gente tranquila da terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14857, 548, 21, 'escancaram para mim a boca, dizendo: “Ah! Ah! Com os nossos olhos, nós o vimos!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14858, 548, 22, 'Vós também, Senhor, vistes! Não guardeis silêncio.* Senhor, não vos aparteis de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14859, 548, 23, 'Acordai e levantai-vos para me defender, ó meu Deus e Senhor meu, em prol de minha causa!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14860, 548, 24, 'Julgai-me, Senhor, segundo vossa justiça. Ó meu Deus, que não se regozijem à minha custa!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14861, 548, 25, 'Não pensem em seus corações: “Ah, tivemos sorte!”. Não digam: “Nós o devoramos!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14862, 548, 26, 'Sejam confundidos todos juntos e se envergonhem os que se alegram com meus males, cubram-se de pejo e ignomínia os que se levantam orgulhosamente contra mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14863, 548, 27, 'Mas exultem e se alegrem os favoráveis à minha causa e digam sem cessar: “Glorificado seja o Senhor, que quis a salvação de seu servo!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14864, 548, 28, 'E a minha língua proclamará vossa justiça, dando-vos perpétuos louvores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14865, 549, 1, 'Ao mestre de canto. De Davi, servo do Senhor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14866, 549, 2, 'A iniquidade fala ao ímpio no seu coração; não existe o temor a Deus ante os seus olhos,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14867, 549, 3, 'porque ele se gloria de que sua culpa não será descoberta nem detestada por ninguém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14868, 549, 4, 'Suas palavras são más e enganosas; renunciou a proceder sabiamente e a fazer o bem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14869, 549, 5, 'Em seu leito ele medita o crime, anda pelo mau caminho, não detesta o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14870, 549, 6, 'Senhor, vossa bondade chega até os céus, vossa fidelidade se eleva até as nuvens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14871, 549, 7, 'Vossa justiça é semelhante às montanhas de Deus, vossos juízos são profundos como o mar. Vós protegeis, Senhor, os homens como os animais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14872, 549, 8, 'Como é preciosa a vossa bondade, ó Deus! À sombra de vossas asas se refugiam os filhos dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14873, 549, 9, 'Eles se saciam da abundância de vossa casa, e lhes dais de beber das torrentes de vossas delícias,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14874, 549, 10, 'porque em vós está a fonte da vida, e é na vossa luz que vemos a luz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14875, 549, 11, 'Continuai a dar vossa bondade aos que vos honram, e a vossa justiça aos retos de coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14876, 549, 12, 'Não me calque o pé do orgulhoso, não me faça fugir a mão do pecador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14877, 549, 13, 'Eis que caíram os defensores da iniquidade, foram prostrados para não mais se erguer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14878, 550, 1, 'De Davi. Não te irrites por causa dos que agem mal, nem invejes os que praticam a iniquidade,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14879, 550, 2, 'pois logo eles serão ceifados como a erva dos campos, e como a erva verde murcharão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14880, 550, 3, 'Espera no Senhor e faze o bem; habitarás a terra em plena segurança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14881, 550, 4, 'Põe tuas delícias no Senhor, e os desejos do teu coração ele atenderá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14882, 550, 5, 'Confia ao Senhor a tua sorte, espera nele, e ele agirá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14883, 550, 6, 'Como a luz, fará brilhar a tua justiça; e como o sol do meio-dia, o teu direito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14884, 550, 7, 'Em silêncio, abandona-te ao Senhor, põe tua esperança nele. Não invejes o que prospera em suas empresas, e leva a bom termo seus maus desígnios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14885, 550, 8, 'Guarda-te da ira, depõe o furor, não te exasperes, que será um mal,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14886, 550, 9, 'porque os maus serão exterminados, mas os que esperam no Senhor possuirão a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14887, 550, 10, 'Mais um pouco e não existirá o ímpio; se olhares o seu lugar, não o acharás.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14888, 550, 11, 'Quanto aos mansos, possuirão a terra, e nela gozarão de imensa paz.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14889, 550, 12, 'O ímpio conspira contra o justo, e para ele range os seus dentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14890, 550, 13, 'Mas o Senhor se ri dele, porque vê o destino que o espera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14891, 550, 14, 'Os maus empunham a espada e retesam o arco, para abater o pobre e o miserável, e liquidar os que vão no caminho reto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14892, 550, 15, 'Sua espada, porém, lhes traspassará o coração, e seus arcos serão partidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14893, 550, 16, 'O pouco que o justo possui vale mais que a opulência dos ímpios;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14894, 550, 17, 'porque os braços dos ímpios serão quebrados, mas os justos o Senhor sustenta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14895, 550, 18, 'O Senhor vela pela vida dos íntegros, e a herança deles será eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14896, 550, 19, 'Não serão confundidos no tempo da desgraça e nos dias de fome serão saciados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14897, 550, 20, 'Porém, os ímpios perecerão e os inimigos do Senhor fenecerão como o verde dos prados; desaparecerão como a fumaça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14898, 550, 21, 'O ímpio pede emprestado e não paga, enquanto o justo se compadece e dá,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14899, 550, 22, 'porque aqueles que o Senhor abençoa possuirão a terra, mas os que ele amaldiçoa serão destruídos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14900, 550, 23, 'O Senhor torna firmes os passos do homem e aprova os seus caminhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14901, 550, 24, 'Ainda que caia, não ficará prostrado, porque o Senhor o sustenta pela mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14902, 550, 25, 'Fui jovem e já sou velho, mas jamais vi o justo abandonado, nem seus filhos a mendigar o pão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14903, 550, 26, 'Todos os dias empresta misericordiosamente, e abençoada é a sua posteridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14904, 550, 27, 'Aparta-te do mal e faze o bem, para que permaneças para sempre,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14905, 550, 28, 'porque o Senhor ama a justiça e não abandona os seus fiéis. Os ímpios serão destruídos, e a raça dos ímpios exterminada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14906, 550, 29, 'Os justos possuirão a terra, e a habitarão eternamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14907, 550, 30, 'A boca do justo fala sabedoria e a sua língua exprime a justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14908, 550, 31, 'Em seu coração está gravada a Lei de Deus; não vacilam os seus passos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14909, 550, 32, 'O ímpio espreita o justo, e procura como fazê-lo perecer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14910, 550, 33, 'Mas o Senhor não o abandonará em suas mãos e, quando for julgado, não o condenará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14911, 550, 34, 'Põe tu confiança no Senhor, e segue os seus caminhos. Ele te exaltará e possuirás a terra; a queda dos ímpios verás com alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14912, 550, 35, 'Vi o ímpio cheio de arrogância, a expandir-se com um cedro frondoso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14913, 550, 36, 'Apenas passei e já não existia; procurei-o por toda a parte e nem traço dele encontrei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14914, 550, 37, 'Observa o homem de bem, considera o justo, pois há prosperidade para o pacífico.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14915, 550, 38, 'Os pecadores serão exterminados, a geração dos ímpios será extirpada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14916, 550, 39, 'Vem do Senhor a salvação dos justos, que é seu refúgio no tempo da provação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14917, 550, 40, 'O Senhor os ajuda e liberta; arranca-os dos ímpios e os salva, porque se refugiam nele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14918, 551, 1, 'Salmo de Davi. Para servir de lembrança.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14919, 551, 2, 'Senhor, em vossa cólera não me repreendais, em vosso furor não me castigueis,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14920, 551, 3, 'porque as vossas flechas me atingiram, e desceu sobre mim a vossa mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14921, 551, 4, 'Vossa cólera nada poupou em minha carne, por causa de meu pecado nada há de intato nos meus ossos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14922, 551, 5, 'Porque minhas culpas se elevaram acima de minha cabeça, como pesado fardo me oprimem em demasia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14923, 551, 6, 'São fétidas e purulentas as chagas que a minha loucura me causou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14924, 551, 7, 'Estou abatido, extremamente recurvado, todo o dia ando cheio de tristeza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14925, 551, 8, 'Inteiramente inflamados os meus rins, não há parte sã em minha carne.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14926, 551, 9, 'Ao extremo enfraquecido e alquebrado, agitado o coração, lanço gritos lancinantes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14927, 551, 10, 'Senhor, diante de vós estão todos os meus desejos, e meu gemido não vos é oculto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14928, 551, 11, 'Palpita-me o coração, abandonam-me as forças, e me falta a própria luz dos olhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14929, 551, 12, 'Amigos e companheiros fogem de minha chaga, e meus parentes permanecem longe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14930, 551, 13, 'Os que odeiam a minha vida armam-me ciladas; os que me procuram perder ameaçam-me de morte; não cessam de planejar traições.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14931, 551, 14, 'Eu, porém, sou como um surdo: não ouço; sou como um mudo que não abre os lábios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14932, 551, 15, 'Fiz-me como um homem que não ouve, e que não tem na boca réplicas a dar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14933, 551, 16, 'Porque é em vós, Senhor, que eu espero; vós me atendereis, Senhor, ó meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14934, 551, 17, 'Eis meu desejo: “Não se alegrem com minha perda; não se engrandeçam quando meu pé resvala”;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14935, 551, 18, 'pois estou prestes a cair, e minha dor é permanente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14936, 551, 19, 'Sim, minha culpa eu a confesso, meu pecado me atormenta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14937, 551, 20, 'Entretanto, são vigorosos e fortes os meus inimigos, e muitos os que me odeiam sem razão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14938, 551, 21, 'Retribuem-me o mal pelo bem, hostilizam-me porque quero fazer o bem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14939, 551, 22, 'Não me abandoneis, Senhor. Ó meu Deus, não fiqueis longe de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14940, 551, 23, 'Depressa, vinde em meu auxílio, Senhor, minha salvação!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14941, 552, 1, 'Ao mestre de canto, a Iditun. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14942, 552, 2, 'Disse comigo mesmo: “Velarei sobre os meus atos, para não mais pecar com a língua. Porei um freio em meus lábios, enquanto o ímpio estiver diante de mim”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14943, 552, 3, 'Fiquei mudo, mas sem resultado, porque minha dor recrudesceu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14944, 552, 4, 'Meu coração se abrasava dentro de mim, meu pensamento se acendia como um fogo. Então, eu me pus a falar:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14945, 552, 5, '“Fazei-me conhecer, Senhor, o meu fim, e o número de meus dias, para que eu veja como sou efêmero.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14946, 552, 6, 'A largura da mão: eis a medida de meus dias, diante de vós minha vida é como um nada; todo homem não é mais que um sopro”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14947, 552, 7, 'De fato, o homem passa como uma sombra, é em vão que ele se agita; amontoa, sem saber quem recolherá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14948, 552, 8, 'E agora, Senhor, que posso esperar? Minha confiança está em vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14949, 552, 9, 'Livrai-me de todas as faltas, não me abandoneis ao riso dos insensatos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14950, 552, 10, 'Calei-me, já não abro a boca, porque sois vós que operais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14951, 552, 11, 'Afastai de mim esse flagelo, pois sucumbo ao rigor de vossa mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14952, 552, 12, 'Quando punis o homem, fazendo-lhe sentir a sua culpa, consumis, como o faria a traça, o que ele tem de mais caro. Verdadeiramente, apenas um sopro é o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14953, 552, 13, 'Ouvi, Senhor, a minha oração, escutai os meus clamores, não fiqueis insensível às minhas lágrimas. Diante de vós não sou mais que um viajor, um peregrino, como foram os meus pais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14954, 552, 14, 'Afastai de mim a vossa ira para que eu tome alento, antes que me vá para não mais voltar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14955, 553, 1, 'Ao mestre de canto. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14956, 553, 2, 'Esperei no Senhor com toda a confiança. Ele se inclinou para mim, ouviu meus brados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14957, 553, 3, 'Tirou-me de uma fossa mortal, de um charco de lodo; assentou-me os pés numa rocha, firmou os meus passos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14958, 553, 4, 'pôs-me nos lábios um novo cântico, um hino à glória de nosso Deus. Muitos verão essas coisas e prestarão homenagem a Deus, e confiarão no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14959, 553, 5, 'Feliz o homem que pôs sua esperança no Senhor, e não segue os idólatras nem os apóstatas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14960, 553, 6, 'Senhor, meu Deus, são maravilhosas as vossas inumeráveis obras e ninguém vos assemelha nos desígnios para conosco. Eu quisera anunciá-los e divulgá-los, mas são mais do que se pode contar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14961, 553, 7, 'Não vos comprazeis em nenhum sacrifício, em nenhuma oferenda, mas me abristes os ouvidos: não desejais holocausto nem vítima de expiação.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14962, 553, 8, 'Então, eu disse: “Eis que eu venho. No rolo do livro está escrito de mim:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14963, 553, 9, 'fazer vossa vontade, meu Deus, é o que me agrada, porque vossa Lei está no íntimo de meu coração”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14964, 553, 10, 'Anunciei a justiça na grande assembleia, não cerrei os meus lábios, Senhor, bem o sabeis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14965, 553, 11, 'Não escondi vossa justiça no coração, mas proclamei alto vossa fidelidade e vossa salvação. Não ocultei a vossa bondade nem a vossa fidelidade à grande assembleia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14966, 553, 12, 'E vós, Senhor, não me recuseis vossas misericórdias; protejam-me sempre vossa graça e vossa fidelidade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14967, 553, 13, 'porque males sem conta me cercaram. Minhas faltas me pesaram, a ponto de não aguentar vê-las; mais numerosas que os cabelos de minha cabeça. Sinto-me desfalecer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14968, 553, 14, 'Comprazei-vos, Senhor, em me livrar. Depressa, Senhor, vinde em meu auxílio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14969, 553, 15, 'Sejam confundidos e humilhados os que procuram arrebatar-me a vida. Recuem e corem de vergonha os que se comprazem com meus males.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14970, 553, 16, 'Fiquem atônitos, cheios de confusão, os que me dizem: “Bem feito! Bem feito”!.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14971, 553, 17, 'Ao contrário, exultem e se alegrem em vós todos os que vos procuram; digam sem cessar aqueles que desejam vosso auxílio: “Glória ao Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14972, 553, 18, 'Quanto a mim, sou pobre e desvalido, mas o Senhor vela por mim. Sois meu protetor e libertador: ó meu Deus, não tardeis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14973, 554, 1, 'Ao mestre de canto. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14974, 554, 2, 'Feliz quem se lembra do necessitado e do pobre, porque no dia da desgraça o Senhor o salvará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14975, 554, 3, 'O Senhor há de guardá-lo e o conservará vivo, há de torná-lo feliz na terra e não o abandonará à mercê de seus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14976, 554, 4, 'O Senhor o assistirá no leito de dores, e na sua doença o reconfortará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14977, 554, 5, 'Quanto a mim, eu vos digo: “Piedade para mim, Senhor; sarai-me, porque pequei contra vós”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14978, 554, 6, 'Meus inimigos falam de mim maldizendo: “Quando há de morrer e se extinguir o seu nome?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14979, 554, 7, 'Se alguém me vem visitar, fala hipocritamente. Seu coração recolhe calúnias e, saindo fora, se apressa em divulgá-las.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14980, 554, 8, 'Todos os que me odeiam murmuram contra mim, e só procuram fazer-me mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14981, 554, 9, '“Um mal mortal – dizem eles – o atingiu; ei-lo deitado, para não mais se levantar.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14982, 554, 10, 'Até o próprio amigo em que eu confiava, que partilhava do meu pão, levantou contra mim o calcanhar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14983, 554, 11, 'Ao menos vós, Senhor, tende piedade de mim; erguei-me, para eu lhes dar a paga que merecem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14984, 554, 12, 'Nisso verei que me sois favorável, se meu inimigo não triunfar de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14985, 554, 13, 'Vós, porém, me conservareis incólume, e na vossa presença me poreis para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14986, 554, 14, 'Bendito seja o Senhor, Deus de Israel, de eternidade em eternidade! Assim seja! Assim seja!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14987, 555, 1, 'Ao mestre de canto. Hino dos filhos de Coré.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14988, 555, 2, 'Como a corça anseia pelas águas vivas, assim minha alma suspira por vós, ó meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14989, 555, 3, 'Minha alma tem sede de Deus, do Deus vivo; quando irei contemplar a face de Deus?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14990, 555, 4, 'Minhas lágrimas se converteram em alimento dia e noite, enquanto me repetem sem cessar: “Teu Deus, onde está?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14991, 555, 5, 'Lembro-me, e esta recordação me parte a alma, como ia entre a turba, e os conduzia à casa de Deus, entre gritos de júbilo e louvor de uma multidão em festa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14992, 555, 6, 'Por que te deprimes, ó minha alma, e te inquietas dentro de mim? Espera em Deus, porque ainda hei de louvá-lo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14993, 555, 7, 'ele é minha salvação e meu Deus. Desfalece-me a alma dentro de mim; por isso, penso em vós do longínquo país do Jordão, perto do Hermon e do monte Misar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14994, 555, 8, 'Uma vaga traz outra no fragor das águas revoltas, todos os vagalhões de vossas torrentes passaram sobre mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14995, 555, 9, 'Conceda-me o Senhor de dia a sua graça; e de noite eu cantarei, louvarei ao Deus de minha vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14996, 555, 10, 'Digo a Deus: “Ó meu rochedo, por que me esqueceis? Por que ando eu triste, sob a opressão do inimigo?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14997, 555, 11, 'Sinto meus ossos se quebrarem quando, em seus insultos, meus adversários me repetem todos os dias: “Teu Deus, onde está ele?’’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14998, 555, 12, 'Por que te deprimes, ó minha alma, e te inquietas dentro de mim? Espera em Deus, porque ainda hei de louvá-lo: ele é minha salvação e meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (14999, 556, 1, 'Fazei-me justiça, ó Deus, e defendei minha causa contra uma nação ímpia. Livrai-me do homem doloso e perverso,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (15000, 556, 2, 'pois vós, ó meu Deus, sois a minha fortaleza; por que me repelis? Por que devo andar triste sob a opressão do inimigo?');
