INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16001, 615, 9, 'Insultam-me continuamente os inimigos, em seu furor me atiram imprecações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16002, 615, 10, 'Como cinza do mesmo modo que pão, lágrimas se misturam à minha bebida,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16003, 615, 11, 'devido à vossa cólera indignada, pois me tomastes para me lançar ao longe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16004, 615, 12, 'Os meus dias se esvaecem como a sombra da noite e me vou murchando como a relva.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16005, 615, 13, 'Vós, porém, Senhor, sois eterno, e vosso nome subsiste em todas as gerações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16006, 615, 14, 'Levantai-vos, pois, e sede propício a Sião; é tempo de compadecer-vos dela, chegou a hora...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16007, 615, 15, 'porque vossos servos têm amor aos seus escombros e se condoem de suas ruínas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16008, 615, 16, 'E as nações pagãs reverenciarão o vosso nome, Senhor, e os reis da terra prestarão homenagens à vossa glória.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16009, 615, 17, 'Quando o Senhor tiver reconstruído Sião, e aparecido em sua glória,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16010, 615, 18, 'quando ele aceitar a oração dos desvalidos e não mais rejeitar as suas súplicas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16011, 615, 19, 'escrevam-se estes fatos para a geração futura, e louve o Senhor o povo que há de vir,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16012, 615, 20, 'porque o Senhor olhou do alto de seu santuário, do céu ele contemplou a terra;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16013, 615, 21, 'para escutar os gemidos dos cativos, para livrar da morte os condenados;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16014, 615, 22, 'para que seja aclamado em Sião o nome do Senhor, e em Jerusalém o seu louvor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16015, 615, 23, 'no dia em que se hão de reunir os povos, e os reinos para servir o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16016, 615, 24, 'Deus esgotou-me as forças no meio do caminho, abreviou-me os dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16017, 615, 25, '“Meu Deus, peço, não me leveis no meio da minha vida, vós cujos anos são eternos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16018, 615, 26, 'No começo criastes a terra, e o céu é obra de vossas mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16019, 615, 27, 'Um e outro passarão, enquanto vós ficareis. Tudo se acaba pelo uso como um traje. Como uma veste, vós os substituís e eles hão de sumir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16020, 615, 28, 'Mas vós permaneceis o mesmo e vossos anos não têm fim.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16021, 615, 29, 'Os filhos de vossos servos habitarão seguros, e sua posteridade se perpetuará diante de vós.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16022, 616, 1, 'Salmo de Davi. Bendize, ó minha alma, o Senhor, e tudo o que existe em mim bendiga o seu santo nome.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16023, 616, 2, 'Bendize, ó minha alma, o Senhor, e jamais te esqueças de todos os seus benefícios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16024, 616, 3, 'É ele que perdoa as tuas faltas, e sara as tuas enfermidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16025, 616, 4, 'É ele que salva tua vida da morte, e te coroa de bondade e de misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16026, 616, 5, 'É ele que cumula de benefícios a tua vida, e renova a tua juventude como a da águia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16027, 616, 6, 'O Senhor faz justiça, dá o direito aos oprimidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16028, 616, 7, 'Revelou seus caminhos a Moisés, e suas obras aos filhos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16029, 616, 8, 'O Senhor é bom e misericordioso, lento para a cólera e cheio de clemência.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16030, 616, 9, 'Ele não está sempre a repreender, nem eterno é o seu ressentimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16031, 616, 10, 'Não nos trata segundo os nossos pecados, nem nos castiga em proporção de nossas faltas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16032, 616, 11, 'porque tanto os céus distam da terra quanto sua misericórdia é grande para os que o temem;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16033, 616, 12, 'tanto o oriente dista do ocidente quanto ele afasta de nós nossos pecados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16034, 616, 13, 'Como um pai tem piedade de seus filhos, assim o Senhor tem compaixão dos que o temem,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16035, 616, 14, 'porque ele sabe de que é que somos feitos, e não se esquece de que somos pó.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16036, 616, 15, 'Os dias do homem são semelhantes a erva, ele floresce como a flor dos campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16037, 616, 16, 'Apenas sopra o vento, já não existe, e nem se conhece mais o seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16038, 616, 17, 'É eterna, porém, a misericórdia do Senhor para com os que o temem. E sua justiça se estende aos filhos de seus filhos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16039, 616, 18, 'sobre os que guardam a sua aliança, e, lembrando, cumprem seus mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16040, 616, 19, 'Nos céus estabeleceu o Senhor o seu trono, e o seu império se estende sobre o universo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16041, 616, 20, 'Bendizei o Senhor todos os seus anjos, valentes heróis que cumpris suas ordens, sempre dóceis à sua palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16042, 616, 21, 'Bendizei o Senhor todos os seus exércitos, ministros que executais sua vontade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16043, 616, 22, 'Bendizei o Senhor todas as suas obras, em todos os lugares onde ele domina. Bendize, ó minha alma, o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16044, 617, 1, 'Bendize, ó minha alma, o Senhor! Senhor, meu Deus, vós sois imensamente grande! De majestade e esplendor vos revestis,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16045, 617, 2, 'envolvido de luz como de um manto. Vós estendestes o céu qual pavilhão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16046, 617, 3, 'acima das águas fixastes vossa morada. De nuvens fazeis vosso carro, andais nas asas do vento;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16047, 617, 4, 'fazeis dos ventos os vossos mensageiros, e dos flamejantes relâmpagos vossos ministros.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16048, 617, 5, 'Fundastes a terra em bases sólidas que são eternamente inabaláveis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16049, 617, 6, 'Vós a tínheis coberto com o manto do oceano, as águas ultrapassavam as montanhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16050, 617, 7, 'Mas à vossa ameaça elas se afastaram, ao estrondo de vosso trovão estremeceram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16051, 617, 8, 'Elevaram-se as montanhas, sulcaram-se os vales nos lugares que vós lhes destinastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16052, 617, 9, 'Estabelecestes os limites, que elas não hão de ultrapassar, para que não mais tornem a cobrir a terra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16053, 617, 10, 'Mandastes as fontes correr em riachos, que serpeiam por entre os montes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16054, 617, 11, 'Ali vão beber os animais dos campos, neles matam a sede os asnos selvagens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16055, 617, 12, 'Os pássaros do céu vêm aninhar em suas margens, e cantam entre as folhagens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16056, 617, 13, 'Do alto de vossas moradas derramais a chuva nas montanhas, do fruto de vossas obras se farta a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16057, 617, 14, 'Fazeis brotar a relva para o gado, e plantas úteis ao homem, para que da terra possa extrair o pão');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16058, 617, 15, 'e o vinho que alegra o coração do homem, o óleo que lhe faz brilhar o rosto e o pão que lhe sustenta as forças.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16059, 617, 16, 'As árvores do Senhor são cheias de seiva, assim como os cedros do Líbano que ele plantou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16060, 617, 17, 'Lá constroem as aves os seus ninhos, nos ciprestes a cegonha tem sua casa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16061, 617, 18, 'Os altos montes dão abrigo às cabras, e os rochedos aos arganazes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16062, 617, 19, 'Fizestes a lua para indicar os tempos; o sol conhece a hora de se pôr.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16063, 617, 20, 'Mal estendeis as trevas e já se faz noite, entram a rondar os animais das selvas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16064, 617, 21, 'Rugem os leõezinhos por sua presa, e pedem a Deus o seu sustento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16065, 617, 22, 'Mas se retiram ao raiar do sol, e vão se deitar em seus covis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16066, 617, 23, 'É então que o homem sai para o trabalho, e trabalha sem descanso até o entardecer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16067, 617, 24, 'Ó Senhor, quão variadas são as vossas obras! Feitas, todas, com sabedoria, a terra está cheia das coisas que criastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16068, 617, 25, 'Eis o mar, imenso e vasto, onde, sem conta, se agitam animais grandes e pequenos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16069, 617, 26, 'Nele navegam as naus e o Leviatã que criastes para brincar nas ondas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16070, 617, 27, 'Todos esses seres esperam de vós que lhes deis de comer em seu tempo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16071, 617, 28, 'Vós lhes dais e eles o recolhem; abris a mão, e se fartam de bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16072, 617, 29, 'Se desviais o rosto, eles se perturbam; se lhes retirais o sopro, expiram e voltam ao pó donde saíram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16073, 617, 30, 'Se enviais, porém, o vosso sopro, eles revivem e renovais a face da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16074, 617, 31, 'Ao Senhor, glória eterna; alegre-se o Senhor em suas obras!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16075, 617, 32, 'Ele, cujo olhar basta para fazer tremer a terra, e cujo contato inflama as montanhas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16076, 617, 33, 'Enquanto viver, cantarei à glória do Senhor, salmodiarei o meu Deus enquanto existir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16077, 617, 34, 'Possam minhas palavras lhe ser agradáveis! Minha única alegria se encontra no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16078, 617, 35, 'Sejam tirados da terra os pecadores e doravante desapareçam os ímpios. Bendize, ó minha alma, o Senhor! Aleluia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16079, 618, 1, 'Aleluia. Celebrai o Senhor, aclamai o seu nome, apregoai entre as nações as suas obras.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16080, 618, 2, 'Cantai-lhe hinos e cânticos, anunciai todas as suas maravilhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16081, 618, 3, 'Gloriai-vos do seu santo nome; rejubile o coração dos que procuram o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16082, 618, 4, 'Recorrei ao Senhor e ao seu poder, procurai continuamente sua face.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16083, 618, 5, 'Recordai as maravilhas que operou, seus prodígios e julgamentos por seus lábios proferidos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16084, 618, 6, 'ó descendência de Abraão, seu servidor, ó filhos de Jacó, seus escolhidos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16085, 618, 7, 'É ele o Senhor, nosso Deus; suas sentenças comandam a terra inteira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16086, 618, 8, 'Ele se lembra eternamente de sua aliança, da palavra que empenhou a mil gerações,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16087, 618, 9, 'que garantiu a Abraão, e jurou a Isaac,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16088, 618, 10, 'e confirmou a Jacó irrevogavelmente, e a Israel como aliança eterna,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16089, 618, 11, 'quando disse: “Eu te darei a terra de Canaã, como parte de vossa herança”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16090, 618, 12, 'Quando não passavam de um reduzido número, minoria insignificante e estrangeiros na terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16091, 618, 13, 'e andavam errantes de nação em nação, de reino em reino,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16092, 618, 14, 'não permitiu que os oprimissem, e castigou a reis por causa deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16093, 618, 15, '“Não ouseis tocar nos que me são consagrados, nem maltratar os meus profetas.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16094, 618, 16, 'E chamou a fome sobre a terra, e os privou do pão que os sustentava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16095, 618, 17, 'Diante deles enviara um homem: José, que fora vendido como escravo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16096, 618, 18, 'Apertaram-lhe os pés entre grilhões, com cadeias cingiram-lhe o pescoço,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16097, 618, 19, 'até que se cumpriu a profecia, e o justificou a palavra de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16098, 618, 20, 'Então o rei ordenou que o soltassem, o soberano de povos o livrou,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16099, 618, 21, 'e o nomeou senhor de sua casa e governador de seus domínios,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16100, 618, 22, 'para, a seu bel-prazer, dar ordens a seus príncipes, e a seus anciãos, lições de sabedoria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16101, 618, 23, 'Então Israel penetrou no Egito, Jacó foi viver na terra de Cam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16102, 618, 24, 'Deus multiplicou grandemente o seu povo, e o tornou mais forte que seus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16103, 618, 25, 'Depois, de tal modo lhes mudou os corações, que com aversão trataram o seu povo, e com perfídia, os seus servidores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16104, 618, 26, 'Mas Deus lhes suscitou Moisés, seu servo, e Aarão, seu escolhido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16105, 618, 27, 'Ambos operaram entre eles prodígios e milagres na terra de Cam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16106, 618, 28, 'Mandou trevas e se fez noite: resistiram, porém, às suas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16107, 618, 29, 'Converteu-lhes as águas em sangue, matando-lhes todos os seus peixes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16108, 618, 30, 'Infestou-lhes a terra de rãs, até nos aposentos reais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16109, 618, 31, 'A uma palavra sua vieram nuvens de moscas, mosquitos em todo o seu território.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16110, 618, 32, 'Em vez de chuva lhes mandou granizo e chamas devorantes sobre a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16111, 618, 33, 'Devastou-lhes as vinhas e figueiras, e partiu-lhes as árvores de seus campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16112, 618, 34, 'A seu mando vieram os gafanhotos, e lagartas em quantidade enorme,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16113, 618, 35, 'que devoraram toda a erva de suas terras e comeram os frutos de seus campos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16114, 618, 36, 'Depois matou os primogênitos do seu povo, primícias de sua virilidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16115, 618, 37, 'E Deus tirou os hebreus carregados de ouro e prata; não houve, nas tribos, nenhum enfermo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16116, 618, 38, 'Alegraram-se os egípcios com sua partida, pelo temor que os hebreus lhes tinham causado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16117, 618, 39, 'Para os abrigar Deus estendeu uma nuvem, e para lhes iluminar a noite uma coluna de fogo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16118, 618, 40, 'A seu pedido, mandou-lhes codornizes, e os fartou com pão vindo do céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16119, 618, 41, 'Abriu o rochedo e jorrou água como um rio a correr pelo deserto,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16120, 618, 42, 'pois se lembrava da palavra sagrada, empenhada a seu servo Abraão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16121, 618, 43, 'E fez sair, com júbilo, o seu povo, e seus eleitos com grande exultação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16122, 618, 44, 'Deu-lhes a terra dos pagãos e desfrutaram das riquezas desses povos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16123, 618, 45, 'sob a condição de guardarem seus mandamentos e observarem fielmente suas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16124, 619, 1, 'Aleluia. Louvai o Senhor porque ele é bom, porque a sua misericórdia é eterna.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16125, 619, 2, 'Quem contará os poderosos feitos do Senhor? Quem poderá apregoar os seus louvores?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16126, 619, 3, 'Felizes aqueles que observam os preceitos, aqueles que, em todo o tempo, fazem o que é reto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16127, 619, 4, 'Lembrai-vos de mim, Senhor, pela benevolência que tendes com o vosso povo. Assisti-me com o vosso socorro,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16128, 619, 5, 'para que eu prove a felicidade de vossos eleitos, compartilhe do júbilo de vosso povo e me glorie com os que constituem vossa herança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16129, 619, 6, 'Como nossos pais, nós também pecamos, cometemos a iniquidade, praticamos o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16130, 619, 7, 'Nossos pais, no Egito, não prezaram os vossos milagres, esqueceram a multidão de vossos benefícios e se revoltaram contra o Altíssimo no mar Vermelho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16131, 619, 8, 'Mas ele os poupou para a honra de seu nome, para tornar patente o seu poder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16132, 619, 9, 'Ameaçou o mar e ele se tornou seco, e os conduziu por entre as ondas como através de um deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16133, 619, 10, 'Livrou-os das mãos daquele que os odiava, e os salvou do poder inimigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16134, 619, 11, 'As águas recobriram seus adversários, nenhum deles escapou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16135, 619, 12, 'Então acreditaram em sua palavra, e cantaram os seus louvores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16136, 619, 13, 'Depressa, porém, esqueceram suas obras, e não confiaram em seus desígnios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16137, 619, 14, 'Entregaram-se à concupiscência no deserto, e tentaram a Deus na solidão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16138, 619, 15, 'Ele lhes concedeu o que pediam, mas os feriu de um mal mortal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16139, 619, 16, 'Em seus acampamentos invejaram Moisés e Aarão, o eleito do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16140, 619, 17, 'Abriu-se a terra e tragou Datã, e sepultou os sequazes de Abiram.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16141, 619, 18, 'Um fogo devassou as suas tropas e as chamas consumiram os ímpios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16142, 619, 19, 'Fabricaram um bezerro de ouro no sopé do Horeb, e adoraram um ídolo de ouro fundido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16143, 619, 20, 'Eles trocaram a sua glória pela estátua de um touro que come feno.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16144, 619, 21, 'Esqueceram a Deus que os salvara, que obrara prodígios no Egito,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16145, 619, 22, 'maravilhas na terra de Cam, estupendos feitos no mar Vermelho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16146, 619, 23, 'Já cogitava em exterminá-los se Moisés, seu eleito, não intercedesse junto dele para impedir que sua cólera os destruísse.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16147, 619, 24, 'Depois, eles desprezaram uma terra de delícias, desconfiados de sua palavra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16148, 619, 25, 'Em suas tendas se puseram a murmurar, e desobedeceram ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16149, 619, 26, 'Então, com a mão alçada, ele jurou que havia de prostrá-los no deserto');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16150, 619, 27, 'e dispersar sua descendência entre as nações pagãs, disseminando-os por toda a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16151, 619, 28, 'Aderiram também ao Baal de Fegor, comeram vítimas oferecidas a deuses sem vida.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16152, 619, 29, 'E, provocando-o com seus crimes, uma peste irrompeu entre eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16153, 619, 30, 'Mas levantou-se Fineias para fazer justiça: cessou a peste.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16154, 619, 31, 'Seu zelo lhe foi imputado como mérito, de geração em geração, para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16155, 619, 32, 'Em seguida, irritaram a Deus nas águas de Meriba, e adveio o mal a Moisés por causa deles.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16156, 619, 33, 'Porque o provocaram tanto, palavras temerárias saíram-lhe dos lábios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16157, 619, 34, 'Não exterminaram os povos, como o Senhor lhes havia ordenado,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16158, 619, 35, 'mas se misturaram com as nações pagãs e aprenderam seus costumes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16159, 619, 36, 'Prestaram culto aos seus ídolos, que se tornaram um laço para eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16160, 619, 37, 'Imolaram os seus filhos e suas filhas aos demônios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16161, 619, 38, 'Derramaram o sangue inocente: o sangue de seus filhos e de suas filhas, que aos ídolos de Canaã sacrificaram; seu país ficou manchado com esse sangue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16162, 619, 39, 'Eles se contaminaram com homicídios, e se prostituíram com seus crimes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16163, 619, 40, 'Então se inflamou contra seu povo a cólera divina, e Deus teve aversão de sua herança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16164, 619, 41, 'Ele os entregou nas mãos das nações pagãs, e foram dominados pelos que os odiavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16165, 619, 42, 'Oprimiram-nos os seus inimigos; foram submetidos ao seu jugo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16166, 619, 43, 'Muitas vezes ele os libertou, mas sua conduta o exasperou de tal modo que foram abatidos por causa de suas iniquidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16167, 619, 44, 'Entretanto, vendo a sua aflição, ouviu-lhes as orações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16168, 619, 45, 'Em favor deles lembrou-se de sua aliança, e por sua misericórdia deles se apiedou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16169, 619, 46, 'E fez com que encontrassem a clemência junto aos que os tinham aprisionado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16170, 619, 47, 'Salvai-nos, Senhor, nosso Deus, e recolhei-nos de entre as nações, para que possamos celebrar o vosso santo nome e ter a satisfação de vos louvar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16171, 619, 48, 'Bendito seja o Senhor, Deus de Israel, pelos séculos dos séculos! E que todo o povo diga: “Amém!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16172, 620, 1, 'Aleluia. Louvai o Senhor, porque ele é bom. Porque eterna é a sua misericórdia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16173, 620, 2, 'Assim o dizem aqueles que o Senhor resgatou, aqueles que ele livrou das mãos do opressor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16174, 620, 3, 'assim como os que congregou de todos os países, do oriente e do ocidente, do norte e do sul.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16175, 620, 4, 'Erravam na solidão do deserto, sem encontrar caminho de cidade habitável.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16176, 620, 5, 'Consumidos de fome e de sede, sentiam desfalecer-lhes a vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16177, 620, 6, 'Em sua angústia clamaram então ao Senhor, ele os livrou de suas tribulações');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16178, 620, 7, 'e os conduziu pelo bom caminho, para chegarem a uma cidade habitável.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16179, 620, 8, 'Agradeçam ao Senhor por sua bondade, e por suas grandes obras em favor dos homens,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16180, 620, 9, 'porque dessedentou a garganta sequiosa, e cumulou de bens a que tinha fome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16181, 620, 10, 'Outros estavam nas trevas e na sombra da morte, prisioneiros na miséria e em ferros,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16182, 620, 11, 'por se haverem revoltado contra as ordens de Deus e terem desprezado os desígnios do Altíssimo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16183, 620, 12, 'Pelo sofrimento lhes humilhara o coração, sucumbiam sem que ninguém os socorresse.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16184, 620, 13, 'Em sua angústia clamaram então para o Senhor, e ele os livrou de suas tribulações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16185, 620, 14, 'Tirou-os das trevas e da sombra da morte, quebrou-lhes os grilhões.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16186, 620, 15, 'Agradeçam ao Senhor por sua bondade, e por suas grandes obras em favor dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16187, 620, 16, 'Ele arrombou as portas de bronze, e despedaçou os ferrolhos de ferro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16188, 620, 17, 'Outros, enfermos por causa de seu mau proceder, eram feridos por causa de seus pecados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16189, 620, 18, 'Todo alimento lhes causava náuseas, chegaram às portas da morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16190, 620, 19, 'Em sua angústia clamaram então ao Senhor; ele os livrou de suas tribulações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16191, 620, 20, 'Enviou a sua palavra para curá-los, para os arrancar da morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16192, 620, 21, 'Agradeçam ao Senhor por sua bondade, e por suas grandes obras em favor dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16193, 620, 22, 'Ofereçam sacrifícios de ação de graças, e proclamem alegremente as suas obras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16194, 620, 23, 'Os que se fizeram ao mar, para trafegar nas muitas águas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16195, 620, 24, 'foram testemunhas das obras do Senhor e de suas maravilhas no alto-mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16196, 620, 25, 'Sua palavra levantou tremendo vento, que impeliu para o alto as suas ondas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16197, 620, 26, 'Subiam até os céus, desciam aos abismos, suas almas definhavam em angústias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16198, 620, 27, 'Titubeavam e cambaleavam como ébrios, e toda a sua perícia se esvaiu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16199, 620, 28, 'Em sua agonia clamaram então ao Senhor, e ele os livrou da tribulação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16200, 620, 29, 'Transformou a tempestade em leve brisa, e as ondas do mar silenciaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16201, 620, 30, 'E se alegraram porque elas amainaram, e os conduziu ao desejado porto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16202, 620, 31, 'Agradeçam eles ao Senhor por sua bondade, e por suas grandes obras em favor dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16203, 620, 32, 'Celebrem-no na assembleia do povo, e o louvem no conselho dos anciãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16204, 620, 33, 'Transformou rios em deserto, e fontes de água em terra árida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16205, 620, 34, 'Converteu o solo fértil em salinas, por causa da malícia de seus habitantes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16206, 620, 35, 'Mudou o deserto em lençol de água, e a terra árida em abundantes fontes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16207, 620, 36, 'Aí fez habitar os esfaimados, que fundaram uma cidade para morar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16208, 620, 37, 'E semearam os campos e plantaram vinhas, colhendo deles abundantes frutos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16209, 620, 38, 'E os abençoou: eles se multiplicaram grandemente, e lhes concedeu rebanhos numerosos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16210, 620, 39, 'Depois seu número se reduziu e caíram na miséria, sob a opressão, a desgraça e o sofrimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16211, 620, 40, 'Mas aquele que lança seu desprezo sobre os grandes, e os faz errar por intransitáveis desertos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16212, 620, 41, '[Deus] soergueu o pobre da miséria, multiplicando famílias como rebanhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16213, 620, 42, 'À vista disso os justos se alegram, e toda a maldade deve fechar a boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16214, 620, 43, 'Quem é sábio para julgar estas coisas e compreender as misericórdias do Senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16215, 621, 1, 'Cântico. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16216, 621, 2, 'Meu coração está firme, ó Deus, meu coração está firme; vou cantar e salmodiar. Desperta-te, ó minha alma;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16217, 621, 3, 'despertai-vos, harpa e cítara; quero acordar a aurora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16218, 621, 4, 'Entre os povos, Senhor, vos louvarei; salmodiarei a vós entre as nações,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16219, 621, 5, 'porque acima do céu se eleva a vossa misericórdia, e até as nuvens a vossa fidelidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16220, 621, 6, 'Resplandecei, ó Deus, nas alturas do céu, e brilhe a vossa glória sobre a terra inteira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16221, 621, 7, 'Para ficarem livres vossos amigos, ajudai-nos com vossa mão, ouvi-nos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16222, 621, 8, 'Deus falou no seu santuário: “Triunfarei, e me apoderarei de Siquém, medirei com o cordel o vale de Sucot.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16223, 621, 9, 'Minha é a terra de Galaad, minha a de Manassés; Efraim será o elmo de minha cabeça; Judá, o meu cetro;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16224, 621, 10, 'Moab, a bacia em que me lavo. Sobre Edom porei minhas sandálias, cantarei vitória sobre a Filisteia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16225, 621, 11, 'Quem me conduzirá à cidade fortificada? Quem me levará até Edom?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16226, 621, 12, 'Quem, senão vós, Senhor, que nos repelistes, e já não andais à frente dos nossos exércitos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16227, 621, 13, 'Dai-nos auxílio contra o inimigo, porque é vão qualquer socorro humano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16228, 621, 14, 'Com Deus faremos proezas: ele esmagará os nossos inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16229, 622, 1, 'Ao mestre de canto. Salmo de Davi. Ó Deus de meu louvor, não fiqueis insensível,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16230, 622, 2, 'porque contra mim se abriu boca ímpia e pérfida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16231, 622, 3, 'Falaram-me com palavras mentirosas, com discursos odiosos me envolveram; e sem motivo me atacaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16232, 622, 4, 'Em resposta ao meu afeto me acusaram. Eu, porém, orava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16233, 622, 5, 'Pagaram-me o bem com o mal, e o amor com o ódio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16234, 622, 6, 'Suscitai contra ele um ímpio, levante-se à sua direita um acusador.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16235, 622, 7, 'Quando o julgarem, saia condenado, e sem efeito o seu recurso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16236, 622, 8, 'Sejam abreviados os seus dias, tome outro o seu encargo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16237, 622, 9, 'Fiquem órfãos os seus filhos, e viúva a sua esposa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16238, 622, 10, 'Andem errantes e mendigos os seus filhos, expulsos de suas casas devastadas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16239, 622, 11, 'Arrebate o credor todos os seus bens, estrangeiros pilhem o fruto de seu trabalho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16240, 622, 12, 'Ninguém lhes tenha misericórdia, nem haja quem se condoa de seus órfãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16241, 622, 13, 'Exterminada seja a sua descendência, extinga-se o seu nome desde a segunda geração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16242, 622, 14, 'Conserve o Senhor a lembrança da culpa de seus pais, jamais se apague o pecado de sua mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16243, 622, 15, 'Deus os tenha sempre presentes na memória, e risque-se da terra a sua lembrança,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16244, 622, 16, 'porque jamais pensou em ter misericórdia, mas perseguiu o pobre e desvalido e teve ódio mortal ao homem de coração abatido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16245, 622, 17, 'Amou a maldição: que ela caia sobre ele! Recusou a bênção: que ela o abandone!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16246, 622, 18, 'Seja coberto de maldição como de um manto: que ela penetre em suas entranhas como água e se infiltre em seus ossos como óleo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16247, 622, 19, 'Seja-lhe como a veste que o cobre, como um cinto que o cinja para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16248, 622, 20, 'Esta, a paga do Senhor àqueles que me acusam e que só dizem mal de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16249, 622, 21, 'Mas vós, Senhor Deus, tratai-me segundo a honra de vosso nome. Salvai-me em nome de vossa benigna misericórdia,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16250, 622, 22, 'porque sou pobre e miserável; trago, dentro de mim, um coração ferido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16251, 622, 23, 'Vou-me extinguindo como a sombra da tarde que declina, sou levado para longe como o gafanhoto.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16252, 622, 24, 'Vacilam-me os joelhos à força de jejuar, e meu corpo se definha de magreza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16253, 622, 25, 'Fizeram-me objeto de escárnio, abanam a cabeça ao me ver.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16254, 622, 26, 'Ajudai-me, Senhor, meu Deus. Salvai-me segundo a vossa misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16255, 622, 27, 'Que reconheçam aqui a vossa mão, e saibam que fostes vós que assim fizestes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16256, 622, 28, 'Enquanto amaldiçoam, abençoai-me. Sejam confundidos os que se insurgem contra mim, e que vosso servo seja cumulado de alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16257, 622, 29, 'Cubram-se de ignomínia meus detratores, e envolvam-se de vergonha como de um manto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16258, 622, 30, 'Celebrarei altamente o Senhor, e o louvarei em meio à multidão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16259, 622, 31, 'porque ele se pôs à direita do pobre, para o salvar dos que o condenam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16260, 623, 1, 'Salmo de Davi. Eis o oráculo do Senhor que se dirige a meu Senhor: “Assenta-te à minha direita, até que eu faça de teus inimigos o escabelo de teus pés”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16261, 623, 2, 'O Senhor estenderá desde Sião teu cetro poderoso: “Dominarás – disse ele –, até no meio de teus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16262, 623, 3, 'No dia de teu nascimento, já possuis a realeza no esplendor da santidade; semelhante ao orvalho, eu te gerei antes da aurora”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16263, 623, 4, 'O Senhor jurou e não se arrependerá: “Tu és sacerdote para sempre, segundo a ordem de Melquisedec”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16264, 623, 5, 'O Senhor está à tua direita: ele destruirá os reis no dia de sua cólera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16265, 623, 6, 'Julgará os povos pagãos, empilhará cadáveres; por toda a terra esmagará cabeças.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16266, 623, 7, 'Beberá da torrente no caminho; por isso, erguerá a sua fronte.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16267, 624, 1, 'Aleluia. Louvarei o Senhor, de todo o coração, na assembleia dos justos e em seu conselho.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16268, 624, 2, 'Grandes são as obras do Senhor, dignas de admiração de todos os que as amam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16269, 624, 3, 'Sua obra é toda ela majestade e magnificência. E eterna a sua justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16270, 624, 4, 'Memoráveis são suas obras maravilhosas; o Senhor é clemente e misericordioso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16271, 624, 5, 'Aos que o temem deu-lhes o sustento; será lembrada eternamente a sua aliança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16272, 624, 6, 'Mostrou ao seu povo o poder de suas obras, dando-lhe a herança das nações pagãs.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16273, 624, 7, 'As obras de suas mãos são verdade e justiça, imutáveis os seus preceitos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16274, 624, 8, 'irrevogáveis pelos séculos eternos, instituídos com justiça e equidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16275, 624, 9, 'Enviou a seu povo a redenção, concluiu com ele uma aliança eterna. Santo e venerável é o seu nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16276, 624, 10, 'O temor do Senhor é o começo da sabedoria; sábios são aqueles que o adoram. Sua glória subsiste eternamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16277, 625, 1, 'Aleluia. Feliz o homem que teme o Senhor, e põe o seu prazer em observar os seus mandamentos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16278, 625, 2, 'Será poderosa sua descendência na terra, e bendita a raça dos homens retos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16279, 625, 3, 'Suntuosa riqueza haverá em sua casa, e para sempre durará sua abundância.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16280, 625, 4, 'Como luz, se eleva, nas trevas, para os retos, o homem benfazejo, misericordioso e justo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16281, 625, 5, 'Feliz o homem que se compadece e empresta, que regula suas ações pela justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16282, 625, 6, 'Nada jamais há de abalá-lo: eterna será a memória do justo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16283, 625, 7, 'Não temerá notícias funestas, porque seu coração está firme e confiante no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16284, 625, 8, 'Inabalável é seu coração, livre de medo, até que possa ver confundidos os seus adversários.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16285, 625, 9, 'Com largueza distribuiu, deu aos pobres; sua liberalidade permanecerá para sempre. Pode levantar a cabeça com altivez.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16286, 625, 10, 'O pecador, porém, não pode vê-lo sem inveja, range os dentes e definha; anulam-se, assim, os desejos dos maus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16287, 626, 1, 'Aleluia. Louvai, ó servos do Senhor, louvai o nome do Senhor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16288, 626, 2, 'Bendito seja o nome do Senhor, agora e para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16289, 626, 3, 'Desde o nascer ao pôr-do-sol, seja louvado o nome do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16290, 626, 4, 'O Senhor é excelso sobre todos os povos, sua glória ultrapassa a altura do céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16291, 626, 5, 'Quem se compara ao Senhor, nosso Deus, que tem seu trono nas alturas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16292, 626, 6, 'e do alto olha o céu e a terra?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16293, 626, 7, 'Ele levanta do pó o indigente e tira o pobre da imundície,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16294, 626, 8, 'para, entre os príncipes, fazê-lo sentar, junto dos grandes de seu povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16295, 626, 9, 'E a mulher, que, antes, era estéril, ele a faz, em sua casa, mãe feliz de muitos filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16296, 627, 1, 'Aleluia. Quando Israel saiu do Egito, e a casa de Jacó se apartou de um povo bárbaro,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16297, 627, 2, 'a terra de Judá tornou-se o santuário do Senhor, e Israel seu reino.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16298, 627, 3, 'O mar, à vista disso, fugiu, o Jordão volveu atrás.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16299, 627, 4, 'Os montes saltaram como carneiros; as colinas, como cordeiros.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16300, 627, 5, 'Que tens, ó mar, para assim fugires? E tu, Jordão, para retrocederes para a tua fonte?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16301, 627, 6, 'Ó montes, por que saltastes como carneiros, e vós, colinas, como cordeiros?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16302, 627, 7, 'Ante a face de Deus, treme, ó terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16303, 627, 8, 'por quem o rochedo se mudou em lençol de água, e a pedra em fonte de água viva.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16304, 627, 9, '(1) Não a nós, Senhor, não a nós, mas ao vosso nome dai glória, por amor de vossa misericórdia e fidelidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16305, 627, 10, '(2) Por que diriam as nações pagãs: “Onde está o Deus deles?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16306, 627, 11, '(3) Nosso Deus está nos céus; ele faz tudo o que lhe apraz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16307, 627, 12, '(4) Quanto a seus ídolos de ouro e prata, são eles simples obras da mão dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16308, 627, 13, '(5) Têm boca, mas não falam, olhos e não podem ver,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16309, 627, 14, '(6) têm ouvidos, mas não ouvem; nariz e não podem cheirar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16310, 627, 15, '(7) Têm mãos, mas não apalpam; pés e não podem andar, sua garganta não emite som algum.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16311, 627, 16, '(8) Semelhantes a eles sejam os que os fabricam e quantos neles põem sua confiança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16312, 627, 17, '(9) Mas Israel, ao contrário, confia no Senhor: ele é o seu amparo e o seu escudo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16313, 627, 18, '(10) Aarão confia no Senhor: ele é o seu amparo e o seu escudo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16314, 627, 19, '(11) Confiam no Senhor os que temem o Senhor: ele é o seu amparo e o seu escudo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16315, 627, 20, '(12) O Senhor se lembra de nós e nos dará a sua bênção; abençoará a casa de Israel, abençoará a casa de Aarão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16316, 627, 21, '(13) abençoará os que temem ao Senhor, os pequenos como os grandes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16317, 627, 22, '(14) O Senhor há de vos multiplicar, vós e vossos filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16318, 627, 23, '(15) Sede os benditos do Senhor, que fez o céu e a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16319, 627, 24, '(16) O céu é o céu do Senhor, mas a terra ele a deu aos filhos de Adão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16320, 627, 25, '(17) Não são os mortos que louvam o Senhor, nem nenhum daqueles que descem aos lugares infernais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16321, 627, 26, '(18) Mas somos nós que bendizemos o Senhor, agora e para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16322, 628, 1, 'Aleluia. Amo o Senhor, porque ele ouviu a voz de minha súplica,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16323, 628, 2, 'porque inclinou para mim os seus ouvidos no dia em que o invoquei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16324, 628, 3, 'Os laços da morte me envolviam, a rede da habitação dos mortos me apanhou de improviso; estava abismado na aflição e na ansiedade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16325, 628, 4, 'Foi então que invoquei o nome do Senhor: “Ó Senhor, salvai-me a vida!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16326, 628, 5, 'O Senhor é bom e justo, cheio de misericórdia é nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16327, 628, 6, 'O Senhor cuida dos corações simples; achava-me na miséria e ele me salvou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16328, 628, 7, 'Volta, minha alma, à tua serenidade, porque o Senhor foi bom para contigo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16329, 628, 8, 'pois livrou-me a alma da morte, preservou-me os olhos do pranto, os pés da queda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16330, 628, 9, 'Na presença do Senhor continuarei o meu caminho na terra dos vivos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16331, 629, 1, '(10) Salmo. Conservei a confiança ainda quando podia dizer: “Em verdade sou extremamente infeliz”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16332, 629, 2, '(11) Em meu pavor eu dizia: “O homem é um apoio falaz”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16333, 629, 3, '(12) Mas que poderei retribuir ao Senhor por tudo o que ele me tem dado?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16334, 629, 4, '(13) Erguerei o cálice da salvação, invocando o nome do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16335, 629, 5, '(14) Cumprirei os meus votos para com o Senhor, na presença de todo o seu povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16336, 629, 6, '(15) É penoso para o Senhor ver morrer os seus fiéis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16337, 629, 7, '(16) Senhor, eu sou vosso servo; vosso servo, filho de vossa serva: quebrastes os meus grilhões.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16338, 629, 8, '(17) Irei oferecer-vos um sacrifício de louvor, invocando o nome do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16339, 629, 9, '(18) Cumprirei os meus votos para com o Senhor, na presença de todo o seu povo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16340, 629, 10, '(19) nos átrios da casa do Senhor, no teu recinto, ó Jerusalém!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16341, 630, 1, 'Aleluia. Louvai ao Senhor todas as nações, louvai-o todos os povos,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16342, 630, 2, 'porque sem limites é a sua misericórdia para conosco, e eterna a fidelidade do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16343, 631, 1, 'Aleluia. Louvai o Senhor, porque ele é bom; porque eterna é a sua misericórdia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16344, 631, 2, 'Diga a casa de Israel: “Eterna é sua misericórdia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16345, 631, 3, 'Proclame a casa de Aarão: “Eterna é sua misericórdia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16346, 631, 4, 'E vós, que temeis o Senhor, repeti: “Eterna é a sua misericórdia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16347, 631, 5, 'Na tribulação invoquei o Senhor; ouviu-me o Senhor e me livrou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16348, 631, 6, 'Comigo está o Senhor, nada temo; que mal me poderia ainda fazer um homem?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16349, 631, 7, 'Comigo está o Senhor, meu amparo; verei logo a ruína dos meus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16350, 631, 8, 'Mais vale procurar refúgio no Senhor do que confiar no homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16351, 631, 9, 'Mais vale procurar refúgio no Senhor do que confiar nos grandes da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16352, 631, 10, 'Ainda que me cercassem todas as nações pagãs, eu as esmagaria em nome do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16353, 631, 11, 'Ainda que me assediassem de todos os lados, eu as esmagaria em nome do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16354, 631, 12, 'Ainda que me envolvessem como um enxame de abelhas, como um braseiro de espinhos, eu as esmagaria em nome do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16355, 631, 13, 'Forçaram-me violentamente para eu cair, mas o Senhor veio em meu auxílio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16356, 631, 14, 'O Senhor é minha força, minha coragem; ele é meu Salvador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16357, 631, 15, 'Brados de alegria e de vitória ressoam nas tendas dos justos:*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16358, 631, 16, '“A destra do Senhor fez prodígios, levantou-me a destra do Senhor; fez maravilhas a destra do Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16359, 631, 17, 'Não hei de morrer; viverei para narrar as obras do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16360, 631, 18, 'O Senhor castigou-me duramente, mas poupou-me à morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16361, 631, 19, 'Abri-me as portas santas, a fim de que eu entre para agradecer ao Senhor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16362, 631, 20, 'Esta é a porta do Senhor: só os justos por ela podem passar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16363, 631, 21, 'Graças vos dou porque me ouvistes, e vos fizestes meu Salvador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16364, 631, 22, 'A pedra rejeitada pelos arquitetos tornou-se a pedra angular.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16365, 631, 23, 'Isto foi obra do Senhor, é um prodígio aos nossos olhos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16366, 631, 24, 'Este é o dia que o Senhor fez: seja para nós dia de alegria e de felicidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16367, 631, 25, 'Senhor, dai-nos a salvação; dai-nos a prosperidade, ó Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16368, 631, 26, 'Bendito seja o que vem em nome do Senhor! Da casa do Senhor nós vos bendizemos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16369, 631, 27, 'O Senhor é nosso Deus, ele fez brilhar sobre nós a sua luz. Organizai uma festa com profusão de coroas. E cheguem até os ângulos do altar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16370, 631, 28, 'Sois o meu Deus, venho agradecer-vos. Venho glorificar-vos, sois o meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16371, 631, 29, 'Dai graças ao Senhor porque ele é bom, eterna é sua misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16372, 632, 1, 'Salmo.* Alef Felizes aqueles cuja vida é pura, e seguem a Lei do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16373, 632, 2, 'Felizes os que guardam com esmero seus preceitos e o procuram de todo o coração;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16374, 632, 3, 'e os que não praticam o mal, mas andam em seus caminhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16375, 632, 4, 'Impusestes vossos preceitos para serem observados fielmente;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16376, 632, 5, 'oxalá se firmem os meus passos na observância de vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16377, 632, 6, 'Não serei então confundido, se fixar os olhos nos vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16378, 632, 7, 'Eu vos louvarei com reto coração, uma vez instruído em vossos justos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16379, 632, 8, 'Guardarei as vossas leis; não me abandoneis jamais. Bet');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16380, 632, 9, 'Como um jovem manterá pura a sua vida? Sendo fiel às vossas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16381, 632, 10, 'De todo o coração eu vos procuro; não permitais que eu me aparte de vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16382, 632, 11, 'Guardo no fundo do meu coração a vossa palavra, para não vos ofender.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16383, 632, 12, 'Sede bendito, Senhor; ensinai-me vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16384, 632, 13, 'Meus lábios enumeram todos os decretos de vossa boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16385, 632, 14, 'Na observância de vossas ordens eu me alegro, muito mais do que em todas as riquezas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16386, 632, 15, 'Sobre os vossos preceitos meditarei, e considerarei vossos caminhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16387, 632, 16, 'Hei de deleitar-me em vossas leis; jamais esquecerei vossas palavras. Guimel');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16388, 632, 17, 'Concedei a vosso servo esta graça: que eu viva guardando vossas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16389, 632, 18, 'Abri meus olhos, para que veja as maravilhas de vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16390, 632, 19, 'Peregrino sou na terra, não me oculteis os vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16391, 632, 20, 'Consome-se minha alma no desejo perpétuo de observar vossos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16392, 632, 21, 'Repreendestes os soberbos; malditos os que se apartam de vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16393, 632, 22, 'Livrai-me do opróbrio e do desprezo, pois observo as vossas ordens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16394, 632, 23, 'Mesmo que os príncipes conspirem contra mim, vosso servo meditará em vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16395, 632, 24, 'Vossos preceitos são minhas delícias, meus conselheiros são as vossas leis. Dalet');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16396, 632, 25, 'Prostrada no pó está minha alma: restituí-me a vida conforme vossa promessa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16397, 632, 26, 'Eu vos exponho a minha vida, para que me atendais: ensinai-me as vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16398, 632, 27, 'Mostrai-me o caminho de vossos preceitos, e meditarei em vossas maravilhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16399, 632, 28, 'Chora de tristeza a minha alma; reconfortai-me segundo vossa promessa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16400, 632, 29, 'Afastai-me do caminho da mentira, e fazei-me fiel à vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16401, 632, 30, 'Escolhi o caminho da verdade, impus-me os vossos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16402, 632, 31, 'Apego-me a vossas ordens, Senhor. Não permitais que eu seja confundido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16403, 632, 32, 'Correrei pelo caminho de vossos mandamentos, porque sois vós que dilatais meu coração. He');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16404, 632, 33, 'Mostrai-me, Senhor, o caminho de vossas leis, para que eu nele permaneça com fidelidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16405, 632, 34, 'Ensinai-me a observar a vossa Lei e a guardá-la de todo o coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16406, 632, 35, 'Conduzi-me pelas sendas de vossas leis, porque nelas estão minhas delícias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16407, 632, 36, 'Inclinai-me o coração às vossas ordens e não para a avareza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16408, 632, 37, 'Não permitais que meus olhos vejam a vaidade, fazei-me viver em vossos caminhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16409, 632, 38, 'Cumpri a promessa para com vosso servo, que fizestes àqueles que vos temem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16410, 632, 39, 'Afastai de mim a vergonha que receio, pois são agradáveis os vossos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16411, 632, 40, 'Anseio pelos vossos preceitos; dai-me que viva segundo vossa justiça. Vau');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16412, 632, 41, 'Desçam a mim as vossas misericórdias, Senhor, e a vossa salvação, conforme vossa promessa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16413, 632, 42, 'Saberei o que responder aos que me ultrajam, porque tenho confiança em vossa palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16414, 632, 43, 'Não me tireis jamais da boca a palavra da verdade, porque tenho confiança em vossos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16415, 632, 44, 'Guardarei constantemente a vossa Lei, para sempre e pelos séculos dos séculos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16416, 632, 45, 'Andarei por um caminho seguro, porque procuro os vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16417, 632, 46, 'Diante dos reis falarei de vossas prescrições, e não me envergonharei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16418, 632, 47, 'Encontrarei minhas delícias em vossos mandamentos, porque os amo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16419, 632, 48, 'Erguerei as mãos para executar vossos mandamentos, e meditarei em vossas leis. Zain');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16420, 632, 49, 'Lembrai-vos da palavra empenhada ao vosso servo, na qual me fizestes encontrar esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16421, 632, 50, 'O único consolo em minha aflição é que vossa palavra me dá vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16422, 632, 51, 'De sarcasmos cumulam-me os soberbos, mas de vossa Lei não me afasto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16423, 632, 52, 'Lembro-me de vossos juízos de outrora, e isso me consola.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16424, 632, 53, 'Revolto-me à vista dos pecadores, que abandonam a vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16425, 632, 54, 'Vossas leis são objeto de meus cantares no lugar de meu exílio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16426, 632, 55, 'De noite, lembro-me, Senhor, de vosso nome; guardarei a vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16427, 632, 56, 'Escolhi, como parte que me toca, observar vossos preceitos. Het');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16428, 632, 57, 'Minha partilha, Senhor, eu o declaro, é guardar as vossas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16429, 632, 58, 'De todo o coração imploro em vossa presença: tende piedade de mim como haveis prometido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16430, 632, 59, 'Considero os meus atos e regulo meus passos conforme as vossas ordens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16431, 632, 60, 'Apresso-me, sem hesitação, em observar os vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16432, 632, 61, 'As malhas dos ímpios me cercaram, mas eu não esqueço a vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16433, 632, 62, 'Em meio à noite levanto-me para vos louvar pelos vossos decretos cheios de justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16434, 632, 63, 'Sou amigo de todos os que vos temem e dos que seguem vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16435, 632, 64, 'De vossa bondade, Senhor, está cheia a terra; ensinai-me as vossas leis. Tet');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16436, 632, 65, 'Tratastes com benevolência o vosso servo, Senhor, segundo a vossa palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16437, 632, 66, 'Dai-me o juízo reto e a sabedoria, porque confio em vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16438, 632, 67, 'Antes de ser afligido pela provação, errei; mas agora guardo a vossa palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16439, 632, 68, 'Vós que sois bom e benfazejo, ensinai-me as vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16440, 632, 69, 'Contra mim os soberbos maquinam caluniosamente, mas eu, de todo o coração, fico fiel aos vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16441, 632, 70, 'Seu espírito tornou-se espesso como sebo; eu, porém, me deleito em vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16442, 632, 71, 'Foi bom para mim ser afligido, a fim de aprender vossos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16443, 632, 72, 'Mais vale para mim a Lei de vossa boca que montes de ouro e prata. Iod');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16444, 632, 73, 'Formaram-me e plasmaram-me vossas mãos, dai-me a sabedoria para aprender os vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16445, 632, 74, 'Aqueles que vos temem alegrem-se ao me ver, porque em vossa palavra pus minha esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16446, 632, 75, 'Sei, Senhor, que são justos os vossos decretos e que com razão vós me provastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16447, 632, 76, 'Venha-me em auxílio a vossa misericórdia, e console-me segundo a promessa feita a vosso servo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16448, 632, 77, 'Venham sobre mim as vossas misericórdias, para que eu viva, porque a vossa Lei são as minhas delícias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16449, 632, 78, 'Sejam confundidos esses orgulhosos que sem razão me afligem; porque medito em vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16450, 632, 79, 'Voltem para mim os que vos temem e os que observam as vossas prescrições.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16451, 632, 80, 'Seja perfeito meu coração na observância de vossas leis, a fim de que eu não seja confundido. Caf');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16452, 632, 81, 'Desfalece-me a alma ansiando por vosso auxílio; em vossa palavra ponho minha esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16453, 632, 82, 'Meus olhos enfraquecem desejando a vossa palavra; quando vireis consolar-me?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16454, 632, 83, 'Assemelho-me a um odre exposto ao fumeiro, e, contudo, não me esqueci de vossas leis.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16455, 632, 84, 'Por quantos dias fareis esperar o vosso servo? Quando lhe fareis justiça de seus perseguidores?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16456, 632, 85, 'Para mim cavaram fossas os orgulhosos, que não guardam a vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16457, 632, 86, 'Todos os vossos mandamentos são justos; sem razão me perseguem; ajudai-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16458, 632, 87, 'Por pouco não me exterminaram da terra; eu, porém, não abandonei vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16459, 632, 88, 'Conservai-me vivo em vossa misericórdia, para que eu observe as prescrições de vossa boca. Lamed');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16460, 632, 89, 'É eterna, Senhor, vossa palavra, tão estável como o céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16461, 632, 90, 'Vossa verdade dura de geração em geração, tão estável como a terra que criastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16462, 632, 91, 'Tudo subsiste perpetuamente pelos vossos decretos, porque o universo vos é sujeito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16463, 632, 92, 'Se em vossa Lei não tivesse encontrado as minhas delícias, já teria perecido em minha aflição.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16464, 632, 93, 'Jamais esquecerei vossos preceitos, porque por eles é que me dais a vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16465, 632, 94, 'Sou vosso, salvai-me, porquanto busco vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16466, 632, 95, 'Espreitam-me os pecadores para me perder, mas eu atendo às vossas ordens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16467, 632, 96, 'Vi que há um termo em toda perfeição, mas vossa Lei se estende sem limites. Mem');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16468, 632, 97, 'Ah, quanto amo, Senhor, a vossa Lei! Durante o dia todo eu a medito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16469, 632, 98, 'Mais sábio que meus inimigos me fizeram os vossos mandamentos, pois eles me acompanham sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16470, 632, 99, 'Sou mais prudente do que todos os meus mestres, porque vossas prescrições são o único objeto de minha meditação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16471, 632, 100, 'Sou mais sensato do que os anciãos, porque observo os vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16472, 632, 101, 'Dos maus caminhos desvio os meus pés, para poder guardar vossas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16473, 632, 102, 'De vossos decretos eu não me desvio, porque vós mos ensinastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16474, 632, 103, 'Quão saborosas são para mim vossas palavras! São mais doces que o mel à minha boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16475, 632, 104, 'Vossos preceitos me fizeram sábio, por isso odeio toda senda iníqua. Num');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16476, 632, 105, 'Vossa palavra é um facho que ilumina meus passos, uma luz em meu caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16477, 632, 106, 'Faço juramento e me obrigo a guardar os vossos justos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16478, 632, 107, 'Estou extremamente aflito, Senhor; conservai-me a vida como prometestes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16479, 632, 108, 'Aceitai, Senhor, a oferenda da minha promessa e ensinai-me as vossas ordens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16480, 632, 109, 'Em constante perigo está a minha vida, mas não me esqueço de vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16481, 632, 110, 'Armaram-me laços os pecadores, mas não fugi de vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16482, 632, 111, 'Minha herança eterna são as vossas prescrições, porque fazem a alegria de meu coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16483, 632, 112, 'Inclinei o meu coração à prática de vossas ordens, perpetuamente e com exatidão. Samec');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16484, 632, 113, 'Odeio os homens hipócritas, mas amo a vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16485, 632, 114, 'Vós sois meu abrigo e meu escudo; vossa palavra é minha esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16486, 632, 115, 'Afastai-vos de mim, homens malignos! E guardarei os mandamentos de meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16487, 632, 116, 'Sustentai-me pela vossa promessa, para que eu viva, não queirais confundir minha esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16488, 632, 117, 'Ajudai-me para que me salve, e sempre atenderei a vossos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16489, 632, 118, 'Desprezais os que se apartam de vossas leis, porque mentirosos são seus pensamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16490, 632, 119, 'Como escória reputais os pecadores, por isso eu amo as vossas prescrições.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16491, 632, 120, 'O respeito que tenho por vós me faz estremecer e vossos decretos inspiram-me temor. Ain');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16492, 632, 121, 'Pratico o direito e a justiça; não me entregueis aos que me querem oprimir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16493, 632, 122, 'Sede fiador de vosso servo para a sua segurança, a fim de que os orgulhosos não me oprimam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16494, 632, 123, 'Desfalecem-me os olhos desejando vossa ajuda e na espera de vossas promessas de felicidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16495, 632, 124, 'Tratai vosso servo segundo vossa bondade, e ensinai-me vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16496, 632, 125, 'Sou vosso servo: ensinai-me a sabedoria, para que conheça as vossas prescrições.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16497, 632, 126, 'Senhor, é tempo de vós intervirdes, porque violaram as vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16498, 632, 127, 'Por isso, amo os vossos mandamentos, mais que o ouro, mesmo o ouro mais fino.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16499, 632, 128, 'Por isso, escolhi as vossas leis como partilha, e detesto o caminho da mentira. Pe');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16500, 632, 129, 'São admiráveis as vossas prescrições, por isso, minha alma as observa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16501, 632, 130, 'Vossas palavras são uma verdadeira luz, que dá sabedoria aos simples.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16502, 632, 131, 'Abro a boca para aspirar, num intenso amor de vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16503, 632, 132, 'Voltai-vos para mim e mostrai-me vossa misericórdia, como fazeis sempre para com os que amam o vosso nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16504, 632, 133, 'Dirigi meus passos segundo a vossa palavra, a fim de que jamais o pecado reine sobre mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16505, 632, 134, 'Livrai-me da opressão dos homens, para que possa guardar as vossas ordens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16506, 632, 135, 'Fazei brilhar sobre o vosso servo o esplendor da vossa face, e ensinai-me as vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16507, 632, 136, 'Muitas lágrimas correram de meus olhos, por não ver observada a vossa Lei. Sade');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16508, 632, 137, 'Justo sois, Senhor, e retos os vossos juízos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16509, 632, 138, 'Promulgastes vossas prescrições com toda a justiça, em toda a verdade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16510, 632, 139, 'Sinto-me consumido pela dor ao ver meus inimigos negligenciar vossas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16511, 632, 140, 'Vossa palavra é isenta de toda a impureza: vosso servo a ama com fervor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16512, 632, 141, 'Sou pequeno e desprezado, mas não esqueço os vossos preceitos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16513, 632, 142, 'Vossa justiça é justiça eterna; e firme, a vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16514, 632, 143, 'Apesar da angústia e da tribulação que caíram sobre mim, vossos mandamentos continuam a ser minhas delícias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16515, 632, 144, 'Eterna é a justiça das vossas prescrições; dai-me a compreensão delas para que eu viva. Cof');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16516, 632, 145, 'De todo o coração eu clamo. Ouvi-me, Senhor; e observarei as vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16517, 632, 146, 'Clamo a vós: salvai-me, para que eu guarde as vossas prescrições.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16518, 632, 147, 'Já desde a aurora imploro vosso auxílio; nas vossas palavras ponho minha esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16519, 632, 148, 'Meus olhos se antecipam às vigílias da noite, para meditarem em vossa palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16520, 632, 149, 'Conforme vossa misericórdia, ouvi, Senhor, a minha voz; e dai-me a vida, segundo vossa promessa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16521, 632, 150, 'Aproximam-se os que me perseguem sem razão, eles estão longe de vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16522, 632, 151, 'Mas vós, Senhor, estais bem perto, e os vossos mandamentos são a verdade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16523, 632, 152, 'De há muito sei que vossas prescrições, vós as estabelecestes desde toda a eternidade. Res');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16524, 632, 153, 'Vede a minha aflição e libertai-me, porque não me esqueci de vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16525, 632, 154, 'Tomai em vossas mãos a minha causa e vingai-me; como prometestes, dai-me a vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16526, 632, 155, 'Longe dos pecadores está a salvação, e daqueles que não observam as vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16527, 632, 156, 'São muitas, Senhor, as vossas misericórdias; dai-me a vida segundo as vossas decisões.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16528, 632, 157, 'Apesar do número dos que me perseguem e oprimem, não me aparto em nada de vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16529, 632, 158, 'Ao ver os prevaricadores sinto desgosto, porque eles não observam a vossa palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16530, 632, 159, 'Vede, Senhor, como amo vossos preceitos; conservai-me vivo segundo vossa promessa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16531, 632, 160, 'O sumário da vossa palavra é a verdade, eternos são os decretos de vossa justiça. Sin');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16532, 632, 161, 'Perseguem-me sem razão os poderosos; meu coração só reverencia vossas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16533, 632, 162, 'Encontro minha alegria na vossa palavra, como a de quem encontra um imenso tesouro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16534, 632, 163, 'Odeio o mal, eu o detesto; mas amo a vossa Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16535, 632, 164, 'Sete vezes ao dia publico vossos louvores, por causa da justiça de vossos juízos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16536, 632, 165, 'Grande paz têm aqueles que amam vossa Lei: não há para eles nada que os perturbe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16537, 632, 166, 'Espero, Senhor, o vosso auxílio, e cumpro os vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16538, 632, 167, 'Minha alma é fiel às vossas prescrições, e eu as amo com fervor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16539, 632, 168, 'Guardo os vossos preceitos e as vossas ordens, porque ante vossos olhos está minha vida inteira. Tau');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16540, 632, 169, 'Chegue até vós, Senhor, o meu clamor; instruí-me segundo a vossa palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16541, 632, 170, 'Chegue até vós a minha prece; livrai-me segundo a vossa palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16542, 632, 171, 'Meus lábios cantem a vós um cântico, por me haverdes ensinado as vossas leis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16543, 632, 172, 'Cante minha língua as vossas palavras, porque justos são os vossos mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16544, 632, 173, 'Estenda-se a vossa mão e me socorra, porque escolhi vossos preceitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16545, 632, 174, 'Suspiro, Senhor, pela vossa salvação, e a vossa Lei são as minhas delícias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16546, 632, 175, 'Viva a minha alma para vos louvar, e ajudem-me os vossos decretos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16547, 632, 176, 'Ando errante como ovelha perdida; vinde em busca do vosso servo, porque não me esqueci de vossos mandamentos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16548, 633, 1, 'Cântico das peregrinações. Na hora da tribulação, clamei ao Senhor e ele me atendeu.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16549, 633, 2, 'Senhor, livrai minha alma dos lábios mentirosos e da língua pérfida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16550, 633, 3, 'Que ganharás, qual será teu proveito, ó língua pérfida?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16551, 633, 4, 'Flechas agudas de guerreiro, carvões ardentes de giesta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16552, 633, 5, 'Ai de mim por habitar em Mosoc e viver em meio às tendas de Cedar!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16553, 633, 6, 'Por muito tempo minha alma tem vivido com aqueles que detestam a paz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16554, 633, 7, 'Só quero a paz, mas quando dela lhes falo, eles se dispõem para a guerra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16555, 634, 1, 'Cântico das peregrinações. Para os montes levanto os olhos: de onde me virá socorro?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16556, 634, 2, 'O meu socorro virá do Senhor, criador do céu e da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16557, 634, 3, 'Ele não permitirá que teus pés resvalem; não dormirá aquele que te guarda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16558, 634, 4, 'Não, não há de dormir, nem adormecer o guarda de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16559, 634, 5, 'O Senhor é teu guarda, o Senhor é teu abrigo, sempre ao teu lado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16560, 634, 6, 'De dia, o sol não te fará mal; nem a lua durante a noite.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16561, 634, 7, 'O Senhor te resguardará de todo o mal; ele velará sobre tua alma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16562, 634, 8, 'O Senhor guardará os teus passos, agora e para todo o sempre.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16563, 635, 1, 'Cântico das peregrinações. De Davi. Que alegria quando me vieram dizer: “Vamos subir à casa do Senhor...”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16564, 635, 2, 'Eis que nossos pés se estacam diante de tuas portas, ó Jerusalém!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16565, 635, 3, 'Jerusalém, cidade tão bem edificada, que forma um tão belo conjunto!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16566, 635, 4, 'Para lá sobem as tribos, as tribos do Senhor, segundo a Lei de Israel, para celebrar o nome do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16567, 635, 5, 'Lá se acham os tronos de justiça, os assentos da casa de Davi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16568, 635, 6, 'Pedi, vós todos, a paz para Jerusalém, e vivam em segurança os que te amam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16569, 635, 7, 'Reine a paz em teus muros, e a tranquilidade em teus palácios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16570, 635, 8, 'Por amor de meus irmãos e de meus amigos, pedirei a paz para ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16571, 635, 9, 'Por amor da casa do Senhor, nosso Deus, pedirei para ti a felicidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16572, 636, 1, 'Cântico das peregrinações. Levanto os olhos para vós, que habitais nos céus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16573, 636, 2, 'Como os olhos dos servos estão fixos nas mãos de seus senhores, como os olhos das servas estão fixos nas mãos de suas senhoras, assim nossos olhos estão voltados para o Senhor, nosso Deus, esperando que ele tenha piedade de nós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16574, 636, 3, 'Tende misericórdia de nós, Senhor, tende misericórdia de nós, porque estamos saturados de desprezo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16575, 636, 4, 'Nossa alma está em excesso repleta da zombaria dos opulentos e do desprezo dos soberbos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16576, 637, 1, 'Cântico das peregrinações. De Davi. Se o Senhor não tivesse estado conosco, sim, diga-o Israel,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16577, 637, 2, 'se o Senhor não tivesse estado conosco, os homens que se insurgiram contra nós');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16578, 637, 3, 'teriam então nos devorado vivos. Quando seu furor se desencadeou contra nós,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16579, 637, 4, 'as águas nos teriam submergido. Uma torrente teria passado sobre nós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16580, 637, 5, 'Então, nos teriam recoberto as ondas intumescidas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16581, 637, 6, 'Bendito seja o Senhor, que não nos entregou como presa aos seus dentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16582, 637, 7, 'Nossa alma escapou como um pássaro, dos laços do caçador. Rompeu-se a armadilha, e nos achamos livres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16583, 637, 8, 'Nosso socorro está no nome do Senhor, criador do céu e da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16584, 638, 1, 'Cântico das peregrinações. Os que confiam no Senhor são como o monte Sião, eternamente firme.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16585, 638, 2, 'Como Jerusalém está toda cercada de montanhas, assim o Senhor envolve seu povo, agora e sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16586, 638, 3, 'Não permanecerá estendido o cetro dos ímpios sobre o destino dos justos, para que também estes não acabem por estender suas mãos ao crime.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16587, 638, 4, 'Fazei bem, Senhor, aos que são bons, e aos homens de reto coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16588, 638, 5, 'Mas aqueles que se desviam por caminhos tortuosos, que o Senhor os leve com os malfeitores. Paz para Israel!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16589, 639, 1, 'Cântico das peregrinações. Quando o Senhor reconduzia os cativos de Sião, estávamos como sonhando.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16590, 639, 2, 'Em nossa boca só havia expressões de alegria, e em nossos lábios canto de triunfo. Entre os pagãos se dizia: “O Senhor fez por eles grandes coisas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16591, 639, 3, 'Sim, o Senhor fez por nós grandes coisas; ficamos exultantes de alegria!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16592, 639, 4, 'Mudai, Senhor, a nossa sorte, como as torrentes nos desertos do sul.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16593, 639, 5, 'Os que semeiam entre lágrimas, recolherão com alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16594, 639, 6, 'Na ida, caminham chorando os que levam a semente a aspergir. Na volta, virão com alegria, quando trouxerem os seus feixes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16595, 640, 1, 'Cântico das peregrinações. De Salomão. Se o Senhor não edificar a casa, em vão trabalham os que a constroem. Se o Senhor não guardar a cidade, debalde vigiam as sentinelas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16596, 640, 2, 'Inútil levantar-vos antes da aurora, e atrasar até alta noite vosso descanso, para comer o pão de um duro trabalho, pois Deus o dá aos seus amados até durante o sono.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16597, 640, 3, 'Vede, os filhos são um dom de Deus: é uma recompensa o fruto das entranhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16598, 640, 4, 'Tais como as flechas nas mãos do guerreiro, assim são os filhos gerados na juventude.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16599, 640, 5, 'Feliz o homem que assim encheu sua aljava: não será confundido quando defender a sua causa contra seus inimigos à porta da cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16600, 641, 1, 'Cântico das peregrinações. Felizes os que temem o Senhor, os que andam em seus caminhos*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16601, 641, 2, 'Poderás viver, então, do trabalho de tuas mãos, serás feliz e terás bem-estar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16602, 641, 3, 'Tua mulher será em teu lar como uma vinha fecunda. Teus filhos em torno à tua mesa serão como brotos de oliveira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16603, 641, 4, 'Assim será abençoado aquele que teme o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16604, 641, 5, 'De Sião te abençoe o Senhor para que em todos os dias de tua vida gozes da prosperidade de Jerusalém,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16605, 641, 6, 'e para que possas ver os filhos dos teus filhos. Reine a paz em Israel!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16606, 642, 1, 'Cântico das peregrinações. Ah, como me perseguiram desde a minha juventude! Que o diga Israel.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16607, 642, 2, 'Como me perseguiram desde a minha juventude! Mas não me puderam vencer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16608, 642, 3, 'Lavraram sobre o meu dorso os lavradores, nele abriram longos sulcos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16609, 642, 4, 'Mas o Senhor é justo, ele cortou as correias com que me afligiram os maus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16610, 642, 5, 'Sejam confundidos e recuem todos os que odeiam Sião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16611, 642, 6, 'Que eles se tornem como a erva do telhado, que seca antes de ser arrancada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16612, 642, 7, 'Com ela não enche as mãos o ceifador, nem seu regaço quem recolhe os feixes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16613, 642, 8, 'Os que passam não lhes dirão: “Desça sobre vós a bênção do Senhor!”. Nem: “Nós vos abençoamos em nome do Senhor.”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16614, 643, 1, 'Cântico das peregrinações. Do fundo do abismo, clamo a vós, Senhor;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16615, 643, 2, 'Senhor, ouvi minha oração. Que vossos ouvidos estejam atentos à voz de minha súplica.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16616, 643, 3, 'Se tiverdes em conta nossos pecados, Senhor, Senhor, quem poderá subsistir diante de vós?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16617, 643, 4, 'Mas em vós se encontra o perdão dos pecados, para que, reverentes, vos sirvamos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16618, 643, 5, 'Ponho a minha esperança no Senhor. Minha alma tem confiança em sua palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16619, 643, 6, 'Minha alma espera pelo Senhor, mais ansiosa do que os vigias pela manhã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16620, 643, 7, 'Mais do que os vigias que aguardam a manhã, espere Israel pelo Senhor, porque junto ao Senhor se acha a misericórdia; encontra-se nele copiosa redenção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16621, 643, 8, 'E ele mesmo há de remir Israel de todas as suas iniquidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16622, 644, 1, 'Cântico das peregrinações. De Davi. Senhor, meu coração não se enche de orgulho, meu olhar não se levanta arrogante. Não procuro grandezas, nem coisas superiores a mim.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16623, 644, 2, 'Ao contrário, mantenho em calma e sossego a minha alma, tal como uma criança no seio materno, assim está minha alma em mim mesmo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16624, 644, 3, 'Israel, põe tua esperança no Senhor, agora e para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16625, 645, 1, 'Cântico das peregrinações. Senhor, lembrai-vos de Davi e de sua grande piedade,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16626, 645, 2, 'como ele fez ao Senhor este juramento, e este voto ao Poderoso de Jacó:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16627, 645, 3, '“Não entrarei na tenda em que moro, não me deitarei no leito de meu repouso,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16628, 645, 4, 'não darei sono aos meus olhos, nem repouso às minhas pálpebras,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16629, 645, 5, 'até que encontre uma residência para o Senhor, uma morada ao Poderoso de Jacó”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16630, 645, 6, 'Ouvimos dizer que a arca estava em Éfrata, nós a encontramos nas campinas de Jaar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16631, 645, 7, 'Entremos em sua morada, prostremo-nos diante do escabelo de seus pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16632, 645, 8, 'Levantai-vos, Senhor, para vir ao vosso repouso, vós e a arca de vossa majestade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16633, 645, 9, 'Vistam-se de justiça os vossos sacerdotes, e jubilosos cantem de alegria vossos fiéis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16634, 645, 10, 'Pelo nome de Davi, vosso servo, não rejeiteis a face daquele que vos é consagrado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16635, 645, 11, 'O Senhor fez a Davi um juramento, de que não há de se retratar: “Colocarei em teu trono um descendente de tua raça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16636, 645, 12, 'Se teus filhos guardarem minha aliança e os preceitos que eu lhes hei de ensinar, também os descendentes deles, para sempre, se sentarão em teu trono”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16637, 645, 13, 'Porque o Senhor escolheu Sião, ele a preferiu para sua morada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16638, 645, 14, '“É aqui para sempre o lugar de meu repouso, é aqui que habitarei porque o escolhi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16639, 645, 15, 'Abençoarei copiosamente sua subsistência, fartarei de pão os seus pobres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16640, 645, 16, 'Revestirei de salvação seus sacerdotes, e seus fiéis exultarão de alegria.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16641, 645, 17, 'Aí farei crescer o poder de Davi, aí prepararei uma lâmpada para o que me é consagrado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16642, 645, 18, 'Cobrirei de confusão seus inimigos; em sua fronte, porém, brilhará meu diadema.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16643, 646, 1, 'Cântico das peregrinações. Oh, como é bom, como é agradável para irmãos unidos viverem juntos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16644, 646, 2, 'É como um óleo suave derramado sobre a fronte, e que desce para a barba, a barba de Aarão, para correr em seguida até a orla de seu manto.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16645, 646, 3, 'É como o orvalho do Hermon, que desce pela colina de Sião; pois ali derrama o Senhor a vida e uma bênção eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16646, 647, 1, 'Cântico das peregrinações. E agora, bendizei o Senhor, vós todos, servos do Senhor, vós que habitais na casa do Senhor, durante as horas da noite.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16647, 647, 2, 'Levantai as mãos para o santuário, e bendizei o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16648, 647, 3, 'De Sião te abençoe o Senhor, ele que fez o céu e a terra!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16649, 648, 1, 'Aleluia. Louvai o nome do Senhor, louvai-o, servos do Senhor,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16650, 648, 2, 'vós que estais no Templo do Senhor, nos átrios da casa de nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16651, 648, 3, 'Louvai o Senhor, porque ele é bom; cantai à glória de seu nome, porque ele é amável.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16652, 648, 4, 'Pois o Senhor escolheu Jacó para si, ele tomou Israel por sua herança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16653, 648, 5, 'Em verdade, sei que o Senhor é grande, e nosso Deus é maior que todos os deuses.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16654, 648, 6, 'O Senhor faz tudo o que lhe apraz, no céu e na terra, no mar e nas profundezas das águas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16655, 648, 7, 'Ele chama as nuvens dos confins da terra, faz chover em meio aos relâmpagos, solta o vento de seus reservatórios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16656, 648, 8, 'Foi ele que feriu os primogênitos do Egito, tanto dos homens como dos brutos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16657, 648, 9, 'Realizou em ti, Egito, sinais e prodígios, contra o faraó de todos os seus servos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16658, 648, 10, 'Abateu numerosas nações, e exterminou reis poderosos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16659, 648, 11, 'Seon, rei dos amorreus; Og, rei de Basã, assim como todos os reis de Canaã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16660, 648, 12, 'E deu a terra deles em herança, como patrimônio para Israel, seu povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16661, 648, 13, 'Ó Senhor, vosso nome é eterno! Senhor, vossa lembrança passa de geração em geração,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16662, 648, 14, 'pois o Senhor é o guarda de seu povo, e tem piedade de seus servos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16663, 648, 15, 'Os ídolos dos pagãos não passam de prata e ouro, são obras de mãos humanas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16664, 648, 16, 'Têm boca e não podem falar; têm olhos e não podem ver;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16665, 648, 17, 'têm ouvidos e não podem ouvir. Não há respiração em sua boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16666, 648, 18, 'Assemelhem-se a eles todos os que os fizeram, e todos os que neles confiam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16667, 648, 19, 'Casa de Israel, bendizei o Senhor; casa de Aarão, bendizei o Senhor;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16668, 648, 20, 'casa de Levi, bendizei o Senhor. Vós todos que o servis, bendizei o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16669, 648, 21, 'De Sião seja bendito o Senhor, que habita em Jerusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16670, 649, 1, 'Aleluia. Louvai o Senhor, porque ele é bom, porque sua misericórdia é eterna.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16671, 649, 2, 'Louvai o Deus dos deuses, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16672, 649, 3, 'Louvai o Senhor dos senhores, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16673, 649, 4, 'Só ele operou maravilhosos prodígios, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16674, 649, 5, 'Ele criou os céus com sabedoria, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16675, 649, 6, 'Ele estendeu a terra sobre as águas, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16676, 649, 7, 'Ele fez os grandes luminares, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16677, 649, 8, 'O sol que domina os dias, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16678, 649, 9, 'A lua e as estrelas para presidirem a noite, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16679, 649, 10, 'Ele feriu os primogênitos dos egípcios, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16680, 649, 11, 'Ele tirou Israel do meio deles, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16681, 649, 12, 'Graças à força de sua mão e ao vigor de seu braço, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16682, 649, 13, 'Ele dividiu em dois o mar Vermelho, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16683, 649, 14, 'Ele fez passar Israel pelo meio dele, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16684, 649, 15, 'Ele precipitou no mar Vermelho o faraó e seu exército, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16685, 649, 16, 'Ele conduziu seu povo através do deserto, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16686, 649, 17, 'Ele abateu grandes reis, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16687, 649, 18, 'Ele exterminou reis poderosos, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16688, 649, 19, 'Seon, rei dos amorreus, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16689, 649, 20, 'E Og, rei de Basã, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16690, 649, 21, 'E deu a terra deles em herança, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16691, 649, 22, 'Como patrimônio de Israel, seu servo, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16692, 649, 23, 'Em nosso abatimento ele se lembrou de nós, porque sua misericórdia é eterna.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16693, 649, 24, 'E nos livrou de nossos inimigos, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16694, 649, 25, 'Ele dá alimento a todos os seres vivos, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16695, 649, 26, 'Louvai o Deus do céu, porque sua misericórdia é eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16696, 650, 1, 'Às margens dos rios de Babilônia, nos assentávamos chorando, lembrando-nos de Sião.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16697, 650, 2, 'Nos salgueiros daquela terra, pendurávamos, então, as nossas harpas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16698, 650, 3, 'porque aqueles que nos tinham deportado pediam-nos um cântico. Nossos opressores exigiam de nós um hino de alegria: “Cantai-nos um dos cânticos de Sião”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16699, 650, 4, 'Como poderíamos nós cantar um cântico do Senhor em terra estranha?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16700, 650, 5, 'Se eu me esquecer de ti, ó Jerusalém, que minha mão direita se paralise!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16701, 650, 6, 'Que minha língua se me apegue ao palato, se eu não me lembrar de ti, se não puser Jerusalém acima de todas as minhas alegrias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16702, 650, 7, 'Contra os filhos de Edom, lembrai-vos, Senhor, do dia da queda de Jerusalém, quando eles gritavam: “Arrasai-a, arrasai-a até os seus alicerces!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16703, 650, 8, 'Ó filha de Babilônia, a devastadora, feliz aquele que te retribuir o mal que nos fizeste!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16704, 650, 9, 'Feliz aquele que se apoderar de teus filhinhos, para os esmagar contra o rochedo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16705, 651, 1, 'De Davi. Eu vos louvarei de todo o coração, Senhor, porque ouvistes as minhas palavras. Na presença dos anjos eu vos cantarei.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16706, 651, 2, 'Ante vosso santo templo irei prostrar-me, e louvarei o vosso nome, pela vossa bondade e fidelidade, porque acima de todas as coisas exaltastes o vosso nome e a vossa promessa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16707, 651, 3, 'Quando vos invoquei, vós me respondestes; fizestes crescer a força de minha alma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16708, 651, 4, 'Hão de vos louvar, Senhor, todos os reis da terra, ao ouvirem as palavras de vossa boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16709, 651, 5, 'E celebrarão os desígnios do Senhor: “Verdadeiramente, grande é a glória do Senhor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16710, 651, 6, 'Sim, excelso é o Senhor, mas olha os pequeninos, enquanto seu olhar perscruta os soberbos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16711, 651, 7, 'Em meio à adversidade vós me conservais a vida, estendeis a mão contra a cólera de meus inimigos; salva-me a vossa mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16712, 651, 8, 'O Senhor completará o que em meu auxílio começou. Senhor, eterna é a vossa bondade: não abandoneis a obra de vossas mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16713, 652, 1, 'Ao mestre de canto. Salmo de Davi. Senhor, vós me perscrutais e me conheceis,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16714, 652, 2, 'sabeis tudo de mim, quando me sento ou me levanto. De longe penetrais meus pensamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16715, 652, 3, 'Quando ando e quando repouso, vós me vedes, observais todos os meus passos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16716, 652, 4, 'A palavra ainda não me chegou à língua, e já, Senhor, a conheceis toda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16717, 652, 5, 'Vós me cercais por trás e pela frente, e estendeis sobre mim a vossa mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16718, 652, 6, 'Conhecimento assim maravilhoso me ultrapassa, ele é tão sublime que não posso atingi-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16719, 652, 7, 'Para onde irei, longe de vosso Espírito? Para onde fugir, apartado de vosso olhar?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16720, 652, 8, 'Se subir até os céus, ali estareis; se descer à região dos mortos, lá vos encontrareis também.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16721, 652, 9, 'Se tomar as asas da aurora, se me fixar nos confins do mar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16722, 652, 10, 'é ainda vossa mão que lá me levará, e vossa destra que me sustentará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16723, 652, 11, 'Se eu dissesse: “Pelo menos as trevas me ocultarão, e a noite, como se fora luz, me há de envolver”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16724, 652, 12, 'As próprias trevas não são escuras para vós, a noite vos é transparente como o dia e a escuridão, clara como a luz.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16725, 652, 13, 'Fostes vós que plasmastes as entranhas de meu corpo, vós me tecestes no seio de minha mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16726, 652, 14, 'Sede bendito por me haverdes feito de modo tão maravilhoso. Pelas vossas obras tão extraordinárias, conheceis até o fundo a minha alma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16727, 652, 15, 'Nada de minha substância vos é oculto, quando fui formado ocultamente, quando fui tecido nas entranhas subterrâneas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16728, 652, 16, 'Cada uma de minhas ações vossos olhos viram, e todas elas foram escritas em vosso livro; cada dia de minha vida foi prefixado, desde antes que um só deles existisse.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16729, 652, 17, 'Ó Deus, como são insondáveis para mim vossos desígnios! E quão imenso é o número deles!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16730, 652, 18, 'Como contá-los? São mais numerosos que a areia do mar; se pudesse chegar ao fim, seria ainda com vossa ajuda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16731, 652, 19, 'Oxalá extermineis os ímpios, ó Deus, e que se apartem de mim os sanguinários!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16732, 652, 20, 'Eles se revoltam insidiosamente contra vós, perfidamente se insurgem vossos inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16733, 652, 21, 'Pois não hei de odiar, Senhor, aos que vos odeiam? Aos que se levantam contra vós, não hei de abominá-los?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16734, 652, 22, 'Eu os odeio com ódio mortal, eu os tenho em conta de meus próprios inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16735, 652, 23, 'Perscrutai-me, Senhor, para conhecer meu coração; provai-me e conhecei meus pensamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16736, 652, 24, 'Vede se ando na senda do mal, e conduzi-me pelo caminho da eternidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16737, 653, 1, 'Ao mestre de canto. Salmo de Davi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16738, 653, 2, 'Livrai-me, Senhor, do homem mau; preservai-me do homem violento,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16739, 653, 3, 'daqueles que tramam o mal no coração, que provocam discórdias diariamente,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16740, 653, 4, 'que aguçam a língua qual serpente, que ocultam nos lábios veneno viperino.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16741, 653, 5, 'Salvai-me, Senhor, das mãos do ímpio; preservai-me do homem violento, daqueles que tramam minha queda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16742, 653, 6, 'Orgulhosos, armam laços contra mim e estendem suas redes, e junto ao caminho me colocam ciladas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16743, 653, 7, 'Digo ao Senhor: vós sois o meu Deus. Escutai, Senhor, a voz de minha súplica.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16744, 653, 8, 'Senhor Deus, meu poderoso apoio! Vós protegeis minha fronte no dia do combate.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16745, 653, 9, 'Não atendais, Senhor, aos desejos do ímpio, não deixeis que se cumpram seus desígnios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16746, 653, 10, 'Que não levantem a cabeça os que me cercam; sobre eles recaia a malícia de seus lábios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16747, 653, 11, 'Carvões ardentes chovam sobre eles: sejam lançados numa fossa de onde não se ergam mais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16748, 653, 12, 'Não terá duração na terra a má língua; o infortúnio surpreenderá o homem violento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16749, 653, 13, 'Sei que o Senhor defende o desvalido, e faz justiça aos pobres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16750, 653, 14, 'Sim, os justos celebrarão o vosso nome, e os retos poderão viver em vossa presença.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16751, 654, 1, 'Salmo de Davi. Senhor, eu vos chamo, vinde logo em meu socorro; escutai a minha voz quando vos invoco.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16752, 654, 2, 'Que minha oração suba até vós como a fumaça do incenso, que minhas mãos estendidas para vós sejam como a oferenda da tarde.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16753, 654, 3, 'Ponde, Senhor, uma guarda em minha boca, uma sentinela à porta de meus lábios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16754, 654, 4, 'Não deixeis meu coração inclinar-se ao mal, para impiamente cometer alguma ação criminosa. Não permitais que eu tome parte nos festins dos homens que praticam o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16755, 654, 5, 'Se o justo me bate é um favor, se me repreende é como perfume em minha fronte. Minha cabeça não o rejeitará; porém, sob seus golpes, apenas rezarei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16756, 654, 6, 'Seus chefes foram precipitados pelas encostas do rochedo, e ouviram quão brandas eram as minhas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16757, 654, 7, 'Como a terra fendida e sulcada pelo arado, assim seus ossos se dispersam à beira da região dos mortos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16758, 654, 8, 'Pois é para vós, Senhor, que se voltam os meus olhos; eu me refugio junto de vós, não me deixeis perecer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16759, 654, 9, 'Guardai-me do laço que me armaram, e das ciladas dos que praticam o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16760, 654, 10, 'Caiam os ímpios, de uma vez, nas próprias malhas; quanto a mim, que eu escape são e salvo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16761, 655, 1, 'Hino de Davi, quando estava na caverna. Oração.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16762, 655, 2, 'Minha voz lança um grande brado ao Senhor, em alta voz imploro ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16763, 655, 3, 'Ponho diante dele a minha inquietação, eu lhe exponho toda a minha angústia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16764, 655, 4, 'Na hora em que meu espírito desfalece, vós conheceis o meu caminho. Na senda em que ando, ocultaram-me um laço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16765, 655, 5, 'Olho para a direita e vejo: não há ninguém que cuide de mim. Não existe para mim um refúgio, ninguém que se interesse pela minha vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16766, 655, 6, 'Eu vos chamo, Senhor, vós sois meu refúgio, meu quinhão na terra dos vivos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16767, 655, 7, 'Atendei ao meu clamor, porque estou numa extrema miséria. Livrai-me daqueles que me perseguem, porque são mais fortes do que eu.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16768, 655, 8, 'Tirai-me desta prisão, para que possa agradecer ao vosso nome. Os justos virão rodear-me, quando me tiverdes feito este benefício.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16769, 656, 1, 'Salmo de Davi. Senhor, ouvi a minha oração; pela vossa fidelidade, escutai a minha súplica, atendei-me em nome de vossa justiça.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16770, 656, 2, 'Não entreis em juízo com o vosso servo, porque ninguém que viva é justo diante de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16771, 656, 3, 'O inimigo trama contra a minha vida, ele me prostrou por terra; relegou-me para as trevas com os mortos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16772, 656, 4, 'Desfalece-me o espírito dentro de mim, gela-me no peito o coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16773, 656, 5, 'Lembro-me dos dias de outrora, penso em tudo aquilo que fizestes, reflito nas obras de vossas mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16774, 656, 6, 'Estendo para vós os braços: minha alma, como terra árida, tem sede de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16775, 656, 7, 'Apressai-vos em me atender, Senhor, pois estou a ponto de desfalecer. Não me oculteis a vossa face, para que não me torne como os que descem à sepultura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16776, 656, 8, 'Fazei-me sentir, logo, vossa bondade, porque ponho em vós a minha confiança. Mostrai-me o caminho que devo seguir, porque é para vós que se eleva a minha alma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16777, 656, 9, 'Livrai-me, Senhor, de meus inimigos, porque é em vós que ponho a minha esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16778, 656, 10, 'Ensinai-me a fazer vossa vontade, pois sois o meu Deus. Que vosso Espírito de bondade me conduza pelo caminho reto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16779, 656, 11, 'Por amor de vosso nome, Senhor, conservai-me a vida; em nome de vossa clemência, livrai minha alma de suas angústias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16780, 656, 12, 'Pela vossa bondade, destruí meus inimigos e exterminai todos os que me oprimem, pois sou vosso servo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16781, 657, 1, 'De Davi. Bendito seja o Senhor, meu rochedo, que adestra minhas mãos para o combate, meus dedos para a guerra;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16782, 657, 2, 'meu benfeitor e meu refúgio, minha cidadela e meu libertador, meu escudo e meu asilo, que submete a mim os povos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16783, 657, 3, 'Que é o homem, Senhor, para cuidardes dele, que é o Filho do Homem para que vos ocupeis dele?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16784, 657, 4, 'O homem é semelhante ao sopro da brisa, seus dias são como a sombra que passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16785, 657, 5, 'Inclinai, Senhor, os vossos céus e descei, tocai as montanhas para que se abrasem,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16786, 657, 6, 'fulminai o raio e dispersai-os, lançai vossas setas e afugentai-os.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16787, 657, 7, 'Estendei do alto a vossa mão, tirai-me do caudal, das mãos do estrangeiro,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16788, 657, 8, 'cuja boca só diz mentiras e cuja mão só faz juramentos falsos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16789, 657, 9, 'Ó Deus, vou cantar-vos um cântico novo, vos louvarei com a harpa de dez cordas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16790, 657, 10, 'Vós que aos reis dais a vitória, que livrastes Davi, vosso servo;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16791, 657, 11, 'salvai-me da espada da malícia, e livrai-me das mãos de estrangeiros, cuja boca só diz mentiras e cuja mão só faz juramentos falsos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16792, 657, 12, 'Sejam nossos filhos como as plantas novas, que crescem na sua juventude; sejam nossas filhas como as colunas angulares esculpidas, como os pilares do templo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16793, 657, 13, 'Encham-se os nossos celeiros de frutos variados e abundantes, multipliquem-se aos milhares nossos rebanhos, por miríades cresçam eles em nossos campos; sejam fecundas as nossas novilhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16794, 657, 14, 'Não haja brechas em nossos muros, nem ruptura nem lamentações em nossas praças.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16795, 657, 15, 'Feliz o povo agraciado com tais bens; feliz o povo cujo Deus é o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16796, 658, 1, 'Louvor. De Davi. Ó meu Deus, meu rei, eu vos glorificarei, e bendirei o vosso nome pelos séculos dos séculos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16797, 658, 2, 'Dia a dia vos bendirei, e louvarei o vosso nome eternamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16798, 658, 3, 'Grande é o Senhor e sumamente louvável, insondável é a sua grandeza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16799, 658, 4, 'Cada geração apregoa à outra as vossas obras, e proclama o vosso poder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16800, 658, 5, 'Elas falam do brilho esplendoroso de vossa majestade, e publicam as vossas maravilhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16801, 658, 6, 'Anunciam o formidável poder de vossas obras e narram a vossa grandeza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16802, 658, 7, 'Proclamam o louvor de vossa bondade imensa, e aclamam a vossa justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16803, 658, 8, 'O Senhor é clemente e compassivo, generoso e cheio de bondade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16804, 658, 9, 'O Senhor é bom para com todos, e sua misericórdia se estende a todas as suas obras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16805, 658, 10, 'Glorifiquem-vos, Senhor, todas as vossas obras, e vos bendigam os vossos fiéis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16806, 658, 11, 'Que eles apregoem a glória de vosso reino, e anunciem o vosso poder,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16807, 658, 12, 'para darem a conhecer aos homens a vossa força, e a glória de vosso reino maravilhoso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16808, 658, 13, 'Vosso reino é um reino eterno, e vosso império subsiste em todas as gerações. O Senhor é fiel em suas palavras, e santo em tudo o que faz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16809, 658, 14, 'O Senhor sustém os que vacilam, e soergue os abatidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16810, 658, 15, 'Todos os olhos esperançosos se dirigem para vós, e a seu tempo vós os alimentais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16811, 658, 16, 'Basta abrirdes as mãos, para saciardes com benevolência todos os viventes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16812, 658, 17, 'O Senhor é justo em seus caminhos, e santo em tudo o que faz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16813, 658, 18, 'O Senhor se aproxima dos que o invocam, daqueles que o invocam com sinceridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16814, 658, 19, 'Ele satisfará o desejo dos que o temem, ouvirá seus clamores e os salvará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16815, 658, 20, 'O Senhor vela por aqueles que o amam, mas exterminará todos os maus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16816, 658, 21, 'Que minha boca proclame o louvor do Senhor, e que todo ser vivo bendiga eternamente o seu santo nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16817, 659, 1, 'Aleluia. Louva, ó minha alma, o Senhor!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16818, 659, 2, 'Louvarei o Senhor por toda a vida. Salmodiarei o meu Deus enquanto existir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16819, 659, 3, 'Não coloqueis nos poderosos a vossa confiança, são apenas homens nos quais não há salvação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16820, 659, 4, 'Quando se lhe for o espírito, ele voltará ao pó, e todos os seus projetos se desvanecerão de uma só vez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16821, 659, 5, 'Feliz aquele que tem por protetor o Deus de Jacó, que põe sua esperança no Senhor, seu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16822, 659, 6, 'É esse o Deus que fez o céu e a terra, o mar e tudo o que eles contêm; que é eternamente fiel à sua palavra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16823, 659, 7, 'que faz justiça aos oprimidos, e dá pão aos que têm fome. O Senhor livra os cativos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16824, 659, 8, 'o Senhor abre os olhos aos cegos; o Senhor ergue os abatidos; o Senhor ama os justos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16825, 659, 9, 'O Senhor protege os peregrinos, ampara o órfão e a viúva; mas entrava os desígnios dos pecadores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16826, 659, 10, 'O Senhor reinará eternamente; ó Sião, teu Deus é rei por toda a eternidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16827, 660, 1, 'Salmo. Louvai o Senhor porque ele é bom; cantai ao nosso Deus porque ele é amável, e o louvor lhe convém.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16828, 660, 2, 'O Senhor reconstrói Jerusalém, e congrega os dispersos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16829, 660, 3, 'Ele cura os que têm o coração ferido, e cuida-lhes das chagas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16830, 660, 4, 'É ele que fixa o número das estrelas, e designa cada uma por seu nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16831, 660, 5, 'Grande é o Senhor nosso e poderosa a sua força; sua sabedoria não tem limites.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16832, 660, 6, 'O Senhor eleva os humildes, mas abate os ímpios até a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16833, 660, 7, 'Cantai ao Senhor um cântico de gratidão, cantai ao nosso Deus com a harpa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16834, 660, 8, 'A ele que cobre os céus de nuvens, que faz cair a chuva à terra; a ele que faz crescer a relva nas montanhas, e germinar plantas úteis para o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16835, 660, 9, 'Que dá sustento aos rebanhos, aos filhotes dos corvos que por ele clamam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16836, 660, 10, 'Não é o vigor do cavalo que lhe agrada, nem ele se compraz nos jarretes do corredor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16837, 660, 11, 'Agradam ao Senhor somente os que o temem, e confiam em sua misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16838, 661, 1, '(12) Salmo. Louva, ó Jerusalém, o Senhor; louva o teu Deus, ó Sião,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16839, 661, 2, '(13) porque ele reforçou os ferrolhos de tuas portas, e abençoou teus filhos em teu seio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16840, 661, 3, '(14) Estabeleceu a paz em tuas fronteiras, e te nutre com a flor do trigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16841, 661, 4, '(15) Ele envia a sua palavra sobre a terra, e aí ela corre velozmente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16842, 661, 5, '(16) Ele faz cair a neve como lã, espalha a geada como cinza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16843, 661, 6, '(17) Atira o seu granizo como migalhas de pão, diante de seu frio as águas se congelam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16844, 661, 7, '(18) À sua ordem, porém, elas se derretem; faz soprar o vento e as águas correm de novo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16845, 661, 8, '(19) Ele revelou sua palavra a Jacó, sua Lei e seus preceitos a Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16846, 661, 9, '(20) Com nenhum outro povo agiu assim, a nenhum deles manifestou seus mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16847, 662, 1, 'Aleluia. Nos céus, louvai o Senhor, louvai-o nas alturas do firmamento.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16848, 662, 2, 'Louvai-o, todos os seus anjos. Louvai-o, todos os seus exércitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16849, 662, 3, 'Louvai-o, sol e lua; louvai-o, astros brilhantes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16850, 662, 4, 'Louvai-o, céus do céu, e vós, ó oceanos dos espaços celestes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16851, 662, 5, 'Louvem o nome do Senhor, porque ele mandou e tudo foi criado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16852, 662, 6, 'Tudo estabeleceu pela eternidade dos séculos; fixou-lhes uma Lei que não será violada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16853, 662, 7, 'Na terra, louvai o Senhor: cetáceos e todos das profundezas do mar;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16854, 662, 8, 'fogo e granizo, neve e neblina; vendaval proceloso dócil às suas ordens;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16855, 662, 9, 'montanhas e colinas, árvores frutíferas, árvores silvestres;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16856, 662, 10, 'feras e rebanhos, répteis e aves;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16857, 662, 11, 'reis da terra e todos os seus povos; príncipes e juízes do mundo;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16858, 662, 12, 'jovens e donzelas; velhos e crianças!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16859, 662, 13, 'Louvem todos o nome do Senhor, porque só o seu nome é excelso. Sua majestade transcende a terra e o céu,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16860, 662, 14, 'e conferiu a seu povo um grande poder. Louvem-no todos os seus fiéis, filhos de Israel, povo a ele mais chegado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16861, 663, 1, 'Aleluia. Cantai ao Senhor um cântico novo, ressoe o seu louvor na assembleia dos fiéis.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16862, 663, 2, 'Alegre-se Israel em seu criador, exultem em seu rei os filhos de Sião.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16863, 663, 3, 'Em coros louvem o seu nome, cantem-lhe salmos com o tambor e a cítara,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16864, 663, 4, 'porque o Senhor ama o seu povo, e dá aos humildes a honra da vitória.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16865, 663, 5, 'Exultem os fiéis na glória, alegrem-se em seus leitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16866, 663, 6, 'Tenham nos lábios o louvor de Deus, e nas mãos a espada de dois gumes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16867, 663, 7, 'para tirar vingança das nações pagãs, e impor castigos aos povos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16868, 663, 8, 'para lançar em ferros os seus reis, e pôr algemas em seus príncipes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16869, 663, 9, 'executando contra eles o julgamento pronunciado. Tal é a glória reservada a todos os seus fiéis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16870, 664, 1, 'Aleluia. Louvai o Senhor em seu santuário, louvai-o em seu majestoso firmamento.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16871, 664, 2, 'Louvai-o por suas obras maravilhosas, louvai-o por sua majestade infinita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16872, 664, 3, 'Louvai-o ao som da trombeta, louvai-o com a lira e a cítara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16873, 664, 4, 'Louvai-o com tímpanos e danças, louvai-o com a harpa e a flauta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16874, 664, 5, 'Louvai-o com címbalos sonoros, louvai-o com címbalos retumbantes. Tudo o que respira louve o Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16875, 665, 1, 'Ora, aconteceu que, já senhor da Grécia, Alexandre, filho de Filipe da Ma­cedônia, oriundo da terra de Cetim, derrotou Dario, rei dos persas e dos medos, e reinou em seu lugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16876, 665, 2, 'Empreendeu inúmeras guerras, apoderou-se de muitas cidades e matou vários reis da região.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16877, 665, 3, 'Avançou até os confins da terra e apoderou-se dos despojos de uma multidão de nações. A terra calou-se diante dele. Tornando-se altivo, seu coração ensoberbeceu-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16878, 665, 4, 'Reuniu um imenso exército,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16879, 665, 5, 'impôs seu poderio aos países, às nações e reis, e todos se tornaram seus tributários.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16880, 665, 6, 'Mas, em seguida, adoeceu e viu que a morte se aproximava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16881, 665, 7, 'Convocou então os mais consi­derados dentre os seus cortesãos, companheiros desde sua juventude, e, ainda em vida, repartiu entre eles o império.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16882, 665, 8, 'Alexandre reinou durante doze anos, e morreu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16883, 665, 9, 'Seus oficiais exerceram o poder, cada qual em seu próprio reino.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16884, 665, 10, 'Puseram todos o dia­de­ma depois de sua morte e, após eles, seus filhos durante muitos anos. E males em quantidade multiplicaram-se sobre a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16885, 665, 11, 'Desses reis originou-se uma raiz de pecado: Antíoco Epífanes, filho do rei Antíoco, que havia estado em Roma como refém e que reinou no ano cento e trinta e sete do reino dos gregos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16886, 665, 12, 'Nessa época, saíram também de Israel uns filhos perversos que seduziram a muitos outros, dizendo: “Vamos e façamos uma aliança com os povos que nos cercam, porque, desde que nos separamos deles, caímos em infortúnios sem conta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16887, 665, 13, 'Semelhante linguagem pareceu-lhes boa');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16888, 665, 14, 'e houve entre o povo quem se apressasse a ir ter com o rei, o qual concedeu a licença de adotarem os costumes pagãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16889, 665, 15, 'Edificaram em Jerusalém um ginásio, como os gentios. Dissimularam os sinais da circuncisão e afastaram-se da alian­ça com Deus para se unirem aos gentios. E se venderam para praticar o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16890, 665, 16, 'Quando seu reino lhe pareceu bem consolidado, concebeu Antíoco o desejo de conquistar também o Egito, a fim de reinar sobre dois reinos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16891, 665, 17, 'Invadiu, pois, o Egito com um poderoso exército, com carros, elefantes, cavaleiros e uma numerosa esquadra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16892, 665, 18, 'Investiu contra Ptolomeu, rei do Egito, o qual, tomado de pânico, fugiu. Foram muitos os que sucumbiram sob seus golpes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16893, 665, 19, 'Tornou-se ele senhor das fortalezas do Egito e apoderou-se das riquezas do país.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16894, 665, 20, 'Após ter derrotado o Egito, pelo ano cento e quarenta e três, regressou Antíoco e atacou Israel, subindo a Jerusalém com um enorme exército.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16895, 665, 21, 'Entrou com arrogância no santuário, tomou o altar de ouro, o candelabro da luz com todos os seus acessórios,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16896, 665, 22, 'a mesa da proposição, os vasos, as alfaias, os turíbulos de ouro, o véu, as coroas, os ornamentos de ouro da fachada e arrancou todo o revestimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16897, 665, 23, 'Tomou a prata, o ouro, os vasos preciosos e os tesouros ocultos que encontrou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16898, 665, 24, 'Arrebatando tudo consigo, regressou à sua terra, após massacrar muitos judeus e pronunciar palavras injuriosas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16899, 665, 25, 'Foi isso um motivo de desolação em extremo para todo o Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16900, 665, 26, 'Príncipes e anciãos gemeram, jovens e moças perderam sua alegria e murchou a beleza das mulheres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16901, 665, 27, 'O recém-casado lamentava-se e a esposa chorava no leito nupcial.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16902, 665, 28, 'A própria terra tremia por todos os seus habitantes e a casa de Jacó cobriu-se de vergonha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16903, 665, 29, 'Dois anos após, Antíoco enviou um oficial a cobrar o tributo nas cidades de Judá. Chegou ele a Jerusalém com uma numerosa tropa.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16904, 665, 30, 'Dirigiu-se aos habitantes com palavras pacíficas, mas astuciosas, nas quais acreditaram. Em seguida, lançou-se de improviso sobre a cidade, pilhou-a seriamente e matou muita gente de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16905, 665, 31, 'Saqueou-a, incendiou-a, destruiu as casas e os muros em derredor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16906, 665, 32, 'Seus soldados conduziram ao cativeiro as mulheres e as crianças e apoderaram-se do gado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16907, 665, 33, 'Cercaram a Cidade de Davi com uma extensa e sólida muralha, com possantes torres, tornando-a sua fortaleza.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16908, 665, 34, 'Instalaram ali uma guarnição brutal de gente sem leis, e se fortificaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16909, 665, 35, 'Ajuntaram armas e provisões. Reunindo todos os espólios do saque de Jerusalém, ali os acumularam. Constituíram-se desse modo em grande ameaça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16910, 665, 36, 'Serviram de cilada para o templo, um inimigo constantemente incitado contra o povo de Israel,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16911, 665, 37, 'derramando sangue inocente ao redor do templo e profanando o santuário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16912, 665, 38, 'Por causa deles, os habitantes de Jerusalém fugiram, e só ficaram lá os estrangeiros. Jerusalém tornou-se estranha a seus próprios filhos e estes a abandonaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16913, 665, 39, 'Seu templo ficou desolado como um deserto, seus dias de festa se transformaram em dias de luto, seus sábados, em dias de vergonha e sua glória em desonra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16914, 665, 40, 'Quanto fora ela honrada, agora foi desprezada e sua exaltação converteu-se em tormento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16915, 665, 41, 'Então, o rei Antíoco publicou para todo o reino um edito, prescrevendo que todos os povos formassem um único povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16916, 665, 42, 'Cada um devia renunciar a seus costumes particulares. Todos os gentios se conformaram a essa ordem do rei, e');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16917, 665, 43, 'muitos de Israel adotaram a sua religião, sacrificando aos ídolos e violando o sábado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16918, 665, 44, 'Por intermédio de mensageiros, o rei enviou, a Jerusalém e às cidades de Judá, cartas prescrevendo que aceitassem os costumes dos outros povos da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16919, 665, 45, 'Deviam suprimir holocaustos, sacrifícios e libações no templo; violar os sábados e as festas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16920, 665, 46, 'profanar o santuário e os santos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16921, 665, 47, 'eri­gir altares, templos e ídolos; sacrificar porcos e outros animais impuros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16922, 665, 48, 'Deviam também deixar seus filhos incir­cun­cidados e macular suas almas com toda sorte de impurezas e abominações, de maneira');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16923, 665, 49, 'a obrigarem-nos a esquecer a Lei e a transgredir as prescrições.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16924, 665, 50, 'Todo aquele que não obedecesse à ordem do rei seria morto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16925, 665, 51, 'Foi nesse teor que o rei escreveu a todo o seu reino e nomeou comissários para vigiar o cumprimento de sua vontade pelo povo. Ordenou às cidades de Judá que oferecessem sacrifícios, cada uma por sua vez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16926, 665, 52, 'Houve muitos dentre o povo que colaboraram com eles e abandonaram a Lei. Fizeram muito mal no país e');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16927, 665, 53, 'constrangeram os israelitas a se refugiarem em asilos e refúgios ocultos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16928, 665, 54, 'No dia quinze do mês de Casleu, no ano cento e quarenta e cinco, Antíoco fez erigir a Abominação da desolação sobre o altar. Também construíram altares em todas as cidades vizinhas de Judá.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16929, 665, 55, 'Ofereciam sacrifícios diante das portas das casas e nas praças públicas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16930, 665, 56, 'Rasgavam e queimavam todos os livros da Lei que achavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16931, 665, 57, 'Em toda parte, todo aquele em poder do qual fosse encontrado um livro do testamento, ou todo aquele que mostrasse gosto pela Lei, morreria por ordem do rei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16932, 665, 58, 'Com esse poder que tinham, tratavam assim, cada mês, os judeus que eles encontravam nas cidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16933, 665, 59, 'No dia vinte e cinco de cada mês, sacrificavam no altar, que sobressaía ao altar do templo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16934, 665, 60, 'As mulheres, que levavam seus filhos a circuncidar eram mortas conforme a ordem do rei,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16935, 665, 61, 'com os filhos suspensos ao pescoço. Massacravam-se também seus próximos e os que tinham feito a circuncisão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16936, 665, 62, 'Numerosos foram os israelitas que tomaram a firme resolução de não comer nada que fosse impuro. Preferiram a morte antes que se manchar com alimentos impuros;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16937, 665, 63, 'não quiseram violar a santa lei e foram trucidados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16938, 665, 64, 'Caiu assim sobre Israel uma imensa cólera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16939, 666, 1, 'Foi nessa época que se levantou Ma­tatias, filho de João, filho de Si­meão, sacerdote da família de Joarib, que veio de Jerusalém se estabelecer em Mo­din.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16940, 666, 2, 'Tinha ele cinco filhos: João, apelidado Gadi;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16941, 666, 3, 'Simão, alcunhado Tasi;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16942, 666, 4, 'Judas, chamado Macabeu;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16943, 666, 5, 'Eleazar, cognominado Auarã; e Jônatas, chamado Afus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16944, 666, 6, 'Vendo as abominações praticadas em Judá e em Jerusalém, exclamou: “Ai de mim! Por que nasci eu, para ver a ruína do meu povo e da cidade santa,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16945, 666, 7, 'e ficar sem fazer nada, enquanto ela é entregue ao poder de seus inimigos');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16946, 666, 8, 'e seu santuário abandonado aos estrangeiros? Seu templo tornou-se como um homem desonrado');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16947, 666, 9, 'e os vasos sagrados, que eram o motivo de seu orgulho, levados como para um cativeiro; seus filhos foram trucidados nas ruas e seus jovens sucumbiram ao gládio do inimigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16948, 666, 10, 'Que povo há que não tenha herdado de seus atributos reais, que não se tenha apoderado dos seus despojos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16949, 666, 11, 'Toda a sua glória desapareceu e, de livre que era, tornou-se escrava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16950, 666, 12, 'Eis que tudo o que tínhamos de sagrado, de belo, de glorioso, foi assolado e profanado pelas nações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16951, 666, 13, 'Por que viver ainda?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16952, 666, 14, 'Matatias e seus filhos rasgaram suas vestes, cobriram-se de sacos e choraram amargamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16953, 666, 15, 'Sobrevieram enviados do rei a Modin, para impor a apostasia e obrigar a sacrificar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16954, 666, 16, 'Muitos dos israelitas uniram-se a eles, mas Matatias e seus filhos permaneceram firmes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16955, 666, 17, 'Em resposta disseram-lhe os que vinham da parte do rei: “Possuis nesta cidade notável influência e consideração, teus irmãos e teus filhos te dão autoridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16956, 666, 18, 'Vem, pois, como primeiro executar a ordem do rei, como o fizeram todas as nações, os habitantes de Judá e os que ficaram em Jerusalém. Serás contado, tu e teus filhos, entre os amigos do rei; a ti e aos teus filhos o rei vos honrará, cumulando-vos de prata, de ouro e de presentes!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16957, 666, 19, 'Matatias respondeu-lhes: “Ainda mesmo que todas as nações que se acham no reino do rei o escutassem, de modo que todos renegassem a fé de seus pais e aquiescessem às suas ordens,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16958, 666, 20, 'eu, meus filhos e meus irmãos perseveraremos na Aliança concluída por nossos antepassados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16959, 666, 21, 'Que Deus nos preserve de abandonarmos a Lei e os mandamentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16960, 666, 22, 'Não obedeceremos a essas ordens do rei e não nos desviaremos de nossa religião nem para a direita nem para a esquerda!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16961, 666, 23, 'Mal havia acabado de falar, eis que um judeu se adiantou para sacrificar no altar de Modin, à vista de todos, conforme as ordens do rei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16962, 666, 24, 'Viu-o Matatias e, no ardor de seu zelo, sentiu estremecerem-se suas entranhas. Num ímpeto de justa cólera arrojou-se e matou o homem sobre o altar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16963, 666, 25, 'Matou ao mesmo tempo o oficial incumbido da ordem de sacrificar e demoliu o altar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16964, 666, 26, 'Com semelhante gesto mostrou ele seu amor pela Lei, como agiu Fineias a respeito de Zambri, filho de Salom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16965, 666, 27, 'Em altos brados Matatias elevou a voz então na cidade: “Quem for fiel à Lei e permanecer firme na Aliança, saia e siga-me”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16966, 666, 28, 'Assim, com seus filhos, fugiu em direção às montanhas, abandonando todos os seus bens na cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16967, 666, 29, 'Então, uma grande parte dos que procuravam a Lei e a justiça encaminharam-se para o deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16968, 666, 30, 'Ali refugiaram-se, com seus filhos, suas mulheres e seus rebanhos, porque as desgraças os oprimiam cruelmente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16969, 666, 31, 'Contaram aos oficiais do rei e às forças acantonadas em Jerusalém, na Cidade de Davi, que certo número de judeus, culpáveis de terem transgredido a ordem real, havia descido ao deserto, para ali se ocultar e que muitos se haviam precipitado em seu seguimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16970, 666, 32, 'Os sírios arremessaram-se ao encalço deles e os alcançaram, depois se prepararam para agredi-los em dia de sábado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16971, 666, 33, '“Isso basta, agora – gritaram-lhes eles –, saí, obedecei à ordem do rei e vivereis.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16972, 666, 34, '“Não sairemos –, replicaram os judeus – e não obedeceremos às ordens do rei, com a profanação do dia de sábado.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16973, 666, 35, 'Instantaneamente os sírios travaram combate contra eles;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16974, 666, 36, 'mas eles não responderam, não atiraram uma só pedra e não obstruíram as entradas dos esconderijos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16975, 666, 37, '“Que morramos todos inocentes. O céu e a terra nos servirão de testemunhas de que nos matais injustamente.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16976, 666, 38, 'E foi assim que os inimigos se lançaram sobre eles em dia de sábado. Eles morreram com suas esposas, seus filhos e seu rebanho. Eram cerca de mil pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16977, 666, 39, 'Matatias e seus amigos o souberam e comoveram-se muito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16978, 666, 40, 'E disseram uns aos outros: “Se todos agirmos como nossos irmãos, se não pelejarmos contra os estrangeiros para pormos a salvo nossas vidas e nossas leis, nos exterminarão bem depressa da terra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16979, 666, 41, 'Tomaram, pois, naquele dia a seguinte resolução: “Mesmo que nos ataquem em dia de sábado, lutaremos contra eles e não nos deixaremos matar a todos nós, como o fizeram nossos irmãos no seu esconderijo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16980, 666, 42, 'Então, se ajuntou a eles o grupo dos judeus assideus, particularmente valentes em Israel, apegados à Lei;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16981, 666, 43, 'e todos os que fugiam das perseguições se ajuntaram do mesmo modo a eles e os reforçaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16982, 666, 44, 'Formaram, pois, um exército e na sua ira e indignação massacraram certo número de prevaricadores e de traidores da Lei; os outros procuraram refúgio junto aos estrangeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16983, 666, 45, 'Assim, Matatias e seus amigos percorreram o país, destruíram os altares e');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16984, 666, 46, 'circuncidaram à força as crianças, ainda incircuncisas nas fronteiras de Israel');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16985, 666, 47, 'e perseguiram os sírios orgulhosos. Sua empresa alcançou bom êxito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16986, 666, 48, 'Arrancaram a Lei do poder dos gentios e dos reis e não permitiram que prevalecesse o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16987, 666, 49, 'Ora, chegou para Matatias o dia de sua morte e ele disse a seus filhos: “O que domina até este momento é o orgulho, o ódio, a desordem e a cólera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16988, 666, 50, 'Sede, pois, agora, meus filhos, os defensores da Lei e dai a vossa vida pela Aliança de nossos pais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16989, 666, 51, 'Recordai-vos dos feitos que vossos antepassados realizaram na época em que viveram, e merecereis grande glória e nome eterno.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16990, 666, 52, 'Porventura, não foi na prova que Abraão permaneceu fiel? E não lhe foi isso imputado em justiça?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16991, 666, 53, 'José observou os mandamentos na sua desgraça e veio a ser o senhor do Egito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16992, 666, 54, 'Fineias, nosso antepassado, por ter sido inflamado de zelo, recebeu a promessa de um sacerdócio perpétuo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16993, 666, 55, 'Josué, cumprindo a palavra de Deus, veio a ser juiz em Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16994, 666, 56, 'Caleb deu testemunho na assembleia e herdou a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16995, 666, 57, 'Por todos os séculos, em vista de sua piedade, mereceu Davi o trono real.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16996, 666, 58, 'Porque ardia em zelo pela Lei, Elias foi arrebatado ao céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16997, 666, 59, 'Ananias, Azarias e Misael foram salvos das chamas por terem tido fé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16998, 666, 60, 'Daniel, na sua retidão, foi salvo da boca dos leões.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (16999, 666, 61, 'Recordai-vos assim, de geração em geração, de que todos os que esperam em Deus não desfalecem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (17000, 666, 62, 'Não temais as ameaças do pecador, pois sua glória acabará em lama e vermes.');
