INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19001, 718, 2, 'põe uma faca na tua garganta, se tu sentes muito apetite;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19002, 718, 3, 'não cobices seus manjares que são alimentos enganosos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19003, 718, 4, 'Não te afadigues para te enriqueceres, evita aplicar a isso teu espírito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19004, 718, 5, 'Mal fixas os olhos nos bens, e nada mais há, porque a riqueza tem asas como a águia que voa para o céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19005, 718, 6, 'Não comas com homem invejoso, não cobices seus manjares,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19006, 718, 7, 'porque ele se mostra tal qual se calculou em si mesmo. Ele te diz: “Come e bebe”, mas seu coração não está contigo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19007, 718, 8, 'Comido o bocado, tu o vomitarás e desperdiçarás tuas amabilidades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19008, 718, 9, 'Não fales aos ouvidos do insensato porque ele desprezaria a sabedoria de tuas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19009, 718, 10, 'Não toques no marco antigo, não penetres na terra dos órfãos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19010, 718, 11, 'porque seu vingador é poderoso e defenderá sua causa contra ti.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19011, 718, 12, 'Aplica teu coração à instrução e teus ouvidos às palavras da ciência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19012, 718, 13, 'Não poupes ao menino a correção: se tu o castigares com a vara, ele não morrerá,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19013, 718, 14, 'castigando-o com a vara, salvarás sua vida da morada dos mortos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19014, 718, 15, 'Meu filho, se o teu espírito for sábio, meu coração se alegrará contigo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19015, 718, 16, 'Meus rins estremecerão de alegria, quando teus lábios proferirem palavras retas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19016, 718, 17, 'Que teu coração não inveje os pecadores, mas permaneça sempre no temor do Senhor');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19017, 718, 18, 'porque então haverá certamente um futuro e tua esperança não será frustrada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19018, 718, 19, 'Ouve, meu filho: sê sabio, dirige teu coração pelo caminho reto,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19019, 718, 20, 'não te ajuntes com os bebedores de vinho, com aqueles que devoram carnes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19020, 718, 21, 'pois o ébrio e o glutão se empobrecem e a sonolência veste-se com andrajos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19021, 718, 22, 'Dá ouvidos a teu pai, àquele que te gerou e não desprezes tua mãe quando envelhecer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19022, 718, 23, 'Adquire a verdade e não a vendas, adquire sabedoria, instruções e inteligência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19023, 718, 24, 'O pai do justo exultará de alegria; aquele que gerou um sábio se alegrará nele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19024, 718, 25, 'Que teu pai se alegre por tua causa, que viva na alegria aquela que te deu à luz!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19025, 718, 26, 'Meu filho, dá-me teu coração. Que teus olhos observem meus caminhos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19026, 718, 27, 'pois a meretriz é uma fossa profunda e a entranha, um poço estreito:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19027, 718, 28, 'como um salteador ele fica de emboscada e, entre os homens, multiplica os infiéis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19028, 718, 29, 'Para quem os “ah”? Para quem os “ais”? Para quem as contendas? Para quem as queixas? Para quem as feridas sem motivo? Para quem o vermelho dos olhos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19029, 718, 30, 'Para aqueles que permanecem junto ao vinho, para aqueles que vão saborear o vinho misturado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19030, 718, 31, 'Não consideres o vinho: “como ele é vermelho, como brilha na taça, como corre suavemente!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19031, 718, 32, 'Mas, no fim, morde como uma serpente e pica como um basilisco!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19032, 718, 33, 'Os teus olhos verão coisas estranhas, teu coração pronunciará coisas incoerentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19033, 718, 34, 'Serás como um homem adormecido no fundo do mar, ou deitado no cimo dum mastro:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19034, 718, 35, '“Feriram-me – dirás tu –; e não sinto dor!”. “Bateram-me... e não sinto nada. Quando despertei eu? Quero mais ainda!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19035, 719, 1, 'Não invejes os maus, nem desejes estar com eles,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19036, 719, 2, 'porque seus corações maquinam a violência e seus lábios só proclamam a iniquidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19037, 719, 3, 'É com sabedoria que se constrói a casa, pela prudência ela se consolida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19038, 719, 4, 'Pela ciência enchem-se os celeiros de todo bem precioso e agradável.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19039, 719, 5, 'O sábio é um homem forte, o douto é cheio de vigor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19040, 719, 6, 'É com a prudência que empreenderás a guerra e a vitória depende de grande número de conselheiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19041, 719, 7, 'A sabedoria é por demais sublime para o tolo; à porta da cidade, ele não abre a boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19042, 719, 8, 'Quem medita fazer o mal, é chamado mestre intrigante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19043, 719, 9, 'O desígnio da loucura é o pecado; e detrator é terror para os outros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19044, 719, 10, 'Se te deixas abater no dia da adversidade, minguada é a tua força.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19045, 719, 11, 'Livra os que foram entregues à morte, salva os que cambaleiam indo para o massacre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19046, 719, 12, 'Se disseres: “Mas, não o sabia!”. Aquele que pesa os corações não o verá? Aquele que vigia tua alma não o saberá? E não retribuirá a cada qual segundo seu procedimento?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19047, 719, 13, 'Meu filho, come mel, pois é bom; um favo de mel é doce para teu paladar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19048, 719, 14, 'Sabe, pois, que assim será a sabedoria para tua alma. Se tu a encontrares, haverá para ti um bom futuro e tua esperança não será frustrada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19049, 719, 15, 'Não conspires, ó ímpio, contra a casa do justo, não destruas sua habitação!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19050, 719, 16, 'Porque o justo cai sete vezes, mas ergue-se, enquanto os ímpios desfalecem na desgraça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19051, 719, 17, 'Não te alegres, se teu inimigo cair, se tropeçar, que não se rejubile teu coração,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19052, 719, 18, 'para não suceder que o Senhor o veja, e isto lhe desagrade, e tire de cima dele sua ira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19053, 719, 19, 'Não te indignes à vista dos maus, não invejes os ímpios,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19054, 719, 20, 'porque para o mal não há futuro e o luzeiro dos ímpios se extinguirá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19055, 719, 21, 'Meu filho, teme o Senhor e o rei, não te mistures com os sediciosos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19056, 719, 22, 'porque, de repente, surgirá sua desgraça. Quem conhece a destruição de uns e de outros?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19057, 719, 23, 'O que segue é ainda dos sábios: Não é bom mostrar-se parcial no julgamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19058, 719, 24, 'Ao que diz ao culpado: “Tu és inocente”, os povos o amaldiçoarão, as nações o abominarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19059, 719, 25, 'Aqueles que sabem repreender são louvados, sobre eles cai uma chuva de bênçãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19060, 719, 26, 'Dá um beijo nos lábios aquele que responde com sinceridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19061, 719, 27, 'Cuida da tua tarefa de fora, aplica-te ao teu campo e depois edificarás tua habitação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19062, 719, 28, 'Não sejas testemunha inconsiderada contra teu próximo. Queres, acaso, que teus lábios te enganem?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19063, 719, 29, 'Não digas: “Eu lhe farei o que me fez, pagarei a este homem segundo seus atos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19064, 719, 30, 'Perto da terra do preguiçoso eu passei, junto à vinha de um homem insensato:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19065, 719, 31, 'eis que, por toda a parte, cresciam abrolhos, urtigas cobriam o solo, o muro de pedra estava por terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19066, 719, 32, 'Vendo isso, refleti; daquilo que havia visto, tirei esta lição:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19067, 719, 33, 'um pouco de sono, um pouco de torpor, um pouco cruzando as mãos para descansar');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19068, 719, 34, 'e virá a indigência como um vagabundo, a miséria como um homem armado!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19069, 720, 1, 'Ainda alguns provérbios de Salomão, recolhidos pelos homens de Ezequias, rei de Judá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19070, 720, 2, 'A glória de Deus é ocultar uma coisa; a glória dos reis é esquadrinhá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19071, 720, 3, 'A altura do céu, a profundeza da terra são impenetráveis, bem como o coração dos reis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19072, 720, 4, 'Tira as escórias da prata e terás um vaso para o ourives;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19073, 720, 5, 'afasta o mau da presença do rei e seu trono se firmará na justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19074, 720, 6, 'Não te faças de pretensioso diante do rei, não te ponhas no lugar dos grandes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19075, 720, 7, 'É melhor que te digam: “Sobe aqui!”, do que seres humilhado diante de um personagem. O que teus olhos viram,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19076, 720, 8, 'não o descubras com precipitação numa contenda, pois, no final das contas, que farás tu quando o outro te houver confundido?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19077, 720, 9, 'Trata teu negócio com teu próximo de maneira a não revelar o segredo de outro,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19078, 720, 10, 'para que não sejas repreendido por aquele que o ouviu nem incorras em descrédito irreparável.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19079, 720, 11, 'Maçãs de ouro sobre prata gravada: tais são as palavras oportunas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19080, 720, 12, 'Anel de ouro, joia de ouro fino: tal é o sábio que admoesta um ouvido atento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19081, 720, 13, 'Frescor de neve no tempo da colheita, tal é um mensageiro fiel para quem o envia: ele restaura a alma de seu senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19082, 720, 14, 'Nuvens e vento sem chuva: tal é o homem que se gaba falsamente de dar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19083, 720, 15, 'Pela paciência o juiz se deixa aplacar: a língua que fala com brandura pode quebrantar ossos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19084, 720, 16, 'Achaste mel? Come o que for suficiente: se comeres demais, tu o vomitarás.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19085, 720, 17, 'Põe raramente o pé na casa do vizinho: enfastiado de ti, ele te viria a aborrecer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19086, 720, 18, 'Clava, espada, flecha penetrante: tal é o que usa de falso testemunho contra seu próximo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19087, 720, 19, 'Dente arruinado, pé que resvala: tal é a confiança de um pérfido no dia da desventura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19088, 720, 20, 'Tirar a capa num dia de frio, derramar vinagre numa ferida: isso faz aquele que canta canções a um coração atribulado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19089, 720, 21, 'Tem o teu inimigo fome? Dá-lhe de comer. Tem sede? Dá-lhe de beber:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19090, 720, 22, 'assim amontoarás brasas ardentes sobre sua cabeça e o Senhor te recompensará.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19091, 720, 23, 'O vento norte traz chuva e a língua detratora anuvia os semblantes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19092, 720, 24, 'É melhor habitar um canto do terraço do que viver com uma mulher impertinente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19093, 720, 25, 'Água fresca para uma garganta sedenta: tal é uma boa-nova vinda de terra longínqua.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19094, 720, 26, 'Fonte turva e manancial contaminado: tal é o justo que cede diante do ímpio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19095, 720, 27, 'Comer mel em demasia não é bom: usa de moderação nas palavras elogiosas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19096, 720, 28, 'Como uma cidade desmantelada, sem muralhas: tal é o homem que não é senhor de si.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19097, 721, 1, 'Assim como a neve é imprópria no estio e a chuva na ceifa, do mesmo modo não convém ao insensato a consideração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19098, 721, 2, 'Como um pássaro que foge, uma andorinha que voa: uma maldição injustificada permanece sem efeito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19099, 721, 3, 'O açoite para o cavalo, o freio para o asno: a vara para as costas do tolo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19100, 721, 4, 'Não respondas ao néscio segundo sua insensatez, para não seres semelhante a ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19101, 721, 5, 'Responde ao tolo segundo sua loucura, para que ele não se julgue sábio aos seus olhos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19102, 721, 6, 'Corta os pés, bebe aflições quem confia uma mensagem a um tolo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19103, 721, 7, 'As pernas de um coxo não têm força: do mesmo modo uma sentença na boca de um tolo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19104, 721, 8, 'É colocar pedra na funda cumprimentar um tolo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19105, 721, 9, 'Um espinho que cai na mão de um embriagado: tal é uma sentença na boca dos insensatos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19106, 721, 10, 'Um arqueiro que fere a todos: tal é aquele que emprega um tolo ou um embriagado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19107, 721, 11, 'Um cão que volta ao seu vômito: tal é o louco que reitera suas loucuras.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19108, 721, 12, 'Tu tens visto um homem que se julga sábio? Há mais a esperar de um tolo do que dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19109, 721, 13, '“Há um leão no caminho – diz o preguiçoso –, um leão na estrada!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19110, 721, 14, 'A porta gira sobre seus gonzos: assim o preguiçoso no seu leito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19111, 721, 15, 'O preguiçoso põe sua mão no prato e custa-lhe muito levá-la à boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19112, 721, 16, 'O preguiçoso julga-se mais sábio do que sete homens que respondem com prudência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19113, 721, 17, 'É pegar pelas orelhas um cão que passa envolver-se num debate que não interessa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19114, 721, 18, 'Um louco furioso que lança chamas, flechas e morte:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19115, 721, 19, 'tal é o homem que engana seu próximo e diz em seguida: “mas era para brincar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19116, 721, 20, 'Sem lenha o fogo se apaga: desaparecido o relator, acaba-se a questão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19117, 721, 21, 'Carvão sobre a brasa, lenha sobre o fogo: tal é um intrigante para atiçar uma disputa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19118, 721, 22, 'As palavras do mexeriqueiro são como guloseimas: penetram até o fundo das entranhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19119, 721, 23, 'Uma liga de prata sobre o pote de argila: tais são as palavras ardentes com um coração malévolo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19120, 721, 24, 'O que odeia, fala com dissimulação; no seu interior maquina a fraude;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19121, 721, 25, 'quando ele falar com amabilidade, não te fies nele porque há sete abominações em seu coração;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19122, 721, 26, 'pode dissimular seu ódio sob aparências, e sua malícia acabará por ser revelada ao público.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19123, 721, 27, 'Quem cava uma fossa, ali cai; quem rola uma pedra, cairá debaixo dela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19124, 721, 28, 'A língua mendaz odeia aqueles que ela atinge, a boca enganosa conduz à ruína.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19125, 722, 1, 'Não te gabes do dia de amanhã porque não sabes o que ele poderá engendrar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19126, 722, 2, 'Que seja outro que te louve, não a tua própria boca; um estranho, não teus próprios lábios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19127, 722, 3, 'Pesada é a pedra, pesada a areia, mais pesada ainda é a cólera de um tolo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19128, 722, 4, 'Crueldade do furor, ímpetos da cólera: mas quem pode suportar o ciúme?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19129, 722, 5, 'Melhor é a correção manifesta do que uma amizade fingida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19130, 722, 6, 'As feridas do amigo são provas de lealdade, mas os beijos do que odeia são abundantes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19131, 722, 7, 'Saciado o apetite, calca aos pés o favo de mel; para o faminto tudo o que é amargo parece doce.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19132, 722, 8, 'Um pássaro que anda longe do seu ninho: tal é o homem que vive longe de sua terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19133, 722, 9, 'Azeite e incenso alegram o coração: a bondade de um amigo consola a alma.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19134, 722, 10, 'Não abandones teu amigo, o amigo de teu pai; não vás à casa do teu irmão em dia de aflição. Vale mais um vizinho que está perto, que um irmão distante.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19135, 722, 11, 'Sê sábio, meu filho, alegrarás meu coração e eu poderei responder ao que me ultrajar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19136, 722, 12, 'O homem prudente percebe o mal e se põe a salvo; os imprudentes passam adiante e aguentam o peso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19137, 722, 13, 'Toma a sua veste, porque ficou fiador de outrem, exige o penhor que deve aos estrangeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19138, 722, 14, 'Quem, desde o amanhecer, louva seu vizinho em alta voz é censurado de o ter amaldiçoado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19139, 722, 15, 'Goteira que cai de contínuo em dia de chuva e mulher litigiosa, tudo é a mesma coisa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19140, 722, 16, 'Querer retê-la é reter o vento, ou pegar azeite com a mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19141, 722, 17, 'O ferro com o ferro se aguça; o homem aguça o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19142, 722, 18, 'Quem trata de sua figueira, comerá seu fruto; quem cuida do seu senhor, será honrado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19143, 722, 19, 'Como o reflexo do rosto na água, assim é o coração do homem para o homem.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19144, 722, 20, 'A morada dos mortos e o abismo nunca se enchem; assim os olhos do homem são insaciáveis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19145, 722, 21, 'Há um crisol para a prata, um forno para o ouro; assim o homem é provado pela sua reputação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19146, 722, 22, 'Ainda que pisasses o insensato num triturador, entre os grãos, com um pilão, sua loucura não se separaria dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19147, 722, 23, 'Certifica-te bem do estado do teu gado miúdo; atende aos teus rebanhos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19148, 722, 24, 'porque a riqueza não é eterna e a coroa não permanece de geração em geração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19149, 722, 25, 'Quando se abre o prado, quando brotam as ervas, uma vez recolhido o feno das montanhas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19150, 722, 26, 'tens ainda cordeiros para te vestir e bodes para pagares um campo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19151, 722, 27, 'leite de cabra suficiente para teu sustento, para o sustento de tua casa e a manutenção das tuas servas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19152, 723, 1, 'O ímpio foge sem que ninguém o persiga, mas o justo sente-se seguro como um leão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19153, 723, 2, 'Por causa do pecado de um país, multiplicam-se os chefes, mas sob um homem sábio e sensato a ordem perdura.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19154, 723, 3, 'Um pobre que oprime miseráveis é qual chuva torrencial, causa de fome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19155, 723, 4, 'Quem abandona a instrução, louva o ímpio; quem a observa, faz-lhe guerra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19156, 723, 5, 'Os homens maus não compreendem o que é justo; os que buscam o Senhor tudo entendem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19157, 723, 6, 'Mais vale um pobre que caminha na integridade do que um rico em caminhos tortuosos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19158, 723, 7, 'Um filho inteligente segue a instrução; quem convive com os devassos, torna-se a vergonha de seu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19159, 723, 8, 'Quem aumenta sua fortuna por usuras e logro, ajunta para o que tem piedade dos pequenos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19160, 723, 9, 'Aquele que afasta o ouvido para não ouvir a instrução, até em sua oração é um objeto de horror.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19161, 723, 10, 'Quem seduz os homens corretos para um mau caminho, cairá no fosso que ele mesmo cavou e para os íntegros caberá a herança da felicidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19162, 723, 11, 'O rico julga-se sábio, mas o pobre inteligente penetra-o a fundo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19163, 723, 12, 'Quando os justos triunfam, há muita alegria; quando os ímpios se erguem, cada qual se esconde.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19164, 723, 13, 'Quem dissimula suas faltas não há de prosperar; quem as confessa e as detesta obtém misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19165, 723, 14, 'Feliz daquele que vive em temor contínuo; mas o que endurece seu coração cairá na desgraça.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19166, 723, 15, 'Leão rugidor, urso esfaimado: tal é o ímpio que domina sobre um povo pobre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19167, 723, 16, 'Um príncipe, destituído de senso, é rico em extorsões, mas o que odeia o lucro viverá longos dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19168, 723, 17, 'O homem sobre o qual pesa o sangue de outro fugirá até o fosso: não o retenhas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19169, 723, 18, 'O que caminha na integridade será salvo; quem seguir por caminhos tortuosos cairá no fosso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19170, 723, 19, 'O que cultiva seu solo terá pão à vontade; o que corre atrás das vaidades se fartará de miséria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19171, 723, 20, 'O homem leal será cumulado de bênçãos; o que, porém, tem pressa de se enriquecer não ficará impune.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19172, 723, 21, 'Não é bom mostrar-se parcial: há quem cometa este pecado por um pedaço de pão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19173, 723, 22, 'O homem invejoso precipita-se atrás da fortuna: não sabe que vai cair sobre ele a indigência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19174, 723, 23, 'Quem corrige alguém encontra no fim mais gratidão do que lisonjas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19175, 723, 24, 'Quem furta de seu pai ou de sua mãe, dizendo: “Isto não é pecado!”, é colega do bandoleiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19176, 723, 25, 'O homem cobiçoso provoca contendas, mas o que se fia no Senhor será saciado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19177, 723, 26, 'O que se fia em seu próprio coração é um tolo; quem caminha com sabedoria escapará do perigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19178, 723, 27, 'O que dá ao pobre não padecerá penúria, mas quem fecha os olhos ficará cheio de maldições.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19179, 723, 28, 'Quando se erguem os ímpios, cada qual se oculta; quando eles perecem, multiplicam-se os justos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19180, 724, 1, 'O homem que, apesar das admoestações, se obstina será logo irremediavelmente arruinado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19181, 724, 2, 'Quando dominam os justos, alegra-se o povo; quando governa o ímpio, o povo geme.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19182, 724, 3, 'Quem ama a sabedoria alegra seu pai; o que frequenta as prostitutas dissipa sua fortuna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19183, 724, 4, 'É pela justiça que um rei firma seu país, mas aquele que o sobrecarrega com muitos impostos o arruína.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19184, 724, 5, 'O homem que adula seu próximo estende redes aos seus pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19185, 724, 6, 'No delito do ímpio há um ardil, mas o justo corre alegremente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19186, 724, 7, 'O justo conhece a causa dos pobres; o ímpio a ignora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19187, 724, 8, 'Os escarnecedores ateiam fogo na cidade, mas os sábios acalmam o furor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19188, 724, 9, 'Discute um sábio com um tolo? Que ele se zangue ou que ele se ria não terá paz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19189, 724, 10, 'Os homens sanguinários odeiam o íntegro, mas os homens retos tomam cuidado com sua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19190, 724, 11, 'O insensato desafoga toda sua ira, mas o sábio a domina e a recalca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19191, 724, 12, 'Quando um soberano presta atenção às mentiras, todos os seus servidores tornam-se maus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19192, 724, 13, 'O pobre e o opressor se encontram: é o Senhor que ilumina os olhos de cada um.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19193, 724, 14, 'Um rei que julga com equidade os humildes terá seu trono firmado para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19194, 724, 15, 'Vara e correção dão a sabedoria; menino abandonado à sua vontade se torna a vergonha da mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19195, 724, 16, 'Quando se multiplicam os ímpios, multiplica-se o crime, mas os justos contemplarão sua queda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19196, 724, 17, 'Corrige teu filho e ele te dará repouso e será as delícias de tua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19197, 724, 18, 'Por falta de visão, o povo vive sem freios; ditoso o que observa a instrução!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19198, 724, 19, 'Não é com palavras que se corrige um escravo, porque ele compreende, mas não se atém a elas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19199, 724, 20, 'Viste um homem precipitado no falar: há mais esperança num tolo do que nele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19200, 724, 21, 'Um escravo mimado desde sua juventude, acaba por se tornar desobediente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19201, 724, 22, 'Um homem irascível excita contendas; o colérico acumula as faltas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19202, 724, 23, 'O orgulho de um homem leva-o à humilhação, mas o humilde de espírito obtém a glória.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19203, 724, 24, 'Quem partilha com o ladrão, odeia-se a si mesmo; ouve a maldição e nada denuncia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19204, 724, 25, 'O temor dos homens prepara um laço, mas quem confia no Senhor permanece seguro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19205, 724, 26, 'Muitos buscam o favor de um príncipe, mas é do Senhor que cada homem alcança justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19206, 724, 27, 'O homem iníquo é abominado pelos justos; o ímpio abomina aquele que anda pelo caminho certo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19207, 725, 1, 'Palavras de Agur, filho de Jaces, de Massa. Palavras desse homem: Eu me fatiguei por Deus, estou esgotado por Deus, eis-me entregue.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19208, 725, 2, 'Porque eu sou o mais insensato dos homens, não tenho a inteligência de um homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19209, 725, 3, 'Não aprendi a sabedoria e não conheci a ciência do Santo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19210, 725, 4, 'Quem subiu ao céu e desceu? Quem reteve o vento em suas mãos? Quem envolveu as águas em seu manto? Quem determinou as extremidades da terra? Qual é o seu nome, qual é o nome de seu filho, se é que o sabes?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19211, 725, 5, 'Toda a palavra de Deus é provada, é um escudo para quem se fia nele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19212, 725, 6, 'Não acrescentes nada às suas palavras, para que ele não te corrija e sejas achado mentiroso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19213, 725, 7, 'Eu te peço duas coisas, não me negues antes de minha morte:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19214, 725, 8, 'afasta de mim falsidade e mentira, não me dês nem pobreza nem riqueza, concede-me o pão que me é necessário,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19215, 725, 9, 'para que, saciado, eu não te renegue, e não diga: “Quem é o Senhor?”. Ou que, pobre, eu não roube, e não profane o nome do meu Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19216, 725, 10, 'Não calunies um escravo junto de seu senhor, para que ele não te amaldiçoe e sofras o castigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19217, 725, 11, 'Há uma raça que amaldiçoa seu pai e que não abençoa sua mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19218, 725, 12, 'Há uma raça que se julga pura e que não está limpa de sua mancha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19219, 725, 13, 'Há uma raça, oh, cujos olhos são altivos, com pálpebras levantadas!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19220, 725, 14, 'Há uma raça cujos dentes são espadas e os maxilares, facas, para devorar os desvalidos da terra e os indigentes dentre os homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19221, 725, 15, 'A sanguessuga tem duas filhas: Dá! Dá! Há três coisas insaciáveis, quatro mesmo, que nunca dizem: “Basta!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19222, 725, 16, 'A habitação dos mortos, o seio estéril, o solo que a água jamais sacia e o fogo que nunca diz: “Basta!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19223, 725, 17, 'Os olhos de quem zomba do pai, de quem se recusa a obedecer a sua mãe: os corvos da torrente o arrebatarão, os filhos da águia o devorarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19224, 725, 18, 'Há três coisas que me são mistério, quatro mesmo, que não compreendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19225, 725, 19, 'O voo da águia nos céus, o rastejar da cobra no rochedo, a navegação de um navio em pleno mar, o caminho de um homem junto a uma jovem.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19226, 725, 20, 'Tal é o procedimento da mulher adúltera: come, depois limpa a boca, dizendo: “Não fiz mal algum”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19227, 725, 21, 'Três coisas fazem tremer a terra, há mesmo quatro que ela não pode suportar:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19228, 725, 22, 'Um escravo que se torna rei, um tolo que está farto de pão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19229, 725, 23, 'uma filha desprezada que se casa, uma serva que suplanta sua senhora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19230, 725, 24, 'Há quatro animais pequenos na terra que, entretanto, são sábios, muito sábios:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19231, 725, 25, 'as formigas, povo sem força, que, durante o verão, preparam suas provisões,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19232, 725, 26, 'os arganazes, povo sem poder, que fazem sua habitação nos rochedos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19233, 725, 27, 'os gafanhotos, que não têm rei e avançam todos em bandos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19234, 725, 28, 'a lagartixa, que se pode pegar na mão e penetra nos palácios reais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19235, 725, 29, 'Há três coisas que têm bela aparência, quatro mesmo, que andam garbosamente:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19236, 725, 30, 'o leão, o mais bravo dos animais, que não recua diante de nada,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19237, 725, 31, 'o animal cingido pelos rins, o bode e o rei acompanhado de seu exército.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19238, 725, 32, 'Se tiveres a asneira de elevar-te a ti mesmo, refletindo nisso, depois, põe tua mão à boca,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19239, 725, 33, 'porque quem comprime o leite, tira dele a manteiga, quem aperta o nariz, faz jorrar o sangue, quem provoca a cólera, promove a disputa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19240, 726, 1, 'Palavras de Lamuel, rei de Massa, que lhe foram ensinadas por sua mãe:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19241, 726, 2, 'Meu filho, filho de minhas entranhas, que te direi eu? Não, ó filho de meus votos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19242, 726, 3, 'Não dês teu vigor às mulheres e teu caminho àquelas que perdem os reis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19243, 726, 4, 'Não é próprio dos reis, Lamuel, não convém aos reis beber vinho, nem aos príncipes dar-se aos licores,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19244, 726, 5, 'para que, bebendo, eles não esqueçam a lei e não desconheçam o direito de todos os infelizes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19245, 726, 6, 'Dai a bebida forte àquele que desfalece e o vinho àquele que tem amargura no coração:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19246, 726, 7, 'que ele beba e esquecerá sua miséria e já não se lembrará de suas mágoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19247, 726, 8, 'Abre tua boca a favor do mundo, pela causa de todos os abandonados;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19248, 726, 9, 'abre tua boca para pronunciar sentenças justas, faze justiça ao aflito e ao indigente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19249, 726, 10, 'Uma mulher virtuosa, quem pode encontrá-la? Superior ao das pérolas é o seu valor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19250, 726, 11, 'Confia nela o coração de seu marido, e jamais lhe faltará coisa alguma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19251, 726, 12, 'Ela lhe proporciona o bem, nunca o mal, em todos os dias de sua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19252, 726, 13, 'Ela procura lã e linho e trabalha com mão alegre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19253, 726, 14, 'Semelhante ao navio do mercador, manda vir seus víveres de longe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19254, 726, 15, 'Levanta-se, ainda de noite, distribui a comida à sua casa e a tarefa às suas servas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19255, 726, 16, 'Ela encontra uma terra, adquire-a. Planta uma vinha com o ganho de suas mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19256, 726, 17, 'Cinge os rins de fortaleza, revigora seus braços.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19257, 726, 18, 'Alegra-se com o seu lucro, e sua lâmpada não se apaga durante a noite.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19258, 726, 19, 'Põe a mão na roca, seus dedos manejam o fuso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19259, 726, 20, 'Estende os braços ao infeliz e abre a mão ao indigente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19260, 726, 21, 'Ela não teme a neve em sua casa, porque toda a sua família tem vestes duplas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19261, 726, 22, 'Faz para si cobertas: suas vestes são de linho fino e de púrpura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19262, 726, 23, 'Seu marido é considerado nas portas da cidade, quando se senta com os anciãos da terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19263, 726, 24, 'Tece linho e o vende, fornece cintos ao mercador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19264, 726, 25, 'Fortaleza e graça lhe servem de ornamentos; ri-se do dia de amanhã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19265, 726, 26, 'Abre a boca com sabedoria, amáveis instruções surgem de sua língua.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19266, 726, 27, 'Vigia o andamento de sua casa e não come o pão da ociosidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19267, 726, 28, 'Seus filhos se levantam para proclamá-la bem-aventurada e seu marido para elogiá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19268, 726, 29, '“Muitas mulheres demonstram vigor, mas tu excedes a todas.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19269, 726, 30, 'A graça é falaz e a beleza é vã; a mulher inteligente é a que se deve louvar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19270, 726, 31, 'Dai-lhe o fruto de suas mãos e que suas obras a louvem nas portas da cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19271, 727, 1, 'Palavras do Eclesiastes, filho de Davi, rei de Jerusalém.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19272, 727, 2, '“Fugacidade das fugacidades!”, diz o Eclesiastes. “fugacidade das fugacidades! Tudo é fugaz!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19273, 727, 3, 'Que proveito tira o homem de todo o trabalho com que se afadiga debaixo do sol?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19274, 727, 4, 'Uma geração passa, outra vem, mas a terra sempre subsiste.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19275, 727, 5, 'O sol se levanta, o sol se põe e se apressa a voltar a seu lugar onde renasce.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19276, 727, 6, 'O vento sopra para o sul, volta para o norte, volteia e gira nos mesmos circuitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19277, 727, 7, 'Todos os rios se dirigem para o mar, e o mar não transborda. Em direção ao mar, para onde correm os rios, para lá correm sem cessar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19278, 727, 8, 'Todas as coisas se afadigam, mais do que se pode dizer. A vista não se farta de ver, o ouvido nunca se sacia de ouvir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19279, 727, 9, 'O que foi é o que será. O que aconteceu é o que há de acontecer. Não há nada de novo debaixo do sol.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19280, 727, 10, 'Se é encontrada alguma coisa da qual se diz: “Veja, isto é novo”, ela já existia nos tempos passados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19281, 727, 11, 'Não há memória do que é antigo, nem nossos descendentes não deixarão memória junto àqueles que virão depois deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19282, 727, 12, 'Eu, o Eclesiastes, fui rei de Israel, em Jerusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19283, 727, 13, 'Apliquei meu espírito a um estudo atencioso e à sábia observação de tudo quanto se passa debaixo do céu. Deus impôs aos homens essa ocupação ingrata.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19284, 727, 14, 'Vi tudo o que se faz debaixo do sol: eis que tudo é fugaz e vento que passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19285, 727, 15, 'O que está torto não se pode endirei­tar, o que falta não se pode calcular.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19286, 727, 16, 'Eu disse comigo mesmo: “Eis que amontoei e acumulei sabedoria mais do que todos os que me precederam em Jerusalém. Porque minha mente estudou muito a sabedoria e a ciência,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19287, 727, 17, 'e apliquei o meu coração ao discernimento da sabedoria, da loucura e da tolice. E cheguei à conclusão de que isso é também vento que passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19288, 727, 18, 'Porque no acúmulo de sabedoria, acumula-se tristeza, e quem aumenta a ciência, aumenta a dor”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19289, 728, 1, 'Eu disse comigo mesmo: “Vou tentar a alegria e gozar o prazer!”. Mas isso é também fugaz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19290, 728, 2, 'Do riso eu disse: “Loucura!”, e da alegria: “Para que serve?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19291, 728, 3, 'Resolvi entregar minha carne ao vinho, enquanto meu coração se aplicaria ainda à sabedoria. Entreguei-me à loucura até ver o que é bom para os filhos dos homens fazerem durante toda a sua vida debaixo do céu.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19292, 728, 4, 'Empreendi grandes trabalhos, construí para mim casas e plantei vinhas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19293, 728, 5, 'fiz jardins e pomares, onde plantei árvores frutíferas de toda espécie.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19294, 728, 6, 'Cavei reservatórios de água para regar o bosque de árvores que germinavam. Comprei escravos e escravas, e possuí outros nascidos em casa.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19295, 728, 7, 'Possuí muito gado, bois e ovelhas, mais que todos os que me precederam em Jerusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19296, 728, 8, 'Acumulei também prata e ouro, riquezas de reis e de províncias. Arranjei cantores e cantoras, e o que faz as delícias dos filhos dos homens: mulheres e mulheres.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19297, 728, 9, 'Fui maior que todos os que me precederam em Jerusalém. E, ainda assim, minha sabedoria permaneceu comigo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19298, 728, 10, 'Tudo o que meus olhos desejaram não lhes recusei, nem privei meu coração de nenhuma alegria. Meu coração encontrava sua alegria no meu trabalho, e esse foi o fruto que dele tirei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19299, 728, 11, 'Mas, quando me pus a considerar todas as obras de minhas mãos e o trabalho ao qual me tinha dado para fazê-las, vi que em tudo havia fugacidade e vento que passa. Nada há de proveitoso debaixo do sol.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19300, 728, 12, 'Passei então à meditação da sabedoria, da loucura e da tolice. Qual é o homem, designado desde muito tempo, que virá depois do rei?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19301, 728, 13, 'Cheguei à conclusão de que a sabedoria leva vantagem sobre a loucura, como a luz leva vantagem sobre as trevas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19302, 728, 14, 'Os olhos do sábio estão na sua cabeça, mas o insensato anda nas trevas. Mas notei que um mesmo destino espera a ambos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19303, 728, 15, 'Por isso, disse comigo mesmo: “A minha sorte será a mesma que a do insensato. Então, para que me serve toda a minha sabedoria?”. E concluí comigo mesmo que tudo isso é ainda ilusão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19304, 728, 16, 'Porque a memória do sábio não é mais eterna que a do insensato, pois que, passados alguns dias, ambos serão esquecidos. Tanto morre o sábio como morre o louco!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19305, 728, 17, 'E assim detestei a vida, pois a meus olhos tudo é mau no que se passa debaixo do sol; sim, tudo é efêmero e vento que passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19306, 728, 18, 'Também se tornou odioso para mim todo o trabalho que produzi debaixo do sol, visto que devo deixá-lo àquele que virá depois de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19307, 728, 19, 'E quem sabe se ele será sábio ou insensato? Contudo, é ele quem disporá de todo o fruto dos meus trabalhos que debaixo do sol me custaram fadiga e sabedoria. Também isso é fugaz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19308, 728, 20, 'E senti o coração cheio de desgosto por todo o labor que suportei debaixo do sol.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19309, 728, 21, 'Porque há homem que trabalha com sabedoria, ciência e bom êxito, para deixar o fruto de seu ganho a outro que em nada colaborou. Também isso é ilusão e grande desgraça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19310, 728, 22, 'Com efeito, que resta ao homem de todo o seu labor, de todas as suas azáfamas a que se entregou debaixo do sol?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19311, 728, 23, 'Todos os seus dias são apenas dores, seus trabalhos, apenas tristezas; mesmo durante a noite ele não goza de descanso. Isso é ainda vaidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19312, 728, 24, 'Não há nada melhor para o homem do que comer, beber e gozar o bem-estar do seu trabalho. Notei que também isso vem da mão de Deus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19313, 728, 25, 'pois, quem come e bebe senão graças a ele? Àquele que lhe é agradável Deus dá sabedoria, ciência e alegria; ao passo que ao pecador ele dá a tarefa de juntar e acumular bens, que depois passará a quem lhe agradar. Isso é ainda fugaz e vento que passa.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19314, 729, 1, 'Para tudo há um tempo, para cada coisa há um momento debaixo do céu:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19315, 729, 2, 'tempo de nascer e tempo de morrer; tempo de plantar e tempo de arrancar o que se plantou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19316, 729, 3, 'Tempo de matar e tempo de curar; tempo de demolir e tempo de construir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19317, 729, 4, 'Tempo de chorar e tempo de rir; tempo de gemer e tempo de dançar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19318, 729, 5, 'Tempo de atirar pedras e tempo de ajuntá-las; tempo de abraçar e tempo de apartar-se.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19319, 729, 6, 'Tempo de procurar e tempo de perder; tempo de guardar e tempo de jogar fora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19320, 729, 7, 'Tempo de rasgar e tempo de costurar; tempo de calar e tempo de falar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19321, 729, 8, 'Tempo de amar e tempo de odiar; tempo de guerra e tempo de paz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19322, 729, 9, 'Que proveito tira o trabalhador de sua obra?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19323, 729, 10, 'Vi o trabalho que Deus impôs aos homens, para que nele se ocupassem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19324, 729, 11, 'As coisas que Deus fez são boas a seu tempo. Ele pôs, além disso, no seu coração, a duração inteira, sem que ninguém possa compreender a obra divina de um extremo ao outro.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19325, 729, 12, 'Assim, concluí que nada é melhor para o homem do que alegrar-se e procurar o bem-estar durante sua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19326, 729, 13, 'Igualmente é dom de Deus que todos possam comer, beber e gozar do fruto de seu trabalho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19327, 729, 14, 'Reconheci que tudo o que Deus faz dura para sempre, sem que se possa ajuntar nada, nem nada suprimir. Deus procede dessa maneira para ser temido.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19328, 729, 15, 'Aquilo que é, já existia, e aquilo que há de ser, já existiu; Deus chama de novo o que passou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19329, 729, 16, 'Debaixo do sol, observei ainda o seguinte: a injustiça ocupa o lugar do direito, e a iniquidade toma o lugar da justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19330, 729, 17, 'Então, disse comigo mesmo: “Deus julgará o justo e o ímpio, porque há um tempo para cada coisa e um tempo para cada obra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19331, 729, 18, 'Eu disse comigo mesmo a respeito dos homens: “Deus quer prová-los e mostrar-lhes que, quanto a eles, são semelhantes aos animais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19332, 729, 19, 'Porque o destino dos filhos dos homens e o destino dos animais é o mesmo, um mesmo fim os espera. Tanto morre um como o outro. A ambos foi dado o mesmo sopro. A vantagem do homem sobre o animal é nula, porque tudo é vão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19333, 729, 20, 'Todos caminham para um mesmo lugar. Todos saem do pó e para o pó voltam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19334, 729, 21, 'Quem sabe se o sopro de vida dos filhos dos homens se eleva para o alto e o sopro de vida dos animais desce para a terra?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19335, 729, 22, 'E verifiquei que nada há de melhor para o homem do que alegrar-se com o fruto de seus trabalhos. Esta é a parte que lhe toca. Pois, quem lhe dará a conhecer o que acontecerá com o volver dos anos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19336, 730, 1, 'Pus-me, então, a considerar todas as opressões que se exercem debaixo do sol. Eis aqui as lágrimas dos oprimidos, sem ninguém para consolá-los. Seus opressores fazem-lhes violência e ninguém os consola.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19337, 730, 2, 'E julguei os mortos, que já faleceram, mais felizes que os vivos, que ainda estão em vida;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19338, 730, 3, 'porém, mais feliz que eles é aquele que não chegou a nascer, porque não viu o mal que se comete debaixo do sol.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19339, 730, 4, 'Vi que todo o trabalho, toda a habilidade numa obra não passa de rivalidade de um homem diante do seu próximo. Isso é também fugacidade e vento que passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19340, 730, 5, 'O insensato cruza os braços e devora sua própria carne.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19341, 730, 6, 'Mais vale um punhado de tranquilidade do que dois punhados de trabalho e de vento que passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19342, 730, 7, 'Vi ainda outra fugacidade debaixo do sol:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19343, 730, 8, 'um homem sozinho, sem alguém junto de si, sem filho nem irmão; trabalha sem parar e, não obstante, seus olhos não se fartam de riquezas: “Para quem trabalho eu, privando-me de todo bem-estar?”. Eis uma fugacidade e um trabalho ingrato.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19344, 730, 9, 'Dois homens juntos são mais felizes que um só, porque obterão um bom salário do seu trabalho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19345, 730, 10, 'Se um vem a cair, o outro o levanta. Mas ai do homem só: se ele cair, não há ninguém para levantá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19346, 730, 11, 'Da mesma forma, se dormirem dois juntos, aquecem-se; mas um homem só, como se há de aquecer?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19347, 730, 12, 'Se é possível dominar o homem que está sozinho, dois podem resistir ao agressor. Uma corda tripla não se rompe facilmente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19348, 730, 13, 'Mais vale um adolescente pobre, mas sábio, do que um rei velho, mas insensato, que já não aceita conselhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19349, 730, 14, 'Porque aquele jovem saiu da prisão para reinar, se bem que tenha nascido pobre no reino deste ancião.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19350, 730, 15, 'Vi todos os viventes, que se acham debaixo do sol, apressarem-se junto do jovem que ia sucedê-lo;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19351, 730, 16, 'era interminável o cortejo dessa multidão, à testa da qual ele caminha­va. Contudo, a geração seguinte não se regozijará por sua causa. Tudo isso é ainda fugacidade e vento que passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19352, 730, 17, 'Vê onde pões teu pé quando entras no Templo do Senhor. Mais vale a obediência que os sacrifícios dos insensatos, porque eles só sabem fazer o mal!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19353, 731, 1, 'Não te apresses em abrir a boca, nem o teu coração não se apresse em proferir palavras diante de Deus, porque Deus está no céu, e tu na terra. Por isso, sejam pouco numerosas as tuas palavras!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19354, 731, 2, 'Porque muitas ocupações geram sonhos, e a torrente de palavras faz nascer resoluções insensatas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19355, 731, 3, 'Quando fizeres um voto a Deus, rea­liza-o sem delonga, porque aos insensatos Deus não é favorável. Portanto, cumpre teu voto!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19356, 731, 4, 'Mais vale não fazer voto, que prometer e não ser fiel à promessa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19357, 731, 5, 'Não permitas à tua boca fazer pecar a tua carne, nem digas ao sacerdote que isso foi apenas uma inadvertência, para não suceder que Deus se irrite com essas palavras e reduza a nada tua empresa.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19358, 731, 6, 'Porque muitos cuidados geram sonhos, e a torrente de palavras, despropósitos. Assim, pois, teme a Deus!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19359, 731, 7, 'Se vires, na província, a opressão do pobre e a violação do direito e da justiça, não te admires, porque o que é grande é observado por outro maior e ambos por maiores ainda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19360, 731, 8, 'Sob todos os pontos de vista, uma vantagem para uma nação é se o rei se ocupa com a terra cultivada.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19361, 731, 9, 'Quem ama o dinheiro nunca se fartará. Quem ama a riqueza não tira dela proveito. Também isso é fugalidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19362, 731, 10, 'Quando abundam os bens, numerosos são aqueles que comem. Que vantagem há para os seus possuidores, senão ver como se comportam?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19363, 731, 11, 'Doce é o sono do trabalhador, tenha ele pouco ou muito para comer; mas a abundância do rico o impede de dormir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19364, 731, 12, 'Vi uma dolorosa miséria debaixo do sol: as riquezas que um possuidor guarda para sua própria desgraça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19365, 731, 13, 'Caso essas riquezas venham a se perder em consequência de algum desagradável acontecimento, se ele tiver um filho, nada lhe restará na sua mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19366, 731, 14, 'Nu saiu ele do ventre de sua mãe, assim nu voltará. De seu trabalho nada receberá que possa levar em suas mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19367, 731, 15, 'Sim, é uma dolorosa miséria que ele se vá assim como veio. Que vantagem terá ele por ter trabalhado para o vento?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19368, 731, 16, 'Todos os seus dias foram consumidos numa sombria dor, em extrema amargura, no sofrimento e na irritação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19369, 731, 17, 'Eis o que reconheci ser bom: que é conveniente ao homem comer, beber, gozar de bem-estar em todo o trabalho ao qual ele se dedica debaixo do sol, durante todos os dias de vida que Deus lhe der. Esta é a sua parte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19370, 731, 18, 'Se Deus dá ao homem bens e riquezas, e lhe concede delas comer e delas tomar sua parte, e se alegrar no seu trabalho, isso é um dom de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19371, 731, 19, 'Ele não pensa no número dos dias de sua vida, quando Deus derrama em seu coração a alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19372, 732, 1, 'Vi um mal debaixo do sol, que calca pesadamente o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19373, 732, 2, 'Isto é, um homem a quem Deus deu sorte, riquezas e honras, e nada que possa desejar lhe falta, mas Deus não lhe concede o gozo, reservando-o a um estrangeiro. Isso é fugalidade e dor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19374, 732, 3, 'Um homem, embora crie cem filhos, viva muitos anos, durando longamente os dias de sua vida, se não puder fartar-se de seus bens e não tiver tido sepultura, eu diria que um aborto lhe seria preferível.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19375, 732, 4, 'Porque é em vão o fato de o aborto ter vindo e ido para as trevas. Seu nome permanecerá na obscuridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19376, 732, 5, 'Não terá visto nem conhecido o sol. Me­lhor é a sua sorte que a deste homem.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19377, 732, 6, 'E, mesmo que alguém vivesse duas vezes mil anos, sem provar a felicidade, não vão todos para o mesmo lugar?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19378, 732, 7, 'Todo o trabalho do homem é para a sua boca, mas seus desejos nunca estão satisfeitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19379, 732, 8, 'Que superioridade tem o sábio sobre o louco? Que vantagem há para o pobre saber se conduzir na vida?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19380, 732, 9, 'Mais vale o que veem os olhos do que a agitação dos desejos. Isso é ainda fugalidade e vento que passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19381, 732, 10, 'A tudo o que existe, desde há muito, foi dado um nome, e sabe-se também o que é o homem: é incapaz de disputar com alguém mais forte do que ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19382, 732, 11, 'Muitas palavras só aumentam a fugalidade. De tudo isso, qual é o proveito para o homem?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19383, 732, 12, 'Pois, quem pode saber o que é bom para o homem na vida, durante os dias de sua vã existência, que ele atravessa como uma sombra? Quem poderá dizer ao homem o que acontecerá depois dele debaixo do sol?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19384, 733, 1, 'Boa fama vale mais que um bom perfume; mais vale o dia da morte que o dia do nascimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19385, 733, 2, 'Melhor é visitar a casa onde há luto do que a casa onde há banquete. Porque ali está o fim de todo homem, e os vivos nele refletem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19386, 733, 3, 'Tristeza vale mais do que riso, porque a tristeza do semblante é boa para o coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19387, 733, 4, 'O coração do sábio está na casa em luto; o coração do insensato está na casa da alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19388, 733, 5, 'É melhor ouvir a reprimenda do sábio do que a canção do tolo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19389, 733, 6, 'porque qual o crepitar dos espinhos na caldeira, tal é o riso do insensato. E isso é também fugalidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19390, 733, 7, 'A opressão torna o sábio insensato, e o suborno corrompe o coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19391, 733, 8, 'Mais vale o fim de uma coisa do que seu começo. Um espírito paciente vale mais do que um espírito orgulhoso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19392, 733, 9, 'Não cedas prontamente ao espírito de irritação, porque é no coração do insensato que reside a irritação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19393, 733, 10, 'Não digas: “Por que os dias de outrora eram melhores do que estes de agora?”. Porque não é a sabedoria que te inspira esta pergunta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19394, 733, 11, 'A sabedoria é tão boa como uma herança, e é de proveito aos que veem o sol.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19395, 733, 12, 'A sabedoria protege, assim como o dinheiro protege. A vantagem do saber consiste em que a sabedoria dá vida a quem a possui.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19396, 733, 13, 'Considera a obra de Deus: Quem poderá endireitar o que ele fez curvo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19397, 733, 14, 'No dia da felicidade, sê alegre. No dia da desgraça, pensa: porque Deus fez um e outro, de tal modo que o homem não descubra o futuro.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19398, 733, 15, 'No decurso de minha vã existência, vi tudo isso: há o justo que morre perma­necendo justo e o ímpio que dura apesar de sua malícia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19399, 733, 16, 'Não sejas justo excessivamente, nem sábio além da medida. Por que te arruinarias a ti mesmo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19400, 733, 17, 'Não sejas excessivamente mau e não sejas insensato. Por que haverias de morrer antes de tua hora?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19401, 733, 18, 'É bom que guardes isto, e que não negligencies aquilo, porque aquele que teme a Deus realizará uma e outra coisa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19402, 733, 19, 'A sabedoria dá ao sábio mais força do que dez chefes de guerra reunidos numa cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19403, 733, 20, 'Não há homem justo sobre a terra, que faça o bem sem jamais pecar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19404, 733, 21, 'Não prestes atenção em todas as palavras que se dizem, para que não ouças dizer que teu servo fala mal de ti,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19405, 733, 22, 'porque teu coração bem sabe que tu mesmo, muitas vezes, falaste mal dos outros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19406, 733, 23, 'Tudo isso perscrutei com sabedoria. Eu disse comigo mesmo: “Eu quero ser sábio”. Mas a sabedoria está longe de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19407, 733, 24, 'Aquilo que acontece está longínquo, profundo, profundo: quem poderá sondá-lo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19408, 733, 25, 'Apliquei-me de todo o coração a perscrutar, a sondar a sabedoria e a razão das coisas, a reconhecer que a maldade é uma loucura e a falta de razão uma demência.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19409, 733, 26, 'Eu também descobri que a mulher é coisa mais amarga que a morte, porque ela é um laço, seu coração é uma rede, suas mãos são cadeias. Aquele que é agradável a Deus dela se livrará, mas o pecador será sua presa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19410, 733, 27, 'Eis o que encontrei, diz o Eclesiastes, examinando uma coisa com outra, para chegar à razão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19411, 733, 28, 'Eis o que procuro continuamente sem descobrir: encontrei um só homem entre mil, mas nenhuma mulher entre todas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19412, 733, 29, 'Somente descobri isto: Deus fez o homem reto, mas é ele quem procura os extravios.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19413, 734, 1, 'Quem é comparável ao sábio? Quem conhece a razão das coisas? A sabedoria de um homem ilumina-lhe o semblante e abranda-lhe a severidade de sua face.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19414, 734, 2, 'Observa as ordens do rei, e isso por causa do juramento feito a Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19415, 734, 3, 'Não te apres­ses a sair de sua presença. Não te comprometas com um mau negócio, porque o rei faz tudo o que lhe apraz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19416, 734, 4, 'Com efeito, sua palavra é soberana, e quem ousaria dizer-lhe: “Que fazes tu?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19417, 734, 5, 'Quem observa os preceitos não experimentará mal algum; e o coração do sábio conhece o tempo e o julgamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19418, 734, 6, 'Porque para tudo há um tempo e um julgamento, e a desgraça pesa muito forte sobre o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19419, 734, 7, 'Ele não conhece o futuro; quem lhe poderia dizer como as coisas se passarão?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19420, 734, 8, 'O homem não é senhor de seu sopro de vida, nem é capaz de retê-lo. Ninguém tem poder sobre o dia da morte, nem a faculdade de afastar esse combate. O crime não pode salvar o criminoso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19421, 734, 9, 'Eis o que eu vi, aplicando meu espírito a tudo o que se faz debaixo do sol, quando um homem domina sobre outro homem para prejudicá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19422, 734, 10, 'Vi ímpios receberem sepultura e gozarem de repouso, enquanto que aqueles que tinham feito o bem iam para longe do lugar santo e eram esquecidos na cidade. Isso é ainda fugacidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19423, 734, 11, 'Porque a sentença contra os maus atos não é executada imediatamente, o coração dos homens se enche do desejo de fazer o mal,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19424, 734, 12, 'porque o pecador culpado de cem crimes vê sua vida prolongada. Eu sei, no entanto, que a felicidade é para os que temem a Deus, porque respeitam a sua face.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19425, 734, 13, 'Mas não haverá felicidade para o ímpio que, como sombra, não prolongará sua vida, porque ele não teme a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19426, 734, 14, 'Há outra fugacidade que acontece sobre a terra: há justos, aos quais acontece o que conviria ao proceder de ímpios, e há ímpios aos quais acontece o que conviria ao proceder de justos. Digo que isso é também fugacidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19427, 734, 15, 'Por isso, louvei a alegria, porque não há nada de melhor para o homem, debaixo do sol, do que comer, beber e se divertir; possa isso acompanhá-lo no seu trabalho, ao longo dos dias que Deus lhe conceder debaixo do sol.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19428, 734, 16, 'Quando meu espírito se entregou ao estudo da sabedoria e à observação dos trabalhos que há sobre a terra, porque nem de dia, nem de noite os olhos dos homens encontram repouso,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19429, 734, 17, 'verifiquei, em toda a obra de Deus, que o homem nada pode descobrir do que se faz debaixo do sol. Ele se fatiga a investigar, mas não encontra, e se mesmo um sábio pensa ter alcançado, isso não acontecerá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19430, 735, 1, 'Apliquei então meu espírito ao esclarecimento de tudo isso: os justos e sábios, com todas as suas obras, estão na mão de Deus. O homem ignora se isso será amor ou ódio. Tudo é possível.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19431, 735, 2, 'Todos têm um só destino: há uma sorte idêntica ao justo e ao ímpio, ao que é bom como ao que é impuro, ao que ofere­ce sacrifícios como ao que deles se abstém. O homem bom é tratado como o pecador e o perjuro como o que respeita seu juramento.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19432, 735, 3, 'Entre tudo o que se faz debaixo do sol, é uma desgraça só existir para todos um mesmo destino. Por isso, o espírito dos homens transborda de malícia, a loucura ocupa o coração deles, durante a vida, depois da qual vão para a casa dos mortos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19433, 735, 4, 'Porque, enquanto um homem permanece entre os vivos, ainda há esperança, pois mais vale um cão vivo do que um leão morto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19434, 735, 5, 'Com efeito, os vivos sabem que hão de morrer, mas os mortos de nada sabem. Para eles não há mais recompensa, porque sua lembrança jaz no esquecimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19435, 735, 6, 'Amor, ódio e inveja acabaram. Não terão mais parte alguma, para o futuro, no que se faz debaixo do sol.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19436, 735, 7, 'Ora, pois, come com alegria o teu pão e bebe contente o teu vinho, porque Deus se agrada de teus trabalhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19437, 735, 8, 'Traja sempre vestes brancas e haja sempre azeite perfumado em tua cabeça.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19438, 735, 9, 'Desfruta da vida com a mulher que amas, durante todos os dias da fugitiva e vã existência que Deus te concede debaixo do sol. Essa é a tua parte na vida, o prêmio do labor a que te entregas debaixo do sol.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19439, 735, 10, 'Tudo o que tua mão encontra para fazer, faze-o com todas as tuas faculdades, pois que na região dos mortos, para onde vais, não há mais trabalho, nem ciência, nem inteligência, nem sabedoria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19440, 735, 11, 'Nas minhas investigações debaixo do sol, vi ainda que a corrida não é para os ágeis, nem a batalha para os bravos, nem o pão para os prudentes, nem a riqueza para os inteligentes, nem o favor para os sábios, porque todos estão à mercê das circunstâncias e da sorte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19441, 735, 12, 'O homem não conhece sua própria hora. Semelhantes aos peixes apanhados pela rede fatal, os passarinhos presos no laço, os homens são enlaçados na hora da calamidade, que se arremessa sobre eles de súbito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19442, 735, 13, 'Vi também, debaixo do sol, este exem­plo de sabedoria, que me pareceu grande.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19443, 735, 14, 'Havia uma pequena cidade, pouco populosa. Veio contra ela um poderoso rei que, sitiando-a, construiu grandes fortificações ao seu redor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19444, 735, 15, 'Ora, aí se encontrava um homem pobre, porém sábio, cuja sabedoria salvou a cidade, contudo ninguém se lembrou desse pobre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19445, 735, 16, 'Por isso, eu disse: “A sabedoria vale mais que a força, mas a sabedoria do pobre é desprezada e às suas palavras não se dão ouvidos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19446, 735, 17, 'As palavras calmas dos sábios são mais bem ouvidas do que os gritos de um chefe entre insensatos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19447, 735, 18, 'A sabedoria vale mais que as máquinas de guerra, mas um só pecador pode causar a perda de muitos bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19448, 736, 1, 'Uma mosca morta infeta e corrompe o azeite perfumado. Um pouco de loucura é suficiente para corromper a sabedoria.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19449, 736, 2, 'O coração do sábio vai para a direi­ta, mas o coração do insensato para a esquerda.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19450, 736, 3, 'No meio da estrada, quando caminha o tolo, falta-lhe o bom senso, e todos dizem: “É um louco”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19451, 736, 4, 'Se a ira do príncipe se inflama contra ti, não abandones o teu lugar, porque a calma previne grandes erros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19452, 736, 5, 'Vi debaixo do sol um mal, uma falha da parte do soberano:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19453, 736, 6, 'o insensato ocupa os mais altos cargos, enquanto que os homens de valor estão colocados em empregos inferiores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19454, 736, 7, 'Vi escravos montarem a cavalo e príncipes andarem a pé como escravos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19455, 736, 8, 'Quem cava uma fossa poderá nela cair, e quem derruba um muro poderá ser picado por uma serpente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19456, 736, 9, 'Quem lavra pedras poderá machucar-se; quem corta a lenha se machuca com ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19457, 736, 10, 'Se o ferro estiver embotado e não for afiado o gume, é preciso redobrar de esforços; mas afiá-lo é uma vantagem que a sabedoria proporciona.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19458, 736, 11, 'Se a serpente morder por erro de encantamento, não vale a pena ser encantador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19459, 736, 12, 'As palavras do sábio alcançam-lhe o favor, mas os lábios do insensato causam a sua perda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19460, 736, 13, 'O começo de suas palavras é uma estultícia, e o final de seu discurso é uma perigosa insânia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19461, 736, 14, 'O insensato multiplica as palavras. O homem não conhece o futuro. Quem lhe poderia dizer o que há de acontecer em seguida?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19462, 736, 15, 'O trabalho do insensato o fatiga, pois nem sequer sabe ir à cidade.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19463, 736, 16, 'Ai de ti, país, cujo rei é um menino e cujos príncipes comem desde a manhã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19464, 736, 17, 'Feliz de ti, país, cujo rei é de família nobre e cujos príncipes comem em hora conveniente, não por devassidão, mas para sua própria refeição.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19465, 736, 18, 'Por causa do desleixo desabará o madeiramento e, quando as mãos são inativas, choverá dentro da casa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19466, 736, 19, 'Faz-se festa para se divertir; o vinho alegra a vida, e o dinheiro serve para tudo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19467, 736, 20, 'Não digas mal do rei, nem mesmo em pensamento! Mesmo dentro de teu quarto, não digas mal do poderoso. Porque um passarinho do céu poderia levar tua palavra, e as aves repetirem tuas frases.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19468, 737, 1, 'Atira teu pão sobre a superfície das águas, porque depois de muito tempo o acharás.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19469, 737, 2, 'Faze de tua riqueza sete e mesmo oito partes, porque não sabes que calamidade sobrevirá à terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19470, 737, 3, 'Quando as nuvens estiverem carregadas, derramarão a chuva sobre a terra. Quando tomba uma árvore para o sul ou para o norte, lá onde cai, fica.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19471, 737, 4, 'Quem observa o vento jamais semeará; quem examina as nuvens nunca segará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19472, 737, 5, 'Do mesmo modo que não sabes qual é o caminho do sopro da vida e como se formam os ossos no seio de uma mãe, assim também ignoras a ação de Deus, que faz todas as coisas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19473, 737, 6, 'Semeia a tua semente desde a manhã e não deixes tuas mãos ociosas até a noite. Porque não sabes o que terá bom êxito, se isto ou aquilo, ou se ambas as coisas são igualmente úteis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19474, 737, 7, 'Doce é a luz, e é um deleite para os olhos ver o sol.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19475, 737, 8, 'Por mais numerosos que sejam os anos de vida, regozija-se o homem em todos eles, mas deve pensar nos dias obscuros que serão numerosos. Tudo o que acontece é vaidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19476, 737, 9, 'Jovem, rejubila-te na tua adolescência e, enquanto ainda és jovem, entrega teu coração à alegria. Anda nos caminhos de teu coração e segundo os olhares de teus olhos, mas fica sabendo que de tudo isso Deus te fará prestar conta!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19477, 737, 10, 'Afasta a tristeza do teu coração e poupa o sofrimento a teu corpo, porque a juventude e a adolescência são passageiras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19478, 738, 1, 'Lembra-te do teu Criador nos dias da tua juventude, antes que venham os maus dias e que apareçam os anos dos quais dirás: “Não sinto prazer neles!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19479, 738, 2, 'Antes que se escureçam o sol, a luz, a lua e as estrelas, e que à chuva sucedam as nuvens;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19480, 738, 3, 'anos nos quais tremem os guardas da casa, nos quais se curvam os homens robustos e param de moer as moleiras pouco numerosas, nos quais se escurecem aqueles que olham pelas janelas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19481, 738, 4, 'nos quais se fecham para a rua os dois batentes da porta, nos quais se enfraquece o ruído do moinho, nos quais os homens se levantam ao canto do passarinho, nos quais se extingue o som da voz,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19482, 738, 5, 'nos quais se temem as subidas; nos quais se terão sobressaltos no caminho, nos quais a amendoeira branqueia, nos quais o gafanhoto engorda, nos quais a alcaparra perde a sua eficácia, porque o homem se encaminha para a morada eterna e os carpidores percorrem as ruas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19483, 738, 6, 'Antes que se rompa o cordão de prata, que se despedace a lâmpada de ouro, antes que se quebre a bilha na fonte, e que se fenda a roldana sobre a cisterna;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19484, 738, 7, 'antes que a poeira retorne à terra para se tornar o que era; e antes que o sopro de vida retorne a Deus que o deu.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19485, 738, 8, 'fugacidade das fugacidades, diz o Eclesiastes, tudo é fugaz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19486, 738, 9, 'Além de ser sábio, o Eclesiastes ensinou a ciência ao povo. Ele pesou, perscrutou e dispôs numerosas máximas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19487, 738, 10, 'O Eclesiastes aplicou-se à procura de sentenças agradáveis e a redigir com exatidão adágios verídicos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19488, 738, 11, 'As palavras dos sábios são seme­lhantes a aguilhões, e as sentenças, reunidas em coleção, são parecidas a estacas plantadas, inspiradas por um só pastor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19489, 738, 12, 'De resto, meu filho, quanto ao maior número de palavras que estas, fica sabendo que se podem multiplicar os li­vros a não mais acabar, e que muito estudo se torna uma fadiga para o corpo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19490, 738, 13, 'Em conclusão: tudo bem entendido, teme a Deus e observa seus preceitos, é este o dever de todo homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19491, 738, 14, 'Deus fará prestar contas de tudo o que está oculto, todo ato, seja ele bom ou mau.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19492, 739, 1, 'O mais belo dos Cânticos de Salomão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19493, 739, 2, '– Ah! Beija-me com os beijos de tua boca! Porque os teus amores são mais deliciosos que o vinho,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19494, 739, 3, 'e suave é a fragrância de teus perfumes; o teu nome é como um perfume derramado: por isso, amam-te as jovens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19495, 739, 4, 'Arrasta-me após ti; corramos! O rei introduziu-me nos seus aposentos. Exultaremos de alegria e de júbilo em ti. Tuas carícias nos inebriarão mais que o vinho. Quanta razão há de te amar!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19496, 739, 5, 'Sou morena, mas sou bela, ó filhas de Jerusalém, como as tendas de Cedar, como os pavilhões de Salomão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19497, 739, 6, 'Não repareis em minha tez morena, pois fui queimada pelo sol. Os filhos de minha mãe irritaram-se contra mim; puseram-me a guardar as vinhas, mas não guardei a minha própria vinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19498, 739, 7, 'Dize-me, ó tu, que meu coração ama, onde apascentas o teu rebanho, onde o levas a repousar ao meio-dia, para que eu não ande vagueando junto aos rebanhos dos teus companheiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19499, 739, 8, '– Se não o sabes, ó tu, a mais bela das mulheres, vai, segue as pisadas das ovelhas e apascenta os cabritos junto às cabanas dos pastores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19500, 739, 9, '– À égua dos carros do faraó eu te comparo, ó minha amada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19501, 739, 10, 'Tuas faces são graciosas entre os brincos, e o teu pescoço entre colares de pérolas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19502, 739, 11, 'Faremos para ti brincos de ouro com glóbulos de prata.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19503, 739, 12, '– Enquanto o rei descansa em seu divã, meu nardo exala o seu perfume.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19504, 739, 13, 'O meu bem-amado é para mim como um saquitel de mirra que repousa entre os meus seios;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19505, 739, 14, 'o meu bem-amado é para mim um cacho de uvas nas vinhas de Engadi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19506, 739, 15, '– Como és formosa, amada minha! Como és bela! Teus olhos são como pombas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19507, 739, 16, '– Como és belo, meu amado! Como és encantador! O nosso leito é um leito verdejante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19508, 739, 17, 'As vigas de nossa casa são de cedro, suas traves, de cipreste!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19509, 740, 1, 'Sou o narciso de Saron, o lírio dos vales.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19510, 740, 2, '– Como o lírio entre os espinhos, assim é minha amada entre as jovens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19511, 740, 3, '– Como a macieira entre as árvores da floresta, assim é o meu amado entre os jovens; gosto de sentar-me à sua sombra, e seu fruto é doce à minha boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19512, 740, 4, 'Ele introduziu-me num celeiro, e o estandarte, que levanta sobre mim, é o amor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19513, 740, 5, 'Restaurou-me com tortas de uva, fortaleceu-me com maçãs, porque estou enferma de amor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19514, 740, 6, 'Sua mão esquerda está sob minha cabeça, e sua direita abraça-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19515, 740, 7, '– Conjuro-vos, ó filhas de Jerusalém, pelas gazelas e as corças dos campos, que não desperteis nem perturbeis o amor, até que ele o queira.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19516, 740, 8, '– Oh, esta é a voz do meu amado! Ei-lo que aí vem, saltando sobre os montes, pulando sobre as colinas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19517, 740, 9, 'Meu amado é como a gazela ou como um cervozinho. Ei-lo que está atrás da nossa parede. Olha pela janela, espreita pelas grades.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19518, 740, 10, 'Meu bem-amado disse-me: “Levanta-te, minha amada; vem, formosa minha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19519, 740, 11, 'Eis que o inverno passou: cessaram e desapareceram as chuvas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19520, 740, 12, 'Apareceram as flores na nossa terra, voltou o tempo das canções. Em nossas terras já se ouve a voz da rola.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19521, 740, 13, 'A figueira já começa a dar os seus figos, e a vinha em flor exala o seu perfume; levanta-te, minha amada, formosa minha, e vem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19522, 740, 14, 'Minha pomba, oculta nas fendas do rochedo, e nos abrigos das rochas escarpadas, mostra-me o teu rosto, faze-me ouvir a tua voz. Tua voz é tão doce e delicado teu rosto!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19523, 740, 15, '– Apanhai-nos as raposas, essas pequenas raposas que devastam nossas vinhas, pois nossas vinhas estão em flor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19524, 740, 16, '– Meu bem-amado é para mim, e eu para ele; ele apascenta entre os lírios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19525, 740, 17, 'Antes que sopre a brisa do dia, e se estendam as sombras, volta, ó meu amado, como a gazela ou o cervozinho para os montes escarpados.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19526, 741, 1, 'Durante a noite, no meu leito, busquei o meu amado; procurei-o, sem encontrá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19527, 741, 2, 'Vou levantar-me e percorrer a cidade, as ruas e as praças, em busca daquele que meu coração ama; procurei-o, sem encontrá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19528, 741, 3, 'Os guardas encontraram-me quando faziam a sua ronda na cidade. “Vistes acaso aquele que meu coração ama?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19529, 741, 4, 'Mal passara por eles, encontrei aquele que meu coração ama. Segurei-o, e não o largarei antes que o tenha introduzido na casa de minha mãe, no quarto daquela que me concebeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19530, 741, 5, '– Conjuro-vos, ó filhas de Jerusalém, pelas gazelas e corças do campo, não desperteis nem perturbeis o amor, até que ele o queira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19531, 741, 6, '– Que é aquilo que sobe do deserto como colunas de fumaça, exalando o perfume de mirra e de incenso, e de todos os aromas dos mercadores?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19532, 741, 7, 'É a liteira de Salomão, escoltada por sessenta guerreiros, sessenta valentes de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19533, 741, 8, 'Todos hábeis manejadores da espada, e exercitados no combate; cada um deles leva a espada ao lado por causa dos terrores noturnos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19534, 741, 9, 'O rei Salomão mandou fazer para si uma liteira de madeira do Líbano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19535, 741, 10, 'Suas colunas são feitas de prata, seu encosto, de ouro, seu assento, de púrpura. O interior é bordado pelo amor das filhas de Jerusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19536, 741, 11, 'Saí, ó filhas de Sião, contemplai o rei Salomão, ostentando o diadema recebido de sua mãe no dia de suas núpcias, no dia da alegria de seu coração.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19537, 742, 1, '– Tu és bela, minha querida, tu és formosa! Através do teu véu os teus olhos são como pombas, teus cabelos são como um rebanho de cabras, descendo impetuosas pela montanha de Galaad,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19538, 742, 2, 'teus dentes são como um rebanho de ovelhas tosquiadas que saem do banho; cada uma leva dois cordeirinhos gêmeos, e nenhuma há estéril entre elas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19539, 742, 3, 'Teus lábios são como um fio de púrpura, e graciosa é tua boca. Tua face é como um pedaço de romã debaixo do teu véu;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19540, 742, 4, 'teu pescoço é semelhante à torre de Davi, construída para depósito de armas. Aí estão pendentes mil escudos, todos os escudos dos valentes.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19541, 742, 5, 'Os teus dois seios são como filhotes gêmeos de uma gazela, pastando entre os lírios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19542, 742, 6, 'Antes que sopre a brisa do dia, e se estendam as sombras, irei ao monte da mirra, e à colina do incenso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19543, 742, 7, 'És toda bela, ó minha amada, e não há mancha em ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19544, 742, 8, 'Vem comigo do Líbano, ó esposa, vem comigo do Líbano! Olha dos cumes do Amaná, do cimo de Sanir e do Hermon, das cavernas dos leões, dos esconderijos das panteras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19545, 742, 9, 'Tu me fazes delirar, minha irmã, minha noiva, tu me fazes delirar com um só dos teus olhares, com um só colar do teu pescoço.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19546, 742, 10, 'Como são deliciosas as tuas carícias, minha irmã, minha noiva! Mais deliciosos que o vinho são teus amores, e o odor dos teus perfumes excede o de todos os aromas!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19547, 742, 11, 'Teus lábios, ó noiva, destilam o mel; há mel e leite sob a tua língua. O perfume de tuas vestes é como o perfume do Líbano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19548, 742, 12, 'És um jardim fechado, minha irmã, minha noiva, uma nascente fechada, uma fonte selada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19549, 742, 13, 'Teus rebentos são como um bosque de romãs com frutos deliciosos; com ligústica e nardo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19550, 742, 14, 'nardo e açafrão, canela e cinamomo, com todas as árvores de incenso, mirra e aloés, com os bálsamos mais preciosos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19551, 742, 15, 'És a fonte do meu jardim, uma fonte de água viva, um riacho que corre do Líbano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19552, 742, 16, '– Levanta-te, vento do norte, vem tu, vento do sul. Sopra no meu jardim para que se espalhem os meus perfumes. Entre meu amado no seu jardim, prove-lhe os frutos deliciosos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19553, 743, 1, '– Entro no meu jardim, minha irmã, minha noiva, colho a minha mirra e o meu bálsamo, como o meu favo com meu mel, bebo o meu vinho com o meu leite. Amigos, comei e bebei; inebriai-vos, ó caríssimos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19554, 743, 2, 'Eu dormia, mas meu coração velava. Eis a voz do meu amado. Ele bate. Abre-me, minha irmã, minha amada, minha pomba, minha perfeita; minha cabeça está coberta de orvalho, e os cachos de meus cabelos cheios das gotas da noite.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19555, 743, 3, 'Tirei minha túnica; como irei revesti-la? Lavei os meus pés; por que sujá-los de novo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19556, 743, 4, 'Meu bem-amado passou a mão pela abertura (da porta) e o meu coração estremeceu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19557, 743, 5, 'Levantei-me para abrir ao meu amado; a mirra escorria de minhas mãos, de meus dedos a mirra líquida sobre os trincos do ferrolho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19558, 743, 6, 'Abri ao meu bem-amado, mas ele já se tinha ido, já tinha desaparecido; ouvindo-o falar, eu ficava fora de mim. Procurei-o e não o encontrei; chamei-o, mas ele não respondeu.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19559, 743, 7, 'Os guardas encontraram-me, quando faziam sua ronda na cidade. Bateram-me, feriram-me, arrancaram-me o manto os guardas das muralhas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19560, 743, 8, 'Conjuro-vos, ó filhas de Jerusalém, se encontrardes o meu amado, que lhe haveis de dizer? Dizei-lhe que estou enferma de amor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19561, 743, 9, '– Que tem o teu bem-amado a mais que os outros, ó tu, a mais bela das mulheres? Que tem o teu bem-amado a mais que os outros, para que assim nos conjures?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19562, 743, 10, '– Meu amado é forte e corado, distingue-se entre dez mil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19563, 743, 11, 'Sua cabeça é de ouro puro, seus cachos flexíveis são negros como o corvo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19564, 743, 12, 'Seus olhos são como pombas à beira dos regatos, banhando-se em leite, pousadas nas praias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19565, 743, 13, 'Suas faces são um jardim perfumado onde crescem plantas perfumadas. Seus lábios são lírios que destilam mirra líquida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19566, 743, 14, 'Suas mãos são argolas de ouro incrustadas de pedrarias. Seu corpo é um bloco de marfim recoberto de safiras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19567, 743, 15, 'Suas pernas são colunas de alabastro erguidas sobre pedestais de ouro puro. Seu aspecto é como o do Líbano, imponente como os cedros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19568, 743, 16, 'Sua boca é cheia de doçura, tudo nele é encanto. Assim é o meu amigo, tal é o meu amado, ó filhas de Jerusalém!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19569, 744, 1, '– Para onde foi o teu amado, ó tu, a mais bela das mulheres? Para onde se retirou o teu amado? Nós o buscaremos contigo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19570, 744, 2, '– O meu bem-amado desceu ao seu jardim, aos canteiros perfumados; para apascentar em meu jardim, e colher lírios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19571, 744, 3, 'Eu sou do meu amado e meu amado é meu. Ele apascenta entre os lírios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19572, 744, 4, '– És formosa, amada minha, como Tirsa, graciosa como Jerusalém, temível como um exército em ordem de batalha.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19573, 744, 5, 'Desvia de mim os teus olhos, porque eles me fascinam. Teus cabelos são como um rebanho de cabras descendo impetuosamente pelas encostas de Galaad.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19574, 744, 6, 'Teus dentes são como um rebanho de ovelhas que sobem do banho; cada uma leva dois (cordeirinhos) gêmeos, e nenhuma delas é estéril.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19575, 744, 7, 'Tua face é como um pedaço de romã debaixo do teu véu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19576, 744, 8, 'Há sessenta rainhas, oitenta concubinas, e inumeráveis jovens mulheres;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19577, 744, 9, 'uma, porém, é a minha pomba, uma só a minha perfeita; ela é a única de sua mãe, a predileta daquela que a deu à luz. Ao vê-la, as donzelas proclamam-na bem-aventurada, rainhas e concubinas a louvam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19578, 744, 10, 'Quem é esta que surge como a aurora, bela como a lua, brilhante como o sol, temível como um exército em ordem de batalha?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19579, 744, 11, 'Eu desci ao jardim das nogueiras para ver a nova vegetação dos vales, e para ver se a vinha crescia e se as romãzeiras estavam em flor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19580, 744, 12, 'Eu não o sabia; minha alma colocou-me nos carros de Aminadab.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19581, 745, 1, '– Volta, volta, ó Sulamita; volta, volta, para que nós te vejamos. – Por que olhais a Sulamita, quando ela entra na dança de Maanaim?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19582, 745, 2, '– Como são graciosos os teus pés nas tuas sandálias, filha de príncipe! A curva de teus quadris assemelha-se a um colar, obra de mãos de artista;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19583, 745, 3, 'teu umbigo é uma taça redonda, cheia de vinho perfumado; teu corpo é um monte de trigo cercado de lírios;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19584, 745, 4, 'teus dois seios são como dois filhotes gêmeos de uma gazela;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19585, 745, 5, 'teu pescoço é uma torre de marfim; teus olhos são as fontes de Hesebon junto à porta de Bat-Rabim. Teu nariz é como a torre do Líbano, que olha para os lados de Damasco;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19586, 745, 6, 'tua cabeça ergue-se sobre ti como o Carmelo; tua cabeleira é como a púrpura, e um rei se acha preso aos seus cachos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19587, 745, 7, '– Como és bela e graciosa, ó meu amor, ó minhas delícias!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19588, 745, 8, 'Teu porte assemelha-se ao da palmeira, de que teus dois seios são os cachos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19589, 745, 9, '“Vou subir à palmeira – disse eu comigo mesmo – e colherei os seus frutos.” Sejam-me os teus seios como cachos da vinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19590, 745, 10, 'E o perfume de tua boca como o odor das maçãs; teus beijos são como um vinho delicioso que corre para o bem-amado, umedecendo-lhe os lábios na hora do sono.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19591, 745, 11, '– Eu sou para o meu amado o objeto de seus desejos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19592, 745, 12, 'Vem, meu bem-amado, saiamos ao campo, passemos a noite nos pomares;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19593, 745, 13, 'pela manhã iremos às vinhas, para ver se a vinha lançou rebentos, se as suas flores se abrem, se as romãzeiras estão em flor. Ali te darei as minhas carícias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19594, 745, 14, 'As mandrágoras exalam o seu perfume; temos à nossa porta frutos excelentes, novos e velhos que guardei para ti, meu bem-amado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19595, 746, 1, 'Ah, se fosses meu irmão, amamentado ao seio de minha mãe! Então, encontrando-te fora, poderia beijar-te sem que ninguém me censurasse.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19596, 746, 2, 'Eu te levaria, te faria entrar na casa de minha mãe; te daria a beber vinho perfumado, licor de minhas romãs.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19597, 746, 3, 'Sua mão esquerda está sob a minha cabeça, e sua direita abraça-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19598, 746, 4, '– Conjuro-vos, ó filhas de Jerusalém, não desperteis nem perturbeis o amor, antes que ele o queira.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19599, 746, 5, '– Quem é esta que sobe do deserto apoiada em seu bem-amado? – Sob a macieira eu te despertei, onde em dores te deu à luz tua mãe, onde em dores te pôs no mundo tua mãe.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19600, 746, 6, '– Põe-me como um selo sobre o teu coração, como um selo sobre os teus braços; porque o amor é forte como a morte, a paixão é violenta como o Sheol.* Suas centelhas são centelhas de fogo, uma chama divina.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19601, 746, 7, 'As torrentes não poderiam extinguir o amor, nem os rios o poderiam submergir. Se alguém desse toda a riqueza de sua casa em troca do amor, só obteria desprezo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19602, 746, 8, 'Temos uma irmã pequenina que não tem ainda os seus seios formados. Que faremos nós de nossa irmã no dia em que for pedida em casamento?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19603, 746, 9, 'Se ela é um muro, construiremos sobre ela ameias de prata. Se é uma porta, a fecharemos com batentes de cedro.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19604, 746, 10, '– Ora, eu sou um muro, e meus seios são como torres; por isso, sou aos seus olhos uma fonte de alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19605, 746, 11, 'Salomão tinha uma videira em Baal-Hamon. Confiou-a aos guardas: cada um dos quais devia dar mil siclos de prata pelos frutos colhidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19606, 746, 12, 'Eu disponho de minha videira: mil siclos para ti, Salomão! Duzentos para aqueles que velam pela colheita.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19607, 746, 13, '– Os amigos estão atentos. Ó tu, que habitas nos jardins, faze-me ouvir a tua voz.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19608, 746, 14, '– Foge, meu bem-amado, como a gazela, ou como o cervozinho sobre os montes perfumados!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19609, 747, 1, 'Amai a justiça, vós que governais a terra, tende para com o Senhor sentimentos perfeitos, e procurai-o na simplicidade do coração,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19610, 747, 2, 'porque ele é encontrado pelos que não o tentam, e se revela aos que não lhe recusam sua confiança;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19611, 747, 3, 'com efeito, os pensamentos tortuosos afastam de Deus, e o seu poder, posto à prova, triunfa dos insensatos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19612, 747, 4, 'A sabedoria não entrará na alma perversa, nem habitará no corpo sujeito ao pecado;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19613, 747, 5, 'o Espírito Santo educador das almas fugirá da perfídia, se afastará dos pensamentos insensatos, e a iniquidade que sobrevém o repelirá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19614, 747, 6, 'Sim, a sabedoria é um espírito que ama os homens, mas não deixará sem castigo o blasfemador pelo crime de seus lábios, porque Deus lhe sonda os rins, penetra até o fundo de seu coração, e ouve as suas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19615, 747, 7, 'Com efeito, o Espírito do Senhor enche o universo, e ele, que tem unidas todas as coisas, ouve toda voz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19616, 747, 8, 'Aquele que profere uma linguagem iníqua, não pode fugir dele, e a justiça vingadora não o deixará escapar;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19617, 747, 9, 'pois os próprios desígnios do ímpio serão cuidadosamente examinados; o som de suas palavras chegará até o Senhor, que lhe imporá o castigo pelos seus pecados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19618, 747, 10, 'É, com efeito, um ouvido cioso, que tudo ouve: nem a menor murmuração lhe passa despercebida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19619, 747, 11, 'Acautelai-vos, pois, de queixar-vos inutilmente, evitai que vossa língua se entregue à crítica, porque até mesmo uma palavra secreta não ficará sem castigo, e a boca que acusa com injustiça arrasta a alma à morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19620, 747, 12, 'Não procureis a morte por uma vida desregrada, não sejais o próprio artífice de vossa perda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19621, 747, 13, 'Deus não é o autor da morte, a perdição dos vivos não lhe dá alegria alguma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19622, 747, 14, 'Ele criou tudo para a existência, e as criaturas do mundo devem cooperar para a salvação. Nelas nenhum princípio é funesto, e a morte não é a rainha da terra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19623, 747, 15, 'porque a justiça é imortal.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19624, 747, 16, 'Mas a morte os ímpios a chamam com o gesto e a voz. Crendo-a amiga, consomem-se de desejos, e fazem aliança com ela; de fato, eles merecem ser sua presa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19625, 748, 1, 'Dizem, com efeito, nos seus falsos raciocínios: “Curta é a nossa vida, e cheia de tristezas; para a morte não há remédio algum; não há notícia de alguém que tenha voltado da região dos mortos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19626, 748, 2, 'Um belo dia nascemos e, depois disso, seremos como se jamais tivéssemos sido! É fumaça a respiração de nossos narizes, e nosso pensamento, uma centelha que salta do bater de nosso coração!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19627, 748, 3, 'Extinta ela, nosso corpo se tornará pó, e o nosso espírito se dissipará como um vapor inconsistente!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19628, 748, 4, 'Com o tempo nosso nome cairá no esquecimento, e ninguém se lembrará de nossas obras. Nossa vida passará como os traços de uma nuvem, ela desvanecerá como uma névoa que os raios do sol expulsam, e que seu calor dissipa.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19629, 748, 5, 'A passagem de uma sombra: eis a nossa vida, e nenhum reinício é possível uma vez chegado o fim, porque o selo lhe é aposto e ninguém volta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19630, 748, 6, 'Vinde, portanto! Aproveitemo-nos das boas coisas que existem! Vivamente gozemos das criaturas durante nossa juventude!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19631, 748, 7, 'Inebriemo-nos de vinhos preciosos e de perfumes, e não deixemos passar a flor da primavera!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19632, 748, 8, 'Coroemo-nos de botões de rosas antes que eles murchem!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19633, 748, 9, 'Ninguém de nós falte à nossa orgia; em toda parte deixemos sinais de nossa alegria, porque esta é a nossa parte, esta a nossa sorte!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19634, 748, 10, 'Tiranizemos o justo na sua pobreza, não poupemos a viúva, e não tenhamos consideração com os cabelos brancos do ancião!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19635, 748, 11, 'Que a nossa força seja o critério do direito, porque o fraco, em verdade, não serve para nada.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19636, 748, 12, 'Cerquemos o justo, porque ele nos incomoda; é contrário às nossas ações; ele nos censura por violar a lei e nos acusa de contrariar a nossa educação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19637, 748, 13, 'Ele se gaba de conhecer a Deus, e se chama a si mesmo filho do Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19638, 748, 14, 'Sua existência é uma censura às nossas ideias; basta sua vista para nos importunar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19639, 748, 15, 'Sua vida, com efeito, não se parece com as outras, e os seus caminhos são muito diferentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19640, 748, 16, 'Ele nos tem por uma moeda de mau quilate, e afasta-se de nossos caminhos como de manchas. Julga feliz a morte do justo, e gloria-se de ter Deus por pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19641, 748, 17, 'Vejamos, pois, se suas palavras são verdadeiras, e experimentemos o que acontecerá quando da sua morte,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19642, 748, 18, 'porque, se o justo é filho de Deus, Deus o defenderá, e o tirará das mãos dos seus adversários.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19643, 748, 19, 'Provemo-lo por ultrajes e torturas, a fim de conhecer a sua doçura e estarmos cientes de sua paciência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19644, 748, 20, 'Condenemo-lo a uma morte infame. Porque, conforme ele, Deus deve intervir”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19645, 748, 21, 'Eis o que pensam, mas enganam-se, sua malícia os cega:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19646, 748, 22, 'eles desconhecem os segredos de Deus, não esperam que a santidade seja recompensada, e não acreditam na glorificação das almas puras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19647, 748, 23, 'Ora, Deus criou o homem para a imortalidade, e o fez à imagem de sua própria natureza.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19648, 748, 24, 'É por inveja do demônio que a morte entrou no mundo, e os que pertencem ao demônio a provarão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19649, 749, 1, 'Mas as almas dos justos estão na mão de Deus, e nenhum tormento os tocará.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19650, 749, 2, 'Aparentemente estão mortos aos olhos dos insensatos: seu desenlace é julgado como uma desgraça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19651, 749, 3, 'E sua morte como uma destruição, quando na verdade estão na paz!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19652, 749, 4, 'Se aos olhos dos homens suportaram uma correção, a esperança deles era portadora de imortalidade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19653, 749, 5, 'e por terem sofrido um pouco, receberão grandes bens, porque Deus, que os provou, achou-os dignos de si.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19654, 749, 6, 'Ele os provou como ouro na fornalha, e os acolheu como holocausto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19655, 749, 7, 'No dia de sua visita, eles se reanimarão, e correrão como centelhas na palha.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19656, 749, 8, 'Eles julgarão as nações e dominarão os povos, e o Senhor reinará sobre eles para sempre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19657, 749, 9, 'Os que põem sua confiança nele compreenderão a verdade, e os que são fiéis habitarão com ele no amor: porque seus eleitos são dignos de favor e misericórdia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19658, 749, 10, 'Mas os ímpios terão o castigo que merecem seus pensamentos, uma vez que desprezaram o justo e se separaram do Senhor: e desgraçado é aquele que rejeita a sabedoria e a disciplina!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19659, 749, 11, 'A esperança deles é vã, seus sofrimentos sem proveito, e as obras deles inúteis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19660, 749, 12, 'Suas mulheres são insensatas e seus filhos malvados; a raça deles é maldita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19661, 749, 13, 'Feliz a mulher estéril, mas pura de toda a mancha, a que não manchou seu leito conjugal: ela carregará seu fruto no dia da retribuição das almas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19662, 749, 14, 'Feliz o eunuco cuja mão não cometeu o mal, que não concebeu iniquidade contra o Senhor, porque ele receberá pela sua fidelidade uma graça superior, e no Templo do Senhor uma parte muito honrosa,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19663, 749, 15, 'porque é esplêndido o fruto de bons trabalhos, e a raiz da sabedoria é sempre fértil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19664, 749, 16, 'Quanto aos filhos dos adúlteros, a nada chegarão, e a raça que descende do pecado será aniquilada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19665, 749, 17, 'Ainda que vivam muito tempo, serão tidos por nada e, finalmente, sua velhice será sem honra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19666, 749, 18, 'Caso morram cedo, não terão esperança alguma, e no dia do julgamento não encontrarão nenhuma piedade:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19667, 749, 19, 'porque é lamentável o fim de uma raça injusta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19668, 750, 1, 'Mais vale uma vida sem filhos, mas rica de virtudes: sua memória será imortal, porque será conhecida de Deus e dos homens.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19669, 750, 2, 'Quando está presente, imitam-na; quando passada, desejam-na; ela leva na glória uma coroa eterna, por ter triunfado sem mancha nos combates.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19670, 750, 3, 'Mas para nada servirá, ainda que numerosa, a raça dos ímpios; procedendo de renovos bastardos, não estenderá raízes profundas, não se estabelecerá numa base sólida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19671, 750, 4, 'Ainda que por algum tempo estenda seus ramos, estando instavelmente assentada, será abalada pelo vento e, pela violência da tempestade, será desarraigada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19672, 750, 5, 'Os galhos serão quebrados antes do desenvolvimento, o fruto deles será inútil, verde demais para ser comido, e impróprio para qualquer uso,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19673, 750, 6, 'porque os filhos nascidos de uniões ilícitas serão no dia do juízo testemunhas a deporem contra seus pais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19674, 750, 7, 'Quanto ao justo, mesmo que morra antes da idade, gozará de repouso.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19675, 750, 8, 'A honra da velhice não provém de uma longa vida, e não se mede pelo número dos anos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19676, 750, 9, 'Mas é a sabedoria que faz as vezes dos cabelos brancos; é uma vida pura que se tem em conta de velhice.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19677, 750, 10, 'Ele agradou a Deus e foi por ele amado, assim (Deus) o transferiu do meio dos pecadores onde vivia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19678, 750, 11, 'Foi arrebatado para que a malícia lhe não corrompesse o sentimento, nem a astúcia lhe pervertesse a alma:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19679, 750, 12, 'porque a fascinação do vício atira um véu sobre a beleza moral, e o movimento das paixões mina uma alma ingênua.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19680, 750, 13, 'Tendo chegado rapidamente ao termo, percorreu uma longa carreira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19681, 750, 14, 'Sua alma era agradável ao Senhor, e é por isso que ele o retirou depressa do meio da perversidade. Os povos que veem esse modo de agir não o compreendem, e não refletem nisto:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19682, 750, 15, 'que o favor de Deus e sua misericórdia são para seus eleitos, e sua assistência está no meio de seus fiéis.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19683, 750, 16, 'O justo, ao morrer, condena os ímpios que sobrevivem, e a juventude, atingindo tão depressa a perfeição, confunde a longa velhice do pecador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19684, 750, 17, 'Eles verão o fim do sábio, e não compreenderão os desígnios do Senhor a seu respeito, nem por que ele o pôs em segurança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19685, 750, 18, 'Eles verão e mostrarão desprezo, mas o Senhor zombará deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19686, 750, 19, 'Depois disso serão cadáveres sem honra, desterrados entre os mortos, em uma eterna ignomínia, porque ele os ferirá, e os precipitará sem voz, ele os abaterá nas suas bases e os mergulhará na última desolação. Eles serão entregues à dor, e a memória deles perecerá.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19687, 750, 20, 'Comparecerão aterrorizados com a lembrança de seus pecados, e suas iniquidades se levantarão contra eles para confundi-los.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19688, 751, 1, 'Então, com grande confiança, o justo se levantará em face dos que o perseguiram e zombaram dos seus males aqui embaixo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19689, 751, 2, 'Diante de sua vista serão presos de grande temor e tomados de assombro ao vê-lo salvo contra sua expectativa;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19690, 751, 3, 'tocados de arrependimento, dirão entre si: e, gemendo na angústia de sua alma, dirão:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19691, 751, 4, '“Ei-lo, aquele de quem outrora escarnecemos, e a quem loucamente cobrimos de insultos! Considerávamos sua vida como uma loucura, e sua morte como uma vergonha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19692, 751, 5, 'Como, pois, é ele do número dos filhos de Deus, e como está seu lugar entre os santos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19693, 751, 6, 'Portanto, nós nos desgarramos para longe da verdade: a luz da justiça não brilhou para nós e o sol não se levantou sobre nós!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19694, 751, 7, 'Nós nos manchamos nas sendas da iniquidade e da perdição, erramos pelos desertos sem caminhos e não conhecemos o caminho do Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19695, 751, 8, 'O que ganhamos com nosso orgulho, e que nos trouxe a riqueza unida à arrogância?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19696, 751, 9, 'Tudo isso desapareceu como sombra, como notícia que passa;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19697, 751, 10, 'como navio que fende a água agitada, sem que se possa reencontrar o rasto de seu itinerário, nem a esteira de sua quilha nas ondas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19698, 751, 11, 'Como a ave que, atravessando o ar em seu voo, não deixa após si o traço de sua passagem, mas, ferindo o ar com suas penas, fende-o com a impetuosa força do bater de suas asas, atravessa-o e logo nem se nota indício de sua passagem;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19699, 751, 12, 'como quando uma flecha, que é lançada ao alvo, o ar que ela cortou volta imediatamente à sua posição de modo que não se pode distinguir sua trajetória,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19700, 751, 13, 'assim, também nós, apenas nascidos, cessamos de ser, e não podemos mostrar traço algum de virtude: é no mal que nossa vida se consumiu!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19701, 751, 14, 'Assim a esperança do ímpio é como a poeira levada pelo vento, e como uma leve espuma espalhada pela tempestade; ela se dissipa como o fumo ao vento, e passa como a lembrança do hóspede de um dia.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19702, 751, 15, 'Mas os justos viverão eternamente; sua recompensa está no Senhor, e o Altíssimo cuidará deles.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19703, 751, 16, 'Por isso, receberão a régia coroa de glória, e o diadema da beleza da mão do Senhor, porque os cobrirá com sua direita, e os protegerá com seu braço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19704, 751, 17, 'Por armadura tomará seu zelo cioso, e armará as criaturas para se vingar de seus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19705, 751, 18, 'Tomará por couraça a justiça, e por capacete a integridade no julgamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19706, 751, 19, 'Ele se cobrirá com a santidade, como com um impenetrável escudo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19707, 751, 20, 'afiará o gume de sua ira para lhe servir de espada, e o mundo se reunirá a ele na luta contra os insensatos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19708, 751, 21, 'Os raios partirão como flechas bem dirigidas, e, como de um arco bem distendido, voarão das nuvens para o alvo;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19709, 751, 22, 'uma balista fará cair uma pesada saraiva de ira; a água do mar se levantará em turbilhão contra eles e os rios os arrastarão impetuosamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19710, 751, 23, 'O sopro do Todo-poderoso se insurgirá contra eles e os dispersará como um furacão; a iniquidade fará de toda a terra um deserto, e a malícia derribará os tronos dos poderosos!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19711, 752, 1, 'Ouvi, pois, ó reis, e entendei; aprendei vós que governais o universo!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19712, 752, 2, 'Prestai ouvidos, vós que reinais sobre as nações e vos gloriais do número de vossos povos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19713, 752, 3, 'Porque é do Senhor que recebestes o poder, e é do Altíssimo que tendes o poderio; é ele que examinará vossas obras e sondará vossos pensamentos!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19714, 752, 4, 'Se, ministros do reino, vós não julgastes equitativamente, nem observastes a Lei, nem andastes segundo a vontade de Deus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19715, 752, 5, 'ele se apresentará a vós, terrível, inesperado, porque aqueles que dominam serão rigorosamente julgados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19716, 752, 6, 'Ao menor, com efeito, a compaixão atrai o perdão, mas os poderosos serão examinados sem piedade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19717, 752, 7, 'O Senhor de todos não fará exceção para ninguém, e não se deixará impor pela grandeza, porque, pequenos ou grandes, é ele que a todos criou, e de todos cuida igualmente;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19718, 752, 8, 'mas para os poderosos o julgamento será severo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19719, 752, 9, 'É a vós, pois, ó príncipes, que me dirijo, para que aprendais a sabedoria e não resvaleis,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19720, 752, 10, 'porque aqueles que santamente observarem as santas leis serão santificados, e os que as tiverem estudado poderão justificar-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19721, 752, 11, 'Ansiai, pois, pelas minhas palavras, reclamai-as ardentemente e sereis instruídos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19722, 752, 12, 'Resplandecente é a sabedoria, e sua beleza é inalterável: os que a amam, descobrem-na facilmente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19723, 752, 13, 'Os que a procuram encontram-na. Ela antecipa-se aos que a desejam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19724, 752, 14, 'Quem, para possuí-la, levanta-se de madrugada não terá trabalho, porque a encontrará sentada à sua porta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19725, 752, 15, 'Fazê-la objeto de seus pensamentos é a prudência perfeita, e quem por ela vigia, em breve não terá mais cuidado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19726, 752, 16, 'Ela mesma vai à procura dos que são dignos dela; ela lhes aparece nos caminhos cheia de benevolência, e vai ao encontro deles em todos os seus pensamentos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19727, 752, 17, 'porque, verdadeiramente, desde o começo, seu desejo é instruir, e desejar instruir-se é amá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19728, 752, 18, 'Mas amá-la é obedecer às suas leis, e obedecer às suas leis é a garantia da imortalidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19729, 752, 19, 'Ora, a imortalidade faz habitar junto de Deus;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19730, 752, 20, 'assim o desejo da sabedoria conduz ao Reino!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19731, 752, 21, 'Se, pois, cetros e tronos vos agradam, ó vós que governais os povos, honrai a sabedoria, e reinareis eternamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19732, 752, 22, 'Mas eu vou dizer o que é a sabedoria e como ela nasceu. Não vos esconderei os seus mistérios; mas a investigarei até sua mais remota origem; porei à luz o que dela pode ser conhecido, e não me afastarei da verdade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19733, 752, 23, 'Não imitarei aquele a quem a inveja consome, porque esse tal não tem nada a ver com a sabedoria:*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19734, 752, 24, 'é no grande número de sábios que se encontra a salvação do mundo, e um rei sensato faz a prosperidade de seu povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19735, 752, 25, 'Deixai-vos, pois, instruir por minhas palavras, e nelas encontrareis grande proveito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19736, 753, 1, 'Eu mesmo não passo de um mortal como todos os outros, e descendo do primeiro homem formado da terra. Meu corpo foi formado no seio de minha mãe,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19737, 753, 2, 'onde, durante dez meses, no sangue tomou consistência, da semente viril e do prazer ajuntado à união conjugal.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19738, 753, 3, 'Eu também, desde meu nascimento, respirei o ar comum; eu caí, da mesma maneira que todos, sobre a mesma terra, e, como todos, nos mesmos prantos soltei o primeiro grito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19739, 753, 4, 'Envolto em faixas fui criado no meio de assíduos cuidados;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19740, 753, 5, 'porque nenhum rei teve outro início na existência;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19741, 753, 6, 'para todos a entrada na vida é a mesma e a partida semelhante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19742, 753, 7, 'Assim implorei e a inteligência me foi dada, supliquei e o espírito da sabedoria veio a mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19743, 753, 8, 'Eu a preferi aos cetros e tronos, e avaliei a riqueza como um nada ao lado da sabedoria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19744, 753, 9, 'Não comparei a ela a pedra preciosa, porque todo o ouro ao lado dela é apenas um pouco de areia, e porque a prata diante dela será tida como lama.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19745, 753, 10, 'Eu a amei mais do que a saúde e a beleza, e gozei dela mais do que da claridade do sol, porque a claridade que dela emana jamais se extingue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19746, 753, 11, 'Com ela me vieram todos os bens, e nas suas mãos inumeráveis riquezas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19747, 753, 12, 'De todos esses bens eu me alegrei, porque é a sabedoria que os guia, mas ignorava que ela fosse sua mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19748, 753, 13, 'Eu estudei lealmente e reparto sem inveja e não escondo a riqueza que ela encerra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19749, 753, 14, 'porque ela é para os homens um tesouro inesgotável; e os que a adquirem preparam-se para se tornar amigos de Deus, recomendados (a ele) pela educação que ela lhes dá.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19750, 753, 15, 'Que Deus me permita falar como eu quisera, e ter pensamentos dignos dos dons que recebi, porque é ele mesmo quem guia a sabedoria e emenda os sábios,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19751, 753, 16, 'porque nós estamos nas suas mãos, nós e nossos discursos, toda a nossa inteligência e nossa habilidade;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19752, 753, 17, 'foi ele quem me deu a verdadeira ciência de todas as coisas, quem me fez conhecer a constituição do mundo e as virtudes dos elementos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19753, 753, 18, 'o começo, o fim e o meio dos tempos, a sucessão dos solstícios e as mutações das estações,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19754, 753, 19, 'os ciclos do ano e as posições dos astros,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19755, 753, 20, 'a natureza dos animais e os instintos dos brutos, os poderes dos espíritos e os pensamentos dos homens, a variedade das plantas e as propriedades das raízes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19756, 753, 21, 'Tudo que está escondido e tudo que está aparente eu conheço: porque foi a sabedoria, criadora de todas as coisas, que mo ensinou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19757, 753, 22, 'Há nela, com efeito, um espírito inteligente, santo, único, múltiplo, sutil, móvel, penetrante, puro, claro, inofensivo, inclinado ao bem, agudo,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19758, 753, 23, 'livre, benéfico, benévolo, estável, seguro, livre de inquietação, que pode tudo, que cuida de tudo, que penetra em todos os espíritos, os inteligentes, os puros, os mais sutis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19759, 753, 24, 'Mais ágil que todo o movimento é a sabedoria, ela atravessa e penetra tudo, graças à sua pureza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19760, 753, 25, 'Ela é um sopro do poder de Deus, uma irradiação límpida da glória do Todo-poderoso; assim mancha nenhuma pode insinuar-se nela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19761, 753, 26, 'É ela uma efusão da luz eterna, um espelho sem mancha da atividade de Deus, e uma imagem de sua bondade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19762, 753, 27, 'Embora única, tudo pode; imutável em si mesma, renova todas as coisas. Ela se derrama de geração em geração nas almas santas e forma os amigos e os intérpretes de Deus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19763, 753, 28, 'porque Deus somente ama quem vive com a sabedoria!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19764, 753, 29, 'É ela, com efeito, mais bela que o sol e ultrapassa o conjunto dos astros. Comparada à luz, ela se sobreleva,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19765, 753, 30, 'porque à luz sucede a noite, enquanto que, contra a sabedoria, o mal não prevalece.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19766, 754, 1, 'Ela estende seu vigor de uma extremidade do mundo à outra e governa todas as coisas com felicidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19767, 754, 2, 'Eu a amei e procurei desde minha juventude, esforcei-me por tê-la por esposa e me enamorei de seus encantos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19768, 754, 3, 'Ela mostra a nobreza de sua origem em conviver com Deus, ela é amada pelo Senhor de todas as coisas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19769, 754, 4, 'Ela é iniciada na ciência de Deus e, por sua escolha, decide de suas obras.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19770, 754, 5, 'Se a riqueza é um bem desejável na vida, que há de mais rico que a sabedoria que tudo criou?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19771, 754, 6, 'Se a inteligência do homem consegue operar, o que, então, mais que a sabedoria, é artífice dos seres?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19772, 754, 7, 'E, se alguém ama a justiça, seus trabalhos são virtudes; ela ensina a temperança e a prudência, a justiça e a força: não há ninguém que seja mais útil aos homens na vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19773, 754, 8, 'Se alguém deseja uma vasta ciência, ela sabe o passado e conjetura o futuro; conhece as sutilezas oratórias e revolve os enigmas; prevê os sinais e os prodígios, e o que tem que acontecer no decurso das idades e dos tempos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19774, 754, 9, 'Portanto, resolvi tomá-la por companheira de minha vida, cuidando que ela será para mim uma boa conselheira, e minha consolação nos cuidados e na tristeza.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19775, 754, 10, 'Graças a ela, receberei as honras das multidões, e, embora jovem como sou, o respeito dos anciãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19776, 754, 11, 'Reconhecerão a penetração de meu julgamento, e excitarei a admiração dos reis.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19777, 754, 12, 'Se me calo, esperarão que eu fale; se falo, estarão atentos; e se prolongo meu discurso, levarão a mão à boca.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19778, 754, 13, 'Por meio dela obterei a imortalidade, e deixarei à posteridade uma lembrança eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19779, 754, 14, 'Governarei povos e as nações me serão submissas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19780, 754, 15, 'Príncipes temíveis estarão cheios de medo ao ouvirem falar de mim; eu me mostrarei bom para com o povo e valoroso no combate.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19781, 754, 16, 'Recolhido em minha casa, repousarei junto dela, porque a sua convivência não tem nada de desagradável, e sua intimidade nada de fastidioso; ela traz consigo, pelo contrário, o contentamento e a alegria!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19782, 754, 17, 'Meditando comigo mesmo nesses pensamentos, e considerando em meu coração que a imortalidade se encontra na aliança com a sabedoria,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19783, 754, 18, 'a alegria perfeita na sua amizade, contínua riqueza na sua atividade, inteligência nas lições de seus entretenimentos familiares, e glória na comunicação de suas sentenças, saí à sua procura, a fim de possuí-la em mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19784, 754, 19, 'Eu era um menino vigoroso, dotado de uma alma excelente,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19785, 754, 20, 'ou antes, como era bom, eu vim a um corpo intato;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19786, 754, 21, 'mas, consciente de não poder possuir a sabedoria, a não ser por dom de Deus, e já era inteligência o saber de onde vem o dom, eu me voltei para o Senhor, e invoquei-o, dizendo do fundo do coração:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19787, 755, 1, '“Deus de nossos pais, e Senhor de misericórdia, que todas as coisas criastes pela vossa palavra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19788, 755, 2, 'e que, por vossa sabedoria, formastes o homem para ser o senhor de todas as vossas criaturas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19789, 755, 3, 'governar o mundo na santidade e na justiça, e proferir seu julgamento na retidão de sua alma,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19790, 755, 4, 'dai-me a sabedoria que partilha do vosso trono, e não me rejeiteis como indigno de ser um de vossos filhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19791, 755, 5, 'Sou, com efeito, vosso servo e filho de vossa serva, um homem fraco, cuja existência é breve, incapaz de compreender vosso julgamento e vossas leis;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19792, 755, 6, 'porque qualquer homem, mesmo perfeito, entre os homens, não será nada, se lhe falta a sabedoria que vem de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19793, 755, 7, 'Ora, vós me escolhestes para ser rei de vosso povo e juiz de vossos filhos e vossas filhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19794, 755, 8, 'Vós me ordenastes construir um templo na vossa montanha santa e um altar na cidade em que habitais: imagem da sagrada habitação que preparastes desde o princípio.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19795, 755, 9, 'Mas ao vosso lado está a sabedoria que conhece vossas obras; ela estava presente quando fizestes o mundo, ela sabe o que vos é agradável, e o que se conforma às vossas ordens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19796, 755, 10, 'Fazei-a, pois, descer de vosso santo céu, e enviai-a do trono de vossa glória, para que, junto de mim, tome parte em meus trabalhos, e para que eu saiba o que vos agrada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19797, 755, 11, 'Com efeito, ela sabe e conhece todas as coisas; prudentemente guiará meus passos e me protegerá no brilho de sua glória.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19798, 755, 12, 'Assim, minhas obras vos serão agradáveis; governarei vosso povo com justiça, e serei digno do trono de meu pai.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19799, 755, 13, 'Que homem, pois, pode conhecer os desígnios de Deus, e penetrar nas determinações do Senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19800, 755, 14, 'Tímidos são os pensamentos dos mortais, e incertas as nossas concepções;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19801, 755, 15, 'porque o corpo corruptível torna pesada a alma, e a morada terrestre oprime o espírito carregado de cuidados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19802, 755, 16, 'Mal podemos compreender o que está sobre a terra, dificilmente encontramos o que temos ao alcance da mão. Quem, portanto, pode descobrir o que se passa no céu?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19803, 755, 17, 'E quem conhece vossas intenções, se vós não lhe dais a sabedoria, e se do mais alto dos céus vós não lhe enviais vosso Espírito Santo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19804, 755, 18, 'Assim se tornaram direitas as veredas dos que estão na terra; os homens aprenderam as coisas que vos agradam e pela sabedoria foram salvos.”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19805, 756, 1, 'O primeiro homem, o pai do mundo, que foi criado sozinho, foi a sabedoria que cuidou dele, tirou-o de seu próprio pecado,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19806, 756, 2, 'e deu-lhe o poder de reinar sobre todas as coisas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19807, 756, 3, 'E porque o perverso, na sua ira, dela se afastou, pereceu depois de seu furor fratricida.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19808, 756, 4, 'E estando a terra submersa por causa dele pelo dilúvio, a sabedoria de novo o salvou, conduzindo o justo num lenho sem valor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19809, 756, 5, 'E quando as nações unânimes caíram no mal, foi ela que distinguiu o justo, o manteve irrepreensível diante de Deus, e lhe deu a força para vencer sua ternura pelo seu filho.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19810, 756, 6, 'Foi ela que, quando do aniquilamento dos ímpios, salvou o justo, subtraindo-o ao fogo que descera sobre a Pentápole,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19811, 756, 7, 'cuja perversidade ainda no presente é testemunhada por uma terra fumegante e deserta, onde as árvores carregam frutos incapazes de amadurecer, e onde está erigida uma coluna de sal, memorial de uma alma incrédula.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19812, 756, 8, 'Porque aqueles que desprezaram a sabedoria, não somente se prejudicaram em ignorar o bem, mas ainda deixaram aos homens um testemunho de sua loucura, para que seus pecados não fossem esquecidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19813, 756, 9, 'Quanto aos que a honram, a sabedoria os liberta de sofrimentos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19814, 756, 10, 'foi ela que guiou por caminhos retos o justo que fugia à ira de seu irmão; mostrou-lhe o Reino de Deus, e deu-lhe o conhecimento das coisas santas; ajudou-o nos seus trabalhos, e fez frutificar seus esforços;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19815, 756, 11, 'cuidou dele contra ávidos opressores e o fez conquistar riquezas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19816, 756, 12, 'ela o protegeu contra seus inimigos e o defendeu dos que lhe armavam ciladas; e, no duro combate, deu-lhe vitória, a fim de que ele soubesse quanto a piedade é mais forte que tudo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19817, 756, 13, 'Ela não abandonou o justo vendido, mas preservou-o do pecado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19818, 756, 14, 'Desceu com ele à prisão, e não o abandonou nas suas cadeias, até que lhe trouxe o cetro do reino e o poder sobre os que o tinham oprimido; revelou-lhe a mentira de seus acusadores, e conferiu-lhe uma glória eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19819, 756, 15, 'Foi ela que livrou das nações que tiranizavam o povo santo e a raça irrepreensível;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19820, 756, 16, 'entrou na alma do servo de Deus, e se opôs, com sinais e prodígios, a reis temíveis.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19821, 756, 17, 'Deu aos santos o galardão de seus trabalhos, conduziu-os por um caminho miraculoso; durante o dia serviu-lhes de proteção, e deu-lhes a luz dos astros durante a noite.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19822, 756, 18, 'Fê-los atravessar o mar Vermelho, e deu-lhes passagem através da massa das águas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19823, 756, 19, 'ao passo que engoliu seus inimigos, e depois os tirou das profundezas do abismo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19824, 756, 20, 'Também os justos, depois de despojados os ímpios, celebraram, Senhor, vosso santo nome, e louvaram, unidos num só coração, vossa mão protetora,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19825, 756, 21, 'porque a sabedoria abriu a boca aos mudos, e tornou eloquente a língua das crianças.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19826, 757, 1, 'Pela mão de um santo profeta aplanou suas dificuldades;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19827, 757, 2, 'eles atravessaram um deserto inabitado, e levantaram suas tendas em lugares ermos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19828, 757, 3, 'resistiram aos que os atacavam, e repeliram seus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19829, 757, 4, 'Tiveram sede e clamaram a vós: do rochedo abrupto a água lhes foi dada, e da pedra seca estancaram sua sede.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19830, 757, 5, 'Porque os elementos que tinham servido para punir seus inimigos, foram-lhes dados, na sua necessidade, como benefício:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19831, 757, 6, 'em lugar das ondas de um rio perene turvadas por uma lama de sangue,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19832, 757, 7, 'pela punição do decreto que consagrava crianças à morte, vós lhes destes, de maneira inesperada, água em abundância,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19833, 757, 8, 'mostrando-lhes, pela sede que então sofreram, como punistes seus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19834, 757, 9, 'Por isso, tratados com piedade na sua provação, reconheceram quanto deviam ter sofrido os ímpios, julgados com ira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19835, 757, 10, 'A estes provastes como um pai que corrige, mas a outros provastes como um rei severo que condena.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19836, 757, 11, 'Tanto estando longe como perto, a dor os consumiu da mesma forma,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19837, 757, 12, 'porque tiveram um segundo para se entristecer e gemer à lembrança dos males passados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19838, 757, 13, 'Compreendendo, com efeito, que o que era para eles castigo, era para outros ocasião de benefício, sentiram a mão do Senhor;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19839, 757, 14, 'e aquele que, outrora exposto e abandonado, tinham repelido com zombaria, admiraram-no finalmente, porque sofreram uma sede diferente da sede do justo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19840, 757, 15, 'Por outro lado, para os punir dos loucos pensamentos de sua perversidade, que os faziam extraviar-se na adoração de répteis irracionais e de vis animais, enviastes contra eles uma multidão de animais estúpidos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19841, 757, 16, 'a fim de que compreendessem que, por onde cada um peca, será punido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19842, 757, 17, 'Não era difícil à vossa mão todo-poderosa, que formou o mundo de matéria informe, mandar contra eles bandos de ursos e de leões ferozes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19843, 757, 18, 'ou animais desconhecidos e de uma nova espécie, cheios de furor, exalando um hálito inflamado, ou espalhando um fumo infeto, ou lançando de seus olhos faíscas terríveis,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19844, 757, 19, 'capazes não só de os exterminar com seus golpes, mas ainda de os matar de terror só pelo seu aspecto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19845, 757, 20, 'E, mesmo sem isso, eles poderiam perecer por um sopro, perseguidos pela justiça e arrebatados pelo vento de vosso poder; mas, dispusestes tudo com medida, quantidade e peso,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19846, 757, 21, 'porque sempre vos é possível mostrar vosso poder imenso, e quem poderá resistir à força de vosso braço?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19847, 757, 22, 'Diante de vós o mundo inteiro é como um nada, que faz pender a balança, ou como uma gota de orvalho, que desce de madrugada sobre a terra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19848, 757, 23, 'Tendes compaixão de todos, porque vós podeis tudo; e para que se arrependam, fechais os olhos aos pecados dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19849, 757, 24, 'Porque amais tudo que existe, e não odiais nada do que fizestes, porquanto, se o odiásseis, não o teríeis feito de modo algum.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19850, 757, 25, 'Como poderia subsistir qualquer coisa, se não o tivésseis querido, e conservar a existência, se por vós não tivesse sido chamada?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19851, 757, 26, 'Mas poupais todos os seres, porque todos são vossos, ó Senhor, que amais a vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19852, 758, 1, 'Vosso espírito incorruptível está em todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19853, 758, 2, 'É por isso que castigais com brandura aqueles que caem, e os advertis mostrando-lhes em que pecam, a fim de que rejeitem sua malícia e creiam em vós, Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19854, 758, 3, 'Foi assim que se deu com os antigos habitantes da Terra Santa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19855, 758, 4, 'Tínheis horror deles por causa de suas obras detestáveis, sua magia e seus ritos ímpios,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19856, 758, 5, 'seus cruéis morticínios de crianças, seus festins de entranhas, carne humana e sangue, suas iniciações nos mistérios orgíacos,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19857, 758, 6, 'e os crimes de pais contra seres indefesos; e resolvestes aniquilá-los pela mão de nossos pais,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19858, 758, 7, 'para que esta terra, que estimais entre todas, recebesse uma digna colônia de filhos de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19859, 758, 8, 'Contudo, porque também eles eram homens, vós os poupastes, enviando-lhes vespas precursoras de vosso exército, para que elas os fizessem perecer pouco a pouco.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19860, 758, 9, 'Não é que vos fosse impossível esmagar os maus por meio dos justos num combate, ou exterminar todos juntos por animais ferozes ou por uma palavra categórica;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19861, 758, 10, 'mas castigando-os pouco a pouco, dáveis tempo para o arrependimento, não ignorando que sua raça era maldita, ingênita a sua perversidade, e que jamais seus pensamentos se mudariam,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19862, 758, 11, 'porque sua estirpe era má desde a origem... Não era por temor do que quer que fosse que vos mostráveis indulgente para com eles em seus pecados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19863, 758, 12, 'Porque, quem ousará dizer-vos: “Que fizeste tu?”. E quem se oporá a vosso julgamento? Quem vos repreenderá de terdes aniquilado nações que criastes? Ou quem se levantará contra vós para defender os culpados?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19864, 758, 13, 'Não há, fora de vós, um Deus que se ocupa de tudo, e a quem deveis mostrar que nada é injusto em vossos julgamentos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19865, 758, 14, 'nem um rei, nem um tirano que vos possa resistir em favor dos que castigastes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19866, 758, 15, 'Mas porque sois justo, governais com toda a justiça, e julgais indigno de vosso poder condenar quem não merece ser punido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19867, 758, 16, 'Porque vossa força é o fundamento de vossa justiça e o fato de serdes Senhor de todos, vos torna indulgente para com todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19868, 758, 17, 'Mostrais vossa força aos que não creem no vosso poder, e confundis os que não a conhecem e ousam afrontá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19869, 758, 18, 'Senhor de vossa força, julgais com bondade, e nos governais com grande indulgência, porque sempre vos é possível empregar vosso poder, quando quiserdes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19870, 758, 19, 'Agindo desta maneira, mostrastes a vosso povo que o justo deve ser cheio de bondade, e inspirastes a vossos filhos a boa esperança de que, após o pecado, lhes dareis tempo para a penitência;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19871, 758, 20, 'porque se os inimigos de vossos filhos, dignos de morte, vós os haveis castigado com tanta prudência e longanimidade, dando-lhes tempo e ocasião para se emendarem,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19872, 758, 21, 'com quanto cuidado não julgareis vós os vossos filhos, a cujos antepassados concedestes com juramento vossa aliança, repleta de ricas promessas!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19873, 758, 22, 'Portanto, quando nos corrigis, castigais mil vezes mais nossos inimigos, para que em nossos julgamentos nos lembremos de vossa bondade, e para que esperemos em vossa indulgência quando formos julgados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19874, 758, 23, 'Por isso, também aqueles que loucamente viveram no mal, vós os torturastes por meio das suas próprias abominações:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19875, 758, 24, 'porque se tinham afastado demais nos caminhos do erro, tomando por deuses os mais vis animais, deixando-se enganar como meninos sem razão;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19876, 758, 25, 'assim é que, como a meninos sem razão, lhes destes um castigo irrisório.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19877, 758, 26, 'Mas os que recusam a advertência de semelhante correção sofrerão um castigo digno de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19878, 758, 27, 'Excitados, então, pelos sofrimentos causados por esses animais que tinham julgado deuses, e que os atormentavam, viram o que no começo tinham recusado ver, e reconheceram o verdadeiro Deus. Por isso é que caiu sobre eles a condenação final.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19879, 759, 1, 'São insensatos por natureza todos os que desconheceram a Deus e, através dos bens visíveis, não souberam conhecer aquele que é, nem reconhecer o artista, considerando suas obras.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19880, 759, 2, 'Tomaram o fogo, ou o vento, ou o ar agitável, ou a esfera estrelada, ou a água impetuosa, ou os astros dos céus, por deuses, regentes do mundo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19881, 759, 3, 'Se tomaram essas coisas por deuses, encantados pela sua beleza, saibam, então, quanto seu Senhor prevalece sobre elas, porque é o criador da beleza que fez essas coisas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19882, 759, 4, 'Se o que os impressionou é a sua força e o seu poder, que eles compreendam, por meio delas, que seu criador é mais forte;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19883, 759, 5, 'pois é a partir da grandeza e da beleza das criaturas que, por analogia, se conhece o seu autor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19884, 759, 6, 'Contudo, estes só incorrem numa ligeira censura, porque, talvez, eles caíram no erro procurando Deus e querendo encontrá-lo:*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19885, 759, 7, 'vivendo entre suas obras, eles as observam com cuidado, e porque eles as consideram belas, deixam-se seduzir pelo seu aspecto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19886, 759, 8, 'Ainda uma vez, entretanto, eles não são desculpáveis,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19887, 759, 9, 'porque, se eles possuíram luz suficiente para poder perscrutar a ordem do mundo, como não encontraram eles mais facilmente aquele que é seu Senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19888, 759, 10, 'Mas são desgraçados e esperam em mortos, aqueles que chamaram de deuses a obras de mãos humanas: o ouro, a prata, artisticamente trabalhados, figuras de animais, alguma pedra inútil a que, outrora, certa mão deu forma.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19889, 759, 11, 'Assim, um lenhador cortou e serrou uma árvore fácil de manejar. Habilmente ele lhe tirou toda a casca, e, com a habilidade do seu ofício, fez dela um móvel útil para seu uso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19890, 759, 12, 'Com as sobras de seu trabalho, cozinhou comida, com que se saciou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19891, 759, 13, 'O que ainda lhe restava não era bom para nada, não passando de madeira torcida e toda cheia de nós; contudo, ele a tomou e consagrou suas horas de lazer a talhá-la; ele a trabalhou com toda a arte que adquirira, e deu-lhe a semelhança de um homem,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19892, 759, 14, 'ou o aspecto de algum vil animal. Pôs-lhe vermelhão, uma demão de uma tinta encarnada, e encobriu-lhe cuidadosamente todo defeito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19893, 759, 15, 'Em seguida, preparou-lhe um nicho digno dele, e o fixou à parede, segurando-o com um prego:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19894, 759, 16, 'foi por medo que caísse, que tomou este cuidado, porque sabe muito bem que ele não pode ajudar-se a si mesmo, pois não passa de uma estátua que tem necessidade de um apoio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19895, 759, 17, 'Mas quando lhe implora por seus bens, seus casamentos, seus filhos, não se envergonha de falar ao que é inanimado, e pede saúde ao que é desprezível.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19896, 759, 18, 'Reclama a vida ao que é morto, e procura socorro no que é débil; e, para uma viagem, invoca o que não pode andar;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19897, 759, 19, 'para um lucro, um trabalho, o bom êxito de uma obra de suas mãos, pede a força ao que nem é capaz de mover as mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19898, 760, 1, 'Outro, por sua vez, que quer navegar e se prepara para atravessar as impetuosas ondas, invoca um madeiro de pior qualidade que o navio que o leva;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19899, 760, 2, 'porque o desejo do lucro inventou o navio, e uma hábil sabedoria dirigiu sua construção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19900, 760, 3, 'Mas sois vós, Pai, que o governais pela vossa Providência, porque, se abristes caminho, mesmo no mar, e uma rota segura no meio das ondas –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19901, 760, 4, 'mostrando por aí que vós podeis tirar do perigo aquele que as afronta mesmo sem meios –,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19902, 760, 5, 'quereis, entretanto, que não sejam inúteis as obras de vossa sabedoria. Por isso, os homens confiam a própria vida a um pouco de madeira e atravessam em segurança as ondas num navio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19903, 760, 6, 'Assim, com efeito, quando na origem dos tempos fizestes perecer gigantes orgulhosos, a esperança do universo, refugiando-se num barco, que vossa mão governava, conservou para o mundo o germe de uma geração.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19904, 760, 7, 'Porque é bendito o madeiro pelo qual se opera a justiça,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19905, 760, 8, 'mas maldito é o ídolo, ele e o que o fez; este porque o formou, aquele porque, sendo corruptível, leva o nome de deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19906, 760, 9, 'Com efeito, Deus odeia tanto o ímpio quanto sua impiedade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19907, 760, 10, 'e a obra sofrerá o mesmo castigo que o autor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19908, 760, 11, 'Este é o motivo por que também os ídolos das nações serão julgados: na criação de Deus, eles se tornaram uma abominação, objetos de escândalo para os homens, e laços para os pés dos insensatos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19909, 760, 12, 'Foi pela idealização dos ídolos que começou a apostasia, e sua invenção foi a perda dos humanos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19910, 760, 13, 'Eles não existiam no princípio e não durarão para sempre;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19911, 760, 14, 'a vaidade dos homens os introduziu no mundo. E, por causa disso, Deus decidiu a sua destruição para breve.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19912, 760, 15, 'Um pai aflito por um luto prematuro, tendo mandado fazer a imagem do filho, tão cedo arrebatado, honrou, em seguida, como a um deus aquele que não passava de um morto, e transmitiu aos seus certos ritos secretos e cerimônias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19913, 760, 16, 'Este costume ímpio, tendo-se firmado com o tempo, foi depois observado como lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19914, 760, 17, 'Foi também em consequência das ordens dos príncipes que se adoraram imagens esculpidas, porque aqueles que não podiam honrar pessoalmente, porque moravam longe deles, fizeram representar o que se achava distante, e expuseram publicamente a imagem do rei venerado, a fim de lisonjeá-lo de longe com seu zelo, como se estivesse presente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19915, 760, 18, 'Isto contribuiu ainda para o estabelecimento deste culto, mesmo entre os que não conheciam o rei; foi a ambição do artista');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19916, 760, 19, 'que, talvez, querendo agradar ao soberano, deu-lhe, por sua arte, a semelhança do belo;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19917, 760, 20, 'e a multidão, seduzida pelo encanto da obra, em breve tomou por deus aquele que tinham honrado como homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19918, 760, 21, 'E isto foi uma cilada para a humanidade: os homens, sujeitando-se à lei da desgraça e da tirania, deram à pedra e à madeira o nome incomunicável.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19919, 760, 22, 'Como se não bastasse terem errado acerca do conhecimento de Deus, embora passando a vida numa longa luta de ignorância, eles dão o nome de paz a um estado tão infeliz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19920, 760, 23, 'Com efeito, sacrificando seus filhos, celebrando mistérios ocultos, ou entregando-se a orgias desenfreadas de religiões exóticas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19921, 760, 24, 'eles já não guardam a honestidade nem na vida nem no casamento, mas um faz desaparecer o outro pelo ardil, ou o ultraja pelo adultério.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19922, 760, 25, 'Tudo está numa confusão completa – sangue, homicídio, furto, fraude, corrupção, deslealdade, revolta, perjúrio,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19923, 760, 26, 'perseguição dos bons, esquecimento dos benefícios, contaminação das almas, perversão dos sexos, instabilidade das uniões, adultérios e impudicícias –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19924, 760, 27, 'porque o culto de inomináveis ídolos é o começo, a causa e o fim de todo o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19925, 760, 28, '(Seus adeptos) incitam o prazer até a loucura, ou fazem vaticínios falsos, ou vivem na injustiça, ou, sem escrúpulo, juram falso,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19926, 760, 29, 'porque, confiando em ídolos inanimados, esperam não ser punidos por má-fé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19927, 760, 30, 'Contudo, o castigo os atingirá por duplo motivo: porque eles desconheceram a Deus, afeiçoando-se aos ídolos, e porque são culpados, por desprezo à santidade da religião, de ter feito juramentos enganadores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19928, 760, 31, 'Pois não é o poder dos ídolos invocados, mas o castigo reservado ao pecador, que sempre persegue as faltas dos maus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19929, 761, 1, 'Mas vós, Deus nosso, sois benfazejo e verdadeiro, vós sois paciente e tudo governais com misericórdia;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19930, 761, 2, 'com efeito, mesmo se pecamos, somos vossos, porque conhecemos vosso poder; mas não pecaremos, cientes de que somos considerados como vossos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19931, 761, 3, 'Porque conhecer-vos é a perfeita justiça, e conhecer vosso poder é a raiz da imortalidade.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19932, 761, 4, 'Não fomos seduzidos pelas invenções da arte corruptora dos homens nem pelo vão trabalho dos pintores: borrada figura de cores misturadas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19933, 761, 5, 'cuja vista excita os desejos dos insensatos, fantasma inanimado de uma imagem sem vida que provoca a paixão!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19934, 761, 6, 'Cativados pelo mal, não merecem esperar senão o mal, os que o fazem, os que o amam e os que o veneram.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19935, 761, 7, 'Eis, portanto, um oleiro que amassa laboriosamente a terra mole, e forma diversos objetos para nosso uso, mas da mesma argila faz vasos destinados a fins nobres e outros, indiferentemente, para usos opostos. Para qual destes usos cada vaso será aplicado? O oleiro será o juiz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19936, 761, 8, 'Do mesmo barro, forma também, como obreiro perverso, uma vã divindade, ele que, ainda há pouco, nasceu da terra, e em breve voltará a ela, de onde foi tirado, quando lhe serão pedidas as contas de sua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19937, 761, 9, 'Ele mesmo não tem preocupação alguma com o próprio desfalecimento, nem com a brevidade da vida; ele rivaliza, pelo contrário, com aqueles que trabalham o ouro e a prata, imita os que trabalham o cobre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19938, 761, 10, 'Pó é o seu coração, mais vil que a terra sua esperança, e põe sua glória em fabricar objetos enganadores. E mais desprezível que o barro é sua vida,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19939, 761, 11, 'porque não reconheceu aquele que o formou, aquele que lhe inspirou uma alma ativa e lhe insuflou o espírito vital.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19940, 761, 12, 'Para ele a vida é um divertimento, e nossa existência um mercado lucrativo, “porque – diz ele –, é preciso aproveitar-se de tudo, mesmo do mal.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19941, 761, 13, 'Mais que qualquer outro, esse homem sabe que peca, fazendo do mesmo barro vasos frágeis e ídolos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19942, 761, 14, 'Ora, verdadeiramente, muitos insensatos, mais infortunados que a alma da criança, são os inimigos de vosso povo, que o oprimiram,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19943, 761, 15, 'porque eles também tiveram por deuses todos os ídolos das nações, que não podem servir-se de seus olhos para ver, que não têm nariz para aspirar o ar, nem ouvidos para ouvir, nem os dedos das mãos para apalpar, e cujos pés são incapazes de andar;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19944, 761, 16, 'foi, com efeito, um homem que os fez, formou-os alguém que recebeu a alma de empréstimo. Nenhum homem pode fazer um deus, mesmo semelhante a si próprio,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19945, 761, 17, 'porque, sendo ele próprio mortal, morto é tudo que produz com suas mãos ímpias. De fato, ele vale mais que os objetos que venera; ele, pelo menos, tem vida, enquanto os ídolos não a têm.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19946, 761, 18, 'Chega-se até a adorar os mais odiosos animais, que são piores ainda que os outros animais irracionais,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19947, 761, 19, 'que nem mesmo possuem o que outros seres vivos possuem: bastante beleza para serem amados, e que foram excluídos da aprovação e da bênção de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19948, 762, 1, 'Por isso, foram justamente castigados por animais dessa espécie e atormentados por uma multidão de animais;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19949, 762, 2, 'em vez de feri-lo assim, vós favorecíeis vosso povo, satisfazendo por um alimento surpreendente o ardor de seu apetite, e oferecendo-lhe por alimento codornizes.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19950, 762, 3, 'De tal modo que aqueles, mau grado sua fome, diante do aspecto hediondo de animais enviados contra eles, experimentaram a náusea; estes, após uma curta privação, receberam um alimento maravilhoso.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19951, 762, 4, 'Pois era preciso que os primeiros, os opressores, fossem oprimidos por uma inexorável fome, e que aos outros fossem apenas mostrados os tormentos suportados por seus inimigos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19952, 762, 5, 'Efetivamente, quando o cruel furor dos animais os atingiu também, e quando pereceram com a mordedura de sinuosas serpentes, vossa cólera não durou até o fim.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19953, 762, 6, 'Foram por pouco tempo atormentados, para sua correção: eles possuíram um sinal de salvação que lhes lembrava o preceito de vossa Lei.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19954, 762, 7, 'E quem se voltava para ele era salvo, não em vista do objeto que olhava, mas por vós, Senhor, que sois o salvador de todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19955, 762, 8, 'Com isso, mostráveis a vossos inimigos, que sois vós que livrais de todo o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19956, 762, 9, 'Quanto a eles as mordeduras dos gafanhotos e das moscas os matavam e não se encontrou remédio para salvar sua vida, porque mereciam ser castigados por tais instrumentos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19957, 762, 10, 'mas a vossos filhos, mesmo os dentes de serpentes venenosas não os puderam vencer porque, sobrevindo a vossa misericórdia, curou-os.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19958, 762, 11, 'Eram picados, para que se lembrassem de vossas palavras, e, em seguida, ficavam curados, para que não viessem a esquecê-las completamente e a subtraírem-se de vossos benefícios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19959, 762, 12, 'Não foi uma erva nem algum unguento que os curou, mas a vossa palavra que cura todas as coisas, Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19960, 762, 13, 'Porque vós sois senhor da vida e da morte. Vós conduzis às portas do Hades e de lá tirais;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19961, 762, 14, 'enquanto o homem, se pode matar por sua maldade, não pode fazer voltar o espírito uma vez saído, nem chamar de volta a alma que o Hades já recebeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19962, 762, 15, 'Escapar à vossa mão é impossível,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19963, 762, 16, 'e os ímpios, que recusaram conhecer-vos, foram fustigados pela força de vosso braço, perseguidos por chuvas extraordinárias, saraivas e implacáveis tempestades, e consumidos pelo fogo dos raios.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19964, 762, 17, 'O que havia de mais admirável ainda é que, na água que tudo extingue, o fogo tomava mais violência, porque o universo toma a defesa dos justos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19965, 762, 18, 'Ora, a chama temperava seu ardor para não queimar os animais enviados contra os ímpios, para que estes se apercebessem e reconhecessem que eram perseguidos pelo julgamento de Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19966, 762, 19, 'Ora, excedendo sua força habitual, o fogo ardia mesmo no meio da água para destruir os frutos de uma terra iníqua...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19967, 762, 20, 'Mas, pelo contrário, foi com o alimento dos anjos que alimentastes vosso povo, e foi do céu que, sem fadiga, vós lhe enviastes um pão já preparado, contendo em si todas as delícias e adaptando-se a todos os gostos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19968, 762, 21, 'Esta substância que dáveis se parecia com a doçura que mostráveis a vossos filhos. Ela se adaptava ao desejo de quem a comia, e transformava-se naquilo que cada qual desejava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19969, 762, 22, 'Embora fosse como neve e gelo, ela suportava o fogo sem se fundir, para mostrar que era para os inimigos que o fogo destruía as colheitas, quando ardia, apesar da saraiva, e brilhava debaixo de chuvas,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19970, 762, 23, 'enquanto que, quando se tratava de alimentar os justos, até perdia sua natural violência.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19971, 762, 24, 'A criatura que vos é submissa, a vós, seu Criador, aumenta sua força para castigar os maus, e os modera para o bem dos que puseram em vós sua fé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19972, 762, 25, 'Do mesmo modo, transformada em tudo que se queria, servia à vossa generosidade que a todos alimenta, segundo a vontade dos que dela tinham necessidade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19973, 762, 26, 'para que os filhos que vós amais, Senhor, aprendessem que não são os frutos da terra que alimentam o homem, mas é vossa palavra que conserva em vida aqueles que creem em vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19974, 762, 27, 'O que não era destruído pelo fogo, fundia-se ao simples calor de um raio de sol,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19975, 762, 28, 'para que se soubesse que é preciso antecipar-se ao sol para vos agradecer, e que é preciso adorar-vos antes de raiar o dia;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19976, 762, 29, 'porque a esperança do ingrato é como a geleira do inverno, que se derramará como água inútil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19977, 763, 1, 'Em verdade, grandes e impenetráveis são vossos juízos, Senhor; por isso, as almas grosseiras caíram no erro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19978, 763, 2, 'Por terem acreditado que podiam oprimir a santa nação, os ímpios, prisioneiros das trevas e encarcerados por uma longa noite, jaziam encerrados nas suas casas, tentando escapar à vossa incessante vigilância.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19979, 763, 3, 'Depois de terem imaginado que, com seus secretos pecados, ficariam escondidos sob o sombrio véu do esquecimento, eles se viram dispersados, como presa de um terrível espanto, e amedrontados por alucinações.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19980, 763, 4, 'Mesmo o canto mais afastado em que se abrigavam não os punha ao abrigo do terror: ruídos aterradores ressoavam em torno deles, e taciturnos espectros de lúgubre aspecto lhes apareciam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19981, 763, 5, 'Nenhuma chama, por intensa que fosse, chegava a iluminar. E a luz brilhante dos astros era impotente para alumiar esta noite sombria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19982, 763, 6, 'Mas aparecia-lhes de súbito nada mais que uma chama aterradora, e, tomados de terror por esta visão fugitiva, julgavam essas aparições mais terríveis ainda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19983, 763, 7, 'A arte dos mágicos se mostrou ilusória, e esta sabedoria, a que eles pretendiam, evidenciou-se vergonhosamente como falsidade.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19984, 763, 8, 'Aqueles que se jactavam de banir das almas doentes o terror e a perturbação, eram eles mesmos atormentados por um ridículo temor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19985, 763, 9, 'Mesmo quando nada de mais grave os aterrorizava, a passagem dos animais e o silvo das serpentes punham-nos fora de si, e eles morriam de medo. Recusavam até mesmo contemplar essa atmosfera à qual nada podia escapar;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19986, 763, 10, 'porque a maldade, condenada por seu próprio testemunho, é medrosa, e, sob o peso da consciência, supõe sempre o pior,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19987, 763, 11, 'pois o temor não é outra coisa que a privação dos socorros trazidos pela reflexão,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19988, 763, 12, 'porque, quanto menor for em sua alma a esperança de auxílio, tanto mais penosa é a ignorância daquilo de que se tem medo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19989, 763, 13, 'Eles, durante essa noite de impotência, saída dos recantos do Hades impotente, dormiam num mesmo sono,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19990, 763, 14, 'agitados, de um lado, pelo terror dos espectros, e paralisados, de outro, pelo desfalecimento da alma; pois era um pavor repentino e inesperado o que se abatera sobre eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19991, 763, 15, 'E todo aquele que caía sem força ficava como que preso e encerrado num cárcere sem ferros.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19992, 763, 16, 'Fosse ele camponês ou pastor, ou o operário que se afadiga sozinho no seu trabalho, uma vez surpreendido, tinha de suportar a inevitável necessidade, porque todos estavam ligados por uma mesma cadeia de trevas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19993, 763, 17, 'O silvo do vento, o canto harmonioso dos passarinhos nos ramos espessos, o murmúrio da água correndo precipitadamente, o estrondo das rochas que se despenhavam,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19994, 763, 18, 'a carreira invisível dos animais que saltavam, os urros dos animais selvagens, o eco que repercutia nas cavidades dos montes: tudo os paralisava de terror.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19995, 763, 19, 'Enquanto o mundo inteiro era alumiado de uma brilhante luz, e sem obstáculo se entregava às suas ocupações,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19996, 763, 20, 'somente sobre eles se estendia uma pesada noite, imagem das trevas que mais tarde os deviam acolher; e eram para si mesmos um peso mais insuportável que esta escuridão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19997, 764, 1, 'Contudo, para vossos santos havia uma luz brilhantíssima. Sem verem seus semblantes, os outros ouviam-lhes a voz, e julgavam-nos felizes por não sofrerem os mesmos tormentos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19998, 764, 2, 'Davam-lhes graças, porque não se vingavam dos maus-tratos suportados, e pediam-lhes perdão de sua inimizade.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (19999, 764, 3, 'Pelo contrário, vós destes uma coluna luminosa para guiá-los na sua marcha para o desconhecido, como um sol que sem incomodá-los alumiava seu glorioso êxodo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (20000, 764, 4, 'Mas eles bem mereciam ser privados da luz e aprisionados nas trevas, eles, que tinham encerrado em prisões os vossos filhos, através dos quais a incorruptível luz da Lei se devia comunicar ao mundo.*');
