INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28001, 1089, 15, 'Tomando então a palavra, Pedro disse: “Explica-nos esta parábola”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28002, 1089, 16, 'Jesus respondeu: “Sois também vós de tão pouca compreensão?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28003, 1089, 17, 'Não compreendeis que tudo o que entra pela boca vai ao ventre e depois é lançado num lugar secreto?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28004, 1089, 18, '“Ao contrário, aquilo que sai da boca provém do coração, e é isso o que mancha o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28005, 1089, 19, 'Porque é do coração que provêm os maus pensamentos, os homicídios, os adulté­rios, as impurezas, os furtos, os falsos testemunhos, as calúnias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28006, 1089, 20, 'Eis o que mancha o homem. Comer, porém, sem ter lavado as mãos, isso não mancha o homem”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28007, 1089, 21, 'Jesus partiu dali e retirou-se para os arredores de Tiro e Sidônia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28008, 1089, 22, 'E eis que uma cananeia, origi­nária daquela terra, gritava: “Se­nhor, filho de Davi, tem piedade de mim! Minha filha está cruelmente atormentada por um demônio”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28009, 1089, 23, 'Jesus não lhe respondeu palavra alguma. Seus discípulos vieram a ele e lhe disseram com insistência: “Despede-a, ela nos persegue com seus gritos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28010, 1089, 24, 'Jesus respondeu-lhes: “Não fui enviado senão às ovelhas perdidas da casa de Israel”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28011, 1089, 25, 'Mas aquela mulher veio prostrar-se diante dele, dizendo: “Senhor, ajuda-me!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28012, 1089, 26, 'Jesus res­pondeu-lhe: “Não convém jogar aos cachorrinhos o pão dos filhos”.* –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28013, 1089, 27, '“Certamente, Senhor, replicou-lhe ela; mas os cachorrinhos ao menos comem as migalhas que caem da mesa de seus donos...”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28014, 1089, 28, 'Disse-lhe, então, Jesus: “Ó mulher, grande é tua fé! Seja-te feito como desejas”. E na mesma hora sua filha ficou curada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28015, 1089, 29, 'Jesus saiu daquela região e voltou para perto do mar da Galileia. Subiu a uma colina e sentou-se ali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28016, 1089, 30, 'Então numerosa multidão aproximou-se dele, trazendo consigo mudos, cegos, coxos, aleijados e muitos outros enfermos. Puseram-nos aos seus pés e ele os curou,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28017, 1089, 31, 'de sorte que o povo estava admirado ante o espetáculo dos mudos que falavam, daqueles aleijados curados, de coxos que andavam, dos cegos que viam; e glorificavam ao Deus de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28018, 1089, 32, 'Jesus, porém, reuniu os seus discípulos e disse-lhes: “Tenho piedade desta multidão: eis que há três dias está perto de mim e não tem nada para comer. Não quero despedi-la em jejum, para que não desfaleça no caminho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28019, 1089, 33, 'Disseram-lhe os discípulos: “De que maneira procuraremos neste lugar deserto pão bastante para saciar tal multidão?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28020, 1089, 34, 'Pergunta-lhes Jesus: “Quantos pães tendes?”. “Sete, e alguns peixi­nhos” – responderam eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28021, 1089, 35, 'Mandou, então, a multidão assentar-se no chão,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28022, 1089, 36, 'tomou os sete pães e os peixes e abençoou-os. Depois os partiu e os deu aos discípulos, que os distribuí­ram à multidão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28023, 1089, 37, 'Todos comeram e ficaram saciados, e, dos pedaços que restaram, encheram sete cestos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28024, 1089, 38, 'Ora, os que se alimentaram foram quatro mil homens, sem contar as mulheres e as crianças.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28025, 1089, 39, 'Jesus então despediu o povo, subiu para a barca e retornou à região de Magadã.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28026, 1090, 1, 'Os fariseus e os saduceus achegaram-se a Jesus para submetê-lo à prova e pediram-lhe que lhes mostrasse um milagre do céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28027, 1090, 2, 'Ele lhes respondeu: “Quando vem a tarde, dizeis: Haverá bom tempo, porque o céu está averme­lhado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28028, 1090, 3, 'E de manhã: Hoje haverá tormenta, porque o céu está de um vermelho sombrio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28029, 1090, 4, 'Hipócritas! Sabeis distinguir o aspecto do céu e não podeis discernir os sinais dos tempos? Essa raça perversa e adúltera pede um milagre! Mas não lhe será dado outro sinal senão o de Jonas!”. Depois, deixando-os, partiu.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28030, 1090, 5, 'Ora, passando para a outra margem do lago, os discípulos haviam esquecido de levar pão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28031, 1090, 6, 'Jesus disse-lhes: “Guardai-vos com cuidado do fermento dos fariseus e dos saduceus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28032, 1090, 7, 'Eles pensavam: “É que não trouxemos pão...”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28033, 1090, 8, 'Jesus, penetrando nos seus pensamentos, disse-lhes: “Homens de pouca fé! Por que julgais que vos falei por não terdes pão?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28034, 1090, 9, 'Ainda não compreendeis? Nem vos lembrais dos cinco pães e dos cinco mil homens, e de quantos cestos recolhestes?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28035, 1090, 10, 'Nem dos sete pães para os quatro mil homens e de quantos cestos enchestes?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28036, 1090, 11, 'Por que não compreendeis que não é do pão que eu vos falava, quando vos disse: Guardai-vos do fermento dos fariseus e dos saduceus?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28037, 1090, 12, 'Então, entenderam que não dissera que se guardassem do fermento do pão, mas da doutrina dos fariseus e dos saduceus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28038, 1090, 13, 'Chegando ao território de Cesareia de Filipe, Jesus perguntou a seus discípulos: “No dizer do povo, quem é o Filho do Homem?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28039, 1090, 14, 'Responderam: “Uns dizem que é João Batista; outros, Elias; outros, Jeremias ou um dos profetas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28040, 1090, 15, 'Disse-lhes Jesus: “E vós quem dizeis que eu sou?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28041, 1090, 16, 'Simão Pedro respondeu: “Tu és o Cristo, o Filho de Deus vivo!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28042, 1090, 17, 'Jesus, então, lhe disse: “Feliz és, Simão, filho de Jonas, porque não foi a carne nem o sangue que te revelou isto, mas meu Pai que está nos céus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28043, 1090, 18, 'E eu te declaro: tu és Pedro, e sobre esta pedra edificarei a minha Igreja; as portas do inferno não prevalecerão contra ela.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28044, 1090, 19, 'Eu te darei as chaves do Reino dos Céus: tudo o que ligares na terra será ligado nos céus, e tudo o que desligares na terra será desligado nos céus”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28045, 1090, 20, 'Depois, ordenou aos seus discípulos que não dissessem a ninguém que ele era o Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28046, 1090, 21, 'Desde então, Jesus começou a manifestar a seus discípulos que precisava ir a Jerusalém e sofrer muito da parte dos anciãos, dos príncipes dos sacerdotes e dos escribas; seria morto e ressuscitaria ao terceiro dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28047, 1090, 22, 'Pedro, então, começou a interpelá-lo e protes­tar nestes termos: “Que Deus não permita isso, Senhor! Isso não te acontecerá!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28048, 1090, 23, 'Mas Jesus, voltando-se para ele, disse-lhe: “Afasta-te, Satanás! Tu és para mim um escândalo; teus pensamentos não são de Deus, mas dos homens!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28049, 1090, 24, 'Em seguida, Jesus disse a seus discípulos: “Se alguém quiser vir comigo, renuncie-se a si mesmo, tome sua cruz e siga-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28050, 1090, 25, 'Porque aquele que quiser salvar a sua vida, irá perdê-la; mas aquele que tiver sacrificado a sua vida por minha causa, irá recobrá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28051, 1090, 26, 'Que servirá a um homem ganhar o mundo inteiro, se vem a prejudicar a sua vida? Ou que dará um homem em troca de sua vida?...*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28052, 1090, 27, 'Porque o Filho do Homem há de vir na glória de seu Pai com seus anjos, e então recompensará a cada um segundo suas obras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28053, 1090, 28, 'Em verdade vos declaro: muitos destes que aqui estão não verão a morte, sem que tenham visto o Filho do Homem voltar na majestade de seu Reino”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28054, 1091, 1, 'Seis dias depois, Jesus tomou consigo Pedro, Tiago e João, seu irmão, e conduziu-os à parte a uma alta montanha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28055, 1091, 2, 'Lá se transfigurou na presença deles: seu rosto brilhou como o sol, suas vestes tornaram-se resplandecentes de brancura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28056, 1091, 3, 'E eis que apareceram Moisés e Elias conversando com ele.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28057, 1091, 4, 'Pedro tomou, então, a palavra e disse-lhe: “Senhor, é bom estarmos aqui. Se queres, farei aqui três tendas: uma para ti, uma para Moisés e outra para Elias”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28058, 1091, 5, 'Falava ele ainda, quando veio uma nuvem luminosa e os envolveu. E daquela nuvem fez-se ouvir uma voz que dizia: “Eis o meu Filho muito amado, em quem pus toda a minha afeição; ouvi-o”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28059, 1091, 6, 'Ouvindo esta voz, os discípulos caíram com a face por terra e tiveram medo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28060, 1091, 7, 'Mas Jesus aproximou-se deles e tocou-os, dizendo: “Levantai-vos e não temais”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28061, 1091, 8, 'Eles levantaram os olhos e não viram mais ninguém, senão unicamente Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28062, 1091, 9, 'E, quando desciam, Jesus lhes fez esta proibição: “Não conteis a ninguém o que vistes, até que o Filho do Homem ressuscite dos mortos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28063, 1091, 10, 'Em seguida, os discípulos o interrogaram: “Por que dizem os escribas que Elias deve voltar primeiro?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28064, 1091, 11, 'Jesus respondeu-lhes: “Elias, de fato, deve voltar e restabelecer todas as coisas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28065, 1091, 12, 'Mas eu vos digo que Elias já veio, mas não o conheceram; antes, fizeram com ele quanto quiseram. Do mesmo modo farão sofrer o Filho do Homem”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28066, 1091, 13, 'Os discípulos compreenderam, então, que ele lhes falava de João Batista.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28067, 1091, 14, 'E, quando eles se reuniram ao povo, um homem aproximou-se deles e prostrou-se diante de Jesus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28068, 1091, 15, 'dizendo: “Senhor, tem piedade de meu filho, porque é lunático e sofre muito: ora cai no fogo, ora na água...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28069, 1091, 16, 'Já o apresentei a teus discípulos, mas eles não o puderam curar”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28070, 1091, 17, 'Respondeu Jesus: “Raça incrédula e perversa, até quando estarei convosco? Até quando hei de aturar-vos? Trazei-mo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28071, 1091, 18, 'Jesus ameaçou o demônio e este saiu do menino, que ficou curado na mesma hora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28072, 1091, 19, 'Então, os discípulos lhe perguntaram em particular: “Por que não pudemos nós expulsar esse demônio?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28073, 1091, 20, 'Jesus respondeu-lhes: “Por causa de vossa falta de fé. Em verdade vos digo: se tiverdes fé, como um grão de mostarda, direis a esta montanha: Transporta-te daqui para lá, e ela irá; e nada vos será impossível.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28074, 1091, 21, 'Quanto a esta espécie de demônio, só se pode expulsar à força de oração e de jejum”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28075, 1091, 22, 'Enquanto caminhava pela Galileia, Jesus lhes disse: “O Filho do Homem deve ser entregue nas mãos dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28076, 1091, 23, 'Eles irão matá-lo, mas ao terceiro dia ressuscitará.” E eles ficaram profundamente aflitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28077, 1091, 24, 'Logo que chegaram a Cafarnaum, aqueles que cobravam o imposto da didracma aproximaram-se de Pedro e lhe perguntaram: “Teu mestre não paga a didracma?”.* –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28078, 1091, 25, '“Paga, sim” – respondeu Pedro. Mas quando chegaram à casa, Jesus preveniu-o, dizendo: “Que te parece, Simão? Os reis da terra, de quem recebem os tributos ou os impostos? De seus filhos ou dos estrangeiros?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28079, 1091, 26, 'Pedro respondeu: “Dos estrangeiros”. Jesus replicou: “Os filhos, então, estão isentos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28080, 1091, 27, 'Mas não convém escandalizá-los. Vai ao mar, lança o anzol, e ao primeiro peixe que pegares, abrirás a boca e encontrarás um estater. Toma-o e dá-o por mim e por ti”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28081, 1092, 1, 'Neste momento, os discípulos aproxima­ram-se de Jesus e perguntaram-lhe: “Quem é o maior no Reino dos Céus?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28082, 1092, 2, 'Jesus chamou uma criancinha, colocou-a no meio deles e disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28083, 1092, 3, '“Em verdade vos declaro: se não vos transformardes e vos tornardes como criancinhas, não entrareis no Reino dos Céus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28084, 1092, 4, 'Aquele que se fizer humilde como esta criança será maior no Reino dos Céus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28085, 1092, 5, 'E o que recebe em meu nome a um menino como este, é a mim que recebe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28086, 1092, 6, 'Mas, se alguém fizer cair em pecado um destes pequenos que creem em mim, melhor fora que lhe atassem ao pescoço a mó de um moinho e o lançassem no fundo do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28087, 1092, 7, 'Ai do mundo por causa dos escândalos! Eles são inevitáveis, mas ai do homem que os causa!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28088, 1092, 8, 'Por isso, se tua mão ou teu pé te fazem cair em pecado, corta-os e lança-os longe de ti: é melhor para ti entrares na vida coxo ou manco que, tendo dois pés e duas mãos, seres lançado no fogo eterno.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28089, 1092, 9, 'Se teu olho te leva ao pecado, arranca-o e lança-o longe de ti: é melhor para ti entrares na vida cego de um olho que seres jogado com teus dois olhos no fogo da geena.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28090, 1092, 10, 'Guardai-vos de menosprezar um só destes pequenos, porque eu vos digo que seus anjos no céu contemplam sem cessar a face de meu Pai que está nos céus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28091, 1092, 11, '[Porque o Filho do Homem veio salvar o que estava perdido.]*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28092, 1092, 12, 'Que vos parece? Um homem possui cem ovelhas: uma delas se desgarra. Não deixa ele as noventa e nove na montanha, para ir buscar aquela que se desgarrou?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28093, 1092, 13, 'E se a encontra, sente mais júbilo do que pelas noventa e nove que não se desgarraram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28094, 1092, 14, 'Assim é a vontade de vosso Pai celeste, que não se perca um só destes pequeninos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28095, 1092, 15, '“Se teu irmão tiver pecado contra ti, vai e repreende-o entre ti e ele somente; se te ouvir, terás ganho teu irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28096, 1092, 16, 'Se não te escutar, toma contigo uma ou duas pessoas, a fim de que toda a questão se resolva pela decisão de duas ou três testemunhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28097, 1092, 17, 'Se recusa ouvi-los, dize-o à Igreja. E se recusar ouvir também a Igreja, seja ele para ti como um pagão e um publicano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28098, 1092, 18, 'Em verdade vos digo: tudo o que ligardes sobre a terra será ligado no céu, e tudo o que desligar­des sobre a terra será também desligado no céu.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28099, 1092, 19, '“Digo-vos ainda isto: se dois de vós se unirem sobre a terra para pedir, seja o que for, o conseguirão de meu Pai que está nos céus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28100, 1092, 20, 'Porque onde dois ou três estão reunidos em meu nome, aí estou eu no meio deles.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28101, 1092, 21, 'Então, Pedro se aproximou dele e disse: “Senhor, quantas vezes devo per­doar a meu irmão, quando ele pecar contra mim? Até sete vezes?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28102, 1092, 22, 'Respondeu Jesus: “Não te digo até sete vezes, mas até setenta vezes sete”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28103, 1092, 23, '“Por isso, o Reino dos Céus é comparado a um rei que quis ajustar contas com seus servos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28104, 1092, 24, 'Quando começou a ajustá-las, trouxeram-lhe um que lhe devia dez mil talentos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28105, 1092, 25, 'Como ele não tinha com que pagar, seu senhor ordenou que fosse vendido, ele, sua mulher, seus filhos e todos os seus bens para pagar a dívida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28106, 1092, 26, 'Este servo, então, prostrou-se por terra diante dele e suplicava-lhe: ‘Dá-me um prazo e eu te pagarei tudo!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28107, 1092, 27, 'Cheio de compaixão, o senhor o deixou ir embora e perdoou-lhe a dívida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28108, 1092, 28, 'Apenas saiu dali, encontrou um de seus companheiros de serviço que lhe devia cem denários. Agarrou-o na garganta e quase o estrangulou, dizendo: ‘Paga o que me deves!’');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28109, 1092, 29, 'O outro caiu-lhe aos pés e pediu-lhe: ‘Dá-me um prazo e eu te pagarei!’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28110, 1092, 30, 'Mas, sem nada querer ouvir, este homem o fez lançar na prisão, até que tivesse pago sua dívida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28111, 1092, 31, 'Vendo isso, os outros servos, profundamente tristes, vieram contar a seu senhor o que se tinha passado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28112, 1092, 32, 'Então, o senhor o chamou e lhe disse: ‘Servo mau, eu te perdoei toda a dívida porque me suplicaste.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28113, 1092, 33, 'Não devias também tu compadecer-te de teu companheiro de serviço, como eu tive piedade de ti?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28114, 1092, 34, 'E o senhor, encolerizado, entregou-o aos algozes, até que pagasse toda a sua dívida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28115, 1092, 35, 'Assim vos tratará meu Pai celeste, se cada um de vós não perdoar a seu irmão, de todo o seu coração.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28116, 1093, 1, 'Após esses discursos, Jesus deixou a Galileia e veio para a Judeia, além do Jor­dão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28117, 1093, 2, 'Uma grande multidão o seguiu e ele curou seus doentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28118, 1093, 3, 'Os fariseus vieram perguntar-lhe para pô-lo à prova: “É permitido a um homem rejeitar sua mu­lher por um motivo qualquer?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28119, 1093, 4, 'Respondeu-lhes Jesus: “Não lestes que o Criador, no começo, fez o homem e a mulher e disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28120, 1093, 5, 'Por isso, o homem deixará seu pai e sua mãe e se unirá à sua mulher; e os dois formarão uma só carne?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28121, 1093, 6, 'Assim, já não são dois, mas uma só carne. Portanto, não separe o homem o que Deus uniu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28122, 1093, 7, 'Disseram-lhe eles: “Por que, então, Moisés ordenou dar um documento de divórcio à mulher, ao rejeitá-la?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28123, 1093, 8, 'Jesus respondeu-lhes: “É por causa da dureza de vosso coração que Moisés havia tolerado o repúdio das mulheres; mas no começo não foi assim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28124, 1093, 9, 'Ora, eu vos declaro que todo aquele que rejeita sua mulher, exceto no caso de matrimônio falso, e desposa uma outra, comete adultério. E aquele que desposa uma mulher rejeitada, comete também adultério”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28125, 1093, 10, 'Seus discípulos disseram-lhe: “Se tal é a condição do homem a respeito da mulher, é melhor não se casar!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28126, 1093, 11, 'Respondeu ele: “Nem todos são capazes de compreender o sentido dessa palavra, mas somente aqueles a quem foi dado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28127, 1093, 12, 'Porque há eunucos que o são desde o ventre de suas mães, há eunucos tornados tais pelas mãos dos homens e há eunucos que a si mesmos se fizeram eunucos por amor do Reino dos Céus. Quem puder compreender, compreenda”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28128, 1093, 13, 'Foram-lhe, então, apresentadas algumas criancinhas para que pusesse as mãos sobre elas e orasse por elas. Os discípulos, porém, as afastavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28129, 1093, 14, 'Disse-lhes Jesus: “Dei­xai vir a mim estas criancinhas e não as impeçais, porque o Reino dos Céus é para aqueles que se lhes assemelham”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28130, 1093, 15, 'E, depois de impor-lhes as mãos, continuou seu caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28131, 1093, 16, 'Um jovem aproximou-se de Jesus e lhe perguntou: “Mestre, que devo fazer de bom para ter a vida eterna?”. Disse-lhe Jesus:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28132, 1093, 17, '“Por que me perguntas a respeito do que se deve fazer de bom? Só Deus é bom. Se queres entrar na vida, observa os mandamentos”.* –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28133, 1093, 18, '“Quais?” – per­guntou ele. Jesus respondeu: “Não matarás, não cometerás adultério, não furtarás, não dirás falso testemunho,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28134, 1093, 19, 'honra teu pai e tua mãe, amarás teu próximo como a ti mesmo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28135, 1093, 20, 'Disse-lhe o jovem: “Tenho obser­vado tudo isso desde a minha infância. Que me falta ainda?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28136, 1093, 21, 'Respondeu Jesus: “Se queres ser perfeito, vai, vende teus bens, dá-os aos pobres e terás um tesouro no céu. Depois, vem e segue-me!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28137, 1093, 22, 'Ouvindo essas palavras, o jovem foi embora muito triste, porque possuía muitos bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28138, 1093, 23, 'Jesus disse então aos seus discípulos: “Em verdade vos declaro: é difícil para um rico entrar no Reino dos Céus!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28139, 1093, 24, 'Eu vos repito: é mais fácil um camelo passar pelo fundo de uma agulha do que um rico entrar no Reino de Deus”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28140, 1093, 25, 'A estas palavras seus discípulos, pasmados, perguntaram: “Quem poderá então salvar-se?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28141, 1093, 26, 'Jesus olhou para eles e disse: “Aos homens isso é impossível, mas a Deus tudo é possível”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28142, 1093, 27, 'Pedro então, tomando a palavra, disse-lhe: “Eis que deixamos tudo para te seguir. Que haverá então para nós?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28143, 1093, 28, 'Respondeu Jesus: “Em verdade vos declaro: no dia da renovação do mundo, quando o Filho do Homem estiver sentado no trono da glória, vós, que me haveis seguido, estareis sentados em doze tronos para julgar as doze tribos de Israel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28144, 1093, 29, 'E todo aquele que por minha causa deixar irmãos, irmãs, pai, mãe, mulher, filhos, terras ou casa receberá o cêntuplo e possuirá a vida eterna”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28145, 1093, 30, '“Muitos dos primeiros serão os últimos e muitos dos últimos serão os primeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28146, 1094, 1, 'Com efeito, o Reino dos Céus é semelhan­te a um pai de família que saiu ao romper da manhã, a fim de contratar operários para sua vinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28147, 1094, 2, 'Ajustou com eles um denário por dia e enviou-os para sua vinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28148, 1094, 3, 'Cerca da terceira hora, saiu ainda e viu alguns que estavam na praça sem fazer nada.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28149, 1094, 4, 'Disse-lhes ele: ‘Ide também vós para minha vinha e vos darei o justo salário’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28150, 1094, 5, 'Eles foram. À sexta hora saiu de novo e igualmente pela nona hora, e fez o mesmo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28151, 1094, 6, 'Finalmente, pela undéci­ma hora, encontrou ainda outros na praça e perguntou-lhes: ‘Por que estais todo o dia sem fazer nada?’');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28152, 1094, 7, 'Eles responderam: ‘É porque ninguém nos contratou’. Disse-lhes ele, então: – Ide vós também para minha vinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28153, 1094, 8, 'Ao cair da tarde, o senhor da vinha disse a seu feitor: ‘Chama os operários e paga-lhes, começando pelos últimos até os primeiros’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28154, 1094, 9, 'Vieram aqueles da undécima hora e receberam cada qual um denário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28155, 1094, 10, 'Chegando por sua vez os primeiros, julgavam que haviam de receber mais. Mas só receberam cada qual um denário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28156, 1094, 11, 'Ao receberem, murmuravam contra o pai de família, dizendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28157, 1094, 12, '‘Os últimos só trabalharam uma hora... e deste-lhes tanto como a nós, que suportamos o peso do dia e do calor’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28158, 1094, 13, 'O senhor, porém, observou a um deles: ‘Meu amigo, não te faço injustiça. Não contrataste comigo um denário?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28159, 1094, 14, 'Toma o que é teu e vai-te. Eu quero dar a este último tanto quanto a ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28160, 1094, 15, 'Ou não me é permitido fazer dos meus bens o que me apraz? Porventura vês com maus olhos que eu seja bom?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28161, 1094, 16, 'Assim, pois, os últimos serão os primeiros e os primeiros serão os últimos. [Muitos serão os chamados, mas poucos os escolhidos.]”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28162, 1094, 17, 'Subindo para Jerusalém, durante o caminho, Jesus tomou à parte os Doze e disse-lhes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28163, 1094, 18, '“Eis que subimos a Jerusalém, e o Filho do Homem será entregue aos prínci­pes dos sacerdotes e aos escribas. Eles o condenarão à morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28164, 1094, 19, 'E o entregarão aos pagãos para ser exposto às suas zombarias, açoitado e crucificado; mas ao terceiro dia ressuscitará”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28165, 1094, 20, 'Nisso aproximou-se a mãe dos filhos de Zebedeu com seus filhos e prostrou-se diante de Jesus para lhe fazer uma súplica.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28166, 1094, 21, 'Perguntou-lhe ele: “Que queres?”. Ela respondeu: “Ordena que estes meus dois filhos se sentem no teu Reino, um à tua direita e outro à tua esquerda”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28167, 1094, 22, 'Jesus disse: “Não sabeis o que pedis. Podeis vós beber o cálice que eu devo beber?”. “Sim” – disseram-lhe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28168, 1094, 23, '“De fato, bebereis meu cálice. Quanto, porém, a sentar-vos à mi­nha direi­ta ou à minha esquerda, isso não depende de mim vo-lo conceder. Esses lugares cabem àqueles aos quais meu Pai os reservou.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28169, 1094, 24, 'Os dez outros, que haviam ouvido tudo, indignaram-se contra os dois irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28170, 1094, 25, 'Jesus, porém, os chamou e lhes disse: “Sabeis que os chefes das nações as subjugam, e que os grandes as governam com autoridade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28171, 1094, 26, 'Não seja assim entre vós. Todo aquele que quiser tornar-se grande entre vós, se faça vosso servo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28172, 1094, 27, 'E o que quiser tornar-se entre vós o primeiro, se faça vosso escravo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28173, 1094, 28, 'Assim como o Filho do Homem veio, não para ser servido, mas para servir e dar sua vida em resgate por uma multidão”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28174, 1094, 29, 'Ao sair de Jericó, uma grande multidão o seguiu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28175, 1094, 30, 'Dois cegos, sentados à beira do caminho, ouvindo dizer que Jesus passava, começaram a gritar: “Senhor, filho de Davi, tem piedade de nós!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28176, 1094, 31, 'A multidão, porém, os repreendia para que se calassem. Mas eles gritavam ainda mais forte: “Se­nhor, filho de Davi, tem piedade de nós!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28177, 1094, 32, 'Jesus parou, chamou-os e perguntou-lhes: “Que quereis que eu vos faça?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28178, 1094, 33, '“Senhor, que nossos olhos se abram!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28179, 1094, 34, 'Jesus, cheio de compaixão, tocou-lhes os olhos. Instantaneamente recobraram a vista e puseram-se a segui-lo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28180, 1095, 1, 'Aproximavam-se de Jerusalém. Quando chegaram a Betfagé, perto do monte das Oliveiras, Jesus enviou dois de seus discípulos,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28181, 1095, 2, 'dizendo-lhes: “Ide à aldeia que está defronte. Encon­tra­reis logo uma jumenta amarrada e com ela seu jumentinho. Desamarrai-os e trazei-mos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28182, 1095, 3, 'Se alguém vos disser qualquer coisa, respondei-lhe que o Senhor necessita deles e que ele sem demora os devolverá”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28183, 1095, 4, 'Assim, neste acontecimento, cumpria-se o oráculo do profeta:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28184, 1095, 5, 'Dizei à filha de Sião: Eis que teu rei vem a ti, cheio de doçura, montado numa jumenta, num jumentinho, filho da que leva o jugo (Zc 9,9).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28185, 1095, 6, 'Os discípulos foram e exe­cutaram a ordem de Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28186, 1095, 7, 'Trouxe­ram a jumenta e o jumentinho, cobriram-nos com seus mantos e fizeram-no montar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28187, 1095, 8, 'Então, a multidão estendia os mantos pelo caminho, cortava ramos de árvores e espalhava-os pela estrada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28188, 1095, 9, 'E toda aquela multidão, que o precedia e que o seguia, clamava: “Hosana ao filho de Davi! Bendito seja aquele que vem em nome do Senhor! Hosana no mais alto dos céus!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28189, 1095, 10, 'Quando ele entrou em Jerusalém, alvoroçou-se toda a cidade, perguntando: “Quem é este?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28190, 1095, 11, 'A multidão respondia: “É Jesus, o profeta de Nazaré da Galileia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28191, 1095, 12, 'Jesus entrou no templo e expulsou dali todos aqueles que se entregavam ao comércio. Derru­bou as mesas dos cambistas e os bancos dos negociantes de pombas,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28192, 1095, 13, 'e disse-lhes: “Está escrito: Minha casa é uma casa de oração (Is 56,7), mas vós fizestes dela um covil de ladrões (Jr 7,11)!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28193, 1095, 14, 'Os cegos e os coxos vieram a ele no templo e ele os curou,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28194, 1095, 15, 'com grande indignação dos príncipes dos sacerdotes e dos escribas que assistiam a seus milagres e ouviam os meninos gritarem no templo: “Hosana ao filho de Davi!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28195, 1095, 16, 'Disseram-lhe eles: “Ouves o que dizem eles?”. “Perfeitamente, respondeu-lhes Jesus. Nunca lestes estas palavras: Da boca dos meninos e das crianças de peito tirastes o vosso louvor” (Sl 8,3)?.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28196, 1095, 17, 'Depois os deixou e saiu da cidade para hospedar-se em Betânia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28197, 1095, 18, 'De manhã, voltando à cidade, teve fome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28198, 1095, 19, 'Vendo uma figueira à beira do caminho, aproximou-se dela, mas só achou nela folhas; e disse-lhe: “Jamais nasça fruto de ti!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28199, 1095, 20, 'E imediatamente a figueira secou. À vista disso, os discípulos ficaram estupefatos e disseram: “Como ficou seca num instante a figueira?!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28200, 1095, 21, 'Respondeu-lhes Jesus: “Em verdade vos declaro que, se tiverdes fé e não hesitardes, não só fareis o que foi feito a esta figueira, mas ainda se disser­des a esta montanha: Levanta-te daí e atira-te ao mar, isso se fará...*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28201, 1095, 22, 'Tudo o que pedirdes com fé na oração, vós o alcançareis”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28202, 1095, 23, 'Dirigiu-se Jesus ao templo. E, enquanto ensinava, os príncipes dos sacerdotes e os anciãos do povo aproximaram-se e perguntaram-lhe: “Com que direito fazes isso? Quem te deu essa autoridade?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28203, 1095, 24, 'Respondeu-lhes Jesus: “Eu vos proporei também uma questão. Se respon­derdes, eu vos direi com que direito o faço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28204, 1095, 25, 'Donde procedia o batismo de João: do céu ou dos homens?”. Ora, eles raciocinavam entre si: “Se respondermos: Do céu, ele nos dirá: Por que não crestes nele?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28205, 1095, 26, 'E se dissermos: Dos homens, é de temer-se a multidão, porque todo o mundo considera João como profeta”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28206, 1095, 27, 'Responde­ram a Jesus: “Não sabemos”. “Pois eu tampouco vos digo” – retorquiu Jesus – “com que direito faço essas coisas.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28207, 1095, 28, '“Que vos parece? Um homem tinha dois filhos. Dirigindo-se ao primeiro, disse-lhe: ‘Meu filho, vai trabalhar hoje na vinha’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28208, 1095, 29, 'Respondeu ele: ‘Não quero’. Mas, em seguida, tocado de arrependimento, foi.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28209, 1095, 30, 'Dirigindo-se depois ao outro, disse-lhe a mesma coisa. O filho res­pondeu: ‘Sim, pai!’. Mas não foi.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28210, 1095, 31, 'Qual dos dois fez a vontade do pai? ‘O primeiro’ – responderam-lhe. E Jesus disse-lhes: ‘Em verdade vos digo: os publicanos e as mere­trizes vos precedem no Reino de Deus!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28211, 1095, 32, 'João veio a vós no caminho da justiça e não crestes nele. Os publicanos, porém, e as prostitutas creram nele. E vós, vendo isso, nem fostes tocados de arrependimento para crerdes nele’.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28212, 1095, 33, '“Ouvi outra parábola: havia um pai de família que plantou uma vinha. Cercou-a com uma sebe, cavou um lagar e edificou uma torre. E, tendo-a arrendado a lavradores, deixou o país.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28213, 1095, 34, 'Vindo o tempo da colheita, enviou seus servos aos lavradores para recolher o produto de sua vinha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28214, 1095, 35, 'Mas os lavradores agarraram os servos, feriram um, mataram outro e apedrejaram o terceiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28215, 1095, 36, 'Enviou outros servos em maior número que os primeiros, e fize­ram-lhes o mesmo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28216, 1095, 37, 'Enfim, enviou seu próprio filho, dizendo: Hão de respeitar meu filho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28217, 1095, 38, 'Os lavradores, porém, vendo o filho, disseram uns aos outros: Eis o herdeiro! Matemo-lo e teremos a sua herança!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28218, 1095, 39, 'Lançaram-lhe as mãos, conduziram-no para fora da vinha e o assassinaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28219, 1095, 40, 'Pois bem: quando voltar o senhor da vinha, que fará ele àqueles lavradores?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28220, 1095, 41, 'Responderam-lhe: “Mandará matar sem piedade aqueles miseráveis e arrendará sua vinha a outros lavradores que lhe pagarão o produto em seu tempo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28221, 1095, 42, 'Jesus acrescentou: “Nunca lestes nas Escrituras: A pedra rejeitada pelos construtores tornou-se a pedra angular; isto é obra do Senhor, e é admirável aos nossos olhos (Sl 117,22)?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28222, 1095, 43, 'Por isso, vos digo: será tirado de vós o Reino de Deus, e será dado a um povo que produzirá os frutos dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28223, 1095, 44, '[Aquele que tropeçar nesta pedra, far-se-á em pedaços; e aquele sobre quem ela cair será esmagado.]”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28224, 1095, 45, 'Ouvindo isso, os príncipes dos sacerdotes e os fariseus compreenderam que era deles que Jesus falava.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28225, 1095, 46, 'E procuravam prendê-lo; mas temeram o povo, que o tinha por um profeta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28226, 1096, 1, 'Jesus tornou a falar-lhes por meio de parábolas:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28227, 1096, 2, '“O Reino dos Céus é comparado a um rei que celebrava as bodas de seu filho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28228, 1096, 3, 'Enviou seus servos para chamar os convidados, mas eles não quiseram vir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28229, 1096, 4, 'Enviou outros ainda, dizendo-lhes: Dizei aos convidados que já está preparado o meu banquete; meus bois e meus animais cevados estão mortos, tudo está preparado. Vinde às bodas!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28230, 1096, 5, 'Mas, sem se importarem com aquele convite, foram-se, um a seu campo e outro para seu negócio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28231, 1096, 6, 'Outros lançaram mãos de seus servos, insultaram-nos e os mataram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28232, 1096, 7, 'O rei soube e indignou-se em extremo. Enviou suas tropas, matou aqueles assassinos e incendiou-lhes a cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28233, 1096, 8, 'Disse depois a seus servos: O festim está pronto, mas os convidados não foram dignos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28234, 1096, 9, 'Ide às encruzilhadas e convidai para as bodas todos quantos achardes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28235, 1096, 10, 'Espalharam-se eles pelos caminhos e reuniram todos quantos acharam, maus e bons, de modo que a sala do banquete ficou repleta de convidados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28236, 1096, 11, 'O rei entrou para vê-los e viu ali um homem que não trazia a veste nupcial.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28237, 1096, 12, 'Perguntou-lhe: Meu amigo, como entraste aqui, sem a veste nupcial? O homem não proferiu palavra alguma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28238, 1096, 13, 'Disse, então, o rei aos servos: Amarrai-lhe os pés e as mãos e lançai-o nas trevas exteriores. Ali haverá choro e ranger de dentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28239, 1096, 14, 'Porque muitos são os chamados, e poucos os escolhidos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28240, 1096, 15, 'Reuniram-se então os fariseus para deliberar entre si sobre a maneira de surpreender Jesus nas suas próprias palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28241, 1096, 16, 'Enviaram seus discípulos com os herodianos, que lhe disseram: “Mestre, sabemos que és verdadeiro e ensinas o caminho de Deus em toda a verdade, sem te preocupares com ninguém, porque não olhas para a aparência dos homens.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28242, 1096, 17, 'Dize-nos, pois, o que te parece: É permitido ou não pagar o imposto a César?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28243, 1096, 18, 'Jesus, percebendo a sua malícia, respondeu: “Por que me tentais, hipócritas?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28244, 1096, 19, 'Mostrai-me a moeda com que se paga o imposto!”. Apresentaram-lhe um denário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28245, 1096, 20, 'Perguntou Jesus: “De quem é esta imagem e esta inscrição?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28246, 1096, 21, '“De César” – responderam-lhe. Disse-lhes então Jesus: “Dai, pois, a César o que é de César e a Deus o que é de Deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28247, 1096, 22, 'Esta resposta encheu-os de admiração e, deixando-o, retiraram-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28248, 1096, 23, 'Naquele mesmo dia, os saduceus, que negavam a ressurreição, interrogaram-no:*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28249, 1096, 24, '“Mestre, Moisés disse: Se um homem morrer sem filhos, seu irmão case-se com a sua viúva e dê-lhe assim uma posteridade (Dt 25,5).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28250, 1096, 25, 'Ora, havia entre nós sete irmãos. O primeiro casou-se e morreu. Como não tinha filhos, deixou sua mulher ao seu irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28251, 1096, 26, 'O mesmo sucedeu ao segundo, depois ao terceiro, até o sétimo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28252, 1096, 27, 'Por sua vez, depois deles todos, morreu também a mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28253, 1096, 28, 'Na ressurreição, de qual dos sete será a mulher, uma vez que todos a tiveram?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28254, 1096, 29, 'Respondeu-lhes Jesus: “Errais, não compreendendo as Escrituras nem o poder de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28255, 1096, 30, 'Na ressurreição, os homens não terão mulheres nem as mulheres, maridos; mas serão como os anjos de Deus no céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28256, 1096, 31, 'Quanto à ressurreição dos mortos, não lestes o que Deus vos disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28257, 1096, 32, 'Eu sou o Deus de Abraão, o Deus de Isaac e o Deus de Jacó (Ex 3,6)? Ora, ele não é Deus dos mortos, mas Deus dos vivos”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28258, 1096, 33, 'E, ouvindo essa doutrina, as turbas se enchiam de grande admiração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28259, 1096, 34, 'Sabendo os fariseus que Jesus reduzira ao silêncio os saduceus, reuniram-se');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28260, 1096, 35, 'e um deles, doutor da Lei, fez-lhe esta pergunta para pô-lo à prova:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28261, 1096, 36, '“Mestre, qual é o maior mandamento da Lei?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28262, 1096, 37, 'Respondeu Jesus: “Amarás o Senhor, teu Deus, de todo o teu o coração, de toda a tua alma e de todo o teu espírito (Dt 6,5).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28263, 1096, 38, 'Esse é o maior e o primeiro mandamento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28264, 1096, 39, 'E o segundo, semelhante a este, é: Amarás teu próximo como a ti mesmo (Lv 19,18).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28265, 1096, 40, 'Nesses dois mandamentos se resumem toda a Lei e os Profetas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28266, 1096, 41, 'Como os fariseus se agrupassem, Jesus interrogou-os:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28267, 1096, 42, '“Que pensais vós de Cristo? De quem é filho?”. Responderam: “De Davi!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28268, 1096, 43, '“Como então, prosseguiu Jesus, Davi, falando sob inspiração do Espírito, chama-o Senhor, dizendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28269, 1096, 44, 'O Senhor disse a meu Senhor: Senta-te à minha direita, até que eu ponha teus inimigos por escabelo dos teus pés (Sl 109,1)?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28270, 1096, 45, 'Se, pois, Davi o chama Senhor, como é ele seu filho?”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28271, 1096, 46, 'Ninguém pôde responder-lhe nada. E, depois daquele dia, ninguém mais ousou interrogá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28272, 1097, 1, 'Dirigindo-se, então, Jesus à multidão e aos seus discípulos, disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28273, 1097, 2, '“Os escribas e os fariseus sentaram-se na cadeira de Moisés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28274, 1097, 3, 'Observai e fazei tudo o que eles dizem, mas não façais como eles, pois dizem e não fazem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28275, 1097, 4, 'Atam fardos pesados e esmagadores e com eles sobrecarregam os ombros dos homens, mas não querem movê-los sequer com o dedo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28276, 1097, 5, 'Fazem todas as suas ações para serem vistos pelos homens, por isso trazem largas faixas e longas franjas nos seus mantos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28277, 1097, 6, 'Gostam dos primeiros lugares nos banquetes e das primeiras cadeiras nas sinagogas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28278, 1097, 7, 'Gostam de ser saudados nas praças públicas e de ser chamados rabi pelos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28279, 1097, 8, 'Mas vós não vos façais chamar rabi, porque um só é o vosso preceptor, e vós sois todos irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28280, 1097, 9, 'E a ninguém chameis de pai sobre a terra, porque um só é vosso Pai, aquele que está nos céus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28281, 1097, 10, 'Nem vos façais chamar de mestres, porque só tendes um Mestre, o Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28282, 1097, 11, 'O maior dentre vós será vosso servo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28283, 1097, 12, 'Aquele que se exaltar será humilhado, e aquele que se humilhar será exaltado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28284, 1097, 13, 'Ai de vós, escribas e fariseus hipócritas! Vós fechais aos homens o Reino dos Céus. Vós mesmos não entrais e nem deixais que entrem os que querem entrar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28285, 1097, 14, '[Ai de vós, escribas e fariseus hipócritas! Devorais as casas das viú­vas, fingindo fazer longas orações. Por isso, sereis castigados com muito maior rigor.]*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28286, 1097, 15, 'Ai de vós, escribas e fariseus hipócritas! Percorreis mares e terras para fazer um prosélito e, quando o conseguis, fazeis dele um filho do inferno duas vezes pior que vós mesmos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28287, 1097, 16, 'Ai de vós, guias cegos! Vós dizeis: Se alguém jura pelo templo, isto não é nada; mas, se jura pelo tesouro do templo, é obrigado pelo seu juramento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28288, 1097, 17, 'Insensatos, cegos! Qual é o maior: o ouro ou o templo que santifica o ouro?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28289, 1097, 18, 'E dizeis ainda: Se alguém jura pelo altar, não é nada; mas, se jura pela oferta que está sobre ele, é obrigado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28290, 1097, 19, 'Cegos! Qual é o maior: a oferta ou o altar que santifica a oferta?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28291, 1097, 20, 'Aquele que jura pelo altar jura ao mesmo tempo por tudo o que está sobre ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28292, 1097, 21, 'Aquele que jura pelo templo, jura ao mesmo tempo por aquele que nele habita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28293, 1097, 22, 'E aquele que jura pelo céu, jura ao mesmo tempo pelo trono de Deus e por aquele que nele está sentado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28294, 1097, 23, '“Ai de vós, escribas e fariseus hipócritas! Pagais o dízimo da hortelã, do endro e do cominho e desprezais os preceitos mais importantes da Lei: a justiça, a misericórdia, a fidelidade. Eis o que era preciso praticar em primeiro lugar sem, contudo, deixar o restante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28295, 1097, 24, 'Guias cegos! Filtrais um mosquito e engolis um camelo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28296, 1097, 25, 'Ai de vós, escribas e fariseus hipócritas! Limpais por fora o copo e o prato e por dentro estais cheios de roubo e de intemperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28297, 1097, 26, 'Fariseu cego! Limpa primeiro o interior do copo e do prato, para que também o que está fora fique limpo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28298, 1097, 27, 'Ai de vós, escribas e fariseus hipócritas! Sois semelhantes aos sepulcros caiados: por fora parecem formosos, mas por dentro estão cheios de ossos, de cadáveres e de toda espécie de podridão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28299, 1097, 28, 'Assim também vós: por fora pareceis justos aos olhos dos homens, mas por dentro estais cheios de hipocrisia e de iniquidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28300, 1097, 29, 'Ai de vós, escribas e fariseus hipócritas! Edificais sepulcros aos profetas, adornais os monumentos dos justos');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28301, 1097, 30, 'e dizeis: Se tivéssemos vivido no tempo de nossos pais, não teríamos manchado nossas mãos como eles no sangue dos profetas...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28302, 1097, 31, 'Testemunhais assim contra vós mesmos que sois de fato os fi­lhos dos assassinos dos profetas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28303, 1097, 32, 'Acabai, pois, de encher a medida de vossos pais!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28304, 1097, 33, 'Serpentes! Raça de víboras! Como escapareis ao castigo do inferno?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28305, 1097, 34, 'Vede, eu vos envio profetas, sábios, doutores. Matareis e crucificareis uns e açoitareis outros nas vossas sinagogas. Eu os perseguireis de cidade em cidade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28306, 1097, 35, 'para que caia sobre vós todos o sangue inocente derramado sobre a terra, desde o sangue de Abel, o justo, até o sangue de Zacarias, filho de Baraquias, a quem matastes entre o templo e o altar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28307, 1097, 36, 'Em verdade vos digo: todos esses crimes pesam sobre esta raça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28308, 1097, 37, 'Jerusalém, Jerusalém, que matas os profetas e apedrejas aqueles que te são enviados! Quantas vezes eu quis reunir teus fi­lhos, como a galinha reúne seus pinti­nhos debaixo de suas asas... e tu não quiseste!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28309, 1097, 38, 'Pois bem, a vossa casa vos é deixada deserta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28310, 1097, 39, 'Porque eu vos digo: já não me vereis de hoje em diante, até que digais: Bendito seja aquele que vem em nome do Senhor.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28311, 1098, 1, 'Ao sair do templo, os dis­cípulos aproximaram-se de Jesus e fizeram-no apreciar as construções.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28312, 1098, 2, 'Jesus, porém, respondeu-lhes: “Vedes todos estes edifícios? Em verdade vos declaro: não ficará aqui pedra sobre pedra; tudo será destruído”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28313, 1098, 3, 'Indo ele assentar-se no monte das Oliveiras, achegaram-se os discípulos e, estando a sós com ele, perguntaram-lhe: “Quando acontecerá isto? E qual será o sinal de tua volta e do fim do mundo?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28314, 1098, 4, 'Respondeu-lhes Jesus: “Cuidai que ninguém vos seduza.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28315, 1098, 5, 'Muitos virão em meu nome, dizendo: Sou eu o Cristo. E seduzirão a muitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28316, 1098, 6, 'Ouvireis falar de guerras e de rumores de guerra. Atenção: que isso não vos perturbe, porque é preciso que isso aconteça. Mas ainda não será o fim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28317, 1098, 7, 'Irá levantar-se nação contra nação, reino contra reino, e haverá fome, peste e grandes desgraças em diversos lugares.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28318, 1098, 8, 'Tudo isso será apenas o início das dores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28319, 1098, 9, 'Então, sereis entregues aos tormentos, sereis mortos e sereis por minha causa, sereis objeto de ódio para todas as nações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28320, 1098, 10, 'Muitos sucumbirão, serão traídos mutuamente e mutuamente se odiarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28321, 1098, 11, 'Irão levantar-se muitos falsos profetas e seduzirão a muitos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28322, 1098, 12, 'E, ante o progresso crescente da iniquidade, a caridade de muitos esfriará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28323, 1098, 13, 'Entretanto, aquele que perseverar até o fim será salvo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28324, 1098, 14, 'Este Evangelho do Reino será pregado pelo mundo inteiro para servir de testemunho a todas as nações, e então chegará o fim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28325, 1098, 15, 'Quando virdes estabelecida no lugar santo a abominação da desolação que foi predita pelo profeta Daniel (9,27) – o leitor entenda bem –,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28326, 1098, 16, 'então os habitantes da Judeia fujam para as montanhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28327, 1098, 17, 'Aquele que está no terraço da casa não desça para tomar o que está em sua casa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28328, 1098, 18, 'E aquele que está no campo não volte para buscar suas vestimentas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28329, 1098, 19, 'Ai das mulheres que estiverem grávidas ou amamentarem naqueles dias!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28330, 1098, 20, 'Rogai para que vossa fuga não seja no inverno, nem em dia de sábado;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28331, 1098, 21, 'porque então a tribulação será tão grande como nunca foi vista, desde o começo do mundo até o presente, nem jamais será.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28332, 1098, 22, 'Se aqueles dias não fossem abreviados, criatura alguma escaparia; mas, por causa dos escolhidos, aqueles dias serão abreviados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28333, 1098, 23, 'Então, se alguém vos disser: Eis, aqui está o Cristo! Ou: Ei-lo acolá!, não creiais.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28334, 1098, 24, 'Porque se levantarão falsos cristos e falsos profetas, que farão milagres a ponto de seduzir, se isso fosse possível, até mesmo os escolhidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28335, 1098, 25, 'Eis que estais prevenidos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28336, 1098, 26, 'Se, pois, vos disserem: Vinde, ele está no deserto, não saiais. Ou: Lá está ele em casa, não o creiais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28337, 1098, 27, 'Porque, como o relâmpago parte do Oriente e ilumina até o Ocidente, assim será a volta do Filho do Homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28338, 1098, 28, 'Onde houver um cadáver, aí se ajuntarão os abutres”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28339, 1098, 29, '“Logo após esses dias de tribulação, o sol escurecerá, a lua não terá claridade, cairão do céu as estrelas e as potências dos céus serão abaladas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28340, 1098, 30, 'Então, aparecerá no céu o sinal do Filho do Homem. Todas as tribos da terra baterão no peito e verão o Filho do Homem vir sobre as nuvens do céu cercado de glória e de majestade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28341, 1098, 31, 'Ele enviará seus anjos com estridentes trombetas, e juntarão seus escolhidos dos quatro ventos, de uma extremidade do céu à outra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28342, 1098, 32, 'Compreendei isso pela comparação da figueira: quando seus ramos estão tenros e crescem as folhas, pressentis que o verão está próximo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28343, 1098, 33, 'Do mesmo modo, quando virdes tudo isso, sabei que o Filho do Homem está próximo, à porta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28344, 1098, 34, 'Em verdade vos declaro: não passará esta geração antes que tudo isso aconteça.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28345, 1098, 35, 'O céu e a terra passarão, mas as minhas palavras não passarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28346, 1098, 36, 'Quanto àquele dia e àquela hora, ninguém o sabe, nem mesmo os anjos do céu, mas somente o Pai.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28347, 1098, 37, 'Assim como foi nos tempos de Noé, assim acontecerá na vinda do Filho do Homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28348, 1098, 38, 'Nos dias que precederam o dilúvio, comiam, bebiam, casavam-se e davam-se em casamento, até o dia em que Noé entrou na arca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28349, 1098, 39, 'E os homens de nada sabiam, até o momento em que veio o dilúvio e os levou a todos. Assim será também na volta do Filho do Homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28350, 1098, 40, 'Dois homens estarão no campo: um será tomado, o outro será deixado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28351, 1098, 41, 'Duas mulheres estarão moendo no mesmo moinho: uma será tomada e a outra será deixada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28352, 1098, 42, 'Vigiai, pois, porque não sabeis a hora em que virá o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28353, 1098, 43, 'Sabei que se o pai de família soubesse em que hora da noite viria o ladrão, vigiaria e não deixaria arrombar a sua casa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28354, 1098, 44, 'Por isso, estai também vós preparados porque o Filho do Homem virá numa hora em que menos pensardes.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28355, 1098, 45, '“Quem é, pois, o servo fiel e prudente que o Senhor constituiu sobre os de sua família, para dar-lhes o alimento no momento oportuno?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28356, 1098, 46, 'Bem-aventurado aquele servo a quem seu senhor, na sua volta, encontrar procedendo assim!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28357, 1098, 47, 'Em verdade vos digo: ele o estabelecerá sobre todos os seus bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28358, 1098, 48, 'Mas, se é um mau servo que imagina consigo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28359, 1098, 49, '‘Meu senhor tarda a vir’, e se põe a bater em seus companheiros e a comer e a beber com os ébrios,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28360, 1098, 50, 'o senhor desse servo virá no dia em que ele não o espera e na hora em que ele não sabe,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28361, 1098, 51, 'e o despedirá e o mandará ao destino dos hipócritas; ali haverá choro e ranger de dentes.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28362, 1099, 1, '“Então, o Reino dos Céus será semelhante a dez virgens, que saíram com suas lâmpadas ao encontro do esposo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28363, 1099, 2, 'Cinco dentre elas eram tolas e cinco, prudentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28364, 1099, 3, 'Tomando suas lâmpadas, as tolas não levaram óleo consigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28365, 1099, 4, 'As prudentes, todavia, levaram de reserva vasos de óleo junto com as lâmpadas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28366, 1099, 5, 'Tardando o esposo, cochilaram todas e adormeceram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28367, 1099, 6, 'No meio da noite, porém, ouviu-se um clamor: Eis o esposo, ide-lhe ao encontro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28368, 1099, 7, 'E as virgens levantaram-se todas e prepararam suas lâmpadas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28369, 1099, 8, 'As tolas disseram às prudentes: Dai-nos de vosso óleo, porque nossas lâmpadas se estão apagando.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28370, 1099, 9, 'As prudentes responderam: Não temos o suficiente para nós e para vós; é preferível irdes aos vendedores, a fim de o comprar para vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28371, 1099, 10, 'Ora, enquanto foram comprar, veio o esposo. As que estavam preparadas entraram com ele para a sala das bodas e foi fechada a porta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28372, 1099, 11, 'Mais tarde, chegaram também as outras e diziam: Se­nhor, senhor, abre-nos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28373, 1099, 12, 'Mas ele respondeu: Em verdade vos digo: não vos conheço!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28374, 1099, 13, 'Vigiai, pois, porque não sabeis nem o dia nem a hora.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28375, 1099, 14, '“Será também como um ho­mem que, tendo de viajar, reuniu seus servos e lhes confiou seus bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28376, 1099, 15, 'A um deu cinco talentos; a outro, dois; e a outro, um, segundo a capacidade de cada um. Depois partiu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28377, 1099, 16, 'Logo em seguida, o que recebeu cinco talentos negociou com eles; fê-los produzir, e ganhou outros cinco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28378, 1099, 17, 'Do mesmo modo, o que recebeu dois, ganhou outros dois.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28379, 1099, 18, 'Mas, o que recebeu apenas um, foi cavar a terra e escondeu o dinheiro de seu senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28380, 1099, 19, 'Muito tempo depois, o senhor daqueles servos voltou e pediu-lhes contas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28381, 1099, 20, 'O que recebeu cinco talen­tos aproximou-se e apresentou outros cinco: ‘Senhor’ – disse-lhe –, ‘confiaste-me cinco talentos; eis aqui outros cinco que ganhei’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28382, 1099, 21, 'Disse-lhe seu senhor: ‘Muito bem, servo bom e fiel; já que foste fiel no pouco, eu te confiarei muito. Vem regozijar-te com teu senhor’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28383, 1099, 22, 'O que recebeu dois talentos adiantou-se também e disse: ‘Se­nhor, confias­te-me dois talentos; eis aqui os dois outros que lucrei’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28384, 1099, 23, 'Disse-lhe seu senhor: ‘Muito bem, servo bom e fiel; já que foste fiel no pouco, eu te confiarei muito. Vem regozijar-te com teu senhor’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28385, 1099, 24, 'Veio, por fim, o que recebeu só um talento: ‘Senhor, disse-lhe, sabia que és um homem duro, que colhes onde não semeaste e recolhes onde não espalhaste.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28386, 1099, 25, 'Por isso, tive medo e fui esconder teu talento na terra. Eis aqui, toma o que te pertence’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28387, 1099, 26, 'Respondeu-lhe seu senhor: ‘Servo mau e preguiçoso! Sabias que colho onde não semeei e que recolho onde não espalhei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28388, 1099, 27, 'Devias, pois, levar meu dinheiro ao banco e, à minha volta, eu receberia com os juros o que é meu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28389, 1099, 28, 'Tirai-lhe este talento e dai-o ao que tem dez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28390, 1099, 29, 'Será dado ao que tem e terá em abundância. Mas ao que não tem será tirado mesmo aquilo que julga ter.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28391, 1099, 30, 'E a esse servo inútil, jogai-o nas trevas exteriores; ali haverá choro e ranger de dentes’.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28392, 1099, 31, '“Quando o Filho do Homem voltar na sua glória e todos os anjos com ele, se sentará no seu trono glorioso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28393, 1099, 32, 'Todas as nações se reu­nirão diante dele e ele separará uns dos outros, como o pastor separa as ovelhas dos cabritos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28394, 1099, 33, 'Colocará as ovelhas à sua direita e os cabritos à sua esquerda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28395, 1099, 34, 'Então, o Rei dirá aos que estão à direita: ‘Vinde, benditos de meu Pai, tomai posse do Reino que vos está preparado desde a criação do mundo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28396, 1099, 35, 'porque tive fome e me destes de comer; tive sede e me destes de beber; era pere­grino e me acolhestes;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28397, 1099, 36, 'nu e me vestistes; enfermo e me visitastes; estava na prisão e viestes a mim’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28398, 1099, 37, 'Os justos lhe perguntarão: ‘Senhor, quando foi que te vimos com fome e te demos de comer, com sede e te demos de beber?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28399, 1099, 38, 'Quando foi que te vimos peregrino e te acolhemos, nu e te vestimos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28400, 1099, 39, 'Quando foi que te vimos enfermo ou na prisão e te fomos visitar?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28401, 1099, 40, 'Responderá o Rei: ‘Em verdade eu vos declaro: todas as vezes que fizestes isso a um destes meus irmãos mais pequeninos, foi a mim mesmo que o fizestes.’');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28402, 1099, 41, 'Ele se voltará em seguida para os da sua esquerda e lhes dirá: ‘Retirai-vos de mim, malditos! Ide para o fogo eterno destinado ao demônio e aos seus anjos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28403, 1099, 42, 'Porque tive fome e não me destes de comer; tive sede e não me destes de beber;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28404, 1099, 43, 'era peregrino e não me acolhes­tes; nu e não me vestistes; enfermo e na prisão e não me visitastes’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28405, 1099, 44, 'Também estes lhe perguntarão: ‘Senhor, quando foi que te vimos com fome, com sede, peregrino, nu, enfermo, ou na prisão e não te socorremos?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28406, 1099, 45, 'E ele responderá: ‘Em verdade eu vos declaro: todas as vezes que deixastes de fazer isso a um destes peque­ninos, foi a mim que o dei­xastes de fazer’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28407, 1099, 46, '“E estes irão para o castigo eterno, e os justos, para a vida eterna.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28408, 1100, 1, 'Quando Jesus acabou todos esses discursos, disse a seus discípulos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28409, 1100, 2, '“Sabeis que daqui a dois dias será a Páscoa, e o Filho do Homem será traído para ser crucificado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28410, 1100, 3, 'Então, os príncipes dos sacer­dotes e os anciãos do povo reuniram-se no pátio do sumo sacerdote, chamado Caifás,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28411, 1100, 4, 'e deliberaram sobre os meios de prender Jesus por astúcia e de o matar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28412, 1100, 5, 'E dizi­am: “Sobretudo, não seja durante a festa. Poderá haver um tumulto entre o povo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28413, 1100, 6, 'Encontrava-se Jesus em Betânia, na casa de Simão, o leproso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28414, 1100, 7, 'Estando à mesa, aproximou-se dele uma mulher com um vaso de alabastro, cheio de perfume muito caro, e derramou-o na sua cabeça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28415, 1100, 8, 'Vendo isso, os discípulos disseram indignados: “Para que este desperdício?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28416, 1100, 9, 'Poderia vender este perfume por um bom preço e dar o dinheiro aos pobres”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28417, 1100, 10, 'Jesus ouviu-os e disse-lhes: “Por que molestais esta mulher? É uma ação boa o que ela me fez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28418, 1100, 11, 'Pobres vós tereis sempre convosco. A mim, porém, nem sempre me tereis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28419, 1100, 12, 'Derramando esse perfume em meu corpo, ela o fez em vista da minha sepultura.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28420, 1100, 13, 'Em verdade eu vos digo: em toda parte onde for pregado este Evangelho pelo mundo inteiro, será contado em sua memória o que ela fez”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28421, 1100, 14, 'Então, um dos Doze, chamado Judas Iscariotes, foi ter com os príncipes dos sacerdotes e perguntou-lhes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28422, 1100, 15, '“Que quereis dar-me e eu vo-lo entregarei”. Ajustaram com ele trinta moedas de prata.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28423, 1100, 16, 'E desde aquele instante, procurava uma ocasião favorável para entregar Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28424, 1100, 17, 'No primeiro dia dos ázimos, os discípulos aproximaram-se de Jesus e perguntaram-lhe: “Onde queres que preparemos a ceia pascal?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28425, 1100, 18, 'Respondeu-lhes Jesus: “Ide à cidade, à casa de um tal, e dizei-lhe: O Mestre manda dizer-te: Meu tempo está próximo. É em tua casa que celebrarei a Páscoa com meus discípulos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28426, 1100, 19, 'Os discípulos fizeram o que Jesus tinha ordenado e prepararam a Páscoa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28427, 1100, 20, 'Ao declinar da tarde, pôs-se Jesus à mesa com os doze discípulos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28428, 1100, 21, 'Durante a ceia, disse: “Em verdade vos digo: um de vós me há de trair”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28429, 1100, 22, 'Com profunda aflição, cada um começou a perguntar: “Sou eu, Senhor?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28430, 1100, 23, 'Respondeu ele: “Aquele que pôs comigo a mão no prato, esse me trairá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28431, 1100, 24, 'O Filho do Homem vai, como dele está escrito. Mas ai daquele homem por quem o Filho do Homem é traído! Seria melhor para esse homem que jamais tivesse nascido!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28432, 1100, 25, 'Judas, o traidor, tomou a palavra e perguntou: “Mestre, serei eu?”. “Sim” – disse Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28433, 1100, 26, 'Durante a refeição, Jesus tomou o pão, benzeu-o, partiu-o e o deu aos discípulos, dizendo: “Tomai e comei, isto é meu corpo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28434, 1100, 27, 'Tomou depois o cálice, rendeu graças e deu-lho, dizendo: “Bebei dele todos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28435, 1100, 28, 'porque isto é meu sangue, o sangue da Nova Aliança, derramado por muitos homens em remissão dos pecados.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28436, 1100, 29, 'Digo-vos: doravante não beberei mais desse fruto da vinha até o dia em que o beberei de novo convosco no Reino de meu Pai”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28437, 1100, 30, 'Depois do canto dos Salmos, dirigiram-se eles para o monte das Oliveiras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28438, 1100, 31, 'Disse-lhes então Jesus: “Esta noite serei para todos vós uma ocasião de queda; porque está escrito: Ferirei o pastor, e as ovelhas do rebanho serão dispersadas (Zc 13,7).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28439, 1100, 32, 'Mas, depois da minha Ressurreição, eu vos precederei na Galileia”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28440, 1100, 33, 'Pedro interveio: “Mesmo que sejas para todos uma ocasião de queda, para mim jamais o serás”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28441, 1100, 34, 'Disse-lhe Jesus: “Em verdade te digo: nesta noite mesma, antes que o galo cante, três vezes me negarás”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28442, 1100, 35, 'Respondeu-lhe Pedro: “Mesmo que seja necessário morrer contigo, jamais te negarei!”. E todos os outros discípulos diziam-lhe o mesmo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28443, 1100, 36, 'Retirou-se Jesus com eles para um lugar chamado Getsêmani e disse-lhes: “Assentai-vos aqui, enquanto eu vou ali orar...*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28444, 1100, 37, 'E, tomando consigo Pedro e os dois filhos de Zebedeu, começou a entristecer-se e a angustiar-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28445, 1100, 38, 'Disse-lhes, então: “Minha alma está triste até a morte. Ficai aqui e vigiai comigo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28446, 1100, 39, 'Adiantou-se um pouco e, prostrando-se com a face por terra, assim rezou: “Meu Pai, se é possível, afasta de mim este cálice! Todavia, não se faça o que eu quero, mas sim o que tu queres”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28447, 1100, 40, 'Foi ter então com os discípulos e os encontrou dormindo. E disse a Pedro: “Então, não pudestes vigiar uma hora comigo...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28448, 1100, 41, 'Vigiai e orai para que não entreis em tentação. O espírito está pronto, mas a carne é fraca”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28449, 1100, 42, 'Afastou-se pela segunda vez e orou, dizendo: “Meu Pai, se não é possível que este cálice passe sem que eu o beba, faça-se a tua vontade!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28450, 1100, 43, 'Voltou ainda e os encontrou novamente dormindo, porque seus olhos estavam pesados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28451, 1100, 44, 'Deixou-os e foi orar pela terceira vez, dizendo as mesmas palavras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28452, 1100, 45, 'Voltou, então, para os seus discípulos e disse-lhes: “Dormi agora e repousai! Chegou a hora: o Filho do Homem vai ser entregue nas mãos dos pecadores...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28453, 1100, 46, 'Levantai-vos, vamos! Aquele que me trai está perto daqui”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28454, 1100, 47, 'Jesus ainda falava, quando veio Judas, um dos Doze, e com ele uma multidão de gente armada de espadas e cacetes, enviada pelos príncipes dos sacerdotes e pelos anciãos do povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28455, 1100, 48, 'O traidor combinara com eles este sinal: “Aquele que eu beijar, é ele. Prendei-o!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28456, 1100, 49, 'Aproximou-se imediatamente de Jesus e disse: “Salve, Mestre”. E beijou-o.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28457, 1100, 50, 'Disse-lhe Jesus: “É, então, para isso que vens aqui?”. Em seguida, adiantaram-se eles e lançaram mão em Jesus para prendê-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28458, 1100, 51, 'Mas um dos companheiros de Jesus desembainhou a espada e feriu um servo do sumo sacerdote, decepando-lhe a orelha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28459, 1100, 52, 'Jesus, no entanto, lhe dis­se: “Embainha tua espada, porque todos aqueles que usarem da espada, pela espada morrerão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28460, 1100, 53, 'Crês tu que não posso invocar meu Pai e ele não me enviaria imediatamente mais de doze legiões de anjos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28461, 1100, 54, 'Mas como se cumpririam então as Escrituras, segundo as quais é preciso que seja assim?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28462, 1100, 55, 'Depois, voltando-se para a turba, falou: “Saístes armados de espadas e porretes para prender-me, como se eu fosse um malfeitor. Entretanto, todos os dias estava eu sentado entre vós ensinando no templo e não me prendestes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28463, 1100, 56, 'Mas tudo isto aconteceu porque era necessário que se cumprissem os oráculos dos profetas”. Então, os discípulos o abandonaram e fugiram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28464, 1100, 57, 'Os que haviam prendido Jesus levaram-no à casa do sumo sacerdote Caifás, onde estavam reu­nidos os escribas e os anciãos do povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28465, 1100, 58, 'Pedro seguia-o de longe, até o pátio do sumo sacerdote. Entrou e sentou-se junto aos criados para ver como terminaria aquilo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28466, 1100, 59, 'Enquanto isso, os príncipes dos sacerdotes e todo o conselho procuravam um falso testemunho contra Jesus, a fim de o levarem à morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28467, 1100, 60, 'Mas não o conseguiram, embora se apresentassem muitas falsas testemunhas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28468, 1100, 61, 'Por fim, apresentaram-se duas testemunhas, que disseram: “Este homem disse: Posso destruir o Templo de Deus e reedificá-lo em três dias”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28469, 1100, 62, 'Levantou-se o sumo sacerdote e lhe perguntou: “Nada tens a responder ao que essa gente depõe contra ti?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28470, 1100, 63, 'Jesus, no entanto, permanecia calado. Disse-lhe o sumo sacerdote: “Por Deus vivo, conjuro-te que nos digas se és o Cristo, o Filho de Deus?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28471, 1100, 64, 'Jesus respondeu: “Sim. Além disso, eu vos declaro que vereis doravante o Filho do Homem sentar-se à direita do Todo-poderoso, e voltar sobre as nuvens do céu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28472, 1100, 65, 'A essas palavras, o sumo sacerdote rasgou suas vestes, exclamando: “Que necessidade temos ainda de testemunhas? Acabastes de ouvir a blasfêmia!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28473, 1100, 66, 'Qual o vosso parecer?”. Eles responderam: “Merece a morte!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28474, 1100, 67, 'Cuspiram-lhe então na face, bateram-lhe com os punhos e deram-lhe tapas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28475, 1100, 68, 'dizendo: “Adi­vinha, ó Cristo: quem te bateu?”. (= Mc 14,66-72 = Lc 22,55-62 = Jo 18,15-27)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28476, 1100, 69, 'Enquanto isso, Pedro estava sentado no pátio. Aproximou-se dele uma das servas, dizendo: “Também tu estavas com Jesus, o Galileu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28477, 1100, 70, 'Mas ele negou publicamente, nestes termos: “Não sei o que dizes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28478, 1100, 71, 'Dirigia-se ele para a porta, a fim de sair, quando outra criada o viu e disse aos que lá estavam: “Este homem também estava com Jesus de Nazaré”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28479, 1100, 72, 'Pedro, pela segunda vez, negou com juramento: “Eu nem conheço tal homem”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28480, 1100, 73, 'Pouco depois, os que ali estavam aproximaram-se de Pedro e disseram: “Sim, tu és daqueles; teu modo de falar te dá a conhecer”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28481, 1100, 74, 'Pedro, então, começou a fazer imprecações, jurando que nem sequer conhecia tal homem. E, neste momento, cantou o galo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28482, 1100, 75, 'Pedro recordou-se do que Jesus lhe dissera: “Antes que o galo cante, tu me negarás três vezes”. E saindo, chorou amargamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28483, 1101, 1, 'Chegando a manhã, todos os príncipes dos sacerdotes e os anciãos do povo reuniram-se em conselho para entregar Jesus à morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28484, 1101, 2, 'Ligaram-no e o levaram ao governador Pilatos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28485, 1101, 3, 'Judas, o traidor, vendo-o então condenado, toma­do de remorsos, foi devolver aos príncipes dos sacerdotes e aos anciãos as trinta moedas de prata,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28486, 1101, 4, 'dizendo-lhes: “Pequei, entregando o sangue de um justo”. Responderam-lhe: “Que nos importa? Isto é lá contigo!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28487, 1101, 5, 'Ele jogou então no templo as moe­das de prata, saiu e foi enforcar-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28488, 1101, 6, 'Os príncipes dos sacerdotes tomaram o dinheiro e disseram: “Não é permitido lançá-lo no tesouro sagrado, porque se trata de preço de sangue”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28489, 1101, 7, 'Depois de haverem deliberado, compraram com aquela soma o campo do Oleiro, para que ali se fizesse um cemitério de estrangeiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28490, 1101, 8, 'Essa é a razão por que aquele terreno é chamado, ainda hoje, “Campo de Sangue”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28491, 1101, 9, 'Assim se cumpriu a profecia do profeta Jere­mias: Eles receberam trinta moedas de prata, preço daquele cujo valor foi estimado pelos filhos de Israel;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28492, 1101, 10, 'e deram-no pelo campo do Oleiro, como o Senhor me havia prescrito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28493, 1101, 11, 'Jesus compareceu diante do governador, que o interrogou: “És o rei dos judeus?”. “Sim” –, respondeu-lhe Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28494, 1101, 12, 'Ele, porém, nada respondia às acusações dos príncipes dos sacerdotes e dos anciãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28495, 1101, 13, 'Perguntou-lhe Pilatos: “Não ouves todos os testemunhos que levantam contra ti?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28496, 1101, 14, 'Mas, para grande admiração do governador, não quis responder a nenhuma acusação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28497, 1101, 15, 'Era costume que o governador soltasse um preso a pedido do povo em cada festa de Páscoa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28498, 1101, 16, 'Ora, havia naquela ocasião um prisio­neiro famoso, chamado Barra­bás.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28499, 1101, 17, 'Pilatos dirigiu-se ao povo reunido: “Qual quereis que eu vos solte: Barrabás ou Jesus, que se chama Cristo?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28500, 1101, 18, '(Ele sabia que tinham entregue Jesus por inveja.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28501, 1101, 19, 'Enquanto estava sentado no tribunal, sua mulher lhe mandou dizer: “Nada faças a esse justo. Fui hoje atormentada por um sonho que lhe diz respeito”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28502, 1101, 20, 'Mas os príncipes dos sacerdotes e os anciãos persuadiram o povo que pedisse a libertação de Barrabás e fizesse morrer Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28503, 1101, 21, 'O governador tomou então a palavra: “Qual dos dois quereis que eu vos solte?”. Res­ponderam: “Barrabás!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28504, 1101, 22, 'Pilatos perguntou: “Que farei então de Jesus, que é chamado o Cristo?”. Todos responderam: “Seja crucificado!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28505, 1101, 23, 'O governador tornou a perguntar: “Mas que mal fez ele?”. E gritavam ainda mais forte: “Seja crucificado!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28506, 1101, 24, 'Pilatos viu que nada adiantava, mas que, ao contrário, o tumulto crescia. Fez com que lhe trouxessem água, lavou as mãos diante do povo e disse: “Sou inocente do sangue deste homem. Isto é lá convosco!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28507, 1101, 25, 'E todo o povo respondeu: “Caia sobre nós o seu sangue e sobre nossos filhos!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28508, 1101, 26, 'Libertou então Barrabás, mandou açoitar Jesus e lho entregou para ser crucificado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28509, 1101, 27, 'Os soldados do governador conduziram Jesus para o pretório e rodearam-no com todo o pelotão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28510, 1101, 28, 'Arrancaram-lhe as vestes e colocaram-lhe um manto escarlate.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28511, 1101, 29, 'Depois, trançaram uma coroa de espinhos, meteram-lha na cabeça e puseram-lhe na mão uma vara. Dobrando os joelhos diante dele, diziam com escárnio: “Salve, rei dos judeus!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28512, 1101, 30, 'Cuspiam-lhe no rosto e, tomando da vara, davam-lhe golpes na cabeça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28513, 1101, 31, 'Depois de escarnecerem dele, tiraram-lhe o manto e entregaram-lhe as vestes. Em seguida, levaram-no para o crucificar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28514, 1101, 32, 'Saindo, encontraram um ho­mem de Cirene, chamado Simão, a quem obrigaram a levar a cruz de Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28515, 1101, 33, 'Chegaram ao lugar chamado Gólgota, isto é, lugar do crânio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28516, 1101, 34, 'Deram-lhe de beber vinho misturado com fel. Ele provou, mas se recusou a beber.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28517, 1101, 35, 'Depois de o haverem crucificado, dividiram suas vestes entre si, tirando à sorte. Cumpriu-se assim a profecia do profeta: Repartiram entre si minhas vestes e sobre meu manto lançaram à sorte (Sl 21,19).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28518, 1101, 36, 'Sentaram-se e montaram guarda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28519, 1101, 37, 'Por cima de sua cabeça penduraram um escrito trazendo o motivo de sua crucificação: “Este é Jesus, o rei dos judeus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28520, 1101, 38, 'Ao mesmo tempo foram crucificados com ele dois ladrões, um à sua direita e outro à sua esquerda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28521, 1101, 39, 'Os que passavam o injuriavam, sacudiam a cabeça e diziam:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28522, 1101, 40, '“Tu, que destróis o templo e o reconstróis em três dias, salva-te a ti mesmo! Se és o Filho de Deus, desce da cruz!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28523, 1101, 41, 'Os príncipes dos sacerdotes, os escribas e os anciãos também zombavam dele:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28524, 1101, 42, '“Ele salvou a outros e não pode salvar-se a si mesmo! Se é rei de Israel, desça agora da cruz e nós creremos nele!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28525, 1101, 43, 'Confiou em Deus, Deus o livre agora, se o ama, porque ele disse: Eu sou o Filho de Deus!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28526, 1101, 44, 'E os ladrões, crucificados com ele, também o ultrajavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28527, 1101, 45, 'Desde a hora sexta até a nona, cobriu-se toda a terra de trevas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28528, 1101, 46, 'Próximo da hora nona, Jesus exclamou em voz forte: “Eli, Eli, lammá sabactáni?” – o que quer dizer: “Meu Deus, meu Deus, por que me abandonaste?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28529, 1101, 47, 'A essas palavras, alguns dos que lá estavam diziam: “Ele chama por Elias”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28530, 1101, 48, 'Imedia­tamente, um deles tomou uma esponja, embebeu-a em vinagre e apresentou-lha na ponta de uma vara para que bebesse.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28531, 1101, 49, 'Os outros diziam: “Deixa! Vejamos se Elias virá socorrê-lo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28532, 1101, 50, 'Jesus de novo lançou um grande brado, e entregou a alma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28533, 1101, 51, 'E eis que o véu do templo se rasgou em duas partes de alto a baixo, a terra tremeu, fenderam-se as rochas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28534, 1101, 52, 'Os sepulcros se abriram e os corpos de muitos justos ressuscitaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28535, 1101, 53, 'Saindo de suas sepulturas, entraram na cidade santa depois da ressurreição de Jesus e apareceram a muitas pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28536, 1101, 54, 'O centurião e seus homens que montavam guarda a Jesus, dian­te do estremecimento da terra e de tudo o que se passava, disseram entre si, possuídos de grande temor: “Verdadeiramente, este homem era Filho de Deus!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28537, 1101, 55, 'Havia ali também algumas mulheres que de longe olha­vam; tinham seguido Jesus desde a Galileia para o servir.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28538, 1101, 56, 'Entre elas se achavam Maria Madalena e Maria, mãe de Tiago e de José, e a mãe dos filhos de Zebedeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28539, 1101, 57, 'À tardinha, um homem rico de Arimateia, chamado José, que era também discípulo de Jesus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28540, 1101, 58, 'foi procurar Pilatos e pediu-lhe o corpo de Jesus. Pilatos cedeu-o.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28541, 1101, 59, 'José tomou o corpo, envolveu-o num lençol branco');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28542, 1101, 60, 'e o depositou num sepulcro novo, que tinha mandado talhar para si na rocha. Depois rolou uma grande pedra à entrada do sepulcro e foi-se embora.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28543, 1101, 61, 'Maria Madalena e a outra Maria ficaram lá, sentadas defronte do túmulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28544, 1101, 62, 'No dia seguinte, isto é, o dia seguinte ao da Preparação, os prínci­pes dos sacerdotes e os fariseus dirigiram-se todos juntos à casa de Pilatos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28545, 1101, 63, 'E disseram-lhe: “Senhor, nós nos lembramos de que aquele impostor disse, enquanto vivia: Depois de três dias ressuscitarei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28546, 1101, 64, 'Ordena, pois, que seu sepulcro seja guardado até o terceiro dia. Os seus discípulos poderiam vir roubar o corpo e dizer ao povo: Ressuscitou dos mortos. E esta última impostura seria pior que a primeira”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28547, 1101, 65, 'Respondeu Pilatos: “Tendes uma guarda. Ide e guardai-o como o entendeis”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28548, 1101, 66, 'Foram, pois, e asseguraram o sepulcro, selando a pedra e colocando guardas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28549, 1102, 1, 'Depois do sábado, quando amanhecia o primeiro dia da semana, Maria Madalena e a ou­tra Maria foram ver o túmulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28550, 1102, 2, 'E eis que houve um violento tremor de terra: um anjo do Senhor desceu do céu, rolou a pedra e sentou-se sobre ela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28551, 1102, 3, 'Resplandecia como relâmpago e suas vestes eram brancas como a neve.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28552, 1102, 4, 'Vendo isso, os guardas pensaram que morre­riam de pavor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28553, 1102, 5, 'Mas o anjo disse às mulheres: “Não temais! Sei que procurais Jesus, que foi crucificado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28554, 1102, 6, 'Não está aqui: ressuscitou como disse. Vinde e vede o lugar em que ele repousou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28555, 1102, 7, 'Ide depressa e dizei aos discípulos que ele ressuscitou dos mortos. Ele vos precede na Galileia. Lá o haveis de rever, eu vo-lo disse”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28556, 1102, 8, 'Elas se afastaram prontamente do túmulo com certo receio, mas ao mesmo tempo com alegria, e correram a dar a Boa-Nova aos discípulos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28557, 1102, 9, 'Nesse momento, Jesus apresentou-se diante delas e disse-lhes: “Salve!”. Aproximaram-se elas e, prostradas diante dele, beijaram-lhe os pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28558, 1102, 10, 'Disse-lhes Jesus: “Não temais! Ide dizer aos meus irmãos que se dirijam à Galileia, pois é lá que eles me verão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28559, 1102, 11, 'Enquanto elas voltavam, alguns homens da guarda já estavam na cidade para anunciar o acontecimento aos príncipes dos sacerdotes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28560, 1102, 12, 'Reuniram-se estes em conselho com os anciãos. Deram aos soldados uma importante soma de dinheiro, ordenando-lhes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28561, 1102, 13, '“Vós direis que seus discípulos vieram retirá-lo à noite, enquanto dormíeis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28562, 1102, 14, 'Se o governador vier a sabê-lo, nós o acalmaremos e vos tiraremos de dificuldades”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28563, 1102, 15, 'Os soldados receberam o dinheiro e seguiram suas instruções. E essa versão é ainda hoje espalhada entre os judeus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28564, 1102, 16, 'Os onze discípulos foram para a Galileia, para a montanha que Jesus lhes tinha designado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28565, 1102, 17, 'Quando o viram, adoraram-no; entretanto, alguns hesitavam ain­da.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28566, 1102, 18, 'Mas Jesus, aproximando-se, lhes disse: “Toda autoridade me foi dada no céu e na terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28567, 1102, 19, 'Ide, pois, e ensinai a todas as nações; batizai-as em nome do Pai, do Filho e do Espírito Santo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28568, 1102, 20, 'Ensinai-as a observar tudo o que vos prescrevi. Eis que estou convosco todos os dias, até o fim do mundo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28569, 1103, 1, 'Princípio da Boa-Nova de Jesus Cristo, Filho de Deus. Conforme está escrito no profeta Isaías:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28570, 1103, 2, 'Eis que envio o meu anjo diante de ti: ele preparará o teu caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28571, 1103, 3, 'Uma voz clama no deserto: Traçai o caminho do Senhor, aplanai as suas veredas (Ml 3,1; Is 40,3).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28572, 1103, 4, 'João Batista apareceu no deserto e pregava um batismo de conversão para a remissão dos pecados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28573, 1103, 5, 'E saíam para ir ter com ele toda a Judeia, toda Jerusalém, e eram batizados por ele no rio Jordão, confessando os seus pecados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28574, 1103, 6, 'João andava vestido de pêlo de camelo e trazia um cinto de couro em volta dos rins, e alimentava-se de gafanhotos e mel silvestre.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28575, 1103, 7, 'Ele pôs-se a proclamar: “Depois de mim vem outro mais poderoso do que eu, ante o qual não sou digno de me prostrar para desatar-lhe a correia do calçado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28576, 1103, 8, 'Eu vos batizei com água; ele, porém, vos batizará no Espírito Santo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28577, 1103, 9, 'Ora, naqueles dias veio Jesus de Nazaré, da Galileia, e foi batizado por João, no Jordão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28578, 1103, 10, 'No momento em que Jesus saía da água, João viu os céus abertos e descer o Espírito em forma de pomba sobre ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28579, 1103, 11, 'E ouviu-se dos céus uma voz: “Tu és o meu Filho muito amado; em ti ponho minha afeição”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28580, 1103, 12, 'E logo o Espírito o impeliu para o deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28581, 1103, 13, 'Aí esteve quarenta dias. Foi tentado pelo demônio e esteve em companhia dos animais selvagens. E os anjos o serviam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28582, 1103, 14, 'Depois que João foi preso, Jesus dirigiu-se para a Galileia. Pregava o Evangelho de Deus, e dizia:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28583, 1103, 15, '“Completou-se o tempo e o Reino de Deus está próximo; fazei penitência e crede no Evangelho”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28584, 1103, 16, 'Passando ao longo do mar da Galileia, viu Simão e André, seu irmão, que lançavam as redes ao mar, pois eram pescadores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28585, 1103, 17, 'Jesus disse-lhes: “Vinde após mim; eu vos farei pescadores de homens”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28586, 1103, 18, 'Eles, no mesmo instante, deixaram as redes e seguiram-no.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28587, 1103, 19, 'Uns poucos passos mais adiante, viu Tiago, filho de Zebedeu, e João, seu irmão, que estavam numa barca, consertando as redes. E chamou-os logo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28588, 1103, 20, 'Eles deixaram na barca seu pai Zebedeu com os empregados e o seguiram. (= Lc 4,31-44)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28589, 1103, 21, 'Dirigiram-se para Cafarnaum. E já no dia de sábado, Jesus entrou na sinagoga e pôs-se a ensinar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28590, 1103, 22, 'Maravilhavam-se da sua doutrina, porque os ensinava como quem tem autoridade e não como os escribas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28591, 1103, 23, 'Ora, na sinagoga deles achava-se um homem possesso de um espírito imundo, que gritou:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28592, 1103, 24, '“Que tens tu conosco, Jesus de Naza­ré? Vieste perder-nos? Sei quem és: o Santo de Deus!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28593, 1103, 25, 'Mas Jesus intimou-o, dizendo: “Cala-te, sai deste homem!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28594, 1103, 26, 'O espírito imundo agitou-o violentamente e, dando um grande grito, saiu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28595, 1103, 27, 'Ficaram todos tão admirados, que perguntavam uns aos outros: “Que é isto? Eis um ensinamento novo, e feito com autoridade; além disso, ele manda até nos espíritos imun­dos e lhe obedecem!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28596, 1103, 28, 'A sua fama divulgou-se logo por todos os arredores da Galileia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28597, 1103, 29, 'Assim que saíram da sinagoga, dirigiram-se com Tiago e João à casa de Simão e André.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28598, 1103, 30, 'A sogra de Simão estava de cama, com febre; e, sem tardar, falaram-lhe a respeito dela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28599, 1103, 31, 'Aproximando-se ele, tomou-a pela mão e levantou-a; imediatamente a febre a deixou e ela pôs-se a servi-los.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28600, 1103, 32, 'À tarde, depois do pôr do sol, levaram-lhe todos os enfermos e possessos do demônio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28601, 1103, 33, 'Toda a cidade estava reunida diante da porta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28602, 1103, 34, 'Ele curou muitos que estavam oprimidos de diversas doenças, e expulsou muitos demônios. Não lhes permitia falar, porque o conheciam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28603, 1103, 35, 'De manhã, tendo-se levantado muito antes do amanhecer, ele saiu e foi para um lugar deserto, e ali se pôs em oração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28604, 1103, 36, 'Simão e os seus companheiros saíram a procurá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28605, 1103, 37, 'Encontraram-no e disseram-lhe: “Todos te procuram”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28606, 1103, 38, 'E ele respondeu-lhes: “Vamos às aldeias vizinhas, para que eu pregue também lá, pois, para isso é que vim”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28607, 1103, 39, 'Ele retirou-se dali, pregando em todas as sinagogas e por toda a Galileia, e expulsando os demônios. (= Mt 8,2-4 = Lc 5,12-16)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28608, 1103, 40, 'Aproximou-se dele um lepro­so, suplicando-lhe de joelhos: “Se queres, podes limpar-me”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28609, 1103, 41, 'Jesus compadeceu-se dele, estendeu a mão, tocou-o e lhe disse: “Eu quero, sê curado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28610, 1103, 42, 'E imediatamente desapareceu dele a lepra e foi purificado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28611, 1103, 43, 'Jesus o despediu em seguida, com esta severa admoestação:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28612, 1103, 44, '“Vê que não o digas a ninguém; mas vai, mostra-te ao sacerdote e apresenta, pela tua purificação, a oferenda prescrita por Moisés para lhe servir de testemunho”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28613, 1103, 45, 'Este homem, porém, logo que se foi, começou a propagar e divulgar o acontecido, de modo que Jesus não podia entrar publicamente em uma cidade. Conservava-se fora, nos lugares despovoados; e de toda parte vinham ter com ele. (= Mt 9,1-8 = Lc 5,17-26)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28614, 1104, 1, 'Alguns dias depois, Jesus entrou novamente em Cafarnaum e souberam que ele estava em casa.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28615, 1104, 2, 'Reuniu-se uma tal multidão, que não podiam encontrar lugar nem mesmo junto à porta. E ele os ins­truía.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28616, 1104, 3, 'Trouxeram-lhe um paralítico, carregado por quatro homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28617, 1104, 4, 'Como não pudessem apresentar-lho por causa da multidão, descobriram o teto por cima do lugar onde Jesus se achava e, por uma abertura, desceram o leito em que jazia o paralítico.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28618, 1104, 5, 'Jesus, vendo-lhes a fé, disse ao paralítico: “Filho, perdoados te são os pecados”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28619, 1104, 6, 'Ora, estavam ali sentados alguns escribas, que diziam uns aos outros:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28620, 1104, 7, '“Como pode este homem falar assim? Ele blasfema. Quem pode per­doar pecados senão Deus?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28621, 1104, 8, 'Mas Jesus, penetrando logo com seu espírito nos seus íntimos pensamentos, disse-lhes: “Por que pensais isto nos vossos corações?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28622, 1104, 9, 'Que é mais fácil dizer ao paralítico: ‘Os pecados te são perdoados’ ou dizer: ‘Levanta-te, toma o teu leito e anda?’.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28623, 1104, 10, 'Ora, para que conheçais o poder concedido ao Filho do Homem sobre a terra (disse ao paralítico),');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28624, 1104, 11, 'eu te ordeno: levanta-te, toma o teu leito e vai para casa”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28625, 1104, 12, 'No mesmo instante, ele se levantou e, tomando o leito, foi-se embora à vista de todos. A multidão inteira encheu-se de profunda admiração e puseram-se a louvar a Deus, dizendo: “Nunca vimos coisa semelhante”. (= Mt 9,9-17 = Lc 5,27-39)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28626, 1104, 13, 'Jesus saiu de novo para perto do mar e toda a multidão foi ter com ele, e ele os ensinava.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28627, 1104, 14, 'Quando ia passando, viu Levi, filho de Alfeu, sentado no posto da arrecadação e disse-lhe: “Segue-me”. E Levi, levantando-se, seguiu-o.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28628, 1104, 15, 'Em seguida, pôs-se à mesa na sua casa e muitos cobradores de impostos e pecadores tomaram lugar com ele e seus discípulos; com efeito, eram numerosos os que o seguiam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28629, 1104, 16, 'Os escribas, do partido dos fariseus, vendo-o comer com as pessoas de má vida e publicanos, diziam aos seus discípulos: “Ele come com os publicanos e com gente de má vida?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28630, 1104, 17, 'Ouvindo-os, Jesus replicou: “Os sãos não precisam de médico, mas os enfermos; não vim chamar os jus­tos, mas os pecadores”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28631, 1104, 18, 'Ora, os discípulos de João e os fariseus jejuavam. Por isso, foram-lhe perguntar: “Por que jejuam os discípulos de João e os dos fariseus, mas os teus discípulos não jejuam?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28632, 1104, 19, 'Jesus respondeu-lhes: “Podem porventura jejuar os convidados das núpcias, enquanto está com eles o esposo? Enquanto têm consigo o esposo, não lhes é possível jejuar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28633, 1104, 20, 'Dias virão, porém, em que o esposo lhes será tirado, e então jejuarão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28634, 1104, 21, 'Ninguém prega retalho de pano novo em roupa velha; do contrário, o remendo arranca novo pedaço da veste usada e torna-se pior o rasgão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28635, 1104, 22, 'E ninguém põe vinho novo em odres velhos; se o fizer, o vinho os arrebentará e se perderá juntamente com os odres; mas para vinho novo, odres novos”.* (= Mt 12,1-8 = Lc 6,1-5)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28636, 1104, 23, 'Num dia de sábado, o Senhor caminhava pelos campos e seus discípulos, andando, começaram a colher espigas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28637, 1104, 24, 'Os fariseus observaram-lhe: “Vede! Por que fazem eles no sábado o que não é permitido?”. Jesus respondeu-lhes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28638, 1104, 25, '“Nunca lestes o que fez Davi, quando se achou em necessidade e teve fome, ele e os seus companheiros?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28639, 1104, 26, 'Ele entrou na casa de Deus, sendo Abiatar príncipe dos sacerdotes, e comeu os pães da proposição, dos quais só aos sacerdotes era permitido comer, e os deu aos seus compa­nheiros”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28640, 1104, 27, 'E dizia-lhes: “O sábado foi feito para o homem, e não o homem para o sábado;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28641, 1104, 28, 'e, para dizer tudo, o Filho do Homem é senhor também do sábado”. (= Mt 12,9-21 = Lc 6,6-11)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28642, 1105, 1, 'Noutra vez, entrou ele na sinagoga e achava-se ali um homem que tinha a mão seca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28643, 1105, 2, 'Ora, estavam-no observando se o curaria no dia de sábado, para o acusarem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28644, 1105, 3, 'Ele diz ao homem da mão seca: “Vem para o meio”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28645, 1105, 4, 'Então, lhes pergunta: “É permitido fazer o bem ou o mal no sábado? Salvar uma vida ou matar?”. Mas eles se calavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28646, 1105, 5, 'Então, lan­çando um olhar indignado sobre eles, e contristado com a dureza de seus corações, diz ao homem: “Estende tua mão!”. Ele estendeu-a e a mão foi curada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28647, 1105, 6, 'Saindo os fariseus dali, deliberaram logo com os herodianos como o haviam de prender.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28648, 1105, 7, 'Jesus retirou-se com os seus discípulos para o mar, e seguia-o uma grande multidão, vinda da Galileia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28649, 1105, 8, 'E da Judeia, de Jerusalém, da Idumeia, do além-Jordão e dos arredores de Tiro e de Sidônia veio a ele uma grande multidão, ao ouvir o que ele fazia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28650, 1105, 9, 'Ele ordenou a seus discípulos que lhe aprontassem uma barca, para que a multidão não o comprimisse.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28651, 1105, 10, 'Curou a muitos, de modo que todos os que padeciam de algum mal se arrojavam a ele para o tocar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28652, 1105, 11, 'Quando os espíritos imundos o viam, prostravam-se diante dele e gritavam: “Tu és o Filho de Deus!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28653, 1105, 12, 'Ele os proibia severamente que o dessem a conhecer. (= Mt 10,1-4 = Lc 6,12-16)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28654, 1105, 13, 'Depois, subiu ao monte e chamou os que ele quis. E foram a ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28655, 1105, 14, 'Designou doze dentre eles para ficar em sua companhia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28656, 1105, 15, 'Ele os enviaria a pregar, com o poder de expulsar os demônios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28657, 1105, 16, 'Escolheu estes doze: Simão, a quem pôs o nome de Pedro;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28658, 1105, 17, 'Tiago, filho de Zebedeu, e João, seu irmão, aos quais pôs o nome de Boanerges, que quer dizer Filhos do Trovão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28659, 1105, 18, 'Ele esco­lheu também André, Filipe, Bartolomeu, Mateus, Tomé, Tiago, filho de Alfeu; Tadeu, Simão, o Zelador;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28660, 1105, 19, 'e Judas Iscariotes, que o entregou. (Mt 12,22-32 = Lc 11,14-26)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28661, 1105, 20, 'Dirigiram-se em seguida a uma casa. Aí afluiu de novo tanta gente, que nem podiam tomar alimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28662, 1105, 21, 'Quando os seus o souberam, saí­ram para o reter; pois diziam: “Ele está fora de si”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28663, 1105, 22, 'Também os escribas, que haviam descido de Jerusalém, diziam: “Ele está possuído de Beelzebul: é pelo príncipe dos demônios que ele expele os demônios”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28664, 1105, 23, 'Mas, havendo-os convocado, dizia-lhes em parábolas: “Como pode Satanás expulsar a Satanás?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28665, 1105, 24, 'Pois, se um reino estiver dividido contra si mesmo, não pode durar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28666, 1105, 25, 'E se uma casa está dividida contra si mesma, tal casa não pode permanecer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28667, 1105, 26, 'E se Satanás se levanta contra si mesmo, está dividido e não poderá continuar, mas desaparecerá.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28668, 1105, 27, 'Ninguém pode entrar na casa do homem forte e roubar-lhe os bens, se antes não o prender; e então saqueará sua casa.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28669, 1105, 28, 'Em verdade vos digo: “Todos os pecados serão perdoados aos filhos dos homens, mesmo as suas blasfêmias;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28670, 1105, 29, 'mas todo o que tiver blasfemado contra o Espírito Santo jamais terá perdão, mas será culpado de um pecado eterno”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28671, 1105, 30, 'Jesus falava assim porque tinham dito: “Ele tem um espírito imundo”. (= Mt 12,46-50 = Lc 8,19ss)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28672, 1105, 31, 'Chegaram sua mãe e seus irmãos e, estando do lado de fora, mandaram chamá-lo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28673, 1105, 32, 'Ora, a multidão estava sentada ao redor dele; e disseram-lhe: “Tua mãe e teus irmãos estão aí fora e te procuram”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28674, 1105, 33, 'Ele respondeu-lhes: “Quem é minha mãe e quem são meus irmãos?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28675, 1105, 34, 'E, correndo o olhar sobre a multidão, que estava sentada ao redor dele, disse: “Eis aqui minha mãe e meus irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28676, 1105, 35, 'Aquele que faz a vontade de Deus, esse é meu irmão, minha irmã e minha mãe”. (= Mt 13,1-23 = Lc 8,4-15)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28677, 1106, 1, 'Jesus pôs-se novamente a ensinar, à beira do mar, e aglome­rou-se junto dele tão grande multidão, que ele teve de entrar numa barca, no mar, e toda a multidão ficou em terra na praia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28678, 1106, 2, 'E ensinava-lhes muitas coisas em parábolas. Dizia-lhes na sua doutrina:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28679, 1106, 3, '“Ouvi: Saiu o semeador a semear.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28680, 1106, 4, 'Enquanto lançava a semente, uma parte caiu à beira do caminho, e vieram as aves e a comeram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28681, 1106, 5, 'Outra parte caiu no pedregulho, onde não havia muita terra; o grão germinou logo, porque a terra não era profunda;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28682, 1106, 6, 'mas, assim que o sol despontou, queimou-se e, como não tivesse raiz, secou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28683, 1106, 7, 'Outra parte caiu entre os espinhos; estes cresceram, sufocaram-na e o grão não deu fruto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28684, 1106, 8, 'Outra caiu em terra boa e deu fruto, cresceu e desenvolveu-se; um grão rendeu trinta, outro sessenta e outro cem”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28685, 1106, 9, 'E dizia: “Quem tem ouvidos para ouvir, ouça!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28686, 1106, 10, 'Quando se acharam a sós, os que o cercavam e os Doze indagaram dele o sentido da parábola.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28687, 1106, 11, 'Ele disse-lhes: “A vós é revelado o mistério do Reino de Deus, mas aos que são de fora tudo se lhes propõe em parábolas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28688, 1106, 12, 'Desse modo, eles olham sem ver, escutam sem compreender, sem que se convertam e lhes seja perdoado”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28689, 1106, 13, 'E acrescentou: “Não entendeis essa parábola? Como entendereis então todas as outras?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28690, 1106, 14, 'O semea­dor semeia a palavra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28691, 1106, 15, 'Alguns se encontram à beira do caminho, onde ela é semeada; apenas a ouvem, vem Satanás tirar a palavra neles semeada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28692, 1106, 16, 'Outros recebem a semente em lugares pedregosos; quando a ouvem, recebem-na com alegria;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28693, 1106, 17, 'mas não têm raiz em si, são inconstantes, e assim que se levanta uma tribulação ou uma perseguição por causa da palavra, eles tropeçam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28694, 1106, 18, 'Outros ainda recebem a semente entre os espinhos; ouvem a palavra,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28695, 1106, 19, 'mas as preocupações mundanas, a ilusão das riquezas, as múltiplas cobiças sufocam-na e a tornam infrutífera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28696, 1106, 20, 'Aqueles que recebem a semente em terra boa escutam a palavra, acolhem-na e dão fruto, trinta, sessenta e cem por um”. Outras parábolas (= Lc 8,16ss)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28697, 1106, 21, 'Dizia-lhes ainda: “Traz-se porventura a candeia para ser colocada debaixo do alqueire ou debaixo da cama? Não é para ser posta no candeeiro?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28698, 1106, 22, 'Porque nada há oculto que não deva ser descoberto, nada secreto que não deva ser publicado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28699, 1106, 23, 'Se alguém tem ouvidos para ouvir, que ouça”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28700, 1106, 24, 'Ele prosseguiu: “Atendei ao que ouvis: com a medida com que medirdes, vos medirão a vós, e ain­da se vos acrescentará.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28701, 1106, 25, 'Pois, ao que tem, se lhe dará; e ao que não tem, se lhe tirará até o que tem”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28702, 1106, 26, 'Dizia também: “O Reino de Deus é como um homem que lança a semente à terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28703, 1106, 27, 'Dorme, levanta-se, de noite e de dia, e a semente brota e cresce, sem ele o perceber.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28704, 1106, 28, 'Pois a terra por si mesma produz, primeiro a planta, depois a espiga e, por último, o grão abundante na espiga.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28705, 1106, 29, 'Quando o fruto amadurece, ele mete-lhe a foice, porque é chegada a colheita”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28706, 1106, 30, 'Dizia ele: “A quem compararemos o Reino de Deus? Ou com que parábola o representaremos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28707, 1106, 31, 'É como o grão de mostarda que, quando é semeado, é a menor de todas as sementes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28708, 1106, 32, 'Mas, depois de semeado, cresce, torna-se maior que todas as hortaliças e estende de tal modo os seus ramos, que as aves do céu podem abrigar-se à sua sombra”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28709, 1106, 33, 'Era por meio de numerosas parábolas desse gênero que ele lhes anunciava a palavra, conforme eram capazes de compreender.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28710, 1106, 34, 'E não lhes falava, a não ser em parábolas; a sós, porém, explicava tudo a seus discípulos. (= Mt 8,23-27 = Lc 8,22-25)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28711, 1106, 35, 'À tarde daquele dia, disse-lhes: “Passemos para o outro lado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28712, 1106, 36, 'Deixando o povo, levaram-no consigo na barca, assim como ele estava. Outras embarcações o escoltavam.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28713, 1106, 37, 'Nisso, surgiu uma grande tormenta e lançava as ondas dentro da barca, de modo que ela já se enchia de água.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28714, 1106, 38, 'Jesus achava-se na popa, dormindo sobre um travesseiro. Eles acordaram-no e disseram-lhe: “Mestre, não te importa que pereçamos?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28715, 1106, 39, 'E ele, despertando, repreendeu o vento e disse ao mar: “Silêncio! Cala-te!”. E cessou o vento e seguiu-se grande bonança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28716, 1106, 40, 'Ele disse-lhes: “Como sois medrosos! Ainda não tendes fé?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28717, 1106, 41, 'Eles ficaram penetrados de grande temor e cochichavam entre si: “Quem é este, a quem até o vento e o mar obedecem?”. (= Mt 8,28-34 = Lc 8,26-39)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28718, 1107, 1, 'Passaram à outra margem do lago, ao território dos gerasenos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28719, 1107, 2, 'Assim que saíram da barca, um homem possesso do espírito imundo saiu do cemitério');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28720, 1107, 3, 'onde tinha seu refúgio e veio-lhe ao encontro. Não podiam atá-lo nem com cadeia, mesmo nos sepulcros,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28721, 1107, 4, 'pois tinha sido ligado muitas vezes com grilhões e cadeias, mas os despedaçara e ninguém o podia subjugar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28722, 1107, 5, 'Sempre, dia e noite, andava pelos sepulcros e nos montes, gritando e ferindo-se com pedras.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28723, 1107, 6, 'Vendo Jesus de longe, correu e prostrou-se diante dele, gritando em alta voz:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28724, 1107, 7, '“Que queres de mim, Jesus, Filho do Deus Altíssimo? Conjuro-te por Deus, que não me atormentes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28725, 1107, 8, 'É que Jesus lhe dizia: “Espírito imundo, sai deste homem!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28726, 1107, 9, 'Perguntou-lhe Jesus: “Qual é o teu nome?”. Respondeu-lhe: “Legião é o meu nome, porque somos muitos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28727, 1107, 10, 'E pediam-lhe com instância que não os lançasse fora daquela região.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28728, 1107, 11, 'Ora, uma grande manada de porcos andava pastando ali jun­to do monte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28729, 1107, 12, 'E os espíritos suplicavam-lhe: “Manda-nos para os porcos, para entrarmos neles”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28730, 1107, 13, 'Jesus lhos permitiu. Então, os espíritos imundos, tendo saído, entraram nos porcos; e a manada, de uns dois mil, precipitou-se no mar, afogando-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28731, 1107, 14, 'Fugiram os pastores e narraram o fato na cidade e pelos arredores. Então, saíram a ver o que tinha acontecido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28732, 1107, 15, 'Aproximaram-se de Jesus e viram o possesso assentado, coberto com seu manto e calmo, ele que tinha sido possuído pela Legião. E o pânico apoderou-se deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28733, 1107, 16, 'As testemunhas do fato contaram-lhes como havia acontecido isso ao endemoninhado, e o caso dos porcos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28734, 1107, 17, 'Começaram então a rogar-lhe que se retirasse da sua região.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28735, 1107, 18, 'Quando ele subia para a barca, veio o que tinha sido possesso e pediu-lhe permissão de acompa­nhá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28736, 1107, 19, 'Jesus não o admitiu, mas disse-lhe: “Vai para casa, para junto dos teus e anuncia-lhes tudo o que o Senhor fez por ti, e como se compadeceu de ti”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28737, 1107, 20, 'Foi-se ele e começou a publicar, na Decápole, tudo o que Jesus lhe havia feito. E todos se admiravam.* (Mt 9,18-26 = Lc 8,40-56)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28738, 1107, 21, 'Tendo Jesus navegado outra vez para a margem oposta, de novo afluiu a ele uma grande multidão. Ele se achava à beira do mar, quando');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28739, 1107, 22, 'um dos chefes da sinagoga, chamado Jairo, se apresentou e, à sua vista, lançou-se a seus pés,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28740, 1107, 23, 'rogando-lhe com insistência: “Minha filhinha está nas últimas. Vem, impõe-lhe as mãos para que se salve e viva”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28741, 1107, 24, 'Jesus foi com ele e grande multidão o seguia, comprimindo-o.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28742, 1107, 25, 'Ora, havia ali uma mulher que já por doze anos padecia de um fluxo de sangue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28743, 1107, 26, 'Sofrera muito nas mãos de vários médicos, gastando tudo o que possuía, sem achar nenhum alívio; pelo contrário, piorava cada vez mais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28744, 1107, 27, 'Tendo ela ouvido falar de Jesus, veio por detrás, entre a multidão, e tocou-lhe no manto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28745, 1107, 28, 'Dizia ela consigo: “Se tocar, ainda que seja na orla do seu manto, estarei curada”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28746, 1107, 29, 'Ora, no mesmo instante se lhe estancou a fonte de sangue, e ela teve a sensação de estar curada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28747, 1107, 30, 'Jesus percebeu imediatamente que saíra dele uma força e, voltando-se para o povo, perguntou: “Quem tocou minhas vestes?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28748, 1107, 31, 'Responderam-lhe os seus discípulos: “Vês que a multidão te comprime e perguntas: Quem me tocou?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28749, 1107, 32, 'E ele olhava em derredor para ver quem o fizera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28750, 1107, 33, 'Ora, a mulher, atemorizada e trêmula, sabendo o que nela se tinha passado, veio lançar-se a seus pés e contou-lhe toda a verdade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28751, 1107, 34, 'Mas ele lhe disse: “Filha, a tua fé te salvou. Vai em paz e sê curada do teu mal”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28752, 1107, 35, 'Enquanto ainda falava, chegou alguém da casa do chefe da sinagoga, anunciando: “Tua filha morreu. Para que ainda incomodas o Mestre?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28753, 1107, 36, 'Ouvindo Jesus a notícia que era transmitida, dirigiu-se ao chefe da sinagoga: “Não temas; crê somente”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28754, 1107, 37, 'E não permitiu que ninguém o acompanhasse, senão Pedro, Tiago e João, irmão de Tiago.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28755, 1107, 38, 'Ao chegar à casa do chefe da sinagoga, viu o alvoroço e os que estavam chorando e fazendo grandes lamentações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28756, 1107, 39, 'Ele entrou e disse-lhes: “Por que todo esse barulho e esses choros? A menina não morreu. Ela está dormindo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28757, 1107, 40, 'Mas riam-se dele. Contudo, tendo mandado sair todos, tomou o pai e a mãe da menina e os que levava consigo, e entrou onde a menina estava deitada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28758, 1107, 41, 'Segurou a mão da menina e disse-lhe: “Talita cumi”, que quer dizer: “Menina, ordeno-te, levanta-te!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28759, 1107, 42, 'E imedia­tamente a menina se levantou e se pôs a caminhar (pois contava doze anos). Eles ficaram assombrados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28760, 1107, 43, 'Ordenou-lhes seve­ramente que ninguém o soubesse e mandou que lhe dessem de comer. (= Mt 13,53-58 = Lc 4,16-30)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28761, 1108, 1, 'Depois, ele partiu dali e foi para a sua pátria, seguido de seus discípulos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28762, 1108, 2, 'Quando chegou o dia de sábado, começou a ensinar na sinagoga. Muitos o ouviam e, tomados de admiração, diziam: “Donde lhe vem isso? Que sabedo­ria é essa que lhe foi dada, e como se operam por suas mãos tão grandes milagres?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28763, 1108, 3, 'Não é ele o carpinteiro, o filho de Maria, o irmão de Tiago, de José, de Judas e de Simão? Não vivem aqui entre nós também suas irmãs?”. E ficaram perplexos a seu respeito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28764, 1108, 4, 'Mas Jesus disse-lhes: “Um profeta só é desprezado na sua pátria, entre os seus parentes e na sua própria casa”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28765, 1108, 5, 'Não pôde fazer ali milagre algum. Curou apenas alguns poucos enfermos, impondo-lhes as mãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28766, 1108, 6, 'Admirava-se ele da desconfiança deles. E, ensinando, percorria as aldeias circunvizinhas. (= Mt 10,5-15 = Lc 9,1-6)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28767, 1108, 7, 'Então, chamou os Doze e começou a enviá-los, dois a dois; e deu-lhes poder sobre os espíritos imundos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28768, 1108, 8, 'Ordenou-lhes que não levassem coisa alguma para o cami­nho, senão somente um bordão; nem pão, nem mochila, nem dinheiro no cinto;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28769, 1108, 9, 'como calçado, unicamente sandálias, e que se não revestissem de duas túnicas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28770, 1108, 10, 'E disse-lhes: “Em qualquer casa em que entrardes, ficai nela, até vos retirardes dali.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28771, 1108, 11, 'Se em algum lugar não vos receberem nem vos escutarem, saí dali e sacudi o pó dos vossos pés em testemunho contra ele”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28772, 1108, 12, 'Eles partiram e pregaram a penitência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28773, 1108, 13, 'Expeliam numerosos demônios, ungiam com óleo a muitos enfermos e os curavam. (= Mt 14,1-12 = Lc 3,19s; 9,7s)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28774, 1108, 14, 'O rei Herodes ouviu falar de Jesus, cujo nome se tornara célebre. Dizia-se: “João Batista ressurgiu dos mortos e por isso o poder de fazer milagres opera nele”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28775, 1108, 15, 'Uns afirmavam: “É Elias!” Diziam outros: “É um profeta como qualquer outro”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28776, 1108, 16, 'Ouvindo isso, Herodes repetia: “É João, a quem mandei decapitar. Ele ressuscitou!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28777, 1108, 17, 'Pois o próprio Herodes mandara prender João e acorrentá-lo no cárcere, por causa de Herodíades, mulher de seu irmão Filipe, com a qual ele se tinha casado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28778, 1108, 18, 'João tinha dito a Herodes: “Não te é permitido ter a mulher de teu irmão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28779, 1108, 19, 'Por isso, Herodíades o odiava e queria matá-lo, não o conseguindo, porém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28780, 1108, 20, 'Pois Herodes respeitava João, sabendo que era um homem justo e santo; protegia-o e, quando o ouvia, sentia-se embaraçado. Mas, mesmo assim, de boa mente o ouvia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28781, 1108, 21, 'Chegou, porém, um dia favorável em que Herodes, por ocasião do seu natalício, deu um banquete aos grandes de sua corte, aos seus oficiais e aos principais da Galileia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28782, 1108, 22, 'A filha de Herodíades apresentou-se e pôs-se a dançar, com grande satisfação de Herodes e dos seus convivas. Disse o rei à moça: “Pede-me o que quiseres, e eu to darei”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28783, 1108, 23, 'E jurou-lhe: “Tudo o que me pedires te darei, ainda que seja a metade do meu reino”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28784, 1108, 24, 'Ela saiu e perguntou à sua mãe: “Que hei de pedir?”. E a mãe respondeu: “A cabeça de João Batista”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28785, 1108, 25, 'Tornando logo a entrar apressadamente à presença do rei, exprimiu-lhe seu desejo: “Quero que sem demora me dês a cabeça de João Batista”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28786, 1108, 26, 'O rei entristeceu-se; todavia, por causa da sua promessa e dos convivas, não quis recusar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28787, 1108, 27, 'Sem tardar, enviou um carrasco com a ordem de trazer a cabeça de João. Ele foi, decapitou João no cárcere,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28788, 1108, 28, 'trouxe a sua cabeça num prato e a deu à moça, e esta a entregou à sua mãe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28789, 1108, 29, 'Ouvindo isso, os seus discípulos foram tomar o seu corpo e o depositaram num sepulcro. (= Mt 14,13-21 = Lc 9,10-17 = Jo 6,1-13)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28790, 1108, 30, 'Os apóstolos voltaram para junto de Jesus e contaram-lhe tudo o que haviam feito e ensinado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28791, 1108, 31, 'Ele disse-lhes: “Vinde à parte, para algum lugar deserto e descansai um pouco.” Porque eram muitos os que iam e vinham e nem tinham tempo para comer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28792, 1108, 32, 'Partiram na barca para um lugar solitário, à parte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28793, 1108, 33, 'Mas viram-nos partir. Por isso, muitos deles perceberam para onde iam, e de todas as cidades acorreram a pé para o lugar aonde se dirigiam, e chegaram primeiro que eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28794, 1108, 34, 'Ao desembarcar, Jesus viu uma grande multidão e compadeceu-se dela, porque era como ovelhas que não têm pastor. E começou a ensinar-lhes muitas coisas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28795, 1108, 35, 'A hora já estava bem avançada quando se achegaram a ele os seus discípulos e disseram: “Este lugar é deserto, e já é tarde.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28796, 1108, 36, 'Despede-os, para irem aos sítios e aldeias vizinhas a comprar algum alimento”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28797, 1108, 37, 'Mas ele respondeu-lhes: “Dai-lhes vós mesmos de comer”. Replicaram-lhe: “Iremos comprar duzentos dená­rios de pão para dar-lhes de comer?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28798, 1108, 38, 'Ele perguntou-lhes: “Quantos pães tendes? Ide ver”. Depois de se terem informado, disseram: “Cinco, e dois peixes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28799, 1108, 39, 'Ordenou-lhes que mandassem todos sentar-se, em grupos, na relva verde.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28800, 1108, 40, 'E assentaram-se em grupos de cem e de cinquenta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28801, 1108, 41, 'Então, tomou os cinco pães e os dois peixes e, erguendo os olhos ao céu, abençoou-os, partiu-os e os deu a seus discípulos, para que lhos distribuíssem, e repartiu entre todos os dois peixes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28802, 1108, 42, 'Todos comeram e ficaram fartos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28803, 1108, 43, 'Recolheram do que sobrou doze cestos cheios de pedaços, e os restos dos peixes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28804, 1108, 44, 'Foram cinco mil os homens que haviam comido daqueles pães.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28805, 1108, 45, 'Imediatamente ele obrigou os seus discípulos a subirem para a barca, para que chegassem antes dele à outra margem, em frente de Betsaida, enquanto ele mesmo despedia o povo. (= Mt 14,22-33 = Jo 6,15-21)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28806, 1108, 46, 'E despedido que foi o povo, retirou-se ao monte para orar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28807, 1108, 47, 'À noite, achava-se a barca no meio do lago e ele, a sós, em terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28808, 1108, 48, 'Vendo-os se fatigarem em remar, sendo-lhes o vento contrário, foi ter com eles pela quarta vigília da noite, andando por cima do mar, e fez como se fosse passar ao lado deles.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28809, 1108, 49, 'À vista de Jesus, caminhando sobre o mar, pensaram que fosse um fantasma e gritaram;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28810, 1108, 50, 'pois todos o viram e se assustaram. Mas ele logo lhes falou: “Tranquilizai-vos, sou eu; não vos assusteis!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28811, 1108, 51, 'E subiu para a barca, junto deles, e o vento cessou. Todos se achavam tomados de um extremo pavor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28812, 1108, 52, 'pois ainda não tinham compreendido o caso dos pães; os seus corações estavam insensíveis. (= Mt 14,34ss)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28813, 1108, 53, 'Navegaram para o outro lado e chegaram à região de Genesaré, onde aportaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28814, 1108, 54, 'Assim que saíram da barca, o povo o reconheceu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28815, 1108, 55, 'Percorrendo toda aquela região, começaram a levar, em leitos, os que padeciam de algum mal, para o lugar onde ouviam dizer que ele se encontrava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28816, 1108, 56, 'Onde quer que ele entrasse, fosse nas aldeias ou nos povoados, ou nas cidades, punham os enfermos nas ruas e pe­diam-lhe que os deixasse tocar ao menos na orla de suas vestes. E todos os que tocavam em Jesus ficavam sãos. (= Mt 15,1-20)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28817, 1109, 1, 'Os fariseus e alguns dos escribas vindos de Jerusalém tinham se reunido em torno dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28818, 1109, 2, 'E perceberam que alguns dos seus discípulos comiam o pão com as mãos impuras, isto é, sem as lavar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28819, 1109, 3, '(Com efeito, os fariseus e todos os judeus, apegando-se à tradição dos antigos, não comem sem lavar cuidadosamente as mãos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28820, 1109, 4, 'e, quando voltam do mercado, não comem sem ter feito abluções. E há muitos outros costumes que observam por tradição, como lavar os copos, os jarros e os pratos de metal)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28821, 1109, 5, 'Os fariseus e os escribas perguntaram-lhe: “Por que não andam os teus discípulos conforme a tradição dos antigos, mas comem o pão com as mãos impuras?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28822, 1109, 6, 'Jesus disse-lhes: “Isaí­as com muita razão profetizou de vós, hipócritas, quando escreveu: Este povo honra-me com os lábios, mas o seu coração está longe de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28823, 1109, 7, 'Em vão, pois, me cultuam, porque ensinam doutrinas e preceitos humanos (29,13).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28824, 1109, 8, 'Deixando o mandamento de Deus, vos apegais à tradição dos homens”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28825, 1109, 9, 'E Jesus acrescentou: “Na realidade, invalidais o mandamento de Deus para estabelecer a vossa tradição.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28826, 1109, 10, 'Pois Moisés disse: Honra teu pai e tua mãe; e: Todo aquele que amaldiçoar pai ou mãe seja morto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28827, 1109, 11, 'Vós, porém, dizeis: Se alguém disser ao pai ou à mãe: Qualquer coisa que de minha parte te pudesse ser útil é corban, isto é, oferta,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28828, 1109, 12, 'e já não lhe deixais fazer coisa alguma a favor de seu pai ou de sua mãe,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28829, 1109, 13, 'anulando a Palavra de Deus por vossa tradição que vós vos transmitistes. E fazeis ainda muitas coisas semelhantes”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28830, 1109, 14, 'Tendo chamado de novo a turba, dizia-lhes: “Ouvi-me todos, e entendei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28831, 1109, 15, 'Nada há fora do homem que, entrando nele, o possa manchar; mas o que sai do homem, isso é que mancha o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28832, 1109, 16, '[A bom entendedor meia palavra basta.]”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28833, 1109, 17, 'Quando deixou o povo e entrou em casa, os seus discípulos perguntaram-lhe acerca da parábola.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28834, 1109, 18, 'Respondeu-lhes: “Sois também vós assim ignorantes? Não compreendeis que tudo o que de fora entra no homem não o pode tornar impuro,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28835, 1109, 19, 'porque não lhe entra no coração, mas vai ao ventre e dali segue sua Lei natural?”. Assim ele declarava puros todos os alimentos. E acrescentava:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28836, 1109, 20, '“Ora, o que sai do homem, isso é que mancha o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28837, 1109, 21, 'Porque é do interior do coração dos homens que procedem os maus pensamentos: devassidões, roubos, assassinatos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28838, 1109, 22, 'adultérios, cobiças, perversidades, fraudes, desonestidade, inveja, difamação, orgulho e insensatez.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28839, 1109, 23, 'Todos estes vícios procedem de dentro e tornam impuro o homem”. (= Mt 15,21-28)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28840, 1109, 24, 'Em seguida, deixando aquele lugar, foi para a terra de Tiro e de Sidônia. E tendo entrado numa casa, não quis que ninguém o soubesse. Mas não pôde ficar oculto,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28841, 1109, 25, 'pois uma mulher, cuja filha possuía um espírito imundo, logo que soube que ele estava ali, entrou e caiu a seus pés.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28842, 1109, 26, '(Essa mulher era pagã, de origem siro-fenícia.) Ora, ela suplicava-lhe que expelisse de sua filha o demônio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28843, 1109, 27, 'Disse-lhe Jesus: “Deixa primeiro que se fartem os filhos, porque não fica bem tomar o pão dos filhos e lançá-lo aos cães”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28844, 1109, 28, 'Mas ela respondeu: “É verdade, Senhor; mas também os cachorrinhos debaixo da mesa comem das migalhas dos filhos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28845, 1109, 29, 'Jesus respondeu-lhe: “Por causa desta palavra, vai-te, que saiu o demônio, de tua filha”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28846, 1109, 30, 'Voltou ela para casa e achou a menina deitada na cama. O demônio havia saído. (= Mt 15,29ss)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28847, 1109, 31, 'Ele deixou de novo as fronteiras de Tiro e foi por Sidônia ao mar da Galileia, no meio do território da Decápole.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28848, 1109, 32, 'Ora, apresentaram-lhe um surdo-mudo, rogando-lhe que lhe impusesse a mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28849, 1109, 33, 'Jesus tomou-o à parte dentre o povo, pôs-lhe os dedos nos ouvidos e tocou-lhe a língua com saliva.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28850, 1109, 34, 'E levantou os olhos ao céu, deu um suspiro e disse-lhe: “Éfeta!”, que quer dizer “abre-te!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28851, 1109, 35, 'No mesmo instante, os ouvidos se lhe abriram, a prisão da língua se lhe desfez e ele falava perfeitamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28852, 1109, 36, 'Proibiu-lhes que o dissessem a alguém. Mas quanto mais lhes proibia, tanto mais o publi­cavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28853, 1109, 37, 'E tanto mais se admiravam, dizendo: “Ele fez bem todas as coisas. Fez ouvirem os surdos e falarem os mudos!”. (= Mt 15,32-39)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28854, 1110, 1, 'Naqueles dias, como fosse novamente numerosa a multidão, e não tivessem o que comer, Jesus convocou os discípulos e lhes disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28855, 1110, 2, '“Tenho compaixão deste povo. Já há três dias perseveram comigo e não têm o que comer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28856, 1110, 3, 'Se os despedir em jejum para suas casas, desfalecerão no caminho; e alguns deles vieram de longe!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28857, 1110, 4, 'Seus discípulos responderam-lhe: “Como poderá alguém fartá-los de pão aqui no deserto?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28858, 1110, 5, 'Mas ele perguntou-lhes: “Quantos pães tendes?” “Sete” –, responderam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28859, 1110, 6, 'Mandou então que o povo se assentasse no chão. Tomando os sete pães, deu graças, partiu-os e entregou-os a seus discípulos, para que os distribuíssem e eles os distribuíram ao povo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28860, 1110, 7, 'Tinham também alguns peixinhos. Ele os abençoou e mandou também distribuí-los.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28861, 1110, 8, 'Comeram e ficaram fartos, e dos pedaços que sobraram levantaram sete cestos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28862, 1110, 9, 'Ora, os que comeram eram cerca de quatro mil pessoas. Em seguida, Jesus os despediu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28863, 1110, 10, 'E, embarcando depois com seus discípulos, foi para o território de Dalmanuta. (= Mt 16,1-12 = Lc 11,29-32)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28864, 1110, 11, 'Vieram os fariseus e puseram-se a disputar com ele e pediram-lhe um sinal do céu, para pô-lo à prova.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28865, 1110, 12, 'Jesus, porém, suspirando no seu coração, disse: “Por que pede esta geração um sinal? Em verdade vos digo: jamais lhe será dado um sinal”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28866, 1110, 13, 'Deixou-os e seguiu de barca para a outra margem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28867, 1110, 14, 'Aconteceu que eles haviam esquecido de levar pães consigo. Na barca havia um único pão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28868, 1110, 15, 'Jesus advertiu-os: “Abri os olhos e acautelai-vos do fermento dos fariseus e do fermento de Herodes!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28869, 1110, 16, 'E eles comentavam entre si que era por não terem pão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28870, 1110, 17, 'Jesus percebeu-o e disse-lhes: “Por que discutis por não terdes pão? Ainda não tendes refletido nem compreendido? Tendes, pois, o coração insensível?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28871, 1110, 18, 'Tendo olhos, não vedes? E tendo ouvidos, não ouvis? Não vos lembrais mais?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28872, 1110, 19, 'Ao partir eu os cinco pães entre os cinco mil, quantos cestos recolhestes cheios de pedaços?”. Responderam-lhe: “Doze”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28873, 1110, 20, '“E quando eu parti os sete pães entre os quatro mil homens, quantos cestos de pedaços levantastes?” “Sete” – responderam-lhe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28874, 1110, 21, 'Jesus disse-lhes: “Como é que ainda não entendeis?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28875, 1110, 22, 'Chegando eles a Betsaida, trouxeram-lhe um cego e suplicaram-lhe que o tocasse.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28876, 1110, 23, 'Jesus tomou o cego pela mão e levou-o para fora da aldeia. Pôs-lhe saliva nos olhos e, impondo-lhe as mãos, perguntou-lhe: “Vês alguma coisa?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28877, 1110, 24, 'O cego levantou os olhos e respondeu: “Vejo os homens como árvores que andam”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28878, 1110, 25, 'Em seguida, Jesus lhe impôs as mãos nos olhos e ele começou a ver e ficou curado, de modo que via distintamente de longe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28879, 1110, 26, 'E mandou-o para casa, dizendo-lhe: “Não entres nem mesmo na aldeia”. (= Mt 16,13-23 = Lc 9,18-22)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28880, 1110, 27, 'Jesus saiu com os seus discípulos para as aldeias de Cesareia de Filipe, e pelo caminho perguntou-lhes: “Quem dizem os homens que eu sou?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28881, 1110, 28, 'Responderam-lhe os discípulos: “João Batista; outros, Elias; outros, um dos profetas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28882, 1110, 29, 'Então, perguntou-lhes Jesus: “E vós, quem dizeis que eu sou?”. Respondeu Pedro: “Tu és o Cristo”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28883, 1110, 30, 'E ordenou-lhes severamente que a ninguém dissessem nada a respeito dele.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28884, 1110, 31, 'E começou a ensinar-lhes que era necessário que o Filho do Homem padecesse muito, fosse rejeitado pelos anciãos, pelos sumos sacerdotes e pelos escribas, e fosse morto, mas ressuscitasse depois de três dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28885, 1110, 32, 'E falava-lhes abertamente dessas coisas. Pedro, tomando-o à parte, começou a repreendê-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28886, 1110, 33, 'Mas, voltando-se ele, olhou para os seus discípulos e repreendeu a Pedro: “Afasta-te de mim, Satanás, porque teus sentimentos não são os de Deus, mas os dos homens”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28887, 1110, 34, 'Em seguida, convocando a multidão juntamente com os seus discípulos, disse-lhes: “Se alguém me quer seguir, renuncie-se a si mesmo, tome a sua cruz e siga-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28888, 1110, 35, 'Porque o que quiser salvar a sua vida, irá perdê-la; mas o que perder a sua vida por amor de mim e do Evangelho, irá salvá-la.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28889, 1110, 36, 'Pois que aproveitará ao homem ganhar o mundo inteiro, se vier a perder a sua vida?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28890, 1110, 37, 'Ou que dará o homem em troca da sua vida?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28891, 1110, 38, 'Porque, se nesta geração adúltera e pecadora alguém se envergonhar de mim e das minhas palavras, também o Filho do Homem se envergonhará dele, quando vier na glória de seu Pai com os seus santos anjos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28892, 1111, 1, 'E dizia-lhes: “Em verdade vos digo: dos que aqui se acham, alguns há que não experimentarão a morte, enquanto não virem chegar o Reino de Deus com poder”.* (= Mt 17,1-13 = Lc 9,28-36)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28893, 1111, 2, 'Seis dias depois, Jesus tomou consigo a Pedro, Tiago e João, e conduziu-os a sós a um alto monte. E');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28894, 1111, 3, 'transfigurou-se diante deles. Suas vestes tornaram-se resplandecentes e de uma brancura tal, que nenhum lavadeiro sobre a terra as pode fazer assim tão brancas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28895, 1111, 4, 'Apareceram-lhes Elias e Moisés, e falavam com Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28896, 1111, 5, 'Pedro tomou a palavra: “Mestre, é bom para nós estarmos aqui; faremos três tendas: uma para ti, outra para Moisés e outra para Elias”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28897, 1111, 6, 'Com efeito, não sabia o que falava, porque estavam sobremaneira atemorizados.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28898, 1111, 7, 'Formou-se então uma nuvem que os encobriu com a sua sombra; e da nuvem veio uma voz: “Este é o meu Filho muito amado; ouvi-o”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28899, 1111, 8, 'E olhando eles logo em derredor, já não viram ninguém, senão só a Jesus com eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28900, 1111, 9, 'Ao descerem do monte, proibiu-lhes Jesus que contassem a quem quer que fosse o que tinham visto, até que o Filho do Homem houvesse ressurgido dos mortos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28901, 1111, 10, 'E guardaram esta recomendação consigo, perguntando entre si o que significaria: “Ser ressuscitado dentre os mortos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28902, 1111, 11, 'Depois lhe perguntaram: “Por que dizem os fariseus e os escribas que primeiro deve voltar Elias?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28903, 1111, 12, 'Respondeu-lhes: “Elias deve voltar primeiro e restabelecer tudo em ordem. Como então está escrito acerca do Filho do Homem que deve padecer muito e ser desprezado?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28904, 1111, 13, 'Mas digo-vos que também Elias já voltou e fizeram-lhe sofrer tudo quanto quiseram, como está escrito dele”.* (= Mt 17,14-20 = Lc 9,37-43a)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28905, 1111, 14, 'Depois, aproximando-se dos discípulos, viu ao redor deles grande multidão, e os escribas a discutir com eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28906, 1111, 15, 'Todo aquele povo, vendo de surpresa Jesus, acorreu a ele para saudá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28907, 1111, 16, 'Ele lhes perguntou: “Que estais discutindo com eles?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28908, 1111, 17, 'Res­pondeu um homem dentre a multidão: “Mestre, eu te trouxe meu filho, que tem um espírito mudo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28909, 1111, 18, 'Este, onde quer que o apanhe, lança-o por terra e ele espuma, range os dentes e fica endurecido. Roguei a teus discípulos que o expelissem, mas não o puderam”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28910, 1111, 19, 'Respondeu-lhes Jesus: “Ó geração incrédula, até quando estarei convosco? Até quando vos hei de aturar? Trazei-o a mim!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28911, 1111, 20, 'Eles lho trouxeram. Assim que o menino avistou Jesus, o espírito o agitou fortemente. Caiu por terra e revolvia-se espumando.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28912, 1111, 21, 'Jesus perguntou ao pai: “Há quanto tempo lhe acontece isto?” “Desde a infância – respondeu-lhe –.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28913, 1111, 22, 'E o tem lançado muitas vezes ao fogo e à água, para o matar. Se tu, porém, podes alguma coisa, ajuda-nos, compadece-te de nós!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28914, 1111, 23, 'Disse-lhe Jesus: “Se podes alguma coisa!... Tudo é possível ao que crê”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28915, 1111, 24, 'Imediatamente exclamou o pai do menino: “Creio! Vem em socorro à minha falta de fé!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28916, 1111, 25, 'Vendo Jesus que o povo afluía, intimou o espírito imundo e disse-lhe: “Espírito mudo e surdo, eu te ordeno: sai deste menino e não tornes a entrar nele”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28917, 1111, 26, 'E, gritando e maltratando-o extremamente, saiu. O menino ficou como morto, de modo que muitos diziam: “Morreu...”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28918, 1111, 27, 'Jesus, porém, tomando-o pela mão, ergueu-o e ele levantou-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28919, 1111, 28, 'Depois de entrar em casa, os seus discípulos perguntaram-lhe em particular: “Por que não pudemos nós expeli-lo?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28920, 1111, 29, 'Ele disse-lhes: “Esta espécie de demônios não se pode expulsar senão pela oração”. (= Mt 17,21s = Lc 9,43b-45)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28921, 1111, 30, 'Tendo partido dali, atravessaram a Galileia. Não queria, porém, que ninguém o soubesse.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28922, 1111, 31, 'E ensinava os seus discípulos: “O Filho do Homem será entregue nas mãos dos homens, e o matarão; e ressuscitará três dias depois de sua morte”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28923, 1111, 32, 'Mas não entendiam essas palavras; e tinham medo de lho perguntar. (= Mt 18,1-10 = Lc 9,46-50)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28924, 1111, 33, 'Em seguida, voltaram para Cafarnaum. Quando já estava em casa, Jesus perguntou-lhes: “De que faláveis pelo caminho?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28925, 1111, 34, 'Mas eles calaram-se, porque pelo caminho haviam discutido entre si qual deles seria o maior.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28926, 1111, 35, 'Sentando-se, chamou os Doze e disse-lhes: “Se alguém quer ser o primeiro, seja o último de todos e o servo de todos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28927, 1111, 36, 'E tomando um menino, colocou-o no meio deles; abraçou-o e disse-lhes:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28928, 1111, 37, '“Todo o que recebe um destes meninos em meu nome, a mim é que recebe; e todo o que recebe a mim, não me recebe, mas aquele que me enviou”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28929, 1111, 38, 'João disse-lhe: “Mestre, vimos alguém, que não nos segue, expulsar demônios em teu nome, e lho proibimos”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28930, 1111, 39, 'Jesus, porém, disse-lhe: “Não lho proibais, porque não há ninguém que faça um prodígio em meu nome e em seguida possa falar mal de mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28931, 1111, 40, 'Pois quem não é contra nós, é a nosso favor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28932, 1111, 41, 'E quem vos der de beber um copo de água porque sois de Cristo, digo-vos em verdade: não perderá a sua recompensa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28933, 1111, 42, 'Mas todo o que fizer cair no pecado a um destes pequeninos que creem em mim, melhor lhe fora que uma pedra de moinho lhe fosse posta ao pescoço e o lançassem ao mar!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28934, 1111, 43, 'Se a tua mão for para ti ocasião de queda, corta-a; melhor te é entrares na vida aleijado do que, tendo duas mãos, ires para a geena, para o fogo inextinguível');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28935, 1111, 44, '[onde o seu verme não morre e o fogo não se apaga].');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28936, 1111, 45, 'Se o teu pé for para ti ocasião de queda, corta-o fora; melhor te é entrares coxo na vida eterna do que, tendo dois pés, seres lançado à geena do fogo inextinguível');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28937, 1111, 46, '[onde o seu verme não morre e o fogo não se apaga].*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28938, 1111, 47, 'Se o teu olho for para ti ocasião de queda, arranca-o; melhor te é entrares com um olho de menos no Reino de Deus do que, tendo dois olhos, seres lançado à geena do fogo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28939, 1111, 48, 'onde o seu verme não morre e o fogo não se apaga.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28940, 1111, 49, 'Porque todo homem será salgado pelo fogo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28941, 1111, 50, 'O sal é uma boa coisa; mas se ele se tornar insípido, com que lhe restituireis o sabor? Tende sal em vós e vivei em paz uns com os outros”. (= Mt 19,1-12)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28942, 1112, 1, 'Saindo dali, ele foi para a região da Judeia, além do Jordão. As multidões voltaram a segui-lo pelo caminho e de novo ele pôs-se a ensiná-las, como era seu costume.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28943, 1112, 2, 'Chegaram os fariseus e perguntaram-lhe, para o pôr à prova, se era permitido ao homem repudiar sua mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28944, 1112, 3, 'Ele respondeu-lhes: “Que vos ordenou Moisés?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28945, 1112, 4, 'Eles responderam: “Moisés permitiu escrever carta de divórcio e despedir a mulher”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28946, 1112, 5, 'Continuou Jesus: “Foi devido à dureza do vosso coração que ele vos deu essa Lei;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28947, 1112, 6, 'mas, no princípio da Criação, Deus os fez homem e mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28948, 1112, 7, 'Por isso, deixará o homem pai e mãe e se unirá à sua mulher;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28949, 1112, 8, 'e os dois não serão senão uma só carne. Assim, já não são dois, mas uma só carne.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28950, 1112, 9, 'Não separe, pois, o homem o que Deus uniu”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28951, 1112, 10, 'Em casa, os discípulos fizeram-lhe perguntas sobre o mesmo assunto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28952, 1112, 11, 'E ele disse-lhes: “Quem repudia sua mulher e se casa com outra, comete adultério contra a primeira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28953, 1112, 12, 'E se a mulher repudia o marido e se casa com outro, comete adultério”. (= Mt 19,13ss = Lc 18,15ss)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28954, 1112, 13, 'Apresentaram-lhe então crian­ças para que as tocasse; mas os discípulos repreendiam os que as apresentavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28955, 1112, 14, 'Vendo-o, Jesus indignou-se e disse-lhes: “Deixai vir a mim os pequeninos e não os impeçais, porque o Reino de Deus é daqueles que se lhes assemelham.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28956, 1112, 15, 'Em verdade vos digo: todo o que não receber o Reino de Deus com a mentalidade de uma crian­ça, nele não entrará”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28957, 1112, 16, 'Em seguida, ele as abraçou e as abençoou, impondo-lhes as mãos. (= Mt 19,16-29 = Lc 18,18-30)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28958, 1112, 17, 'Tendo ele saído para se pôr a caminho, veio alguém correndo e, dobrando os joelhos diante dele, suplicou-lhe: “Bom Mestre, que farei para alcançar a vida eterna?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28959, 1112, 18, 'Jesus disse-lhe: “Por que me chamas bom? Só Deus é bom.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28960, 1112, 19, 'Conheces os mandamentos: não mates; não cometas adultério; não furtes; não digas falso testemunho; não cometas fraudes; honra pai e mãe”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28961, 1112, 20, 'Ele respondeu-lhe: “Mestre, tudo isso tenho observado desde a minha mocidade”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28962, 1112, 21, 'Jesus fixou nele o olhar, amou-o e disse-lhe: “Uma só coisa te falta; vai, vende tudo o que tens e dá-o aos pobres e terás um tesouro no céu. Depois, vem e segue-me”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28963, 1112, 22, 'Ele entristeceu-se com essas palavras e foi-se todo abatido, porque possuía muitos bens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28964, 1112, 23, 'E, olhando Jesus em derredor, disse a seus discípulos: “Quão dificilmente entrarão no Reino de Deus os ricos!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28965, 1112, 24, 'Os discípulos ficaram assombrados com suas palavras. Mas Jesus replicou: “Filhinhos, quão difícil é entrarem no Reino de Deus os que põem a sua confiança nas riquezas!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28966, 1112, 25, 'É mais fácil passar o camelo pelo fundo de uma agulha do que entrar o rico no Reino de Deus”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28967, 1112, 26, 'Eles ainda mais se admiravam, dizendo a si próprios: “Quem pode então salvar-se?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28968, 1112, 27, 'Olhando Jesus para eles, disse: “Aos homens isso é impossível, mas não a Deus; pois a Deus tudo é possível”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28969, 1112, 28, 'Pedro começou a dizer-lhe: “Eis que deixamos tudo e te seguimos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28970, 1112, 29, 'Respondeu-lhe Jesus: “Em verdade vos digo: ninguém há que tenha deixado casa ou irmãos, ou irmãs, ou pai, ou mãe, ou filhos, ou terras por causa de mim e por causa do Evangelho');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28971, 1112, 30, 'que não receba, já neste século, cem vezes mais casas, irmãos, irmãs, mães, filhos e terras, com perseguições – e no século vindouro a vida eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28972, 1112, 31, 'Muitos dos primeiros serão os últimos, e dos últimos serão os primeiros”. (= Mt 20,17ss = Lc 18,31-34)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28973, 1112, 32, 'Estavam a caminho de Jerusalém e Jesus ia adiante deles. Estavam perturbados e o seguiam com medo. E tomando novamente a si os Doze, começou a predizer-lhes as coisas que lhe ha­viam de acontecer:*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28974, 1112, 33, '“Eis que subimos a Jerusalém e o Filho do Homem será entregue aos príncipes dos sacerdotes e aos escribas; irão condená-lo à morte e será entregue aos gentios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28975, 1112, 34, 'Escarnecerão dele, cuspirão nele, irão açoitá-lo e hão de matá-lo; mas ao terceiro dia ele ressurgirá”. (= Mt 20,20-28 = Lc 22,24-30)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28976, 1112, 35, 'Aproximaram-se de Jesus Tiago e João, filhos de Zebedeu, e disseram-lhe: “Mestre, queremos que nos concedas tudo o que te pedirmos” –.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28977, 1112, 36, '“Que quereis que vos faça?” –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28978, 1112, 37, '“Concede-nos que nos sentemos na tua glória, um à tua direita e outro à tua esquerda.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28979, 1112, 38, '– “Não sabeis o que pedis – retorquiu Jesus –. Podeis vós beber o cálice que eu vou beber, ou ser batizados no batismo em que eu vou ser batizado?”* –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28980, 1112, 39, '“Podemos” – asseguraram eles. Jesus prosseguiu: “Vós bebereis o cálice que eu devo beber e sereis batizados no batismo em que eu devo ser batizado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28981, 1112, 40, 'Mas, quanto a assentardes à minha direita ou à minha esquerda, isto não depende de mim: o lugar compete àqueles a quem está destinado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28982, 1112, 41, 'Ouvindo isso, os outros dez começaram a indignar-se contra Tiago e João.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28983, 1112, 42, 'Jesus chamou-os e deu-lhes esta lição: “Sabeis que os que são considerados chefes das nações dominam sobre elas e os seus intendentes exercem poder sobre elas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28984, 1112, 43, 'Entre vós, porém, não será assim: todo o que quiser tornar-se grande entre vós, seja o vosso servo;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28985, 1112, 44, 'e todo o que entre vós quiser ser o primeiro, seja escravo de todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28986, 1112, 45, 'Porque o Filho do Homem não veio para ser servido, mas para servir e dar a sua vida em redenção por muitos”. (= Mt 20,29-34 = Lc 18,35-43)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28987, 1112, 46, 'Chegaram a Jericó. Ao sair dali Jesus, seus discípulos e numerosa multidão, estava sentado à beira do caminho, mendigando, Bartimeu, que era cego, filho de Timeu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28988, 1112, 47, 'Sabendo que era Jesus de Nazaré, começou a gritar: “Jesus, filho de Davi, tem compaixão de mim!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28989, 1112, 48, 'Muitos o repreendiam, para que se calasse, mas ele gritava ainda mais alto: “Filho de Davi, tem compaixão de mim!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28990, 1112, 49, 'Jesus parou e disse: “Chamai-o”. Chamaram o cego, dizendo-lhe: “Coragem! Levanta-te, ele te chama”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28991, 1112, 50, 'Lançando fora a capa, o cego ergueu-se de um salto e foi ter com ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28992, 1112, 51, 'Jesus, tomando a palavra, perguntou-lhe: “Que queres que te faça?” “Rabôni” –, respondeu-lhe o cego – “que eu veja!”. *');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28993, 1112, 52, 'Jesus disse-lhe: “Vai, a tua fé te salvou”. No mesmo instante, ele recuperou a vista e foi seguindo Jesus pelo caminho. (= Mt 21,1-11 = Lc 19,29-40 = Jo 12,12-19)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28994, 1113, 1, 'Jesus e seus discípulos aproximavam-se de Jerusalém e chegaram aos arredores de Betfagé e de Betânia, perto do monte das Oliveiras. Desse lugar Jesus enviou dois de seus discípulos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28995, 1113, 2, 'dizendo-lhes: “Ide à aldeia que está defronte de vós e, logo ao entrardes nela, achareis preso um jumentinho, em que não montou ainda homem algum; desprendei-o e trazei-mo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28996, 1113, 3, 'E se alguém vos perguntar: Que fazeis?, dizei: O Senhor precisa dele, mas daqui a pouco o devolverá”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28997, 1113, 4, 'Indo eles, acharam o jumentinho atado fora, diante de uma porta, na curva do caminho. Iam-no desprendendo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28998, 1113, 5, 'quando alguns dos que ali estavam perguntaram: “Ei, que estais fazendo? Por que soltais o jumentinho?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (28999, 1113, 6, 'Responderam como Jesus lhes havia ordenado; e deixaram-no levar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (29000, 1113, 7, 'Conduziram a Jesus o jumen­tinho, cobriram-no com seus mantos, e Jesus montou nele.');
