INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32001, 1183, 24, 'Mas nada disso temo, nem faço caso da minha vida, contanto que termine a minha carreira e o ministério da palavra que recebi do Senhor Jesus, para dar testemunho ao Evangelho da graça de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32002, 1183, 25, 'Sei agora que não tornareis a ver a minha face, todos vós, por entre os quais andei pregando o Reino de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32003, 1183, 26, 'Portanto, hoje eu protesto diante de vós que sou inocente do sangue de todos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32004, 1183, 27, 'porque nada omiti no anúncio que vos fiz dos desígnios de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32005, 1183, 28, 'Cuidai de vós mesmos e de todo o rebanho sobre o qual o Espírito Santo vos constituiu bispos, para pastorear a Igreja de Deus, que ele adquiriu com o seu próprio sangue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32006, 1183, 29, 'Sei que depois da minha partida se introduzirão entre vós lobos cruéis, que não pouparão o rebanho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32007, 1183, 30, 'Mesmo dentre vós surgirão homens que hão de proferir doutrinas perversas, com o intento de arrebatarem após si os discípulos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32008, 1183, 31, 'Vigiai! Lembrai-vos, portanto, de que por três anos não cessei, noite e dia, de admoestar, com lágrimas, a cada um de vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32009, 1183, 32, 'Agora eu vos encomendo a Deus e à palavra da sua graça, àquele que é poderoso para edificar e dar a herança com os santificados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32010, 1183, 33, 'De ninguém cobicei prata, nem ouro, nem vestes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32011, 1183, 34, 'Vós mesmos sabeis: estas mãos proveram às minhas necessidades e às dos meus companheiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32012, 1183, 35, 'Em tudo vos tenho mostrado que assim, trabalhando, convém acudir os fracos e lembrar-se das palavras do Senhor Jesus, porquanto ele mesmo disse: É maior felicidade dar que receber!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32013, 1183, 36, 'A essas palavras, ele se pôs de joelhos a orar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32014, 1183, 37, 'Derramaram-se em lágrimas e lançaram-se ao pescoço de Paulo para abraçá-lo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32015, 1183, 38, 'aflitos, sobretudo pela palavra que tinha dito: “Já não vereis a minha face”. Em seguida, acompanharam-no até o navio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32016, 1184, 1, 'Depois de nos separarmos dele, embarcamos e fomos em direção a Cós, e no dia seguinte a Rodes e dali a Pátara.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32017, 1184, 2, 'Encontramos aí um navio que ia partir para a Fenícia. Entramos e seguimos viagem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32018, 1184, 3, 'Quando estávamos à vista de Chipre, deixando-a à esquerda, continuamos rumo à Síria e aportamos em Tiro, onde o navio devia ser descarregado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32019, 1184, 4, 'Como achássemos uns discípulos, detive­mo-nos com eles por sete dias. Eles, sob a inspiração do Espírito, aconselhavam Paulo que não subisse a Jerusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32020, 1184, 5, 'Mas, passados que foram esses dias, partimos e seguimos a nossa via­gem. Todos eles com suas mulheres e filhos acompanharam-nos até fora da cidade. Ajoelhados na praia, fizemos a nossa oração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32021, 1184, 6, 'Despe­dimo-nos então e embarcamos, enquanto eles voltaram para suas casas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32022, 1184, 7, 'Navegando, fomos de Tiro a Ptolemaida, onde saudamos os irmãos, passando um dia com eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32023, 1184, 8, 'Partindo no dia seguinte, chegamos a Cesareia e, entrando na casa de Filipe, o Evangelista, que era um dos sete, ficamos com ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32024, 1184, 9, 'Tinha quatro filhas virgens que profetizavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32025, 1184, 10, 'Já estávamos aí fazia alguns dias, quando chegou da Judeia um profeta, chamado Ágabo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32026, 1184, 11, 'Veio ter conosco, tomou o cinto de Paulo e, amarrando-se com ele pés e mãos, disse: “Isto diz o Espírito Santo: assim os judeus em Jerusalém ligarão o homem a quem pertence este cinto e o entregarão às mãos dos pagãos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32027, 1184, 12, 'A estas palavras, nós e os fiéis que eram daquele lugar, rogamos-lhe que não subisse a Jerusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32028, 1184, 13, 'Paulo, porém, respondeu: “Por que chorais e me magoais o coração? Pois eu estou pronto não só a ser preso, mas também a morrer em Jerusalém pelo nome do Senhor Jesus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32029, 1184, 14, 'Como não pudéssemos persuadi-lo, desistimos, dizendo: “Faça-se a vontade do Senhor!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32030, 1184, 15, 'Depois desses dias, terminados os preparativos, subimos a Jerusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32031, 1184, 16, 'Foram também conosco alguns dos discípulos de Cesareia, que nos levaram à casa de Menason de Chipre, um antigo discípulo em cuja casa nos devíamos hospedar. Paulo em Jerusalém');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32032, 1184, 17, 'À nossa chegada em Jerusalém, os irmãos nos receberam com alegria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32033, 1184, 18, 'No dia seguinte, Paulo dirigiu-se conosco à casa de Tiago, onde todos os anciãos se reuniram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32034, 1184, 19, 'Tendo-os saudado, contou-lhes uma por uma todas as coisas que Deus fizera entre os pagãos por seu ministério.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32035, 1184, 20, 'Ouvindo isso, glorificaram a Deus e disseram a Paulo: “Bem vês, irmão, quantos milhares de judeus abraçaram a fé sem abandonar seu zelo pela Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32036, 1184, 21, 'Eles têm ouvido dizer de ti que ensinas os judeus, que vivem entre os gentios, a deixarem Moisés, dizendo que não devem circuncidar os seus filhos nem observar os costumes (mosaicos).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32037, 1184, 22, 'Que se há de fazer? Sem dúvida, saberão de tua chegada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32038, 1184, 23, 'Faze, pois, o que te vamos dizer. Temos aqui quatro homens que têm um voto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32039, 1184, 24, 'Toma-os contigo, faze com eles os ritos da purificação e paga por eles (a oferta obrigatória) para que rapem a cabeça. Então, todos saberão que é falso quanto de ti ouviram, mas que também tu guardas a Lei.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32040, 1184, 25, 'Mas a respeito dos que creram dentre os gentios, já escrevemos, ordenando que se abstenham do que for sacrificado aos ídolos, do sangue, da carne sufocada e da fornicação”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32041, 1184, 26, 'Então, Paulo acompanhou aqueles homens no dia seguinte e, purificando-se com eles, entrou no templo e fez aí uma declaração do termo do voto, findo o qual se devia oferecer um sacrifício a favor de cada um deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32042, 1184, 27, 'Ao fim dos sete dias, os judeus, vindos da Ásia, viram Paulo no templo e amotinaram todo o povo. Lançando-lhe as mãos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32043, 1184, 28, 'gritavam: “Ó judeus, valei-nos! Este é o homem que por toda parte prega a todos contra o povo, a Lei e o templo. Além disso, introduziu até gregos no templo e profanou o lugar santo”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32044, 1184, 29, 'É que tinham visto Trófimo, de Éfeso, com ele na cidade, e pensavam que Paulo o tivesse introduzido no templo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32045, 1184, 30, 'Alvoroçou-se toda a cidade com grande ajuntamento de povo. Agarraram Paulo e arrastaram-no para fora do templo, cujas portas se fecharam imediatamente.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32046, 1184, 31, 'Como quisessem matá-lo, o tribuno da coorte foi avisado de que toda a Jerusalém estava amotinada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32047, 1184, 32, 'Ele tomou logo soldados e ofi­ciais e correu aos manifestantes. Estes, ao avistarem o tribuno e os soldados, cessaram de espancar Paulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32048, 1184, 33, 'Aproximando-se então o tribu­no, prendeu-o e mandou acorrentá-lo com duas cadeias. Perguntou então quem era e o que havia feito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32049, 1184, 34, 'Na multidão todos gritavam de tal modo que, não podendo apurar a verdade por causa do tumulto, mandou que fosse recolhido à cidadela.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32050, 1184, 35, 'Quando Paulo chegou às escadas, foi carregado pelos soldados, por causa do furor da multidão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32051, 1184, 36, 'O povo o seguia em massa dizendo aos gritos: “À morte!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32052, 1184, 37, 'Quando estava para ser introduzido na fortaleza, Paulo perguntou ao tribuno: “É-me permitido dizer duas palavras?” Este respondeu: “Sabes o grego!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32053, 1184, 38, 'Não és tu, portanto, aquele egípcio que há tempos levantou um tumulto e conduziu ao deserto quatro mil extremistas?”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32054, 1184, 39, 'Paulo replicou: “Eu sou judeu, natural de Tarso, na Cilícia, cidadão dessa ilustre cidade. Mas rogo-te que me permitas falar ao povo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32055, 1184, 40, 'O tribuno lho permitiu. Paulo, em pé nos degraus, acenou ao povo com a mão e se fez um grande silêncio. Falou em língua hebraica do seguinte modo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32056, 1185, 1, '“Irmãos e pais, ouvi o que vos tenho a dizer em minha defesa.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32057, 1185, 2, 'Quando ouviram que lhes falava em língua hebraica, escutaram-no com a maior atenção.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32058, 1185, 3, 'Continuou ele: “Eu sou judeu, nasci em Tarso da Cilícia, mas criei-me nesta cidade, instruí-me aos pés de Gamaliel, em toda a observância da lei de nossos pais, partidário en­tusias­ta da causa de Deus como todos vós também o sois no dia de hoje.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32059, 1185, 4, 'Eu persegui de morte essa doutrina, prendendo e metendo em cárceres homens e mulheres.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32060, 1185, 5, 'O sumo sacerdote e todo o conselho dos anciãos me são testemunhas. E foi deles que também recebi cartas para os irmãos de Damasco, para onde me dirigi, com o fim de prender os que lá se achassem e trazê-los a Jerusalém, para que fossem castigados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32061, 1185, 6, 'Ora, estando eu a caminho, e aproximando-me de Damasco, pelo meio-dia, de repente me cercou uma forte luz do céu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32062, 1185, 7, 'Caí por terra e ouvi uma voz que me dizia: Saulo, Saulo, por que me persegues?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32063, 1185, 8, 'Eu repliquei: Quem és tu, Senhor? A voz me disse: Eu sou Jesus de Nazaré, a quem tu persegues.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32064, 1185, 9, 'Os meus companheiros viram a luz, mas não ouviram a voz de quem falava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32065, 1185, 10, 'Então, eu disse: Senhor, que devo fazer? E o Senhor me respondeu: Levanta-te, vai a Damasco e lá te será dito tudo o que deves fazer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32066, 1185, 11, 'Como eu não pudesse ver por causa da intensidade daquela luz, guiado pela mão dos meus companheiros, cheguei a Damasco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32067, 1185, 12, 'Um certo Ananias, homem piedoso e observador da Lei, muito bem conceituado entre todos os judeus daquela cidade,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32068, 1185, 13, 'veio ter comigo e disse-me: Irmão Saulo, recobra a tua vista. Naquela mesma hora pude enxergá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32069, 1185, 14, 'Continuou ele: O Deus de nossos pais te predes­tinou para que conhecesses a sua vontade, visses o Justo e ouvisses a palavra da sua boca,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32070, 1185, 15, 'pois lhe serás, diante de todos os homens, testemunha das coisas que tens visto e ouvido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32071, 1185, 16, 'E agora, por que tardas? Levanta-te. Recebe o batismo e purifica-te dos teus pecados, invocando o seu nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32072, 1185, 17, 'Voltei para Jerusalém e, orando no templo, fui arrebatado em êxtase.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32073, 1185, 18, 'E vi Jesus que me dizia: Apressa-te e sai logo de Jerusalém, porque não receberão o teu testemunho a meu respeito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32074, 1185, 19, 'Eu repliquei: Senhor, eles sabem que eu encarcerava e açoitava com varas nas sinagogas os que creem em ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32075, 1185, 20, 'E quando se derramou o sangue de Estêvão, tua testemunha, eu estava presente, consentia nisso e guardava os mantos dos que o matavam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32076, 1185, 21, 'Mas ele me respondeu: Vai, porque eu te enviarei para longe, às nações...”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32077, 1185, 22, 'Haviam-no escutado até essa palavra. Então, levantaram a voz: “Tira do mundo esse homem! Não é digno de viver!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32078, 1185, 23, 'Como vocife­rassem, arrojassem de si as vestes e lançassem pó ao ar,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32079, 1185, 24, 'o tribuno mandou recolhê-lo à cidadela, açoitá-lo e submetê-lo a torturas, para saber por que causa clamavam assim contra ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32080, 1185, 25, 'Quando o iam amarrando com a correia, Paulo perguntou a um centurião que estava presente: “É permitido açoitar um cidadão romano que nem sequer foi julgado?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32081, 1185, 26, 'Ao ouvir isso, o centurião foi ter com o tribuno e avisou-o: “Que vais fazer? Este homem é cidadão romano”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32082, 1185, 27, 'Veio o tribuno e perguntou-lhe: “Dize-me, és romano?” – “Sim”, res­pondeu-lhe.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32083, 1185, 28, 'O tribuno replicou: “Eu adquiri este direito de cidadão por grande soma de dinheiro.” Paulo respondeu: “Pois eu o sou de nascimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32084, 1185, 29, 'Apartaram-se então dele os que iam torturá-lo. O tribuno alarmou-se porque o mandara acorrentar, sendo ele um cidadão romano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32085, 1185, 30, 'No dia seguinte, querendo saber com mais exatidão de que os judeus o acusavam, soltou-o e ordenou que se reunissem os sumos sacerdotes e todo o Grande Conselho. Trouxe Paulo e o mandou comparecer diante deles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32086, 1186, 1, 'Paulo, fitando os olhos nos membros do conselho, disse: “Irmãos, eu tenho procedido diante de Deus com toda a boa cons­ciência até o dia de hoje...”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32087, 1186, 2, 'Mas Ananias, sumo sacerdote, mandou aos que estavam ao seu lado que lhe batessem na boca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32088, 1186, 3, 'Então, Paulo lhe disse: “Deus te ferirá também a ti, hipócrita! Tu estás aí assentado para julgar-me segundo a Lei, e contra a Lei mandas que eu seja ferido?”*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32089, 1186, 4, 'Os assistentes disseram: “Tu injurias o sumo sacerdote de Deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32090, 1186, 5, 'Res­pondeu Paulo: “Não sabia, irmãos, que é o sumo sacerdote, pois está escrito: Não falarás mal do príncipe do teu povo” (Ex 22,28).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32091, 1186, 6, 'Paulo sabia que uma parte do Sinédrio era de saduceus e a outra de fariseus e disse em alta voz: “Irmãos, eu sou fariseu, filho de fariseus. Por causa da minha esperança na ressurreição dos mortos é que sou julgado”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32092, 1186, 7, 'Ao dizer ele estas palavras, houve uma discussão entre os fariseus e os saduceus, e dividiu-se a assembleia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32093, 1186, 8, '(Pois os saduceus afirmam não haver ressurreição, nem anjos, nem espíritos, mas os fariseus admitem uma e outra coisa.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32094, 1186, 9, 'Originou-se, então, grande vozearia. Levantaram-se alguns es­cri­bas dos fariseus e contestaram ruidosamente: “Não achamos mal algum neste homem. (Quem sabe) se não lhe falou algum espírito ou um anjo...”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32095, 1186, 10, 'A discussão fazia-se sempre mais violenta. O tribuno temeu que Paulo fosse despedaçado por eles e mandou aos soldados que descessem, que o tirassem do meio deles e o levassem para a cidadela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32096, 1186, 11, 'Na noite seguinte, apareceu-lhe o Senhor e lhe disse: “Coragem! Deste testemunho de mim em Jerusalém, assim importa também que o dês em Roma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32097, 1186, 12, 'Quando amanheceu, coliga­ram-se alguns judeus e juraram com imprecações não comer nem beber nada, enquanto não matassem Paulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32098, 1186, 13, 'Eram mais de quarenta as pes­soas que fizeram essa conjuração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32099, 1186, 14, 'Foram apresentar-se aos sumos sacerdotes e aos cidadãos, dizendo: “Juramos solenemente nada comer enquanto não matarmos Paulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32100, 1186, 15, 'Vós, pois, ide com o conselho requerer do tribuno que o conduza à vossa presença, como se houvésseis de investigar com mais precisão a sua causa; e nós estamos prontos para matá-lo durante o trajeto”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32101, 1186, 16, 'Mas um filho da irmã de Paulo, inteirado da cilada, dirigiu-se à cidadela e o comunicou a Paulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32102, 1186, 17, 'Este chamou a si um dos centu­riões e disse-lhe: “Leva este moço ao tribuno, porque tem alguma coisa a lhe transmitir”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32103, 1186, 18, 'Ele o introduziu à presença do tribuno e lhe disse: “O preso Paulo rogou-me que trouxesse este moço à tua presença, porque tem alguma coisa a dizer-te”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32104, 1186, 19, 'O tribuno, tomando-o pela mão, retirou-se com ele à parte e perguntou: “Que tens a dizer-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32105, 1186, 20, 'Respondeu-lhe ele: “Os judeus têm combinado rogar-te amanhã que apresentes Paulo ao Grande Conselho, como se houvessem de inquirir dele alguma coisa com mais precisão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32106, 1186, 21, 'Mas tu não creias, porque mais de quarenta homens dentre eles lhe armam traição. Juraram solenemente nada comer, nem beber, enquanto não o matarem. Eles já estão preparados e só esperam a tua permissão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32107, 1186, 22, 'Então, o tribuno despediu o moço, ordenando-lhe que a ninguém dissesse que o havia avisado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32108, 1186, 23, 'Depois disso, chamou ele dois centuriões e disse-lhes: “Preparai duzentos soldados, setenta cavaleiros e duzentos lanceiros para irem a Cesareia à terceira hora da noite.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32109, 1186, 24, 'Aprontai também cavalgaduras para Paulo, que tendes de levar com toda a segurança ao governador Félix”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32110, 1186, 25, 'E ele escreveu uma carta nestes termos:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32111, 1186, 26, '“Cláudio Lísias ao excelentíssimo governador Félix, saudações!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32112, 1186, 27, 'Esse homem foi preso pelos judeus e estava a ponto de ser morto por eles, quando eu, sobrevindo com a tropa, o livrei, ao saber que era romano.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32113, 1186, 28, 'Então, querendo saber a causa por que o acusavam, levei-o ao Grande Conselho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32114, 1186, 29, 'Soube que era acusado sobre questões da Lei deles, sem haver nele delito algum que merecesse morte ou prisão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32115, 1186, 30, 'Mas, como tivesse chegado a mim a notícia das traições que maquinavam contra ele, envio-o com urgência a ti, intimando também aos acusadores que recorram a ti”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32116, 1186, 31, 'Os soldados, conforme lhes fora ordenado, tomaram Paulo e o levaram de noite a Antipátride.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32117, 1186, 32, 'No dia seguinte, voltaram para a guarnição, deixando que os soldados da cavalaria o escoltassem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32118, 1186, 33, 'À sua chegada a Cesareia, entregaram ao governador a carta e apresentaram-lhe também Paulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32119, 1186, 34, 'Ele, depois de lê-la e perguntar de que província ele era, sabendo que era da Cilícia, disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32120, 1186, 35, '“Eu te ouvirei quando chegarem teus acusadores.” Mandou, então, que Paulo fosse guardado no pretório de Herodes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32121, 1187, 1, 'Cinco dias depois, desceu o sumo sacerdote Ananias com alguns an­ciãos e Tertulo, advo­gado. Compareceram eles ante o governador para acusar Paulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32122, 1187, 2, 'Este foi citado e Tertulo começou a acusá-lo nestes termos: “Graças a ti nós gozamos de paz, e pela tua providência se têm corrigido muitos abusos em nossa nação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32123, 1187, 3, 'Nós o reconhecemos em todo o tempo e lugar, excelentíssimo Félix, com toda a gratidão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32124, 1187, 4, 'Mas, para não te enfadar por mais tempo, rogo-te que, na tua bondade, nos ouças por um momento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32125, 1187, 5, 'Encontramos este homem, uma peste, um indivíduo que fomenta discórdia entre os judeus no mundo inteiro. É um dos líderes da seita dos nazarenos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32126, 1187, 6, 'Tentou mesmo profanar o templo. Nós, porém, o prendemos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32127, 1187, 7, '(Quisemos julgá-lo segundo a nossa Lei, mas, sobrevindo o tribuno Lísias, no-lo tirou das mãos com grande violência, ordenando que os seus acusadores comparecessem diante de ti.)*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32128, 1187, 8, 'Tu mesmo, interrogando-o, poderás verificar todas essas coisas de que nós o acusamos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32129, 1187, 9, 'Os judeus o apoiaram, confirmando que as coisas de fato eram assim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32130, 1187, 10, 'Depois disso, a um sinal do go­ver­nador, Paulo respondeu: “Sabendo eu que há muitos anos és governador desta nação, é com confiança que farei a minha defesa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32131, 1187, 11, 'Podes verificar que não há mais de doze dias que eu subi a Jerusalém para fazer minhas devoções.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32132, 1187, 12, 'Não me acharam disputando com alguém, nem amotinando o povo, quer no templo, quer nas sinagogas, ou na cidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32133, 1187, 13, 'Nem tampouco te podem provar as coisas de que agora me acusam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32134, 1187, 14, 'Reconheço na tua presença que, segundo a doutrina que eles chamam de sectária, sirvo a Deus de nossos pais, crendo em todas as coisas que estão escritas na Lei e nos profetas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32135, 1187, 15, 'Tenho esperança em Deus, como também eles esperam, de que há de haver a ressurreição dos justos e dos pecadores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32136, 1187, 16, 'Por isso, procuro ter sempre sem mácula a minha consciência diante de Deus e dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32137, 1187, 17, 'Depois de muitos anos (de ausência) vim trazer à minha nação esmolas e oferendas (rituais).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32138, 1187, 18, 'Nessa ocasião, acharam-me no templo, depois de uma purificação, sem aglome­ração e sem tumulto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32139, 1187, 19, 'Viram-me ali uns judeus vindos da Ásia, e estes é que deviam comparecer diante de ti e me acusar, se tivessem alguma queixa contra mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32140, 1187, 20, 'Ou digam estes aqui que crime terão achado em mim, quando eu compareci diante do Grande Conselho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32141, 1187, 21, 'A não ser esta única frase que proferi em voz alta no meio deles: Por causa da ressurreição dos mortos é que sou julgado hoje diante de vós!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32142, 1187, 22, 'Félix conhecia bem esta religião e, adiando a questão, disse: “Quando descer o tribuno Lísias, então examinarei a fundo a vossa questão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32143, 1187, 23, 'Ordenou ao centurião que o guardasse e o tratasse com brandura, sem proibir que os seus o servissem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32144, 1187, 24, 'Passados que foram alguns dias, veio Félix com sua mulher Drusila, que era judia. Chamou Paulo e ouvia-o falar da fé em Jesus Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32145, 1187, 25, 'Mas, como Paulo lhe falasse sobre a justiça, a castidade e o juízo futuro, Félix, todo atemorizado, disse-lhe: “Por ora, podes retirar-te. Na primeira ocasião, eu te chamarei.”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32146, 1187, 26, 'Esperava outrossim, ao mesmo tempo, que Paulo lhe desse algum dinheiro, pelo que o mandava chamar com frequência e se entretinha com ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32147, 1187, 27, 'Decorridos dois anos, Félix teve por sucessor a Pórcio Festo. Querendo, porém, agradar aos judeus, deixou Paulo na prisão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32148, 1188, 1, 'Três dias depois de sua chegada à província, Festo subiu de Cesareia a Jerusalém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32149, 1188, 2, 'Aí os sumos sacerdotes e os judeus mais notáveis foram ter com ele, acusando Paulo, e rogaram-lhe,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32150, 1188, 3, 'com insistência, como um favor, que o mandasse de volta para Jerusalém. É que queriam armar-lhe uma emboscada para o assassinarem no caminho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32151, 1188, 4, 'Festo, porém, respondeu que Paulo se achava detido em Cesareia e que ele mesmo partiria para lá dentro de poucos dias. E acrescentou:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32152, 1188, 5, '“Portanto, os que dentre vós são de prestígio desçam comigo; e se houver algum crime nesse homem, acusem-no”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32153, 1188, 6, 'Demorou-se entre eles cerca de oito ou dez dias e desceu a Cesareia. No dia seguinte, sentou-se no tribunal e citou Paulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32154, 1188, 7, 'Assim que este compareceu, rodearam-no os judeus que tinham descido de Jerusalém e acusaram-no de muitos e graves delitos que não podiam provar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32155, 1188, 8, 'Paulo alegava em sua defesa: “Em nada tenho pecado contra a Lei dos judeus, nem contra o templo, nem contra César!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32156, 1188, 9, 'Mas Festo, querendo agradar aos judeus, disse a Paulo: “Queres subir a Jerusalém e ser julgado ali diante de mim?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32157, 1188, 10, 'Paulo, porém, disse: “Estou perante o tribunal de César. É lá que devo ser julgado. Não fiz mal algum aos judeus, como bem sabes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32158, 1188, 11, 'Se lhes tenho feito algum mal ou coisa digna de morte, não recuso morrer. Mas, se nada há daquilo de que estes me acusam, ninguém tem o direito de entregar-me a eles. Apelo para César!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32159, 1188, 12, 'Então, Festo conferenciou com os seus assessores e respondeu: “Para César apelaste, a César irás”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32160, 1188, 13, 'Alguns dias depois, o rei Agripa e Bereni­ce desceram a Cesareia para saudar Festo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32161, 1188, 14, 'Como se demorassem ali muitos dias, Festo expôs ao rei o caso de Paulo: “Félix deixou preso aqui um certo homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32162, 1188, 15, 'Quando estive em Jerusalém, os sumos sacerdotes e os anciãos dos judeus vieram queixar-se dele comigo pedindo a sua condenação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32163, 1188, 16, 'Respondi-lhes que não era costume dos romanos condenar homem algum, antes de ter confrontado o acusado com os seus acusadores e antes de se lhes dar a liberdade de defender-se dos crimes que lhes são imputados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32164, 1188, 17, 'Compareceram aqui. E eu, sem demora, logo no dia seguinte, dei audiência e ordenei que conduzissem esse homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32165, 1188, 18, 'Apresentaram-se os seus acusadores, mas não o acusaram de nenhum dos crimes de que eu suspeitava.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32166, 1188, 19, 'Eram só desavenças entre eles a respeito da sua religião, e uma discussão a respeito de um tal Jesus, já morto, e que Paulo afirma estar vivo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32167, 1188, 20, 'Vi-me perplexo quanto ao modo de inquirir essas questões e perguntei-lhe se queria ir a Jerusalém e ser ali julgado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32168, 1188, 21, 'Mas, como Paulo apelou para o julgamento do imperador, mandei que fique detido até que o remeta a César”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32169, 1188, 22, 'Agripa disse então a Festo: “Eu também desejava ouvir esse homem”. Ao que ele respondeu: “Amanhã o ouvirás .');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32170, 1188, 23, 'No dia seguinte, Agripa e Berenice apre­sen­taram-se com grande pompa. E, entrando com os tribunos e as pessoas de mais relevo da cidade na sala de audiência, foi também Paulo introduzido por ordem de Festo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32171, 1188, 24, 'Festo tomou a palavra: “Ó rei, e todos vós que estais aqui presentes, vedes este homem contra quem os judeus em massa e com grandes gritos vieram reclamar a morte, tanto aqui como em Jerusalém.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32172, 1188, 25, 'Mas tenho averiguado que ele não fez coisa alguma digna de morte. Entretanto, havendo ele apelado para o imperador, determinei remeter-lho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32173, 1188, 26, 'Mas dele não tenho nada reprovável que possa escrever ao imperador, e por isso mandei-o comparecer diante de vós, mormente diante de tua majestade, para que essa audiência apure alguma coisa que eu possa escrever.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32174, 1188, 27, 'Pois não me parece razoável remeter um preso, sem mencionar ao mesmo tempo as acusações formuladas contra ele”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32175, 1189, 1, 'Agripa disse a Paulo: “Tens permissão de fazer a tua defe­sa”. Paulo então fez um gesto com a mão e começou a sua justificação:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32176, 1189, 2, '“Julgo-me feliz de poder hoje fazer a minha defesa, na tua presença, ó rei Agripa, de tudo quanto me acusam os judeus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32177, 1189, 3, 'porque tu conheces perfeitamente os seus costumes e controvérsias. Peço-te, pois, que me ouças com paciência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32178, 1189, 4, 'Minha vida, desde a minha primeira juventude, tem decorrido no meio de minha pátria e em Jerusalém, e é conhecida dos judeus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32179, 1189, 5, 'Sabem eles, desde longa data, e se quiserem poderão testemunhá-lo, que vivi segundo a seita mais rigorosa da nossa religião, isto é, como fariseu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32180, 1189, 6, 'Mas agora sou acusado em juízo, por esperar a promessa que foi feita por Deus a nossos pais,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32181, 1189, 7, 'e a qual as nossas doze tribos esperam alcançar, servindo a Deus noite e dia. Por essa esperança, ó rei, é que sou acusado pelos judeus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32182, 1189, 8, 'Que pensais vós? É coisa incrível que Deus ressuscite os mortos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32183, 1189, 9, 'Também eu acreditei que devia fazer a maior oposição ao nome de Jesus de Nazaré.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32184, 1189, 10, 'Assim procedi de fato em Jerusalém e tenho encerrado muitos irmãos em cárceres, havendo recebido para isso poder dos sumos sacerdotes; quando os sentenciavam à morte, eu dava a minha plena aprovação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32185, 1189, 11, 'Muitas vezes, perseguindo-os por todas as sinagogas, eu os maltratava para obrigá-los a blasfemar. Enfurecendo-me mais e mais contra eles, eu os perseguia até no estrangeiro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32186, 1189, 12, 'Nesse intuito, fui a Damasco, com poder e comissão dos sumos sacerdotes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32187, 1189, 13, 'Era meio-dia, ó rei. Eu estava a caminho quando uma luz do céu, mais fulgurante que o sol, brilhou em torno de mim e dos meus companheiros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32188, 1189, 14, 'Caímos todos nós por terra, e ouvi uma voz que me dizia­ em língua hebraica: Saulo, Saulo, por que me persegues? Dura coisa te é recalcitrar contra o aguilhão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32189, 1189, 15, 'Então, eu disse: Quem és, Senhor? O Senhor respondeu: Eu sou Jesus, a quem persegues.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32190, 1189, 16, 'Mas levanta-te e põe-te em pé, pois eu te apareci para te fazer ministro e testemunha das coisas que viste e de outras para as quais hei de manifes­tar-me a ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32191, 1189, 17, 'Escolhi-te do meio do povo e dos pagãos, aos quais agora te envio');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32192, 1189, 18, 'para abrir-lhes os olhos, a fim de que se convertam das trevas à luz e do poder de Satanás a Deus, para que, pela fé em mim, recebam perdão dos pecados e herança entre os que foram santificados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32193, 1189, 19, 'Desde então, ó rei, não fui desobediente à visão celestial.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32194, 1189, 20, 'Preguei primeiramente aos de Damasco e depois em Jerusalém e por toda a terra da Judeia, e aos pagãos, para que se arrependessem e se convertessem a Deus, fazendo dignas obras correspondentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32195, 1189, 21, 'Por isso, os judeus me prenderam no templo e tentaram matar-me.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32196, 1189, 22, 'Mas, assistido do socorro de Deus, permaneço vivo até o dia de hoje. Dou testemunho a pequenos e a grandes, nada dizendo senão o que os profetas e Moisés disseram que havia de acontecer,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32197, 1189, 23, 'a saber: que Cristo havia de padecer e seria o primeiro que, pela ressurreição dos mortos, havia de anunciar a luz ao povo judeu e aos pagãos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32198, 1189, 24, 'Dizendo ele essas coisas em sua defesa, Festo exclamou em alta voz: “Estás louco, Paulo! O teu muito saber tira-te o juízo”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32199, 1189, 25, 'Paulo, então, respondeu: “Não estou louco, excelentíssimo Festo, mas digo palavras de verdade e de prudência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32200, 1189, 26, 'Pois dessas coisas tem conhecimento o rei, em cuja presença falo com franqueza. Sei que nada disso lhe é oculto, porque nenhuma dessas coisas se fez ali ocul­tamente”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32201, 1189, 27, '“Crês, ó rei, nos profetas? Bem sei que crês!”');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32202, 1189, 28, 'Disse, então, Agripa a Paulo: “Por pouco não me persuades a fazer-me cristão!”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32203, 1189, 29, 'Respondeu Paulo: “Prouvera a Deus que, por pouco e por muito, não somente tu, senão também quantos me ouvem, se fizessem hoje tal qual eu sou... menos estas algemas!”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32204, 1189, 30, 'Então o rei, o governador, Berenice e os que estavam sentados com eles se levantaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32205, 1189, 31, 'Retirando-se, comentavam uns com os ou­tros: “Esse homem não fez coisa que mereça a morte ou prisão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32206, 1189, 32, 'Agripa ainda disse a Festo: “Ele poderia ser solto, se não tivesse apelado para César”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32207, 1190, 1, 'Logo que foi determinado que embarcássemos para a Itália, Paulo foi entregue com outros presos a um centurião da coorte Augusta, chamado Júlio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32208, 1190, 2, 'Embarcamos num navio de Adra­mito que devia costear as terras da Ásia, e levantamos âncora. Em nossa companhia estava Aristarco, macedônio de Tessa­lônica.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32209, 1190, 3, 'No dia seguinte, fazendo escala em Sidô­nia, Júlio, usando de bondade com Paulo, permitiu-lhe ir ver os seus amigos e prover-se do que havia de necessário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32210, 1190, 4, 'Dali, fazendo-nos ao mar, fomos navegan­do perto das costas de Chipre, por nos serem contrários os ventos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32211, 1190, 5, 'Tendo atravessado o mar da Cilícia e da Panfília, chegamos a Mira, cidade da Lícia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32212, 1190, 6, 'O centurião encontrou ali um navio de Alexandria, que rumava para a Itália, e fez-nos passar para ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32213, 1190, 7, 'Por muitos dias navegamos lentamente e com dificuldade até diante de Cnido, onde o vento não nos permitiu aportar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32214, 1190, 8, 'Fomos então costeando ao sul da ilha de Creta, junto ao cabo Salmona. Navegando com dificuldade ao longo da costa, chegamos afinal a um lugar, a que chamam Bons Portos, perto do qual está a cidade de Lasaia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32215, 1190, 9, 'Passara o tempo – já havia passado a época do jejum – e a navegação se tornava perigosa. Paulo advertiu-os:*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32216, 1190, 10, '“Amigos, vejo que a navegação não se fará sem perigo e sem graves danos, não somente ao navio e à sua carga, mas ainda às nossas vidas”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32217, 1190, 11, 'O centurião, porém, dava mais crédito ao piloto e ao mestre do que ao que Paulo dizia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32218, 1190, 12, 'O porto era impróprio para passar o inverno, pelo que a maior parte deles foi de parecer que se retornasse ao mar, na esperança de chegar a Fenice, para passar ali o inverno, por ser esse um porto de Creta, abrigado dos ventos do Sudeste e do Nordeste.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32219, 1190, 13, 'Soprava então brandamente o vento sul. Julgavam poder exe­cutar os seus planos. Levantaram a âncora e foram costeando de perto a ilha de Creta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32220, 1190, 14, 'Mas, não muito depois, veio do lado da ilha um tufão chamado Euroaquilão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32221, 1190, 15, 'Sem poder resistir à ventania, o navio foi arrebatado e deixamo-nos arrastar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32222, 1190, 16, 'Impelidos rapidamente para uma pequena ilha chamada Cauda, conseguimos, com muito esforço, recolher o batel.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32223, 1190, 17, 'Içaram-no e, depois, como meio de segurança, cingiram o navio com cabos. Então, temendo encalhar em Sirte, arria­ram as velas e entregaram-se à mercê dos ventos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32224, 1190, 18, 'No dia seguinte, sendo a tempestade ain­da mais violenta, atiraram fora a carga.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32225, 1190, 19, 'No terceiro dia, atiramos para fora com as nossas próprias mãos os acessórios do navio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32226, 1190, 20, 'Ora, não aparecendo por muitos dias nem sol nem estrelas e sendo batidos por forte tempestade, tínhamos por fim perdido toda a esperança de sermos salvos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32227, 1190, 21, 'Desde muito tempo ninguém havia comido nada. Paulo levantou-se no meio deles e disse: “Amigos, deveras devíeis ter-me atendido e não ter saído de Creta, e assim evitar esse perigo e essas perdas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32228, 1190, 22, 'Agora, porém, vos admoesto a que tenhais coragem, pois não perecerá nenhum de vós, mas somente o navio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32229, 1190, 23, 'Esta noite apareceu-me um anjo de Deus, a quem pertenço e a quem sirvo, o qual me disse:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32230, 1190, 24, 'Não temas, Paulo. É necessário que compareças dian­te de César. Deus deu-te todos os que navegam contigo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32231, 1190, 25, 'Por isso, amigos, coragem! Eu confio em Deus que há de acontecer como me foi dito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32232, 1190, 26, 'Encalharemos em uma ilha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32233, 1190, 27, 'Já estávamos na décima quarta noite, pelo mar Adriático, quando, pela meia-noite, os marinheiros pressentiram que estavam perto de alguma terra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32234, 1190, 28, 'Então, atirando a sonda, perceberam que a profundidade era de vinte braças. Depois, um pouco mais adiante, viram que era de quinze braças.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32235, 1190, 29, 'Temendo que déssemos em algum recife, lançaram quatro âncoras da popa, esperando ansiosos que amanhecesse o dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32236, 1190, 30, 'Imediatamente, os marinhei­ros procuraram fugir e, sob o pretexto de largar as âncoras da proa, lançaram o bote ao mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32237, 1190, 31, 'Paulo disse ao centurião e aos soldados: “Se estes homens não permanecerem no navio, não podereis salvar-vos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32238, 1190, 32, 'Os soldados cortaram, então, os cabos do bote e deixaram-no cair.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32239, 1190, 33, 'Enquanto ia amanhecendo, Paulo encorajou a todos que comessem alguma coisa, e disse: “Já faz hoje catorze dias que estais em jejum, sem comer nada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32240, 1190, 34, 'Rogo-vos que comais alguma coisa, no interes­se de vossa vida, porque nem um cabelo da cabeça de alguém de vós perecerá”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32241, 1190, 35, 'Tendo dito isso, tomou do pão, pronunciou uma bênção na presença de todos e, depois de parti-lo, começou a comer.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32242, 1190, 36, 'Com isso, todos cobraram ânimo e puseram-se igualmente a comer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32243, 1190, 37, 'No navio éramos ao todo duzentas e setenta e seis pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32244, 1190, 38, 'Depois de terem comido à vontade, aliviaram o navio, atirando o trigo ao mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32245, 1190, 39, 'Afinal, clareou o dia. Os mari­nheiros não reconheceram a terra, mas viram uma enseada com uma praia, na qual tencionavam enca­lhar o navio, caso o pudessem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32246, 1190, 40, 'Levantaram as âncoras e largaram ao mesmo tempo as amarras dos lemes. Desfraldaram ao vento a vela mestra e rumaram para a praia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32247, 1190, 41, 'Mas deram numa língua de terra, e o navio encalhou aí. A proa, encalha­da, permanecia imóvel, ao mesmo tempo que a popa se abria com a força do mar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32248, 1190, 42, 'Os soldados tencionavam matar os presos, por temerem que algum deles fugisse a nado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32249, 1190, 43, 'O centu­rião, porém, querendo salvar Paulo, impediu que o fizessem e ordenou que aqueles que pudessem nadar fossem os primeiros a lançar-se ao mar e alcançar a terra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32250, 1190, 44, 'Os demais, uns atingiram a terra em tábuas, ou­tros em cima dos destroços do navio. Desse modo, todos conseguiram chegar à terra sãos e salvos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32251, 1191, 1, 'Estando já salvos, soubemos então que a ilha se chamava Malta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32252, 1191, 2, 'Os indígenas trataram-nos com extraordinária benevolência. Acenderam uma grande fogueira e em torno dela nos recolheram, em vista da chuva que caía e do frio que fazia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32253, 1191, 3, 'Paulo ajuntou um feixe de gravetos e o pôs na fogueira. Nisso uma víbora, que fugira ao fogo, mordeu-lhe a mão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32254, 1191, 4, 'Quando os indígenas viram a serpente pendendo de sua mão, diziam uns aos outros: “Sem dúvida, este homem é homicida, pois, tendo escapado ao mar, a justiça não o deixa viver”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32255, 1191, 5, 'Ele, porém, sacudindo a víbora no fogo, não sofreu mal algum.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32256, 1191, 6, 'Julgavam os indígenas que ele viesse a inchar, e que subitamente caísse morto. Mas, depois de esperarem muito tempo, vendo que não lhe acontecia mal nenhum, mudaram de parecer e disseram: “Ele é um deus”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32257, 1191, 7, 'Havia na vizinhança sítios pertencentes ao principal da ilha, chamado Públio. Este homem nos hospedou por três dias em sua casa, tratando-nos bem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32258, 1191, 8, 'Ora, o pai desse Públio achava-se acamado com febre e sofrendo de disen­teria. Paulo foi visitá-lo e, orando e impondo-lhe as mãos, sarou-o.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32259, 1191, 9, 'Depois desse fato, vieram ter com ele todos os habitantes da ilha que se achavam doentes, e foram curados. Tiveram assim conosco toda sorte de considerações e,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32260, 1191, 10, 'quando estávamos para navegar, proveram-nos do que era necessário.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32261, 1191, 11, 'Ao termo de três meses, embarcamos num navio de Ale­xandria, que havia passado o inverno na ilha. Esse navio levava por insígnias os Dióscuros.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32262, 1191, 12, 'Fizemos escala em Siracusa, onde ficamos três dias.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32263, 1191, 13, 'De lá, seguindo a costa, atingimos Régio. No dia seguinte, soprava o vento sul e chegamos em dois dias a Poz­zuoli.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32264, 1191, 14, 'Ali encontramos irmãos que nos rogaram que ficássemos na sua compa­nhia sete dias. Em seguida, nos dirigimos a Roma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32265, 1191, 15, 'Os irmãos de Roma foram informados de nossa chegada e vieram ao nosso encontro até o Foro de ápio e as Três Taver­nas. Ao vê-los, Paulo deu graças a Deus e se sentiu animado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32266, 1191, 16, 'Chegados que fomos a Roma, foi concedida licença a Paulo para que ficasse em casa própria com um soldado que o guardava.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32267, 1191, 17, 'Três dias depois, Paulo convocou os judeus mais notáveis. Estando reunidos, disse-lhes: “Irmãos, sem cometer nada contra o povo nem contra os costumes de nossos pais, fui preso em Jerusalém e entregue nas mãos dos romanos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32268, 1191, 18, 'Estes, depois de terem instruído o meu processo, quiseram soltar-me, visto não achar em mim crime algum que merecesse morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32269, 1191, 19, 'Mas, opondo-se a isso os judeus, vi-me obrigado a apelar para César, sem intentar contudo acusar de alguma coisa a minha nação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32270, 1191, 20, 'Por esse motivo, mandei chamar-vos, para vos ver e falar convosco. Porquanto, pela esperança de Israel, é que estou preso com esta corrente”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32271, 1191, 21, 'Responderam-lhe eles: “Não temos recebido carta alguma da Judeia, que fale em ti, nem de lá tem vindo irmão algum que nos dissesse ou falasse mal de ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32272, 1191, 22, 'Quiséramos, porém, que tu mesmo nos dissesses o que pensas, pois o que nós sabemos dessa seita é que em toda parte lhe fazem oposição”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32273, 1191, 23, 'Marcaram um dia e muitos foram procurá-lo no albergue onde se achava hospedado. A conversa durou desde a manhã até a tarde. Paulo expôs-lhes o Reino de Deus e apresentou, sempre de novo, testemunhos destinados a convencê-los a respeito de Jesus, baseando-se na Lei de Moisés e nos profetas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32274, 1191, 24, 'Alguns se persuadiram pelas suas palavras, outros não acreditaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32275, 1191, 25, 'Não estando concordes entre si, retiraram-se, enquanto Paulo lhes fazia esta reflexão: “Bem falou o Espírito Santo pelo profeta Isaías a vossos pais, dizendo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32276, 1191, 26, 'Vai a este povo e dize-lhes: Com vossos ouvidos ouvireis, sem compreender. Com vossos olhos olhareis, sem enxergar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32277, 1191, 27, 'Coração obstinado o deste povo, ouvido duro, olhos fechados, para não verem com a vista, nem ouvirem com o ouvido, nem entenderem com o coração, e se converterem e eu os curar (Is 6,9s).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32278, 1191, 28, 'Ficai, pois, sabendo que aos gentios é enviada agora esta salvação de Deus; e eles a ouvirão”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32279, 1191, 29, '[Havendo dito isso, saíram dali os judeus, discutindo animosamente entre si.]');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32280, 1191, 30, 'Paulo permaneceu por dois anos inteiros no aposento alugado, e recebia a todos os que vinham procurá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32281, 1191, 31, 'Pregava o Reino de Deus e ensinava as coisas a respeito do Senhor Jesus Cristo, com toda a liberdade e sem proibição.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32282, 1192, 1, 'Paulo, servo de Jesus Cristo, escolhido para ser apóstolo, reservado para anunciar o Evange­lho de Deus;* –');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32283, 1192, 2, 'este Evangelho Deus prometera outrora pelos seus profetas na Sagrada Escritura,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32284, 1192, 3, 'acerca de seu Filho Jesus Cristo, nosso Se­nhor, des­cendente de Davi quanto à carne,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32285, 1192, 4, 'que, segundo o Espírito de santidade, foi estabelecido Filho de Deus no poder por sua ressurreição dos mortos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32286, 1192, 5, 'e do qual temos recebido a graça e o apostolado, a fim de levar, em seu nome, todas as nações pagãs à obediência da fé,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32287, 1192, 6, 'entre as quais também vós sois os eleitos de Jesus Cristo –,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32288, 1192, 7, 'a todos os que estão em Roma, queridos de Deus, chamados a serem santos: a vós, graça e paz da parte de Deus, nosso Pai, e da parte do Senhor Jesus Cristo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32289, 1192, 8, 'Primeiramente, dou graças a meu Deus, por meio de Jesus Cristo, por todos vós, porque em todo o mundo é preconizada a vossa fé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32290, 1192, 9, 'Pois Deus, a quem sirvo em meu espírito, anunciando o Evangelho de seu Filho, me é testemunha de como vos menciono incessantemente em minhas orações.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32291, 1192, 10, 'A ele suplico, se for de sua vontade, conceder-me finalmente ocasião favorável de vos visitar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32292, 1192, 11, 'Desejo ardentemente ver-vos, a fim de comunicar-vos alguma graça espiritual, com que sejais confirmados,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32293, 1192, 12, 'ou melhor, para me encorajar juntamente convosco naquela vossa e minha fé que nos é comum.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32294, 1192, 13, 'Pois não quero que igno­reis, irmãos, como muitas vezes me tenho proposto ir ter convosco. (Eu queria recolher algum fruto entre vós, como entre os outros pagãos), mas até agora tenho sido impedido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32295, 1192, 14, 'Sou devedor a gregos e a bárbaros, a sábios e a simples.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32296, 1192, 15, 'Daí o ardente desejo que eu sinto de vos anunciar o Evangelho também a vós, que habitais em Roma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32297, 1192, 16, 'Com efeito, não me envergonho do Evangelho, pois ele é uma força vinda de Deus para a salvação de todo o que crê, ao judeu em primeiro lugar e depois ao grego.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32298, 1192, 17, 'Porque nele se revela a justiça de Deus, que se obtém pela fé e conduz à fé, como está escrito: O justo viverá pela fé (Hab 2,4).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32299, 1192, 18, 'A ira de Deus se manifesta do alto do céu contra toda a impiedade e perversidade dos homens, que pela injustiça aprisionam a verdade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32300, 1192, 19, 'Porquanto o que se pode conhecer de Deus eles o leem em si mesmos, pois Deus lhes revelou com evidência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32301, 1192, 20, 'Desde a criação do mundo, as perfeições invisíveis de Deus, o seu sempiterno poder e divindade, se tornam visíveis à inteligência, por suas obras; de modo que não se podem escusar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32302, 1192, 21, 'Porque, conhecendo a Deus, não o glorificaram como Deus, nem lhe deram graças. Pelo contrário, extraviaram-se em seus vãos pensamentos, e se lhes obscureceu o coração insensato.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32303, 1192, 22, 'Pretendendo-se sábios, tornaram-se estultos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32304, 1192, 23, 'Mudaram a majestade de Deus incorruptível em representações e figuras de homem corruptível, de aves, quadrú­pedes e répteis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32305, 1192, 24, 'Por isso, Deus os entregou aos desejos dos seus corações, à imun­dície, de modo que desonraram entre si os próprios corpos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32306, 1192, 25, 'Trocaram a verdade de Deus pela mentira, e adoraram e serviram à criatura em vez do Criador, que é bendito pelos séculos. Amém!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32307, 1192, 26, 'Por isso, Deus os entregou a paixões vergonhosas: as suas mu­lheres mudaram as relações natu­rais em relações contra a natureza.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32308, 1192, 27, 'Do mesmo modo também os homens, deixando o uso natural da mulher, arderam em desejos uns para com os outros, cometendo homens com homens a torpeza, e recebendo em seus corpos a paga devida ao seu desvario.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32309, 1192, 28, 'Como não se preocupassem em adquirir o conhecimento de Deus, Deus entregou-os aos sentimentos depravados, e daí o seu procedimento indigno.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32310, 1192, 29, 'São repletos de toda espécie de malícia, perversidade, cobiça, maldade; cheios de inveja, homicídio, contenda, engano, malignidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32311, 1192, 30, 'São difamadores, caluniadores, inimigos de Deus, insolentes, soberbos, altivos, inventores de maldades, rebeldes contra os pais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32312, 1192, 31, 'São insensatos, desleais, sem coração, sem misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32313, 1192, 32, 'Apesar de conhecerem o justo decreto de Deus que considera dignos de morte aqueles que fazem tais coisas, não somente as praticam, como também aplaudem os que as cometem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32314, 1193, 1, 'Assim, és inescusável, ó homem, quem quer que sejas, que te arvoras em juiz. Naquilo que julgas a outrem, a ti mesmo te condenas; pois tu, que julgas, fazes as mesmas coisas que eles.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32315, 1193, 2, 'Ora, sabemos que o juízo de Deus contra aqueles que fazem tais coisas cor­res­ponde à verdade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32316, 1193, 3, 'Tu, ó homem, que julgas os que praticam tais coisas, mas as cometes também, pensas que escaparás ao juízo de Deus?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32317, 1193, 4, 'Ou desprezas as riquezas da sua bondade, tolerância e longanimidade, desconhecendo que a bondade de Deus te convida ao arrependimento?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32318, 1193, 5, 'Mas, pela tua obstinação e coração impenitente, vais acumulando ira contra ti, para o dia da cólera e da revelação do justo juízo de Deus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32319, 1193, 6, 'que retribuirá a cada um segundo as suas obras:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32320, 1193, 7, 'a vida eterna aos que, perseverando em fazer o bem, buscam a glória, a honra e a imortalidade;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32321, 1193, 8, 'mas ira e indignação aos contumazes, rebeldes à verdade e seguidores do mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32322, 1193, 9, 'Tribulação e angústia sobrevirão a todo aquele que pratica o mal, primeiro ao judeu e depois ao grego;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32323, 1193, 10, 'mas glória, honra e paz a todo o que faz o bem, primeiro ao judeu e depois ao grego.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32324, 1193, 11, 'Porque, dian­te de Deus, não há distinção de pessoas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32325, 1193, 12, 'Todos os que sem a Lei peca­ram, sem aplicação da lei perecerão; e quantos pecaram sob o regime da lei, pela Lei serão julgados.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32326, 1193, 13, 'Porque diante de Deus não são justos os que ouvem a lei, mas serão tidos por justos os que praticam a lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32327, 1193, 14, 'Os pagãos, que não têm a Lei, fazendo naturalmente as coisas que são da Lei, embora não tenham a lei, a si mesmos servem de lei;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32328, 1193, 15, 'eles mostram que o objeto da lei está gravado nos seus corações, dando-lhes testemunho a sua consciên­cia, bem como os seus raciocínios, com os quais se acusam ou se escusam mutuamente.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32329, 1193, 16, 'Isso aparecerá claramente no dia em que, segundo o meu Evangelho, Deus julgar as ações secretas dos homens, por Jesus Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32330, 1193, 17, 'Mas tu, que és chamado judeu, e te apoias na lei, e te glorias de teu Deus;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32331, 1193, 18, 'tu, que conheces a sua vontade, e instruído pela lei sabes aquilatar a diferença das coisas;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32332, 1193, 19, 'tu, que te ufanas de ser guia dos cegos, luzeiro dos que estão em trevas,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32333, 1193, 20, 'doutor dos ignorantes, mestre dos simples, porque encontras na lei a regra da ciência e da verdade;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32334, 1193, 21, 'tu, que ensinas aos outros... não te ensinas a ti mesmo! Tu, que pregas que não se deve furtar, furtas!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32335, 1193, 22, 'Tu, que dizes que não se deve adulterar, adulteras! Tu, que abominas os ídolos, pi­lhas os seus templos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32336, 1193, 23, 'Tu, que te glorias da lei, desonras a Deus pela transgressão da lei!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32337, 1193, 24, 'Porque assim fala a Escritura: Por vossa causa o nome de Deus é blasfemado entre os pagãos (Is 52,5).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32338, 1193, 25, 'A circuncisão, em verdade, é proveitosa se guardares a Lei. Mas, se fores transgressor da Lei, serás, com tua circuncisão, um mero incir­cun­ciso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32339, 1193, 26, 'Se, portanto, o incircunciso observa os preceitos da lei, não será ele considerado como circunciso, apesar de sua incircuncisão?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32340, 1193, 27, 'Ainda mais, o incircunciso de nascimento, cumprindo a lei, te julgará que, com a letra e com a circuncisão, és transgressor da lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32341, 1193, 28, 'Não é verdadeiro judeu o que o é exteriormente, nem verdadeira circuncisão a que aparece exteriormente na carne.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32342, 1193, 29, 'Mas é judeu o que o é interiormente, e verdadeira circuncisão é a do coração, segundo o espírito da lei, e não segundo a letra. Tal judeu recebe o louvor não dos homens, e sim de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32343, 1194, 1, 'Em que, então, se avantaja o judeu? Ou qual é a utilidade da circuncisão?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32344, 1194, 2, 'Muita, em todos os aspectos. Principalmente porque lhes foram confiados os oráculos de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32345, 1194, 3, 'Mas então! Se alguns deles não foram fiéis, acaso a sua infidelidade destruirá a fidelidade de Deus?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32346, 1194, 4, 'De modo algum. Porque Deus há de ser reconhecido como veraz, e todo homem como mentiroso, segundo está escrito: Assim, serás reconhecido justo nas tuas palavras e vencerás, quando julgares (Sl 50,6).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32347, 1194, 5, 'Portanto, se a nossa injustiça real­ça a justiça de Deus, que diremos então? Para falar como os homens: não é injusto Deus quando descarrega a sua cólera?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32348, 1194, 6, 'Certo que não! De outra maneira, como julgaria Deus o mundo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32349, 1194, 7, 'Mas, se a verdade de Deus brilha ainda mais para a sua glória por minha mentira, por que serei eu ainda julgado pecador?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32350, 1194, 8, 'Então, por que não faríamos o mal para que dele venha o bem, expressão que os caluniadores, falsamente, nos atribuem? É justo que estes tais sejam condenados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32351, 1194, 9, 'E então? Avantajamo-nos a eles? De maneira alguma. Pois já demonstramos que judeus e gregos estão todos sob o domínio do pecado, como está escrito:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32352, 1194, 10, 'Não há nenhum justo, não há sequer um.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32353, 1194, 11, 'Não há um só que tenha inteligência, um só que busque a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32354, 1194, 12, 'Extraviaram-se todos e todos se perverteram. Não há quem faça o bem, não há sequer um (Sl 13,lss).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32355, 1194, 13, 'A sua garganta é um sepulcro aberto; com as suas línguas enganam; veneno de áspide está debaixo dos seus lábios (Sl 5,10; 139,4).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32356, 1194, 14, 'A sua boca está cheia de maldição e amargor (Sl 9,28).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32357, 1194, 15, 'Os seus pés são velozes para derramar sangue.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32358, 1194, 16, 'Há destruição e ruína nos seus caminhos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32359, 1194, 17, 'e não conhecem o caminho da paz (Is 59,7s).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32360, 1194, 18, 'Não há temor a Deus diante dos seus olhos (Sl 35,2).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32361, 1194, 19, 'Ora, sabemos que tudo o que diz a lei, di-lo aos que estão sujeitos à Lei, para que toda boca fique fechada e que o mundo inteiro seja reconhecido culpado diante de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32362, 1194, 20, 'Porquanto pela obser­vância da Lei nenhum homem será justificado diante dele, porque a Lei se limita a dar o conhecimento do pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32363, 1194, 21, 'Mas, agora, sem o concurso da lei, manifestou-se a justiça de Deus, atestada pela Lei e pelos profetas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32364, 1194, 22, 'Esta é a justiça de Deus pela fé em Jesus Cristo, para todos os fiéis (pois não há distinção;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32365, 1194, 23, 'com efeito, todos pecaram e todos estão privados da glória de Deus),');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32366, 1194, 24, 'e são justificados gratuitamente por sua graça; tal é a obra da redenção, realizada em Jesus Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32367, 1194, 25, 'Deus o destinou para ser, pelo seu sangue, vítima de propiciação mediante a fé. Assim, ele manifesta a sua justiça; porque no tempo de sua paciência, ele havia deixado sem castigo os pecados anteriores.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32368, 1194, 26, 'Assim, digo eu, ele manifesta a sua justiça no tempo presente, exercendo a justiça e justificando aquele que tem fé em Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32369, 1194, 27, 'Onde está, portanto, o motivo de se gloriar? Foi eliminado. Por qual Lei? Pela das obras? Não, mas pela Lei da fé.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32370, 1194, 28, 'Porque julgamos que o homem é justificado pela fé, sem as observâncias da lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32371, 1194, 29, 'Ou Deus só o é dos judeus? Não é também Deus dos pagãos? Sim, ele o é também dos pagãos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32372, 1194, 30, 'Porque não há mais que um só Deus, o qual justificará pela fé os circuncisos e, também pela fé, os incircuncisos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32373, 1194, 31, 'Destruímos então a Lei pela fé? De modo algum. Pelo contrário, damos-lhe toda a sua força.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32374, 1195, 1, 'Que vantagem diremos, pois, que conseguiu Abraão, nosso pai segundo a carne?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32375, 1195, 2, 'Porque, se Abraão foi justificado em virtude de sua observância, tem de que se gloriar; mas não diante de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32376, 1195, 3, 'Ora, que diz a Escritura? Abraão creu em Deus e isso lhe foi imputado em conta de justiça (Gn 15,6).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32377, 1195, 4, 'Ora, o salário não é gratificação, mas uma dívida ao trabalhador.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32378, 1195, 5, 'Mas aquele que sem obra alguma crê naquele que justifica o ímpio, a sua fé lhe é imputada em conta de justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32379, 1195, 6, 'É assim que Davi proclama bem-aventurado o homem a quem Deus atribui justiça, independentemente das obras:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32380, 1195, 7, 'Bem-aventurados aqueles cujas iniquidades foram perdoadas e cujos pecados foram cobertos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32381, 1195, 8, 'Bem-aventurado o homem ao qual o Senhor não imputou o seu pecado (Sl 31,1s).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32382, 1195, 9, 'Essa bem-aventurança é somente para os circuncisos, ou também para os incircuncisos? Dizemos, com efeito, que a fé foi imputada a Abraão em conta de justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32383, 1195, 10, 'Quando lhe foi ela imputada? Depois ou antes de sua circuncisão? Não depois, mas antes de ser circuncidado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32384, 1195, 11, 'Depois é que recebeu o sinal da circuncisão, como selo da justiça que tinha obtido pela fé antes de ser circuncidado. E assim se tornou o pai de todos os incircuncisos que creem, a fim de que também a estes seja imputada a justiça.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32385, 1195, 12, 'Pai também dos circuncisos, que não só trazem o sinal, mas que acompanham as pegadas da fé que nosso pai Abraão possuía antes de ser circuncidado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32386, 1195, 13, 'Com efeito, não foi em virtude da Lei que a promessa de herdar o mundo foi feita a Abraão ou à sua posteridade, mas em virtude da justiça da fé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32387, 1195, 14, 'Porque, se a heran­ça é reservada aos observadores da Lei, a fé já não tem razão de ser e a promessa fica sem valor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32388, 1195, 15, 'Porquanto a Lei produz a ira; e onde não existe Lei, não há transgressão.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32389, 1195, 16, 'Logo, é pela fé que alguém se torna herdeiro. Portanto, gratuitamente; e a promessa é assegurada a toda a posteridade de Abraão, não somente aos que procedem da Lei, mas também aos que possuem a fé de Abraão, que é pai de todos nós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32390, 1195, 17, 'Em verdade, está escrito: Eu te constituí pai de muitas nações (Gn 17,5); (nosso pai, portanto) diante dos olhos daquele em quem acreditou, o Deus que dá vida aos mortos e chama à existência as coisas que estão no nada.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32391, 1195, 18, 'Esperando, contra toda a esperança, Abraão teve fé e se tornou pai de muitas nações, segundo o que lhe fora dito: Assim será a tua descendência (Gn 15,5).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32392, 1195, 19, 'Não vacilou na fé, embora reconhecendo o seu próprio corpo sem vigor – pois tinha quase cem anos – e o seio de Sara igualmente amortecido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32393, 1195, 20, 'Ante a promessa de Deus, não vacilou, não desconfiou, mas conservou-se forte na fé e deu glória a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32394, 1195, 21, 'Estava plenamente convencido de que Deus era poderoso para cumprir o que prometera.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32395, 1195, 22, 'Eis por que sua fé lhe foi contada como justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32396, 1195, 23, 'Ora, não é só para ele que está escrito que a fé lhe foi imputada em conta de justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32397, 1195, 24, 'É também para nós, pois a nossa fé deve ser-nos imputada igualmente, porque cremos naquele que dos mortos ressuscitou Jesus, nosso Senhor,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32398, 1195, 25, 'o qual foi entregue por nossos pecados e ressuscitado para a nossa justificação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32399, 1196, 1, 'Justificados, pois, pela fé temos a paz com Deus, por meio de nosso Senhor Jesus Cristo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32400, 1196, 2, 'Por ele é que tivemos acesso a essa graça, na qual estamos firmes, e nos gloria­mos na esperança de possuir um dia a glória de Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32401, 1196, 3, 'Não só isso, mas nos gloriamos até das tribulações. Pois sabemos que a tribulação produz a paciência,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32402, 1196, 4, 'a paciência prova a fidelidade e a fidelidade, comprovada, produz a esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32403, 1196, 5, 'E a esperança não engana. Porque o amor de Deus foi derramado em nossos corações pelo Espírito Santo que nos foi dado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32404, 1196, 6, 'Com efeito, quando éramos ainda fracos, Cristo a seu tempo morreu pelos ímpios.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32405, 1196, 7, 'Com muita dificuldade, a gente aceitaria morrer por um justo, por um homem de bem, quiçá se consentiria em morrer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32406, 1196, 8, 'Mas eis aqui uma prova brilhante de amor de Deus por nós: quando éramos ainda pecadores, Cristo morreu por nós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32407, 1196, 9, 'Portanto, muito mais agora, que estamos justificados pelo seu sangue, se­remos por ele salvos da ira.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32408, 1196, 10, 'Se, quando éramos ainda inimigos, fomos recon­ciliados com Deus pela morte de seu Filho, com muito mais razão, estando já reconciliados, seremos salvos por sua vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32409, 1196, 11, 'Ainda mais: nós nos gloriamos em Deus por nosso Senhor Jesus Cristo, por quem desde agora temos recebido a reconciliação!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32410, 1196, 12, 'Por isso, como por um só homem entrou o pecado no mundo, e pelo pecado a morte, assim a morte passou a todo o gênero humano, porque todos pecaram...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32411, 1196, 13, 'De fato, até à Lei, o mal estava no mundo. Mas o mal não é imputado quando não há Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32412, 1196, 14, 'No entanto, desde Adão até Moisés reinou a morte, mesmo sobre aqueles que não pecaram à imitação da transgressão de Adão (o qual é figura do que havia de vir).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32413, 1196, 15, 'Mas, com o dom gratuito, não se dá o mesmo que com a falta. Pois se a falta de um só causou a morte de todos os outros, com muito mais razão o dom de Deus e o benefício da graça obtida por um só homem, Jesus Cristo, foram concedidos copiosamente a todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32414, 1196, 16, 'Nem aconteceu com o dom o mesmo que com as conse­quên­cias do pecado de um só: a falta de um só teve por consequência um veredicto de condenação, ao passo que, depois de muitas ofensas, o dom da graça atrai um juízo de justificação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32415, 1196, 17, 'Se pelo pecado de um só homem reinou a morte (por esse único homem), muito mais aqueles que receberam a abundância da graça e o dom da justiça reinarão na vida por um só, que é Jesus Cristo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32416, 1196, 18, 'Portanto, como pelo pecado de um só a condenação se estendeu a todos os homens, assim por um único ato de justiça recebem todos os homens a justificação que dá a vida.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32417, 1196, 19, 'Assim como pela desobediência de um só homem foram todos constituídos pecadores, assim pela obediência de um só todos se tornarão justos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32418, 1196, 20, 'Sobreveio a Lei para que abundasse o pecado. Mas onde abundou o pecado, superabun­dou a graça.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32419, 1196, 21, 'Assim como o pecado reinou para a morte, assim também a graça reinaria pela justiça para a vida eterna, por meio de Jesus Cristo, nosso Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32420, 1197, 1, 'Então, que diremos? Per­ma­ne­ceremos no pecado, para que haja abundância da graça?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32421, 1197, 2, 'De modo algum. Nós, que já morremos ao pecado, como poderíamos ainda viver nele?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32422, 1197, 3, 'Ou ignorais que todos os que fomos batizados em Jesus Cristo, fomos batizados na sua morte?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32423, 1197, 4, 'Fomos, pois, sepultados com ele na sua morte pelo batismo para que, como Cristo ressurgiu dos mortos pela glória do Pai, assim nós também vivamos uma vida nova.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32424, 1197, 5, 'Se fomos feitos o mesmo ser com ele por uma morte semelhante à sua, o seremos igualmente por uma comum ressurreição.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32425, 1197, 6, 'Sabemos que o nosso velho homem foi crucificado com ele, para que seja reduzido à impotência o corpo (outrora) subjugado ao pecado, e já não sejamos escravos do pecado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32426, 1197, 7, '(Pois quem morreu, libertado está do pecado.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32427, 1197, 8, 'Ora, se morremos com Cristo, cremos que viveremos também com ele,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32428, 1197, 9, 'pois sabemos que Cristo, tendo ressurgido dos mortos, já não morre, nem a morte terá mais domínio sobre ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32429, 1197, 10, 'Morto, ele o foi uma vez por todas pelo pecado; porém, está vivo, continua vivo para Deus!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32430, 1197, 11, 'Portanto, vós também considerai-vos mortos ao pecado, porém vivos para Deus, em Cristo Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32431, 1197, 12, 'Não reine, pois, o pecado em vosso corpo mortal, de modo que obedeçais aos seus apetites.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32432, 1197, 13, 'Nem ofereçais os vossos membros ao pecado, como instrumentos do mal. Oferecei-vos a Deus, como vivos, salvos da morte, para que os vossos membros sejam instrumentos do bem a seu serviço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32433, 1197, 14, 'O pecado já não vos dominará, porque agora não estais mais sob a lei, e sim sob a graça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32434, 1197, 15, 'Então? Havemos de pecar, pelo fato de não estarmos sob a Lei, mas sob a graça? De modo algum.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32435, 1197, 16, 'Não sabeis que, quando vos ofereceis a alguém para lhe obedecer, sois escravos daquele a quem obedeceis, quer seja do pecado para a morte, quer da obediência para a justiça?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32436, 1197, 17, 'Graças a Deus, porém, que, depois de terdes sido escravos do pecado, obedecestes de coração à regra da doutrina na qual tendes sido instruídos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32437, 1197, 18, 'E, libertados do pecado, vos tornastes servos da justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32438, 1197, 19, 'Vou-me servir de linguagem corrente entre os homens, por causa da fraqueza da vossa carne. Pois, como pusestes os vossos membros a serviço da impureza e do mal para cometer a iniquidade, assim ponde agora os vossos membros a serviço da justiça para chegar à santidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32439, 1197, 20, 'Quando éreis escravos do pecado, éreis livres a respeito da justiça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32440, 1197, 21, 'Que frutos produzíeis então? Frutos dos quais agora vos envergonhais. O fim deles é a morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32441, 1197, 22, 'Mas agora, libertados do pecado e feitos servos de Deus, tendes por fruto a santidade; e o termo é a vida eterna.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32442, 1197, 23, 'Porque o salário do pecado é a morte, enquanto o dom de Deus é a vida eterna em Cristo Jesus, nosso Senhor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32443, 1198, 1, 'Ignorais, irmãos (falo aos que têm conhecimentos jurídicos), que a Lei só tem domínio sobre o homem durante o tempo que vive?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32444, 1198, 2, 'Assim, a mulher casada está sujeita ao marido pela Lei enquanto ele vive; mas, se o marido morrer, fica desobrigada da Lei que a ligava ao marido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32445, 1198, 3, 'Por isso, enquanto viver o marido, se se tornar mulher de outro homem, será chamada adúltera. Porém, morrendo o marido, fica desligada da Lei, de maneira que, sem se tornar adúltera, poderá casar-se com outro homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32446, 1198, 4, 'Assim, meus irmãos, também vós estais mortos para a Lei, pelo sacrifício do corpo de Cristo, para pertencerdes a outrem, àquele que ressuscitou dentre os mortos, a fim de que demos frutos para Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32447, 1198, 5, 'De fato, quando estávamos na carne, as paixões pecaminosas despertadas pela Lei operavam em nossos membros, a fim de frutificarmos para a morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32448, 1198, 6, 'Agora, mortos para essa Lei que nos mantinha sujeitos, dela nos temos libertado, e nosso serviço realiza-se conforme a renovação do Espírito e não mais sob a autoridade envelhecida da letra.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32449, 1198, 7, 'Que diremos, então? Que a Lei é pecado? De modo algum. Mas eu não conheci o pecado senão pela Lei. Porque não teria ideia da concupiscência, se a Lei não dissesse: Não cobiçarás (Ex 20,17).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32450, 1198, 8, 'Foi o pecado, portanto, que, aproveitando-se da ocasião que lhe foi dada pelo preceito, excitou em mim todas as concupiscências; porque, sem a Lei, o pecado estava morto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32451, 1198, 9, 'Quando eu estava sem a Lei, eu vivia; mas, sobrevindo o preceito, o pecado recobrou vida,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32452, 1198, 10, 'e eu morri. Assim o mandamento, que me devia dar a vida, conduziu-me à morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32453, 1198, 11, 'Porque o pecado, aproveitando da ocasião do mandamento, seduziu-me, e por ele me levou à morte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32454, 1198, 12, 'Por conseguinte, a Lei é santa e o mandamento é santo, e justo, e bom...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32455, 1198, 13, 'Então, o que é bom tornou-se causa de morte para mim? Decerto que não. Foi o pecado que, para se mostrar realmente pecado, acarretou para mim a morte por meio do que é bom, a fim de que, pelo mandamento, o pecado se fizesse excessivamente pecaminoso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32456, 1198, 14, 'Sabemos, de fato, que a Lei é espiritual, mas eu sou carnal, vendido ao pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32457, 1198, 15, 'Não entendo, absolutamente, o que faço, pois não faço o que quero; faço o que aborreço.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32458, 1198, 16, 'E, se faço o que não quero, reconheço que a Lei é boa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32459, 1198, 17, 'Mas, então, não sou eu que o faço, mas o pecado que em mim habita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32460, 1198, 18, 'Eu sei que em mim, isto é, na minha carne, não habita o bem, porque o querer o bem está em mim, mas não sou capaz de efetuá-lo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32461, 1198, 19, 'Não faço o bem que quereria, mas o mal que não quero.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32462, 1198, 20, 'Ora, se faço o que não quero, já não sou eu que faço, mas sim o pecado que em mim habita.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32463, 1198, 21, 'Encontro, pois, em mim esta Lei: quando quero fazer o bem, o que se me depara é o mal.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32464, 1198, 22, 'Deleito-me na Lei de Deus, no íntimo do meu ser.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32465, 1198, 23, 'Sinto, porém, nos meus membros outra Lei, que luta contra a Lei do meu espírito e me prende à Lei do pecado, que está nos meus membros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32466, 1198, 24, 'Homem infeliz que sou! Quem me livrará deste corpo que me acarreta a morte?...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32467, 1198, 25, 'Graças sejam dadas a Deus por Jesus Cristo, nosso Senhor!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32468, 1198, 26, 'Assim, pois, de um lado, pelo meu espírito, sou submisso à Lei de Deus; de outro lado, por minha carne, sou escravo da Lei do pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32469, 1199, 1, 'De agora em diante, pois, já não há nenhuma condenação para aqueles que estão em Jesus Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32470, 1199, 2, 'A Lei do Espírito de Vida me libertou, em Jesus Cristo, da Lei do pecado e da morte.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32471, 1199, 3, 'O que era impossível à Lei, visto que a carne a tornava impotente, Deus o fez. Enviando, por causa do pecado, o seu próprio Filho numa carne semelhante à do pecado, condenou o pecado na carne,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32472, 1199, 4, 'a fim de que a justiça, prescrita pela Lei, fosse realizada em nós, que vivemos não segundo a carne, mas segundo o espírito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32473, 1199, 5, 'Os que vivem segundo a carne gostam do que é carnal; os que vivem segundo o espírito apreciam as coisas que são do espírito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32474, 1199, 6, 'Ora, a aspiração da carne é a morte, enquanto a aspiração do espírito é a vida e a paz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32475, 1199, 7, 'Porque o desejo da carne é hostil a Deus, pois a carne não se submete à Lei de Deus, e nem o pode.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32476, 1199, 8, 'Os que vivem segundo a carne não podem agradar a Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32477, 1199, 9, 'Vós, porém, não viveis segundo a carne, mas segundo o Espírito, se realmente o Espírito de Deus habita em vós. Se alguém não possui o Espírito de Cristo, este não é dele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32478, 1199, 10, 'Ora, se Cristo está em vós, o corpo, em verdade, está morto pelo pecado, mas o Espírito vive pela justificação.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32479, 1199, 11, 'Se o Espírito daquele que ressuscitou Jesus dos mortos habita em vós, ele, que ressuscitou Jesus Cristo dos mortos, também dará a vida aos vossos corpos mortais, pelo seu Espírito que habita em vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32480, 1199, 12, 'Portanto, irmãos, não somos devedores da carne, para que vivamos segundo a carne.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32481, 1199, 13, 'De fato, se viverdes segundo a carne, haveis de morrer; mas, se pelo Espírito mortificardes as obras da carne, vivereis,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32482, 1199, 14, 'pois todos os que são conduzidos pelo Espírito de Deus são filhos de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32483, 1199, 15, 'Porquanto não recebestes um espírito de escravidão para viverdes ainda no temor, mas recebestes o espírito de adoção pelo qual clamamos: Aba! Pai!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32484, 1199, 16, 'O Espírito mesmo dá testemunho ao nosso espírito de que somos filhos de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32485, 1199, 17, 'E, se filhos, também herdeiros, herdeiros de Deus e coerdeiros de Cristo, contanto que soframos com ele, para que também com ele sejamos glorificados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32486, 1199, 18, 'Tenho para mim que os sofrimentos da presente vida não têm proporção alguma com a glória futura que nos deve ser manifestada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32487, 1199, 19, 'Por isso, a criação aguarda ansiosamente a manifestação dos filhos de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32488, 1199, 20, 'Pois a criação foi sujeita à vaidade (não voluntariamente, mas por vontade daquele que a sujeitou),*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32489, 1199, 21, 'todavia, com a esperança de ser também ela libertada do cativeiro da corrupção, para participar da gloriosa liberdade dos filhos de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32490, 1199, 22, 'Pois sabemos que toda a criação geme e sofre como que dores de parto até o presente dia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32491, 1199, 23, 'Não só ela, mas também nós, que temos as primícias do Espírito, gememos em nós mesmos, aguardando a adoção, a redenção do nosso corpo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32492, 1199, 24, 'Porque pela esperança é que fomos salvos. Ora, ver o objeto da esperança já não é esperança; porque o que alguém vê, como é que ainda o espera?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32493, 1199, 25, 'Nós, que esperamos o que não vemos, é em paciência que o aguardamos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32494, 1199, 26, 'Outrossim, o Espírito vem em auxílio à nossa fraqueza; porque não sabemos o que devemos pedir, nem orar como convém, mas o Espírito mesmo intercede por nós com gemidos inefáveis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32495, 1199, 27, 'E aquele que perscruta os corações sabe o que deseja o Espírito, o qual intercede pelos santos, segundo Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32496, 1199, 28, 'Aliás, sabemos que todas as coisas concorrem para o bem daqueles que amam a Deus, daqueles que são os eleitos, segundo os seus desígnios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32497, 1199, 29, 'Os que ele distinguiu de antemão, também os predes­tinou para serem conformes à imagem de seu Filho, a fim de que este seja o primogênito entre uma multidão de irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32498, 1199, 30, 'E aos que predestinou, também os chamou; e aos que chamou, também os justificou; e aos que justificou, também os glorificou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32499, 1199, 31, 'Que diremos depois disso? Se Deus é por nós, quem será contra nós?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32500, 1199, 32, 'Aquele que não poupou seu próprio Filho, mas que por todos nós o entregou, como não nos dará também com ele todas as coisas?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32501, 1199, 33, 'Quem poderia acusar os escolhidos de Deus? É Deus quem os justifica.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32502, 1199, 34, 'Quem os condenará? Cristo Jesus, que morreu, ou melhor, que ressuscitou, que está à mão direita de Deus, é quem intercede por nós!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32503, 1199, 35, 'Quem nos separará do amor de Cristo? A tribulação? A angústia? A perseguição? A fome? A nudez? O perigo? A espada?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32504, 1199, 36, 'Realmente, está escrito: Por amor de ti somos entregues à morte o dia inteiro; somos tratados como gado destinado ao matadouro (Sl 43,23).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32505, 1199, 37, 'Mas, em todas essas coisas, somos mais que vencedores pela virtude daquele que nos amou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32506, 1199, 38, 'Pois estou persuadido de que nem a morte, nem a vida, nem os anjos, nem os principados, nem o presente, nem o futuro, nem as potestades,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32507, 1199, 39, 'nem as alturas, nem os abismos, nem outra qualquer criatura nos poderá apartar do amor que Deus nos testemunha em Cristo Jesus, nosso Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32508, 1200, 1, 'Digo a verdade em Jesus Cristo, não minto; a minha consciência me dá testemunho pelo Espírito Santo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32509, 1200, 2, 'sinto grande pesar, incessante amargura no coração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32510, 1200, 3, 'Porque eu mesmo desejaria ser reprovado, separado de Cristo, por amor de meus irmãos, que são do mesmo sangue que eu, segundo a carne.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32511, 1200, 4, 'Eles são os israelitas; a eles foram dadas a adoção, a glória, as alianças, a Lei, o culto, as promes­sas*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32512, 1200, 5, 'e os patriarcas; deles des­cende Cristo, segundo a carne, o qual é, sobre todas as coisas, Deus bendito para sempre. Amém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32513, 1200, 6, 'Não quer dizer, porém, que a Palavra de Deus tenha falhado. Porque nem todos os que descendem de Israel são verdadeiros israelitas,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32514, 1200, 7, 'como nem todos os descendentes de Abraão são filhos de Abraão; mas: É em Isaac que terás uma des­cendência que trará o teu nome (Gn 21,12).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32515, 1200, 8, 'Isto é, não são os filhos da carne que são filhos de Deus, mas os filhos da promessa é que serão considerados como descendentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32516, 1200, 9, 'Realmente, a palavra da promessa é esta: Por este tempo virei, e Sara terá um filho (Gn 18,10).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32517, 1200, 10, 'E não somente ela, senão também Rebeca, que concebeu (dois filhos) de um só homem, Isaac, nosso patriar­ca.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32518, 1200, 11, 'Antes mesmo que fossem nascidos, e antes que tivessem feito bem ou mal algum (para que fosse confirmada a liberdade da escolha de Deus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32519, 1200, 12, 'que depende não das obras, mas daquele que chama), foi dito a Rebeca: O mais velho servirá o mais moço (Gn 25,23).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32520, 1200, 13, 'Como está escrito: Amei Jacó, porém aborreci Esaú (Ml 1,3).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32521, 1200, 14, 'Que diremos, pois? Haverá injustiça em Deus? De modo algum!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32522, 1200, 15, 'Porque ele disse a Moisés: Farei misericórdia a quem eu fizer miseri­córdia; terei compaixão de quem eu tiver compaixão (Ex 33,19).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32523, 1200, 16, 'Dessa forma, a escolha não depende daquele que quer, nem daquele que corre, mas da misericórdia de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32524, 1200, 17, 'Por isso, diz a Escritura ao faraó: Eis o motivo por que te suscitei, para mostrar em ti o meu poder e para que se anuncie o meu nome por toda a terra (Ex 9,16).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32525, 1200, 18, 'Portanto, ele tem misericórdia de quem quer, e endurece a quem quer.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32526, 1200, 19, 'Tu me dirás talvez: “Por que ele ainda se queixa? Quem pode resistir à sua vontade?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32527, 1200, 20, 'Mas quem és tu, ó homem, para contestar a Deus? Porventura o vaso de barro diz ao oleiro: “Por que me fizeste assim?”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32528, 1200, 21, 'Ou não tem o oleiro poder sobre o barro para fazer da mesma massa um vaso de uso nobre e outro de uso vulgar?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32529, 1200, 22, '(Onde, então, está a injustiça) em ter Deus, para mostrar a sua ira e manifestar o seu poder, suportado com muita paciência os objetos de ira preparados para a perdição,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32530, 1200, 23, 'mostrando as riquezas da sua glória para com os objetos de misericórdia que, de antemão, preparou para a glória?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32531, 1200, 24, '(Esses somos nós, que ele chamou não só dentre os judeus, mas também dentre os pagãos.) É o que ele diz em Oseias:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32532, 1200, 25, 'Chamarei meu povo ao que não era meu povo, e amada a que não era amada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32533, 1200, 26, 'E no lugar mesmo em que lhes foi dito: Vós não sois meu povo, ali serão chamados filhos de Deus vivo (Os 2,1).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32534, 1200, 27, 'A respeito de Israel, exclama Isaías: Ainda que o número de filhos de Israel fosse como a areia do mar, só um resto será salvo;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32535, 1200, 28, 'porque o Senhor realizará plenamente e prontamente a sua palavra sobre a terra (10,22s).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32536, 1200, 29, 'E ainda como predisse Isaías: Se o Senhor dos exércitos não nos tivesse deixado um rebento, ficaríamos como Sodoma, seríamos como Gomorra (Is 1,9).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32537, 1200, 30, 'Então, que diremos? Que os gentios, que não buscavam a justiça, alcançaram a justificação, a que vem da fé,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32538, 1200, 31, 'ao passo que Israel, que procurava uma Lei que desse a justificação, não a encontrou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32539, 1200, 32, 'Por quê? Porque Israel a buscava como fruto não da fé, e sim das obras. E tropeçou na pedra do escândalo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32540, 1200, 33, 'como está escrito: Eis que ponho em Sião uma pedra de escândalo, um rochedo que faz cair; quem nele crer não será confundido (Is 8,14; 28,16).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32541, 1201, 1, 'Irmãos, o desejo do meu coração e a súplica que dirijo a Deus por eles são para que se salvem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32542, 1201, 2, 'Pois lhes dou testemunho de que têm zelo por Deus, mas um zelo sem discerni­mento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32543, 1201, 3, 'Desconhecendo a justiça de Deus e procurando estabelecer a sua própria justiça, não se sujeitaram à justiça de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32544, 1201, 4, 'Porque Cristo é o fim da Lei, para justificar todo aquele que crê.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32545, 1201, 5, 'Ora, Moisés escreve da justiça que vem da Lei: O homem que a praticar viverá por ela (Lv 18,5).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32546, 1201, 6, 'Mas a justiça que vem da fé diz assim: “Não digas em teu coração: ‘Quem subirá ao céu?’. Isto é, para trazer do alto o Cristo;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32547, 1201, 7, 'ou: ‘Quem descerá ao abismo?’. Isto é, para fazer voltar Cristo dentre os mortos”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32548, 1201, 8, 'Que diz ela, afinal? A palavra está perto de ti, na tua boca e no teu coração (Dt 30,14). Essa é a palavra da fé, que pregamos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32549, 1201, 9, 'Portanto, se com tua boca confessares que Jesus é o Senhor, e se em teu coração creres que Deus o ressuscitou dentre os mortos, serás salvo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32550, 1201, 10, 'É crendo de coração que se obtém a justiça, e é professando com palavras que se chega à salvação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32551, 1201, 11, 'A Escritura diz: Todo o que nele crer não será confundido (Is 28,16).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32552, 1201, 12, 'Pois não há distinção entre judeu e grego, porque todos têm um mesmo Senhor, rico para com todos os que o invocam,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32553, 1201, 13, 'porque todo aquele que invocar o nome do Senhor será salvo (Jl 3,5).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32554, 1201, 14, 'Porém, como invocarão aquele em quem não têm fé? E como crerão naquele de quem não ouviram falar? E como ouvirão falar, se não houver quem pregue?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32555, 1201, 15, 'E como pregarão, se não forem enviados, como está escrito: Quão formosos são os pés daqueles que anunciam as boas-novas (Is 52,7)?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32556, 1201, 16, 'Mas não são todos que pres­taram ouvido à Boa-Nova. É o que exclama Isaías: Senhor, quem acredi­tou na nossa pregação (Is 53,1)?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32557, 1201, 17, 'Logo, a fé provém da pregação e a pregação se exerce em razão da palavra de Cristo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32558, 1201, 18, 'Pergunto, agora: Acaso não ouviram? Claro que sim! Por toda a terra correu a sua voz, e até os confins do mundo foram as suas palavras (Sl 18,5).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32559, 1201, 19, 'E pergunto ainda: Acaso Israel não o compreendeu? Já Moisés lhes havia dito: Eu vos despertarei ciúmes com um povo que não merece este nome; eu vos provocarei a ira contra uma nação insensata (Dt 32,21).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32560, 1201, 20, 'E Isaías se abalança a dizer: Fui achado pelos que não me buscavam; manifestei-me aos que não perguntavam por mim (Is 65,1).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32561, 1201, 21, 'Ao passo que a res­peito de Israel, ele diz: Todo o dia estendi as minhas mãos a um povo desobediente e teimoso (Is 65,2).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32562, 1202, 1, 'Pergunto, então: Acaso rejeitou Deus o seu povo? De maneira alguma. Pois eu mesmo sou israelita, descendente de Abraão, da tribo de Benjamim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32563, 1202, 2, 'Deus não repeliu o seu povo, que ele de antemão distinguiu! Desconheceis o que narra a Escritura, no episódio de Elias, quando este se queixava de Israel a Deus:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32564, 1202, 3, 'Senhor, mataram vossos profetas, destruíram vossos altares. Fiquei apenas eu, e ainda procuram tirar-me a vida (1Rs 19,10)?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32565, 1202, 4, 'Que lhe respondeu a voz divina? Reservei para mim sete mil homens, que não dobraram o joelho diante de Baal (1Rs 19,18).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32566, 1202, 5, 'É o que continua a acontecer no tempo presente: subsiste um resto, segundo a eleição da graça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32567, 1202, 6, 'E se é pela graça, já não o é pelas obras; de outra maneira, a graça cessaria de ser graça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32568, 1202, 7, 'Consequência? Que Israel não conseguiu o que procura. Os escolhidos, estes sim, o conseguiram. Quanto aos mais, foram obcecados,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32569, 1202, 8, 'como está escrito: Deus lhes deu um espírito de torpor, olhos para que não vejam e ouvidos para que não ouçam, até o dia presente (Dt 29,4).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32570, 1202, 9, 'Davi também o diz: A mesa se lhes torne em laço, em armadilha, em ocasião de tropeço, em justo castigo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32571, 1202, 10, 'A vista se lhes obscureça para não verem! Dobra-lhes o espinhaço sem cessar (Sl 68,23s)!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32572, 1202, 11, 'Pergunto ainda: Tropeçaram acaso para cair? De modo algum. Mas sua queda, tornando a salvação acessível aos pagãos, incitou-os à emulação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32573, 1202, 12, 'Ora, se o seu pecado ocasionou a riqueza do mundo, e a sua decadência a riqueza dos pagãos, que não fará a sua conversão em massa?!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32574, 1202, 13, 'Declaro-o a vós, homens de origem pagã: como apóstolo dos pagãos, eu procuro honrar o meu ministério,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32575, 1202, 14, 'com o intuito de, eventualmente, excitar à emulação os homens da minha raça e salvar alguns deles.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32576, 1202, 15, 'Porque, se de sua rejeição resultou a reconcilia­ção do mundo, qual será o efeito de sua reintegração, senão uma ressurreição dentre os mortos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32577, 1202, 16, 'Se as primícias são santas, também a massa o é; e se a raiz é santa, os ramos também o são.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32578, 1202, 17, 'Se alguns dos ramos foram cortados, e se tu, oliveira selvagem, foste enxer­tada em seu lugar e agora recebes seiva da raiz da oliveira,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32579, 1202, 18, 'não te envaideças nem menosprezes os ramos. Pois, se te gloriares, sabe que não és tu que sustentas a raiz, mas a raiz a ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32580, 1202, 19, 'Dirás, talvez: “Os ramos foram cortados para que eu fosse enxertada”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32581, 1202, 20, 'Sem dúvida! É pela incredulidade que foram cortados, ao passo que tu é pela fé que estás firme. Não te ensoberbeças, antes, teme.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32582, 1202, 21, 'Se Deus não poupou os ramos naturais, bem poderá não poupar a ti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32583, 1202, 22, 'Considera, pois, a bondade e a severidade de Deus: severidade para com aqueles que caíram, bondade para contigo, suposto que permaneças fiel a essa bondade; do contrário, também tu serás cortada.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32584, 1202, 23, 'E eles, se não persistirem na incredulidade, serão enxertados; pois Deus é poderoso para enxertá-los de novo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32585, 1202, 24, 'Se tu, cortada da oliveira de natureza selvagem, contra a tua natureza foste enxertada em boa oliveira, quanto mais eles, que são naturais, poderão ser enxertados na sua própria oliveira!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32586, 1202, 25, 'Não quero, irmãos, que igno­reis este mistério, para que não vos gabeis de vossa sabedoria: esta cegueira de uma parte de Israel só durará até que haja entrado a totalidade dos pagãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32587, 1202, 26, 'Então, Israel em peso será salvo, como está escrito: Virá de Sião o libertador, apartará de Jacó a impiedade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32588, 1202, 27, 'E esta será a minha alian­ça com eles, quando eu tirar os seus pecados (Is 59,20s; 27,9).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32589, 1202, 28, 'Se, quanto ao Evangelho, eles são inimigos de Deus, para proveito vosso, quanto à eleição eles são muito queridos por causa de seus pais.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32590, 1202, 29, 'Pois os dons e o chamado de Deus são irrevogáveis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32591, 1202, 30, 'Assim como vós antes fostes desobedientes a Deus, e agora obtivestes misericórdia com a deso­bediência deles,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32592, 1202, 31, 'assim eles são incrédulos agora, em consequência da misericórdia feita a vós, para que eles também mais tarde alcancem, por sua vez, a misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32593, 1202, 32, 'Deus encerrou a todos esses homens na desobediência para usar com todos de misericórdia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32594, 1202, 33, 'Ó abismo de riqueza, de sabedoria e de ciência em Deus! Quão impenetráveis são os seus juízos e inexploráveis os seus caminhos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32595, 1202, 34, 'Quem pode compreender o pensamento do Se­nhor? Quem jamais foi o seu conselheiro?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32596, 1202, 35, 'Quem lhe deu primeiro, para que lhe seja retribuído?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32597, 1202, 36, 'Dele, por ele e para ele são todas as coisas. A ele a glória por toda a eternidade! Amém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32598, 1203, 1, 'Eu vos exorto, pois, irmãos, pelas misericórdias de Deus, a ofere­cerdes vossos corpos em sacri­fício vivo, santo, agradável a Deus: é este o vosso culto espiritual.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32599, 1203, 2, 'Não vos conformeis com este mundo, mas transformai-vos pela renovação do vosso espírito, para que possais discernir qual é a vontade de Deus, o que é bom, o que lhe agrada e o que é perfeito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32600, 1203, 3, 'Em virtude da graça que me foi dada, recomendo a todos e a cada um: não façam de si próprios uma opinião maior do que convém, mas um conceito razoavelmente modesto, de acordo com o grau de fé que Deus lhes distribuiu.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32601, 1203, 4, 'Pois, como em um só corpo temos muitos membros e cada um dos nossos membros tem diferente função,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32602, 1203, 5, 'assim nós, embora sejamos muitos, formamos um só corpo em Cristo, e cada um de nós é membro um do outro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32603, 1203, 6, 'Temos dons diferentes, conforme a graça que nos foi conferida. Aquele que tem o dom da profecia, exerça-o conforme a fé.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32604, 1203, 7, 'Aquele que é chamado ao ministério, dedique-se ao ministério. Se tem o dom de ensinar, que ensine;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32605, 1203, 8, 'o dom de exortar, que exorte; aquele que distribui as esmolas, faça-o com simplicidade; aquele que preside, presida com zelo; aquele que exerce a misericórdia, que o faça com afabilidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32606, 1203, 9, 'Que vossa caridade não seja fingida. Aborrecei o mal, apegai-vos solidamente ao bem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32607, 1203, 10, 'Amai-vos mu­tuamente com afeição terna e fraternal. Adiantai-vos em honrar uns aos outros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32608, 1203, 11, 'Não relaxeis o vosso zelo. Sede fervorosos de espírito. Servi ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32609, 1203, 12, 'Sede alegres na esperança, pacientes na tribulação e perseverantes na oração.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32610, 1203, 13, 'Socorrei às necessidades dos fiéis. Esmerai-vos na prática da hospitalidade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32611, 1203, 14, 'Abençoai os que vos perse­guem; abençoai-os, e não os prague­jeis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32612, 1203, 15, 'Alegrai-vos com os que se alegram; chorai com os que choram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32613, 1203, 16, 'Vivei em boa harmonia uns com os ou­tros. Não vos deixeis levar pelo gosto das grandezas; afeiçoai-vos com as coisas modestas. Não sejais sábios aos vossos próprios olhos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32614, 1203, 17, 'Não pagueis a ninguém o mal com o mal. Aplicai-vos a fazer o bem diante de todos os homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32615, 1203, 18, 'Se for possível, quanto depender de vós, vivei em paz com todos os homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32616, 1203, 19, 'Não vos vingueis uns dos outros, caríssimos, mas deixai agir a ira de Deus, porque está escrito: A mim a vingança; a mim exercer a justiça, diz o Senhor (Dt 32,35).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32617, 1203, 20, 'Se o teu inimigo tiver fome, dá-lhe de comer; se tiver sede, dá-lhe de beber. Procedendo assim, amontoarás carvões em brasa sobre a sua cabeça (Pr 25,21s).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32618, 1203, 21, 'Não te deixes vencer pelo mal, mas triunfa do mal com o bem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32619, 1204, 1, 'Cada qual seja submisso às autoridades constituídas, porque não há autoridade que não venha de Deus; as que existem foram instituídas por Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32620, 1204, 2, 'Assim, aquele que resiste à autoridade opõe-se à ordem estabe­lecida por Deus; e os que a ela se opõem atraem sobre si a condenação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32621, 1204, 3, 'Em verdade, as autoridades ins­piram temor, não porém a quem pratica o bem, e sim a quem faz o mal! Queres não ter o que temer a autoridade? Faze o bem e terás o seu louvor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32622, 1204, 4, 'Porque ela é instrumento de Deus para teu bem. Mas, se fizeres o mal, teme, porque não é sem razão que leva a espada: é ministro de Deus, para fazer justiça e para exercer a ira contra aquele que pratica o mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32623, 1204, 5, 'Portanto, é necessário submeter-se, não somente por temor do castigo, mas também por dever de consciência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32624, 1204, 6, 'É também por essa razão que pagais os impostos, pois os magis­trados são ministros de Deus, quando exercem pontualmente esse ofício.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32625, 1204, 7, 'Pagai a cada um o que lhe compete: o imposto, a quem deveis o imposto; o tributo, a quem deveis o tributo; o temor e o respeito, a quem deveis o temor e o respeito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32626, 1204, 8, 'A ninguém fiqueis devendo coi­sa alguma, a não ser o amor recíproco; porque aquele que ama o seu próximo cumpriu toda a Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32627, 1204, 9, 'Pois os preceitos: Não cometerás adultério, não matarás, não furtarás, não cobiçarás, e ainda outros mandamentos que existam, eles se resumem nestas palavras: Amarás o teu próximo como a ti mesmo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32628, 1204, 10, 'A caridade não pratica o mal contra o próximo. Portanto, a caridade é o pleno cumprimento da Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32629, 1204, 11, 'Isso é tanto mais importante porque sabeis em que tempo vivemos. Já é hora de despertardes do sono. A salvação está mais perto do que quando abraçamos a fé.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32630, 1204, 12, 'A noite vai adiantada, e o dia vem chegando. Despojemo-nos das obras das trevas e vistamo-nos das armas da luz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32631, 1204, 13, 'Com­por­temo-nos honestamente, como em pleno dia: nada de orgias, nada de bebedeira; nada de desonestidades nem dissoluções; nada de contendas, nada de ciúmes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32632, 1204, 14, 'Ao contrário, revesti-vos do Senhor Jesus Cristo e não façais caso da carne nem lhe satisfaçais aos apetites.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32633, 1205, 1, 'Acolhei aquele que é fraco na fé, com bondade, sem discutir as suas opiniões.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32634, 1205, 2, 'Um crê poder comer de tudo; outro, que é fraco, só come legumes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32635, 1205, 3, 'Quem come de tudo não despreze aquele que não come. Quem não come não julgue aquele que come, porque Deus o acolhe do mesmo modo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32636, 1205, 4, 'Quem és tu, para julgares o servo de outros? Que esteja firme, ou caia, isto é lá com o seu se­nhor. Mas ele estará firme, porque poderoso é Deus para o sustentar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32637, 1205, 5, 'Um faz distinção entre dia e dia; outro, porém, considera iguais todos os dias. Cada um proceda segundo sua convicção.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32638, 1205, 6, 'Quem distingue o dia, age assim pelo Senhor. Quem come de tudo o faz pelo Senhor, porque dá graças a Deus. E quem não come, abstém-se pelo Senhor, e igualmente dá graças a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32639, 1205, 7, 'Nenhum de nós vive para si, e ninguém morre para si.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32640, 1205, 8, 'Se vivemos, vivemos para o Senhor; se morremos, morremos para o Se­nhor. Quer vivamos, quer morramos, pertencemos ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32641, 1205, 9, 'Para isso é que morreu Cristo e retomou a vida, para ser o Senhor tanto dos mortos como dos vivos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32642, 1205, 10, 'Por que julgas, então, o teu irmão? Ou por que des­prezas o teu irmão? Todos temos que comparecer perante o tribunal de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32643, 1205, 11, 'Porque está escrito: Por minha vida, diz o Senhor, diante de mim se dobrará todo joelho, e toda língua dará glória a Deus (Is 45,23).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32644, 1205, 12, 'Assim, pois, cada um de nós dará conta de si mesmo a Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32645, 1205, 13, 'Deixemos, pois, de nos julgar uns aos outros; antes, cuidai em não pôr um tropeço dian­te do vosso irmão ou dar-lhe ocasião de queda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32646, 1205, 14, 'Sei, estou convencido no Senhor Jesus de que nenhuma coisa é impura em si mesma; somente o é para quem a considera impura.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32647, 1205, 15, 'Ora, se por uma questão de comida entristeces o teu irmão, já não vives segundo a caridade. Pela comida não causes a perdição daquele por quem Cristo morreu!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32648, 1205, 16, 'Não venha a tornar-se objeto de calúnia a tua vantagem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32649, 1205, 17, 'O Reino de Deus não é comida nem bebida, mas justiça, paz e gozo no Espírito Santo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32650, 1205, 18, 'Quem deste modo serve a Cristo, agrada a Deus e goza de estima dos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32651, 1205, 19, 'Portanto, apliquemo-nos ao que contribui para a paz e para a mútua edificação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32652, 1205, 20, 'Não destruas a obra de Deus por questão de comida. Todas as coisas, em verdade, são puras, mas o que é mau para um homem é o fato de comer provocando um escândalo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32653, 1205, 21, 'Bom é não comer carne, nem beber vinho, nem outra coisa que para teu irmão possa ser uma ocasião de queda.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32654, 1205, 22, 'Tens uma convicção; guarda-a para ti mesmo, diante de Deus. Feliz é aquele que não se condena a si mesmo no ato a que se decide.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32655, 1205, 23, 'Mas, aquele que come apesar de suas dúvidas, condena-se, por não se guiar pela convicção. Tudo o que não procede da convicção é pecado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32656, 1206, 1, 'Nós, que somos os fortes, devemos suportar as fraquezas dos que são fracos, e não agir a nosso modo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32657, 1206, 2, 'Cada um de vós procure contentar o próximo, para seu bem e sua edificação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32658, 1206, 3, 'Cristo não se agradou a si mesmo; pelo contrário, como está escrito: Os insultos dos que vos ultrajam caíram sobre mim (Sl 68,10).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32659, 1206, 4, 'Ora, tudo quanto outrora foi escrito, foi escrito para a nossa instrução, a fim de que, pela perseverança e pela consolação que dão as Escrituras, tenhamos esperança.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32660, 1206, 5, 'O Deus da perseve­rança e da consolação vos conceda o mesmo sentimento uns para com os outros, segundo Jesus Cristo,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32661, 1206, 6, 'para que, com um só coração e uma só voz, glorifiqueis a Deus, Pai de nosso Senhor Jesus Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32662, 1206, 7, 'Por isso, acolhei-vos uns aos outros, como Cristo nos acolheu para a glória de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32663, 1206, 8, 'Pois asseguro que Cristo exerceu seu ministério entre os circuncisos para manifestar a veracidade de Deus pela realização das promessas feitas aos patriarcas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32664, 1206, 9, 'Quanto aos pagãos, eles só glorificam a Deus em razão de sua misericórdia, como está escrito: Por isso, eu vos louvarei entre as nações e cantarei louvores ao vosso nome (2Sm 22,50; Sl 17,50).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32665, 1206, 10, 'Noutro lugar diz: Alegrai-vos, nações, com o seu povo (Dt 32,43).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32666, 1206, 11, 'E ainda diz: Louvai ao Senhor, nações todas, e glorificai-o todos os povos (Sl 116,1)!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32667, 1206, 12, 'Isaías também diz: Da raiz de Jessé surgirá um rebento que governará as nações; nele esperarão as nações (Is 11,10).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32668, 1206, 13, 'O Deus da esperança vos en­cha de toda a alegria e de toda a paz na vossa fé, para que pela virtude do Espírito Santo transbordeis de esperança!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32669, 1206, 14, 'Estou pessoalmente convencido, meus irmãos, de que estais cheios de bondade, cheios de um perfeito conhecimento, capazes de vos admoestar uns aos outros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32670, 1206, 15, 'Se, em parte, vos escrevi com particular liberdade, foi para relem­brar-vos. E o fiz em virtude da graça que me foi dada por Deus,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32671, 1206, 16, 'de ser o ministro de Jesus Cristo entre os pagãos, exercendo a função sagrada do Evangelho de Deus. E isso para que os pagãos, santificados pelo Espírito Santo, lhe sejam uma oferta agradável.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32672, 1206, 17, 'Tenho motivo de gloriar-me em Jesus Cristo, no que diz respeito ao serviço de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32673, 1206, 18, 'Porque não ousaria mencionar ação alguma que Cristo não houvesse rea­lizado por meu ministério, para levar os pagãos a aceitar o Evangelho, pela palavra e pela ação,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32674, 1206, 19, 'pelo poder dos milagres e prodígios, pela virtude do Espírito. De maneira que tenho divulgado o Evangelho de Cristo desde Jerusalém e suas terras vizinhas até a Ilíria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32675, 1206, 20, 'E me empenhei por anunciar o Evangelho onde ainda não havia sido anunciado o nome de Cristo, pois não queria edificar sobre fundamento lançado por outro.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32676, 1206, 21, 'Fiz bem assim como está escrito: Irão vê-la aqueles aos quais ainda não tinha sido anunciado; chegarão a conhecê-lo aqueles que dele ainda não tinham ouvido falar (Is 52,15).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32677, 1206, 22, 'Foi isso o que muitas vezes me impediu de ir ter convosco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32678, 1206, 23, 'Mas, agora, já não tenho com que me ocupar nestas terras; e como há muitos anos tenho saudades de vós,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32679, 1206, 24, 'espero ver-vos de passagem, quando eu for à Espanha. Espero também ser por vós conduzido até lá, depois que tiver satisfeito, ao menos em parte, o meu desejo de estar convosco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32680, 1206, 25, 'Mas no momento vou a Jerusalém para ajuda dos irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32681, 1206, 26, 'A Macedônia e a Acaia houveram por bem fazer uma coleta para os irmãos de Jerusalém que se acham em pobre­za.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32682, 1206, 27, 'Houveram-no por bem; aliás, o devem a eles, pois se os pagãos têm parte nos bens espirituais dos judeus, devem por sua vez assisti-los com os bens materiais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32683, 1206, 28, 'Logo que eu tiver desempenhado essa incumbência, e lhes tiver feito entrega fiel dessa coleta, irei à Espanha, passando por vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32684, 1206, 29, 'E sei que, quando for ter convosco, irei com todas as riquezas das bênçãos de Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32685, 1206, 30, 'Rogo-vos, irmãos, em nome de nosso Senhor Jesus Cristo e em nome da caridade que é dada pelo Espírito, combatei comigo, dirigindo vossas orações a Deus por mim,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32686, 1206, 31, 'para que eu escape dos infiéis que estão na Judeia e para que o auxílio que levo a Jerusalém seja bem acolhido pelos irmãos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32687, 1206, 32, 'Então, poderei ir ver-vos com alegria e, se for a vontade de Deus, encontrar no vosso meio algum repouso.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32688, 1206, 33, 'E o Deus da paz esteja com todos vós. Amém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32689, 1207, 1, 'Recomendo-vos a nossa irmã Febe, que é diaconisa da igreja de Cên­cris,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32690, 1207, 2, 'para que a recebais no Senhor, de um modo digno dos santos, e a ajudeis em qualquer coisa que de vós venha a precisar; porque ela tem ajudado a muitos e também a mim.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32691, 1207, 3, 'Saudai Prisca e Áquila, meus coope­radores em Cristo Jesus;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32692, 1207, 4, 'pela minha vida eles expuseram as suas cabeças. E isso lhes agradeço, não só eu, mas também todas as igrejas dos gentios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32693, 1207, 5, 'Saudai também a comunidade que se reúne em sua casa.* Saudai o meu querido Epêneto, que foi as primícias da Ásia para Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32694, 1207, 6, 'Saudai Maria, que muito trabalhou por vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32695, 1207, 7, 'Saudai Andrônico e Júnias, meus parentes e companheiros de prisão, os quais são muito estimados entre os apóstolos e se tornaram discípulos de Cristo antes de mim.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32696, 1207, 8, 'Saudai Ampliato, amicíssimo meu no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32697, 1207, 9, 'Saudai Urbano, nosso colaborador em Cristo Jesus, e o meu amigo Estáquis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32698, 1207, 10, 'Saudai Apeles, provado em Cristo. Saudai aqueles que são da casa de Aristóbulo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32699, 1207, 11, 'Saudai Herodião, meu parente. Saudai os que são da família de Narciso, que estão no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32700, 1207, 12, 'Saudai Trifena e Trifosa, que trabalham para o Senhor. Saudai a estimada Pérside, que muito trabalhou para o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32701, 1207, 13, 'Saudai Rufo, esco­lhido no Senhor, e sua mãe, que considero como minha.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32702, 1207, 14, 'Saudai Asíncrito, Flegonte, Hermes, Pátro­bas, Hermas e os irmãos que estão com eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32703, 1207, 15, 'Saudai Filólogo e Júlia, Nereu e sua irmã, Olímpio e todos os irmãos que estão com eles.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32704, 1207, 16, 'Saudai-vos uns aos outros com ósculo santo. Todas as igrejas de Cristo vos saúdam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32705, 1207, 17, 'Rogo-vos, irmãos, que desconfieis daqueles que causam divisões e escândalos, apartando-se da doutrina que recebestes. Evitai-os!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32706, 1207, 18, 'Esses tais não servem a Cristo, nosso Senhor, mas ao próprio ventre. E com palavras adocicadas e linguagem lisonjeira, enganam os corações simples.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32707, 1207, 19, 'A vossa obediência se tornou notória em toda parte, razão por que eu me alegro a vosso respeito. Mas quero que sejais prudentes no tocante ao bem, e simples no tocante ao mal.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32708, 1207, 20, 'O Deus da paz em breve não tardará a esmagar Satanás debaixo dos vossos pés. A graça de nosso Senhor Jesus Cristo esteja convosco!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32709, 1207, 21, 'Saúdam-vos Timóteo, meu cooperador, Lúcio, Jasão e Sosípatro, meus parentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32710, 1207, 22, 'Eu, Tércio, que escrevi esta carta, vos saúdo no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32711, 1207, 23, 'Saúda-vos Gaio, meu hospedeiro, e de toda a Igreja.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32712, 1207, 24, 'Saúda-vos Erasto, tesoureiro da cidade, e Quarto, nosso irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32713, 1207, 25, 'Àquele que é poderoso para vos confirmar, segundo o meu Evangelho, na pregação de Jesus Cristo – conforme a revelação do mis­tério, guardado em segredo durante séculos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32714, 1207, 26, 'mas agora manifestado por ordem do eterno Deus e, por meio das Escrituras proféticas, dado a conhecer a todas as nações, a fim de levá-las à obediência da fé –,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32715, 1207, 27, 'a Deus, único, sábio, por Jesus Cristo, glória por toda a eternidade! Amém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32716, 1208, 1, 'Paulo, apóstolo de Jesus Cristo por chamado e vontade de Deus, e o irmão Sóstenes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32717, 1208, 2, 'à igreja de Deus que está em Corinto, aos fiéis santificados em Jesus Cristo, chamados à santidade, juntamente com todos os que, em qualquer lugar que estejam, invocam o nome de nosso Senhor Jesus Cristo, Se­nhor deles e nosso;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32718, 1208, 3, 'a vós, graça e paz da parte de Deus, nosso Pai, e da parte do Senhor Jesus Cristo!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32719, 1208, 4, 'Não cesso de agradecer a Deus por vós, pela graça divina que vos foi dada em Jesus Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32720, 1208, 5, 'Nele fostes ricamente contemplados com todos os dons, com os da palavra e os da ciência,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32721, 1208, 6, 'tão solidamente foi confirmado em vós o testemunho de Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32722, 1208, 7, 'Assim, enquanto aguardais a manifestação de nosso Senhor Jesus Cristo, não vos falta dom algum.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32723, 1208, 8, 'Ele há de vos confirmar até o fim, para que sejais irrepreensíveis no dia de nosso Senhor Jesus Cristo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32724, 1208, 9, 'Fiel é Deus, por quem fostes chamados à comunhão de seu Filho Jesus Cristo, nosso Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32725, 1208, 10, 'Rogo-vos, irmãos, em nome de nosso Senhor Jesus Cristo, que todos estejais em pleno acordo e que não haja entre vós divisões. Vivei em boa harmonia, no mesmo espírito e no mesmo sentimento.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32726, 1208, 11, 'Pois acerca de vós, irmãos meus, fui informado pelos que são da casa de Cloé, que há contendas entre vós.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32727, 1208, 12, 'Refiro-me ao fato de entre vós se usar esta linguagem: “Eu sou discípulo de Paulo; eu, de Apolo; eu, de Cefas; eu, de Cristo”.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32728, 1208, 13, 'Então, estaria Cristo dividido? É Paulo quem foi crucificado por vós? É em nome de Paulo que fostes batizados?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32729, 1208, 14, 'Graças a Deus, não batizei nenhum de vós, à exceção de Crispo e Gaio.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32730, 1208, 15, 'Assim ninguém poderá dizer que fostes batizados em meu nome.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32731, 1208, 16, '(Aliás, batizei também a família de Estéfanas. Além destes, não me cons­ta ter batizado ninguém mais.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32732, 1208, 17, 'Cristo não me enviou para batizar, mas para pregar o Evangelho; e isso sem recorrer à habilidade da arte oratória, para que não se desvirtue a cruz de Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32733, 1208, 18, 'A linguagem da cruz é loucura para os que se perdem, mas, para os que foram salvos, para nós, é uma força divina.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32734, 1208, 19, 'Está escrito: Destrui­rei a sabedoria dos sábios, e anularei a prudência dos prudentes (Is 29,14).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32735, 1208, 20, 'Onde está o sábio? Onde o erudito? Onde o argumentador deste mundo? Acaso não declarou Deus por loucura a sabedoria deste mundo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32736, 1208, 21, 'Já que o mundo, com a sua sabedoria, não reconheceu a Deus na sabedoria divina, aprouve a Deus salvar os que creem pela loucura de sua mensagem.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32737, 1208, 22, 'Os judeus pedem milagres, os gregos reclamam a sabedoria;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32738, 1208, 23, 'mas nós pregamos Cristo crucificado, escândalo para os judeus e loucura para os pagãos;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32739, 1208, 24, 'mas, para os eleitos – quer judeus quer gregos –, força de Deus e sabedoria de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32740, 1208, 25, 'Pois a loucura de Deus é mais sábia do que os homens, e a fraqueza de Deus é mais forte do que os homens.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32741, 1208, 26, 'Vede, irmãos, o vosso grupo de eleitos: não há entre vós muitos sábios, humanamente falando, nem muitos poderosos, nem muitos nobres.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32742, 1208, 27, 'O que é estulto no mundo, Deus o escolheu para confundir os sábios; e o que é fraco no mundo, Deus o escolheu para confundir os fortes;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32743, 1208, 28, 'e o que é vil e desprezível no mundo, Deus o escolheu, como também aquelas coisas que nada são, para destruir as que são.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32744, 1208, 29, 'Assim, nenhuma criatura se vangloriará diante de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32745, 1208, 30, 'É por sua graça que estais em Jesus Cristo, que, da parte de Deus, se tornou para nós sabedoria, justiça, santificação e redenção,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32746, 1208, 31, 'para que, como está escrito: quem se gloria, glorie-se no Senhor (Jr 9,23).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32747, 1209, 1, 'Também eu, quando fui ter convosco, irmãos, não fui com o prestígio da eloquência nem da sabedoria anunciar-vos o testemunho de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32748, 1209, 2, 'Julguei não dever saber coisa alguma entre vós, senão Jesus Cristo, e Jesus Cristo crucificado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32749, 1209, 3, 'Eu me apresentei em vosso meio num estado de fraqueza, de desassossego e de temor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32750, 1209, 4, 'A minha palavra e a minha pregação longe estavam da eloquência persuasiva da sabedoria; eram, antes, uma demonstração do Espírito e do poder divino,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32751, 1209, 5, 'para que vossa fé não se baseasse na sabedoria dos homens, mas no poder de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32752, 1209, 6, 'Entretanto, o que pregamos entre os perfeitos é uma sabedo­ria, porém não a sabedoria des­te mundo nem a dos grandes des­te mundo, que são, aos olhos daquela, desqualificados.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32753, 1209, 7, 'Pregamos a sabedoria de Deus, misteriosa e secreta, que Deus predeterminou antes de existir o tempo, para a nossa glória.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32754, 1209, 8, 'Sabedoria que nenhuma autoridade deste mundo conheceu (pois se a houvessem conhecido, não teriam crucificado o Senhor da glória).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32755, 1209, 9, 'É como está escrito: Coisas que os olhos não viram, nem os ouvidos ouviram, nem o coração humano imaginou (Is 64,4), tais são os bens que Deus tem preparado para aqueles que o amam.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32756, 1209, 10, 'Todavia, Deus no-las revelou pelo seu Espírito, porque o Espírito penetra tudo, mesmo as profundezas de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32757, 1209, 11, 'Pois quem conhe­ce as coisas que há no homem, senão o espírito do homem que nele reside? Assim também as coisas de Deus ninguém as conhece, senão o Espírito de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32758, 1209, 12, 'Ora, nós não recebemos o espírito do mundo, mas sim o Espírito que vem de Deus, que nos dá a conhecer as graças que Deus nos prodigalizou');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32759, 1209, 13, 'e que pregamos numa linguagem que nos foi ensinada não pela sabedoria humana, mas pelo Espírito, que exprime as coisas espirituais em termos espirituais.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32760, 1209, 14, 'Mas o homem natural não aceita as coisas do Espírito de Deus, pois para ele são loucuras. Nem as pode compreender, porque é pelo Espírito que se devem ponderar.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32761, 1209, 15, 'O homem espiritual, ao contrário, julga todas as coisas e não é julgado por ninguém.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32762, 1209, 16, 'Por que quem co­nheceu o pensamento do Senhor, se abalançará a instruí-lo (Is 40,13)? Nós, porém, temos o pensamento de Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32763, 1210, 1, 'A vós, irmãos, não vos pude falar como a homens espirituais, mas como a carnais, como a criancinhas em Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32764, 1210, 2, 'Eu vos dei leite a beber, e não alimento sólido que ainda não podíeis suportar. Nem ainda agora o podeis, porque ainda sois carnais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32765, 1210, 3, 'Com efeito, enquanto houver entre vós ciúmes e contendas, não será porque sois carnais e procedeis de um modo totalmente humano?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32766, 1210, 4, 'Quando, entre vós, um diz: “Eu sou de Paulo” – e outro –: “Eu, de Apolo” –, não é isso um modo de pensar totalmente humano?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32767, 1210, 5, 'Pois quem é Apolo? E quem é Paulo? Simples servos, por cujo intermédio abraçastes a fé, e isso conforme a medida que o Senhor repartiu a cada um deles:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32768, 1210, 6, 'eu plantei, Apolo regou, mas Deus é quem fez crescer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32769, 1210, 7, 'Assim, nem o que planta é alguma coisa nem o que rega, mas só Deus, que faz crescer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32770, 1210, 8, 'O que planta ou o que rega são iguais; cada um receberá a sua recompensa, segundo o seu trabalho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32771, 1210, 9, 'Nós somos operários com Deus. Vós, o campo de Deus, o edi­fício de Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32772, 1210, 10, 'Segundo a graça que Deus me deu, como sábio arquiteto lancei o fundamento, mas outro edifica sobre ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32773, 1210, 11, 'Quanto ao fundamento, ninguém pode pôr outro diverso daquele que já foi posto: Jesus Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32774, 1210, 12, 'Agora, se alguém edifica sobre este fundamento, com ouro, ou com prata, ou com pedras preciosas, com madeira, ou com feno, ou com palha,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32775, 1210, 13, 'a obra de cada um aparecerá. O dia (do julgamento) irá demonstrá-lo. Será descoberto pelo fogo; o fogo provará o que vale o trabalho de cada um.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32776, 1210, 14, 'Se a construção resistir, o cons­trutor receberá a recompensa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32777, 1210, 15, 'Se pegar fogo, arcará com os danos. Ele será salvo, porém passando de alguma maneira através do fogo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32778, 1210, 16, 'Não sabeis que sois o Templo de Deus, e que o Espírito de Deus habita em vós?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32779, 1210, 17, 'Se alguém destruir o Templo de Deus, Deus o destruirá. Porque o templo de Deus é sagrado – e isso sois vós.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32780, 1210, 18, 'Ninguém se engane a si mesmo. Se alguém dentre vós se julga sábio à maneira deste mundo, faça-se louco para tornar-se sábio,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32781, 1210, 19, 'porque a sabedoria deste mundo é loucura diante de Deus; pois (diz a Escritura) ele apanhará os sábios na sua própria astúcia (Jó 5,13).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32782, 1210, 20, 'E em outro lugar: O Senhor conhece os pensamentos dos sábios, e ele sabe que são vãos (Sl 93,11).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32783, 1210, 21, 'Portanto, ninguém ponha sua glória nos homens. Tudo é vosso:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32784, 1210, 22, 'Paulo, Apolo, Cefas, o mundo, a vida, a morte, o presente e o futuro. Tudo é vosso!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32785, 1210, 23, 'Mas vós sois de Cristo, e Cristo é de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32786, 1211, 1, 'Que os homens nos conside­rem, pois, como simples ope­rários de Cristo e administradores dos mistérios de Deus.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32787, 1211, 2, 'Ora, o que se exige dos administradores é que sejam fiéis.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32788, 1211, 3, 'A mim pouco se me dá ser julgado por vós ou por tribunal humano, pois nem eu me julgo a mim mesmo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32789, 1211, 4, 'De nada me acusa a consciência; contudo, nem por isso sou justificado. Meu juiz é o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32790, 1211, 5, 'Por isso, não julgueis antes do tempo; esperai que venha o Senhor. Ele porá às claras o que se acha escondido nas trevas. Ele manifestará as intenções dos corações. Então, cada um receberá de Deus o louvor que merece.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32791, 1211, 6, 'Se apliquei tudo isso a mim e a Apolo foi por vossa causa, para que, por meio de nós, aprendais a não ultrapassar o que está escrito e para que vos não ensoberbeçais tomando partido a favor de um e com prejuízo de outrem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32792, 1211, 7, 'O que há de superior em ti? Que é que possuis que não tenhas recebido? E, se o recebeste, por que te glorias, como se o não tivesses recebido?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32793, 1211, 8, 'Já estais fartos! Já estais ricos! Sem nós, sois reis! Praza a Deus que reineis, de fato, para que também nós reinemos convosco!*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32794, 1211, 9, 'Porque, ao que parece, Deus nos tem posto a nós, apóstolos, na última classe dos homens, por assim dizer sentenciados à morte, visto que fomos entregues em espetáculo ao mundo, aos anjos e aos homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32795, 1211, 10, 'Nós, estultos por causa de Cristo; e vós, sábios em Cristo! Nós, fracos; e vós, fortes! Vós, honrados; e nós, desprezados!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32796, 1211, 11, 'Até esta hora padecemos fome, sede e nudez. Somos esbofeteados, somos errantes,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32797, 1211, 12, 'fatigamo-nos, trabalhando com as nossas próprias mãos. Insultados, abençoamos; perseguidos, suportamos; caluniados, consolamos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32798, 1211, 13, 'Chegamos a ser como que o lixo do mundo, a escória de todos até agora...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32799, 1211, 14, 'Não vos escrevo estas coisas para vos envergonhar, mas admoesto-vos como meus filhos muito amados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32800, 1211, 15, 'Com efeito, ain­da que tivésseis dez mil mestres em Cristo, não tendes muitos pais; ora, fui eu que vos gerei em Cristo Jesus pelo Evangelho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32801, 1211, 16, 'Por isso, vos conjuro a que sejais meus imitadores.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32802, 1211, 17, 'Para isso é que vos enviei Timóteo, meu filho muito amado e fiel no Se­nhor. Ele vos recordará as minhas normas de conduta, tais como as ensino por toda parte, em todas as igrejas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32803, 1211, 18, 'Alguns, presumindo que eu não mais iria ter convosco, encheram-se de orgulho.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32804, 1211, 19, 'Mas brevemente irei ter convosco, se Deus quiser, e tomarei conhecimento não do que esses orgulhosos falam, mas do que são capazes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32805, 1211, 20, 'Porque o Reino de Deus não consiste em palavras, mas em atos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32806, 1211, 21, 'Que preferis? Que eu vá visitar-vos com a vara, ou com caridade e espírito de mansidão?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32807, 1212, 1, 'Ouve-se dizer constantemente que se comete, em vosso meio, a luxúria, e uma luxúria tão grave que não se costuma encontrar nem mesmo entre os pagãos: há entre vós quem vive com a mulher de seu pai!...*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32808, 1212, 2, 'E continuais cheios de orgulho, em vez de manifestardes tristeza, para que seja tirado dentre vós o que cometeu tal ação!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32809, 1212, 3, 'Pois eu, em verdade, ainda que distante corporalmente, mas presente em espírito, já julguei, como se estivesse presente, aquele que assim se comportou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32810, 1212, 4, 'Em nome do Se­nhor Jesus – reunidos vós e o meu espírito, com o poder de nosso Senhor Jesus –,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32811, 1212, 5, 'seja esse homem entregue a Satanás, para mortificação do seu corpo, a fim de que a sua alma seja salva no dia do Senhor Jesus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32812, 1212, 6, 'Não é nada belo o motivo da vossa jactância! Não sabeis que um pouco de fermento leveda a massa toda?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32813, 1212, 7, 'Purificai-vos do velho fermento, para que sejais massa nova, porque sois pães ázimos, porquanto Cristo, nossa Páscoa, foi imolado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32814, 1212, 8, 'Celebremos, pois, a festa, não com o fermento velho nem com o fermento da malícia e da corrupção, mas com os pães não fermentados de pureza e de verdade.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32815, 1212, 9, 'Na minha carta vos escrevi que não tivésseis familiaridade com os impudicos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32816, 1212, 10, 'Porém, não me referia de um modo absoluto a todos os impudicos deste mundo, os avarentos, os ladrões ou os idólatras, pois neste caso deveríeis sair deste mundo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32817, 1212, 11, 'Mas eu simplesmente quis dizer-vos que não tenhais comunicação com aquele que, chamando-se irmão, é impuro, avarento, idólatra, difamador, beberrão, ladrão. Com tais indivíduos nem sequer deveis comer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32818, 1212, 12, 'Pois que tenho eu de julgar os que estão fora? Não são os de dentro que deveis julgar?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32819, 1212, 13, 'Os de fora é Deus que os julgará... Tirai o perverso de vosso meio.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32820, 1213, 1, 'Quando algum de vós tem litígio contra outro, como é que se atreve a pedir justiça perante os injustos, em vez de recorrer aos (irmãos) santos?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32821, 1213, 2, 'Não sabeis que os santos julgarão o mundo? E, se o mundo há de ser julgado por vós, seríeis indignos de julgar os processos de mínima importância?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32822, 1213, 3, 'Não sabeis que julgaremos os anjos? Quanto mais as pequenas questões des­ta vida!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32823, 1213, 4, 'No entanto, quando tendes contendas desse gênero, escolheis para juízes pessoas cuja opinião é tida em nada pela Igreja.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32824, 1213, 5, 'Digo-o para confusão vossa. Será possível que não há entre vós um homem sábio, nem um sequer que possa julgar entre seus irmãos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32825, 1213, 6, 'Mas um irmão litiga com outro irmão, e isso dian­te de infiéis!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32826, 1213, 7, 'Na verdade, já é um mal para vós o fato de terdes processos uns contra os outros. Por que não preferis sofrer injustiça? Por que não preferis ser espoliados?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32827, 1213, 8, 'Não! Vós é que fazeis injustiça, vós é que espoliais – e isso entre irmãos!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32828, 1213, 9, 'Acaso não sabeis que os injustos não hão de possuir o Reino de Deus? Não vos enganeis: nem os impuros, nem os idólatras, nem os adúlteros, nem os efeminados, nem os devassos,');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32829, 1213, 10, 'nem os ladrões, nem os avarentos, nem os bêbados, nem os difamadores, nem os assaltantes hão de possuir o Reino de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32830, 1213, 11, 'Ao menos alguns de vós têm sido isso. Mas fostes lavados, mas fostes santificados, mas fostes justificados, em nome do Senhor Jesus Cristo e pelo Espírito de nosso Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32831, 1213, 12, 'Tudo me é permitido, mas nem tudo convém. Tudo me é permitido, mas eu não me deixarei dominar por coisa alguma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32832, 1213, 13, 'Os alimentos são para o estômago e o estômago para os alimentos: Deus destruirá tanto aqueles como este. O corpo, porém, não é para a impureza, mas para o Senhor e o Senhor para o corpo:');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32833, 1213, 14, 'Deus, que ressuscitou o Senhor, também nos ressuscitará a nós pelo seu poder.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32834, 1213, 15, 'Não sabeis que vossos corpos são membros de Cristo? Tomarei, então, os membros de Cristo e os farei membros de uma prostituta? De modo algum!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32835, 1213, 16, 'Ou não sabeis que o que se ajunta a uma prostituta se torna um só corpo com ela? Está escrito: Os dois serão uma só carne (Gn 2,24).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32836, 1213, 17, 'Pelo contrário, quem se une ao Senhor torna-se com ele um só espírito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32837, 1213, 18, 'Fugi da fornicação. Qualquer outro pecado que o homem comete é fora do corpo, mas o impuro peca contra o seu próprio corpo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32838, 1213, 19, 'Ou não sabeis que o vosso corpo é templo do Espírito Santo, que habi­ta em vós, o qual recebes­tes de Deus e que, por isso mesmo, já não vos pertenceis?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32839, 1213, 20, 'Porque fostes comprados por um grande preço. Glorificai, pois, a Deus no vosso corpo.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32840, 1214, 1, 'Agora, a respeito das coisas que me escrevestes. Penso que seria bom ao homem não tocar mulher alguma.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32841, 1214, 2, 'Todavia, conside­rando o perigo da incontinência, cada um tenha sua mulher, e cada mulher tenha seu marido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32842, 1214, 3, 'O marido cumpra o seu dever para com a sua esposa e da mesma forma também a esposa o cumpra para com o marido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32843, 1214, 4, 'A mulher não pode dispor de seu corpo: ele pertence ao seu marido. E da mesma forma o marido não pode dispor do seu corpo: ele pertence à sua esposa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32844, 1214, 5, 'Não vos recuseis um ao outro, a não ser de comum acordo, por algum tempo, para vos aplicar­des à oração; e depois retornai novamente um para o outro, para que não vos tente Satanás por vossa incontinência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32845, 1214, 6, 'Isto digo como concessão, não como ordem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32846, 1214, 7, 'Pois quereria que todos fossem como eu; mas cada um tem de Deus um dom particular: uns este, outros aquele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32847, 1214, 8, 'Aos solteiros e às viúvas, digo que lhes é bom se permanecerem assim, como eu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32848, 1214, 9, 'Mas, se não podem guardar a continência, ca­sem-se. É melhor casar do que abrasar-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32849, 1214, 10, 'Aos casados mando (não eu, mas o Senhor) que a mulher não se separe do marido.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32850, 1214, 11, 'E, se ela estiver separada, que fique sem se casar, ou que se reconcilie com seu marido. Igualmente, o marido não repudie sua mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32851, 1214, 12, 'Aos outros, digo eu, não o Senhor: se um irmão desposou uma mulher pagã (“sem a fé”) e esta consente em morar com ele, não a repudie.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32852, 1214, 13, 'Se uma mulher desposou um marido pagão e este consente em coabitar com ela, não repudie o marido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32853, 1214, 14, 'Porque o marido que não tem a fé é santificado por sua mu­lher; assim como a mulher que não tem a fé é santificada pelo marido que recebeu a fé. Do contrário, os vossos filhos seriam impuros quando, na realidade, são santos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32854, 1214, 15, 'Mas, se o pagão quer separar-se, que se separe; em tal caso, nem o irmão nem a irmã estão ligados. Deus vos chamou a viver em paz.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32855, 1214, 16, 'Aliás, como sabes tu, ó mulher, se salvarás o teu marido? Ou como sabes tu, ó marido, se salvarás a tua mulher?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32856, 1214, 17, 'Quanto ao mais, que cada um viva na condição na qual o Senhor o colocou ou em que o Senhor o chamou. É o que recomendo a todas as igrejas.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32857, 1214, 18, 'O que era circunciso quando foi chamado (à fé), não dissimule sua circuncisão. Quem era incircunciso não se faça circuncidar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32858, 1214, 19, 'A circuncisão de nada vale, e a incircuncisão de nada vale, o que importa é a observância dos mandamentos de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32859, 1214, 20, 'Cada um permaneça na profissão em que foi chamado por Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32860, 1214, 21, 'Eras escravo, quando Deus te chamou? Não te preocupes disto. Mesmo que possas tornar-te livre, antes cuida de aproveitar melhor o teu chamado.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32861, 1214, 22, 'Pois o escravo, que foi chamado pelo Senhor, conquistou a liberdade do Senhor. Da mesma forma, quem era livre por ocasião do chamado, fez-se escravo de Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32862, 1214, 23, 'Por alto preço fostes comprados, não vos torneis escravos de homens.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32863, 1214, 24, 'Irmãos, cada um permaneça diante de Deus na condição em que estava quando Deus o chamou.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32864, 1214, 25, 'A respeito das pessoas virgens, não tenho mandamento do Senhor; porém, dou o meu conselho, como homem que recebeu da misericórdia do Senhor a graça de ser digno de con­fian­ça.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32865, 1214, 26, 'Julgo, pois, em razão das dificuldades presentes, ser conveniente ao homem ficar assim como é.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32866, 1214, 27, 'Estás casado? Não procures desligar-te. Não estás casado? Não procures mulher.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32867, 1214, 28, 'Mas, se queres casar-te, não pecas; assim como a jovem que se casa não peca. Todavia, padecerão a tribulação da carne; e eu quisera poupar-vos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32868, 1214, 29, 'Mas eis o que vos digo, irmãos: o tempo é breve. O que importa é que os que têm mulher vivam como se a não tivessem;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32869, 1214, 30, 'os que choram, como se não chorassem; os que se alegram, como se não se alegrassem; os que compram, como se não possuíssem;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32870, 1214, 31, 'os que usam deste mundo, como se dele não usassem. Porque a figura deste mundo passa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32871, 1214, 32, 'Quisera ver-vos livres de toda preocupação. O solteiro cuida das coisas que são do Senhor, de como agradar ao Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32872, 1214, 33, 'O casado preo­cupa-se com as coisas do mundo, procurando agradar à sua esposa.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32873, 1214, 34, 'A mesma diferença existe com a mulher solteira ou a virgem. Aquela que não é casada cuida das coisas do Senhor, para ser santa no corpo e no espírito; mas a casada cuida das coisas do mundo, procurando agradar ao marido.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32874, 1214, 35, 'Digo isto para vosso proveito, não para vos estender um laço, mas para vos ensinar o que melhor convém, o que vos poderá unir ao Senhor sem partilha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32875, 1214, 36, 'Se alguém julga que é inconveniente para a sua filha ultrapassar a idade de casar-se e que é seu dever casá-la, faça-o como quiser: não há falta alguma em fazê-la casar-se.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32876, 1214, 37, 'Mas aquele que, sem nenhum constrangimento e com perfeita liberdade de escolha, tiver tomado no seu coração a decisão de guardar a sua filha virgem, procede bem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32877, 1214, 38, 'Em suma, aquele que casa a sua filha faz bem; e aquele que não a casa, faz ainda melhor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32878, 1214, 39, 'A mulher está ligada ao marido enquanto ele viver. Mas, se morrer o marido, ela fica livre e poderá casar-se com quem quiser, contanto que seja no Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32879, 1214, 40, 'Contudo, na minha opinião, ela será mais feliz se permanecer como está. E creio que também eu tenho o Espírito de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32880, 1215, 1, 'Quanto às carnes oferecidas aos ídolos, somos esclarecidos, possuímos todos a ciência... Porém, a ciência incha, a caridade cons­trói.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32881, 1215, 2, 'Se alguém pensa que sabe alguma coisa, ainda não co­nhece nada como convém conhe­cer.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32882, 1215, 3, 'Mas, se alguém ama a Deus, esse é conhecido por ele.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32883, 1215, 4, 'Assim, pois, quanto ao comer das carnes imoladas aos ídolos, sabemos que não existem realmente ídolos no mundo e que não há outro Deus, senão um só.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32884, 1215, 5, 'Pretende-se, é verdade, que existam outros deu­ses, quer no céu quer na terra (e há um bom número desses deuses e senhores).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32885, 1215, 6, 'Mas, para nós, há um só Deus, o Pai, do qual procedem todas as coisas e para o qual existimos, e um só Senhor, Jesus Cristo, por quem todas as coisas existem e nós também.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32886, 1215, 7, 'Todavia, nem todos têm esse conhecimento. Alguns, habituados ao modo antigo de considerar o ídolo, comem a carne como sacrificada ao ídolo; e sua consciência, por ser débil, se mancha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32887, 1215, 8, 'Não é, entretanto, a comida que nos torna agradáveis a Deus: comendo, não ganhamos nada; e não comendo, nada perdemos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32888, 1215, 9, 'Atenção, porém: que essa vossa liberdade não venha a ser ocasião de queda aos fracos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32889, 1215, 10, 'Se alguém te vir, a ti que és instruído, sentado à mesa no templo dos ídolos, não se sentirá, por fraqueza de consciên­cia, também autorizado a comer do sacrifício aos ídolos?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32890, 1215, 11, 'E assim por tua ciência vai se perder quem é fraco, um irmão, pelo qual Cristo morreu!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32891, 1215, 12, 'Assim, pecando vós contra os irmãos e fe­rindo sua débil consciência, pecais contra Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32892, 1215, 13, 'Pelo que, se a comida serve de ocasião de queda a meu irmão, jamais comerei carne, a fim de que eu não me torne ocasião de queda para o meu irmão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32893, 1216, 1, 'Não sou eu livre? Não sou apóstolo? Não vi Jesus nosso Senhor? Não sois vós minha obra no Senhor?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32894, 1216, 2, 'Se para outros não sou apóstolo, ao menos para vós o sou, porque vós sois no Senhor o selo do meu apostolado.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32895, 1216, 3, 'Esta é a minha defesa contra os que me denigrem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32896, 1216, 4, 'Não temos nós porventura o direito de comer e beber?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32897, 1216, 5, 'Acaso não temos nós direito de deixar que nos acompanhe uma mulher irmã, a exemplo dos outros apóstolos e dos irmãos do Senhor e de Cefas?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32898, 1216, 6, 'Ou só eu e Barnabé não temos direito de deixar o trabalho?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32899, 1216, 7, 'Quem, jamais, vai à guerra à sua custa? Quem planta uma vinha e não come do seu fruto? Quem apascenta um rebanho e não se alimenta do leite do rebanho?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32900, 1216, 8, 'Trata-se, acaso, de simples norma entre os homens? Ou a Lei não diz também o mesmo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32901, 1216, 9, 'Na Lei de Moisés está escrito: Não atarás a boca ao boi que debulha (Dt 25,4). Acaso Deus tem dó dos bois?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32902, 1216, 10, 'Não é, na realidade, em atenção a nós que ele diz isso? Sim! É por nós que está escrito. Quem trabalha deve trabalhar com esperança e igualmente quem debulha deve debu­lhar com esperança de receber a sua parte.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32903, 1216, 11, 'Se entre vós semeamos bens espirituais, será, porventura, demasiada exigência colhermos de vossos bens materiais?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32904, 1216, 12, 'Se outros se arrogam este direito sobre vós, não o temos muito mais? Entretanto, não temos feito uso deste direito: sofremos tudo para não pôr obstáculo algum ao Evangelho de Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32905, 1216, 13, 'Não sabeis que os ministros do culto vivem do culto, e que os que servem ao altar participam do altar?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32906, 1216, 14, 'Assim também ordenou o Senhor que os que anunciam o Evangelho vivam do Evangelho.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32907, 1216, 15, 'Mas não tenho usado de ne­nhum desses direitos; e nem escrevo isto para reclamá-los. Preferiria morrer a... Mas ninguém me tirará este título de glória.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32908, 1216, 16, 'Anunciar o Evangelho não é glória para mim; é uma obrigação que se me impõe. Ai de mim, se eu não anunciar o Evangelho!');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32909, 1216, 17, 'Se o fizesse de minha iniciativa, mereceria recompensa. Se o faço independentemente de minha vontade, é uma missão que me foi imposta.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32910, 1216, 18, 'Então, em que consiste a minha recompensa? Em que, na pregação do Evangelho, o anuncio gratuitamente, sem usar do direito que esta pregação me confere.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32911, 1216, 19, 'Embora livre de sujeição de qualquer pessoa, eu me fiz servo de todos para ganhar o maior número possível.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32912, 1216, 20, 'Para os judeus fiz-me judeu, a fim de ganhar os judeus. Para os que estão debaixo da Lei, fiz-me como se eu estivesse debaixo da Lei, embora eu não esteja, a fim de ganhar aqueles que estão debaixo da Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32913, 1216, 21, 'Para os que não têm Lei, fiz-me como se eu não tivesse Lei, ainda que eu não esteja isento da Lei de Deus – porquanto estou sob a Lei de Cristo –, a fim de ganhar os que não têm Lei.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32914, 1216, 22, 'Fiz-me fraco com os fracos, a fim de ganhar os fracos. Fiz-me tudo para todos, a fim de salvar a todos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32915, 1216, 23, 'E tudo isso faço por causa do Evangelho, para dele me fazer participante.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32916, 1216, 24, 'Nas corridas de um estádio, todos correm, mas bem sabeis que um só recebe o prêmio. Correi, pois, de tal maneira que o consigais.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32917, 1216, 25, 'Todos os atletas se impõem a si muitas privações; e o fazem para alcançar uma coroa corruptível. Nós o fazemos por uma coroa incorruptível.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32918, 1216, 26, 'Assim, eu corro, mas não sem rumo certo. Dou golpes, mas não no ar.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32919, 1216, 27, 'Ao contrário, castigo o meu corpo e o mantenho em servidão, de medo de vir eu mesmo a ser excluí­do depois de eu ter pre­gado aos outros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32920, 1217, 1, '(Não quero que ignoreis, irmãos), que os nossos pais estiveram todos debaixo da nuvem e que todos atravessaram o mar;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32921, 1217, 2, 'todos foram batizados em Moisés, na nuvem e no mar;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32922, 1217, 3, 'todos comeram do mesmo alimento espiritual;*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32923, 1217, 4, 'todos beberam da mesma bebida espiritual (pois todos bebiam da pedra espiritual que os seguia; e essa pedra era Cristo).*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32924, 1217, 5, 'Não obs­tante, a maioria deles desgostou a Deus, pois seus cadáveres cobriram o deserto.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32925, 1217, 6, 'Essas coisas aconteceram para nos servir de exemplo, a fim de não cobiçarmos coisas más, como eles as cobiçaram.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32926, 1217, 7, 'Nem vos torneis idólatras, como alguns deles, conforme está escrito: O povo sentou-se para comer e para beber, e depois levantou-se para se divertir (Ex 32,6).');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32927, 1217, 8, 'Nem nos en­treguemos à impureza como alguns deles se entregaram, e morreram num só dia vinte e três mil.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32928, 1217, 9, 'Nem tentemos o Senhor, como alguns deles o tentaram, e pereceram mordidos pelas serpentes.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32929, 1217, 10, 'Nem murmureis, como murmuraram alguns deles, e foram mortos pelo exterminador.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32930, 1217, 11, 'Todas essas desgraças lhes aconteceram para nosso exemplo; foram escritas para advertência nossa, para nós, que tocamos o final dos tempos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32931, 1217, 12, 'Portanto, quem pensa estar de pé veja que não caia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32932, 1217, 13, 'Não vos sobreveio tentação alguma que ultrapassasse as forças humanas. Deus é fiel: não permitirá que sejais tentados além das vossas forças, mas com a tentação, ele vos dará os meios de suportá-la e sairdes dela.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32933, 1217, 14, 'Portanto, caríssimos meus, fugi da idolatria.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32934, 1217, 15, 'Falo como a pessoas sensatas; julgai vós mesmos o que digo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32935, 1217, 16, 'O cálice de bênção, que benzemos, não é a comunhão do sangue de Cristo? E o pão, que partimos, não é a comunhão do corpo de Cristo?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32936, 1217, 17, 'Uma vez que há um único pão, nós, embora sendo muitos, formamos um só corpo, porque todos nós comungamos do mesmo pão.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32937, 1217, 18, 'Considerai Israel segundo a carne: não entram em comunhão com o altar os que comem as vítimas?*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32938, 1217, 19, 'Que quero afirmar com isso? Que a carne sacrificada aos ídolos ou o próprio ídolo são alguma coisa?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32939, 1217, 20, 'Não! As coisas que os pagãos sacrificam, sacrificam-nas a demônios e não a Deus. E eu não quero que tenhais comunhão com os demônios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32940, 1217, 21, 'Não podeis beber ao mesmo tempo o cálice do Senhor e o cálice dos demônios. Não podeis participar ao mesmo tempo da mesa do Senhor e da mesa dos demônios.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32941, 1217, 22, 'Ou queremos provocar a ira do Senhor? Acaso somos mais fortes do que ele?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32942, 1217, 23, 'Tudo é permitido, mas nem tudo é oportuno. Tudo é permitido, mas nem tudo edifica.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32943, 1217, 24, 'Ninguém busque o seu interesse, mas o do próximo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32944, 1217, 25, 'Comei de tudo o que se vende no açougue, sem indagar de coisa alguma por motivo de consciência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32945, 1217, 26, 'Do Senhor é a terra e tudo que ela encerra.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32946, 1217, 27, 'Se algum infiel vos convidar e quiserdes ir, comei de tudo o que se vos puser diante sem indagar de coisa alguma por motivo de consciên­cia.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32947, 1217, 28, 'Mas se alguém disser: “Isto foi sacrificado aos ídolos”, não o comais, em atenção àquele que o advertiu e por motivo de consciência.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32948, 1217, 29, 'Dizendo consciência, refiro-me não à tua, mas à do outro. Com efeito, por que razão seria regulada a minha liberdade pela consciência alheia?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32949, 1217, 30, 'Se eu como com ações de graças, por que serei eu censurado por causa do alimento pelo qual rendo graças?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32950, 1217, 31, 'Portanto, quer comais, quer bebais ou façais qualquer outra coisa, fazei tudo para a glória de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32951, 1217, 32, 'Não vos torneis causa de escândalo, nem para os judeus, nem para os gentios, nem para a Igreja de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32952, 1217, 33, 'Fazei como eu: em todas as circunstâncias, procuro agradar a todos. Não busco os meus interes­ses próprios, mas os interesses dos outros, para que todos sejam salvos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32953, 1218, 1, 'Tornai-vos os meus imitadores, como eu o sou de Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32954, 1218, 2, 'Eu vos felicito, porque em tudo vos lem­brais de mim, e guardais as minhas instruções, tais como eu vo-las transmiti.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32955, 1218, 3, 'Mas quero que saibais que senhor de todo homem é Cristo, senhor da mulher é o homem, senhor de Cristo é Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32956, 1218, 4, 'Todo homem que ora ou profetiza com a cabeça coberta falta ao respeito ao seu senhor.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32957, 1218, 5, 'E toda mulher que ora ou profetiza, não tendo coberta a cabeça, falta ao respeito ao seu senhor, porque é como se estivesse rapada.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32958, 1218, 6, 'Se uma mulher não se cobre com um véu, então corte o cabelo. Ora, se é vergonhoso para a mulher ter os cabelos cortados ou a cabeça rapada, então que se cubra com um véu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32959, 1218, 7, 'Quanto ao homem, não deve cobrir sua cabeça, porque é imagem e esplendor de Deus; a mulher é o reflexo do homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32960, 1218, 8, 'Com efeito, o homem não foi tirado da mulher, mas a mulher do homem;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32961, 1218, 9, 'nem foi o homem criado para a mulher, mas sim a mulher para o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32962, 1218, 10, 'Por isso, a mulher deve trazer o sinal da submissão sobre sua cabeça, por causa dos anjos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32963, 1218, 11, 'Com tudo isso, aos olhos do Senhor, nem o homem existe sem a mulher, nem a mulher sem o homem.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32964, 1218, 12, 'Pois a mulher foi tirada do homem, porém o homem nasce da mulher, e ambos vêm de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32965, 1218, 13, 'Julgai vós mesmos: é decente que uma mulher reze a Deus sem estar coberta com véu?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32966, 1218, 14, 'A própria natureza não vos ensina que é uma desonra para o homem usar cabelo comprido?');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32967, 1218, 15, 'Ao passo que é glória para a mulher uma longa cabeleira, porque lhe foi dada como um véu.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32968, 1218, 16, 'Se, no entanto, alguém quiser contestar, nós não temos tal costume e nem as igrejas de Deus.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32969, 1218, 17, 'Fazendo-vos essas adver­tências, não vos posso louvar a respeito de vossas assembleias que causam mais prejuízo que proveito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32970, 1218, 18, 'Em primeiro lugar, ouço dizer que, quando se reúne a vossa assembleia, há desarmonias entre vós. (E em parte eu acredito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32971, 1218, 19, 'É necessário que entre vós haja partidos para que possam manifestar-se os que são realmente virtuosos.)');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32972, 1218, 20, 'Desse modo, quando vos reunis, já não é para comer a ceia do Senhor,*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32973, 1218, 21, 'porquanto, mal vos pondes à mesa, cada um se apressa a tomar sua própria refeição; e enquanto uns têm fome, outros se fartam.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32974, 1218, 22, 'Porventura não tendes casa onde comer e beber? Ou menosprezais a Igreja de Deus, e quereis envergonhar aqueles que nada têm? Que vos direi? Devo louvar-vos? Não! Nisso não vos louvo...');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32975, 1218, 23, 'Eu recebi do Senhor o que vos transmiti: que o Senhor Jesus, na noite em que foi traído, tomou o pão');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32976, 1218, 24, 'e, depois de ter dado graças, partiu-o e disse: “Isto é o meu corpo, que é entregue por vós; fazei isto em memória de mim”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32977, 1218, 25, 'Do mesmo modo, depois de haver ceado, tomou também o cálice, dizendo: “Este cálice é a Nova Aliança no meu sangue; todas as vezes que o beberdes, fazei-o em memória de mim”.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32978, 1218, 26, 'Assim, todas as vezes que comeis desse pão e bebeis desse cálice lembrais a morte do Senhor, até que venha.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32979, 1218, 27, 'Portanto, todo aquele que comer o pão ou beber o cálice do Senhor indignamente será culpável do corpo e do sangue do Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32980, 1218, 28, 'Que cada um se examine a si mesmo e, assim, coma desse pão e beba desse cálice.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32981, 1218, 29, 'Aquele que o come e o bebe sem distinguir o corpo do Senhor, come e bebe a sua própria condenação.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32982, 1218, 30, 'Essa é a razão por que entre vós há muitos adoentados e fracos, e muitos mortos.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32983, 1218, 31, 'Se nos examinássemos a nós mesmos, não seríamos julgados.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32984, 1218, 32, 'Mas, sendo julgados pelo Senhor, ele nos castiga para não sermos condenados com o mundo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32985, 1218, 33, 'Portanto, irmãos meus, quando vos reunis para a ceia, esperai uns pelos outros.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32986, 1218, 34, 'Se alguém tem fome, coma em casa. Assim vossas reuniões não vos atrairão a condenação. As demais coisas eu determinarei quando for ter convosco.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32987, 1219, 1, 'A respeito dos dons espirituais, irmãos, não quero que vivais na ignorância.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32988, 1219, 2, 'Sabeis que, quando éreis pagãos, vos deixáveis levar, conforme vossas tendências, aos ídolos mudos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32989, 1219, 3, 'Por isso, eu vos declaro: ninguém, falando sob a ação divina, pode dizer: “Jesus seja maldito”; e ninguém pode dizer: “Jesus é o Senhor”, senão sob a ação do Espírito Santo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32990, 1219, 4, 'Há diversidade de dons, mas um só Espírito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32991, 1219, 5, 'Os ministérios são diversos, mas um só é o Senhor.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32992, 1219, 6, 'Há também diversas operações, mas é o mesmo Deus que opera tudo em todos.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32993, 1219, 7, 'A cada um é dada a manifestação do Espírito para proveito comum.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32994, 1219, 8, 'A um é dada pelo Espírito uma palavra de sabedoria; a outro, uma palavra de ciência, por esse mesmo Espírito;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32995, 1219, 9, 'a outro, a fé, pelo mesmo Espírito; a outro, a graça de curar as doenças, no mesmo Espírito;');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32996, 1219, 10, 'a outro, o dom de milagres; a outro, a profecia; a outro, o discernimento dos espíritos; a outro, a variedade de línguas; a outro, por fim, a interpretação das línguas.*');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32997, 1219, 11, 'Mas um e o mesmo Espírito distribui todos esses dons, repartindo a cada um como lhe apraz.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32998, 1219, 12, 'Porque, como o corpo é um todo com muitos membros, e todos os membros do corpo, embora muitos, formam um só corpo, assim também é Cristo.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (32999, 1219, 13, 'Em um só Espírito fomos batizados todos nós, para formar um só corpo, judeus ou gregos, escravos ou livres; e todos fomos impregnados do mesmo Espírito.');
INSERT INTO bible_verses (id, chapter_id, verse_number, text) VALUES (33000, 1219, 14, 'Assim, o corpo não consiste em um só membro, mas em muitos.');
